INSERT INTO `bible_verses_tr` VALUES ('1', '40', '1', '1', 'βιβλος γενεσεως ιησου χριστου υιου δαβιδ υιου αβρααμ');
INSERT INTO `bible_verses_tr` VALUES ('2', '40', '1', '2', 'αβρααμ εγεννησεν τον ισαακ ισαακ δε εγεννησεν τον ιακωβ ιακωβ δε εγεννησεν τον ιουδαν και τους αδελφους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3', '40', '1', '3', 'ιουδας δε εγεννησεν τον φαρες και τον ζαρα εκ της θαμαρ φαρες δε εγεννησεν τον εσρωμ εσρωμ δε εγεννησεν τον αραμ');
INSERT INTO `bible_verses_tr` VALUES ('4', '40', '1', '4', 'αραμ δε εγεννησεν τον αμιναδαβ αμιναδαβ δε εγεννησεν τον ναασσων ναασσων δε εγεννησεν τον σαλμων');
INSERT INTO `bible_verses_tr` VALUES ('5', '40', '1', '5', 'σαλμων δε εγεννησεν τον βοοζ εκ της ραχαβ βοοζ δε εγεννησεν τον ωβηδ εκ της ρουθ ωβηδ δε εγεννησεν τον ιεσσαι');
INSERT INTO `bible_verses_tr` VALUES ('6', '40', '1', '6', 'ιεσσαι δε εγεννησεν τον δαβιδ τον βασιλεα δαβιδ δε ο βασιλευς εγεννησεν τον σολομωντα εκ της του ουριου');
INSERT INTO `bible_verses_tr` VALUES ('7', '40', '1', '7', 'σολομων δε εγεννησεν τον ροβοαμ ροβοαμ δε εγεννησεν τον αβια αβια δε εγεννησεν τον ασα');
INSERT INTO `bible_verses_tr` VALUES ('8', '40', '1', '8', 'ασα δε εγεννησεν τον ιωσαφατ ιωσαφατ δε εγεννησεν τον ιωραμ ιωραμ δε εγεννησεν τον οζιαν');
INSERT INTO `bible_verses_tr` VALUES ('9', '40', '1', '9', 'οζιας δε εγεννησεν τον ιωαθαμ ιωαθαμ δε εγεννησεν τον αχαζ αχαζ δε εγεννησεν τον εζεκιαν');
INSERT INTO `bible_verses_tr` VALUES ('10', '40', '1', '10', 'εζεκιας δε εγεννησεν τον μανασση μανασσης δε εγεννησεν τον αμων αμων δε εγεννησεν τον ιωσιαν');
INSERT INTO `bible_verses_tr` VALUES ('11', '40', '1', '11', 'ιωσιας δε εγεννησεν τον ιεχονιαν και τους αδελφους αυτου επι της μετοικεσιας βαβυλωνος');
INSERT INTO `bible_verses_tr` VALUES ('12', '40', '1', '12', 'μετα δε την μετοικεσιαν βαβυλωνος ιεχονιας εγεννησεν τον σαλαθιηλ σαλαθιηλ δε εγεννησεν τον ζοροβαβελ');
INSERT INTO `bible_verses_tr` VALUES ('13', '40', '1', '13', 'ζοροβαβελ δε εγεννησεν τον αβιουδ αβιουδ δε εγεννησεν τον ελιακειμ ελιακειμ δε εγεννησεν τον αζωρ');
INSERT INTO `bible_verses_tr` VALUES ('14', '40', '1', '14', 'αζωρ δε εγεννησεν τον σαδωκ σαδωκ δε εγεννησεν τον αχειμ αχειμ δε εγεννησεν τον ελιουδ');
INSERT INTO `bible_verses_tr` VALUES ('15', '40', '1', '15', 'ελιουδ δε εγεννησεν τον ελεαζαρ ελεαζαρ δε εγεννησεν τον ματθαν ματθαν δε εγεννησεν τον ιακωβ');
INSERT INTO `bible_verses_tr` VALUES ('16', '40', '1', '16', 'ιακωβ δε εγεννησεν τον ιωσηφ τον ανδρα μαριας εξ ης εγεννηθη ιησους ο λεγομενος χριστος');
INSERT INTO `bible_verses_tr` VALUES ('17', '40', '1', '17', 'πασαι ουν αι γενεαι απο αβρααμ εως δαβιδ γενεαι δεκατεσσαρες και απο δαβιδ εως της μετοικεσιας βαβυλωνος γενεαι δεκατεσσαρες και απο της μετοικεσιας βαβυλωνος εως του χριστου γενεαι δεκατεσσαρες');
INSERT INTO `bible_verses_tr` VALUES ('18', '40', '1', '18', 'του δε ιησου χριστου η γεννησις ουτως ην μνηστευθεισης γαρ της μητρος αυτου μαριας τω ιωσηφ πριν η συνελθειν αυτους ευρεθη εν γαστρι εχουσα εκ πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('19', '40', '1', '19', 'ιωσηφ δε ο ανηρ αυτης δικαιος ων και μη θελων αυτην παραδειγματισαι εβουληθη λαθρα απολυσαι αυτην');
INSERT INTO `bible_verses_tr` VALUES ('20', '40', '1', '20', 'ταυτα δε αυτου ενθυμηθεντος ιδου αγγελος κυριου κατ οναρ εφανη αυτω λεγων ιωσηφ υιος δαβιδ μη φοβηθης παραλαβειν μαριαμ την γυναικα σου το γαρ εν αυτη γεννηθεν εκ πνευματος εστιν αγιου');
INSERT INTO `bible_verses_tr` VALUES ('21', '40', '1', '21', 'τεξεται δε υιον και καλεσεις το ονομα αυτου ιησουν αυτος γαρ σωσει τον λαον αυτου απο των αμαρτιων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('22', '40', '1', '22', 'τουτο δε ολον γεγονεν ινα πληρωθη το ρηθεν υπο του κυριου δια του προφητου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('23', '40', '1', '23', 'ιδου η παρθενος εν γαστρι εξει και τεξεται υιον και καλεσουσιν το ονομα αυτου εμμανουηλ ο εστιν μεθερμηνευομενον μεθ ημων ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('24', '40', '1', '24', 'διεγερθεις δε ο ιωσηφ απο του υπνου εποιησεν ως προσεταξεν αυτω ο αγγελος κυριου και παρελαβεν την γυναικα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('25', '40', '1', '25', 'και ουκ εγινωσκεν αυτην εως ου ετεκεν τον υιον αυτης τον πρωτοτοκον και εκαλεσεν το ονομα αυτου ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('26', '40', '2', '1', 'του δε ιησου γεννηθεντος εν βηθλεεμ της ιουδαιας εν ημεραις ηρωδου του βασιλεως ιδου μαγοι απο ανατολων παρεγενοντο εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('27', '40', '2', '2', 'λεγοντες που εστιν ο τεχθεις βασιλευς των ιουδαιων ειδομεν γαρ αυτου τον αστερα εν τη ανατολη και ηλθομεν προσκυνησαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('28', '40', '2', '3', 'ακουσας δε ηρωδης ο βασιλευς εταραχθη και πασα ιεροσολυμα μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('29', '40', '2', '4', 'και συναγαγων παντας τους αρχιερεις και γραμματεις του λαου επυνθανετο παρ αυτων που ο χριστος γενναται');
INSERT INTO `bible_verses_tr` VALUES ('30', '40', '2', '5', 'οι δε ειπον αυτω εν βηθλεεμ της ιουδαιας ουτως γαρ γεγραπται δια του προφητου');
INSERT INTO `bible_verses_tr` VALUES ('31', '40', '2', '6', 'και συ βηθλεεμ γη ιουδα ουδαμως ελαχιστη ει εν τοις ηγεμοσιν ιουδα εκ σου γαρ εξελευσεται ηγουμενος οστις ποιμανει τον λαον μου τον ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('32', '40', '2', '7', 'τοτε ηρωδης λαθρα καλεσας τους μαγους ηκριβωσεν παρ αυτων τον χρονον του φαινομενου αστερος');
INSERT INTO `bible_verses_tr` VALUES ('33', '40', '2', '8', 'και πεμψας αυτους εις βηθλεεμ ειπεν πορευθεντες ακριβως εξετασατε περι του παιδιου επαν δε ευρητε απαγγειλατε μοι οπως καγω ελθων προσκυνησω αυτω');
INSERT INTO `bible_verses_tr` VALUES ('34', '40', '2', '9', 'οι δε ακουσαντες του βασιλεως επορευθησαν και ιδου ο αστηρ ον ειδον εν τη ανατολη προηγεν αυτους εως ελθων εστη επανω ου ην το παιδιον');
INSERT INTO `bible_verses_tr` VALUES ('35', '40', '2', '10', 'ιδοντες δε τον αστερα εχαρησαν χαραν μεγαλην σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('36', '40', '2', '11', 'και ελθοντες εις την οικιαν  {VAR1: ευρον } {VAR2: ειδον } το παιδιον μετα μαριας της μητρος αυτου και πεσοντες προσεκυνησαν αυτω και ανοιξαντες τους θησαυρους αυτων προσηνεγκαν αυτω δωρα χρυσον και λιβανον και σμυρναν');
INSERT INTO `bible_verses_tr` VALUES ('37', '40', '2', '12', 'και χρηματισθεντες κατ οναρ μη ανακαμψαι προς ηρωδην δι αλλης οδου ανεχωρησαν εις την χωραν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('38', '40', '2', '13', 'αναχωρησαντων δε αυτων ιδου αγγελος κυριου φαινεται κατ οναρ τω ιωσηφ λεγων εγερθεις παραλαβε το παιδιον και την μητερα αυτου και φευγε εις αιγυπτον και ισθι εκει εως αν ειπω σοι μελλει γαρ ηρωδης ζητειν το παιδιον του απολεσαι αυτο');
INSERT INTO `bible_verses_tr` VALUES ('39', '40', '2', '14', 'ο δε εγερθεις παρελαβεν το παιδιον και την μητερα αυτου νυκτος και ανεχωρησεν εις αιγυπτον');
INSERT INTO `bible_verses_tr` VALUES ('40', '40', '2', '15', 'και ην εκει εως της τελευτης ηρωδου ινα πληρωθη το ρηθεν υπο του κυριου δια του προφητου λεγοντος εξ αιγυπτου εκαλεσα τον υιον μου');
INSERT INTO `bible_verses_tr` VALUES ('41', '40', '2', '16', 'τοτε ηρωδης ιδων οτι ενεπαιχθη υπο των μαγων εθυμωθη λιαν και αποστειλας ανειλεν παντας τους παιδας τους εν βηθλεεμ και εν πασιν τοις οριοις αυτης απο διετους και κατωτερω κατα τον χρονον ον ηκριβωσεν παρα των μαγων');
INSERT INTO `bible_verses_tr` VALUES ('42', '40', '2', '17', 'τοτε επληρωθη το ρηθεν υπο ιερεμιου του προφητου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('43', '40', '2', '18', 'φωνη εν ραμα ηκουσθη θρηνος και κλαυθμος και οδυρμος πολυς ραχηλ κλαιουσα τα τεκνα αυτης και ουκ ηθελεν παρακληθηναι οτι ουκ εισιν');
INSERT INTO `bible_verses_tr` VALUES ('44', '40', '2', '19', 'τελευτησαντος δε του ηρωδου ιδου αγγελος κυριου κατ οναρ φαινεται τω ιωσηφ εν αιγυπτω');
INSERT INTO `bible_verses_tr` VALUES ('45', '40', '2', '20', 'λεγων εγερθεις παραλαβε το παιδιον και την μητερα αυτου και πορευου εις γην ισραηλ τεθνηκασιν γαρ οι ζητουντες την ψυχην του παιδιου');
INSERT INTO `bible_verses_tr` VALUES ('46', '40', '2', '21', 'ο δε εγερθεις παρελαβεν το παιδιον και την μητερα αυτου και ηλθεν εις γην ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('47', '40', '2', '22', 'ακουσας δε οτι αρχελαος βασιλευει επι της ιουδαιας αντι ηρωδου του πατρος αυτου εφοβηθη εκει απελθειν χρηματισθεις δε κατ οναρ ανεχωρησεν εις τα μερη της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('48', '40', '2', '23', 'και ελθων κατωκησεν εις πολιν λεγομενην  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } οπως πληρωθη το ρηθεν δια των προφητων οτι ναζωραιος κληθησεται');
INSERT INTO `bible_verses_tr` VALUES ('49', '40', '3', '1', 'εν δε ταις ημεραις εκειναις παραγινεται ιωαννης ο βαπτιστης κηρυσσων εν τη ερημω της ιουδαιας');
INSERT INTO `bible_verses_tr` VALUES ('50', '40', '3', '2', 'και λεγων μετανοειτε ηγγικεν γαρ η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('51', '40', '3', '3', 'ουτος γαρ εστιν ο ρηθεις υπο ησαιου του προφητου λεγοντος φωνη βοωντος εν τη ερημω ετοιμασατε την οδον κυριου ευθειας ποιειτε τας τριβους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('52', '40', '3', '4', 'αυτος δε ο ιωαννης ειχεν το ενδυμα αυτου απο τριχων καμηλου και ζωνην δερματινην περι την οσφυν αυτου η δε τροφη αυτου ην ακριδες και μελι αγριον');
INSERT INTO `bible_verses_tr` VALUES ('53', '40', '3', '5', 'τοτε εξεπορευετο προς αυτον ιεροσολυμα και πασα η ιουδαια και πασα η περιχωρος του ιορδανου');
INSERT INTO `bible_verses_tr` VALUES ('54', '40', '3', '6', 'και εβαπτιζοντο εν τω ιορδανη υπ αυτου εξομολογουμενοι τας αμαρτιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('55', '40', '3', '7', 'ιδων δε πολλους των φαρισαιων και σαδδουκαιων ερχομενους επι το βαπτισμα αυτου ειπεν αυτοις γεννηματα εχιδνων τις υπεδειξεν υμιν φυγειν απο της μελλουσης οργης');
INSERT INTO `bible_verses_tr` VALUES ('56', '40', '3', '8', 'ποιησατε ουν καρπους αξιους της μετανοιας');
INSERT INTO `bible_verses_tr` VALUES ('57', '40', '3', '9', 'και μη δοξητε λεγειν εν εαυτοις πατερα εχομεν τον αβρααμ λεγω γαρ υμιν οτι δυναται ο θεος εκ των λιθων τουτων εγειραι τεκνα τω αβρααμ');
INSERT INTO `bible_verses_tr` VALUES ('58', '40', '3', '10', 'ηδη δε και η αξινη προς την ριζαν των δενδρων κειται παν ουν δενδρον μη ποιουν καρπον καλον εκκοπτεται και εις πυρ βαλλεται');
INSERT INTO `bible_verses_tr` VALUES ('59', '40', '3', '11', 'εγω μεν βαπτιζω υμας εν υδατι εις μετανοιαν ο δε οπισω μου ερχομενος ισχυροτερος μου εστιν ου ουκ ειμι ικανος τα υποδηματα βαστασαι αυτος υμας βαπτισει εν πνευματι αγιω και πυρι');
INSERT INTO `bible_verses_tr` VALUES ('60', '40', '3', '12', 'ου το πτυον εν τη χειρι αυτου και διακαθαριει την αλωνα αυτου και συναξει τον σιτον αυτου εις την αποθηκην το δε αχυρον κατακαυσει πυρι ασβεστω');
INSERT INTO `bible_verses_tr` VALUES ('61', '40', '3', '13', 'τοτε παραγινεται ο ιησους απο της γαλιλαιας επι τον ιορδανην προς τον ιωαννην του βαπτισθηναι υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('62', '40', '3', '14', 'ο δε ιωαννης διεκωλυεν αυτον λεγων εγω χρειαν εχω υπο σου βαπτισθηναι και συ ερχη προς με');
INSERT INTO `bible_verses_tr` VALUES ('63', '40', '3', '15', 'αποκριθεις δε ο ιησους ειπεν προς αυτον αφες αρτι ουτως γαρ πρεπον εστιν ημιν πληρωσαι πασαν δικαιοσυνην τοτε αφιησιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('64', '40', '3', '16', 'και βαπτισθεις ο ιησους ανεβη ευθυς απο του υδατος και ιδου ανεωχθησαν αυτω οι ουρανοι και ειδεν το πνευμα του θεου καταβαινον ωσει περιστεραν και ερχομενον επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('65', '40', '3', '17', 'και ιδου φωνη εκ των ουρανων λεγουσα ουτος εστιν ο υιος μου ο αγαπητος εν ω ευδοκησα');
INSERT INTO `bible_verses_tr` VALUES ('66', '40', '4', '1', 'τοτε ο ιησους ανηχθη εις την ερημον υπο του πνευματος πειρασθηναι υπο του διαβολου');
INSERT INTO `bible_verses_tr` VALUES ('67', '40', '4', '2', 'και νηστευσας ημερας τεσσαρακοντα και νυκτας τεσσαρακοντα υστερον επεινασεν');
INSERT INTO `bible_verses_tr` VALUES ('68', '40', '4', '3', 'και προσελθων αυτω ο πειραζων ειπεν ει υιος ει του θεου ειπε ινα οι λιθοι ουτοι αρτοι γενωνται');
INSERT INTO `bible_verses_tr` VALUES ('69', '40', '4', '4', 'ο δε αποκριθεις ειπεν γεγραπται ουκ επ αρτω μονω ζησεται ανθρωπος αλλ επι παντι ρηματι εκπορευομενω δια στοματος θεου');
INSERT INTO `bible_verses_tr` VALUES ('70', '40', '4', '5', 'τοτε παραλαμβανει αυτον ο διαβολος εις την αγιαν πολιν και ιστησιν αυτον επι το πτερυγιον του ιερου');
INSERT INTO `bible_verses_tr` VALUES ('71', '40', '4', '6', 'και λεγει αυτω ει υιος ει του θεου βαλε σεαυτον κατω γεγραπται γαρ οτι τοις αγγελοις αυτου εντελειται περι σου και επι χειρων αρουσιν σε μηποτε προσκοψης προς λιθον τον ποδα σου');
INSERT INTO `bible_verses_tr` VALUES ('72', '40', '4', '7', 'εφη αυτω ο ιησους παλιν γεγραπται ουκ εκπειρασεις κυριον τον θεον σου');
INSERT INTO `bible_verses_tr` VALUES ('73', '40', '4', '8', 'παλιν παραλαμβανει αυτον ο διαβολος εις ορος υψηλον λιαν και δεικνυσιν αυτω πασας τας βασιλειας του κοσμου και την δοξαν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('74', '40', '4', '9', 'και λεγει αυτω ταυτα παντα σοι δωσω εαν πεσων προσκυνησης μοι');
INSERT INTO `bible_verses_tr` VALUES ('75', '40', '4', '10', 'τοτε λεγει αυτω ο ιησους υπαγε σατανα γεγραπται γαρ κυριον τον θεον σου προσκυνησεις και αυτω μονω λατρευσεις');
INSERT INTO `bible_verses_tr` VALUES ('76', '40', '4', '11', 'τοτε αφιησιν αυτον ο διαβολος και ιδου αγγελοι προσηλθον και διηκονουν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('77', '40', '4', '12', 'ακουσας δε ο ιησους οτι ιωαννης παρεδοθη ανεχωρησεν εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('78', '40', '4', '13', 'και καταλιπων την  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } ελθων κατωκησεν εις καπερναουμ την παραθαλασσιαν εν οριοις ζαβουλων και νεφθαλειμ');
INSERT INTO `bible_verses_tr` VALUES ('79', '40', '4', '14', 'ινα πληρωθη το ρηθεν δια ησαιου του προφητου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('80', '40', '4', '15', 'γη ζαβουλων και γη νεφθαλειμ οδον θαλασσης περαν του ιορδανου γαλιλαια των εθνων');
INSERT INTO `bible_verses_tr` VALUES ('81', '40', '4', '16', 'ο λαος ο καθημενος εν σκοτει ειδε φως μεγα και τοις καθημενοις εν χωρα και σκια θανατου φως ανετειλεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('82', '40', '4', '17', 'απο τοτε ηρξατο ο ιησους κηρυσσειν και λεγειν μετανοειτε ηγγικεν γαρ η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('83', '40', '4', '18', 'περιπατων δε ο ιησους παρα την θαλασσαν της γαλιλαιας ειδεν δυο αδελφους σιμωνα τον λεγομενον πετρον και ανδρεαν τον αδελφον αυτου βαλλοντας αμφιβληστρον εις την θαλασσαν ησαν γαρ αλιεις');
INSERT INTO `bible_verses_tr` VALUES ('84', '40', '4', '19', 'και λεγει αυτοις δευτε οπισω μου και ποιησω υμας αλιεις ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('85', '40', '4', '20', 'οι δε ευθεως αφεντες τα δικτυα ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('86', '40', '4', '21', 'και προβας εκειθεν ειδεν αλλους δυο αδελφους ιακωβον τον του ζεβεδαιου και ιωαννην τον αδελφον αυτου εν τω πλοιω μετα ζεβεδαιου του πατρος αυτων καταρτιζοντας τα δικτυα αυτων και εκαλεσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('87', '40', '4', '22', 'οι δε ευθεως αφεντες το πλοιον και τον πατερα αυτων ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('88', '40', '4', '23', 'και περιηγεν ολην την γαλιλαιαν ο ιησους διδασκων εν ταις συναγωγαις αυτων και κηρυσσων το ευαγγελιον της βασιλειας και θεραπευων πασαν νοσον και πασαν μαλακιαν εν τω λαω');
INSERT INTO `bible_verses_tr` VALUES ('89', '40', '4', '24', 'και απηλθεν η ακοη αυτου εις ολην την συριαν και προσηνεγκαν αυτω παντας τους κακως εχοντας ποικιλαις νοσοις και βασανοις συνεχομενους και δαιμονιζομενους και σεληνιαζομενους και παραλυτικους και εθεραπευσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('90', '40', '4', '25', 'και ηκολουθησαν αυτω οχλοι πολλοι απο της γαλιλαιας και δεκαπολεως και ιεροσολυμων και ιουδαιας και περαν του ιορδανου');
INSERT INTO `bible_verses_tr` VALUES ('91', '40', '5', '1', 'ιδων δε τους οχλους ανεβη εις το ορος και καθισαντος αυτου προσηλθον αυτω οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('92', '40', '5', '2', 'και ανοιξας το στομα αυτου εδιδασκεν αυτους λεγων');
INSERT INTO `bible_verses_tr` VALUES ('93', '40', '5', '3', 'μακαριοι οι πτωχοι τω πνευματι οτι αυτων εστιν η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('94', '40', '5', '4', 'μακαριοι οι πενθουντες οτι αυτοι παρακληθησονται');
INSERT INTO `bible_verses_tr` VALUES ('95', '40', '5', '5', 'μακαριοι οι πραεις οτι αυτοι κληρονομησουσιν την γην');
INSERT INTO `bible_verses_tr` VALUES ('96', '40', '5', '6', 'μακαριοι οι πεινωντες και διψωντες την δικαιοσυνην οτι αυτοι χορτασθησονται');
INSERT INTO `bible_verses_tr` VALUES ('97', '40', '5', '7', 'μακαριοι οι ελεημονες οτι αυτοι ελεηθησονται');
INSERT INTO `bible_verses_tr` VALUES ('98', '40', '5', '8', 'μακαριοι οι καθαροι τη καρδια οτι αυτοι τον θεον οψονται');
INSERT INTO `bible_verses_tr` VALUES ('99', '40', '5', '9', 'μακαριοι οι ειρηνοποιοι οτι αυτοι υιοι θεου κληθησονται');
INSERT INTO `bible_verses_tr` VALUES ('100', '40', '5', '10', 'μακαριοι οι δεδιωγμενοι ενεκεν δικαιοσυνης οτι αυτων εστιν η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('101', '40', '5', '11', 'μακαριοι εστε οταν ονειδισωσιν υμας και διωξωσιν και ειπωσιν παν πονηρον ρημα καθ υμων ψευδομενοι ενεκεν εμου');
INSERT INTO `bible_verses_tr` VALUES ('102', '40', '5', '12', 'χαιρετε και αγαλλιασθε οτι ο μισθος υμων πολυς εν τοις ουρανοις ουτως γαρ εδιωξαν τους προφητας τους προ υμων');
INSERT INTO `bible_verses_tr` VALUES ('103', '40', '5', '13', 'υμεις εστε το αλας της γης εαν δε το αλας μωρανθη εν τινι αλισθησεται εις ουδεν ισχυει ετι ει μη βληθηναι εξω και καταπατεισθαι υπο των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('104', '40', '5', '14', 'υμεις εστε το φως του κοσμου ου δυναται πολις κρυβηναι επανω ορους κειμενη');
INSERT INTO `bible_verses_tr` VALUES ('105', '40', '5', '15', 'ουδε καιουσιν λυχνον και τιθεασιν αυτον υπο τον μοδιον αλλ επι την λυχνιαν και λαμπει πασιν τοις εν τη οικια');
INSERT INTO `bible_verses_tr` VALUES ('106', '40', '5', '16', 'ουτως λαμψατω το φως υμων εμπροσθεν των ανθρωπων οπως ιδωσιν υμων τα καλα εργα και δοξασωσιν τον πατερα υμων τον εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('107', '40', '5', '17', 'μη νομισητε οτι ηλθον καταλυσαι τον νομον η τους προφητας ουκ ηλθον καταλυσαι αλλα πληρωσαι');
INSERT INTO `bible_verses_tr` VALUES ('108', '40', '5', '18', 'αμην γαρ λεγω υμιν εως αν παρελθη ο ουρανος και η γη ιωτα εν η μια κεραια ου μη παρελθη απο του νομου εως αν παντα γενηται');
INSERT INTO `bible_verses_tr` VALUES ('109', '40', '5', '19', 'ος εαν ουν λυση μιαν των εντολων τουτων των ελαχιστων και διδαξη ουτως τους ανθρωπους ελαχιστος κληθησεται εν τη βασιλεια των ουρανων ος δ αν ποιηση και διδαξη ουτος μεγας κληθησεται εν τη βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('110', '40', '5', '20', 'λεγω γαρ υμιν οτι εαν μη περισσευση η δικαιοσυνη υμων πλειον των γραμματεων και φαρισαιων ου μη εισελθητε εις την βασιλειαν των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('111', '40', '5', '21', 'ηκουσατε οτι ερρεθη τοις αρχαιοις ου φονευσεις ος δ αν φονευση ενοχος εσται τη κρισει');
INSERT INTO `bible_verses_tr` VALUES ('112', '40', '5', '22', 'εγω δε λεγω υμιν οτι πας ο οργιζομενος τω αδελφω αυτου εικη ενοχος εσται τη κρισει ος δ αν ειπη τω αδελφω αυτου ρακα ενοχος εσται τω συνεδριω ος δ αν ειπη μωρε ενοχος εσται εις την γεενναν του πυρος');
INSERT INTO `bible_verses_tr` VALUES ('113', '40', '5', '23', 'εαν ουν προσφερης το δωρον σου επι το θυσιαστηριον κακει μνησθης οτι ο αδελφος σου εχει τι κατα σου');
INSERT INTO `bible_verses_tr` VALUES ('114', '40', '5', '24', 'αφες εκει το δωρον σου εμπροσθεν του θυσιαστηριου και υπαγε πρωτον διαλλαγηθι τω αδελφω σου και τοτε ελθων προσφερε το δωρον σου');
INSERT INTO `bible_verses_tr` VALUES ('115', '40', '5', '25', 'ισθι ευνοων τω αντιδικω σου ταχυ εως οτου ει εν τη οδω μετ αυτου μηποτε σε παραδω ο αντιδικος τω κριτη και ο κριτης σε παραδω τω υπηρετη και εις φυλακην βληθηση');
INSERT INTO `bible_verses_tr` VALUES ('116', '40', '5', '26', 'αμην λεγω σοι ου μη εξελθης εκειθεν εως αν αποδως τον εσχατον κοδραντην');
INSERT INTO `bible_verses_tr` VALUES ('117', '40', '5', '27', 'ηκουσατε οτι ερρεθη τοις αρχαιοις ου μοιχευσεις');
INSERT INTO `bible_verses_tr` VALUES ('118', '40', '5', '28', 'εγω δε λεγω υμιν οτι πας ο βλεπων γυναικα προς το επιθυμησαι αυτης ηδη εμοιχευσεν αυτην εν τη καρδια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('119', '40', '5', '29', 'ει δε ο οφθαλμος σου ο δεξιος σκανδαλιζει σε εξελε αυτον και βαλε απο σου συμφερει γαρ σοι ινα αποληται εν των μελων σου και μη ολον το σωμα σου βληθη εις γεενναν');
INSERT INTO `bible_verses_tr` VALUES ('120', '40', '5', '30', 'και ει η δεξια σου χειρ σκανδαλιζει σε εκκοψον αυτην και βαλε απο σου συμφερει γαρ σοι ινα αποληται εν των μελων σου και μη ολον το σωμα σου βληθη εις γεενναν');
INSERT INTO `bible_verses_tr` VALUES ('121', '40', '5', '31', 'ερρεθη δε οτι ος αν απολυση την γυναικα αυτου δοτω αυτη αποστασιον');
INSERT INTO `bible_verses_tr` VALUES ('122', '40', '5', '32', 'εγω δε λεγω υμιν οτι ος αν απολυση την γυναικα αυτου παρεκτος λογου πορνειας ποιει αυτην μοιχασθαι και ος εαν απολελυμενην γαμηση μοιχαται');
INSERT INTO `bible_verses_tr` VALUES ('123', '40', '5', '33', 'παλιν ηκουσατε οτι ερρεθη τοις αρχαιοις ουκ επιορκησεις αποδωσεις δε τω κυριω τους ορκους σου');
INSERT INTO `bible_verses_tr` VALUES ('124', '40', '5', '34', 'εγω δε λεγω υμιν μη ομοσαι ολως μητε εν τω ουρανω οτι θρονος εστιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('125', '40', '5', '35', 'μητε εν τη γη οτι υποποδιον εστιν των ποδων αυτου μητε εις ιεροσολυμα οτι πολις εστιν του μεγαλου βασιλεως');
INSERT INTO `bible_verses_tr` VALUES ('126', '40', '5', '36', 'μητε εν τη κεφαλη σου ομοσης οτι ου δυνασαι μιαν τριχα λευκην η μελαιναν ποιησαι');
INSERT INTO `bible_verses_tr` VALUES ('127', '40', '5', '37', 'εστω δε ο λογος υμων ναι ναι ου ου το δε περισσον τουτων εκ του πονηρου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('128', '40', '5', '38', 'ηκουσατε οτι ερρεθη οφθαλμον αντι οφθαλμου και οδοντα αντι οδοντος');
INSERT INTO `bible_verses_tr` VALUES ('129', '40', '5', '39', 'εγω δε λεγω υμιν μη αντιστηναι τω πονηρω αλλ οστις σε ραπισει επι την δεξιαν σου σιαγονα στρεψον αυτω και την αλλην');
INSERT INTO `bible_verses_tr` VALUES ('130', '40', '5', '40', 'και τω θελοντι σοι κριθηναι και τον χιτωνα σου λαβειν αφες αυτω και το ιματιον');
INSERT INTO `bible_verses_tr` VALUES ('131', '40', '5', '41', 'και οστις σε αγγαρευσει μιλιον εν υπαγε μετ αυτου δυο');
INSERT INTO `bible_verses_tr` VALUES ('132', '40', '5', '42', 'τω αιτουντι σε διδου και τον θελοντα απο σου δανεισασθαι μη αποστραφης');
INSERT INTO `bible_verses_tr` VALUES ('133', '40', '5', '43', 'ηκουσατε οτι ερρεθη αγαπησεις τον πλησιον σου και μισησεις τον εχθρον σου');
INSERT INTO `bible_verses_tr` VALUES ('134', '40', '5', '44', 'εγω δε λεγω υμιν αγαπατε τους εχθρους υμων ευλογειτε τους καταρωμενους υμας καλως ποιειτε τους μισουντας υμας και προσευχεσθε υπερ των επηρεαζοντων υμας και διωκοντων υμας');
INSERT INTO `bible_verses_tr` VALUES ('135', '40', '5', '45', 'οπως γενησθε υιοι του πατρος υμων του εν ουρανοις οτι τον ηλιον αυτου ανατελλει επι πονηρους και αγαθους και βρεχει επι δικαιους και αδικους');
INSERT INTO `bible_verses_tr` VALUES ('136', '40', '5', '46', 'εαν γαρ αγαπησητε τους αγαπωντας υμας τινα μισθον εχετε ουχι και οι τελωναι το αυτο ποιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('137', '40', '5', '47', 'και εαν ασπασησθε τους αδελφους υμων μονον τι περισσον ποιειτε ουχι και οι τελωναι ουτως ποιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('138', '40', '5', '48', 'εσεσθε ουν υμεις τελειοι ωσπερ ο πατηρ υμων ο εν τοις ουρανοις τελειος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('139', '40', '6', '1', 'προσεχετε την ελεημοσυνην υμων μη ποιειν εμπροσθεν των ανθρωπων προς το θεαθηναι αυτοις ει δε μηγε μισθον ουκ εχετε παρα τω πατρι υμων τω εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('140', '40', '6', '2', 'οταν ουν ποιης ελεημοσυνην μη σαλπισης εμπροσθεν σου ωσπερ οι υποκριται ποιουσιν εν ταις συναγωγαις και εν ταις ρυμαις οπως δοξασθωσιν υπο των ανθρωπων αμην λεγω υμιν απεχουσιν τον μισθον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('141', '40', '6', '3', 'σου δε ποιουντος ελεημοσυνην μη γνωτω η αριστερα σου τι ποιει η δεξια σου');
INSERT INTO `bible_verses_tr` VALUES ('142', '40', '6', '4', 'οπως η σου η ελεημοσυνη εν τω κρυπτω και ο πατηρ σου ο βλεπων εν τω κρυπτω αυτος αποδωσει σοι εν τω φανερω');
INSERT INTO `bible_verses_tr` VALUES ('143', '40', '6', '5', 'και οταν προσευχη ουκ εση ωσπερ οι υποκριται οτι φιλουσιν εν ταις συναγωγαις και εν ταις γωνιαις των πλατειων εστωτες προσευχεσθαι οπως αν φανωσιν τοις ανθρωποις αμην λεγω υμιν οτι απεχουσιν τον μισθον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('144', '40', '6', '6', 'συ δε οταν προσευχη εισελθε εις το ταμιειον σου και κλεισας την θυραν σου προσευξαι τω πατρι σου τω εν τω κρυπτω και ο πατηρ σου ο βλεπων εν τω κρυπτω αποδωσει σοι εν τω φανερω');
INSERT INTO `bible_verses_tr` VALUES ('145', '40', '6', '7', 'προσευχομενοι δε μη βαττολογησητε ωσπερ οι εθνικοι δοκουσιν γαρ οτι εν τη πολυλογια αυτων εισακουσθησονται');
INSERT INTO `bible_verses_tr` VALUES ('146', '40', '6', '8', 'μη ουν ομοιωθητε αυτοις οιδεν γαρ ο πατηρ υμων ων χρειαν εχετε προ του υμας αιτησαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('147', '40', '6', '9', 'ουτως ουν προσευχεσθε υμεις πατερ ημων ο εν τοις ουρανοις αγιασθητω το ονομα σου');
INSERT INTO `bible_verses_tr` VALUES ('148', '40', '6', '10', 'ελθετω η βασιλεια σου γενηθητω το θελημα σου ως εν ουρανω και επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('149', '40', '6', '11', 'τον αρτον ημων τον επιουσιον δος ημιν σημερον');
INSERT INTO `bible_verses_tr` VALUES ('150', '40', '6', '12', 'και αφες ημιν τα οφειληματα ημων ως και ημεις αφιεμεν τοις οφειλεταις ημων');
INSERT INTO `bible_verses_tr` VALUES ('151', '40', '6', '13', 'και μη εισενεγκης ημας εις πειρασμον αλλα ρυσαι ημας απο του πονηρου οτι σου εστιν η βασιλεια και η δυναμις και η δοξα εις τους αιωνας αμην');
INSERT INTO `bible_verses_tr` VALUES ('152', '40', '6', '14', 'εαν γαρ αφητε τοις ανθρωποις τα παραπτωματα αυτων αφησει και υμιν ο πατηρ υμων ο ουρανιος');
INSERT INTO `bible_verses_tr` VALUES ('153', '40', '6', '15', 'εαν δε μη αφητε τοις ανθρωποις τα παραπτωματα αυτων ουδε ο πατηρ υμων αφησει τα παραπτωματα υμων');
INSERT INTO `bible_verses_tr` VALUES ('154', '40', '6', '16', 'οταν δε νηστευητε μη γινεσθε ωσπερ οι υποκριται σκυθρωποι αφανιζουσιν γαρ τα προσωπα αυτων οπως φανωσιν τοις ανθρωποις νηστευοντες αμην λεγω υμιν οτι απεχουσιν τον μισθον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('155', '40', '6', '17', 'συ δε νηστευων αλειψαι σου την κεφαλην και το προσωπον σου νιψαι');
INSERT INTO `bible_verses_tr` VALUES ('156', '40', '6', '18', 'οπως μη φανης τοις ανθρωποις νηστευων αλλα τω πατρι σου τω εν τω κρυπτω και ο πατηρ σου ο βλεπων εν τω κρυπτω αποδωσει σοι εν τω φανερω');
INSERT INTO `bible_verses_tr` VALUES ('157', '40', '6', '19', 'μη θησαυριζετε υμιν θησαυρους επι της γης οπου σης και βρωσις αφανιζει και οπου κλεπται διορυσσουσιν και κλεπτουσιν');
INSERT INTO `bible_verses_tr` VALUES ('158', '40', '6', '20', 'θησαυριζετε δε υμιν θησαυρους εν ουρανω οπου ουτε σης ουτε βρωσις αφανιζει και οπου κλεπται ου διορυσσουσιν ουδε κλεπτουσιν');
INSERT INTO `bible_verses_tr` VALUES ('159', '40', '6', '21', 'οπου γαρ εστιν ο θησαυρος υμων εκει εσται και η καρδια υμων');
INSERT INTO `bible_verses_tr` VALUES ('160', '40', '6', '22', 'ο λυχνος του σωματος εστιν ο οφθαλμος εαν ουν ο οφθαλμος σου απλους η ολον το σωμα σου φωτεινον εσται');
INSERT INTO `bible_verses_tr` VALUES ('161', '40', '6', '23', 'εαν δε ο οφθαλμος σου πονηρος η ολον το σωμα σου σκοτεινον εσται ει ουν το φως το εν σοι σκοτος εστιν το σκοτος ποσον');
INSERT INTO `bible_verses_tr` VALUES ('162', '40', '6', '24', 'ουδεις δυναται δυσι κυριοις δουλευειν η γαρ τον ενα μισησει και τον ετερον αγαπησει η ενος ανθεξεται και του ετερου καταφρονησει ου δυνασθε θεω δουλευειν και μαμμωνα');
INSERT INTO `bible_verses_tr` VALUES ('163', '40', '6', '25', 'δια τουτο λεγω υμιν μη μεριμνατε τη ψυχη υμων τι φαγητε και τι πιητε μηδε τω σωματι υμων τι ενδυσησθε ουχι η ψυχη πλειον εστιν της τροφης και το σωμα του ενδυματος');
INSERT INTO `bible_verses_tr` VALUES ('164', '40', '6', '26', 'εμβλεψατε εις τα πετεινα του ουρανου οτι ου σπειρουσιν ουδε θεριζουσιν ουδε συναγουσιν εις αποθηκας και ο πατηρ υμων ο ουρανιος τρεφει αυτα ουχ υμεις μαλλον διαφερετε αυτων');
INSERT INTO `bible_verses_tr` VALUES ('165', '40', '6', '27', 'τις δε εξ υμων μεριμνων δυναται προσθειναι επι την ηλικιαν αυτου πηχυν ενα');
INSERT INTO `bible_verses_tr` VALUES ('166', '40', '6', '28', 'και περι ενδυματος τι μεριμνατε καταμαθετε τα κρινα του αγρου πως αυξανει ου κοπια ουδε νηθει');
INSERT INTO `bible_verses_tr` VALUES ('167', '40', '6', '29', 'λεγω δε υμιν οτι ουδε σολομων εν παση τη δοξη αυτου περιεβαλετο ως εν τουτων');
INSERT INTO `bible_verses_tr` VALUES ('168', '40', '6', '30', 'ει δε τον χορτον του αγρου σημερον οντα και αυριον εις κλιβανον βαλλομενον ο θεος ουτως αμφιεννυσιν ου πολλω μαλλον υμας ολιγοπιστοι');
INSERT INTO `bible_verses_tr` VALUES ('169', '40', '6', '31', 'μη ουν μεριμνησητε λεγοντες τι φαγωμεν η τι πιωμεν η τι περιβαλωμεθα');
INSERT INTO `bible_verses_tr` VALUES ('170', '40', '6', '32', 'παντα γαρ ταυτα τα εθνη επιζητει οιδεν γαρ ο πατηρ υμων ο ουρανιος οτι χρηζετε τουτων απαντων');
INSERT INTO `bible_verses_tr` VALUES ('171', '40', '6', '33', 'ζητειτε δε πρωτον την βασιλειαν του θεου και την δικαιοσυνην αυτου και ταυτα παντα προστεθησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('172', '40', '6', '34', 'μη ουν μεριμνησητε εις την αυριον η γαρ αυριον μεριμνησει τα εαυτης αρκετον τη ημερα η κακια αυτης');
INSERT INTO `bible_verses_tr` VALUES ('173', '40', '7', '1', 'μη κρινετε ινα μη κριθητε');
INSERT INTO `bible_verses_tr` VALUES ('174', '40', '7', '2', 'εν ω γαρ κριματι κρινετε κριθησεσθε και εν ω μετρω μετρειτε αντιμετρηθησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('175', '40', '7', '3', 'τι δε βλεπεις το καρφος το εν τω οφθαλμω του αδελφου σου την δε εν τω σω οφθαλμω δοκον ου κατανοεις');
INSERT INTO `bible_verses_tr` VALUES ('176', '40', '7', '4', 'η πως ερεις τω αδελφω σου αφες εκβαλω το καρφος απο του οφθαλμου σου και ιδου η δοκος εν τω οφθαλμω σου');
INSERT INTO `bible_verses_tr` VALUES ('177', '40', '7', '5', 'υποκριτα εκβαλε πρωτον την δοκον εκ του οφθαλμου σου και τοτε διαβλεψεις εκβαλειν το καρφος εκ του οφθαλμου του αδελφου σου');
INSERT INTO `bible_verses_tr` VALUES ('178', '40', '7', '6', 'μη δωτε το αγιον τοις κυσιν μηδε βαλητε τους μαργαριτας υμων εμπροσθεν των χοιρων μηποτε καταπατησωσιν αυτους εν τοις ποσιν αυτων και στραφεντες ρηξωσιν υμας');
INSERT INTO `bible_verses_tr` VALUES ('179', '40', '7', '7', 'αιτειτε και δοθησεται υμιν ζητειτε και ευρησετε κρουετε και ανοιγησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('180', '40', '7', '8', 'πας γαρ ο αιτων λαμβανει και ο ζητων ευρισκει και τω κρουοντι ανοιγησεται');
INSERT INTO `bible_verses_tr` VALUES ('181', '40', '7', '9', 'η τις εστιν εξ υμων ανθρωπος ον εαν αιτηση ο υιος αυτου αρτον μη λιθον επιδωσει αυτω');
INSERT INTO `bible_verses_tr` VALUES ('182', '40', '7', '10', 'και εαν ιχθυν αιτηση μη οφιν επιδωσει αυτω');
INSERT INTO `bible_verses_tr` VALUES ('183', '40', '7', '11', 'ει ουν υμεις πονηροι οντες οιδατε δοματα αγαθα διδοναι τοις τεκνοις υμων ποσω μαλλον ο πατηρ υμων ο εν τοις ουρανοις δωσει αγαθα τοις αιτουσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('184', '40', '7', '12', 'παντα ουν οσα αν θελητε ινα ποιωσιν υμιν οι ανθρωποι ουτως και υμεις ποιειτε αυτοις ουτος γαρ εστιν ο νομος και οι προφηται');
INSERT INTO `bible_verses_tr` VALUES ('185', '40', '7', '13', 'εισελθετε δια της στενης πυλης οτι πλατεια η πυλη και ευρυχωρος η οδος η απαγουσα εις την απωλειαν και πολλοι εισιν οι εισερχομενοι δι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('186', '40', '7', '14', 'οτι στενη η πυλη και τεθλιμμενη η οδος η απαγουσα εις την ζωην και ολιγοι εισιν οι ευρισκοντες αυτην');
INSERT INTO `bible_verses_tr` VALUES ('187', '40', '7', '15', 'προσεχετε δε απο των ψευδοπροφητων οιτινες ερχονται προς υμας εν ενδυμασιν προβατων εσωθεν δε εισιν λυκοι αρπαγες');
INSERT INTO `bible_verses_tr` VALUES ('188', '40', '7', '16', 'απο των καρπων αυτων επιγνωσεσθε αυτους μητι συλλεγουσιν απο ακανθων σταφυλην η απο τριβολων συκα');
INSERT INTO `bible_verses_tr` VALUES ('189', '40', '7', '17', 'ουτως παν δενδρον αγαθον καρπους καλους ποιει το δε σαπρον δενδρον καρπους πονηρους ποιει');
INSERT INTO `bible_verses_tr` VALUES ('190', '40', '7', '18', 'ου δυναται δενδρον αγαθον καρπους πονηρους ποιειν ουδε δενδρον σαπρον καρπους καλους ποιειν');
INSERT INTO `bible_verses_tr` VALUES ('191', '40', '7', '19', 'παν δενδρον μη ποιουν καρπον καλον εκκοπτεται και εις πυρ βαλλεται');
INSERT INTO `bible_verses_tr` VALUES ('192', '40', '7', '20', 'αραγε απο των καρπων αυτων επιγνωσεσθε αυτους');
INSERT INTO `bible_verses_tr` VALUES ('193', '40', '7', '21', 'ου πας ο λεγων μοι κυριε κυριε εισελευσεται εις την βασιλειαν των ουρανων αλλ ο ποιων το θελημα του πατρος μου του εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('194', '40', '7', '22', 'πολλοι ερουσιν μοι εν εκεινη τη ημερα κυριε κυριε ου τω σω ονοματι προεφητευσαμεν και τω σω ονοματι δαιμονια εξεβαλομεν και τω σω ονοματι δυναμεις πολλας εποιησαμεν');
INSERT INTO `bible_verses_tr` VALUES ('195', '40', '7', '23', 'και τοτε ομολογησω αυτοις οτι ουδεποτε εγνων υμας αποχωρειτε απ εμου οι εργαζομενοι την ανομιαν');
INSERT INTO `bible_verses_tr` VALUES ('196', '40', '7', '24', 'πας ουν οστις ακουει μου τους λογους τουτους και ποιει αυτους ομοιωσω αυτον ανδρι φρονιμω οστις ωκοδομησεν την οικιαν αυτου επι την πετραν');
INSERT INTO `bible_verses_tr` VALUES ('197', '40', '7', '25', 'και κατεβη η βροχη και ηλθον οι ποταμοι και επνευσαν οι ανεμοι και προσεπεσον τη οικια εκεινη και ουκ επεσεν τεθεμελιωτο γαρ επι την πετραν');
INSERT INTO `bible_verses_tr` VALUES ('198', '40', '7', '26', 'και πας ο ακουων μου τους λογους τουτους και μη ποιων αυτους ομοιωθησεται ανδρι μωρω οστις ωκοδομησεν την οικιαν αυτου επι την αμμον');
INSERT INTO `bible_verses_tr` VALUES ('199', '40', '7', '27', 'και κατεβη η βροχη και ηλθον οι ποταμοι και επνευσαν οι ανεμοι και προσεκοψαν τη οικια εκεινη και επεσεν και ην η πτωσις αυτης μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('200', '40', '7', '28', 'και εγενετο οτε συνετελεσεν ο ιησους τους λογους τουτους εξεπλησσοντο οι οχλοι επι τη διδαχη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('201', '40', '7', '29', 'ην γαρ διδασκων αυτους ως εξουσιαν εχων και ουχ ως οι γραμματεις');
INSERT INTO `bible_verses_tr` VALUES ('202', '40', '8', '1', 'καταβαντι δε αυτω απο του ορους ηκολουθησαν αυτω οχλοι πολλοι');
INSERT INTO `bible_verses_tr` VALUES ('203', '40', '8', '2', 'και ιδου λεπρος ελθων προσεκυνει αυτω λεγων κυριε εαν θελης δυνασαι με καθαρισαι');
INSERT INTO `bible_verses_tr` VALUES ('204', '40', '8', '3', 'και εκτεινας την χειρα ηψατο αυτου ο ιησους λεγων θελω καθαρισθητι και ευθεως εκαθαρισθη αυτου η λεπρα');
INSERT INTO `bible_verses_tr` VALUES ('205', '40', '8', '4', 'και λεγει αυτω ο ιησους ορα μηδενι ειπης αλλ υπαγε σεαυτον δειξον τω ιερει και προσενεγκε το δωρον ο προσεταξεν μωσης εις μαρτυριον αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('206', '40', '8', '5', 'εισελθοντι δε τω ιησου εις καπερναουμ προσηλθεν αυτω εκατονταρχος παρακαλων αυτον');
INSERT INTO `bible_verses_tr` VALUES ('207', '40', '8', '6', 'και λεγων κυριε ο παις μου βεβληται εν τη οικια παραλυτικος δεινως βασανιζομενος');
INSERT INTO `bible_verses_tr` VALUES ('208', '40', '8', '7', 'και λεγει αυτω ο ιησους εγω ελθων θεραπευσω αυτον');
INSERT INTO `bible_verses_tr` VALUES ('209', '40', '8', '8', 'και αποκριθεις ο εκατονταρχος εφη κυριε ουκ ειμι ικανος ινα μου υπο την στεγην εισελθης αλλα μονον ειπε λογον και ιαθησεται ο παις μου');
INSERT INTO `bible_verses_tr` VALUES ('210', '40', '8', '9', 'και γαρ εγω ανθρωπος ειμι υπο εξουσιαν εχων υπ εμαυτον στρατιωτας και λεγω τουτω πορευθητι και πορευεται και αλλω ερχου και ερχεται και τω δουλω μου ποιησον τουτο και ποιει');
INSERT INTO `bible_verses_tr` VALUES ('211', '40', '8', '10', 'ακουσας δε ο ιησους εθαυμασεν και ειπεν τοις ακολουθουσιν αμην λεγω υμιν ουδε εν τω ισραηλ τοσαυτην πιστιν ευρον');
INSERT INTO `bible_verses_tr` VALUES ('212', '40', '8', '11', 'λεγω δε υμιν οτι πολλοι απο ανατολων και δυσμων ηξουσιν και ανακλιθησονται μετα αβρααμ και ισαακ και ιακωβ εν τη βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('213', '40', '8', '12', 'οι δε υιοι της βασιλειας εκβληθησονται εις το σκοτος το εξωτερον εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('214', '40', '8', '13', 'και ειπεν ο ιησους τω εκατονταρχω υπαγε και ως επιστευσας γενηθητω σοι και ιαθη ο παις αυτου εν τη ωρα εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('215', '40', '8', '14', 'και ελθων ο ιησους εις την οικιαν πετρου ειδεν την πενθεραν αυτου βεβλημενην και πυρεσσουσαν');
INSERT INTO `bible_verses_tr` VALUES ('216', '40', '8', '15', 'και ηψατο της χειρος αυτης και αφηκεν αυτην ο πυρετος και ηγερθη και διηκονει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('217', '40', '8', '16', 'οψιας δε γενομενης προσηνεγκαν αυτω δαιμονιζομενους πολλους και εξεβαλεν τα πνευματα λογω και παντας τους κακως εχοντας εθεραπευσεν');
INSERT INTO `bible_verses_tr` VALUES ('218', '40', '8', '17', 'οπως πληρωθη το ρηθεν δια ησαιου του προφητου λεγοντος αυτος τας ασθενειας ημων ελαβεν και τας νοσους εβαστασεν');
INSERT INTO `bible_verses_tr` VALUES ('219', '40', '8', '18', 'ιδων δε ο ιησους πολλους οχλους περι αυτον εκελευσεν απελθειν εις το περαν');
INSERT INTO `bible_verses_tr` VALUES ('220', '40', '8', '19', 'και προσελθων εις γραμματευς ειπεν αυτω διδασκαλε ακολουθησω σοι οπου εαν απερχη');
INSERT INTO `bible_verses_tr` VALUES ('221', '40', '8', '20', 'και λεγει αυτω ο ιησους αι αλωπεκες φωλεους εχουσιν και τα πετεινα του ουρανου κατασκηνωσεις ο δε υιος του ανθρωπου ουκ εχει που την κεφαλην κλινη');
INSERT INTO `bible_verses_tr` VALUES ('222', '40', '8', '21', 'ετερος δε των μαθητων αυτου ειπεν αυτω κυριε επιτρεψον μοι πρωτον απελθειν και θαψαι τον πατερα μου');
INSERT INTO `bible_verses_tr` VALUES ('223', '40', '8', '22', 'ο δε ιησους ειπεν αυτω ακολουθει μοι και αφες τους νεκρους θαψαι τους εαυτων νεκρους');
INSERT INTO `bible_verses_tr` VALUES ('224', '40', '8', '23', 'και εμβαντι αυτω εις το πλοιον ηκολουθησαν αυτω οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('225', '40', '8', '24', 'και ιδου σεισμος μεγας εγενετο εν τη θαλασση ωστε το πλοιον καλυπτεσθαι υπο των κυματων αυτος δε εκαθευδεν');
INSERT INTO `bible_verses_tr` VALUES ('226', '40', '8', '25', 'και προσελθοντες οι μαθηται αυτου ηγειραν αυτον λεγοντες κυριε σωσον ημας απολλυμεθα');
INSERT INTO `bible_verses_tr` VALUES ('227', '40', '8', '26', 'και λεγει αυτοις τι δειλοι εστε ολιγοπιστοι τοτε εγερθεις επετιμησεν τοις ανεμοις και τη θαλασση και εγενετο γαληνη μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('228', '40', '8', '27', 'οι δε ανθρωποι εθαυμασαν λεγοντες ποταπος εστιν ουτος οτι και οι ανεμοι και η θαλασσα υπακουουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('229', '40', '8', '28', 'και ελθοντι αυτω εις το περαν εις την χωραν των γεργεσηνων υπηντησαν αυτω δυο δαιμονιζομενοι εκ των μνημειων εξερχομενοι χαλεποι λιαν ωστε μη ισχυειν τινα παρελθειν δια της οδου εκεινης');
INSERT INTO `bible_verses_tr` VALUES ('230', '40', '8', '29', 'και ιδου εκραξαν λεγοντες τι ημιν και σοι ιησου υιε του θεου ηλθες ωδε προ καιρου βασανισαι ημας');
INSERT INTO `bible_verses_tr` VALUES ('231', '40', '8', '30', 'ην δε μακραν απ αυτων αγελη χοιρων πολλων βοσκομενη');
INSERT INTO `bible_verses_tr` VALUES ('232', '40', '8', '31', 'οι δε δαιμονες παρεκαλουν αυτον λεγοντες ει εκβαλλεις ημας επιτρεψον ημιν απελθειν εις την αγελην των χοιρων');
INSERT INTO `bible_verses_tr` VALUES ('233', '40', '8', '32', 'και ειπεν αυτοις υπαγετε οι δε εξελθοντες απηλθον εις την αγελην των χοιρων και ιδου ωρμησεν πασα η αγελη των χοιρων κατα του κρημνου εις την θαλασσαν και απεθανον εν τοις υδασιν');
INSERT INTO `bible_verses_tr` VALUES ('234', '40', '8', '33', 'οι δε βοσκοντες εφυγον και απελθοντες εις την πολιν απηγγειλαν παντα και τα των δαιμονιζομενων');
INSERT INTO `bible_verses_tr` VALUES ('235', '40', '8', '34', 'και ιδου πασα η πολις εξηλθεν εις συναντησιν τω ιησου και ιδοντες αυτον παρεκαλεσαν οπως μεταβη απο των οριων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('236', '40', '9', '1', 'και εμβας εις το πλοιον διεπερασεν και ηλθεν εις την ιδιαν πολιν');
INSERT INTO `bible_verses_tr` VALUES ('237', '40', '9', '2', 'και ιδου προσεφερον αυτω παραλυτικον επι κλινης βεβλημενον και ιδων ο ιησους την πιστιν αυτων ειπεν τω παραλυτικω θαρσει τεκνον αφεωνται σοι αι αμαρτιαι σου');
INSERT INTO `bible_verses_tr` VALUES ('238', '40', '9', '3', 'και ιδου τινες των γραμματεων ειπον εν εαυτοις ουτος βλασφημει');
INSERT INTO `bible_verses_tr` VALUES ('239', '40', '9', '4', 'και ιδων ο ιησους τας ενθυμησεις αυτων ειπεν ινα τι υμεις ενθυμεισθε πονηρα εν ταις καρδιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('240', '40', '9', '5', 'τι γαρ εστιν ευκοπωτερον ειπειν αφεωνται σοι αι αμαρτιαι η ειπειν εγειραι και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('241', '40', '9', '6', 'ινα δε ειδητε οτι εξουσιαν εχει ο υιος του ανθρωπου επι της γης αφιεναι αμαρτιας τοτε λεγει τω παραλυτικω εγερθεις αρον σου την κλινην και υπαγε εις τον οικον σου');
INSERT INTO `bible_verses_tr` VALUES ('242', '40', '9', '7', 'και εγερθεις απηλθεν εις τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('243', '40', '9', '8', 'ιδοντες δε οι οχλοι εθαυμασαν και εδοξασαν τον θεον τον δοντα εξουσιαν τοιαυτην τοις ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('244', '40', '9', '9', 'και παραγων ο ιησους εκειθεν ειδεν ανθρωπον καθημενον επι το τελωνιον ματθαιον λεγομενον και λεγει αυτω ακολουθει μοι και αναστας ηκολουθησεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('245', '40', '9', '10', 'και εγενετο αυτου ανακειμενου εν τη οικια και ιδου πολλοι τελωναι και αμαρτωλοι ελθοντες συνανεκειντο τω ιησου και τοις μαθηταις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('246', '40', '9', '11', 'και ιδοντες οι φαρισαιοι ειπον τοις μαθηταις αυτου δια τι μετα των τελωνων και αμαρτωλων εσθιει ο διδασκαλος υμων');
INSERT INTO `bible_verses_tr` VALUES ('247', '40', '9', '12', 'ο δε ιησους ακουσας ειπεν αυτοις ου χρειαν εχουσιν οι ισχυοντες ιατρου αλλ οι κακως εχοντες');
INSERT INTO `bible_verses_tr` VALUES ('248', '40', '9', '13', 'πορευθεντες δε μαθετε τι εστιν ελεον θελω και ου θυσιαν ου γαρ ηλθον καλεσαι δικαιους αλλ αμαρτωλους εις μετανοιαν');
INSERT INTO `bible_verses_tr` VALUES ('249', '40', '9', '14', 'τοτε προσερχονται αυτω οι μαθηται ιωαννου λεγοντες δια τι ημεις και οι φαρισαιοι νηστευομεν πολλα οι δε μαθηται σου ου νηστευουσιν');
INSERT INTO `bible_verses_tr` VALUES ('250', '40', '9', '15', 'και ειπεν αυτοις ο ιησους μη δυνανται οι υιοι του νυμφωνος πενθειν εφ οσον μετ αυτων εστιν ο νυμφιος ελευσονται δε ημεραι οταν απαρθη απ αυτων ο νυμφιος και τοτε νηστευσουσιν');
INSERT INTO `bible_verses_tr` VALUES ('251', '40', '9', '16', 'ουδεις δε επιβαλλει επιβλημα ρακους αγναφου επι ιματιω παλαιω αιρει γαρ το πληρωμα αυτου απο του ιματιου και χειρον σχισμα γινεται');
INSERT INTO `bible_verses_tr` VALUES ('252', '40', '9', '17', 'ουδε βαλλουσιν οινον νεον εις ασκους παλαιους ει δε μηγε ρηγνυνται οι ασκοι και ο οινος εκχειται και οι ασκοι απολουνται αλλα βαλλουσιν οινον νεον εις ασκους καινους και αμφοτερα συντηρουνται');
INSERT INTO `bible_verses_tr` VALUES ('253', '40', '9', '18', 'ταυτα αυτου λαλουντος αυτοις ιδου αρχων  {VAR2: εις } ελθων προσεκυνει αυτω λεγων οτι η θυγατηρ μου αρτι ετελευτησεν αλλα ελθων επιθες την χειρα σου επ αυτην και ζησεται');
INSERT INTO `bible_verses_tr` VALUES ('254', '40', '9', '19', 'και εγερθεις ο ιησους ηκολουθησεν αυτω και οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('255', '40', '9', '20', 'και ιδου γυνη αιμορροουσα δωδεκα ετη προσελθουσα οπισθεν ηψατο του κρασπεδου του ιματιου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('256', '40', '9', '21', 'ελεγεν γαρ εν εαυτη εαν μονον αψωμαι του ιματιου αυτου σωθησομαι');
INSERT INTO `bible_verses_tr` VALUES ('257', '40', '9', '22', 'ο δε ιησους επιστραφεις και ιδων αυτην ειπεν θαρσει θυγατερ η πιστις σου σεσωκεν σε και εσωθη η γυνη απο της ωρας εκεινης');
INSERT INTO `bible_verses_tr` VALUES ('258', '40', '9', '23', 'και ελθων ο ιησους εις την οικιαν του αρχοντος και ιδων τους αυλητας και τον οχλον θορυβουμενον');
INSERT INTO `bible_verses_tr` VALUES ('259', '40', '9', '24', 'λεγει αυτοις αναχωρειτε ου γαρ απεθανεν το κορασιον αλλα καθευδει και κατεγελων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('260', '40', '9', '25', 'οτε δε εξεβληθη ο οχλος εισελθων εκρατησεν της χειρος αυτης και ηγερθη το κορασιον');
INSERT INTO `bible_verses_tr` VALUES ('261', '40', '9', '26', 'και εξηλθεν η φημη αυτη εις ολην την γην εκεινην');
INSERT INTO `bible_verses_tr` VALUES ('262', '40', '9', '27', 'και παραγοντι εκειθεν τω ιησου ηκολουθησαν αυτω δυο τυφλοι κραζοντες και λεγοντες ελεησον ημας υιε δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('263', '40', '9', '28', 'ελθοντι δε εις την οικιαν προσηλθον αυτω οι τυφλοι και λεγει αυτοις ο ιησους πιστευετε οτι δυναμαι τουτο ποιησαι λεγουσιν αυτω ναι κυριε');
INSERT INTO `bible_verses_tr` VALUES ('264', '40', '9', '29', 'τοτε ηψατο των οφθαλμων αυτων λεγων κατα την πιστιν υμων γενηθητω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('265', '40', '9', '30', 'και ανεωχθησαν αυτων οι οφθαλμοι και ενεβριμησατο αυτοις ο ιησους λεγων ορατε μηδεις γινωσκετω');
INSERT INTO `bible_verses_tr` VALUES ('266', '40', '9', '31', 'οι δε εξελθοντες διεφημισαν αυτον εν ολη τη γη εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('267', '40', '9', '32', 'αυτων δε εξερχομενων ιδου προσηνεγκαν αυτω ανθρωπον κωφον δαιμονιζομενον');
INSERT INTO `bible_verses_tr` VALUES ('268', '40', '9', '33', 'και εκβληθεντος του δαιμονιου ελαλησεν ο κωφος και εθαυμασαν οι οχλοι λεγοντες  {VAR1: οτι } ουδεποτε εφανη ουτως εν τω ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('269', '40', '9', '34', 'οι δε φαρισαιοι ελεγον εν τω αρχοντι των δαιμονιων εκβαλλει τα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('270', '40', '9', '35', 'και περιηγεν ο ιησους τας πολεις πασας και τας κωμας διδασκων εν ταις συναγωγαις αυτων και κηρυσσων το ευαγγελιον της βασιλειας και θεραπευων πασαν νοσον και πασαν μαλακιαν εν τω λαω');
INSERT INTO `bible_verses_tr` VALUES ('271', '40', '9', '36', 'ιδων δε τους οχλους εσπλαγχνισθη περι αυτων οτι ησαν εκλελυμενοι και ερριμμενοι ωσει προβατα μη εχοντα ποιμενα');
INSERT INTO `bible_verses_tr` VALUES ('272', '40', '9', '37', 'τοτε λεγει τοις μαθηταις αυτου ο μεν θερισμος πολυς οι δε εργαται ολιγοι');
INSERT INTO `bible_verses_tr` VALUES ('273', '40', '9', '38', 'δεηθητε ουν του κυριου του θερισμου οπως εκβαλη εργατας εις τον θερισμον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('274', '40', '10', '1', 'και προσκαλεσαμενος τους δωδεκα μαθητας αυτου εδωκεν αυτοις εξουσιαν πνευματων ακαθαρτων ωστε εκβαλλειν αυτα και θεραπευειν πασαν νοσον και πασαν μαλακιαν');
INSERT INTO `bible_verses_tr` VALUES ('275', '40', '10', '2', 'των δε δωδεκα αποστολων τα ονοματα εστιν ταυτα πρωτος σιμων ο λεγομενος πετρος και ανδρεας ο αδελφος αυτου ιακωβος ο του ζεβεδαιου και ιωαννης ο αδελφος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('276', '40', '10', '3', 'φιλιππος και βαρθολομαιος θωμας και ματθαιος ο τελωνης ιακωβος ο του αλφαιου και λεββαιος ο επικληθεις θαδδαιος');
INSERT INTO `bible_verses_tr` VALUES ('277', '40', '10', '4', 'σιμων ο κανανιτης και ιουδας ισκαριωτης ο και παραδους αυτον');
INSERT INTO `bible_verses_tr` VALUES ('278', '40', '10', '5', 'τουτους τους δωδεκα απεστειλεν ο ιησους παραγγειλας αυτοις λεγων εις οδον εθνων μη απελθητε και εις πολιν σαμαρειτων μη εισελθητε');
INSERT INTO `bible_verses_tr` VALUES ('279', '40', '10', '6', 'πορευεσθε δε μαλλον προς τα προβατα τα απολωλοτα οικου ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('280', '40', '10', '7', 'πορευομενοι δε κηρυσσετε λεγοντες οτι ηγγικεν η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('281', '40', '10', '8', 'ασθενουντας θεραπευετε λεπρους καθαριζετε νεκρους εγειρετε δαιμονια εκβαλλετε δωρεαν ελαβετε δωρεαν δοτε');
INSERT INTO `bible_verses_tr` VALUES ('282', '40', '10', '9', 'μη κτησησθε χρυσον μηδε αργυρον μηδε χαλκον εις τας ζωνας υμων');
INSERT INTO `bible_verses_tr` VALUES ('283', '40', '10', '10', 'μη πηραν εις οδον μηδε δυο χιτωνας μηδε υποδηματα μηδε  {VAR1: ραβδον } {VAR2: ραβδους } αξιος γαρ ο εργατης της τροφης αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('284', '40', '10', '11', 'εις ην δ αν πολιν η κωμην εισελθητε εξετασατε τις εν αυτη αξιος εστιν κακει μεινατε εως αν εξελθητε');
INSERT INTO `bible_verses_tr` VALUES ('285', '40', '10', '12', 'εισερχομενοι δε εις την οικιαν ασπασασθε αυτην');
INSERT INTO `bible_verses_tr` VALUES ('286', '40', '10', '13', 'και εαν μεν η η οικια αξια ελθετω η ειρηνη υμων επ αυτην εαν δε μη η αξια η ειρηνη υμων προς υμας επιστραφητω');
INSERT INTO `bible_verses_tr` VALUES ('287', '40', '10', '14', 'και ος εαν μη δεξηται υμας μηδε ακουση τους λογους υμων εξερχομενοι της οικιας η της πολεως εκεινης εκτιναξατε τον κονιορτον των ποδων υμων');
INSERT INTO `bible_verses_tr` VALUES ('288', '40', '10', '15', 'αμην λεγω υμιν ανεκτοτερον εσται γη σοδομων και γομορρων εν ημερα κρισεως η τη πολει εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('289', '40', '10', '16', 'ιδου εγω αποστελλω υμας ως προβατα εν μεσω λυκων γινεσθε ουν φρονιμοι ως οι οφεις και ακεραιοι ως αι περιστεραι');
INSERT INTO `bible_verses_tr` VALUES ('290', '40', '10', '17', 'προσεχετε δε απο των ανθρωπων παραδωσουσιν γαρ υμας εις συνεδρια και εν ταις συναγωγαις αυτων μαστιγωσουσιν υμας');
INSERT INTO `bible_verses_tr` VALUES ('291', '40', '10', '18', 'και επι ηγεμονας δε και βασιλεις αχθησεσθε ενεκεν εμου εις μαρτυριον αυτοις και τοις εθνεσιν');
INSERT INTO `bible_verses_tr` VALUES ('292', '40', '10', '19', 'οταν δε παραδιδωσιν υμας μη μεριμνησητε πως η τι λαλησητε δοθησεται γαρ υμιν εν εκεινη τη ωρα τι λαλησετε');
INSERT INTO `bible_verses_tr` VALUES ('293', '40', '10', '20', 'ου γαρ υμεις εστε οι λαλουντες αλλα το πνευμα του πατρος υμων το λαλουν εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('294', '40', '10', '21', 'παραδωσει δε αδελφος αδελφον εις θανατον και πατηρ τεκνον και επαναστησονται τεκνα επι γονεις και θανατωσουσιν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('295', '40', '10', '22', 'και εσεσθε μισουμενοι υπο παντων δια το ονομα μου ο δε υπομεινας εις τελος ουτος σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('296', '40', '10', '23', 'οταν δε διωκωσιν υμας εν τη πολει ταυτη φευγετε εις την αλλην αμην γαρ λεγω υμιν ου μη τελεσητε τας πολεις του ισραηλ εως αν ελθη ο υιος του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('297', '40', '10', '24', 'ουκ εστιν μαθητης υπερ τον διδασκαλον ουδε δουλος υπερ τον κυριον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('298', '40', '10', '25', 'αρκετον τω μαθητη ινα γενηται ως ο διδασκαλος αυτου και ο δουλος ως ο κυριος αυτου ει τον οικοδεσποτην  {VAR1: βεελζεβουλ } {VAR2: βεελζεβουβ } εκαλεσαν ποσω μαλλον τους οικιακους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('299', '40', '10', '26', 'μη ουν φοβηθητε αυτους ουδεν γαρ εστιν κεκαλυμμενον ο ουκ αποκαλυφθησεται και κρυπτον ο ου γνωσθησεται');
INSERT INTO `bible_verses_tr` VALUES ('300', '40', '10', '27', 'ο λεγω υμιν εν τη σκοτια ειπατε εν τω φωτι και ο εις το ους ακουετε κηρυξατε επι των δωματων');
INSERT INTO `bible_verses_tr` VALUES ('301', '40', '10', '28', 'και μη φοβηθητε απο των αποκτεινοντων το σωμα την δε ψυχην μη δυναμενων αποκτειναι φοβηθητε δε μαλλον τον δυναμενον και ψυχην και σωμα απολεσαι εν γεεννη');
INSERT INTO `bible_verses_tr` VALUES ('302', '40', '10', '29', 'ουχι δυο στρουθια ασσαριου πωλειται και εν εξ αυτων ου πεσειται επι την γην ανευ του πατρος υμων');
INSERT INTO `bible_verses_tr` VALUES ('303', '40', '10', '30', 'υμων δε και αι τριχες της κεφαλης πασαι ηριθμημεναι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('304', '40', '10', '31', 'μη ουν φοβηθητε πολλων στρουθιων διαφερετε υμεις');
INSERT INTO `bible_verses_tr` VALUES ('305', '40', '10', '32', 'πας ουν οστις ομολογησει εν εμοι εμπροσθεν των ανθρωπων ομολογησω καγω εν αυτω εμπροσθεν του πατρος μου του εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('306', '40', '10', '33', 'οστις δ αν αρνησηται με εμπροσθεν των ανθρωπων αρνησομαι αυτον καγω εμπροσθεν του πατρος μου του εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('307', '40', '10', '34', 'μη νομισητε οτι ηλθον βαλειν ειρηνην επι την γην ουκ ηλθον βαλειν ειρηνην αλλα μαχαιραν');
INSERT INTO `bible_verses_tr` VALUES ('308', '40', '10', '35', 'ηλθον γαρ διχασαι ανθρωπον κατα του πατρος αυτου και θυγατερα κατα της μητρος αυτης και νυμφην κατα της πενθερας αυτης');
INSERT INTO `bible_verses_tr` VALUES ('309', '40', '10', '36', 'και εχθροι του ανθρωπου οι οικιακοι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('310', '40', '10', '37', 'ο φιλων πατερα η μητερα υπερ εμε ουκ εστιν μου αξιος και ο φιλων υιον η θυγατερα υπερ εμε ουκ εστιν μου αξιος');
INSERT INTO `bible_verses_tr` VALUES ('311', '40', '10', '38', 'και ος ου λαμβανει τον σταυρον αυτου και ακολουθει οπισω μου ουκ εστιν μου αξιος');
INSERT INTO `bible_verses_tr` VALUES ('312', '40', '10', '39', 'ο ευρων την ψυχην αυτου απολεσει αυτην και ο απολεσας την ψυχην αυτου ενεκεν εμου ευρησει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('313', '40', '10', '40', 'ο δεχομενος υμας εμε δεχεται και ο εμε δεχομενος δεχεται τον αποστειλαντα με');
INSERT INTO `bible_verses_tr` VALUES ('314', '40', '10', '41', 'ο δεχομενος προφητην εις ονομα προφητου μισθον προφητου ληψεται και ο δεχομενος δικαιον εις ονομα δικαιου μισθον δικαιου ληψεται');
INSERT INTO `bible_verses_tr` VALUES ('315', '40', '10', '42', 'και ος εαν ποτιση ενα των μικρων τουτων ποτηριον ψυχρου μονον εις ονομα μαθητου αμην λεγω υμιν ου μη απολεση τον μισθον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('316', '40', '11', '1', 'και εγενετο οτε ετελεσεν ο ιησους διατασσων τοις δωδεκα μαθηταις αυτου μετεβη εκειθεν του διδασκειν και κηρυσσειν εν ταις πολεσιν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('317', '40', '11', '2', 'ο δε ιωαννης ακουσας εν τω δεσμωτηριω τα εργα του χριστου πεμψας δυο των μαθητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('318', '40', '11', '3', 'ειπεν αυτω συ ει ο ερχομενος η ετερον προσδοκωμεν');
INSERT INTO `bible_verses_tr` VALUES ('319', '40', '11', '4', 'και αποκριθεις ο ιησους ειπεν αυτοις πορευθεντες απαγγειλατε ιωαννη α ακουετε και βλεπετε');
INSERT INTO `bible_verses_tr` VALUES ('320', '40', '11', '5', 'τυφλοι αναβλεπουσιν και χωλοι περιπατουσιν λεπροι καθαριζονται και κωφοι ακουουσιν νεκροι εγειρονται και πτωχοι ευαγγελιζονται');
INSERT INTO `bible_verses_tr` VALUES ('321', '40', '11', '6', 'και μακαριος εστιν ος εαν μη σκανδαλισθη εν εμοι');
INSERT INTO `bible_verses_tr` VALUES ('322', '40', '11', '7', 'τουτων δε πορευομενων ηρξατο ο ιησους λεγειν τοις οχλοις περι ιωαννου τι εξηλθετε εις την ερημον θεασασθαι καλαμον υπο ανεμου σαλευομενον');
INSERT INTO `bible_verses_tr` VALUES ('323', '40', '11', '8', 'αλλα τι εξηλθετε ιδειν ανθρωπον εν μαλακοις ιματιοις ημφιεσμενον ιδου οι τα μαλακα φορουντες εν τοις οικοις των βασιλεων εισιν');
INSERT INTO `bible_verses_tr` VALUES ('324', '40', '11', '9', 'αλλα τι εξηλθετε ιδειν προφητην ναι λεγω υμιν και περισσοτερον προφητου');
INSERT INTO `bible_verses_tr` VALUES ('325', '40', '11', '10', 'ουτος γαρ εστιν περι ου γεγραπται ιδου εγω αποστελλω τον αγγελον μου προ προσωπου σου ος κατασκευασει την οδον σου εμπροσθεν σου');
INSERT INTO `bible_verses_tr` VALUES ('326', '40', '11', '11', 'αμην λεγω υμιν ουκ εγηγερται εν γεννητοις γυναικων μειζων ιωαννου του βαπτιστου ο δε μικροτερος εν τη βασιλεια των ουρανων μειζων αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('327', '40', '11', '12', 'απο δε των ημερων ιωαννου του βαπτιστου εως αρτι η βασιλεια των ουρανων βιαζεται και βιασται αρπαζουσιν αυτην');
INSERT INTO `bible_verses_tr` VALUES ('328', '40', '11', '13', 'παντες γαρ οι προφηται και ο νομος εως ιωαννου προεφητευσαν');
INSERT INTO `bible_verses_tr` VALUES ('329', '40', '11', '14', 'και ει θελετε δεξασθαι αυτος εστιν ηλιας ο μελλων ερχεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('330', '40', '11', '15', 'ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('331', '40', '11', '16', 'τινι δε ομοιωσω την γενεαν ταυτην ομοια εστιν παιδαριοις εν αγοραις καθημενοις και προσφωνουσιν τοις εταιροις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('332', '40', '11', '17', 'και λεγουσιν ηυλησαμεν υμιν και ουκ ωρχησασθε εθρηνησαμεν υμιν και ουκ εκοψασθε');
INSERT INTO `bible_verses_tr` VALUES ('333', '40', '11', '18', 'ηλθεν γαρ ιωαννης μητε εσθιων μητε πινων και λεγουσιν δαιμονιον εχει');
INSERT INTO `bible_verses_tr` VALUES ('334', '40', '11', '19', 'ηλθεν ο υιος του ανθρωπου εσθιων και πινων και λεγουσιν ιδου ανθρωπος φαγος και οινοποτης τελωνων φιλος και αμαρτωλων και εδικαιωθη η σοφια απο των τεκνων αυτης');
INSERT INTO `bible_verses_tr` VALUES ('335', '40', '11', '20', 'τοτε ηρξατο ονειδιζειν τας πολεις εν αις εγενοντο αι πλεισται δυναμεις αυτου οτι ου μετενοησαν');
INSERT INTO `bible_verses_tr` VALUES ('336', '40', '11', '21', 'ουαι σοι χοραζιν ουαι σοι  {VAR1: βηθσαιδαν } {VAR2: βηθσαιδα } οτι ει εν τυρω και σιδωνι εγενοντο αι δυναμεις αι γενομεναι εν υμιν παλαι αν εν σακκω και σποδω μετενοησαν');
INSERT INTO `bible_verses_tr` VALUES ('337', '40', '11', '22', 'πλην λεγω υμιν τυρω και σιδωνι ανεκτοτερον εσται εν ημερα κρισεως η υμιν');
INSERT INTO `bible_verses_tr` VALUES ('338', '40', '11', '23', 'και συ καπερναουμ η εως του ουρανου υψωθεισα εως αδου καταβιβασθηση οτι ει εν σοδομοις εγενοντο αι δυναμεις αι γενομεναι εν σοι εμειναν αν μεχρι της σημερον');
INSERT INTO `bible_verses_tr` VALUES ('339', '40', '11', '24', 'πλην λεγω υμιν οτι γη σοδομων ανεκτοτερον εσται εν ημερα κρισεως η σοι');
INSERT INTO `bible_verses_tr` VALUES ('340', '40', '11', '25', 'εν εκεινω τω καιρω αποκριθεις ο ιησους ειπεν εξομολογουμαι σοι πατερ κυριε του ουρανου και της γης οτι απεκρυψας ταυτα απο σοφων και συνετων και απεκαλυψας αυτα νηπιοις');
INSERT INTO `bible_verses_tr` VALUES ('341', '40', '11', '26', 'ναι ο πατηρ οτι ουτως εγενετο ευδοκια εμπροσθεν σου');
INSERT INTO `bible_verses_tr` VALUES ('342', '40', '11', '27', 'παντα μοι παρεδοθη υπο του πατρος μου και ουδεις επιγινωσκει τον υιον ει μη ο πατηρ ουδε τον πατερα τις επιγινωσκει ει μη ο υιος και ω εαν βουληται ο υιος αποκαλυψαι');
INSERT INTO `bible_verses_tr` VALUES ('343', '40', '11', '28', 'δευτε προς με παντες οι κοπιωντες και πεφορτισμενοι καγω αναπαυσω υμας');
INSERT INTO `bible_verses_tr` VALUES ('344', '40', '11', '29', 'αρατε τον ζυγον μου εφ υμας και μαθετε απ εμου οτι πραος ειμι και ταπεινος τη καρδια και ευρησετε αναπαυσιν ταις ψυχαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('345', '40', '11', '30', 'ο γαρ ζυγος μου χρηστος και το φορτιον μου ελαφρον εστιν');
INSERT INTO `bible_verses_tr` VALUES ('346', '40', '12', '1', 'εν εκεινω τω καιρω επορευθη ο ιησους τοις σαββασιν δια των σποριμων οι δε μαθηται αυτου επεινασαν και ηρξαντο τιλλειν σταχυας και εσθιειν');
INSERT INTO `bible_verses_tr` VALUES ('347', '40', '12', '2', 'οι δε φαρισαιοι ιδοντες ειπον αυτω ιδου οι μαθηται σου ποιουσιν ο ουκ εξεστιν ποιειν εν σαββατω');
INSERT INTO `bible_verses_tr` VALUES ('348', '40', '12', '3', 'ο δε ειπεν αυτοις ουκ ανεγνωτε τι εποιησεν δαβιδ οτε επεινασεν αυτος και οι μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('349', '40', '12', '4', 'πως εισηλθεν εις τον οικον του θεου και τους αρτους της προθεσεως εφαγεν ους ουκ εξον ην αυτω φαγειν ουδε τοις μετ αυτου ει μη τοις ιερευσιν μονοις');
INSERT INTO `bible_verses_tr` VALUES ('350', '40', '12', '5', 'η ουκ ανεγνωτε εν τω νομω οτι τοις σαββασιν οι ιερεις εν τω ιερω το σαββατον βεβηλουσιν και αναιτιοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('351', '40', '12', '6', 'λεγω δε υμιν οτι του ιερου μειζων εστιν ωδε');
INSERT INTO `bible_verses_tr` VALUES ('352', '40', '12', '7', 'ει δε εγνωκειτε τι εστιν ελεον θελω και ου θυσιαν ουκ αν κατεδικασατε τους αναιτιους');
INSERT INTO `bible_verses_tr` VALUES ('353', '40', '12', '8', 'κυριος γαρ εστιν και του σαββατου ο υιος του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('354', '40', '12', '9', 'και μεταβας εκειθεν ηλθεν εις την συναγωγην αυτων');
INSERT INTO `bible_verses_tr` VALUES ('355', '40', '12', '10', 'και ιδου ανθρωπος ην την χειρα εχων ξηραν και επηρωτησαν αυτον λεγοντες ει εξεστιν τοις σαββασιν θεραπευειν ινα κατηγορησωσιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('356', '40', '12', '11', 'ο δε ειπεν αυτοις τις εσται εξ υμων ανθρωπος ος εξει προβατον εν και εαν εμπεση τουτο τοις σαββασιν εις βοθυνον ουχι κρατησει αυτο και εγερει');
INSERT INTO `bible_verses_tr` VALUES ('357', '40', '12', '12', 'ποσω ουν διαφερει ανθρωπος προβατου ωστε εξεστιν τοις σαββασιν καλως ποιειν');
INSERT INTO `bible_verses_tr` VALUES ('358', '40', '12', '13', 'τοτε λεγει τω ανθρωπω εκτεινον την χειρα σου και εξετεινεν και αποκατεσταθη υγιης ως η αλλη');
INSERT INTO `bible_verses_tr` VALUES ('359', '40', '12', '14', 'οι δε φαρισαιοι συμβουλιον ελαβον κατ αυτου εξελθοντες οπως αυτον απολεσωσιν');
INSERT INTO `bible_verses_tr` VALUES ('360', '40', '12', '15', 'ο δε ιησους γνους ανεχωρησεν εκειθεν και ηκολουθησαν αυτω οχλοι πολλοι και εθεραπευσεν αυτους παντας');
INSERT INTO `bible_verses_tr` VALUES ('361', '40', '12', '16', 'και επετιμησεν αυτοις ινα μη φανερον αυτον ποιησωσιν');
INSERT INTO `bible_verses_tr` VALUES ('362', '40', '12', '17', 'οπως πληρωθη το ρηθεν δια ησαιου του προφητου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('363', '40', '12', '18', 'ιδου ο παις μου ον ηρετισα ο αγαπητος μου εις ον ευδοκησεν η ψυχη μου θησω το πνευμα μου επ αυτον και κρισιν τοις εθνεσιν απαγγελει');
INSERT INTO `bible_verses_tr` VALUES ('364', '40', '12', '19', 'ουκ ερισει ουδε κραυγασει ουδε ακουσει τις εν ταις πλατειαις την φωνην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('365', '40', '12', '20', 'καλαμον συντετριμμενον ου κατεαξει και λινον τυφομενον ου σβεσει εως αν εκβαλη εις νικος την κρισιν');
INSERT INTO `bible_verses_tr` VALUES ('366', '40', '12', '21', 'και εν τω ονοματι αυτου εθνη ελπιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('367', '40', '12', '22', 'τοτε προσηνεχθη αυτω δαιμονιζομενος τυφλος και κωφος και εθεραπευσεν αυτον ωστε τον τυφλον και κωφον και λαλειν και βλεπειν');
INSERT INTO `bible_verses_tr` VALUES ('368', '40', '12', '23', 'και εξισταντο παντες οι οχλοι και ελεγον μητι ουτος εστιν ο υιος δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('369', '40', '12', '24', 'οι δε φαρισαιοι ακουσαντες ειπον ουτος ουκ εκβαλλει τα δαιμονια ει μη εν τω βεελζεβουλ αρχοντι των δαιμονιων');
INSERT INTO `bible_verses_tr` VALUES ('370', '40', '12', '25', 'ειδως δε ο ιησους τας ενθυμησεις αυτων ειπεν αυτοις πασα βασιλεια μερισθεισα καθ εαυτης ερημουται και πασα πολις η οικια μερισθεισα καθ εαυτης ου σταθησεται');
INSERT INTO `bible_verses_tr` VALUES ('371', '40', '12', '26', 'και ει ο σατανας τον σαταναν εκβαλλει εφ εαυτον εμερισθη πως ουν σταθησεται η βασιλεια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('372', '40', '12', '27', 'και ει εγω εν βεελζεβουλ εκβαλλω τα δαιμονια οι υιοι υμων εν τινι εκβαλλουσιν δια τουτο αυτοι υμων εσονται κριται');
INSERT INTO `bible_verses_tr` VALUES ('373', '40', '12', '28', 'ει δε εγω εν πνευματι θεου εκβαλλω τα δαιμονια αρα εφθασεν εφ υμας η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('374', '40', '12', '29', 'η πως δυναται τις εισελθειν εις την οικιαν του ισχυρου και τα σκευη αυτου διαρπασαι εαν μη πρωτον δηση τον ισχυρον και τοτε την οικιαν αυτου διαρπασει');
INSERT INTO `bible_verses_tr` VALUES ('375', '40', '12', '30', 'ο μη ων μετ εμου κατ εμου εστιν και ο μη συναγων μετ εμου σκορπιζει');
INSERT INTO `bible_verses_tr` VALUES ('376', '40', '12', '31', 'δια τουτο λεγω υμιν πασα αμαρτια και βλασφημια αφεθησεται τοις ανθρωποις η δε του πνευματος βλασφημια ουκ αφεθησεται τοις ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('377', '40', '12', '32', 'και ος αν ειπη λογον κατα του υιου του ανθρωπου αφεθησεται αυτω ος δ αν ειπη κατα του πνευματος του αγιου ουκ αφεθησεται αυτω ουτε εν τουτω τω αιωνι ουτε εν τω μελλοντι');
INSERT INTO `bible_verses_tr` VALUES ('378', '40', '12', '33', 'η ποιησατε το δενδρον καλον και τον καρπον αυτου καλον η ποιησατε το δενδρον σαπρον και τον καρπον αυτου σαπρον εκ γαρ του καρπου το δενδρον γινωσκεται');
INSERT INTO `bible_verses_tr` VALUES ('379', '40', '12', '34', 'γεννηματα εχιδνων πως δυνασθε αγαθα λαλειν πονηροι οντες εκ γαρ του περισσευματος της καρδιας το στομα λαλει');
INSERT INTO `bible_verses_tr` VALUES ('380', '40', '12', '35', 'ο αγαθος ανθρωπος εκ του αγαθου θησαυρου της καρδιας εκβαλλει τα αγαθα και ο πονηρος ανθρωπος εκ του πονηρου θησαυρου εκβαλλει πονηρα');
INSERT INTO `bible_verses_tr` VALUES ('381', '40', '12', '36', 'λεγω δε υμιν οτι παν ρημα αργον ο εαν λαλησωσιν οι ανθρωποι αποδωσουσιν περι αυτου λογον εν ημερα κρισεως');
INSERT INTO `bible_verses_tr` VALUES ('382', '40', '12', '37', 'εκ γαρ των λογων σου δικαιωθηση και εκ των λογων σου καταδικασθηση');
INSERT INTO `bible_verses_tr` VALUES ('383', '40', '12', '38', 'τοτε απεκριθησαν τινες των γραμματεων και φαρισαιων λεγοντες διδασκαλε θελομεν απο σου σημειον ιδειν');
INSERT INTO `bible_verses_tr` VALUES ('384', '40', '12', '39', 'ο δε αποκριθεις ειπεν αυτοις γενεα πονηρα και μοιχαλις σημειον επιζητει και σημειον ου δοθησεται αυτη ει μη το σημειον ιωνα του προφητου');
INSERT INTO `bible_verses_tr` VALUES ('385', '40', '12', '40', 'ωσπερ γαρ ην ιωνας εν τη κοιλια του κητους τρεις ημερας και τρεις νυκτας ουτως εσται ο υιος του ανθρωπου εν τη καρδια της γης τρεις ημερας και τρεις νυκτας');
INSERT INTO `bible_verses_tr` VALUES ('386', '40', '12', '41', 'ανδρες νινευιται αναστησονται εν τη κρισει μετα της γενεας ταυτης και κατακρινουσιν αυτην οτι μετενοησαν εις το κηρυγμα ιωνα και ιδου πλειον ιωνα ωδε');
INSERT INTO `bible_verses_tr` VALUES ('387', '40', '12', '42', 'βασιλισσα νοτου εγερθησεται εν τη κρισει μετα της γενεας ταυτης και κατακρινει αυτην οτι ηλθεν εκ των περατων της γης ακουσαι την σοφιαν σολομωντος και ιδου πλειον σολομωντος ωδε');
INSERT INTO `bible_verses_tr` VALUES ('388', '40', '12', '43', 'οταν δε το ακαθαρτον πνευμα εξελθη απο του ανθρωπου διερχεται δι ανυδρων τοπων ζητουν αναπαυσιν και ουχ ευρισκει');
INSERT INTO `bible_verses_tr` VALUES ('389', '40', '12', '44', 'τοτε λεγει επιστρεψω εις τον οικον μου οθεν εξηλθον και ελθον ευρισκει σχολαζοντα σεσαρωμενον και κεκοσμημενον');
INSERT INTO `bible_verses_tr` VALUES ('390', '40', '12', '45', 'τοτε πορευεται και παραλαμβανει μεθ εαυτου επτα ετερα πνευματα πονηροτερα εαυτου και εισελθοντα κατοικει εκει και γινεται τα εσχατα του ανθρωπου εκεινου χειρονα των πρωτων ουτως εσται και τη γενεα ταυτη τη πονηρα');
INSERT INTO `bible_verses_tr` VALUES ('391', '40', '12', '46', 'ετι δε αυτου λαλουντος τοις οχλοις ιδου η μητηρ και οι αδελφοι αυτου ειστηκεισαν εξω ζητουντες αυτω λαλησαι');
INSERT INTO `bible_verses_tr` VALUES ('392', '40', '12', '47', 'ειπεν δε τις αυτω ιδου η μητηρ σου και οι αδελφοι σου εξω εστηκασιν ζητουντες σοι λαλησαι');
INSERT INTO `bible_verses_tr` VALUES ('393', '40', '12', '48', 'ο δε αποκριθεις ειπεν τω ειποντι αυτω τις εστιν η μητηρ μου και τινες εισιν οι αδελφοι μου');
INSERT INTO `bible_verses_tr` VALUES ('394', '40', '12', '49', 'και εκτεινας την χειρα αυτου επι τους μαθητας αυτου ειπεν ιδου η μητηρ μου και οι αδελφοι μου');
INSERT INTO `bible_verses_tr` VALUES ('395', '40', '12', '50', 'οστις γαρ αν ποιηση το θελημα του πατρος μου του εν ουρανοις αυτος μου αδελφος και αδελφη και μητηρ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('396', '40', '13', '1', 'εν δε τη ημερα εκεινη εξελθων ο ιησους απο της οικιας εκαθητο παρα την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('397', '40', '13', '2', 'και συνηχθησαν προς αυτον οχλοι πολλοι ωστε αυτον εις το πλοιον εμβαντα καθησθαι και πας ο οχλος επι τον αιγιαλον ειστηκει');
INSERT INTO `bible_verses_tr` VALUES ('398', '40', '13', '3', 'και ελαλησεν αυτοις πολλα εν παραβολαις λεγων ιδου εξηλθεν ο σπειρων του σπειρειν');
INSERT INTO `bible_verses_tr` VALUES ('399', '40', '13', '4', 'και εν τω σπειρειν αυτον α μεν επεσεν παρα την οδον και ηλθεν τα πετεινα και κατεφαγεν αυτα');
INSERT INTO `bible_verses_tr` VALUES ('400', '40', '13', '5', 'αλλα δε επεσεν επι τα πετρωδη οπου ουκ ειχεν γην πολλην και ευθεως εξανετειλεν δια το μη εχειν βαθος γης');
INSERT INTO `bible_verses_tr` VALUES ('401', '40', '13', '6', 'ηλιου δε ανατειλαντος εκαυματισθη και δια το μη εχειν ριζαν εξηρανθη');
INSERT INTO `bible_verses_tr` VALUES ('402', '40', '13', '7', 'αλλα δε επεσεν επι τας ακανθας και ανεβησαν αι ακανθαι και απεπνιξαν αυτα');
INSERT INTO `bible_verses_tr` VALUES ('403', '40', '13', '8', 'αλλα δε επεσεν επι την γην την καλην και εδιδου καρπον ο μεν εκατον ο δε εξηκοντα ο δε τριακοντα');
INSERT INTO `bible_verses_tr` VALUES ('404', '40', '13', '9', 'ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('405', '40', '13', '10', 'και προσελθοντες οι μαθηται ειπον αυτω δια τι εν παραβολαις λαλεις αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('406', '40', '13', '11', 'ο δε αποκριθεις ειπεν αυτοις οτι υμιν δεδοται γνωναι τα μυστηρια της βασιλειας των ουρανων εκεινοις δε ου δεδοται');
INSERT INTO `bible_verses_tr` VALUES ('407', '40', '13', '12', 'οστις γαρ εχει δοθησεται αυτω και περισσευθησεται οστις δε ουκ εχει και ο εχει αρθησεται απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('408', '40', '13', '13', 'δια τουτο εν παραβολαις αυτοις λαλω οτι βλεποντες ου βλεπουσιν και ακουοντες ουκ ακουουσιν ουδε συνιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('409', '40', '13', '14', 'και αναπληρουται επ αυτοις η προφητεια ησαιου η λεγουσα ακοη ακουσετε και ου μη συνητε και βλεποντες βλεψετε και ου μη ιδητε');
INSERT INTO `bible_verses_tr` VALUES ('410', '40', '13', '15', 'επαχυνθη γαρ η καρδια του λαου τουτου και τοις ωσιν βαρεως ηκουσαν και τους οφθαλμους αυτων εκαμμυσαν μηποτε ιδωσιν τοις οφθαλμοις και τοις ωσιν ακουσωσιν και τη καρδια συνωσιν και επιστρεψωσιν και ιασωμαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('411', '40', '13', '16', 'υμων δε μακαριοι οι οφθαλμοι οτι βλεπουσιν και τα ωτα υμων οτι ακουει');
INSERT INTO `bible_verses_tr` VALUES ('412', '40', '13', '17', 'αμην γαρ λεγω υμιν οτι πολλοι προφηται και δικαιοι επεθυμησαν ιδειν α βλεπετε και ουκ ειδον και ακουσαι α ακουετε και ουκ ηκουσαν');
INSERT INTO `bible_verses_tr` VALUES ('413', '40', '13', '18', 'υμεις ουν ακουσατε την παραβολην του σπειροντος');
INSERT INTO `bible_verses_tr` VALUES ('414', '40', '13', '19', 'παντος ακουοντος τον λογον της βασιλειας και μη συνιεντος ερχεται ο πονηρος και αρπαζει το εσπαρμενον εν τη καρδια αυτου ουτος εστιν ο παρα την οδον σπαρεις');
INSERT INTO `bible_verses_tr` VALUES ('415', '40', '13', '20', 'ο δε επι τα πετρωδη σπαρεις ουτος εστιν ο τον λογον ακουων και ευθυς μετα χαρας λαμβανων αυτον');
INSERT INTO `bible_verses_tr` VALUES ('416', '40', '13', '21', 'ουκ εχει δε ριζαν εν εαυτω αλλα προσκαιρος εστιν γενομενης δε θλιψεως η διωγμου δια τον λογον ευθυς σκανδαλιζεται');
INSERT INTO `bible_verses_tr` VALUES ('417', '40', '13', '22', 'ο δε εις τας ακανθας σπαρεις ουτος εστιν ο τον λογον ακουων και η μεριμνα του αιωνος τουτου και η απατη του πλουτου συμπνιγει τον λογον και ακαρπος γινεται');
INSERT INTO `bible_verses_tr` VALUES ('418', '40', '13', '23', 'ο δε επι την γην την καλην σπαρεις ουτος εστιν ο τον λογον ακουων και συνιων ος δη καρποφορει και ποιει ο μεν εκατον ο δε εξηκοντα ο δε τριακοντα');
INSERT INTO `bible_verses_tr` VALUES ('419', '40', '13', '24', 'αλλην παραβολην παρεθηκεν αυτοις λεγων ωμοιωθη η βασιλεια των ουρανων ανθρωπω  {VAR1: σπειροντι } {VAR2: σπειραντι } καλον σπερμα εν τω αγρω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('420', '40', '13', '25', 'εν δε τω καθευδειν τους ανθρωπους ηλθεν αυτου ο εχθρος και εσπειρεν ζιζανια ανα μεσον του σιτου και απηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('421', '40', '13', '26', 'οτε δε εβλαστησεν ο χορτος και καρπον εποιησεν τοτε εφανη και τα ζιζανια');
INSERT INTO `bible_verses_tr` VALUES ('422', '40', '13', '27', 'προσελθοντες δε οι δουλοι του οικοδεσποτου ειπον αυτω κυριε ουχι καλον σπερμα εσπειρας εν τω σω αγρω ποθεν ουν εχει τα ζιζανια');
INSERT INTO `bible_verses_tr` VALUES ('423', '40', '13', '28', 'ο δε εφη αυτοις εχθρος ανθρωπος τουτο εποιησεν οι δε δουλοι ειπον αυτω θελεις ουν απελθοντες συλλεξωμεν αυτα');
INSERT INTO `bible_verses_tr` VALUES ('424', '40', '13', '29', 'ο δε εφη ου μηποτε συλλεγοντες τα ζιζανια εκριζωσητε αμα αυτοις τον σιτον');
INSERT INTO `bible_verses_tr` VALUES ('425', '40', '13', '30', 'αφετε συναυξανεσθαι αμφοτερα μεχρι του θερισμου και εν τω καιρω του θερισμου ερω τοις θερισταις συλλεξατε πρωτον τα ζιζανια και δησατε αυτα εις δεσμας προς το κατακαυσαι αυτα τον δε σιτον συναγαγετε εις την αποθηκην μου');
INSERT INTO `bible_verses_tr` VALUES ('426', '40', '13', '31', 'αλλην παραβολην παρεθηκεν αυτοις λεγων ομοια εστιν η βασιλεια των ουρανων κοκκω σιναπεως ον λαβων ανθρωπος εσπειρεν εν τω αγρω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('427', '40', '13', '32', 'ο μικροτερον μεν εστιν παντων των σπερματων οταν δε αυξηθη μειζον των λαχανων εστιν και γινεται δενδρον ωστε ελθειν τα πετεινα του ουρανου και κατασκηνουν εν τοις κλαδοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('428', '40', '13', '33', 'αλλην παραβολην ελαλησεν αυτοις ομοια εστιν η βασιλεια των ουρανων ζυμη ην λαβουσα γυνη ενεκρυψεν εις αλευρου σατα τρια εως ου εζυμωθη ολον');
INSERT INTO `bible_verses_tr` VALUES ('429', '40', '13', '34', 'ταυτα παντα ελαλησεν ο ιησους εν παραβολαις τοις οχλοις και χωρις παραβολης ουκ ελαλει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('430', '40', '13', '35', 'οπως πληρωθη το ρηθεν δια του προφητου λεγοντος ανοιξω εν παραβολαις το στομα μου ερευξομαι κεκρυμμενα απο καταβολης κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('431', '40', '13', '36', 'τοτε αφεις τους οχλους ηλθεν εις την οικιαν ο ιησους και προσηλθον αυτω οι μαθηται αυτου λεγοντες φρασον ημιν την παραβολην των ζιζανιων του αγρου');
INSERT INTO `bible_verses_tr` VALUES ('432', '40', '13', '37', 'ο δε αποκριθεις ειπεν αυτοις ο σπειρων το καλον σπερμα εστιν ο υιος του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('433', '40', '13', '38', 'ο δε αγρος εστιν ο κοσμος το δε καλον σπερμα ουτοι εισιν οι υιοι της βασιλειας τα δε ζιζανια εισιν οι υιοι του πονηρου');
INSERT INTO `bible_verses_tr` VALUES ('434', '40', '13', '39', 'ο δε εχθρος ο σπειρας αυτα εστιν ο διαβολος ο δε θερισμος συντελεια του αιωνος εστιν οι δε θερισται αγγελοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('435', '40', '13', '40', 'ωσπερ ουν συλλεγεται τα ζιζανια και πυρι κατακαιεται ουτως εσται εν τη συντελεια του αιωνος τουτου');
INSERT INTO `bible_verses_tr` VALUES ('436', '40', '13', '41', 'αποστελει ο υιος του ανθρωπου τους αγγελους αυτου και συλλεξουσιν εκ της βασιλειας αυτου παντα τα σκανδαλα και τους ποιουντας την ανομιαν');
INSERT INTO `bible_verses_tr` VALUES ('437', '40', '13', '42', 'και βαλουσιν αυτους εις την καμινον του πυρος εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('438', '40', '13', '43', 'τοτε οι δικαιοι εκλαμψουσιν ως ο ηλιος εν τη βασιλεια του πατρος αυτων ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('439', '40', '13', '44', 'παλιν ομοια εστιν η βασιλεια των ουρανων θησαυρω κεκρυμμενω εν τω αγρω ον ευρων ανθρωπος εκρυψεν και απο της χαρας αυτου υπαγει και παντα οσα εχει πωλει και αγοραζει τον αγρον εκεινον');
INSERT INTO `bible_verses_tr` VALUES ('440', '40', '13', '45', 'παλιν ομοια εστιν η βασιλεια των ουρανων ανθρωπω εμπορω ζητουντι καλους μαργαριτας');
INSERT INTO `bible_verses_tr` VALUES ('441', '40', '13', '46', 'ος ευρων ενα πολυτιμον μαργαριτην απελθων πεπρακεν παντα οσα ειχεν και ηγορασεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('442', '40', '13', '47', 'παλιν ομοια εστιν η βασιλεια των ουρανων σαγηνη βληθειση εις την θαλασσαν και εκ παντος γενους συναγαγουση');
INSERT INTO `bible_verses_tr` VALUES ('443', '40', '13', '48', 'ην οτε επληρωθη αναβιβασαντες επι τον αιγιαλον και καθισαντες συνελεξαν τα καλα εις αγγεια τα δε σαπρα εξω εβαλον');
INSERT INTO `bible_verses_tr` VALUES ('444', '40', '13', '49', 'ουτως εσται εν τη συντελεια του αιωνος εξελευσονται οι αγγελοι και αφοριουσιν τους πονηρους εκ μεσου των δικαιων');
INSERT INTO `bible_verses_tr` VALUES ('445', '40', '13', '50', 'και βαλουσιν αυτους εις την καμινον του πυρος εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('446', '40', '13', '51', 'λεγει αυτοις ο ιησους συνηκατε ταυτα παντα λεγουσιν αυτω ναι κυριε');
INSERT INTO `bible_verses_tr` VALUES ('447', '40', '13', '52', 'ο δε ειπεν αυτοις δια τουτο πας γραμματευς μαθητευθεις εις την βασιλειαν των ουρανων ομοιος εστιν ανθρωπω οικοδεσποτη οστις εκβαλλει εκ του θησαυρου αυτου καινα και παλαια');
INSERT INTO `bible_verses_tr` VALUES ('448', '40', '13', '53', 'και εγενετο οτε ετελεσεν ο ιησους τας παραβολας ταυτας μετηρεν εκειθεν');
INSERT INTO `bible_verses_tr` VALUES ('449', '40', '13', '54', 'και ελθων εις την πατριδα αυτου εδιδασκεν αυτους εν τη συναγωγη αυτων ωστε εκπληττεσθαι αυτους και λεγειν ποθεν τουτω η σοφια αυτη και αι δυναμεις');
INSERT INTO `bible_verses_tr` VALUES ('450', '40', '13', '55', 'ουχ ουτος εστιν ο του τεκτονος υιος ουχι η μητηρ αυτου λεγεται μαριαμ και οι αδελφοι αυτου ιακωβος και ιωσης και σιμων και ιουδας');
INSERT INTO `bible_verses_tr` VALUES ('451', '40', '13', '56', 'και αι αδελφαι αυτου ουχι πασαι προς ημας εισιν ποθεν ουν τουτω ταυτα παντα');
INSERT INTO `bible_verses_tr` VALUES ('452', '40', '13', '57', 'και εσκανδαλιζοντο εν αυτω ο δε ιησους ειπεν αυτοις ουκ εστιν προφητης ατιμος ει μη εν τη πατριδι αυτου και εν τη οικια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('453', '40', '13', '58', 'και ουκ εποιησεν εκει δυναμεις πολλας δια την απιστιαν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('454', '40', '14', '1', 'εν εκεινω τω καιρω ηκουσεν ηρωδης ο τετραρχης την ακοην ιησου');
INSERT INTO `bible_verses_tr` VALUES ('455', '40', '14', '2', 'και ειπεν τοις παισιν αυτου ουτος εστιν ιωαννης ο βαπτιστης αυτος ηγερθη απο των νεκρων και δια τουτο αι δυναμεις ενεργουσιν εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('456', '40', '14', '3', 'ο γαρ ηρωδης κρατησας τον ιωαννην εδησεν αυτον και εθετο εν φυλακη δια ηρωδιαδα την γυναικα φιλιππου του αδελφου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('457', '40', '14', '4', 'ελεγεν γαρ αυτω ο ιωαννης ουκ εξεστιν σοι εχειν αυτην');
INSERT INTO `bible_verses_tr` VALUES ('458', '40', '14', '5', 'και θελων αυτον αποκτειναι εφοβηθη τον οχλον οτι ως προφητην αυτον ειχον');
INSERT INTO `bible_verses_tr` VALUES ('459', '40', '14', '6', 'γενεσιων δε αγομενων του ηρωδου ωρχησατο η θυγατηρ της ηρωδιαδος εν τω μεσω και ηρεσεν τω ηρωδη');
INSERT INTO `bible_verses_tr` VALUES ('460', '40', '14', '7', 'οθεν μεθ ορκου ωμολογησεν αυτη δουναι ο εαν αιτησηται');
INSERT INTO `bible_verses_tr` VALUES ('461', '40', '14', '8', 'η δε προβιβασθεισα υπο της μητρος αυτης δος μοι φησιν ωδε επι πινακι την κεφαλην ιωαννου του βαπτιστου');
INSERT INTO `bible_verses_tr` VALUES ('462', '40', '14', '9', 'και ελυπηθη ο βασιλευς δια δε τους ορκους και τους συνανακειμενους εκελευσεν δοθηναι');
INSERT INTO `bible_verses_tr` VALUES ('463', '40', '14', '10', 'και πεμψας απεκεφαλισεν τον ιωαννην εν τη φυλακη');
INSERT INTO `bible_verses_tr` VALUES ('464', '40', '14', '11', 'και ηνεχθη η κεφαλη αυτου επι πινακι και εδοθη τω κορασιω και ηνεγκεν τη μητρι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('465', '40', '14', '12', 'και προσελθοντες οι μαθηται αυτου ηραν το σωμα και εθαψαν αυτο και ελθοντες απηγγειλαν τω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('466', '40', '14', '13', 'και ακουσας ο ιησους ανεχωρησεν εκειθεν εν πλοιω εις ερημον τοπον κατ ιδιαν και ακουσαντες οι οχλοι ηκολουθησαν αυτω πεζη απο των πολεων');
INSERT INTO `bible_verses_tr` VALUES ('467', '40', '14', '14', 'και εξελθων ο ιησους ειδεν πολυν οχλον και εσπλαγχνισθη επ αυτους και εθεραπευσεν τους αρρωστους αυτων');
INSERT INTO `bible_verses_tr` VALUES ('468', '40', '14', '15', 'οψιας δε γενομενης προσηλθον αυτω οι μαθηται αυτου λεγοντες ερημος εστιν ο τοπος και η ωρα ηδη παρηλθεν απολυσον τους οχλους ινα απελθοντες εις τας κωμας αγορασωσιν εαυτοις βρωματα');
INSERT INTO `bible_verses_tr` VALUES ('469', '40', '14', '16', 'ο δε ιησους ειπεν αυτοις ου χρειαν εχουσιν απελθειν δοτε αυτοις υμεις φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('470', '40', '14', '17', 'οι δε λεγουσιν αυτω ουκ εχομεν ωδε ει μη πεντε αρτους και δυο ιχθυας');
INSERT INTO `bible_verses_tr` VALUES ('471', '40', '14', '18', 'ο δε ειπεν φερετε μοι αυτους ωδε');
INSERT INTO `bible_verses_tr` VALUES ('472', '40', '14', '19', 'και κελευσας τους οχλους ανακλιθηναι επι τους χορτους και λαβων τους πεντε αρτους και τους δυο ιχθυας αναβλεψας εις τον ουρανον ευλογησεν και κλασας εδωκεν τοις μαθηταις τους αρτους οι δε μαθηται τοις οχλοις');
INSERT INTO `bible_verses_tr` VALUES ('473', '40', '14', '20', 'και εφαγον παντες και εχορτασθησαν και ηραν το περισσευον των κλασματων δωδεκα κοφινους πληρεις');
INSERT INTO `bible_verses_tr` VALUES ('474', '40', '14', '21', 'οι δε εσθιοντες ησαν ανδρες ωσει πεντακισχιλιοι χωρις γυναικων και παιδιων');
INSERT INTO `bible_verses_tr` VALUES ('475', '40', '14', '22', 'και ευθεως ηναγκασεν ο ιησους τους μαθητας αυτου εμβηναι εις το πλοιον και προαγειν αυτον εις το περαν εως ου απολυση τους οχλους');
INSERT INTO `bible_verses_tr` VALUES ('476', '40', '14', '23', 'και απολυσας τους οχλους ανεβη εις το ορος κατ ιδιαν προσευξασθαι οψιας δε γενομενης μονος ην εκει');
INSERT INTO `bible_verses_tr` VALUES ('477', '40', '14', '24', 'το δε πλοιον ηδη μεσον της θαλασσης ην βασανιζομενον υπο των κυματων ην γαρ εναντιος ο ανεμος');
INSERT INTO `bible_verses_tr` VALUES ('478', '40', '14', '25', 'τεταρτη δε φυλακη της νυκτος απηλθεν προς αυτους ο ιησους περιπατων επι της θαλασσης');
INSERT INTO `bible_verses_tr` VALUES ('479', '40', '14', '26', 'και ιδοντες αυτον οι μαθηται επι την θαλασσαν περιπατουντα εταραχθησαν λεγοντες οτι φαντασμα εστιν και απο του φοβου εκραξαν');
INSERT INTO `bible_verses_tr` VALUES ('480', '40', '14', '27', 'ευθεως δε ελαλησεν αυτοις ο ιησους λεγων θαρσειτε εγω ειμι μη φοβεισθε');
INSERT INTO `bible_verses_tr` VALUES ('481', '40', '14', '28', 'αποκριθεις δε αυτω ο πετρος ειπεν κυριε ει συ ει κελευσον με προς σε ελθειν επι τα υδατα');
INSERT INTO `bible_verses_tr` VALUES ('482', '40', '14', '29', 'ο δε ειπεν ελθε και καταβας απο του πλοιου ο πετρος περιεπατησεν επι τα υδατα ελθειν προς τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('483', '40', '14', '30', 'βλεπων δε τον ανεμον ισχυρον εφοβηθη και αρξαμενος καταποντιζεσθαι εκραξεν λεγων κυριε σωσον με');
INSERT INTO `bible_verses_tr` VALUES ('484', '40', '14', '31', 'ευθεως δε ο ιησους εκτεινας την χειρα επελαβετο αυτου και λεγει αυτω ολιγοπιστε εις τι εδιστασας');
INSERT INTO `bible_verses_tr` VALUES ('485', '40', '14', '32', 'και εμβαντων αυτων εις το πλοιον εκοπασεν ο ανεμος');
INSERT INTO `bible_verses_tr` VALUES ('486', '40', '14', '33', 'οι δε εν τω πλοιω ελθοντες προσεκυνησαν αυτω λεγοντες αληθως θεου υιος ει');
INSERT INTO `bible_verses_tr` VALUES ('487', '40', '14', '34', 'και διαπερασαντες ηλθον εις την γην γεννησαρετ');
INSERT INTO `bible_verses_tr` VALUES ('488', '40', '14', '35', 'και επιγνοντες αυτον οι ανδρες του τοπου εκεινου απεστειλαν εις ολην την περιχωρον εκεινην και προσηνεγκαν αυτω παντας τους κακως εχοντας');
INSERT INTO `bible_verses_tr` VALUES ('489', '40', '14', '36', 'και παρεκαλουν αυτον ινα μονον αψωνται του κρασπεδου του ιματιου αυτου και οσοι ηψαντο διεσωθησαν');
INSERT INTO `bible_verses_tr` VALUES ('490', '40', '15', '1', 'τοτε προσερχονται τω ιησου οι απο ιεροσολυμων γραμματεις και φαρισαιοι λεγοντες');
INSERT INTO `bible_verses_tr` VALUES ('491', '40', '15', '2', 'δια τι οι μαθηται σου παραβαινουσιν την παραδοσιν των πρεσβυτερων ου γαρ νιπτονται τας χειρας αυτων οταν αρτον εσθιωσιν');
INSERT INTO `bible_verses_tr` VALUES ('492', '40', '15', '3', 'ο δε αποκριθεις ειπεν αυτοις δια τι και υμεις παραβαινετε την εντολην του θεου δια την παραδοσιν υμων');
INSERT INTO `bible_verses_tr` VALUES ('493', '40', '15', '4', 'ο γαρ θεος ενετειλατο λεγων τιμα τον πατερα σου και την μητερα και ο κακολογων πατερα η μητερα θανατω τελευτατω');
INSERT INTO `bible_verses_tr` VALUES ('494', '40', '15', '5', 'υμεις δε λεγετε ος αν ειπη τω πατρι η τη μητρι δωρον ο εαν εξ εμου ωφεληθης');
INSERT INTO `bible_verses_tr` VALUES ('495', '40', '15', '6', 'και ου μη τιμηση τον πατερα αυτου η την μητερα αυτου και ηκυρωσατε την εντολην του θεου δια την παραδοσιν υμων');
INSERT INTO `bible_verses_tr` VALUES ('496', '40', '15', '7', 'υποκριται καλως προεφητευσεν περι υμων ησαιας λεγων');
INSERT INTO `bible_verses_tr` VALUES ('497', '40', '15', '8', 'εγγιζει μοι ο λαος ουτος τω στοματι αυτων και τοις χειλεσιν με τιμα η δε καρδια αυτων πορρω απεχει απ εμου');
INSERT INTO `bible_verses_tr` VALUES ('498', '40', '15', '9', 'ματην δε σεβονται με διδασκοντες διδασκαλιας ενταλματα ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('499', '40', '15', '10', 'και προσκαλεσαμενος τον οχλον ειπεν αυτοις ακουετε και συνιετε');
INSERT INTO `bible_verses_tr` VALUES ('500', '40', '15', '11', 'ου το εισερχομενον εις το στομα κοινοι τον ανθρωπον αλλα το εκπορευομενον εκ του στοματος τουτο κοινοι τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('501', '40', '15', '12', 'τοτε προσελθοντες οι μαθηται αυτου ειπον αυτω οιδας οτι οι φαρισαιοι ακουσαντες τον λογον εσκανδαλισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('502', '40', '15', '13', 'ο δε αποκριθεις ειπεν πασα φυτεια ην ουκ εφυτευσεν ο πατηρ μου ο ουρανιος εκριζωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('503', '40', '15', '14', 'αφετε αυτους οδηγοι εισιν τυφλοι τυφλων τυφλος δε τυφλον εαν οδηγη αμφοτεροι εις βοθυνον πεσουνται');
INSERT INTO `bible_verses_tr` VALUES ('504', '40', '15', '15', 'αποκριθεις δε ο πετρος ειπεν αυτω φρασον ημιν την παραβολην ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('505', '40', '15', '16', 'ο δε ιησους ειπεν ακμην και υμεις ασυνετοι εστε');
INSERT INTO `bible_verses_tr` VALUES ('506', '40', '15', '17', 'ουπω νοειτε οτι παν το εισπορευομενον εις το στομα εις την κοιλιαν χωρει και εις αφεδρωνα εκβαλλεται');
INSERT INTO `bible_verses_tr` VALUES ('507', '40', '15', '18', 'τα δε εκπορευομενα εκ του στοματος εκ της καρδιας εξερχεται κακεινα κοινοι τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('508', '40', '15', '19', 'εκ γαρ της καρδιας εξερχονται διαλογισμοι πονηροι φονοι μοιχειαι πορνειαι κλοπαι ψευδομαρτυριαι βλασφημιαι');
INSERT INTO `bible_verses_tr` VALUES ('509', '40', '15', '20', 'ταυτα εστιν τα κοινουντα τον ανθρωπον το δε ανιπτοις χερσιν φαγειν ου κοινοι τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('510', '40', '15', '21', 'και εξελθων εκειθεν ο ιησους ανεχωρησεν εις τα μερη τυρου και σιδωνος');
INSERT INTO `bible_verses_tr` VALUES ('511', '40', '15', '22', 'και ιδου γυνη χαναναια απο των οριων εκεινων εξελθουσα εκραυγασεν αυτω λεγουσα ελεησον με κυριε υιε δαβιδ η θυγατηρ μου κακως δαιμονιζεται');
INSERT INTO `bible_verses_tr` VALUES ('512', '40', '15', '23', 'ο δε ουκ απεκριθη αυτη λογον και προσελθοντες οι μαθηται αυτου ηρωτων αυτον λεγοντες απολυσον αυτην οτι κραζει οπισθεν ημων');
INSERT INTO `bible_verses_tr` VALUES ('513', '40', '15', '24', 'ο δε αποκριθεις ειπεν ουκ απεσταλην ει μη εις τα προβατα τα απολωλοτα οικου ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('514', '40', '15', '25', 'η δε ελθουσα προσεκυνει αυτω λεγουσα κυριε βοηθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('515', '40', '15', '26', 'ο δε αποκριθεις ειπεν ουκ εστιν καλον λαβειν τον αρτον των τεκνων και βαλειν τοις κυναριοις');
INSERT INTO `bible_verses_tr` VALUES ('516', '40', '15', '27', 'η δε ειπεν ναι κυριε και γαρ τα κυναρια εσθιει απο των ψιχιων των πιπτοντων απο της τραπεζης των κυριων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('517', '40', '15', '28', 'τοτε αποκριθεις ο ιησους ειπεν αυτη ω γυναι μεγαλη σου η πιστις γενηθητω σοι ως θελεις και ιαθη η θυγατηρ αυτης απο της ωρας εκεινης');
INSERT INTO `bible_verses_tr` VALUES ('518', '40', '15', '29', 'και μεταβας εκειθεν ο ιησους ηλθεν παρα την θαλασσαν της γαλιλαιας και αναβας εις το ορος εκαθητο εκει');
INSERT INTO `bible_verses_tr` VALUES ('519', '40', '15', '30', 'και προσηλθον αυτω οχλοι πολλοι εχοντες μεθ εαυτων χωλους τυφλους κωφους κυλλους και ετερους πολλους και ερριψαν αυτους παρα τους ποδας του ιησου και εθεραπευσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('520', '40', '15', '31', 'ωστε τους οχλους θαυμασαι βλεποντας κωφους λαλουντας κυλλους υγιεις χωλους περιπατουντας και τυφλους βλεποντας και εδοξασαν τον θεον ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('521', '40', '15', '32', 'ο δε ιησους προσκαλεσαμενος τους μαθητας αυτου ειπεν σπλαγχνιζομαι επι τον οχλον οτι ηδη ημερας τρεις προσμενουσιν μοι και ουκ εχουσιν τι φαγωσιν και απολυσαι αυτους νηστεις ου θελω μηποτε εκλυθωσιν εν τη οδω');
INSERT INTO `bible_verses_tr` VALUES ('522', '40', '15', '33', 'και λεγουσιν αυτω οι μαθηται αυτου ποθεν ημιν εν ερημια αρτοι τοσουτοι ωστε χορτασαι οχλον τοσουτον');
INSERT INTO `bible_verses_tr` VALUES ('523', '40', '15', '34', 'και λεγει αυτοις ο ιησους ποσους αρτους εχετε οι δε ειπον επτα και ολιγα ιχθυδια');
INSERT INTO `bible_verses_tr` VALUES ('524', '40', '15', '35', 'και εκελευσεν τοις οχλοις αναπεσειν επι την γην');
INSERT INTO `bible_verses_tr` VALUES ('525', '40', '15', '36', 'και λαβων τους επτα αρτους και τους ιχθυας ευχαριστησας εκλασεν και εδωκεν τοις μαθηταις αυτου οι δε μαθηται τω οχλω');
INSERT INTO `bible_verses_tr` VALUES ('526', '40', '15', '37', 'και εφαγον παντες και εχορτασθησαν και ηραν το περισσευον των κλασματων επτα σπυριδας πληρεις');
INSERT INTO `bible_verses_tr` VALUES ('527', '40', '15', '38', 'οι δε εσθιοντες ησαν τετρακισχιλιοι ανδρες χωρις γυναικων και παιδιων');
INSERT INTO `bible_verses_tr` VALUES ('528', '40', '15', '39', 'και απολυσας τους οχλους ενεβη εις το πλοιον και ηλθεν εις τα ορια μαγδαλα');
INSERT INTO `bible_verses_tr` VALUES ('529', '40', '16', '1', 'και προσελθοντες οι φαρισαιοι και σαδδουκαιοι πειραζοντες επηρωτησαν αυτον σημειον εκ του ουρανου επιδειξαι αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('530', '40', '16', '2', 'ο δε αποκριθεις ειπεν αυτοις οψιας γενομενης λεγετε ευδια πυρραζει γαρ ο ουρανος');
INSERT INTO `bible_verses_tr` VALUES ('531', '40', '16', '3', 'και πρωι σημερον χειμων πυρραζει γαρ στυγναζων ο ουρανος υποκριται το μεν προσωπον του ουρανου γινωσκετε διακρινειν τα δε σημεια των καιρων ου δυνασθε');
INSERT INTO `bible_verses_tr` VALUES ('532', '40', '16', '4', 'γενεα πονηρα και μοιχαλις σημειον επιζητει και σημειον ου δοθησεται αυτη ει μη το σημειον ιωνα του προφητου και καταλιπων αυτους απηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('533', '40', '16', '5', 'και ελθοντες οι μαθηται αυτου εις το περαν επελαθοντο αρτους λαβειν');
INSERT INTO `bible_verses_tr` VALUES ('534', '40', '16', '6', 'ο δε ιησους ειπεν αυτοις ορατε και προσεχετε απο της ζυμης των φαρισαιων και σαδδουκαιων');
INSERT INTO `bible_verses_tr` VALUES ('535', '40', '16', '7', 'οι δε διελογιζοντο εν εαυτοις λεγοντες οτι αρτους ουκ ελαβομεν');
INSERT INTO `bible_verses_tr` VALUES ('536', '40', '16', '8', 'γνους δε ο ιησους ειπεν αυτοις τι διαλογιζεσθε εν εαυτοις ολιγοπιστοι οτι αρτους ουκ ελαβετε');
INSERT INTO `bible_verses_tr` VALUES ('537', '40', '16', '9', 'ουπω νοειτε ουδε μνημονευετε τους πεντε αρτους των πεντακισχιλιων και ποσους κοφινους ελαβετε');
INSERT INTO `bible_verses_tr` VALUES ('538', '40', '16', '10', 'ουδε τους επτα αρτους των τετρακισχιλιων και ποσας σπυριδας ελαβετε');
INSERT INTO `bible_verses_tr` VALUES ('539', '40', '16', '11', 'πως ου νοειτε οτι ου περι αρτου ειπον υμιν προσεχειν απο της ζυμης των φαρισαιων και σαδδουκαιων');
INSERT INTO `bible_verses_tr` VALUES ('540', '40', '16', '12', 'τοτε συνηκαν οτι ουκ ειπεν προσεχειν απο της ζυμης του αρτου αλλ απο της διδαχης των φαρισαιων και σαδδουκαιων');
INSERT INTO `bible_verses_tr` VALUES ('541', '40', '16', '13', 'ελθων δε ο ιησους εις τα μερη καισαρειας της φιλιππου ηρωτα τους μαθητας αυτου λεγων τινα με λεγουσιν οι ανθρωποι ειναι τον υιον του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('542', '40', '16', '14', 'οι δε ειπον οι μεν ιωαννην τον βαπτιστην αλλοι δε ηλιαν ετεροι δε ιερεμιαν η ενα των προφητων');
INSERT INTO `bible_verses_tr` VALUES ('543', '40', '16', '15', 'λεγει αυτοις υμεις δε τινα με λεγετε ειναι');
INSERT INTO `bible_verses_tr` VALUES ('544', '40', '16', '16', 'αποκριθεις δε σιμων πετρος ειπεν συ ει ο χριστος ο υιος του θεου του ζωντος');
INSERT INTO `bible_verses_tr` VALUES ('545', '40', '16', '17', 'και αποκριθεις ο ιησους ειπεν αυτω μακαριος ει σιμων βαρ ιωνα οτι σαρξ και αιμα ουκ απεκαλυψεν σοι αλλ ο πατηρ μου ο εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('546', '40', '16', '18', 'καγω δε σοι λεγω οτι συ ει πετρος και επι ταυτη τη πετρα οικοδομησω μου την εκκλησιαν και πυλαι αδου ου κατισχυσουσιν αυτης');
INSERT INTO `bible_verses_tr` VALUES ('547', '40', '16', '19', 'και δωσω σοι τας κλεις της βασιλειας των ουρανων και ο εαν δησης επι της γης εσται δεδεμενον εν τοις ουρανοις και ο εαν λυσης επι της γης εσται λελυμενον εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('548', '40', '16', '20', 'τοτε διεστειλατο τοις μαθηταις αυτου ινα μηδενι ειπωσιν οτι αυτος εστιν ιησους ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('549', '40', '16', '21', 'απο τοτε ηρξατο ο ιησους δεικνυειν τοις μαθηταις αυτου οτι δει αυτον απελθειν εις ιεροσολυμα και πολλα παθειν απο των πρεσβυτερων και αρχιερεων και γραμματεων και αποκτανθηναι και τη τριτη ημερα εγερθηναι');
INSERT INTO `bible_verses_tr` VALUES ('550', '40', '16', '22', 'και προσλαβομενος αυτον ο πετρος ηρξατο επιτιμαν αυτω λεγων ιλεως σοι κυριε ου μη εσται σοι τουτο');
INSERT INTO `bible_verses_tr` VALUES ('551', '40', '16', '23', 'ο δε στραφεις ειπεν τω πετρω υπαγε οπισω μου σατανα σκανδαλον μου ει οτι ου φρονεις τα του θεου αλλα τα των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('552', '40', '16', '24', 'τοτε ο ιησους ειπεν τοις μαθηταις αυτου ει τις θελει οπισω μου ελθειν απαρνησασθω εαυτον και αρατω τον σταυρον αυτου και ακολουθειτω μοι');
INSERT INTO `bible_verses_tr` VALUES ('553', '40', '16', '25', 'ος γαρ αν θελη την ψυχην αυτου σωσαι απολεσει αυτην ος δ αν απολεση την ψυχην αυτου ενεκεν εμου ευρησει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('554', '40', '16', '26', 'τι γαρ ωφελειται ανθρωπος εαν τον κοσμον ολον κερδηση την δε ψυχην αυτου ζημιωθη η τι δωσει ανθρωπος ανταλλαγμα της ψυχης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('555', '40', '16', '27', 'μελλει γαρ ο υιος του ανθρωπου ερχεσθαι εν τη δοξη του πατρος αυτου μετα των αγγελων αυτου και τοτε αποδωσει εκαστω κατα την πραξιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('556', '40', '16', '28', 'αμην λεγω υμιν εισιν τινες των ωδε εστηκοτων οιτινες ου μη γευσωνται θανατου εως αν ιδωσιν τον υιον του ανθρωπου ερχομενον εν τη βασιλεια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('557', '40', '17', '1', 'και μεθ ημερας εξ παραλαμβανει ο ιησους τον πετρον και ιακωβον και ιωαννην τον αδελφον αυτου και αναφερει αυτους εις ορος υψηλον κατ ιδιαν');
INSERT INTO `bible_verses_tr` VALUES ('558', '40', '17', '2', 'και μετεμορφωθη εμπροσθεν αυτων και ελαμψεν το προσωπον αυτου ως ο ηλιος τα δε ιματια αυτου εγενετο λευκα ως το φως');
INSERT INTO `bible_verses_tr` VALUES ('559', '40', '17', '3', 'και ιδου ωφθησαν αυτοις μωσης και ηλιας μετ αυτου συλλαλουντες');
INSERT INTO `bible_verses_tr` VALUES ('560', '40', '17', '4', 'αποκριθεις δε ο πετρος ειπεν τω ιησου κυριε καλον εστιν ημας ωδε ειναι ει θελεις ποιησωμεν ωδε τρεις σκηνας σοι μιαν και μωση μιαν και μιαν ηλια');
INSERT INTO `bible_verses_tr` VALUES ('561', '40', '17', '5', 'ετι αυτου λαλουντος ιδου νεφελη φωτεινη επεσκιασεν αυτους και ιδου φωνη εκ της νεφελης λεγουσα ουτος εστιν ο υιος μου ο αγαπητος εν ω ευδοκησα αυτου ακουετε');
INSERT INTO `bible_verses_tr` VALUES ('562', '40', '17', '6', 'και ακουσαντες οι μαθηται επεσον επι προσωπον αυτων και εφοβηθησαν σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('563', '40', '17', '7', 'και προσελθων ο ιησους ηψατο αυτων και ειπεν εγερθητε και μη φοβεισθε');
INSERT INTO `bible_verses_tr` VALUES ('564', '40', '17', '8', 'επαραντες δε τους οφθαλμους αυτων ουδενα ειδον ει μη τον ιησουν μονον');
INSERT INTO `bible_verses_tr` VALUES ('565', '40', '17', '9', 'και καταβαινοντων αυτων απο του ορους ενετειλατο αυτοις ο ιησους λεγων μηδενι ειπητε το οραμα εως ου ο υιος του ανθρωπου εκ νεκρων αναστη');
INSERT INTO `bible_verses_tr` VALUES ('566', '40', '17', '10', 'και επηρωτησαν αυτον οι μαθηται αυτου λεγοντες τι ουν οι γραμματεις λεγουσιν οτι ηλιαν δει ελθειν πρωτον');
INSERT INTO `bible_verses_tr` VALUES ('567', '40', '17', '11', 'ο δε ιησους αποκριθεις ειπεν αυτοις ηλιας μεν ερχεται πρωτον και αποκαταστησει παντα');
INSERT INTO `bible_verses_tr` VALUES ('568', '40', '17', '12', 'λεγω δε υμιν οτι ηλιας ηδη ηλθεν και ουκ επεγνωσαν αυτον αλλ εποιησαν εν αυτω οσα ηθελησαν ουτως και ο υιος του ανθρωπου μελλει πασχειν υπ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('569', '40', '17', '13', 'τοτε συνηκαν οι μαθηται οτι περι ιωαννου του βαπτιστου ειπεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('570', '40', '17', '14', 'και ελθοντων αυτων προς τον οχλον προσηλθεν αυτω ανθρωπος γονυπετων αυτω');
INSERT INTO `bible_verses_tr` VALUES ('571', '40', '17', '15', 'και λεγων κυριε ελεησον μου τον υιον οτι σεληνιαζεται και κακως πασχει πολλακις γαρ πιπτει εις το πυρ και πολλακις εις το υδωρ');
INSERT INTO `bible_verses_tr` VALUES ('572', '40', '17', '16', 'και προσηνεγκα αυτον τοις μαθηταις σου και ουκ ηδυνηθησαν αυτον θεραπευσαι');
INSERT INTO `bible_verses_tr` VALUES ('573', '40', '17', '17', 'αποκριθεις δε ο ιησους ειπεν ω γενεα απιστος και διεστραμμενη εως ποτε εσομαι μεθ υμων εως ποτε ανεξομαι υμων φερετε μοι αυτον ωδε');
INSERT INTO `bible_verses_tr` VALUES ('574', '40', '17', '18', 'και επετιμησεν αυτω ο ιησους και εξηλθεν απ αυτου το δαιμονιον και εθεραπευθη ο παις απο της ωρας εκεινης');
INSERT INTO `bible_verses_tr` VALUES ('575', '40', '17', '19', 'τοτε προσελθοντες οι μαθηται τω ιησου κατ ιδιαν ειπον δια τι ημεις ουκ ηδυνηθημεν εκβαλειν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('576', '40', '17', '20', 'ο δε ιησους ειπεν αυτοις δια την απιστιαν υμων αμην γαρ λεγω υμιν εαν εχητε πιστιν ως κοκκον σιναπεως ερειτε τω ορει τουτω μεταβηθι εντευθεν εκει και μεταβησεται και ουδεν αδυνατησει υμιν');
INSERT INTO `bible_verses_tr` VALUES ('577', '40', '17', '21', 'τουτο δε το γενος ουκ εκπορευεται ει μη εν προσευχη και νηστεια');
INSERT INTO `bible_verses_tr` VALUES ('578', '40', '17', '22', 'αναστρεφομενων δε αυτων εν τη γαλιλαια ειπεν αυτοις ο ιησους μελλει ο υιος του ανθρωπου παραδιδοσθαι εις χειρας ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('579', '40', '17', '23', 'και αποκτενουσιν αυτον και τη τριτη ημερα εγερθησεται και ελυπηθησαν σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('580', '40', '17', '24', 'ελθοντων δε αυτων εις καπερναουμ προσηλθον οι τα διδραχμα λαμβανοντες τω πετρω και ειπον ο διδασκαλος υμων ου τελει τα διδραχμα');
INSERT INTO `bible_verses_tr` VALUES ('581', '40', '17', '25', 'λεγει ναι και οτε εισηλθεν εις την οικιαν προεφθασεν αυτον ο ιησους λεγων τι σοι δοκει σιμων οι βασιλεις της γης απο τινων λαμβανουσιν τελη η κηνσον απο των υιων αυτων η απο των αλλοτριων');
INSERT INTO `bible_verses_tr` VALUES ('582', '40', '17', '26', 'λεγει αυτω ο πετρος απο των αλλοτριων εφη αυτω ο ιησους αραγε ελευθεροι εισιν οι υιοι');
INSERT INTO `bible_verses_tr` VALUES ('583', '40', '17', '27', 'ινα δε μη σκανδαλισωμεν αυτους πορευθεις εις την θαλασσαν βαλε αγκιστρον και τον αναβαντα πρωτον ιχθυν αρον και ανοιξας το στομα αυτου ευρησεις στατηρα εκεινον λαβων δος αυτοις αντι εμου και σου');
INSERT INTO `bible_verses_tr` VALUES ('584', '40', '18', '1', 'εν εκεινη τη ωρα προσηλθον οι μαθηται τω ιησου λεγοντες τις αρα μειζων εστιν εν τη βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('585', '40', '18', '2', 'και προσκαλεσαμενος ο ιησους παιδιον εστησεν αυτο εν μεσω αυτων');
INSERT INTO `bible_verses_tr` VALUES ('586', '40', '18', '3', 'και ειπεν αμην λεγω υμιν εαν μη στραφητε και γενησθε ως τα παιδια ου μη εισελθητε εις την βασιλειαν των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('587', '40', '18', '4', 'οστις ουν ταπεινωση εαυτον ως το παιδιον τουτο ουτος εστιν ο μειζων εν τη βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('588', '40', '18', '5', 'και ος εαν δεξηται παιδιον τοιουτον εν επι τω ονοματι μου εμε δεχεται');
INSERT INTO `bible_verses_tr` VALUES ('589', '40', '18', '6', 'ος δ αν σκανδαλιση ενα των μικρων τουτων των πιστευοντων εις εμε συμφερει αυτω ινα κρεμασθη μυλος ονικος επι τον τραχηλον αυτου και καταποντισθη εν τω πελαγει της θαλασσης');
INSERT INTO `bible_verses_tr` VALUES ('590', '40', '18', '7', 'ουαι τω κοσμω απο των σκανδαλων αναγκη γαρ εστιν ελθειν τα σκανδαλα πλην ουαι τω ανθρωπω εκεινω δι ου το σκανδαλον ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('591', '40', '18', '8', 'ει δε η χειρ σου η ο πους σου σκανδαλιζει σε εκκοψον αυτα και βαλε απο σου καλον σοι εστιν εισελθειν εις την ζωην χωλον η κυλλον η δυο χειρας η δυο ποδας εχοντα βληθηναι εις το πυρ το αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('592', '40', '18', '9', 'και ει ο οφθαλμος σου σκανδαλιζει σε εξελε αυτον και βαλε απο σου καλον σοι εστιν μονοφθαλμον εις την ζωην εισελθειν η δυο οφθαλμους εχοντα βληθηναι εις την γεενναν του πυρος');
INSERT INTO `bible_verses_tr` VALUES ('593', '40', '18', '10', 'ορατε μη καταφρονησητε ενος των μικρων τουτων λεγω γαρ υμιν οτι οι αγγελοι αυτων εν ουρανοις δια παντος βλεπουσιν το προσωπον του πατρος μου του εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('594', '40', '18', '11', 'ηλθεν γαρ ο υιος του ανθρωπου σωσαι το απολωλος');
INSERT INTO `bible_verses_tr` VALUES ('595', '40', '18', '12', 'τι υμιν δοκει εαν γενηται τινι ανθρωπω εκατον προβατα και πλανηθη εν εξ αυτων ουχι αφεις τα εννενηκονταεννεα επι τα ορη πορευθεις ζητει το πλανωμενον');
INSERT INTO `bible_verses_tr` VALUES ('596', '40', '18', '13', 'και εαν γενηται ευρειν αυτο αμην λεγω υμιν οτι χαιρει επ αυτω μαλλον η επι τοις εννενηκονταεννεα τοις μη πεπλανημενοις');
INSERT INTO `bible_verses_tr` VALUES ('597', '40', '18', '14', 'ουτως ουκ εστιν θελημα εμπροσθεν του πατρος υμων του εν ουρανοις ινα αποληται εις των μικρων τουτων');
INSERT INTO `bible_verses_tr` VALUES ('598', '40', '18', '15', 'εαν δε αμαρτηση εις σε ο αδελφος σου υπαγε και ελεγξον αυτον μεταξυ σου και αυτου μονου εαν σου ακουση εκερδησας τον αδελφον σου');
INSERT INTO `bible_verses_tr` VALUES ('599', '40', '18', '16', 'εαν δε μη ακουση παραλαβε μετα σου ετι ενα η δυο ινα επι στοματος δυο μαρτυρων η τριων σταθη παν ρημα');
INSERT INTO `bible_verses_tr` VALUES ('600', '40', '18', '17', 'εαν δε παρακουση αυτων ειπε τη εκκλησια εαν δε και της εκκλησιας παρακουση εστω σοι ωσπερ ο εθνικος και ο τελωνης');
INSERT INTO `bible_verses_tr` VALUES ('601', '40', '18', '18', 'αμην λεγω υμιν οσα εαν δησητε επι της γης εσται δεδεμενα εν τω ουρανω και οσα εαν λυσητε επι της γης εσται λελυμενα εν τω ουρανω');
INSERT INTO `bible_verses_tr` VALUES ('602', '40', '18', '19', 'παλιν λεγω υμιν οτι εαν δυο υμων συμφωνησωσιν επι της γης περι παντος πραγματος ου εαν αιτησωνται γενησεται αυτοις παρα του πατρος μου του εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('603', '40', '18', '20', 'ου γαρ εισιν δυο η τρεις συνηγμενοι εις το εμον ονομα εκει ειμι εν μεσω αυτων');
INSERT INTO `bible_verses_tr` VALUES ('604', '40', '18', '21', 'τοτε προσελθων αυτω ο πετρος ειπεν κυριε ποσακις αμαρτησει εις εμε ο αδελφος μου και αφησω αυτω εως επτακις');
INSERT INTO `bible_verses_tr` VALUES ('605', '40', '18', '22', 'λεγει αυτω ο ιησους ου λεγω σοι εως επτακις αλλ εως εβδομηκοντακις επτα');
INSERT INTO `bible_verses_tr` VALUES ('606', '40', '18', '23', 'δια τουτο ωμοιωθη η βασιλεια των ουρανων ανθρωπω βασιλει ος ηθελησεν συναραι λογον μετα των δουλων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('607', '40', '18', '24', 'αρξαμενου δε αυτου συναιρειν προσηνεχθη αυτω εις οφειλετης μυριων ταλαντων');
INSERT INTO `bible_verses_tr` VALUES ('608', '40', '18', '25', 'μη εχοντος δε αυτου αποδουναι εκελευσεν αυτον ο κυριος αυτου πραθηναι και την γυναικα αυτου και τα τεκνα και παντα οσα ειχεν και αποδοθηναι');
INSERT INTO `bible_verses_tr` VALUES ('609', '40', '18', '26', 'πεσων ουν ο δουλος προσεκυνει αυτω λεγων κυριε μακροθυμησον επ εμοι και παντα σοι αποδωσω');
INSERT INTO `bible_verses_tr` VALUES ('610', '40', '18', '27', 'σπλαγχνισθεις δε ο κυριος του δουλου εκεινου απελυσεν αυτον και το δανειον αφηκεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('611', '40', '18', '28', 'εξελθων δε ο δουλος εκεινος ευρεν ενα των συνδουλων αυτου ος ωφειλεν αυτω εκατον δηναρια και κρατησας αυτον επνιγεν λεγων αποδος μοι ο τι οφειλεις');
INSERT INTO `bible_verses_tr` VALUES ('612', '40', '18', '29', 'πεσων ουν ο συνδουλος αυτου εις τους ποδας αυτου παρεκαλει αυτον λεγων μακροθυμησον επ εμοι και παντα αποδωσω σοι');
INSERT INTO `bible_verses_tr` VALUES ('613', '40', '18', '30', 'ο δε ουκ ηθελεν  {VAR1: αλλα } {VAR2: αλλ } απελθων εβαλεν αυτον εις φυλακην εως ου αποδω το οφειλομενον');
INSERT INTO `bible_verses_tr` VALUES ('614', '40', '18', '31', 'ιδοντες δε οι συνδουλοι αυτου τα γενομενα ελυπηθησαν σφοδρα και ελθοντες διεσαφησαν τω κυριω αυτων παντα τα γενομενα');
INSERT INTO `bible_verses_tr` VALUES ('615', '40', '18', '32', 'τοτε προσκαλεσαμενος αυτον ο κυριος αυτου λεγει αυτω δουλε πονηρε πασαν την οφειλην εκεινην αφηκα σοι επει παρεκαλεσας με');
INSERT INTO `bible_verses_tr` VALUES ('616', '40', '18', '33', 'ουκ εδει και σε ελεησαι τον συνδουλον σου ως και εγω σε ηλεησα');
INSERT INTO `bible_verses_tr` VALUES ('617', '40', '18', '34', 'και οργισθεις ο κυριος αυτου παρεδωκεν αυτον τοις βασανισταις εως ου αποδω παν το οφειλομενον αυτω');
INSERT INTO `bible_verses_tr` VALUES ('618', '40', '18', '35', 'ουτως και ο πατηρ μου ο επουρανιος ποιησει υμιν εαν μη αφητε εκαστος τω αδελφω αυτου απο των καρδιων υμων τα παραπτωματα αυτων');
INSERT INTO `bible_verses_tr` VALUES ('619', '40', '19', '1', 'και εγενετο οτε ετελεσεν ο ιησους τους λογους τουτους μετηρεν απο της γαλιλαιας και ηλθεν εις τα ορια της ιουδαιας περαν του ιορδανου');
INSERT INTO `bible_verses_tr` VALUES ('620', '40', '19', '2', 'και ηκολουθησαν αυτω οχλοι πολλοι και εθεραπευσεν αυτους εκει');
INSERT INTO `bible_verses_tr` VALUES ('621', '40', '19', '3', 'και προσηλθον αυτω οι φαρισαιοι πειραζοντες αυτον και λεγοντες αυτω ει εξεστιν ανθρωπω απολυσαι την γυναικα αυτου κατα πασαν αιτιαν');
INSERT INTO `bible_verses_tr` VALUES ('622', '40', '19', '4', 'ο δε αποκριθεις ειπεν αυτοις ουκ ανεγνωτε οτι ο ποιησας απ αρχης αρσεν και θηλυ εποιησεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('623', '40', '19', '5', 'και ειπεν ενεκεν τουτου καταλειψει ανθρωπος τον πατερα και την μητερα και προσκολληθησεται τη γυναικι αυτου και εσονται οι δυο εις σαρκα μιαν');
INSERT INTO `bible_verses_tr` VALUES ('624', '40', '19', '6', 'ωστε ουκετι εισιν δυο αλλα σαρξ μια ο ουν ο θεος συνεζευξεν ανθρωπος μη χωριζετω');
INSERT INTO `bible_verses_tr` VALUES ('625', '40', '19', '7', 'λεγουσιν αυτω τι ουν μωσης ενετειλατο δουναι βιβλιον αποστασιου και απολυσαι αυτην');
INSERT INTO `bible_verses_tr` VALUES ('626', '40', '19', '8', 'λεγει αυτοις οτι μωσης προς την σκληροκαρδιαν υμων επετρεψεν υμιν απολυσαι τας γυναικας υμων απ αρχης δε ου γεγονεν ουτως');
INSERT INTO `bible_verses_tr` VALUES ('627', '40', '19', '9', 'λεγω δε υμιν οτι ος αν απολυση την γυναικα αυτου ει μη επι πορνεια και γαμηση αλλην μοιχαται και ο απολελυμενην γαμησας μοιχαται');
INSERT INTO `bible_verses_tr` VALUES ('628', '40', '19', '10', 'λεγουσιν αυτω οι μαθηται αυτου ει ουτως εστιν η αιτια του ανθρωπου μετα της γυναικος ου συμφερει γαμησαι');
INSERT INTO `bible_verses_tr` VALUES ('629', '40', '19', '11', 'ο δε ειπεν αυτοις ου παντες χωρουσιν τον λογον τουτον αλλ οις δεδοται');
INSERT INTO `bible_verses_tr` VALUES ('630', '40', '19', '12', 'εισιν γαρ ευνουχοι οιτινες εκ κοιλιας μητρος εγεννηθησαν ουτως και εισιν ευνουχοι οιτινες ευνουχισθησαν υπο των ανθρωπων και εισιν ευνουχοι οιτινες ευνουχισαν εαυτους δια την βασιλειαν των ουρανων ο δυναμενος χωρειν χωρειτω');
INSERT INTO `bible_verses_tr` VALUES ('631', '40', '19', '13', 'τοτε προσηνεχθη αυτω παιδια ινα τας χειρας επιθη αυτοις και προσευξηται οι δε μαθηται επετιμησαν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('632', '40', '19', '14', 'ο δε ιησους ειπεν αφετε τα παιδια και μη κωλυετε αυτα ελθειν προς με των γαρ τοιουτων εστιν η βασιλεια των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('633', '40', '19', '15', 'και επιθεις αυτοις τας χειρας επορευθη εκειθεν');
INSERT INTO `bible_verses_tr` VALUES ('634', '40', '19', '16', 'και ιδου εις προσελθων ειπεν αυτω διδασκαλε αγαθε τι αγαθον ποιησω ινα εχω ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('635', '40', '19', '17', 'ο δε ειπεν αυτω τι με λεγεις αγαθον ουδεις αγαθος ει μη εις ο θεος ει δε θελεις εισελθειν εις την ζωην τηρησον τας εντολας');
INSERT INTO `bible_verses_tr` VALUES ('636', '40', '19', '18', 'λεγει αυτω ποιας ο δε ιησους ειπεν το ου φονευσεις ου μοιχευσεις ου κλεψεις ου ψευδομαρτυρησεις');
INSERT INTO `bible_verses_tr` VALUES ('637', '40', '19', '19', 'τιμα τον πατερα σου και την μητερα και αγαπησεις τον πλησιον σου ως σεαυτον');
INSERT INTO `bible_verses_tr` VALUES ('638', '40', '19', '20', 'λεγει αυτω ο νεανισκος παντα ταυτα εφυλαξαμην εκ νεοτητος μου τι ετι υστερω');
INSERT INTO `bible_verses_tr` VALUES ('639', '40', '19', '21', 'εφη αυτω ο ιησους ει θελεις τελειος ειναι υπαγε πωλησον σου τα υπαρχοντα και δος πτωχοις και εξεις θησαυρον εν ουρανω και δευρο ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('640', '40', '19', '22', 'ακουσας δε ο νεανισκος τον λογον απηλθεν λυπουμενος ην γαρ εχων κτηματα πολλα');
INSERT INTO `bible_verses_tr` VALUES ('641', '40', '19', '23', 'ο δε ιησους ειπεν τοις μαθηταις αυτου αμην λεγω υμιν οτι δυσκολως πλουσιος εισελευσεται εις την βασιλειαν των ουρανων');
INSERT INTO `bible_verses_tr` VALUES ('642', '40', '19', '24', 'παλιν δε λεγω υμιν ευκοπωτερον εστιν καμηλον δια τρυπηματος ραφιδος διελθειν η πλουσιον εις την βασιλειαν του θεου εισελθειν');
INSERT INTO `bible_verses_tr` VALUES ('643', '40', '19', '25', 'ακουσαντες δε οι μαθηται αυτου εξεπλησσοντο σφοδρα λεγοντες τις αρα δυναται σωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('644', '40', '19', '26', 'εμβλεψας δε ο ιησους ειπεν αυτοις παρα ανθρωποις τουτο αδυνατον εστιν παρα δε θεω παντα δυνατα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('645', '40', '19', '27', 'τοτε αποκριθεις ο πετρος ειπεν αυτω ιδου ημεις αφηκαμεν παντα και ηκολουθησαμεν σοι τι αρα εσται ημιν');
INSERT INTO `bible_verses_tr` VALUES ('646', '40', '19', '28', 'ο δε ιησους ειπεν αυτοις αμην λεγω υμιν οτι υμεις οι ακολουθησαντες μοι εν τη παλιγγενεσια οταν καθιση ο υιος του ανθρωπου επι θρονου δοξης αυτου καθισεσθε και υμεις επι δωδεκα θρονους κρινοντες τας δωδεκα φυλας του ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('647', '40', '19', '29', 'και πας ος αφηκεν οικιας η αδελφους η αδελφας η πατερα η μητερα η γυναικα η τεκνα η αγρους ενεκεν του ονοματος μου εκατονταπλασιονα ληψεται και ζωην αιωνιον κληρονομησει');
INSERT INTO `bible_verses_tr` VALUES ('648', '40', '19', '30', 'πολλοι δε εσονται πρωτοι εσχατοι και εσχατοι πρωτοι');
INSERT INTO `bible_verses_tr` VALUES ('649', '40', '20', '1', 'ομοια γαρ εστιν η βασιλεια των ουρανων ανθρωπω οικοδεσποτη οστις εξηλθεν αμα πρωι μισθωσασθαι εργατας εις τον αμπελωνα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('650', '40', '20', '2', 'συμφωνησας δε μετα των εργατων εκ δηναριου την ημεραν απεστειλεν αυτους εις τον αμπελωνα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('651', '40', '20', '3', 'και εξελθων περι την τριτην ωραν ειδεν αλλους εστωτας εν τη αγορα αργους');
INSERT INTO `bible_verses_tr` VALUES ('652', '40', '20', '4', 'κακεινοις ειπεν υπαγετε και υμεις εις τον αμπελωνα και ο εαν η δικαιον δωσω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('653', '40', '20', '5', 'οι δε απηλθον παλιν εξελθων περι εκτην και εννατην ωραν εποιησεν ωσαυτως');
INSERT INTO `bible_verses_tr` VALUES ('654', '40', '20', '6', 'περι δε την ενδεκατην ωραν εξελθων ευρεν αλλους εστωτας αργους και λεγει αυτοις τι ωδε εστηκατε ολην την ημεραν αργοι');
INSERT INTO `bible_verses_tr` VALUES ('655', '40', '20', '7', 'λεγουσιν αυτω οτι ουδεις ημας εμισθωσατο λεγει αυτοις υπαγετε και υμεις εις τον αμπελωνα και ο εαν η δικαιον ληψεσθε');
INSERT INTO `bible_verses_tr` VALUES ('656', '40', '20', '8', 'οψιας δε γενομενης λεγει ο κυριος του αμπελωνος τω επιτροπω αυτου καλεσον τους εργατας και αποδος αυτοις τον μισθον αρξαμενος απο των εσχατων εως των πρωτων');
INSERT INTO `bible_verses_tr` VALUES ('657', '40', '20', '9', 'και ελθοντες οι περι την ενδεκατην ωραν ελαβον ανα δηναριον');
INSERT INTO `bible_verses_tr` VALUES ('658', '40', '20', '10', 'ελθοντες δε οι πρωτοι ενομισαν οτι πλειονα ληψονται και ελαβον και αυτοι ανα δηναριον');
INSERT INTO `bible_verses_tr` VALUES ('659', '40', '20', '11', 'λαβοντες δε εγογγυζον κατα του οικοδεσποτου');
INSERT INTO `bible_verses_tr` VALUES ('660', '40', '20', '12', 'λεγοντες οτι ουτοι οι εσχατοι μιαν ωραν εποιησαν και ισους ημιν αυτους εποιησας τοις βαστασασιν το βαρος της ημερας και τον καυσωνα');
INSERT INTO `bible_verses_tr` VALUES ('661', '40', '20', '13', 'ο δε αποκριθεις ειπεν ενι αυτων εταιρε ουκ αδικω σε ουχι δηναριου συνεφωνησας μοι');
INSERT INTO `bible_verses_tr` VALUES ('662', '40', '20', '14', 'αρον το σον και υπαγε θελω δε τουτω τω εσχατω δουναι ως και σοι');
INSERT INTO `bible_verses_tr` VALUES ('663', '40', '20', '15', 'η ουκ εξεστιν μοι ποιησαι ο θελω εν τοις εμοις ει ο οφθαλμος σου πονηρος εστιν οτι εγω αγαθος ειμι');
INSERT INTO `bible_verses_tr` VALUES ('664', '40', '20', '16', 'ουτως εσονται οι εσχατοι πρωτοι και οι πρωτοι εσχατοι πολλοι γαρ εισιν κλητοι ολιγοι δε εκλεκτοι');
INSERT INTO `bible_verses_tr` VALUES ('665', '40', '20', '17', 'και αναβαινων ο ιησους εις ιεροσολυμα παρελαβεν τους δωδεκα μαθητας κατ ιδιαν εν τη οδω και ειπεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('666', '40', '20', '18', 'ιδου αναβαινομεν εις ιεροσολυμα και ο υιος του ανθρωπου παραδοθησεται τοις αρχιερευσιν και γραμματευσιν και κατακρινουσιν αυτον θανατω');
INSERT INTO `bible_verses_tr` VALUES ('667', '40', '20', '19', 'και παραδωσουσιν αυτον τοις εθνεσιν εις το εμπαιξαι και μαστιγωσαι και σταυρωσαι και τη τριτη ημερα αναστησεται');
INSERT INTO `bible_verses_tr` VALUES ('668', '40', '20', '20', 'τοτε προσηλθεν αυτω η μητηρ των υιων ζεβεδαιου μετα των υιων αυτης προσκυνουσα και αιτουσα τι παρ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('669', '40', '20', '21', 'ο δε ειπεν αυτη τι θελεις λεγει αυτω ειπε ινα καθισωσιν ουτοι οι δυο υιοι μου εις εκ δεξιων σου και εις εξ ευωνυμων εν τη βασιλεια σου');
INSERT INTO `bible_verses_tr` VALUES ('670', '40', '20', '22', 'αποκριθεις δε ο ιησους ειπεν ουκ οιδατε τι αιτεισθε δυνασθε πιειν το ποτηριον ο εγω μελλω πινειν και το βαπτισμα ο εγω βαπτιζομαι βαπτισθηναι λεγουσιν αυτω δυναμεθα');
INSERT INTO `bible_verses_tr` VALUES ('671', '40', '20', '23', 'και λεγει αυτοις το μεν ποτηριον μου πιεσθε και το βαπτισμα ο εγω βαπτιζομαι βαπτισθησεσθε το δε καθισαι εκ δεξιων μου και εξ ευωνυμων μου ουκ εστιν εμον δουναι αλλ οις ητοιμασται υπο του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('672', '40', '20', '24', 'και ακουσαντες οι δεκα ηγανακτησαν περι των δυο αδελφων');
INSERT INTO `bible_verses_tr` VALUES ('673', '40', '20', '25', 'ο δε ιησους προσκαλεσαμενος αυτους ειπεν οιδατε οτι οι αρχοντες των εθνων κατακυριευουσιν αυτων και οι μεγαλοι κατεξουσιαζουσιν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('674', '40', '20', '26', 'ουχ ουτως δε εσται εν υμιν αλλ ος εαν θελη εν υμιν μεγας γενεσθαι εστω υμων διακονος');
INSERT INTO `bible_verses_tr` VALUES ('675', '40', '20', '27', 'και ος εαν θελη εν υμιν ειναι πρωτος εστω υμων δουλος');
INSERT INTO `bible_verses_tr` VALUES ('676', '40', '20', '28', 'ωσπερ ο υιος του ανθρωπου ουκ ηλθεν διακονηθηναι αλλα διακονησαι και δουναι την ψυχην αυτου λυτρον αντι πολλων');
INSERT INTO `bible_verses_tr` VALUES ('677', '40', '20', '29', 'και εκπορευομενων αυτων απο ιεριχω ηκολουθησεν αυτω οχλος πολυς');
INSERT INTO `bible_verses_tr` VALUES ('678', '40', '20', '30', 'και ιδου δυο τυφλοι καθημενοι παρα την οδον ακουσαντες οτι ιησους παραγει εκραξαν λεγοντες ελεησον ημας κυριε υιος δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('679', '40', '20', '31', 'ο δε οχλος επετιμησεν αυτοις ινα σιωπησωσιν οι δε μειζον εκραζον λεγοντες ελεησον ημας κυριε υιος δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('680', '40', '20', '32', 'και στας ο ιησους εφωνησεν αυτους και ειπεν τι θελετε ποιησω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('681', '40', '20', '33', 'λεγουσιν αυτω κυριε ινα ανοιχθωσιν ημων οι οφθαλμοι');
INSERT INTO `bible_verses_tr` VALUES ('682', '40', '20', '34', 'σπλαγχνισθεις δε ο ιησους ηψατο των οφθαλμων αυτων και ευθεως ανεβλεψαν αυτων οι οφθαλμοι και ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('683', '40', '21', '1', 'και οτε ηγγισαν εις ιεροσολυμα και ηλθον εις βηθφαγη προς το ορος των ελαιων τοτε ο ιησους απεστειλεν δυο μαθητας');
INSERT INTO `bible_verses_tr` VALUES ('684', '40', '21', '2', 'λεγων αυτοις πορευθητε εις την κωμην την απεναντι υμων και ευθεως ευρησετε ονον δεδεμενην και πωλον μετ αυτης λυσαντες αγαγετε μοι');
INSERT INTO `bible_verses_tr` VALUES ('685', '40', '21', '3', 'και εαν τις υμιν ειπη τι ερειτε οτι ο κυριος αυτων χρειαν εχει ευθεως δε αποστελει αυτους');
INSERT INTO `bible_verses_tr` VALUES ('686', '40', '21', '4', 'τουτο δε ολον γεγονεν ινα πληρωθη το ρηθεν δια του προφητου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('687', '40', '21', '5', 'ειπατε τη θυγατρι σιων ιδου ο βασιλευς σου ερχεται σοι πραυς και επιβεβηκως επι ονον και πωλον υιον υποζυγιου');
INSERT INTO `bible_verses_tr` VALUES ('688', '40', '21', '6', 'πορευθεντες δε οι μαθηται και ποιησαντες καθως προσεταξεν αυτοις ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('689', '40', '21', '7', 'ηγαγον την ονον και τον πωλον και επεθηκαν επανω αυτων τα ιματια αυτων και  {VAR1: επεκαθισεν } {VAR2: επεκαθισαν } επανω αυτων');
INSERT INTO `bible_verses_tr` VALUES ('690', '40', '21', '8', 'ο δε πλειστος οχλος εστρωσαν εαυτων τα ιματια εν τη οδω αλλοι δε εκοπτον κλαδους απο των δενδρων και εστρωννυον εν τη οδω');
INSERT INTO `bible_verses_tr` VALUES ('691', '40', '21', '9', 'οι δε οχλοι οι προαγοντες και οι ακολουθουντες εκραζον λεγοντες ωσαννα τω υιω δαβιδ ευλογημενος ο ερχομενος εν ονοματι κυριου ωσαννα εν τοις υψιστοις');
INSERT INTO `bible_verses_tr` VALUES ('692', '40', '21', '10', 'και εισελθοντος αυτου εις ιεροσολυμα εσεισθη πασα η πολις λεγουσα τις εστιν ουτος');
INSERT INTO `bible_verses_tr` VALUES ('693', '40', '21', '11', 'οι δε οχλοι ελεγον ουτος εστιν ιησους ο προφητης ο απο  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('694', '40', '21', '12', 'και εισηλθεν ο ιησους εις το ιερον του θεου και εξεβαλεν παντας τους πωλουντας και αγοραζοντας εν τω ιερω και τας τραπεζας των κολλυβιστων κατεστρεψεν και τας καθεδρας των πωλουντων τας περιστερας');
INSERT INTO `bible_verses_tr` VALUES ('695', '40', '21', '13', 'και λεγει αυτοις γεγραπται ο οικος μου οικος προσευχης κληθησεται υμεις δε αυτον εποιησατε σπηλαιον ληστων');
INSERT INTO `bible_verses_tr` VALUES ('696', '40', '21', '14', 'και προσηλθον αυτω τυφλοι και χωλοι εν τω ιερω και εθεραπευσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('697', '40', '21', '15', 'ιδοντες δε οι αρχιερεις και οι γραμματεις τα θαυμασια α εποιησεν και τους παιδας κραζοντας εν τω ιερω και λεγοντας ωσαννα τω υιω δαβιδ ηγανακτησαν');
INSERT INTO `bible_verses_tr` VALUES ('698', '40', '21', '16', 'και ειπον αυτω ακουεις τι ουτοι λεγουσιν ο δε ιησους λεγει αυτοις ναι ουδεποτε ανεγνωτε οτι εκ στοματος νηπιων και θηλαζοντων κατηρτισω αινον');
INSERT INTO `bible_verses_tr` VALUES ('699', '40', '21', '17', 'και καταλιπων αυτους εξηλθεν εξω της πολεως εις βηθανιαν και ηυλισθη εκει');
INSERT INTO `bible_verses_tr` VALUES ('700', '40', '21', '18', 'πρωιας δε επαναγων εις την πολιν επεινασεν');
INSERT INTO `bible_verses_tr` VALUES ('701', '40', '21', '19', 'και ιδων συκην μιαν επι της οδου ηλθεν επ αυτην και ουδεν ευρεν εν αυτη ει μη φυλλα μονον και λεγει αυτη μηκετι εκ σου καρπος γενηται εις τον αιωνα και εξηρανθη παραχρημα η συκη');
INSERT INTO `bible_verses_tr` VALUES ('702', '40', '21', '20', 'και ιδοντες οι μαθηται εθαυμασαν λεγοντες πως παραχρημα εξηρανθη η συκη');
INSERT INTO `bible_verses_tr` VALUES ('703', '40', '21', '21', 'αποκριθεις δε ο ιησους ειπεν αυτοις αμην λεγω υμιν εαν εχητε πιστιν και μη διακριθητε ου μονον το της συκης ποιησετε αλλα καν τω ορει τουτω ειπητε αρθητι και βληθητι εις την θαλασσαν γενησεται');
INSERT INTO `bible_verses_tr` VALUES ('704', '40', '21', '22', 'και παντα οσα αν αιτησητε εν τη προσευχη πιστευοντες ληψεσθε');
INSERT INTO `bible_verses_tr` VALUES ('705', '40', '21', '23', 'και ελθοντι αυτω εις το ιερον προσηλθον αυτω διδασκοντι οι αρχιερεις και οι πρεσβυτεροι του λαου λεγοντες εν ποια εξουσια ταυτα ποιεις και τις σοι εδωκεν την εξουσιαν ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('706', '40', '21', '24', 'αποκριθεις δε ο ιησους ειπεν αυτοις ερωτησω υμας καγω λογον ενα ον εαν ειπητε μοι καγω υμιν ερω εν ποια εξουσια ταυτα ποιω');
INSERT INTO `bible_verses_tr` VALUES ('707', '40', '21', '25', 'το βαπτισμα ιωαννου ποθεν ην εξ ουρανου η εξ ανθρωπων οι δε διελογιζοντο παρ εαυτοις λεγοντες εαν ειπωμεν εξ ουρανου ερει ημιν δια τι ουν ουκ επιστευσατε αυτω');
INSERT INTO `bible_verses_tr` VALUES ('708', '40', '21', '26', 'εαν δε ειπωμεν εξ ανθρωπων φοβουμεθα τον οχλον παντες γαρ εχουσιν τον ιωαννην ως προφητην');
INSERT INTO `bible_verses_tr` VALUES ('709', '40', '21', '27', 'και αποκριθεντες τω ιησου ειπον ουκ οιδαμεν εφη αυτοις και αυτος ουδε εγω λεγω υμιν εν ποια εξουσια ταυτα ποιω');
INSERT INTO `bible_verses_tr` VALUES ('710', '40', '21', '28', 'τι δε υμιν δοκει ανθρωπος ειχεν τεκνα δυο και προσελθων τω πρωτω ειπεν τεκνον υπαγε σημερον εργαζου εν τω αμπελωνι μου');
INSERT INTO `bible_verses_tr` VALUES ('711', '40', '21', '29', 'ο δε αποκριθεις ειπεν ου θελω υστερον δε μεταμεληθεις απηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('712', '40', '21', '30', 'και προσελθων τω δευτερω ειπεν ωσαυτως ο δε αποκριθεις ειπεν εγω κυριε και ουκ απηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('713', '40', '21', '31', 'τις εκ των δυο εποιησεν το θελημα του πατρος λεγουσιν αυτω ο πρωτος λεγει αυτοις ο ιησους αμην λεγω υμιν οτι οι τελωναι και αι πορναι προαγουσιν υμας εις την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('714', '40', '21', '32', 'ηλθεν γαρ προς υμας ιωαννης εν οδω δικαιοσυνης και ουκ επιστευσατε αυτω οι δε τελωναι και αι πορναι επιστευσαν αυτω υμεις δε ιδοντες ου μετεμεληθητε υστερον του πιστευσαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('715', '40', '21', '33', 'αλλην παραβολην ακουσατε ανθρωπος τις ην οικοδεσποτης οστις εφυτευσεν αμπελωνα και φραγμον αυτω περιεθηκεν και ωρυξεν εν αυτω ληνον και ωκοδομησεν πυργον και εξεδοτο αυτον γεωργοις και απεδημησεν');
INSERT INTO `bible_verses_tr` VALUES ('716', '40', '21', '34', 'οτε δε ηγγισεν ο καιρος των καρπων απεστειλεν τους δουλους αυτου προς τους γεωργους λαβειν τους καρπους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('717', '40', '21', '35', 'και λαβοντες οι γεωργοι τους δουλους αυτου ον μεν εδειραν ον δε απεκτειναν ον δε ελιθοβολησαν');
INSERT INTO `bible_verses_tr` VALUES ('718', '40', '21', '36', 'παλιν απεστειλεν αλλους δουλους πλειονας των πρωτων και εποιησαν αυτοις ωσαυτως');
INSERT INTO `bible_verses_tr` VALUES ('719', '40', '21', '37', 'υστερον δε απεστειλεν προς αυτους τον υιον αυτου λεγων εντραπησονται τον υιον μου');
INSERT INTO `bible_verses_tr` VALUES ('720', '40', '21', '38', 'οι δε γεωργοι ιδοντες τον υιον ειπον εν εαυτοις ουτος εστιν ο κληρονομος δευτε αποκτεινωμεν αυτον και κατασχωμεν την κληρονομιαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('721', '40', '21', '39', 'και λαβοντες αυτον εξεβαλον εξω του αμπελωνος και απεκτειναν');
INSERT INTO `bible_verses_tr` VALUES ('722', '40', '21', '40', 'οταν ουν ελθη ο κυριος του αμπελωνος τι ποιησει τοις γεωργοις εκεινοις');
INSERT INTO `bible_verses_tr` VALUES ('723', '40', '21', '41', 'λεγουσιν αυτω κακους κακως απολεσει αυτους και τον αμπελωνα εκδοσεται αλλοις γεωργοις οιτινες αποδωσουσιν αυτω τους καρπους εν τοις καιροις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('724', '40', '21', '42', 'λεγει αυτοις ο ιησους ουδεποτε ανεγνωτε εν ταις γραφαις λιθον ον απεδοκιμασαν οι οικοδομουντες ουτος εγενηθη εις κεφαλην γωνιας παρα κυριου εγενετο αυτη και εστιν θαυμαστη εν οφθαλμοις ημων');
INSERT INTO `bible_verses_tr` VALUES ('725', '40', '21', '43', 'δια τουτο λεγω υμιν οτι αρθησεται αφ υμων η βασιλεια του θεου και δοθησεται εθνει ποιουντι τους καρπους αυτης');
INSERT INTO `bible_verses_tr` VALUES ('726', '40', '21', '44', 'και ο πεσων επι τον λιθον τουτον συνθλασθησεται εφ ον δ αν πεση λικμησει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('727', '40', '21', '45', 'και ακουσαντες οι αρχιερεις και οι φαρισαιοι τας παραβολας αυτου εγνωσαν οτι περι αυτων λεγει');
INSERT INTO `bible_verses_tr` VALUES ('728', '40', '21', '46', 'και ζητουντες αυτον κρατησαι εφοβηθησαν τους οχλους επειδη ως προφητην αυτον ειχον');
INSERT INTO `bible_verses_tr` VALUES ('729', '40', '22', '1', 'και αποκριθεις ο ιησους παλιν ειπεν αυτοις εν παραβολαις λεγων');
INSERT INTO `bible_verses_tr` VALUES ('730', '40', '22', '2', 'ωμοιωθη η βασιλεια των ουρανων ανθρωπω βασιλει οστις εποιησεν γαμους τω υιω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('731', '40', '22', '3', 'και απεστειλεν τους δουλους αυτου καλεσαι τους κεκλημενους εις τους γαμους και ουκ ηθελον ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('732', '40', '22', '4', 'παλιν απεστειλεν αλλους δουλους λεγων ειπατε τοις κεκλημενοις ιδου το αριστον μου ητοιμασα οι ταυροι μου και τα σιτιστα τεθυμενα και παντα ετοιμα δευτε εις τους γαμους');
INSERT INTO `bible_verses_tr` VALUES ('733', '40', '22', '5', 'οι δε αμελησαντες απηλθον ο μεν εις τον ιδιον αγρον ο δε εις την εμποριαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('734', '40', '22', '6', 'οι δε λοιποι κρατησαντες τους δουλους αυτου υβρισαν και απεκτειναν');
INSERT INTO `bible_verses_tr` VALUES ('735', '40', '22', '7', 'ακουσας δε ο βασιλευς ωργισθη και πεμψας τα στρατευματα αυτου απωλεσεν τους φονεις εκεινους και την πολιν αυτων ενεπρησεν');
INSERT INTO `bible_verses_tr` VALUES ('736', '40', '22', '8', 'τοτε λεγει τοις δουλοις αυτου ο μεν γαμος ετοιμος εστιν οι δε κεκλημενοι ουκ ησαν αξιοι');
INSERT INTO `bible_verses_tr` VALUES ('737', '40', '22', '9', 'πορευεσθε ουν επι τας διεξοδους των οδων και οσους αν ευρητε καλεσατε εις τους γαμους');
INSERT INTO `bible_verses_tr` VALUES ('738', '40', '22', '10', 'και εξελθοντες οι δουλοι εκεινοι εις τας οδους συνηγαγον παντας οσους ευρον πονηρους τε και αγαθους και επλησθη ο γαμος ανακειμενων');
INSERT INTO `bible_verses_tr` VALUES ('739', '40', '22', '11', 'εισελθων δε ο βασιλευς θεασασθαι τους ανακειμενους ειδεν εκει ανθρωπον ουκ ενδεδυμενον ενδυμα γαμου');
INSERT INTO `bible_verses_tr` VALUES ('740', '40', '22', '12', 'και λεγει αυτω εταιρε πως εισηλθες ωδε μη εχων ενδυμα γαμου ο δε εφιμωθη');
INSERT INTO `bible_verses_tr` VALUES ('741', '40', '22', '13', 'τοτε ειπεν ο βασιλευς τοις διακονοις δησαντες αυτου ποδας και χειρας αρατε αυτον και εκβαλετε εις το σκοτος το εξωτερον εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('742', '40', '22', '14', 'πολλοι γαρ εισιν κλητοι ολιγοι δε εκλεκτοι');
INSERT INTO `bible_verses_tr` VALUES ('743', '40', '22', '15', 'τοτε πορευθεντες οι φαρισαιοι συμβουλιον ελαβον οπως αυτον παγιδευσωσιν εν λογω');
INSERT INTO `bible_verses_tr` VALUES ('744', '40', '22', '16', 'και αποστελλουσιν αυτω τους μαθητας αυτων μετα των ηρωδιανων λεγοντες διδασκαλε οιδαμεν οτι αληθης ει και την οδον του θεου εν αληθεια διδασκεις και ου μελει σοι περι ουδενος ου γαρ βλεπεις εις προσωπον ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('745', '40', '22', '17', 'ειπε ουν ημιν τι σοι δοκει εξεστιν δουναι κηνσον καισαρι η ου');
INSERT INTO `bible_verses_tr` VALUES ('746', '40', '22', '18', 'γνους δε ο ιησους την πονηριαν αυτων ειπεν τι με πειραζετε υποκριται');
INSERT INTO `bible_verses_tr` VALUES ('747', '40', '22', '19', 'επιδειξατε μοι το νομισμα του κηνσου οι δε προσηνεγκαν αυτω δηναριον');
INSERT INTO `bible_verses_tr` VALUES ('748', '40', '22', '20', 'και λεγει αυτοις τινος η εικων αυτη και η επιγραφη');
INSERT INTO `bible_verses_tr` VALUES ('749', '40', '22', '21', 'λεγουσιν αυτω καισαρος τοτε λεγει αυτοις αποδοτε ουν τα καισαρος καισαρι και τα του θεου τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('750', '40', '22', '22', 'και ακουσαντες εθαυμασαν και αφεντες αυτον απηλθον');
INSERT INTO `bible_verses_tr` VALUES ('751', '40', '22', '23', 'εν εκεινη τη ημερα προσηλθον αυτω σαδδουκαιοι οι λεγοντες μη ειναι αναστασιν και επηρωτησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('752', '40', '22', '24', 'λεγοντες διδασκαλε μωσης ειπεν εαν τις αποθανη μη εχων τεκνα επιγαμβρευσει ο αδελφος αυτου την γυναικα αυτου και αναστησει σπερμα τω αδελφω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('753', '40', '22', '25', 'ησαν δε παρ ημιν επτα αδελφοι και ο πρωτος γαμησας ετελευτησεν και μη εχων σπερμα αφηκεν την γυναικα αυτου τω αδελφω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('754', '40', '22', '26', 'ομοιως και ο δευτερος και ο τριτος εως των επτα');
INSERT INTO `bible_verses_tr` VALUES ('755', '40', '22', '27', 'υστερον δε παντων απεθανεν και η γυνη');
INSERT INTO `bible_verses_tr` VALUES ('756', '40', '22', '28', 'εν τη ουν αναστασει τινος των επτα εσται γυνη παντες γαρ εσχον αυτην');
INSERT INTO `bible_verses_tr` VALUES ('757', '40', '22', '29', 'αποκριθεις δε ο ιησους ειπεν αυτοις πλανασθε μη ειδοτες τας γραφας μηδε την δυναμιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('758', '40', '22', '30', 'εν γαρ τη αναστασει ουτε γαμουσιν ουτε εκγαμιζονται αλλ ως αγγελοι του θεου εν ουρανω εισιν');
INSERT INTO `bible_verses_tr` VALUES ('759', '40', '22', '31', 'περι δε της αναστασεως των νεκρων ουκ ανεγνωτε το ρηθεν υμιν υπο του θεου λεγοντος');
INSERT INTO `bible_verses_tr` VALUES ('760', '40', '22', '32', 'εγω ειμι ο θεος αβρααμ και ο θεος ισαακ και ο θεος ιακωβ ουκ εστιν ο θεος θεος νεκρων αλλα ζωντων');
INSERT INTO `bible_verses_tr` VALUES ('761', '40', '22', '33', 'και ακουσαντες οι οχλοι εξεπλησσοντο επι τη διδαχη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('762', '40', '22', '34', 'οι δε φαρισαιοι ακουσαντες οτι εφιμωσεν τους σαδδουκαιους συνηχθησαν επι το αυτο');
INSERT INTO `bible_verses_tr` VALUES ('763', '40', '22', '35', 'και επηρωτησεν εις εξ αυτων νομικος πειραζων αυτον και λεγων');
INSERT INTO `bible_verses_tr` VALUES ('764', '40', '22', '36', 'διδασκαλε ποια εντολη μεγαλη εν τω νομω');
INSERT INTO `bible_verses_tr` VALUES ('765', '40', '22', '37', 'ο δε ιησους ειπεν αυτω αγαπησεις κυριον τον θεον σου εν ολη τη καρδια σου και εν ολη τη ψυχη σου και εν ολη τη διανοια σου');
INSERT INTO `bible_verses_tr` VALUES ('766', '40', '22', '38', 'αυτη εστιν πρωτη και μεγαλη εντολη');
INSERT INTO `bible_verses_tr` VALUES ('767', '40', '22', '39', 'δευτερα δε ομοια αυτη αγαπησεις τον πλησιον σου ως σεαυτον');
INSERT INTO `bible_verses_tr` VALUES ('768', '40', '22', '40', 'εν ταυταις ταις δυσιν εντολαις ολος ο νομος και οι προφηται κρεμανται');
INSERT INTO `bible_verses_tr` VALUES ('769', '40', '22', '41', 'συνηγμενων δε των φαρισαιων επηρωτησεν αυτους ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('770', '40', '22', '42', 'λεγων τι υμιν δοκει περι του χριστου τινος υιος εστιν λεγουσιν αυτω του δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('771', '40', '22', '43', 'λεγει αυτοις πως ουν δαβιδ εν πνευματι κυριον αυτον καλει λεγων');
INSERT INTO `bible_verses_tr` VALUES ('772', '40', '22', '44', 'ειπεν ο κυριος τω κυριω μου καθου εκ δεξιων μου εως αν θω τους εχθρους σου υποποδιον των ποδων σου');
INSERT INTO `bible_verses_tr` VALUES ('773', '40', '22', '45', 'ει ουν δαβιδ καλει αυτον κυριον πως υιος αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('774', '40', '22', '46', 'και ουδεις εδυνατο αυτω αποκριθηναι λογον ουδε ετολμησεν τις απ εκεινης της ημερας επερωτησαι αυτον ουκετι');
INSERT INTO `bible_verses_tr` VALUES ('775', '40', '23', '1', 'τοτε ο ιησους ελαλησεν τοις οχλοις και τοις μαθηταις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('776', '40', '23', '2', 'λεγων επι της μωσεως καθεδρας εκαθισαν οι γραμματεις και οι φαρισαιοι');
INSERT INTO `bible_verses_tr` VALUES ('777', '40', '23', '3', 'παντα ουν οσα αν ειπωσιν υμιν τηρειν τηρειτε και ποιειτε κατα δε τα εργα αυτων μη ποιειτε λεγουσιν γαρ και ου ποιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('778', '40', '23', '4', 'δεσμευουσιν γαρ φορτια βαρεα και δυσβαστακτα και επιτιθεασιν επι τους ωμους των ανθρωπων τω δε δακτυλω αυτων ου θελουσιν κινησαι αυτα');
INSERT INTO `bible_verses_tr` VALUES ('779', '40', '23', '5', 'παντα δε τα εργα αυτων ποιουσιν προς το θεαθηναι τοις ανθρωποις πλατυνουσιν δε τα φυλακτηρια αυτων και μεγαλυνουσιν τα κρασπεδα των ιματιων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('780', '40', '23', '6', 'φιλουσιν τε την πρωτοκλισιαν εν τοις δειπνοις και τας πρωτοκαθεδριας εν ταις συναγωγαις');
INSERT INTO `bible_verses_tr` VALUES ('781', '40', '23', '7', 'και τους ασπασμους εν ταις αγοραις και καλεισθαι υπο των ανθρωπων ραββι ραββι');
INSERT INTO `bible_verses_tr` VALUES ('782', '40', '23', '8', 'υμεις δε μη κληθητε ραββι εις γαρ εστιν υμων ο καθηγητης ο χριστος παντες δε υμεις αδελφοι εστε');
INSERT INTO `bible_verses_tr` VALUES ('783', '40', '23', '9', 'και πατερα μη καλεσητε υμων επι της γης εις γαρ εστιν ο πατηρ υμων ο εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('784', '40', '23', '10', 'μηδε κληθητε καθηγηται εις γαρ υμων εστιν ο καθηγητης ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('785', '40', '23', '11', 'ο δε μειζων υμων εσται υμων διακονος');
INSERT INTO `bible_verses_tr` VALUES ('786', '40', '23', '12', 'οστις δε υψωσει εαυτον ταπεινωθησεται και οστις ταπεινωσει εαυτον υψωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('787', '40', '23', '13', '{VAR1: ουαι δε } {VAR2: ουαι } υμιν γραμματεις και φαρισαιοι υποκριται οτι κατεσθιετε τας οικιας των χηρων και προφασει μακρα προσευχομενοι δια τουτο ληψεσθε περισσοτερον κριμα');
INSERT INTO `bible_verses_tr` VALUES ('788', '40', '23', '14', '{VAR1: ουαι } {VAR2: ουαι δε } υμιν γραμματεις και φαρισαιοι υποκριται οτι κλειετε την βασιλειαν των ουρανων εμπροσθεν των ανθρωπων υμεις γαρ ουκ εισερχεσθε ουδε τους εισερχομενους αφιετε εισελθειν');
INSERT INTO `bible_verses_tr` VALUES ('789', '40', '23', '15', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι περιαγετε την θαλασσαν και την ξηραν ποιησαι ενα προσηλυτον και οταν γενηται ποιειτε αυτον υιον γεεννης διπλοτερον υμων');
INSERT INTO `bible_verses_tr` VALUES ('790', '40', '23', '16', 'ουαι υμιν οδηγοι τυφλοι οι λεγοντες ος αν ομοση εν τω ναω ουδεν εστιν ος δ αν ομοση εν τω χρυσω του ναου οφειλει');
INSERT INTO `bible_verses_tr` VALUES ('791', '40', '23', '17', 'μωροι και τυφλοι τις γαρ μειζων εστιν ο χρυσος η ο ναος ο αγιαζων τον χρυσον');
INSERT INTO `bible_verses_tr` VALUES ('792', '40', '23', '18', 'και ος εαν ομοση εν τω θυσιαστηριω ουδεν εστιν ος δ αν ομοση εν τω δωρω τω επανω αυτου οφειλει');
INSERT INTO `bible_verses_tr` VALUES ('793', '40', '23', '19', 'μωροι και τυφλοι τι γαρ μειζον το δωρον η το θυσιαστηριον το αγιαζον το δωρον');
INSERT INTO `bible_verses_tr` VALUES ('794', '40', '23', '20', 'ο ουν ομοσας εν τω θυσιαστηριω ομνυει εν αυτω και εν πασιν τοις επανω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('795', '40', '23', '21', 'και ο ομοσας εν τω ναω ομνυει εν αυτω και εν τω κατοικουντι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('796', '40', '23', '22', 'και ο ομοσας εν τω ουρανω ομνυει εν τω θρονω του θεου και εν τω καθημενω επανω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('797', '40', '23', '23', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι αποδεκατουτε το ηδυοσμον και το ανηθον και το κυμινον και αφηκατε τα βαρυτερα του νομου την κρισιν και τον ελεον και την πιστιν ταυτα εδει ποιησαι κακεινα μη αφιεναι');
INSERT INTO `bible_verses_tr` VALUES ('798', '40', '23', '24', 'οδηγοι τυφλοι οι διυλιζοντες τον κωνωπα την δε καμηλον καταπινοντες');
INSERT INTO `bible_verses_tr` VALUES ('799', '40', '23', '25', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι καθαριζετε το εξωθεν του ποτηριου και της παροψιδος εσωθεν δε γεμουσιν εξ αρπαγης και ακρασιας');
INSERT INTO `bible_verses_tr` VALUES ('800', '40', '23', '26', 'φαρισαιε τυφλε καθαρισον πρωτον το εντος του ποτηριου και της παροψιδος ινα γενηται και το εκτος αυτων καθαρον');
INSERT INTO `bible_verses_tr` VALUES ('801', '40', '23', '27', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι παρομοιαζετε ταφοις κεκονιαμενοις οιτινες εξωθεν μεν φαινονται ωραιοι εσωθεν δε γεμουσιν οστεων νεκρων και πασης ακαθαρσιας');
INSERT INTO `bible_verses_tr` VALUES ('802', '40', '23', '28', 'ουτως και υμεις εξωθεν μεν φαινεσθε τοις ανθρωποις δικαιοι εσωθεν δε μεστοι εστε υποκρισεως και ανομιας');
INSERT INTO `bible_verses_tr` VALUES ('803', '40', '23', '29', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι οικοδομειτε τους ταφους των προφητων και κοσμειτε τα μνημεια των δικαιων');
INSERT INTO `bible_verses_tr` VALUES ('804', '40', '23', '30', 'και λεγετε ει ημεν εν ταις ημεραις των πατερων ημων ουκ αν ημεν κοινωνοι αυτων εν τω αιματι των προφητων');
INSERT INTO `bible_verses_tr` VALUES ('805', '40', '23', '31', 'ωστε μαρτυρειτε εαυτοις οτι υιοι εστε των φονευσαντων τους προφητας');
INSERT INTO `bible_verses_tr` VALUES ('806', '40', '23', '32', 'και υμεις πληρωσατε το μετρον των πατερων υμων');
INSERT INTO `bible_verses_tr` VALUES ('807', '40', '23', '33', 'οφεις γεννηματα εχιδνων πως φυγητε απο της κρισεως της γεεννης');
INSERT INTO `bible_verses_tr` VALUES ('808', '40', '23', '34', 'δια τουτο ιδου εγω αποστελλω προς υμας προφητας και σοφους και γραμματεις και εξ αυτων αποκτενειτε και σταυρωσετε και εξ αυτων μαστιγωσετε εν ταις συναγωγαις υμων και διωξετε απο πολεως εις πολιν');
INSERT INTO `bible_verses_tr` VALUES ('809', '40', '23', '35', 'οπως ελθη εφ υμας παν αιμα δικαιον εκχυνομενον επι της γης απο του αιματος αβελ του δικαιου εως του αιματος ζαχαριου υιου βαραχιου ον εφονευσατε μεταξυ του ναου και του θυσιαστηριου');
INSERT INTO `bible_verses_tr` VALUES ('810', '40', '23', '36', 'αμην λεγω υμιν ηξει ταυτα παντα επι την γενεαν ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('811', '40', '23', '37', 'ιερουσαλημ ιερουσαλημ η αποκτεινουσα τους προφητας και λιθοβολουσα τους απεσταλμενους προς αυτην ποσακις ηθελησα επισυναγαγειν τα τεκνα σου ον τροπον επισυναγει ορνις τα νοσσια εαυτης υπο τας πτερυγας και ουκ ηθελησατε');
INSERT INTO `bible_verses_tr` VALUES ('812', '40', '23', '38', 'ιδου αφιεται υμιν ο οικος υμων ερημος');
INSERT INTO `bible_verses_tr` VALUES ('813', '40', '23', '39', 'λεγω γαρ υμιν ου μη με ιδητε απ αρτι εως αν ειπητε ευλογημενος ο ερχομενος εν ονοματι κυριου');
INSERT INTO `bible_verses_tr` VALUES ('814', '40', '24', '1', 'και εξελθων ο ιησους επορευετο απο του ιερου και προσηλθον οι μαθηται αυτου επιδειξαι αυτω τας οικοδομας του ιερου');
INSERT INTO `bible_verses_tr` VALUES ('815', '40', '24', '2', 'ο δε ιησους ειπεν αυτοις ου βλεπετε παντα ταυτα αμην λεγω υμιν ου μη αφεθη ωδε λιθος επι λιθον ος ου μη καταλυθησεται');
INSERT INTO `bible_verses_tr` VALUES ('816', '40', '24', '3', 'καθημενου δε αυτου επι του ορους των ελαιων προσηλθον αυτω οι μαθηται κατ ιδιαν λεγοντες ειπε ημιν ποτε ταυτα εσται και τι το σημειον της σης παρουσιας και της συντελειας του αιωνος');
INSERT INTO `bible_verses_tr` VALUES ('817', '40', '24', '4', 'και αποκριθεις ο ιησους ειπεν αυτοις βλεπετε μη τις υμας πλανηση');
INSERT INTO `bible_verses_tr` VALUES ('818', '40', '24', '5', 'πολλοι γαρ ελευσονται επι τω ονοματι μου λεγοντες εγω ειμι ο χριστος και πολλους πλανησουσιν');
INSERT INTO `bible_verses_tr` VALUES ('819', '40', '24', '6', 'μελλησετε δε ακουειν πολεμους και ακοας πολεμων ορατε μη θροεισθε δει γαρ παντα γενεσθαι αλλ ουπω εστιν το τελος');
INSERT INTO `bible_verses_tr` VALUES ('820', '40', '24', '7', 'εγερθησεται γαρ εθνος επι εθνος και βασιλεια επι βασιλειαν και εσονται λιμοι και λοιμοι και σεισμοι κατα τοπους');
INSERT INTO `bible_verses_tr` VALUES ('821', '40', '24', '8', 'παντα δε ταυτα αρχη ωδινων');
INSERT INTO `bible_verses_tr` VALUES ('822', '40', '24', '9', 'τοτε παραδωσουσιν υμας εις θλιψιν και αποκτενουσιν υμας και εσεσθε μισουμενοι υπο παντων των εθνων δια το ονομα μου');
INSERT INTO `bible_verses_tr` VALUES ('823', '40', '24', '10', 'και τοτε σκανδαλισθησονται πολλοι και αλληλους παραδωσουσιν και μισησουσιν αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('824', '40', '24', '11', 'και πολλοι ψευδοπροφηται εγερθησονται και πλανησουσιν πολλους');
INSERT INTO `bible_verses_tr` VALUES ('825', '40', '24', '12', 'και δια το πληθυνθηναι την ανομιαν ψυγησεται η αγαπη των πολλων');
INSERT INTO `bible_verses_tr` VALUES ('826', '40', '24', '13', 'ο δε υπομεινας εις τελος ουτος σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('827', '40', '24', '14', 'και κηρυχθησεται τουτο το ευαγγελιον της βασιλειας εν ολη τη οικουμενη εις μαρτυριον πασιν τοις εθνεσιν και τοτε ηξει το τελος');
INSERT INTO `bible_verses_tr` VALUES ('828', '40', '24', '15', 'οταν ουν ιδητε το βδελυγμα της ερημωσεως το ρηθεν δια δανιηλ του προφητου  {VAR1: εστος } {VAR2: εστως } εν τοπω αγιω ο αναγινωσκων νοειτω');
INSERT INTO `bible_verses_tr` VALUES ('829', '40', '24', '16', 'τοτε οι εν τη ιουδαια φευγετωσαν επι τα ορη');
INSERT INTO `bible_verses_tr` VALUES ('830', '40', '24', '17', 'ο επι του δωματος μη καταβαινετω αραι τι εκ της οικιας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('831', '40', '24', '18', 'και ο εν τω αγρω μη επιστρεψατω οπισω αραι τα ιματια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('832', '40', '24', '19', 'ουαι δε ταις εν γαστρι εχουσαις και ταις θηλαζουσαις εν εκειναις ταις ημεραις');
INSERT INTO `bible_verses_tr` VALUES ('833', '40', '24', '20', 'προσευχεσθε δε ινα μη γενηται η φυγη υμων χειμωνος μηδε εν σαββατω');
INSERT INTO `bible_verses_tr` VALUES ('834', '40', '24', '21', 'εσται γαρ τοτε θλιψις μεγαλη οια ου γεγονεν απ αρχης κοσμου εως του νυν ουδ ου μη γενηται');
INSERT INTO `bible_verses_tr` VALUES ('835', '40', '24', '22', 'και ει μη εκολοβωθησαν αι ημεραι εκειναι ουκ αν εσωθη πασα σαρξ δια δε τους εκλεκτους κολοβωθησονται αι ημεραι εκειναι');
INSERT INTO `bible_verses_tr` VALUES ('836', '40', '24', '23', 'τοτε εαν τις υμιν ειπη ιδου ωδε ο χριστος η ωδε μη πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('837', '40', '24', '24', 'εγερθησονται γαρ ψευδοχριστοι και ψευδοπροφηται και δωσουσιν σημεια μεγαλα και τερατα ωστε πλανησαι ει δυνατον και τους εκλεκτους');
INSERT INTO `bible_verses_tr` VALUES ('838', '40', '24', '25', 'ιδου προειρηκα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('839', '40', '24', '26', 'εαν ουν ειπωσιν υμιν ιδου εν τη ερημω εστιν μη εξελθητε ιδου εν τοις ταμειοις μη πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('840', '40', '24', '27', 'ωσπερ γαρ η αστραπη εξερχεται απο ανατολων και φαινεται εως δυσμων ουτως εσται και η παρουσια του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('841', '40', '24', '28', 'οπου γαρ εαν η το πτωμα εκει συναχθησονται οι αετοι');
INSERT INTO `bible_verses_tr` VALUES ('842', '40', '24', '29', 'ευθεως δε μετα την θλιψιν των ημερων εκεινων ο ηλιος σκοτισθησεται και η σεληνη ου δωσει το φεγγος αυτης και οι αστερες πεσουνται απο του ουρανου και αι δυναμεις των ουρανων σαλευθησονται');
INSERT INTO `bible_verses_tr` VALUES ('843', '40', '24', '30', 'και τοτε φανησεται το σημειον του υιου του ανθρωπου εν τω ουρανω και τοτε κοψονται πασαι αι φυλαι της γης και οψονται τον υιον του ανθρωπου ερχομενον επι των νεφελων του ουρανου μετα δυναμεως και δοξης πολλης');
INSERT INTO `bible_verses_tr` VALUES ('844', '40', '24', '31', 'και αποστελει τους αγγελους αυτου μετα σαλπιγγος φωνης μεγαλης και επισυναξουσιν τους εκλεκτους αυτου εκ των τεσσαρων ανεμων απ ακρων ουρανων εως ακρων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('845', '40', '24', '32', 'απο δε της συκης μαθετε την παραβολην οταν ηδη ο κλαδος αυτης γενηται απαλος και τα φυλλα εκφυη γινωσκετε οτι εγγυς το θερος');
INSERT INTO `bible_verses_tr` VALUES ('846', '40', '24', '33', 'ουτως και υμεις οταν ιδητε παντα ταυτα γινωσκετε οτι εγγυς εστιν επι θυραις');
INSERT INTO `bible_verses_tr` VALUES ('847', '40', '24', '34', 'αμην λεγω υμιν ου μη παρελθη η γενεα αυτη εως αν παντα ταυτα γενηται');
INSERT INTO `bible_verses_tr` VALUES ('848', '40', '24', '35', 'ο ουρανος και η γη παρελευσονται οι δε λογοι μου ου μη παρελθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('849', '40', '24', '36', 'περι δε της ημερας εκεινης και της ωρας ουδεις οιδεν ουδε οι αγγελοι των ουρανων ει μη ο πατηρ μου μονος');
INSERT INTO `bible_verses_tr` VALUES ('850', '40', '24', '37', 'ωσπερ δε αι ημεραι του νωε ουτως εσται και η παρουσια του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('851', '40', '24', '38', 'ωσπερ γαρ ησαν εν ταις ημεραις ταις προ του κατακλυσμου τρωγοντες και πινοντες γαμουντες και εκγαμιζοντες αχρι ης ημερας εισηλθεν νωε εις την κιβωτον');
INSERT INTO `bible_verses_tr` VALUES ('852', '40', '24', '39', 'και ουκ εγνωσαν εως ηλθεν ο κατακλυσμος και ηρεν απαντας ουτως εσται και η παρουσια του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('853', '40', '24', '40', 'τοτε δυο εσονται εν τω αγρω ο εις παραλαμβανεται και ο εις αφιεται');
INSERT INTO `bible_verses_tr` VALUES ('854', '40', '24', '41', 'δυο αληθουσαι εν τω μυλωνι μια παραλαμβανεται και μια αφιεται');
INSERT INTO `bible_verses_tr` VALUES ('855', '40', '24', '42', 'γρηγορειτε ουν οτι ουκ οιδατε ποια ωρα ο κυριος υμων ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('856', '40', '24', '43', 'εκεινο δε γινωσκετε οτι ει ηδει ο οικοδεσποτης ποια φυλακη ο κλεπτης ερχεται εγρηγορησεν αν και ουκ αν ειασεν διορυγηναι την οικιαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('857', '40', '24', '44', 'δια τουτο και υμεις γινεσθε ετοιμοι οτι η ωρα ου δοκειτε ο υιος του ανθρωπου ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('858', '40', '24', '45', 'τις αρα εστιν ο πιστος δουλος και φρονιμος ον κατεστησεν ο κυριος αυτου επι της θεραπειας αυτου του διδοναι αυτοις την τροφην εν καιρω');
INSERT INTO `bible_verses_tr` VALUES ('859', '40', '24', '46', 'μακαριος ο δουλος εκεινος ον ελθων ο κυριος αυτου ευρησει ποιουντα ουτως');
INSERT INTO `bible_verses_tr` VALUES ('860', '40', '24', '47', 'αμην λεγω υμιν οτι επι πασιν τοις υπαρχουσιν αυτου καταστησει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('861', '40', '24', '48', 'εαν δε ειπη ο κακος δουλος εκεινος εν τη καρδια αυτου χρονιζει ο κυριος μου ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('862', '40', '24', '49', 'και αρξηται τυπτειν τους συνδουλους εσθιειν δε και πινειν μετα των μεθυοντων');
INSERT INTO `bible_verses_tr` VALUES ('863', '40', '24', '50', 'ηξει ο κυριος του δουλου εκεινου εν ημερα η ου προσδοκα και εν ωρα η ου γινωσκει');
INSERT INTO `bible_verses_tr` VALUES ('864', '40', '24', '51', 'και διχοτομησει αυτον και το μερος αυτου μετα των υποκριτων θησει εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('865', '40', '25', '1', 'τοτε ομοιωθησεται η βασιλεια των ουρανων δεκα παρθενοις αιτινες λαβουσαι τας λαμπαδας αυτων εξηλθον εις απαντησιν του νυμφιου');
INSERT INTO `bible_verses_tr` VALUES ('866', '40', '25', '2', 'πεντε δε ησαν εξ αυτων φρονιμοι και αι πεντε μωραι');
INSERT INTO `bible_verses_tr` VALUES ('867', '40', '25', '3', 'αιτινες μωραι λαβουσαι τας λαμπαδας εαυτων ουκ ελαβον μεθ εαυτων ελαιον');
INSERT INTO `bible_verses_tr` VALUES ('868', '40', '25', '4', 'αι δε φρονιμοι ελαβον ελαιον εν τοις αγγειοις αυτων μετα των λαμπαδων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('869', '40', '25', '5', 'χρονιζοντος δε του νυμφιου ενυσταξαν πασαι και εκαθευδον');
INSERT INTO `bible_verses_tr` VALUES ('870', '40', '25', '6', 'μεσης δε νυκτος κραυγη γεγονεν ιδου ο νυμφιος ερχεται εξερχεσθε εις απαντησιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('871', '40', '25', '7', 'τοτε ηγερθησαν πασαι αι παρθενοι εκειναι και εκοσμησαν τας λαμπαδας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('872', '40', '25', '8', 'αι δε μωραι ταις φρονιμοις ειπον δοτε ημιν εκ του ελαιου υμων οτι αι λαμπαδες ημων σβεννυνται');
INSERT INTO `bible_verses_tr` VALUES ('873', '40', '25', '9', 'απεκριθησαν δε αι φρονιμοι λεγουσαι μηποτε ουκ αρκεση ημιν και υμιν πορευεσθε δε μαλλον προς τους πωλουντας και αγορασατε εαυταις');
INSERT INTO `bible_verses_tr` VALUES ('874', '40', '25', '10', 'απερχομενων δε αυτων αγορασαι ηλθεν ο νυμφιος και αι ετοιμοι εισηλθον μετ αυτου εις τους γαμους και εκλεισθη η θυρα');
INSERT INTO `bible_verses_tr` VALUES ('875', '40', '25', '11', 'υστερον δε ερχονται και αι λοιπαι παρθενοι λεγουσαι κυριε κυριε ανοιξον ημιν');
INSERT INTO `bible_verses_tr` VALUES ('876', '40', '25', '12', 'ο δε αποκριθεις ειπεν αμην λεγω υμιν ουκ οιδα υμας');
INSERT INTO `bible_verses_tr` VALUES ('877', '40', '25', '13', 'γρηγορειτε ουν οτι ουκ οιδατε την ημεραν ουδε την ωραν εν η ο υιος του ανθρωπου ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('878', '40', '25', '14', 'ωσπερ γαρ ανθρωπος αποδημων εκαλεσεν τους ιδιους δουλους και παρεδωκεν αυτοις τα υπαρχοντα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('879', '40', '25', '15', 'και ω μεν εδωκεν πεντε ταλαντα ω δε δυο ω δε εν εκαστω κατα την ιδιαν δυναμιν και απεδημησεν ευθεως');
INSERT INTO `bible_verses_tr` VALUES ('880', '40', '25', '16', 'πορευθεις δε ο τα πεντε ταλαντα λαβων ειργασατο εν αυτοις και εποιησεν αλλα πεντε ταλαντα');
INSERT INTO `bible_verses_tr` VALUES ('881', '40', '25', '17', 'ωσαυτως και ο τα δυο εκερδησεν και αυτος αλλα δυο');
INSERT INTO `bible_verses_tr` VALUES ('882', '40', '25', '18', 'ο δε το εν λαβων απελθων ωρυξεν εν τη γη και απεκρυψεν το αργυριον του κυριου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('883', '40', '25', '19', 'μετα δε χρονον πολυν ερχεται ο κυριος των δουλων εκεινων και συναιρει μετ αυτων λογον');
INSERT INTO `bible_verses_tr` VALUES ('884', '40', '25', '20', 'και προσελθων ο τα πεντε ταλαντα λαβων προσηνεγκεν αλλα πεντε ταλαντα λεγων κυριε πεντε ταλαντα μοι παρεδωκας ιδε αλλα πεντε ταλαντα εκερδησα επ αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('885', '40', '25', '21', 'εφη δε αυτω ο κυριος αυτου ευ δουλε αγαθε και πιστε επι ολιγα ης πιστος επι πολλων σε καταστησω εισελθε εις την χαραν του κυριου σου');
INSERT INTO `bible_verses_tr` VALUES ('886', '40', '25', '22', 'προσελθων δε και ο τα δυο ταλαντα λαβων ειπεν κυριε δυο ταλαντα μοι παρεδωκας ιδε αλλα δυο ταλαντα εκερδησα επ αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('887', '40', '25', '23', 'εφη αυτω ο κυριος αυτου ευ δουλε αγαθε και πιστε επι ολιγα ης πιστος επι πολλων σε καταστησω εισελθε εις την χαραν του κυριου σου');
INSERT INTO `bible_verses_tr` VALUES ('888', '40', '25', '24', 'προσελθων δε και ο το εν ταλαντον ειληφως ειπεν κυριε εγνων σε οτι σκληρος ει ανθρωπος θεριζων οπου ουκ εσπειρας και συναγων οθεν ου διεσκορπισας');
INSERT INTO `bible_verses_tr` VALUES ('889', '40', '25', '25', 'και φοβηθεις απελθων εκρυψα το ταλαντον σου εν τη γη ιδε εχεις το σον');
INSERT INTO `bible_verses_tr` VALUES ('890', '40', '25', '26', 'αποκριθεις δε ο κυριος αυτου ειπεν αυτω πονηρε δουλε και οκνηρε ηδεις οτι θεριζω οπου ουκ εσπειρα και συναγω οθεν ου διεσκορπισα');
INSERT INTO `bible_verses_tr` VALUES ('891', '40', '25', '27', 'εδει ουν σε βαλειν το αργυριον μου τοις τραπεζιταις και ελθων εγω εκομισαμην αν το εμον συν τοκω');
INSERT INTO `bible_verses_tr` VALUES ('892', '40', '25', '28', 'αρατε ουν απ αυτου το ταλαντον και δοτε τω εχοντι τα δεκα ταλαντα');
INSERT INTO `bible_verses_tr` VALUES ('893', '40', '25', '29', 'τω γαρ εχοντι παντι δοθησεται και περισσευθησεται απο δε του μη εχοντος και ο εχει αρθησεται απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('894', '40', '25', '30', 'και τον αχρειον δουλον εκβαλλετε εις το σκοτος το εξωτερον εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων');
INSERT INTO `bible_verses_tr` VALUES ('895', '40', '25', '31', 'οταν δε ελθη ο υιος του ανθρωπου εν τη δοξη αυτου και παντες οι αγιοι αγγελοι μετ αυτου τοτε καθισει επι θρονου δοξης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('896', '40', '25', '32', 'και συναχθησεται εμπροσθεν αυτου παντα τα εθνη και αφοριει αυτους απ αλληλων ωσπερ ο ποιμην αφοριζει τα προβατα απο των εριφων');
INSERT INTO `bible_verses_tr` VALUES ('897', '40', '25', '33', 'και στησει τα μεν προβατα εκ δεξιων αυτου τα δε εριφια εξ ευωνυμων');
INSERT INTO `bible_verses_tr` VALUES ('898', '40', '25', '34', 'τοτε ερει ο βασιλευς τοις εκ δεξιων αυτου δευτε οι ευλογημενοι του πατρος μου κληρονομησατε την ητοιμασμενην υμιν βασιλειαν απο καταβολης κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('899', '40', '25', '35', 'επεινασα γαρ και εδωκατε μοι φαγειν εδιψησα και εποτισατε με ξενος ημην και συνηγαγετε με');
INSERT INTO `bible_verses_tr` VALUES ('900', '40', '25', '36', 'γυμνος και περιεβαλετε με ησθενησα και επεσκεψασθε με εν φυλακη ημην και ηλθετε προς με');
INSERT INTO `bible_verses_tr` VALUES ('901', '40', '25', '37', 'τοτε αποκριθησονται αυτω οι δικαιοι λεγοντες κυριε ποτε σε ειδομεν πεινωντα και εθρεψαμεν η διψωντα και εποτισαμεν');
INSERT INTO `bible_verses_tr` VALUES ('902', '40', '25', '38', 'ποτε δε σε ειδομεν ξενον και συνηγαγομεν η γυμνον και περιεβαλομεν');
INSERT INTO `bible_verses_tr` VALUES ('903', '40', '25', '39', 'ποτε δε σε ειδομεν ασθενη η εν φυλακη και ηλθομεν προς σε');
INSERT INTO `bible_verses_tr` VALUES ('904', '40', '25', '40', 'και αποκριθεις ο βασιλευς ερει αυτοις αμην λεγω υμιν εφ οσον εποιησατε ενι τουτων των αδελφων μου των ελαχιστων εμοι εποιησατε');
INSERT INTO `bible_verses_tr` VALUES ('905', '40', '25', '41', 'τοτε ερει και τοις εξ ευωνυμων πορευεσθε απ εμου οι κατηραμενοι εις το πυρ το αιωνιον το ητοιμασμενον τω διαβολω και τοις αγγελοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('906', '40', '25', '42', 'επεινασα γαρ και ουκ εδωκατε μοι φαγειν εδιψησα και ουκ εποτισατε με');
INSERT INTO `bible_verses_tr` VALUES ('907', '40', '25', '43', 'ξενος ημην και ου συνηγαγετε με γυμνος και ου περιεβαλετε με ασθενης και εν φυλακη και ουκ επεσκεψασθε με');
INSERT INTO `bible_verses_tr` VALUES ('908', '40', '25', '44', 'τοτε αποκριθησονται αυτω και αυτοι λεγοντες κυριε ποτε σε ειδομεν πεινωντα η διψωντα η ξενον η γυμνον η ασθενη η εν φυλακη και ου διηκονησαμεν σοι');
INSERT INTO `bible_verses_tr` VALUES ('909', '40', '25', '45', 'τοτε αποκριθησεται αυτοις λεγων αμην λεγω υμιν εφ οσον ουκ εποιησατε ενι τουτων των ελαχιστων ουδε εμοι εποιησατε');
INSERT INTO `bible_verses_tr` VALUES ('910', '40', '25', '46', 'και απελευσονται ουτοι εις κολασιν αιωνιον οι δε δικαιοι εις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('911', '40', '26', '1', 'και εγενετο οτε ετελεσεν ο ιησους παντας τους λογους τουτους ειπεν τοις μαθηταις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('912', '40', '26', '2', 'οιδατε οτι μετα δυο ημερας το πασχα γινεται και ο υιος του ανθρωπου παραδιδοται εις το σταυρωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('913', '40', '26', '3', 'τοτε συνηχθησαν οι αρχιερεις και οι γραμματεις και οι πρεσβυτεροι του λαου εις την αυλην του αρχιερεως του λεγομενου καιαφα');
INSERT INTO `bible_verses_tr` VALUES ('914', '40', '26', '4', 'και συνεβουλευσαντο ινα τον ιησουν κρατησωσιν δολω και αποκτεινωσιν');
INSERT INTO `bible_verses_tr` VALUES ('915', '40', '26', '5', 'ελεγον δε μη εν τη εορτη ινα μη θορυβος γενηται εν τω λαω');
INSERT INTO `bible_verses_tr` VALUES ('916', '40', '26', '6', 'του δε ιησου γενομενου εν βηθανια εν οικια σιμωνος του λεπρου');
INSERT INTO `bible_verses_tr` VALUES ('917', '40', '26', '7', 'προσηλθεν αυτω γυνη αλαβαστρον μυρου εχουσα βαρυτιμου και κατεχεεν επι την κεφαλην αυτου ανακειμενου');
INSERT INTO `bible_verses_tr` VALUES ('918', '40', '26', '8', 'ιδοντες δε οι μαθηται αυτου ηγανακτησαν λεγοντες εις τι η απωλεια αυτη');
INSERT INTO `bible_verses_tr` VALUES ('919', '40', '26', '9', 'ηδυνατο γαρ τουτο το μυρον πραθηναι πολλου και δοθηναι πτωχοις');
INSERT INTO `bible_verses_tr` VALUES ('920', '40', '26', '10', 'γνους δε ο ιησους ειπεν αυτοις τι κοπους παρεχετε τη γυναικι εργον γαρ καλον ειργασατο εις εμε');
INSERT INTO `bible_verses_tr` VALUES ('921', '40', '26', '11', 'παντοτε γαρ τους πτωχους εχετε μεθ εαυτων εμε δε ου παντοτε εχετε');
INSERT INTO `bible_verses_tr` VALUES ('922', '40', '26', '12', 'βαλουσα γαρ αυτη το μυρον τουτο επι του σωματος μου προς το ενταφιασαι με εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('923', '40', '26', '13', 'αμην λεγω υμιν οπου εαν κηρυχθη το ευαγγελιον τουτο εν ολω τω κοσμω λαληθησεται και ο εποιησεν αυτη εις μνημοσυνον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('924', '40', '26', '14', 'τοτε πορευθεις εις των δωδεκα ο λεγομενος ιουδας ισκαριωτης προς τους αρχιερεις');
INSERT INTO `bible_verses_tr` VALUES ('925', '40', '26', '15', 'ειπεν τι θελετε μοι δουναι καγω υμιν παραδωσω αυτον οι δε εστησαν αυτω τριακοντα αργυρια');
INSERT INTO `bible_verses_tr` VALUES ('926', '40', '26', '16', 'και απο τοτε εζητει ευκαιριαν ινα αυτον παραδω');
INSERT INTO `bible_verses_tr` VALUES ('927', '40', '26', '17', 'τη δε πρωτη των αζυμων προσηλθον οι μαθηται τω ιησου λεγοντες αυτω που θελεις ετοιμασωμεν σοι φαγειν το πασχα');
INSERT INTO `bible_verses_tr` VALUES ('928', '40', '26', '18', 'ο δε ειπεν υπαγετε εις την πολιν προς τον δεινα και ειπατε αυτω ο διδασκαλος λεγει ο καιρος μου εγγυς εστιν προς σε ποιω το πασχα μετα των μαθητων μου');
INSERT INTO `bible_verses_tr` VALUES ('929', '40', '26', '19', 'και εποιησαν οι μαθηται ως συνεταξεν αυτοις ο ιησους και ητοιμασαν το πασχα');
INSERT INTO `bible_verses_tr` VALUES ('930', '40', '26', '20', 'οψιας δε γενομενης ανεκειτο μετα των δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('931', '40', '26', '21', 'και εσθιοντων αυτων ειπεν αμην λεγω υμιν οτι εις εξ υμων παραδωσει με');
INSERT INTO `bible_verses_tr` VALUES ('932', '40', '26', '22', 'και λυπουμενοι σφοδρα ηρξαντο λεγειν αυτω εκαστος αυτων μητι εγω ειμι κυριε');
INSERT INTO `bible_verses_tr` VALUES ('933', '40', '26', '23', 'ο δε αποκριθεις ειπεν ο εμβαψας μετ εμου εν τω τρυβλιω την χειρα ουτος με παραδωσει');
INSERT INTO `bible_verses_tr` VALUES ('934', '40', '26', '24', 'ο μεν υιος του ανθρωπου υπαγει καθως γεγραπται περι αυτου ουαι δε τω ανθρωπω εκεινω δι ου ο υιος του ανθρωπου παραδιδοται καλον ην αυτω ει ουκ εγεννηθη ο ανθρωπος εκεινος');
INSERT INTO `bible_verses_tr` VALUES ('935', '40', '26', '25', 'αποκριθεις δε ιουδας ο παραδιδους αυτον ειπεν μητι εγω ειμι ραββι λεγει αυτω συ ειπας');
INSERT INTO `bible_verses_tr` VALUES ('936', '40', '26', '26', 'εσθιοντων δε αυτων λαβων ο ιησους τον αρτον και ευλογησας εκλασεν και εδιδου τοις μαθηταις και ειπεν λαβετε φαγετε τουτο εστιν το σωμα μου');
INSERT INTO `bible_verses_tr` VALUES ('937', '40', '26', '27', 'και λαβων το ποτηριον και ευχαριστησας εδωκεν αυτοις λεγων πιετε εξ αυτου παντες');
INSERT INTO `bible_verses_tr` VALUES ('938', '40', '26', '28', 'τουτο γαρ εστιν το αιμα μου το της καινης διαθηκης το περι πολλων εκχυνομενον εις αφεσιν αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('939', '40', '26', '29', 'λεγω δε υμιν οτι ου μη πιω απ αρτι εκ τουτου του γεννηματος της αμπελου εως της ημερας εκεινης οταν αυτο πινω μεθ υμων καινον εν τη βασιλεια του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('940', '40', '26', '30', 'και υμνησαντες εξηλθον εις το ορος των ελαιων');
INSERT INTO `bible_verses_tr` VALUES ('941', '40', '26', '31', 'τοτε λεγει αυτοις ο ιησους παντες υμεις σκανδαλισθησεσθε εν εμοι εν τη νυκτι ταυτη γεγραπται γαρ παταξω τον ποιμενα και διασκορπισθησεται τα προβατα της ποιμνης');
INSERT INTO `bible_verses_tr` VALUES ('942', '40', '26', '32', 'μετα δε το εγερθηναι με προαξω υμας εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('943', '40', '26', '33', 'αποκριθεις δε ο πετρος ειπεν αυτω ει και παντες σκανδαλισθησονται εν σοι εγω ουδεποτε σκανδαλισθησομαι');
INSERT INTO `bible_verses_tr` VALUES ('944', '40', '26', '34', 'εφη αυτω ο ιησους αμην λεγω σοι οτι εν ταυτη τη νυκτι πριν αλεκτορα φωνησαι τρις απαρνηση με');
INSERT INTO `bible_verses_tr` VALUES ('945', '40', '26', '35', 'λεγει αυτω ο πετρος καν δεη με συν σοι αποθανειν ου μη σε απαρνησομαι ομοιως και παντες οι μαθηται ειπον');
INSERT INTO `bible_verses_tr` VALUES ('946', '40', '26', '36', 'τοτε ερχεται μετ αυτων ο ιησους εις χωριον λεγομενον γεθσημανη και λεγει τοις μαθηταις καθισατε αυτου εως ου απελθων προσευξωμαι εκει');
INSERT INTO `bible_verses_tr` VALUES ('947', '40', '26', '37', 'και παραλαβων τον πετρον και τους δυο υιους ζεβεδαιου ηρξατο λυπεισθαι και αδημονειν');
INSERT INTO `bible_verses_tr` VALUES ('948', '40', '26', '38', 'τοτε λεγει αυτοις περιλυπος εστιν η ψυχη μου εως θανατου μεινατε ωδε και γρηγορειτε μετ εμου');
INSERT INTO `bible_verses_tr` VALUES ('949', '40', '26', '39', 'και προελθων μικρον επεσεν επι προσωπον αυτου προσευχομενος και λεγων πατερ μου ει δυνατον εστιν παρελθετω απ εμου το ποτηριον τουτο πλην ουχ ως εγω θελω αλλ ως συ');
INSERT INTO `bible_verses_tr` VALUES ('950', '40', '26', '40', 'και ερχεται προς τους μαθητας και ευρισκει αυτους καθευδοντας και λεγει τω πετρω ουτως ουκ ισχυσατε μιαν ωραν γρηγορησαι μετ εμου');
INSERT INTO `bible_verses_tr` VALUES ('951', '40', '26', '41', 'γρηγορειτε και προσευχεσθε ινα μη εισελθητε εις πειρασμον το μεν πνευμα προθυμον η δε σαρξ ασθενης');
INSERT INTO `bible_verses_tr` VALUES ('952', '40', '26', '42', 'παλιν εκ δευτερου απελθων προσηυξατο λεγων πατερ μου ει ου δυναται τουτο το ποτηριον παρελθειν απ εμου εαν μη αυτο πιω γενηθητω το θελημα σου');
INSERT INTO `bible_verses_tr` VALUES ('953', '40', '26', '43', 'και ελθων ευρισκει αυτους παλιν καθευδοντας ησαν γαρ αυτων οι οφθαλμοι βεβαρημενοι');
INSERT INTO `bible_verses_tr` VALUES ('954', '40', '26', '44', 'και αφεις αυτους απελθων παλιν προσηυξατο εκ τριτου τον αυτον λογον ειπων');
INSERT INTO `bible_verses_tr` VALUES ('955', '40', '26', '45', 'τοτε ερχεται προς τους μαθητας αυτου και λεγει αυτοις καθευδετε το λοιπον και αναπαυεσθε ιδου ηγγικεν η ωρα και ο υιος του ανθρωπου παραδιδοται εις χειρας αμαρτωλων');
INSERT INTO `bible_verses_tr` VALUES ('956', '40', '26', '46', 'εγειρεσθε αγωμεν ιδου ηγγικεν ο παραδιδους με');
INSERT INTO `bible_verses_tr` VALUES ('957', '40', '26', '47', 'και ετι αυτου λαλουντος ιδου ιουδας εις των δωδεκα ηλθεν και μετ αυτου οχλος πολυς μετα μαχαιρων και ξυλων απο των αρχιερεων και πρεσβυτερων του λαου');
INSERT INTO `bible_verses_tr` VALUES ('958', '40', '26', '48', 'ο δε παραδιδους αυτον εδωκεν αυτοις σημειον λεγων ον αν φιλησω αυτος εστιν κρατησατε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('959', '40', '26', '49', 'και ευθεως προσελθων τω ιησου ειπεν χαιρε ραββι και κατεφιλησεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('960', '40', '26', '50', 'ο δε ιησους ειπεν αυτω εταιρε εφ ω παρει τοτε προσελθοντες επεβαλον τας χειρας επι τον ιησουν και εκρατησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('961', '40', '26', '51', 'και ιδου εις των μετα ιησου εκτεινας την χειρα απεσπασεν την μαχαιραν αυτου και παταξας τον δουλον του αρχιερεως αφειλεν αυτου το ωτιον');
INSERT INTO `bible_verses_tr` VALUES ('962', '40', '26', '52', 'τοτε λεγει αυτω ο ιησους αποστρεψον σου την μαχαιραν εις τον τοπον αυτης παντες γαρ οι λαβοντες μαχαιραν εν μαχαιρα απολουνται');
INSERT INTO `bible_verses_tr` VALUES ('963', '40', '26', '53', 'η δοκεις οτι ου δυναμαι αρτι παρακαλεσαι τον πατερα μου και παραστησει μοι πλειους η δωδεκα λεγεωνας αγγελων');
INSERT INTO `bible_verses_tr` VALUES ('964', '40', '26', '54', 'πως ουν πληρωθωσιν αι γραφαι οτι ουτως δει γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('965', '40', '26', '55', 'εν εκεινη τη ωρα ειπεν ο ιησους τοις οχλοις ως επι ληστην εξηλθετε μετα μαχαιρων και ξυλων συλλαβειν με καθ ημεραν προς υμας εκαθεζομην διδασκων εν τω ιερω και ουκ εκρατησατε με');
INSERT INTO `bible_verses_tr` VALUES ('966', '40', '26', '56', 'τουτο δε ολον γεγονεν ινα πληρωθωσιν αι γραφαι των προφητων τοτε οι μαθηται παντες αφεντες αυτον εφυγον');
INSERT INTO `bible_verses_tr` VALUES ('967', '40', '26', '57', 'οι δε κρατησαντες τον ιησουν απηγαγον προς καιαφαν τον αρχιερεα οπου οι γραμματεις και οι πρεσβυτεροι συνηχθησαν');
INSERT INTO `bible_verses_tr` VALUES ('968', '40', '26', '58', 'ο δε πετρος ηκολουθει αυτω απο μακροθεν εως της αυλης του αρχιερεως και εισελθων εσω εκαθητο μετα των υπηρετων ιδειν το τελος');
INSERT INTO `bible_verses_tr` VALUES ('969', '40', '26', '59', 'οι δε αρχιερεις και οι πρεσβυτεροι και το συνεδριον ολον εζητουν ψευδομαρτυριαν κατα του ιησου οπως αυτον θανατωσωσιν');
INSERT INTO `bible_verses_tr` VALUES ('970', '40', '26', '60', 'και ουχ ευρον και πολλων ψευδομαρτυρων προσελθοντων ουχ ευρον');
INSERT INTO `bible_verses_tr` VALUES ('971', '40', '26', '61', 'υστερον δε προσελθοντες δυο ψευδομαρτυρες ειπον ουτος εφη δυναμαι καταλυσαι τον ναον του θεου και δια τριων ημερων οικοδομησαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('972', '40', '26', '62', 'και αναστας ο αρχιερευς ειπεν αυτω ουδεν αποκρινη τι ουτοι σου καταμαρτυρουσιν');
INSERT INTO `bible_verses_tr` VALUES ('973', '40', '26', '63', 'ο δε ιησους εσιωπα και αποκριθεις ο αρχιερευς ειπεν αυτω εξορκιζω σε κατα του θεου του ζωντος ινα ημιν ειπης ει συ ει ο χριστος ο υιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('974', '40', '26', '64', 'λεγει αυτω ο ιησους συ ειπας πλην λεγω υμιν απ αρτι οψεσθε τον υιον του ανθρωπου καθημενον εκ δεξιων της δυναμεως και ερχομενον επι των νεφελων του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('975', '40', '26', '65', 'τοτε ο αρχιερευς διερρηξεν τα ιματια αυτου λεγων οτι εβλασφημησεν τι ετι χρειαν εχομεν μαρτυρων ιδε νυν ηκουσατε την βλασφημιαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('976', '40', '26', '66', 'τι υμιν δοκει οι δε αποκριθεντες ειπον ενοχος θανατου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('977', '40', '26', '67', 'τοτε ενεπτυσαν εις το προσωπον αυτου και εκολαφισαν αυτον οι δε ερραπισαν');
INSERT INTO `bible_verses_tr` VALUES ('978', '40', '26', '68', 'λεγοντες προφητευσον ημιν χριστε τις εστιν ο παισας σε');
INSERT INTO `bible_verses_tr` VALUES ('979', '40', '26', '69', 'ο δε πετρος εξω εκαθητο εν τη αυλη και προσηλθεν αυτω μια παιδισκη λεγουσα και συ ησθα μετα ιησου του γαλιλαιου');
INSERT INTO `bible_verses_tr` VALUES ('980', '40', '26', '70', 'ο δε ηρνησατο εμπροσθεν παντων λεγων ουκ οιδα τι λεγεις');
INSERT INTO `bible_verses_tr` VALUES ('981', '40', '26', '71', 'εξελθοντα δε αυτον εις τον πυλωνα ειδεν αυτον αλλη και λεγει τοις εκει και ουτος ην μετα ιησου του ναζωραιου');
INSERT INTO `bible_verses_tr` VALUES ('982', '40', '26', '72', 'και παλιν ηρνησατο μεθ ορκου οτι ουκ οιδα τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('983', '40', '26', '73', 'μετα μικρον δε προσελθοντες οι εστωτες ειπον τω πετρω αληθως και συ εξ αυτων ει και γαρ η λαλια σου δηλον σε ποιει');
INSERT INTO `bible_verses_tr` VALUES ('984', '40', '26', '74', 'τοτε ηρξατο καταναθεματιζειν και ομνυειν οτι ουκ οιδα τον ανθρωπον και ευθεως αλεκτωρ εφωνησεν');
INSERT INTO `bible_verses_tr` VALUES ('985', '40', '26', '75', 'και εμνησθη ο πετρος του ρηματος του ιησου ειρηκοτος αυτω οτι πριν αλεκτορα φωνησαι τρις απαρνηση με και εξελθων εξω εκλαυσεν πικρως');
INSERT INTO `bible_verses_tr` VALUES ('986', '40', '27', '1', 'πρωιας δε γενομενης συμβουλιον ελαβον παντες οι αρχιερεις και οι πρεσβυτεροι του λαου κατα του ιησου ωστε θανατωσαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('987', '40', '27', '2', 'και δησαντες αυτον απηγαγον και παρεδωκαν αυτον ποντιω πιλατω τω ηγεμονι');
INSERT INTO `bible_verses_tr` VALUES ('988', '40', '27', '3', 'τοτε ιδων ιουδας ο παραδιδους αυτον οτι κατεκριθη μεταμεληθεις απεστρεψεν τα τριακοντα αργυρια τοις αρχιερευσιν και τοις πρεσβυτεροις');
INSERT INTO `bible_verses_tr` VALUES ('989', '40', '27', '4', 'λεγων ημαρτον παραδους αιμα αθωον οι δε ειπον τι προς ημας συ οψει');
INSERT INTO `bible_verses_tr` VALUES ('990', '40', '27', '5', 'και ριψας τα αργυρια εν τω ναω ανεχωρησεν και απελθων απηγξατο');
INSERT INTO `bible_verses_tr` VALUES ('991', '40', '27', '6', 'οι δε αρχιερεις λαβοντες τα αργυρια ειπον ουκ εξεστιν βαλειν αυτα εις τον κορβαναν επει τιμη αιματος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('992', '40', '27', '7', 'συμβουλιον δε λαβοντες ηγορασαν εξ αυτων τον αγρον του κεραμεως εις ταφην τοις ξενοις');
INSERT INTO `bible_verses_tr` VALUES ('993', '40', '27', '8', 'διο εκληθη ο αγρος εκεινος αγρος αιματος εως της σημερον');
INSERT INTO `bible_verses_tr` VALUES ('994', '40', '27', '9', 'τοτε επληρωθη το ρηθεν δια ιερεμιου του προφητου λεγοντος και ελαβον τα τριακοντα αργυρια την τιμην του τετιμημενου ον ετιμησαντο απο υιων ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('995', '40', '27', '10', 'και εδωκαν αυτα εις τον αγρον του κεραμεως καθα συνεταξεν μοι κυριος');
INSERT INTO `bible_verses_tr` VALUES ('996', '40', '27', '11', 'ο δε ιησους εστη εμπροσθεν του ηγεμονος και επηρωτησεν αυτον ο ηγεμων λεγων συ ει ο βασιλευς των ιουδαιων ο δε ιησους εφη αυτω συ λεγεις');
INSERT INTO `bible_verses_tr` VALUES ('997', '40', '27', '12', 'και εν τω κατηγορεισθαι αυτον υπο των αρχιερεων και των πρεσβυτερων ουδεν απεκρινατο');
INSERT INTO `bible_verses_tr` VALUES ('998', '40', '27', '13', 'τοτε λεγει αυτω ο πιλατος ουκ ακουεις ποσα σου καταμαρτυρουσιν');
INSERT INTO `bible_verses_tr` VALUES ('999', '40', '27', '14', 'και ουκ απεκριθη αυτω προς ουδε εν ρημα ωστε θαυμαζειν τον ηγεμονα λιαν');
INSERT INTO `bible_verses_tr` VALUES ('1000', '40', '27', '15', 'κατα δε εορτην ειωθει ο ηγεμων απολυειν ενα τω οχλω δεσμιον ον ηθελον');
INSERT INTO `bible_verses_tr` VALUES ('1001', '40', '27', '16', 'ειχον δε τοτε δεσμιον επισημον λεγομενον βαραββαν');
INSERT INTO `bible_verses_tr` VALUES ('1002', '40', '27', '17', 'συνηγμενων ουν αυτων ειπεν αυτοις ο πιλατος τινα θελετε απολυσω υμιν βαραββαν η ιησουν τον λεγομενον χριστον');
INSERT INTO `bible_verses_tr` VALUES ('1003', '40', '27', '18', 'ηδει γαρ οτι δια φθονον παρεδωκαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1004', '40', '27', '19', 'καθημενου δε αυτου επι του βηματος απεστειλεν προς αυτον η γυνη αυτου λεγουσα μηδεν σοι και τω δικαιω εκεινω πολλα γαρ επαθον σημερον κατ οναρ δι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1005', '40', '27', '20', 'οι δε αρχιερεις και οι πρεσβυτεροι επεισαν τους οχλους ινα αιτησωνται τον βαραββαν τον δε ιησουν απολεσωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1006', '40', '27', '21', 'αποκριθεις δε ο ηγεμων ειπεν αυτοις τινα θελετε απο των δυο απολυσω υμιν οι δε ειπον βαραββαν');
INSERT INTO `bible_verses_tr` VALUES ('1007', '40', '27', '22', 'λεγει αυτοις ο πιλατος τι ουν ποιησω ιησουν τον λεγομενον χριστον λεγουσιν αυτω παντες σταυρωθητω');
INSERT INTO `bible_verses_tr` VALUES ('1008', '40', '27', '23', 'ο δε ηγεμων εφη τι γαρ κακον εποιησεν οι δε περισσως εκραζον λεγοντες σταυρωθητω');
INSERT INTO `bible_verses_tr` VALUES ('1009', '40', '27', '24', 'ιδων δε ο πιλατος οτι ουδεν ωφελει αλλα μαλλον θορυβος γινεται λαβων υδωρ απενιψατο τας χειρας απεναντι του οχλου λεγων αθωος ειμι απο του αιματος του δικαιου τουτου υμεις οψεσθε');
INSERT INTO `bible_verses_tr` VALUES ('1010', '40', '27', '25', 'και αποκριθεις πας ο λαος ειπεν το αιμα αυτου εφ ημας και επι τα τεκνα ημων');
INSERT INTO `bible_verses_tr` VALUES ('1011', '40', '27', '26', 'τοτε απελυσεν αυτοις τον βαραββαν τον δε ιησουν φραγελλωσας παρεδωκεν ινα σταυρωθη');
INSERT INTO `bible_verses_tr` VALUES ('1012', '40', '27', '27', 'τοτε οι στρατιωται του ηγεμονος παραλαβοντες τον ιησουν εις το πραιτωριον συνηγαγον επ αυτον ολην την σπειραν');
INSERT INTO `bible_verses_tr` VALUES ('1013', '40', '27', '28', 'και εκδυσαντες αυτον περιεθηκαν αυτω χλαμυδα κοκκινην');
INSERT INTO `bible_verses_tr` VALUES ('1014', '40', '27', '29', 'και πλεξαντες στεφανον εξ ακανθων επεθηκαν επι την κεφαλην αυτου και καλαμον επι την δεξιαν αυτου και γονυπετησαντες εμπροσθεν αυτου ενεπαιζον αυτω λεγοντες χαιρε ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1015', '40', '27', '30', 'και εμπτυσαντες εις αυτον ελαβον τον καλαμον και ετυπτον εις την κεφαλην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1016', '40', '27', '31', 'και οτε ενεπαιξαν αυτω εξεδυσαν αυτον την χλαμυδα και ενεδυσαν αυτον τα ιματια αυτου και απηγαγον αυτον εις το σταυρωσαι');
INSERT INTO `bible_verses_tr` VALUES ('1017', '40', '27', '32', 'εξερχομενοι δε ευρον ανθρωπον κυρηναιον ονοματι σιμωνα τουτον ηγγαρευσαν ινα αρη τον σταυρον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1018', '40', '27', '33', 'και ελθοντες εις τοπον λεγομενον γολγοθα ος εστιν λεγομενος κρανιου τοπος');
INSERT INTO `bible_verses_tr` VALUES ('1019', '40', '27', '34', 'εδωκαν αυτω πιειν οξος μετα χολης μεμιγμενον και γευσαμενος ουκ ηθελεν πιειν');
INSERT INTO `bible_verses_tr` VALUES ('1020', '40', '27', '35', 'σταυρωσαντες δε αυτον διεμερισαντο τα ιματια αυτου βαλλοντες κληρον ινα πληρωθη το ρηθεν υπο του προφητου διεμερισαντο τα ιματια μου εαυτοις και επι τον ιματισμον μου εβαλον κληρον');
INSERT INTO `bible_verses_tr` VALUES ('1021', '40', '27', '36', 'και καθημενοι ετηρουν αυτον εκει');
INSERT INTO `bible_verses_tr` VALUES ('1022', '40', '27', '37', 'και επεθηκαν επανω της κεφαλης αυτου την αιτιαν αυτου γεγραμμενην ουτος εστιν ιησους ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1023', '40', '27', '38', 'τοτε σταυρουνται συν αυτω δυο λησται εις εκ δεξιων και εις εξ ευωνυμων');
INSERT INTO `bible_verses_tr` VALUES ('1024', '40', '27', '39', 'οι δε παραπορευομενοι εβλασφημουν αυτον κινουντες τας κεφαλας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1025', '40', '27', '40', 'και λεγοντες ο καταλυων τον ναον και εν τρισιν ημεραις οικοδομων σωσον σεαυτον ει υιος ει του θεου καταβηθι απο του σταυρου');
INSERT INTO `bible_verses_tr` VALUES ('1026', '40', '27', '41', 'ομοιως δε και οι αρχιερεις εμπαιζοντες μετα των γραμματεων και πρεσβυτερων ελεγον');
INSERT INTO `bible_verses_tr` VALUES ('1027', '40', '27', '42', 'αλλους εσωσεν εαυτον ου δυναται σωσαι ει βασιλευς ισραηλ εστιν καταβατω νυν απο του σταυρου και πιστευσομεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1028', '40', '27', '43', 'πεποιθεν επι τον θεον ρυσασθω νυν αυτον ει θελει αυτον ειπεν γαρ οτι θεου ειμι υιος');
INSERT INTO `bible_verses_tr` VALUES ('1029', '40', '27', '44', 'το δ αυτο και οι λησται οι συσταυρωθεντες αυτω ωνειδιζον αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1030', '40', '27', '45', 'απο δε εκτης ωρας σκοτος εγενετο επι πασαν την γην εως ωρας εννατης');
INSERT INTO `bible_verses_tr` VALUES ('1031', '40', '27', '46', 'περι δε την εννατην ωραν ανεβοησεν ο ιησους φωνη μεγαλη λεγων ηλι ηλι λαμα σαβαχθανι τουτ εστιν θεε μου θεε μου ινα τι με εγκατελιπες');
INSERT INTO `bible_verses_tr` VALUES ('1032', '40', '27', '47', 'τινες δε των εκει εστωτων ακουσαντες ελεγον οτι ηλιαν φωνει ουτος');
INSERT INTO `bible_verses_tr` VALUES ('1033', '40', '27', '48', 'και ευθεως δραμων εις εξ αυτων και λαβων σπογγον πλησας τε οξους και περιθεις καλαμω εποτιζεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1034', '40', '27', '49', 'οι δε λοιποι ελεγον αφες ιδωμεν ει ερχεται ηλιας σωσων αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1035', '40', '27', '50', 'ο δε ιησους παλιν κραξας φωνη μεγαλη αφηκεν το πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('1036', '40', '27', '51', 'και ιδου το καταπετασμα του ναου εσχισθη εις δυο απο ανωθεν εως κατω και η γη εσεισθη και αι πετραι εσχισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('1037', '40', '27', '52', 'και τα μνημεια ανεωχθησαν και πολλα σωματα των κεκοιμημενων αγιων ηγερθη');
INSERT INTO `bible_verses_tr` VALUES ('1038', '40', '27', '53', 'και εξελθοντες εκ των μνημειων μετα την εγερσιν αυτου εισηλθον εις την αγιαν πολιν και ενεφανισθησαν πολλοις');
INSERT INTO `bible_verses_tr` VALUES ('1039', '40', '27', '54', 'ο δε εκατονταρχος και οι μετ αυτου τηρουντες τον ιησουν ιδοντες τον σεισμον και τα γενομενα εφοβηθησαν σφοδρα λεγοντες αληθως θεου υιος ην ουτος');
INSERT INTO `bible_verses_tr` VALUES ('1040', '40', '27', '55', 'ησαν δε εκει γυναικες πολλαι απο μακροθεν θεωρουσαι αιτινες ηκολουθησαν τω ιησου απο της γαλιλαιας διακονουσαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1041', '40', '27', '56', 'εν αις ην μαρια η μαγδαληνη και μαρια η του ιακωβου και ιωση μητηρ και η μητηρ των υιων ζεβεδαιου');
INSERT INTO `bible_verses_tr` VALUES ('1042', '40', '27', '57', 'οψιας δε γενομενης ηλθεν ανθρωπος πλουσιος απο αριμαθαιας τουνομα ιωσηφ ος και αυτος εμαθητευσεν τω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('1043', '40', '27', '58', 'ουτος προσελθων τω πιλατω ητησατο το σωμα του ιησου τοτε ο πιλατος εκελευσεν αποδοθηναι το σωμα');
INSERT INTO `bible_verses_tr` VALUES ('1044', '40', '27', '59', 'και λαβων το σωμα ο ιωσηφ ενετυλιξεν αυτο σινδονι καθαρα');
INSERT INTO `bible_verses_tr` VALUES ('1045', '40', '27', '60', 'και εθηκεν αυτο εν τω καινω αυτου μνημειω ο ελατομησεν εν τη πετρα και προσκυλισας λιθον μεγαν τη θυρα του μνημειου απηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('1046', '40', '27', '61', 'ην δε εκει μαρια η μαγδαληνη και η αλλη μαρια καθημεναι απεναντι του ταφου');
INSERT INTO `bible_verses_tr` VALUES ('1047', '40', '27', '62', 'τη δε επαυριον ητις εστιν μετα την παρασκευην συνηχθησαν οι αρχιερεις και οι φαρισαιοι προς πιλατον');
INSERT INTO `bible_verses_tr` VALUES ('1048', '40', '27', '63', 'λεγοντες κυριε εμνησθημεν οτι εκεινος ο πλανος ειπεν ετι ζων μετα τρεις ημερας εγειρομαι');
INSERT INTO `bible_verses_tr` VALUES ('1049', '40', '27', '64', 'κελευσον ουν ασφαλισθηναι τον ταφον εως της τριτης ημερας μηποτε ελθοντες οι μαθηται αυτου νυκτος κλεψωσιν αυτον και ειπωσιν τω λαω ηγερθη απο των νεκρων και εσται η εσχατη πλανη χειρων της πρωτης');
INSERT INTO `bible_verses_tr` VALUES ('1050', '40', '27', '65', 'εφη δε αυτοις ο πιλατος εχετε κουστωδιαν υπαγετε ασφαλισασθε ως οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('1051', '40', '27', '66', 'οι δε πορευθεντες ησφαλισαντο τον ταφον σφραγισαντες τον λιθον μετα της κουστωδιας');
INSERT INTO `bible_verses_tr` VALUES ('1052', '40', '28', '1', 'οψε δε σαββατων τη επιφωσκουση εις μιαν σαββατων ηλθεν μαρια η μαγδαληνη και η αλλη μαρια θεωρησαι τον ταφον');
INSERT INTO `bible_verses_tr` VALUES ('1053', '40', '28', '2', 'και ιδου σεισμος εγενετο μεγας αγγελος γαρ κυριου καταβας εξ ουρανου προσελθων απεκυλισεν τον λιθον απο της θυρας και εκαθητο επανω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1054', '40', '28', '3', 'ην δε η ιδεα αυτου ως αστραπη και το ενδυμα αυτου λευκον ωσει χιων');
INSERT INTO `bible_verses_tr` VALUES ('1055', '40', '28', '4', 'απο δε του φοβου αυτου εσεισθησαν οι τηρουντες και εγενοντο ωσει νεκροι');
INSERT INTO `bible_verses_tr` VALUES ('1056', '40', '28', '5', 'αποκριθεις δε ο αγγελος ειπεν ταις γυναιξιν μη φοβεισθε υμεις οιδα γαρ οτι ιησουν τον εσταυρωμενον ζητειτε');
INSERT INTO `bible_verses_tr` VALUES ('1057', '40', '28', '6', 'ουκ εστιν ωδε ηγερθη γαρ καθως ειπεν δευτε ιδετε τον τοπον οπου εκειτο ο κυριος');
INSERT INTO `bible_verses_tr` VALUES ('1058', '40', '28', '7', 'και ταχυ πορευθεισαι ειπατε τοις μαθηταις αυτου οτι ηγερθη απο των νεκρων και ιδου προαγει υμας εις την γαλιλαιαν εκει αυτον οψεσθε ιδου ειπον υμιν');
INSERT INTO `bible_verses_tr` VALUES ('1059', '40', '28', '8', 'και εξελθουσαι ταχυ απο του μνημειου μετα φοβου και χαρας μεγαλης εδραμον απαγγειλαι τοις μαθηταις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1060', '40', '28', '9', 'ως δε επορευοντο απαγγειλαι τοις μαθηταις αυτου και ιδου ο ιησους απηντησεν αυταις λεγων χαιρετε αι δε προσελθουσαι εκρατησαν αυτου τους ποδας και προσεκυνησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1061', '40', '28', '10', 'τοτε λεγει αυταις ο ιησους μη φοβεισθε υπαγετε απαγγειλατε τοις αδελφοις μου ινα απελθωσιν εις την γαλιλαιαν κακει με οψονται');
INSERT INTO `bible_verses_tr` VALUES ('1062', '40', '28', '11', 'πορευομενων δε αυτων ιδου τινες της κουστωδιας ελθοντες εις την πολιν απηγγειλαν τοις αρχιερευσιν απαντα τα γενομενα');
INSERT INTO `bible_verses_tr` VALUES ('1063', '40', '28', '12', 'και συναχθεντες μετα των πρεσβυτερων συμβουλιον τε λαβοντες αργυρια ικανα εδωκαν τοις στρατιωταις');
INSERT INTO `bible_verses_tr` VALUES ('1064', '40', '28', '13', 'λεγοντες ειπατε οτι οι μαθηται αυτου νυκτος ελθοντες εκλεψαν αυτον ημων κοιμωμενων');
INSERT INTO `bible_verses_tr` VALUES ('1065', '40', '28', '14', 'και εαν ακουσθη τουτο επι του ηγεμονος ημεις πεισομεν αυτον και υμας αμεριμνους ποιησομεν');
INSERT INTO `bible_verses_tr` VALUES ('1066', '40', '28', '15', 'οι δε λαβοντες τα αργυρια εποιησαν ως εδιδαχθησαν και διεφημισθη ο λογος ουτος παρα ιουδαιοις μεχρι της σημερον');
INSERT INTO `bible_verses_tr` VALUES ('1067', '40', '28', '16', 'οι δε ενδεκα μαθηται επορευθησαν εις την γαλιλαιαν εις το ορος ου εταξατο αυτοις ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('1068', '40', '28', '17', 'και ιδοντες αυτον προσεκυνησαν αυτω οι δε εδιστασαν');
INSERT INTO `bible_verses_tr` VALUES ('1069', '40', '28', '18', 'και προσελθων ο ιησους ελαλησεν αυτοις λεγων εδοθη μοι πασα εξουσια εν ουρανω και επι γης');
INSERT INTO `bible_verses_tr` VALUES ('1070', '40', '28', '19', 'πορευθεντες ουν μαθητευσατε παντα τα εθνη βαπτιζοντες αυτους εις το ονομα του πατρος και του υιου και του αγιου πνευματος');
INSERT INTO `bible_verses_tr` VALUES ('1071', '40', '28', '20', 'διδασκοντες αυτους τηρειν παντα οσα ενετειλαμην υμιν και ιδου εγω μεθ υμων ειμι πασας τας ημερας εως της συντελειας του αιωνος αμην');
INSERT INTO `bible_verses_tr` VALUES ('1072', '41', '1', '1', 'αρχη του ευαγγελιου ιησου χριστου υιου του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1073', '41', '1', '2', 'ως γεγραπται εν τοις προφηταις ιδου εγω αποστελλω τον αγγελον μου προ προσωπου σου ος κατασκευασει την οδον σου εμπροσθεν σου');
INSERT INTO `bible_verses_tr` VALUES ('1074', '41', '1', '3', 'φωνη βοωντος εν τη ερημω ετοιμασατε την οδον κυριου ευθειας ποιειτε τας τριβους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1075', '41', '1', '4', 'εγενετο ιωαννης βαπτιζων εν τη ερημω και κηρυσσων βαπτισμα μετανοιας εις αφεσιν αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('1076', '41', '1', '5', 'και εξεπορευετο προς αυτον πασα η ιουδαια χωρα και οι ιεροσολυμιται και εβαπτιζοντο παντες εν τω ιορδανη ποταμω υπ αυτου εξομολογουμενοι τας αμαρτιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1077', '41', '1', '6', 'ην δε ιωαννης ενδεδυμενος τριχας καμηλου και ζωνην δερματινην περι την οσφυν αυτου και εσθιων ακριδας και μελι αγριον');
INSERT INTO `bible_verses_tr` VALUES ('1078', '41', '1', '7', 'και εκηρυσσεν λεγων ερχεται ο ισχυροτερος μου οπισω μου ου ουκ ειμι ικανος κυψας λυσαι τον ιμαντα των υποδηματων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1079', '41', '1', '8', 'εγω μεν εβαπτισα υμας εν υδατι αυτος δε βαπτισει υμας εν πνευματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('1080', '41', '1', '9', 'και εγενετο εν εκειναις ταις ημεραις ηλθεν ιησους απο  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } της γαλιλαιας και εβαπτισθη υπο ιωαννου εις τον ιορδανην');
INSERT INTO `bible_verses_tr` VALUES ('1081', '41', '1', '10', 'και ευθεως αναβαινων απο του υδατος ειδεν σχιζομενους τους ουρανους και το πνευμα ωσει περιστεραν καταβαινον επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1082', '41', '1', '11', 'και φωνη εγενετο εκ των ουρανων συ ει ο υιος μου ο αγαπητος εν ω ευδοκησα');
INSERT INTO `bible_verses_tr` VALUES ('1083', '41', '1', '12', 'και ευθυς το πνευμα αυτον εκβαλλει εις την ερημον');
INSERT INTO `bible_verses_tr` VALUES ('1084', '41', '1', '13', 'και ην εκει εν τη ερημω ημερας τεσσαρακοντα πειραζομενος υπο του σατανα και ην μετα των θηριων και οι αγγελοι διηκονουν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1085', '41', '1', '14', 'μετα δε το παραδοθηναι τον ιωαννην ηλθεν ο ιησους εις την γαλιλαιαν κηρυσσων το ευαγγελιον της βασιλειας του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1086', '41', '1', '15', 'και λεγων οτι πεπληρωται ο καιρος και ηγγικεν η βασιλεια του θεου μετανοειτε και πιστευετε εν τω ευαγγελιω');
INSERT INTO `bible_verses_tr` VALUES ('1087', '41', '1', '16', 'περιπατων δε παρα την θαλασσαν της γαλιλαιας ειδεν σιμωνα και ανδρεαν τον αδελφον αυτου βαλλοντας αμφιβληστρον εν τη θαλασση ησαν γαρ αλιεις');
INSERT INTO `bible_verses_tr` VALUES ('1088', '41', '1', '17', 'και ειπεν αυτοις ο ιησους δευτε οπισω μου και ποιησω υμας γενεσθαι αλιεις ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('1089', '41', '1', '18', 'και ευθεως αφεντες τα δικτυα αυτων ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1090', '41', '1', '19', 'και προβας εκειθεν ολιγον ειδεν ιακωβον τον του ζεβεδαιου και ιωαννην τον αδελφον αυτου και αυτους εν τω πλοιω καταρτιζοντας τα δικτυα');
INSERT INTO `bible_verses_tr` VALUES ('1091', '41', '1', '20', 'και ευθεως εκαλεσεν αυτους και αφεντες τον πατερα αυτων ζεβεδαιον εν τω πλοιω μετα των μισθωτων απηλθον οπισω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1092', '41', '1', '21', 'και εισπορευονται εις καπερναουμ και ευθεως τοις σαββασιν εισελθων εις την συναγωγην εδιδασκεν');
INSERT INTO `bible_verses_tr` VALUES ('1093', '41', '1', '22', 'και εξεπλησσοντο επι τη διδαχη αυτου ην γαρ διδασκων αυτους ως εξουσιαν εχων και ουχ ως οι γραμματεις');
INSERT INTO `bible_verses_tr` VALUES ('1094', '41', '1', '23', 'και ην εν τη συναγωγη αυτων ανθρωπος εν πνευματι ακαθαρτω και ανεκραξεν');
INSERT INTO `bible_verses_tr` VALUES ('1095', '41', '1', '24', 'λεγων εα τι ημιν και σοι ιησου ναζαρηνε ηλθες απολεσαι ημας οιδα σε τις ει ο αγιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1096', '41', '1', '25', 'και επετιμησεν αυτω ο ιησους λεγων φιμωθητι και εξελθε εξ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1097', '41', '1', '26', 'και σπαραξαν αυτον το πνευμα το ακαθαρτον και κραξαν φωνη μεγαλη εξηλθεν εξ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1098', '41', '1', '27', 'και εθαμβηθησαν παντες ωστε συζητειν προς αυτους λεγοντας τι εστιν τουτο τις η διδαχη η καινη αυτη οτι κατ εξουσιαν και τοις πνευμασιν τοις ακαθαρτοις επιτασσει και υπακουουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1099', '41', '1', '28', 'εξηλθεν δε η ακοη αυτου ευθυς εις ολην την περιχωρον της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('1100', '41', '1', '29', 'και ευθεως εκ της συναγωγης εξελθοντες ηλθον εις την οικιαν σιμωνος και ανδρεου μετα ιακωβου και ιωαννου');
INSERT INTO `bible_verses_tr` VALUES ('1101', '41', '1', '30', 'η δε πενθερα σιμωνος κατεκειτο πυρεσσουσα και ευθεως λεγουσιν αυτω περι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1102', '41', '1', '31', 'και προσελθων ηγειρεν αυτην κρατησας της χειρος αυτης και αφηκεν αυτην ο πυρετος ευθεως και διηκονει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1103', '41', '1', '32', 'οψιας δε γενομενης οτε εδυ ο ηλιος εφερον προς αυτον παντας τους κακως εχοντας και τους δαιμονιζομενους');
INSERT INTO `bible_verses_tr` VALUES ('1104', '41', '1', '33', 'και η πολις ολη επισυνηγμενη ην προς την θυραν');
INSERT INTO `bible_verses_tr` VALUES ('1105', '41', '1', '34', 'και εθεραπευσεν πολλους κακως εχοντας ποικιλαις νοσοις και δαιμονια πολλα εξεβαλεν και ουκ ηφιεν λαλειν τα δαιμονια οτι ηδεισαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1106', '41', '1', '35', 'και πρωι εννυχον λιαν αναστας εξηλθεν και απηλθεν εις ερημον τοπον κακει προσηυχετο');
INSERT INTO `bible_verses_tr` VALUES ('1107', '41', '1', '36', 'και κατεδιωξαν αυτον ο σιμων και οι μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1108', '41', '1', '37', 'και ευροντες αυτον λεγουσιν αυτω οτι παντες ζητουσιν σε');
INSERT INTO `bible_verses_tr` VALUES ('1109', '41', '1', '38', 'και λεγει αυτοις αγωμεν εις τας εχομενας κωμοπολεις ινα κακει κηρυξω εις τουτο γαρ εξεληλυθα');
INSERT INTO `bible_verses_tr` VALUES ('1110', '41', '1', '39', 'και ην κηρυσσων εν ταις συναγωγαις αυτων εις ολην την γαλιλαιαν και τα δαιμονια εκβαλλων');
INSERT INTO `bible_verses_tr` VALUES ('1111', '41', '1', '40', 'και ερχεται προς αυτον λεπρος παρακαλων αυτον και γονυπετων αυτον και λεγων αυτω οτι εαν θελης δυνασαι με καθαρισαι');
INSERT INTO `bible_verses_tr` VALUES ('1112', '41', '1', '41', 'ο δε ιησους σπλαγχνισθεις εκτεινας την χειρα ηψατο αυτου και λεγει αυτω θελω καθαρισθητι');
INSERT INTO `bible_verses_tr` VALUES ('1113', '41', '1', '42', 'και ειποντος αυτου ευθεως απηλθεν απ αυτου η λεπρα και εκαθαρισθη');
INSERT INTO `bible_verses_tr` VALUES ('1114', '41', '1', '43', 'και εμβριμησαμενος αυτω ευθεως εξεβαλεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1115', '41', '1', '44', 'και λεγει αυτω ορα μηδενι μηδεν ειπης αλλ υπαγε σεαυτον δειξον τω ιερει και προσενεγκε περι του καθαρισμου σου α προσεταξεν μωσης εις μαρτυριον αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1116', '41', '1', '45', 'ο δε εξελθων ηρξατο κηρυσσειν πολλα και διαφημιζειν τον λογον ωστε μηκετι αυτον δυνασθαι φανερως εις πολιν εισελθειν αλλ εξω εν ερημοις τοποις ην και ηρχοντο προς αυτον πανταχοθεν');
INSERT INTO `bible_verses_tr` VALUES ('1117', '41', '2', '1', 'και παλιν εισηλθεν εις καπερναουμ δι ημερων και ηκουσθη οτι εις οικον εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1118', '41', '2', '2', 'και ευθεως συνηχθησαν πολλοι ωστε μηκετι χωρειν μηδε τα προς την θυραν και ελαλει αυτοις τον λογον');
INSERT INTO `bible_verses_tr` VALUES ('1119', '41', '2', '3', 'και ερχονται προς αυτον παραλυτικον φεροντες αιρομενον υπο τεσσαρων');
INSERT INTO `bible_verses_tr` VALUES ('1120', '41', '2', '4', 'και μη δυναμενοι προσεγγισαι αυτω δια τον οχλον απεστεγασαν την στεγην οπου ην και εξορυξαντες χαλωσιν τον κραββατον εφ ω ο παραλυτικος κατεκειτο');
INSERT INTO `bible_verses_tr` VALUES ('1121', '41', '2', '5', 'ιδων δε ο ιησους την πιστιν αυτων λεγει τω παραλυτικω τεκνον αφεωνται σοι αι αμαρτιαι σου');
INSERT INTO `bible_verses_tr` VALUES ('1122', '41', '2', '6', 'ησαν δε τινες των γραμματεων εκει καθημενοι και διαλογιζομενοι εν ταις καρδιαις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1123', '41', '2', '7', 'τι ουτος ουτως λαλει βλασφημιας τις δυναται αφιεναι αμαρτιας ει μη εις ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('1124', '41', '2', '8', 'και ευθεως επιγνους ο ιησους τω πνευματι αυτου οτι ουτως διαλογιζονται εν εαυτοις ειπεν αυτοις τι ταυτα διαλογιζεσθε εν ταις καρδιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('1125', '41', '2', '9', 'τι εστιν ευκοπωτερον ειπειν τω παραλυτικω αφεωνται σοι αι αμαρτιαι η ειπειν εγειραι και αρον σου τον κραββατον και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('1126', '41', '2', '10', 'ινα δε ειδητε οτι εξουσιαν εχει ο υιος του ανθρωπου αφιεναι επι της γης αμαρτιας λεγει τω παραλυτικω');
INSERT INTO `bible_verses_tr` VALUES ('1127', '41', '2', '11', 'σοι λεγω εγειραι και αρον τον κραββατον σου και υπαγε εις τον οικον σου');
INSERT INTO `bible_verses_tr` VALUES ('1128', '41', '2', '12', 'και ηγερθη ευθεως και αρας τον κραββατον εξηλθεν εναντιον παντων ωστε εξιστασθαι παντας και δοξαζειν τον θεον λεγοντας οτι ουδεποτε ουτως ειδομεν');
INSERT INTO `bible_verses_tr` VALUES ('1129', '41', '2', '13', 'και εξηλθεν παλιν παρα την θαλασσαν και πας ο οχλος ηρχετο προς αυτον και εδιδασκεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1130', '41', '2', '14', 'και παραγων ειδεν λευιν τον του αλφαιου καθημενον επι το τελωνιον και λεγει αυτω ακολουθει μοι και αναστας ηκολουθησεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1131', '41', '2', '15', 'και εγενετο εν τω κατακεισθαι αυτον εν τη οικια αυτου και πολλοι τελωναι και αμαρτωλοι συνανεκειντο τω ιησου και τοις μαθηταις αυτου ησαν γαρ πολλοι και ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1132', '41', '2', '16', 'και οι γραμματεις και οι φαρισαιοι ιδοντες αυτον εσθιοντα μετα των τελωνων και αμαρτωλων ελεγον τοις μαθηταις αυτου τι οτι μετα των τελωνων και αμαρτωλων εσθιει και πινει');
INSERT INTO `bible_verses_tr` VALUES ('1133', '41', '2', '17', 'και ακουσας ο ιησους λεγει αυτοις ου χρειαν εχουσιν οι ισχυοντες ιατρου αλλ οι κακως εχοντες ουκ ηλθον καλεσαι δικαιους αλλα αμαρτωλους εις μετανοιαν');
INSERT INTO `bible_verses_tr` VALUES ('1134', '41', '2', '18', 'και ησαν οι μαθηται ιωαννου και οι των φαρισαιων νηστευοντες και ερχονται και λεγουσιν αυτω δια τι οι μαθηται ιωαννου και οι των φαρισαιων νηστευουσιν οι δε σοι μαθηται ου νηστευουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1135', '41', '2', '19', 'και ειπεν αυτοις ο ιησους μη δυνανται οι υιοι του νυμφωνος εν ω ο νυμφιος μετ αυτων εστιν νηστευειν οσον χρονον μεθ εαυτων εχουσιν τον νυμφιον ου δυνανται νηστευειν');
INSERT INTO `bible_verses_tr` VALUES ('1136', '41', '2', '20', 'ελευσονται δε ημεραι οταν απαρθη απ αυτων ο νυμφιος και τοτε νηστευσουσιν εν εκειναις ταις ημεραις');
INSERT INTO `bible_verses_tr` VALUES ('1137', '41', '2', '21', 'και ουδεις επιβλημα ρακους αγναφου επιρραπτει επι ιματιω παλαιω ει δε μη αιρει το πληρωμα αυτου το καινον του παλαιου και χειρον σχισμα γινεται');
INSERT INTO `bible_verses_tr` VALUES ('1138', '41', '2', '22', 'και ουδεις βαλλει οινον νεον εις ασκους παλαιους ει δε μη ρησσει ο οινος ο νεος τους ασκους και ο οινος εκχειται και οι ασκοι απολουνται αλλα οινον νεον εις ασκους καινους βλητεον');
INSERT INTO `bible_verses_tr` VALUES ('1139', '41', '2', '23', 'και εγενετο παραπορευεσθαι αυτον εν τοις σαββασιν δια των σποριμων και ηρξαντο οι μαθηται αυτου οδον ποιειν τιλλοντες τους σταχυας');
INSERT INTO `bible_verses_tr` VALUES ('1140', '41', '2', '24', 'και οι φαρισαιοι ελεγον αυτω ιδε τι ποιουσιν εν τοις σαββασιν ο ουκ εξεστιν');
INSERT INTO `bible_verses_tr` VALUES ('1141', '41', '2', '25', 'και αυτος ελεγεν αυτοις ουδεποτε ανεγνωτε τι εποιησεν δαβιδ οτε χρειαν εσχεν και επεινασεν αυτος και οι μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1142', '41', '2', '26', 'πως εισηλθεν εις τον οικον του θεου επι αβιαθαρ του αρχιερεως και τους αρτους της προθεσεως εφαγεν ους ουκ εξεστιν φαγειν ει μη τοις ιερευσιν και εδωκεν και τοις συν αυτω ουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1143', '41', '2', '27', 'και ελεγεν αυτοις το σαββατον δια τον ανθρωπον εγενετο ουχ ο ανθρωπος δια το σαββατον');
INSERT INTO `bible_verses_tr` VALUES ('1144', '41', '2', '28', 'ωστε κυριος εστιν ο υιος του ανθρωπου και του σαββατου');
INSERT INTO `bible_verses_tr` VALUES ('1145', '41', '3', '1', 'και εισηλθεν παλιν εις την συναγωγην και ην εκει ανθρωπος εξηραμμενην εχων την χειρα');
INSERT INTO `bible_verses_tr` VALUES ('1146', '41', '3', '2', 'και παρετηρουν αυτον ει τοις σαββασιν θεραπευσει αυτον ινα κατηγορησωσιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1147', '41', '3', '3', 'και λεγει τω ανθρωπω τω εξηραμμενην εχοντι την χειρα εγειραι εις το μεσον');
INSERT INTO `bible_verses_tr` VALUES ('1148', '41', '3', '4', 'και λεγει αυτοις εξεστιν τοις σαββασιν αγαθοποιησαι η κακοποιησαι ψυχην σωσαι η αποκτειναι οι δε εσιωπων');
INSERT INTO `bible_verses_tr` VALUES ('1149', '41', '3', '5', 'και περιβλεψαμενος αυτους μετ οργης συλλυπουμενος επι τη πωρωσει της καρδιας αυτων λεγει τω ανθρωπω εκτεινον την χειρα σου και εξετεινεν και αποκατεσταθη η χειρ αυτου υγιης ως η αλλη');
INSERT INTO `bible_verses_tr` VALUES ('1150', '41', '3', '6', 'και εξελθοντες οι φαρισαιοι ευθεως μετα των ηρωδιανων συμβουλιον εποιουν κατ αυτου οπως αυτον απολεσωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1151', '41', '3', '7', 'και ο ιησους ανεχωρησεν μετα των μαθητων αυτου προς την θαλασσαν και πολυ πληθος απο της γαλιλαιας ηκολουθησαν αυτω και απο της ιουδαιας');
INSERT INTO `bible_verses_tr` VALUES ('1152', '41', '3', '8', 'και απο ιεροσολυμων και απο της ιδουμαιας και περαν του ιορδανου και οι περι τυρον και σιδωνα πληθος πολυ ακουσαντες οσα εποιει ηλθον προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1153', '41', '3', '9', 'και ειπεν τοις μαθηταις αυτου ινα πλοιαριον προσκαρτερη αυτω δια τον οχλον ινα μη θλιβωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1154', '41', '3', '10', 'πολλους γαρ εθεραπευσεν ωστε επιπιπτειν αυτω ινα αυτου αψωνται οσοι ειχον μαστιγας');
INSERT INTO `bible_verses_tr` VALUES ('1155', '41', '3', '11', 'και τα πνευματα τα ακαθαρτα οταν αυτον εθεωρει προσεπιπτεν αυτω και εκραζεν λεγοντα οτι συ ει ο υιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1156', '41', '3', '12', 'και πολλα επετιμα αυτοις ινα μη αυτον φανερον ποιησωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1157', '41', '3', '13', 'και αναβαινει εις το ορος και προσκαλειται ους ηθελεν αυτος και απηλθον προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1158', '41', '3', '14', 'και εποιησεν δωδεκα ινα ωσιν μετ αυτου και ινα αποστελλη αυτους κηρυσσειν');
INSERT INTO `bible_verses_tr` VALUES ('1159', '41', '3', '15', 'και εχειν εξουσιαν θεραπευειν τας νοσους και εκβαλλειν τα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('1160', '41', '3', '16', 'και επεθηκεν τω σιμωνι ονομα πετρον');
INSERT INTO `bible_verses_tr` VALUES ('1161', '41', '3', '17', 'και ιακωβον τον του ζεβεδαιου και ιωαννην τον αδελφον του ιακωβου και επεθηκεν αυτοις ονοματα βοανεργες ο εστιν υιοι βροντης');
INSERT INTO `bible_verses_tr` VALUES ('1162', '41', '3', '18', 'και ανδρεαν και φιλιππον και βαρθολομαιον και ματθαιον και θωμαν και ιακωβον τον του αλφαιου και θαδδαιον και σιμωνα τον κανανιτην');
INSERT INTO `bible_verses_tr` VALUES ('1163', '41', '3', '19', 'και ιουδαν ισκαριωτην ος και παρεδωκεν αυτον και ερχονται εις οικον');
INSERT INTO `bible_verses_tr` VALUES ('1164', '41', '3', '20', 'και συνερχεται παλιν οχλος ωστε μη δυνασθαι αυτους μητε αρτον φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('1165', '41', '3', '21', 'και ακουσαντες οι παρ αυτου εξηλθον κρατησαι αυτον ελεγον γαρ οτι εξεστη');
INSERT INTO `bible_verses_tr` VALUES ('1166', '41', '3', '22', 'και οι γραμματεις οι απο ιεροσολυμων καταβαντες ελεγον οτι βεελζεβουλ εχει και οτι εν τω αρχοντι των δαιμονιων εκβαλλει τα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('1167', '41', '3', '23', 'και προσκαλεσαμενος αυτους εν παραβολαις ελεγεν αυτοις πως δυναται σατανας σαταναν εκβαλλειν');
INSERT INTO `bible_verses_tr` VALUES ('1168', '41', '3', '24', 'και εαν βασιλεια εφ εαυτην μερισθη ου δυναται σταθηναι η βασιλεια εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('1169', '41', '3', '25', 'και εαν οικια εφ εαυτην μερισθη ου δυναται σταθηναι η οικια εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('1170', '41', '3', '26', 'και ει ο σατανας ανεστη εφ εαυτον και μεμερισται ου δυναται σταθηναι αλλα τελος εχει');
INSERT INTO `bible_verses_tr` VALUES ('1171', '41', '3', '27', 'ου δυναται ουδεις τα σκευη του ισχυρου εισελθων εις την οικιαν αυτου διαρπασαι εαν μη πρωτον τον ισχυρον δηση και τοτε την οικιαν αυτου διαρπασει');
INSERT INTO `bible_verses_tr` VALUES ('1172', '41', '3', '28', 'αμην λεγω υμιν οτι παντα αφεθησεται τα αμαρτηματα τοις υιοις των ανθρωπων και βλασφημιαι οσας αν βλασφημησωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1173', '41', '3', '29', 'ος δ αν βλασφημηση εις το πνευμα το αγιον ουκ εχει αφεσιν εις τον αιωνα αλλ ενοχος εστιν αιωνιου κρισεως');
INSERT INTO `bible_verses_tr` VALUES ('1174', '41', '3', '30', 'οτι ελεγον πνευμα ακαθαρτον εχει');
INSERT INTO `bible_verses_tr` VALUES ('1175', '41', '3', '31', 'ερχονται ουν οι αδελφοι και η μητηρ αυτου και εξω εστωτες απεστειλαν προς αυτον φωνουντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1176', '41', '3', '32', 'και εκαθητο οχλος περι αυτον ειπον δε αυτω ιδου η μητηρ σου και οι αδελφοι σου εξω ζητουσιν σε');
INSERT INTO `bible_verses_tr` VALUES ('1177', '41', '3', '33', 'και απεκριθη αυτοις λεγων τις εστιν η μητηρ μου η οι αδελφοι μου');
INSERT INTO `bible_verses_tr` VALUES ('1178', '41', '3', '34', 'και περιβλεψαμενος κυκλω τους περι αυτον καθημενους λεγει ιδε η μητηρ μου και οι αδελφοι μου');
INSERT INTO `bible_verses_tr` VALUES ('1179', '41', '3', '35', 'ος γαρ αν ποιηση το θελημα του θεου ουτος αδελφος μου και αδελφη μου και μητηρ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1180', '41', '4', '1', 'και παλιν ηρξατο διδασκειν παρα την θαλασσαν και συνηχθη προς αυτον οχλος πολυς ωστε αυτον εμβαντα εις το πλοιον καθησθαι εν τη θαλασση και πας ο οχλος προς την θαλασσαν επι της γης ην');
INSERT INTO `bible_verses_tr` VALUES ('1181', '41', '4', '2', 'και εδιδασκεν αυτους εν παραβολαις πολλα και ελεγεν αυτοις εν τη διδαχη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1182', '41', '4', '3', 'ακουετε ιδου εξηλθεν ο σπειρων του σπειραι');
INSERT INTO `bible_verses_tr` VALUES ('1183', '41', '4', '4', 'και εγενετο εν τω σπειρειν ο μεν επεσεν παρα την οδον και ηλθεν τα πετεινα του ουρανου και κατεφαγεν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('1184', '41', '4', '5', 'αλλο δε επεσεν επι το πετρωδες οπου ουκ ειχεν γην πολλην και ευθεως εξανετειλεν δια το μη εχειν βαθος γης');
INSERT INTO `bible_verses_tr` VALUES ('1185', '41', '4', '6', 'ηλιου δε ανατειλαντος εκαυματισθη και δια το μη εχειν ριζαν εξηρανθη');
INSERT INTO `bible_verses_tr` VALUES ('1186', '41', '4', '7', 'και αλλο επεσεν εις τας ακανθας και ανεβησαν αι ακανθαι και συνεπνιξαν αυτο και καρπον ουκ εδωκεν');
INSERT INTO `bible_verses_tr` VALUES ('1187', '41', '4', '8', 'και αλλο επεσεν εις την γην την καλην και εδιδου καρπον αναβαινοντα και αυξανοντα και εφερεν εν τριακοντα και εν εξηκοντα και εν εκατον');
INSERT INTO `bible_verses_tr` VALUES ('1188', '41', '4', '9', 'και ελεγεν αυτοις ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('1189', '41', '4', '10', 'οτε δε εγενετο καταμονας ηρωτησαν αυτον οι περι αυτον συν τοις δωδεκα την παραβολην');
INSERT INTO `bible_verses_tr` VALUES ('1190', '41', '4', '11', 'και ελεγεν αυτοις υμιν δεδοται γνωναι το μυστηριον της βασιλειας του θεου εκεινοις δε τοις εξω εν παραβολαις τα παντα γινεται');
INSERT INTO `bible_verses_tr` VALUES ('1191', '41', '4', '12', 'ινα βλεποντες βλεπωσιν και μη ιδωσιν και ακουοντες ακουωσιν και μη συνιωσιν μηποτε επιστρεψωσιν και αφεθη αυτοις τα αμαρτηματα');
INSERT INTO `bible_verses_tr` VALUES ('1192', '41', '4', '13', 'και λεγει αυτοις ουκ οιδατε την παραβολην ταυτην και πως πασας τας παραβολας γνωσεσθε');
INSERT INTO `bible_verses_tr` VALUES ('1193', '41', '4', '14', 'ο σπειρων τον λογον σπειρει');
INSERT INTO `bible_verses_tr` VALUES ('1194', '41', '4', '15', 'ουτοι δε εισιν οι παρα την οδον οπου σπειρεται ο λογος και οταν ακουσωσιν ευθεως ερχεται ο σατανας και αιρει τον λογον τον εσπαρμενον εν ταις καρδιαις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1195', '41', '4', '16', 'και ουτοι εισιν ομοιως οι επι τα πετρωδη σπειρομενοι οι οταν ακουσωσιν τον λογον ευθεως μετα χαρας λαμβανουσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1196', '41', '4', '17', 'και ουκ εχουσιν ριζαν εν εαυτοις αλλα προσκαιροι εισιν ειτα γενομενης θλιψεως η διωγμου δια τον λογον ευθεως σκανδαλιζονται');
INSERT INTO `bible_verses_tr` VALUES ('1197', '41', '4', '18', 'και ουτοι εισιν οι εις τας ακανθας σπειρομενοι  {VAR1: ουτοι εισιν } οι τον λογον ακουοντες');
INSERT INTO `bible_verses_tr` VALUES ('1198', '41', '4', '19', 'και αι μεριμναι του αιωνος τουτου και η απατη του πλουτου και αι περι τα λοιπα επιθυμιαι εισπορευομεναι συμπνιγουσιν τον λογον και ακαρπος γινεται');
INSERT INTO `bible_verses_tr` VALUES ('1199', '41', '4', '20', 'και ουτοι εισιν οι επι την γην την καλην σπαρεντες οιτινες ακουουσιν τον λογον και παραδεχονται και καρποφορουσιν εν τριακοντα και εν εξηκοντα και εν εκατον');
INSERT INTO `bible_verses_tr` VALUES ('1200', '41', '4', '21', 'και ελεγεν αυτοις μητι ο λυχνος ερχεται ινα υπο τον μοδιον τεθη η υπο την κλινην ουχ ινα επι την λυχνιαν επιτεθη');
INSERT INTO `bible_verses_tr` VALUES ('1201', '41', '4', '22', 'ου γαρ εστιν τι κρυπτον ο εαν μη φανερωθη ουδε εγενετο αποκρυφον αλλ ινα εις φανερον ελθη');
INSERT INTO `bible_verses_tr` VALUES ('1202', '41', '4', '23', 'ει τις εχει ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('1203', '41', '4', '24', 'και ελεγεν αυτοις βλεπετε τι ακουετε εν ω μετρω μετρειτε μετρηθησεται υμιν και προστεθησεται υμιν τοις ακουουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1204', '41', '4', '25', 'ος γαρ αν εχη δοθησεται αυτω και ος ουκ εχει και ο εχει αρθησεται απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1205', '41', '4', '26', 'και ελεγεν ουτως εστιν η βασιλεια του θεου ως εαν ανθρωπος βαλη τον σπορον επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('1206', '41', '4', '27', 'και καθευδη και εγειρηται νυκτα και ημεραν και ο σπορος βλαστανη και μηκυνηται ως ουκ οιδεν αυτος');
INSERT INTO `bible_verses_tr` VALUES ('1207', '41', '4', '28', 'αυτοματη γαρ η γη καρποφορει πρωτον χορτον ειτα σταχυν ειτα πληρη σιτον εν τω σταχυι');
INSERT INTO `bible_verses_tr` VALUES ('1208', '41', '4', '29', 'οταν δε παραδω ο καρπος ευθεως αποστελλει το δρεπανον οτι παρεστηκεν ο θερισμος');
INSERT INTO `bible_verses_tr` VALUES ('1209', '41', '4', '30', 'και ελεγεν τινι ομοιωσωμεν την βασιλειαν του θεου η εν ποια παραβολη παραβαλωμεν αυτην');
INSERT INTO `bible_verses_tr` VALUES ('1210', '41', '4', '31', 'ως κοκκω σιναπεως ος οταν σπαρη επι της γης μικροτερος παντων των σπερματων εστιν των επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('1211', '41', '4', '32', 'και οταν σπαρη αναβαινει και γινεται παντων των λαχανων μειζων και ποιει κλαδους μεγαλους ωστε δυνασθαι υπο την σκιαν αυτου τα πετεινα του ουρανου κατασκηνουν');
INSERT INTO `bible_verses_tr` VALUES ('1212', '41', '4', '33', 'και τοιαυταις παραβολαις πολλαις ελαλει αυτοις τον λογον καθως ηδυναντο ακουειν');
INSERT INTO `bible_verses_tr` VALUES ('1213', '41', '4', '34', 'χωρις δε παραβολης ουκ ελαλει αυτοις κατ ιδιαν δε τοις μαθηταις αυτου επελυεν παντα');
INSERT INTO `bible_verses_tr` VALUES ('1214', '41', '4', '35', 'και λεγει αυτοις εν εκεινη τη ημερα οψιας γενομενης διελθωμεν εις το περαν');
INSERT INTO `bible_verses_tr` VALUES ('1215', '41', '4', '36', 'και αφεντες τον οχλον παραλαμβανουσιν αυτον ως ην εν τω πλοιω και αλλα δε πλοιαρια ην μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1216', '41', '4', '37', 'και γινεται λαιλαψ ανεμου μεγαλη τα δε κυματα επεβαλλεν εις το πλοιον ωστε αυτο ηδη γεμιζεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('1217', '41', '4', '38', 'και ην αυτος επι τη πρυμνη επι το προσκεφαλαιον καθευδων και διεγειρουσιν αυτον και λεγουσιν αυτω διδασκαλε ου μελει σοι οτι απολλυμεθα');
INSERT INTO `bible_verses_tr` VALUES ('1218', '41', '4', '39', 'και διεγερθεις επετιμησεν τω ανεμω και ειπεν τη θαλασση σιωπα πεφιμωσο και εκοπασεν ο ανεμος και εγενετο γαληνη μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('1219', '41', '4', '40', 'και ειπεν αυτοις τι δειλοι εστε ουτως πως ουκ εχετε πιστιν');
INSERT INTO `bible_verses_tr` VALUES ('1220', '41', '4', '41', 'και εφοβηθησαν φοβον μεγαν και ελεγον προς αλληλους τις αρα ουτος εστιν οτι και ο ανεμος και η θαλασσα υπακουουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1221', '41', '5', '1', 'και ηλθον εις το περαν της θαλασσης εις την χωραν των γαδαρηνων');
INSERT INTO `bible_verses_tr` VALUES ('1222', '41', '5', '2', 'και εξελθοντι αυτω εκ του πλοιου ευθεως απηντησεν αυτω εκ των μνημειων ανθρωπος εν πνευματι ακαθαρτω');
INSERT INTO `bible_verses_tr` VALUES ('1223', '41', '5', '3', 'ος την κατοικησιν ειχεν εν τοις μνημειοις και ουτε αλυσεσιν ουδεις ηδυνατο αυτον δησαι');
INSERT INTO `bible_verses_tr` VALUES ('1224', '41', '5', '4', 'δια το αυτον πολλακις πεδαις και αλυσεσιν δεδεσθαι και διεσπασθαι υπ αυτου τας αλυσεις και τας πεδας συντετριφθαι και ουδεις αυτον ισχυεν δαμασαι');
INSERT INTO `bible_verses_tr` VALUES ('1225', '41', '5', '5', 'και διαπαντος νυκτος και ημερας εν τοις ορεσιν και εν τοις μνημασιν ην κραζων και κατακοπτων εαυτον λιθοις');
INSERT INTO `bible_verses_tr` VALUES ('1226', '41', '5', '6', 'ιδων δε τον ιησουν απο μακροθεν εδραμεν και προσεκυνησεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1227', '41', '5', '7', 'και κραξας φωνη μεγαλη ειπεν τι εμοι και σοι ιησου υιε του θεου του υψιστου ορκιζω σε τον θεον μη με βασανισης');
INSERT INTO `bible_verses_tr` VALUES ('1228', '41', '5', '8', 'ελεγεν γαρ αυτω εξελθε το πνευμα το ακαθαρτον εκ του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('1229', '41', '5', '9', 'και επηρωτα αυτον τι σοι ονομα και απεκριθη λεγων λεγεων ονομα μοι οτι πολλοι εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('1230', '41', '5', '10', 'και παρεκαλει αυτον πολλα ινα μη αυτους αποστειλη εξω της χωρας');
INSERT INTO `bible_verses_tr` VALUES ('1231', '41', '5', '11', 'ην δε εκει προς τα ορη αγελη χοιρων μεγαλη βοσκομενη');
INSERT INTO `bible_verses_tr` VALUES ('1232', '41', '5', '12', 'και παρεκαλεσαν αυτον παντες οι δαιμονες λεγοντες πεμψον ημας εις τους χοιρους ινα εις αυτους εισελθωμεν');
INSERT INTO `bible_verses_tr` VALUES ('1233', '41', '5', '13', 'και επετρεψεν αυτοις ευθεως ο ιησους και εξελθοντα τα πνευματα τα ακαθαρτα εισηλθον εις τους χοιρους και ωρμησεν η αγελη κατα του κρημνου εις την θαλασσαν ησαν δε ως δισχιλιοι και επνιγοντο εν τη θαλασση');
INSERT INTO `bible_verses_tr` VALUES ('1234', '41', '5', '14', 'οι δε βοσκοντες τους χοιρους εφυγον και ανηγγειλαν εις την πολιν και εις τους αγρους και εξηλθον ιδειν τι εστιν το γεγονος');
INSERT INTO `bible_verses_tr` VALUES ('1235', '41', '5', '15', 'και ερχονται προς τον ιησουν και θεωρουσιν τον δαιμονιζομενον καθημενον και ιματισμενον και σωφρονουντα τον εσχηκοτα τον λεγεωνα και εφοβηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('1236', '41', '5', '16', 'και διηγησαντο αυτοις οι ιδοντες πως εγενετο τω δαιμονιζομενω και περι των χοιρων');
INSERT INTO `bible_verses_tr` VALUES ('1237', '41', '5', '17', 'και ηρξαντο παρακαλειν αυτον απελθειν απο των οριων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1238', '41', '5', '18', 'και εμβαντος αυτου εις το πλοιον παρεκαλει αυτον ο δαιμονισθεις ινα η μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1239', '41', '5', '19', 'ο δε ιησους ουκ αφηκεν αυτον αλλα λεγει αυτω υπαγε εις τον οικον σου προς τους σους και αναγγειλον αυτοις οσα σοι ο κυριος εποιησεν και ηλεησεν σε');
INSERT INTO `bible_verses_tr` VALUES ('1240', '41', '5', '20', 'και απηλθεν και ηρξατο κηρυσσειν εν τη δεκαπολει οσα εποιησεν αυτω ο ιησους και παντες εθαυμαζον');
INSERT INTO `bible_verses_tr` VALUES ('1241', '41', '5', '21', 'και διαπερασαντος του ιησου εν τω πλοιω παλιν εις το περαν συνηχθη οχλος πολυς επ αυτον και ην παρα την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('1242', '41', '5', '22', 'και ιδου ερχεται εις των αρχισυναγωγων ονοματι ιαειρος και ιδων αυτον πιπτει προς τους ποδας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1243', '41', '5', '23', 'και παρεκαλει αυτον πολλα λεγων οτι το θυγατριον μου εσχατως εχει ινα ελθων επιθης αυτη τας χειρας οπως σωθη και ζησεται');
INSERT INTO `bible_verses_tr` VALUES ('1244', '41', '5', '24', 'και απηλθεν μετ αυτου και ηκολουθει αυτω οχλος πολυς και συνεθλιβον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1245', '41', '5', '25', 'και γυνη τις ουσα εν ρυσει αιματος ετη δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('1246', '41', '5', '26', 'και πολλα παθουσα υπο πολλων ιατρων και δαπανησασα τα παρ εαυτης παντα και μηδεν ωφεληθεισα αλλα μαλλον εις το χειρον ελθουσα');
INSERT INTO `bible_verses_tr` VALUES ('1247', '41', '5', '27', 'ακουσασα περι του ιησου ελθουσα εν τω οχλω οπισθεν ηψατο του ιματιου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1248', '41', '5', '28', 'ελεγεν γαρ οτι καν των ιματιων αυτου αψωμαι σωθησομαι');
INSERT INTO `bible_verses_tr` VALUES ('1249', '41', '5', '29', 'και ευθεως εξηρανθη η πηγη του αιματος αυτης και εγνω τω σωματι οτι ιαται απο της μαστιγος');
INSERT INTO `bible_verses_tr` VALUES ('1250', '41', '5', '30', 'και ευθεως ο ιησους επιγνους εν εαυτω την εξ αυτου δυναμιν εξελθουσαν επιστραφεις εν τω οχλω ελεγεν τις μου ηψατο των ιματιων');
INSERT INTO `bible_verses_tr` VALUES ('1251', '41', '5', '31', 'και ελεγον αυτω οι μαθηται αυτου βλεπεις τον οχλον συνθλιβοντα σε και λεγεις τις μου ηψατο');
INSERT INTO `bible_verses_tr` VALUES ('1252', '41', '5', '32', 'και περιεβλεπετο ιδειν την τουτο ποιησασαν');
INSERT INTO `bible_verses_tr` VALUES ('1253', '41', '5', '33', 'η δε γυνη φοβηθεισα και τρεμουσα ειδυια ο γεγονεν επ αυτη ηλθεν και προσεπεσεν αυτω και ειπεν αυτω πασαν την αληθειαν');
INSERT INTO `bible_verses_tr` VALUES ('1254', '41', '5', '34', 'ο δε ειπεν αυτη θυγατερ η πιστις σου σεσωκεν σε υπαγε εις ειρηνην και ισθι υγιης απο της μαστιγος σου');
INSERT INTO `bible_verses_tr` VALUES ('1255', '41', '5', '35', 'ετι αυτου λαλουντος ερχονται απο του αρχισυναγωγου λεγοντες οτι η θυγατηρ σου απεθανεν τι ετι σκυλλεις τον διδασκαλον');
INSERT INTO `bible_verses_tr` VALUES ('1256', '41', '5', '36', 'ο δε ιησους ευθεως ακουσας τον λογον λαλουμενον λεγει τω αρχισυναγωγω μη φοβου μονον πιστευε');
INSERT INTO `bible_verses_tr` VALUES ('1257', '41', '5', '37', 'και ουκ αφηκεν ουδενα αυτω συνακολουθησαι ει μη πετρον και ιακωβον και ιωαννην τον αδελφον ιακωβου');
INSERT INTO `bible_verses_tr` VALUES ('1258', '41', '5', '38', 'και ερχεται εις τον οικον του αρχισυναγωγου και θεωρει θορυβον  {VAR2: και } κλαιοντας και αλαλαζοντας πολλα');
INSERT INTO `bible_verses_tr` VALUES ('1259', '41', '5', '39', 'και εισελθων λεγει αυτοις τι θορυβεισθε και κλαιετε το παιδιον ουκ απεθανεν αλλα καθευδει');
INSERT INTO `bible_verses_tr` VALUES ('1260', '41', '5', '40', 'και κατεγελων αυτου ο δε εκβαλων απαντας παραλαμβανει τον πατερα του παιδιου και την μητερα και τους μετ αυτου και εισπορευεται οπου ην το παιδιον ανακειμενον');
INSERT INTO `bible_verses_tr` VALUES ('1261', '41', '5', '41', 'και κρατησας της χειρος του παιδιου λεγει αυτη ταλιθα κουμι ο εστιν μεθερμηνευομενον το κορασιον σοι λεγω εγειραι');
INSERT INTO `bible_verses_tr` VALUES ('1262', '41', '5', '42', 'και ευθεως ανεστη το κορασιον και περιεπατει ην γαρ ετων δωδεκα και εξεστησαν εκστασει μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('1263', '41', '5', '43', 'και διεστειλατο αυτοις πολλα ινα μηδεις γνω τουτο και ειπεν δοθηναι αυτη φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('1264', '41', '6', '1', 'και εξηλθεν εκειθεν και ηλθεν εις την πατριδα αυτου και ακολουθουσιν αυτω οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1265', '41', '6', '2', 'και γενομενου σαββατου ηρξατο εν τη συναγωγη διδασκειν και πολλοι ακουοντες εξεπλησσοντο λεγοντες ποθεν τουτω ταυτα και τις η σοφια η δοθεισα αυτω οτι και δυναμεις τοιαυται δια των χειρων αυτου γινονται');
INSERT INTO `bible_verses_tr` VALUES ('1266', '41', '6', '3', 'ουχ ουτος εστιν ο τεκτων ο υιος μαριας αδελφος δε ιακωβου και ιωση και ιουδα και σιμωνος και ουκ εισιν αι αδελφαι αυτου ωδε προς ημας και εσκανδαλιζοντο εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1267', '41', '6', '4', 'ελεγεν δε αυτοις ο ιησους οτι ουκ εστιν προφητης ατιμος ει μη εν τη πατριδι αυτου και εν τοις συγγενεσιν και εν τη οικια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1268', '41', '6', '5', 'και ουκ ηδυνατο εκει ουδεμιαν δυναμιν ποιησαι ει μη ολιγοις αρρωστοις επιθεις τας χειρας εθεραπευσεν');
INSERT INTO `bible_verses_tr` VALUES ('1269', '41', '6', '6', 'και εθαυμαζεν δια την απιστιαν αυτων και περιηγεν τας κωμας κυκλω διδασκων');
INSERT INTO `bible_verses_tr` VALUES ('1270', '41', '6', '7', 'και προσκαλειται τους δωδεκα και ηρξατο αυτους αποστελλειν δυο δυο και εδιδου αυτοις εξουσιαν των πνευματων των ακαθαρτων');
INSERT INTO `bible_verses_tr` VALUES ('1271', '41', '6', '8', 'και παρηγγειλεν αυτοις ινα μηδεν αιρωσιν εις οδον ει μη ραβδον μονον μη πηραν μη αρτον μη εις την ζωνην χαλκον');
INSERT INTO `bible_verses_tr` VALUES ('1272', '41', '6', '9', 'αλλ υποδεδεμενους σανδαλια και μη  {VAR1: ενδυσησθε } {VAR2: ενδυσασθαι } δυο χιτωνας');
INSERT INTO `bible_verses_tr` VALUES ('1273', '41', '6', '10', 'και ελεγεν αυτοις οπου εαν εισελθητε εις οικιαν εκει μενετε εως αν εξελθητε εκειθεν');
INSERT INTO `bible_verses_tr` VALUES ('1274', '41', '6', '11', 'και οσοι αν μη δεξωνται υμας μηδε ακουσωσιν υμων εκπορευομενοι εκειθεν εκτιναξατε τον χουν τον υποκατω των ποδων υμων εις μαρτυριον αυτοις αμην λεγω υμιν ανεκτοτερον εσται σοδομοις η γομορροις εν ημερα κρισεως η τη πολει εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('1275', '41', '6', '12', 'και εξελθοντες εκηρυσσον ινα μετανοησωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1276', '41', '6', '13', 'και δαιμονια πολλα εξεβαλλον και ηλειφον ελαιω πολλους αρρωστους και εθεραπευον');
INSERT INTO `bible_verses_tr` VALUES ('1277', '41', '6', '14', 'και ηκουσεν ο βασιλευς ηρωδης φανερον γαρ εγενετο το ονομα αυτου και ελεγεν οτι ιωαννης ο βαπτιζων εκ νεκρων ηγερθη και δια τουτο ενεργουσιν αι δυναμεις εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1278', '41', '6', '15', 'αλλοι ελεγον οτι ηλιας εστιν αλλοι δε ελεγον οτι προφητης εστιν η ως εις των προφητων');
INSERT INTO `bible_verses_tr` VALUES ('1279', '41', '6', '16', 'ακουσας δε ο ηρωδης ειπεν οτι ον εγω απεκεφαλισα ιωαννην ουτος εστιν αυτος ηγερθη εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('1280', '41', '6', '17', 'αυτος γαρ ο ηρωδης αποστειλας εκρατησεν τον ιωαννην και εδησεν αυτον εν τη φυλακη δια ηρωδιαδα την γυναικα φιλιππου του αδελφου αυτου οτι αυτην εγαμησεν');
INSERT INTO `bible_verses_tr` VALUES ('1281', '41', '6', '18', 'ελεγεν γαρ ο ιωαννης τω ηρωδη οτι ουκ εξεστιν σοι εχειν την γυναικα του αδελφου σου');
INSERT INTO `bible_verses_tr` VALUES ('1282', '41', '6', '19', 'η δε ηρωδιας ενειχεν αυτω και ηθελεν αυτον αποκτειναι και ουκ ηδυνατο');
INSERT INTO `bible_verses_tr` VALUES ('1283', '41', '6', '20', 'ο γαρ ηρωδης εφοβειτο τον ιωαννην ειδως αυτον ανδρα δικαιον και αγιον και συνετηρει αυτον και ακουσας αυτου πολλα εποιει και ηδεως αυτου ηκουεν');
INSERT INTO `bible_verses_tr` VALUES ('1284', '41', '6', '21', 'και γενομενης ημερας ευκαιρου οτε ηρωδης τοις γενεσιοις αυτου δειπνον εποιει τοις μεγιστασιν αυτου και τοις χιλιαρχοις και τοις πρωτοις της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('1285', '41', '6', '22', 'και εισελθουσης της θυγατρος αυτης της ηρωδιαδος και ορχησαμενης και αρεσασης τω ηρωδη και τοις συνανακειμενοις ειπεν ο βασιλευς τω κορασιω αιτησον με ο εαν θελης και δωσω σοι');
INSERT INTO `bible_verses_tr` VALUES ('1286', '41', '6', '23', 'και ωμοσεν αυτη οτι ο εαν με αιτησης δωσω σοι εως ημισους της βασιλειας μου');
INSERT INTO `bible_verses_tr` VALUES ('1287', '41', '6', '24', 'η δε εξελθουσα ειπεν τη μητρι αυτης τι αιτησομαι η δε ειπεν την κεφαλην ιωαννου του βαπτιστου');
INSERT INTO `bible_verses_tr` VALUES ('1288', '41', '6', '25', 'και εισελθουσα ευθεως μετα σπουδης προς τον βασιλεα ητησατο λεγουσα θελω ινα μοι δως εξαυτης επι πινακι την κεφαλην ιωαννου του βαπτιστου');
INSERT INTO `bible_verses_tr` VALUES ('1289', '41', '6', '26', 'και περιλυπος γενομενος ο βασιλευς δια τους ορκους και τους συνανακειμενους ουκ ηθελησεν αυτην αθετησαι');
INSERT INTO `bible_verses_tr` VALUES ('1290', '41', '6', '27', 'και ευθεως αποστειλας ο βασιλευς σπεκουλατωρα επεταξεν ενεχθηναι την κεφαλην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1291', '41', '6', '28', 'ο δε απελθων απεκεφαλισεν αυτον εν τη φυλακη και ηνεγκεν την κεφαλην αυτου επι πινακι και εδωκεν αυτην τω κορασιω και το κορασιον εδωκεν αυτην τη μητρι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1292', '41', '6', '29', 'και ακουσαντες οι μαθηται αυτου ηλθον και ηραν το πτωμα αυτου και εθηκαν αυτο εν  {VAR1: τω } μνημειω');
INSERT INTO `bible_verses_tr` VALUES ('1293', '41', '6', '30', 'και συναγονται οι αποστολοι προς τον ιησουν και απηγγειλαν αυτω παντα και οσα εποιησαν και οσα εδιδαξαν');
INSERT INTO `bible_verses_tr` VALUES ('1294', '41', '6', '31', 'και ειπεν αυτοις δευτε υμεις αυτοι κατ ιδιαν εις ερημον τοπον και αναπαυεσθε ολιγον ησαν γαρ οι ερχομενοι και οι υπαγοντες πολλοι και ουδε φαγειν ηυκαιρουν');
INSERT INTO `bible_verses_tr` VALUES ('1295', '41', '6', '32', 'και απηλθον εις ερημον τοπον τω πλοιω κατ ιδιαν');
INSERT INTO `bible_verses_tr` VALUES ('1296', '41', '6', '33', 'και ειδον αυτους υπαγοντας οι οχλοι και επεγνωσαν αυτον πολλοι και πεζη απο πασων των πολεων συνεδραμον εκει και προηλθον αυτους και συνηλθον προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1297', '41', '6', '34', 'και εξελθων ειδεν ο ιησους πολυν οχλον και εσπλαγχνισθη επ αυτοις οτι ησαν ως προβατα μη εχοντα ποιμενα και ηρξατο διδασκειν αυτους πολλα');
INSERT INTO `bible_verses_tr` VALUES ('1298', '41', '6', '35', 'και ηδη ωρας πολλης γενομενης προσελθοντες αυτω οι μαθηται αυτου λεγουσιν οτι ερημος εστιν ο τοπος και ηδη ωρα πολλη');
INSERT INTO `bible_verses_tr` VALUES ('1299', '41', '6', '36', 'απολυσον αυτους ινα απελθοντες εις τους κυκλω αγρους και κωμας αγορασωσιν εαυτοις αρτους τι γαρ φαγωσιν ουκ εχουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1300', '41', '6', '37', 'ο δε αποκριθεις ειπεν αυτοις δοτε αυτοις υμεις φαγειν και λεγουσιν αυτω απελθοντες αγορασωμεν διακοσιων δηναριων αρτους και δωμεν αυτοις φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('1301', '41', '6', '38', 'ο δε λεγει αυτοις ποσους αρτους εχετε υπαγετε και ιδετε και γνοντες λεγουσιν πεντε και δυο ιχθυας');
INSERT INTO `bible_verses_tr` VALUES ('1302', '41', '6', '39', 'και επεταξεν αυτοις ανακλιναι παντας συμποσια συμποσια επι τω χλωρω χορτω');
INSERT INTO `bible_verses_tr` VALUES ('1303', '41', '6', '40', 'και ανεπεσον πρασιαι πρασιαι ανα εκατον και ανα πεντηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('1304', '41', '6', '41', 'και λαβων τους πεντε αρτους και τους δυο ιχθυας αναβλεψας εις τον ουρανον ευλογησεν και κατεκλασεν τους αρτους και εδιδου τοις μαθηταις αυτου ινα παραθωσιν αυτοις και τους δυο ιχθυας εμερισεν πασιν');
INSERT INTO `bible_verses_tr` VALUES ('1305', '41', '6', '42', 'και εφαγον παντες και εχορτασθησαν');
INSERT INTO `bible_verses_tr` VALUES ('1306', '41', '6', '43', 'και ηραν κλασματων δωδεκα κοφινους πληρεις και απο των ιχθυων');
INSERT INTO `bible_verses_tr` VALUES ('1307', '41', '6', '44', 'και ησαν οι φαγοντες τους αρτους ωσει πεντακισχιλιοι ανδρες');
INSERT INTO `bible_verses_tr` VALUES ('1308', '41', '6', '45', 'και ευθεως ηναγκασεν τους μαθητας αυτου εμβηναι εις το πλοιον και προαγειν εις το περαν προς  {VAR1: βηθσαιδαν } {VAR2: βηθσαιδα } εως αυτος απολυση τον οχλον');
INSERT INTO `bible_verses_tr` VALUES ('1309', '41', '6', '46', 'και αποταξαμενος αυτοις απηλθεν εις το ορος προσευξασθαι');
INSERT INTO `bible_verses_tr` VALUES ('1310', '41', '6', '47', 'και οψιας γενομενης ην το πλοιον εν μεσω της θαλασσης και αυτος μονος επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('1311', '41', '6', '48', 'και ειδεν αυτους βασανιζομενους εν τω ελαυνειν ην γαρ ο ανεμος εναντιος αυτοις και περι τεταρτην φυλακην της νυκτος ερχεται προς αυτους περιπατων επι της θαλασσης και ηθελεν παρελθειν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1312', '41', '6', '49', 'οι δε ιδοντες αυτον περιπατουντα επι της θαλασσης εδοξαν φαντασμα ειναι και ανεκραξαν');
INSERT INTO `bible_verses_tr` VALUES ('1313', '41', '6', '50', 'παντες γαρ αυτον ειδον και εταραχθησαν και ευθεως ελαλησεν μετ αυτων και λεγει αυτοις θαρσειτε εγω ειμι μη φοβεισθε');
INSERT INTO `bible_verses_tr` VALUES ('1314', '41', '6', '51', 'και ανεβη προς αυτους εις το πλοιον και εκοπασεν ο ανεμος και λιαν εκ περισσου εν εαυτοις εξισταντο και εθαυμαζον');
INSERT INTO `bible_verses_tr` VALUES ('1315', '41', '6', '52', 'ου γαρ συνηκαν επι τοις αρτοις ην γαρ η καρδια αυτων πεπωρωμενη');
INSERT INTO `bible_verses_tr` VALUES ('1316', '41', '6', '53', 'και διαπερασαντες ηλθον επι την γην  {VAR1: γενησαρετ } {VAR2: γεννησαρετ } και προσωρμισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('1317', '41', '6', '54', 'και εξελθοντων αυτων εκ του πλοιου ευθεως επιγνοντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1318', '41', '6', '55', 'περιδραμοντες ολην την περιχωρον εκεινην ηρξαντο επι τοις κραββατοις τους κακως εχοντας περιφερειν οπου ηκουον οτι εκει εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1319', '41', '6', '56', 'και οπου αν εισεπορευετο εις κωμας η πολεις η αγρους εν ταις αγοραις ετιθουν τους ασθενουντας και παρεκαλουν αυτον ινα καν του κρασπεδου του ιματιου αυτου αψωνται και οσοι αν ηπτοντο αυτου εσωζοντο');
INSERT INTO `bible_verses_tr` VALUES ('1320', '41', '7', '1', 'και συναγονται προς αυτον οι φαρισαιοι και τινες των γραμματεων ελθοντες απο ιεροσολυμων');
INSERT INTO `bible_verses_tr` VALUES ('1321', '41', '7', '2', 'και ιδοντες τινας των μαθητων αυτου κοιναις χερσιν τουτ εστιν ανιπτοις εσθιοντας αρτους εμεμψαντο');
INSERT INTO `bible_verses_tr` VALUES ('1322', '41', '7', '3', 'οι γαρ φαρισαιοι και παντες οι ιουδαιοι εαν μη πυγμη νιψωνται τας χειρας ουκ εσθιουσιν κρατουντες την παραδοσιν των πρεσβυτερων');
INSERT INTO `bible_verses_tr` VALUES ('1323', '41', '7', '4', 'και απο αγορας εαν μη βαπτισωνται ουκ εσθιουσιν και αλλα πολλα εστιν α παρελαβον κρατειν βαπτισμους ποτηριων και ξεστων και χαλκιων και κλινων');
INSERT INTO `bible_verses_tr` VALUES ('1324', '41', '7', '5', 'επειτα επερωτωσιν αυτον οι φαρισαιοι και οι γραμματεις δια τι οι μαθηται σου ου περιπατουσιν κατα την παραδοσιν των πρεσβυτερων αλλα ανιπτοις χερσιν εσθιουσιν τον αρτον');
INSERT INTO `bible_verses_tr` VALUES ('1325', '41', '7', '6', 'ο δε αποκριθεις ειπεν αυτοις οτι καλως προεφητευσεν ησαιας περι υμων των υποκριτων ως γεγραπται ουτος ο λαος τοις χειλεσιν με τιμα η δε καρδια αυτων πορρω απεχει απ εμου');
INSERT INTO `bible_verses_tr` VALUES ('1326', '41', '7', '7', 'ματην δε σεβονται με διδασκοντες διδασκαλιας ενταλματα ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('1327', '41', '7', '8', 'αφεντες γαρ την εντολην του θεου κρατειτε την παραδοσιν των ανθρωπων βαπτισμους ξεστων και ποτηριων και αλλα παρομοια τοιαυτα πολλα ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('1328', '41', '7', '9', 'και ελεγεν αυτοις καλως αθετειτε την εντολην του θεου ινα την παραδοσιν υμων τηρησητε');
INSERT INTO `bible_verses_tr` VALUES ('1329', '41', '7', '10', 'μωσης γαρ ειπεν τιμα τον πατερα σου και την μητερα σου και ο κακολογων πατερα η μητερα θανατω τελευτατω');
INSERT INTO `bible_verses_tr` VALUES ('1330', '41', '7', '11', 'υμεις δε λεγετε εαν ειπη ανθρωπος τω πατρι η τη μητρι κορβαν ο εστιν δωρον ο εαν εξ εμου ωφεληθης');
INSERT INTO `bible_verses_tr` VALUES ('1331', '41', '7', '12', 'και ουκετι αφιετε αυτον ουδεν ποιησαι τω πατρι αυτου η τη μητρι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1332', '41', '7', '13', 'ακυρουντες τον λογον του θεου τη παραδοσει υμων η παρεδωκατε και παρομοια τοιαυτα πολλα ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('1333', '41', '7', '14', 'και προσκαλεσαμενος παντα τον οχλον ελεγεν αυτοις ακουετε μου παντες και συνιετε');
INSERT INTO `bible_verses_tr` VALUES ('1334', '41', '7', '15', 'ουδεν εστιν εξωθεν του ανθρωπου εισπορευομενον εις αυτον ο δυναται αυτον κοινωσαι αλλα τα εκπορευομενα απ αυτου εκεινα εστιν τα κοινουντα τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('1335', '41', '7', '16', 'ει τις εχει ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('1336', '41', '7', '17', 'και οτε εισηλθεν εις οικον απο του οχλου επηρωτων αυτον οι μαθηται αυτου περι της παραβολης');
INSERT INTO `bible_verses_tr` VALUES ('1337', '41', '7', '18', 'και λεγει αυτοις ουτως και υμεις ασυνετοι εστε ου νοειτε οτι παν το εξωθεν εισπορευομενον εις τον ανθρωπον ου δυναται αυτον κοινωσαι');
INSERT INTO `bible_verses_tr` VALUES ('1338', '41', '7', '19', 'οτι ουκ εισπορευεται αυτου εις την καρδιαν αλλ εις την κοιλιαν και εις τον αφεδρωνα εκπορευεται καθαριζον παντα τα βρωματα');
INSERT INTO `bible_verses_tr` VALUES ('1339', '41', '7', '20', 'ελεγεν δε οτι το εκ του ανθρωπου εκπορευομενον εκεινο κοινοι τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('1340', '41', '7', '21', 'εσωθεν γαρ εκ της καρδιας των ανθρωπων οι διαλογισμοι οι κακοι εκπορευονται μοιχειαι πορνειαι φονοι');
INSERT INTO `bible_verses_tr` VALUES ('1341', '41', '7', '22', 'κλοπαι πλεονεξιαι πονηριαι δολος ασελγεια οφθαλμος πονηρος βλασφημια υπερηφανια αφροσυνη');
INSERT INTO `bible_verses_tr` VALUES ('1342', '41', '7', '23', 'παντα ταυτα τα πονηρα εσωθεν εκπορευεται και κοινοι τον ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('1343', '41', '7', '24', 'και εκειθεν αναστας απηλθεν εις τα μεθορια τυρου και σιδωνος και εισελθων εις την οικιαν ουδενα ηθελεν γνωναι και ουκ ηδυνηθη λαθειν');
INSERT INTO `bible_verses_tr` VALUES ('1344', '41', '7', '25', 'ακουσασα γαρ γυνη περι αυτου ης ειχεν το θυγατριον αυτης πνευμα ακαθαρτον ελθουσα προσεπεσεν προς τους ποδας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1345', '41', '7', '26', 'ην δε η γυνη ελληνις συροφοινισσα τω γενει και ηρωτα αυτον ινα το δαιμονιον εκβαλλη εκ της θυγατρος αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1346', '41', '7', '27', 'ο δε ιησους ειπεν αυτη αφες πρωτον χορτασθηναι τα τεκνα ου γαρ καλον εστιν λαβειν τον αρτον των τεκνων και βαλειν τοις κυναριοις');
INSERT INTO `bible_verses_tr` VALUES ('1347', '41', '7', '28', 'η δε απεκριθη και λεγει αυτω ναι κυριε και γαρ τα κυναρια υποκατω της τραπεζης εσθιει απο των ψιχιων των παιδιων');
INSERT INTO `bible_verses_tr` VALUES ('1348', '41', '7', '29', 'και ειπεν αυτη δια τουτον τον λογον υπαγε εξεληλυθεν το δαιμονιον εκ της θυγατρος σου');
INSERT INTO `bible_verses_tr` VALUES ('1349', '41', '7', '30', 'και απελθουσα εις τον οικον αυτης ευρεν το δαιμονιον εξεληλυθος και την θυγατερα βεβλημενην επι της κλινης');
INSERT INTO `bible_verses_tr` VALUES ('1350', '41', '7', '31', 'και παλιν εξελθων εκ των οριων τυρου και σιδωνος ηλθεν προς την θαλασσαν της γαλιλαιας ανα μεσον των οριων δεκαπολεως');
INSERT INTO `bible_verses_tr` VALUES ('1351', '41', '7', '32', 'και φερουσιν αυτω κωφον μογιλαλον και παρακαλουσιν αυτον ινα επιθη αυτω την χειρα');
INSERT INTO `bible_verses_tr` VALUES ('1352', '41', '7', '33', 'και απολαβομενος αυτον απο του οχλου κατ ιδιαν εβαλεν τους δακτυλους αυτου εις τα ωτα αυτου και πτυσας ηψατο της γλωσσης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1353', '41', '7', '34', 'και αναβλεψας εις τον ουρανον εστεναξεν και λεγει αυτω εφφαθα ο εστιν διανοιχθητι');
INSERT INTO `bible_verses_tr` VALUES ('1354', '41', '7', '35', 'και ευθεως διηνοιχθησαν αυτου αι ακοαι και ελυθη ο δεσμος της γλωσσης αυτου και ελαλει ορθως');
INSERT INTO `bible_verses_tr` VALUES ('1355', '41', '7', '36', 'και διεστειλατο αυτοις ινα μηδενι ειπωσιν οσον δε αυτος αυτοις διεστελλετο μαλλον περισσοτερον εκηρυσσον');
INSERT INTO `bible_verses_tr` VALUES ('1356', '41', '7', '37', 'και υπερπερισσως εξεπλησσοντο λεγοντες καλως παντα πεποιηκεν και τους κωφους ποιει ακουειν και τους αλαλους λαλειν');
INSERT INTO `bible_verses_tr` VALUES ('1357', '41', '8', '1', 'εν εκειναις ταις ημεραις παμπολλου οχλου οντος και μη εχοντων τι φαγωσιν προσκαλεσαμενος ο ιησους τους μαθητας αυτου λεγει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1358', '41', '8', '2', 'σπλαγχνιζομαι επι τον οχλον οτι ηδη ημερας τρεις προσμενουσιν μοι και ουκ εχουσιν τι φαγωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1359', '41', '8', '3', 'και εαν απολυσω αυτους νηστεις εις οικον αυτων εκλυθησονται εν τη οδω τινες γαρ αυτων μακροθεν ηκασιν');
INSERT INTO `bible_verses_tr` VALUES ('1360', '41', '8', '4', 'και απεκριθησαν αυτω οι μαθηται αυτου ποθεν τουτους δυνησεται τις ωδε χορτασαι αρτων επ ερημιας');
INSERT INTO `bible_verses_tr` VALUES ('1361', '41', '8', '5', 'και επηρωτα αυτους ποσους εχετε αρτους οι δε ειπον επτα');
INSERT INTO `bible_verses_tr` VALUES ('1362', '41', '8', '6', 'και παρηγγειλεν τω οχλω αναπεσειν επι της γης και λαβων τους επτα αρτους ευχαριστησας εκλασεν και εδιδου τοις μαθηταις αυτου ινα παραθωσιν και παρεθηκαν τω οχλω');
INSERT INTO `bible_verses_tr` VALUES ('1363', '41', '8', '7', 'και ειχον ιχθυδια ολιγα και ευλογησας ειπεν παραθειναι και αυτα');
INSERT INTO `bible_verses_tr` VALUES ('1364', '41', '8', '8', 'εφαγον δε και εχορτασθησαν και ηραν περισσευματα κλασματων επτα σπυριδας');
INSERT INTO `bible_verses_tr` VALUES ('1365', '41', '8', '9', 'ησαν δε οι φαγοντες ως τετρακισχιλιοι και απελυσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1366', '41', '8', '10', 'και ευθεως εμβας εις το πλοιον μετα των μαθητων αυτου ηλθεν εις τα μερη δαλμανουθα');
INSERT INTO `bible_verses_tr` VALUES ('1367', '41', '8', '11', 'και εξηλθον οι φαρισαιοι και ηρξαντο συζητειν αυτω ζητουντες παρ αυτου σημειον απο του ουρανου πειραζοντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1368', '41', '8', '12', 'και αναστεναξας τω πνευματι αυτου λεγει τι η γενεα αυτη σημειον επιζητει αμην λεγω υμιν ει δοθησεται τη γενεα ταυτη σημειον');
INSERT INTO `bible_verses_tr` VALUES ('1369', '41', '8', '13', 'και αφεις αυτους εμβας παλιν εις το πλοιον απηλθεν εις το περαν');
INSERT INTO `bible_verses_tr` VALUES ('1370', '41', '8', '14', 'και επελαθοντο  {VAR2: οι μαθηται } λαβειν αρτους και ει μη ενα αρτον ουκ ειχον μεθ εαυτων εν τω πλοιω');
INSERT INTO `bible_verses_tr` VALUES ('1371', '41', '8', '15', 'και διεστελλετο αυτοις λεγων ορατε βλεπετε απο της ζυμης των φαρισαιων και της ζυμης ηρωδου');
INSERT INTO `bible_verses_tr` VALUES ('1372', '41', '8', '16', 'και διελογιζοντο προς αλληλους λεγοντες οτι αρτους ουκ εχομεν');
INSERT INTO `bible_verses_tr` VALUES ('1373', '41', '8', '17', 'και γνους ο ιησους λεγει αυτοις τι διαλογιζεσθε οτι αρτους ουκ εχετε ουπω νοειτε ουδε συνιετε ετι πεπωρωμενην εχετε την καρδιαν υμων');
INSERT INTO `bible_verses_tr` VALUES ('1374', '41', '8', '18', 'οφθαλμους εχοντες ου βλεπετε και ωτα εχοντες ουκ ακουετε και ου μνημονευετε');
INSERT INTO `bible_verses_tr` VALUES ('1375', '41', '8', '19', 'οτε τους πεντε αρτους εκλασα εις τους πεντακισχιλιους ποσους κοφινους πληρεις κλασματων ηρατε λεγουσιν αυτω δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('1376', '41', '8', '20', 'οτε δε τους επτα εις τους τετρακισχιλιους ποσων σπυριδων πληρωματα κλασματων ηρατε οι δε ειπον επτα');
INSERT INTO `bible_verses_tr` VALUES ('1377', '41', '8', '21', 'και ελεγεν αυτοις πως ου συνιετε');
INSERT INTO `bible_verses_tr` VALUES ('1378', '41', '8', '22', 'και ερχεται εις  {VAR1: βηθσαιδαν } {VAR2: βηθσαιδα } και φερουσιν αυτω τυφλον και παρακαλουσιν αυτον ινα αυτου αψηται');
INSERT INTO `bible_verses_tr` VALUES ('1379', '41', '8', '23', 'και επιλαβομενος της χειρος του τυφλου εξηγαγεν αυτον εξω της κωμης και πτυσας εις τα ομματα αυτου επιθεις τας χειρας αυτω επηρωτα αυτον ει τι βλεπει');
INSERT INTO `bible_verses_tr` VALUES ('1380', '41', '8', '24', 'και αναβλεψας ελεγεν βλεπω τους ανθρωπους  {VAR1: οτι } ως δενδρα  {VAR1: ορω } περιπατουντας');
INSERT INTO `bible_verses_tr` VALUES ('1381', '41', '8', '25', 'ειτα παλιν επεθηκεν τας χειρας επι τους οφθαλμους αυτου και εποιησεν αυτον αναβλεψαι και αποκατεσταθη και ενεβλεψεν τηλαυγως απαντας');
INSERT INTO `bible_verses_tr` VALUES ('1382', '41', '8', '26', 'και απεστειλεν αυτον εις τον οικον αυτου λεγων μηδε εις την κωμην εισελθης μηδε ειπης τινι εν τη κωμη');
INSERT INTO `bible_verses_tr` VALUES ('1383', '41', '8', '27', 'και εξηλθεν ο ιησους και οι μαθηται αυτου εις τας κωμας καισαρειας της φιλιππου και εν τη οδω επηρωτα τους μαθητας αυτου λεγων αυτοις τινα με λεγουσιν οι ανθρωποι ειναι');
INSERT INTO `bible_verses_tr` VALUES ('1384', '41', '8', '28', 'οι δε απεκριθησαν ιωαννην τον βαπτιστην και αλλοι ηλιαν αλλοι δε ενα των προφητων');
INSERT INTO `bible_verses_tr` VALUES ('1385', '41', '8', '29', 'και αυτος λεγει αυτοις υμεις δε τινα με λεγετε ειναι αποκριθεις δε ο πετρος λεγει αυτω συ ει ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('1386', '41', '8', '30', 'και επετιμησεν αυτοις ινα μηδενι λεγωσιν περι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1387', '41', '8', '31', 'και ηρξατο διδασκειν αυτους οτι δει τον υιον του ανθρωπου πολλα παθειν και αποδοκιμασθηναι απο των πρεσβυτερων και αρχιερεων και γραμματεων και αποκτανθηναι και μετα τρεις ημερας αναστηναι');
INSERT INTO `bible_verses_tr` VALUES ('1388', '41', '8', '32', 'και παρρησια τον λογον ελαλει και προσλαβομενος αυτον ο πετρος ηρξατο επιτιμαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1389', '41', '8', '33', 'ο δε επιστραφεις και ιδων τους μαθητας αυτου επετιμησεν τω πετρω λεγων υπαγε οπισω μου σατανα οτι ου φρονεις τα του θεου αλλα τα των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('1390', '41', '8', '34', 'και προσκαλεσαμενος τον οχλον συν τοις μαθηταις αυτου ειπεν αυτοις οστις θελει οπισω μου ελθειν απαρνησασθω εαυτον και αρατω τον σταυρον αυτου και ακολουθειτω μοι');
INSERT INTO `bible_verses_tr` VALUES ('1391', '41', '8', '35', 'ος γαρ αν θελη την ψυχην αυτου σωσαι απολεσει αυτην ος δ αν απολεση την ψυχην αυτου ενεκεν εμου και του ευαγγελιου ουτος σωσει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('1392', '41', '8', '36', 'τι γαρ ωφελησει ανθρωπον εαν κερδηση τον κοσμον ολον και ζημιωθη την ψυχην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1393', '41', '8', '37', 'η τι δωσει ανθρωπος ανταλλαγμα της ψυχης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1394', '41', '8', '38', 'ος γαρ αν επαισχυνθη με και τους εμους λογους εν τη γενεα ταυτη τη μοιχαλιδι και αμαρτωλω και ο υιος του ανθρωπου επαισχυνθησεται αυτον οταν ελθη εν τη δοξη του πατρος αυτου μετα των αγγελων των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('1395', '41', '9', '1', 'και ελεγεν αυτοις αμην λεγω υμιν οτι εισιν τινες των ωδε εστηκοτων οιτινες ου μη γευσωνται θανατου εως αν ιδωσιν την βασιλειαν του θεου εληλυθυιαν εν δυναμει');
INSERT INTO `bible_verses_tr` VALUES ('1396', '41', '9', '2', 'και μεθ ημερας εξ παραλαμβανει ο ιησους τον πετρον και τον ιακωβον και τον ιωαννην και αναφερει αυτους εις ορος υψηλον κατ ιδιαν μονους και μετεμορφωθη εμπροσθεν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1397', '41', '9', '3', 'και τα ιματια αυτου εγενετο στιλβοντα λευκα λιαν ως χιων οια γναφευς επι της γης ου δυναται λευκαναι');
INSERT INTO `bible_verses_tr` VALUES ('1398', '41', '9', '4', 'και ωφθη αυτοις ηλιας συν μωσει και ησαν συλλαλουντες τω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('1399', '41', '9', '5', 'και αποκριθεις ο πετρος λεγει τω ιησου ραββι καλον εστιν ημας ωδε ειναι και ποιησωμεν σκηνας τρεις σοι μιαν και μωσει μιαν και ηλια μιαν');
INSERT INTO `bible_verses_tr` VALUES ('1400', '41', '9', '6', 'ου γαρ ηδει τι λαληση ησαν γαρ εκφοβοι');
INSERT INTO `bible_verses_tr` VALUES ('1401', '41', '9', '7', 'και εγενετο νεφελη επισκιαζουσα αυτοις και ηλθεν φωνη εκ της νεφελης λεγουσα ουτος εστιν ο υιος μου ο αγαπητος αυτου ακουετε');
INSERT INTO `bible_verses_tr` VALUES ('1402', '41', '9', '8', 'και εξαπινα περιβλεψαμενοι ουκετι ουδενα ειδον αλλα τον ιησουν μονον μεθ εαυτων');
INSERT INTO `bible_verses_tr` VALUES ('1403', '41', '9', '9', 'καταβαινοντων δε αυτων απο του ορους διεστειλατο αυτοις ινα μηδενι διηγησωνται α ειδον ει μη οταν ο υιος του ανθρωπου εκ νεκρων αναστη');
INSERT INTO `bible_verses_tr` VALUES ('1404', '41', '9', '10', 'και τον λογον εκρατησαν προς εαυτους συζητουντες τι εστιν το εκ νεκρων αναστηναι');
INSERT INTO `bible_verses_tr` VALUES ('1405', '41', '9', '11', 'και επηρωτων αυτον λεγοντες οτι λεγουσιν οι γραμματεις οτι ηλιαν δει ελθειν πρωτον');
INSERT INTO `bible_verses_tr` VALUES ('1406', '41', '9', '12', 'ο δε αποκριθεις ειπεν αυτοις ηλιας μεν ελθων πρωτον αποκαθιστα παντα και πως γεγραπται επι τον υιον του ανθρωπου ινα πολλα παθη και εξουδενωθη');
INSERT INTO `bible_verses_tr` VALUES ('1407', '41', '9', '13', 'αλλα λεγω υμιν οτι και ηλιας εληλυθεν και εποιησαν αυτω οσα ηθελησαν καθως γεγραπται επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1408', '41', '9', '14', 'και ελθων προς τους μαθητας ειδεν οχλον πολυν περι αυτους και γραμματεις συζητουντας αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1409', '41', '9', '15', 'και ευθεως πας ο οχλος ιδων αυτον εξεθαμβηθη και προστρεχοντες ησπαζοντο αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1410', '41', '9', '16', 'και επηρωτησεν τους γραμματεις τι συζητειτε προς αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1411', '41', '9', '17', 'και αποκριθεις εις εκ του οχλου ειπεν διδασκαλε ηνεγκα τον υιον μου προς σε εχοντα πνευμα αλαλον');
INSERT INTO `bible_verses_tr` VALUES ('1412', '41', '9', '18', 'και οπου αν αυτον καταλαβη ρησσει αυτον και αφριζει και τριζει τους οδοντας αυτου και ξηραινεται και ειπον τοις μαθηταις σου ινα αυτο εκβαλωσιν και ουκ ισχυσαν');
INSERT INTO `bible_verses_tr` VALUES ('1413', '41', '9', '19', 'ο δε αποκριθεις αυτω λεγει ω γενεα απιστος εως ποτε προς υμας εσομαι εως ποτε ανεξομαι υμων φερετε αυτον προς με');
INSERT INTO `bible_verses_tr` VALUES ('1414', '41', '9', '20', 'και ηνεγκαν αυτον προς αυτον και ιδων αυτον ευθεως το πνευμα εσπαραξεν αυτον και πεσων επι της γης εκυλιετο αφριζων');
INSERT INTO `bible_verses_tr` VALUES ('1415', '41', '9', '21', 'και επηρωτησεν τον πατερα αυτου ποσος χρονος εστιν ως τουτο γεγονεν αυτω ο δε ειπεν παιδιοθεν');
INSERT INTO `bible_verses_tr` VALUES ('1416', '41', '9', '22', 'και πολλακις αυτον και εις πυρ εβαλεν και εις υδατα ινα απολεση αυτον αλλ ει τι δυνασαι βοηθησον ημιν σπλαγχνισθεις εφ ημας');
INSERT INTO `bible_verses_tr` VALUES ('1417', '41', '9', '23', 'ο δε ιησους ειπεν αυτω το ει δυνασαι πιστευσαι παντα δυνατα τω πιστευοντι');
INSERT INTO `bible_verses_tr` VALUES ('1418', '41', '9', '24', 'και ευθεως κραξας ο πατηρ του παιδιου μετα δακρυων ελεγεν πιστευω κυριε βοηθει μου τη απιστια');
INSERT INTO `bible_verses_tr` VALUES ('1419', '41', '9', '25', 'ιδων δε ο ιησους οτι επισυντρεχει οχλος επετιμησεν τω πνευματι τω ακαθαρτω λεγων αυτω το πνευμα το αλαλον και κωφον εγω σοι επιτασσω εξελθε εξ αυτου και μηκετι εισελθης εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1420', '41', '9', '26', 'και κραξαν και πολλα σπαραξαν αυτον εξηλθεν και εγενετο ωσει νεκρος ωστε πολλους λεγειν οτι απεθανεν');
INSERT INTO `bible_verses_tr` VALUES ('1421', '41', '9', '27', 'ο δε ιησους κρατησας αυτον της χειρος ηγειρεν αυτον και ανεστη');
INSERT INTO `bible_verses_tr` VALUES ('1422', '41', '9', '28', 'και εισελθοντα αυτον εις οικον οι μαθηται αυτου επηρωτων αυτον κατ ιδιαν οτι ημεις ουκ ηδυνηθημεν εκβαλειν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('1423', '41', '9', '29', 'και ειπεν αυτοις τουτο το γενος εν ουδενι δυναται εξελθειν ει μη εν προσευχη και νηστεια');
INSERT INTO `bible_verses_tr` VALUES ('1424', '41', '9', '30', 'και εκειθεν εξελθοντες παρεπορευοντο δια της γαλιλαιας και ουκ ηθελεν ινα τις γνω');
INSERT INTO `bible_verses_tr` VALUES ('1425', '41', '9', '31', 'εδιδασκεν γαρ τους μαθητας αυτου και ελεγεν αυτοις οτι ο υιος του ανθρωπου παραδιδοται εις χειρας ανθρωπων και αποκτενουσιν αυτον και αποκτανθεις τη τριτη ημερα αναστησεται');
INSERT INTO `bible_verses_tr` VALUES ('1426', '41', '9', '32', 'οι δε ηγνοουν το ρημα και εφοβουντο αυτον επερωτησαι');
INSERT INTO `bible_verses_tr` VALUES ('1427', '41', '9', '33', 'και ηλθεν εις καπερναουμ και εν τη οικια γενομενος επηρωτα αυτους τι εν τη οδω προς εαυτους διελογιζεσθε');
INSERT INTO `bible_verses_tr` VALUES ('1428', '41', '9', '34', 'οι δε εσιωπων προς αλληλους γαρ διελεχθησαν εν τη οδω τις μειζων');
INSERT INTO `bible_verses_tr` VALUES ('1429', '41', '9', '35', 'και καθισας εφωνησεν τους δωδεκα και λεγει αυτοις ει τις θελει πρωτος ειναι εσται παντων εσχατος και παντων διακονος');
INSERT INTO `bible_verses_tr` VALUES ('1430', '41', '9', '36', 'και λαβων παιδιον εστησεν αυτο εν μεσω αυτων και εναγκαλισαμενος αυτο ειπεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1431', '41', '9', '37', 'ος εαν εν των τοιουτων παιδιων δεξηται επι τω ονοματι μου εμε δεχεται και ος εαν εμε δεξηται ουκ εμε δεχεται αλλα τον αποστειλαντα με');
INSERT INTO `bible_verses_tr` VALUES ('1432', '41', '9', '38', 'απεκριθη δε αυτω ο ιωαννης λεγων διδασκαλε ειδομεν τινα  {VAR2: εν } τω ονοματι σου εκβαλλοντα δαιμονια ος ουκ ακολουθει ημιν και εκωλυσαμεν αυτον οτι ουκ ακολουθει ημιν');
INSERT INTO `bible_verses_tr` VALUES ('1433', '41', '9', '39', 'ο δε ιησους ειπεν μη κωλυετε αυτον ουδεις γαρ εστιν ος ποιησει δυναμιν επι τω ονοματι μου και δυνησεται ταχυ κακολογησαι με');
INSERT INTO `bible_verses_tr` VALUES ('1434', '41', '9', '40', 'ος γαρ ουκ εστιν καθ  {VAR1: υμων υπερ υμων } {VAR2: ημων υπερ ημων } εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1435', '41', '9', '41', 'ος γαρ αν ποτιση υμας ποτηριον υδατος εν τω ονοματι μου οτι χριστου εστε αμην λεγω υμιν ου μη απολεση τον μισθον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1436', '41', '9', '42', 'και ος αν σκανδαλιση ενα των μικρων  {VAR2: τουτων } των πιστευοντων εις εμε καλον εστιν αυτω μαλλον ει περικειται λιθος μυλικος περι τον τραχηλον αυτου και βεβληται εις την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('1437', '41', '9', '43', 'και εαν σκανδαλιζη σε η χειρ σου αποκοψον αυτην καλον σοι εστιν κυλλον εις την ζωην εισελθειν η τας δυο χειρας εχοντα απελθειν εις την γεενναν εις το πυρ το ασβεστον');
INSERT INTO `bible_verses_tr` VALUES ('1438', '41', '9', '44', 'οπου ο σκωληξ αυτων ου τελευτα και το πυρ ου σβεννυται');
INSERT INTO `bible_verses_tr` VALUES ('1439', '41', '9', '45', 'και εαν ο πους σου σκανδαλιζη σε αποκοψον αυτον καλον εστιν σοι εισελθειν εις την ζωην χωλον η τους δυο ποδας εχοντα βληθηναι εις την γεενναν εις το πυρ το ασβεστον');
INSERT INTO `bible_verses_tr` VALUES ('1440', '41', '9', '46', 'οπου ο σκωληξ αυτων ου τελευτα και το πυρ ου σβεννυται');
INSERT INTO `bible_verses_tr` VALUES ('1441', '41', '9', '47', 'και εαν ο οφθαλμος σου σκανδαλιζη σε εκβαλε αυτον καλον σοι εστιν μονοφθαλμον εισελθειν εις την βασιλειαν του θεου η δυο οφθαλμους εχοντα βληθηναι εις την γεενναν του πυρος');
INSERT INTO `bible_verses_tr` VALUES ('1442', '41', '9', '48', 'οπου ο σκωληξ αυτων ου τελευτα και το πυρ ου σβεννυται');
INSERT INTO `bible_verses_tr` VALUES ('1443', '41', '9', '49', 'πας γαρ πυρι αλισθησεται και πασα θυσια αλι αλισθησεται');
INSERT INTO `bible_verses_tr` VALUES ('1444', '41', '9', '50', 'καλον το αλας εαν δε το αλας αναλον γενηται εν τινι αυτο αρτυσετε εχετε εν εαυτοις αλας και ειρηνευετε εν αλληλοις');
INSERT INTO `bible_verses_tr` VALUES ('1445', '41', '10', '1', 'κακειθεν αναστας ερχεται εις τα ορια της ιουδαιας δια του περαν του ιορδανου και συμπορευονται παλιν οχλοι προς αυτον και ως ειωθει παλιν εδιδασκεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1446', '41', '10', '2', 'και προσελθοντες οι φαρισαιοι επηρωτησαν αυτον ει εξεστιν ανδρι γυναικα απολυσαι πειραζοντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1447', '41', '10', '3', 'ο δε αποκριθεις ειπεν αυτοις τι υμιν ενετειλατο μωσης');
INSERT INTO `bible_verses_tr` VALUES ('1448', '41', '10', '4', 'οι δε ειπον μωσης επετρεψεν βιβλιον αποστασιου γραψαι και απολυσαι');
INSERT INTO `bible_verses_tr` VALUES ('1449', '41', '10', '5', 'και αποκριθεις ο ιησους ειπεν αυτοις προς την σκληροκαρδιαν υμων εγραψεν υμιν την εντολην ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('1450', '41', '10', '6', 'απο δε αρχης κτισεως αρσεν και θηλυ εποιησεν αυτους ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('1451', '41', '10', '7', 'ενεκεν τουτου καταλειψει ανθρωπος τον πατερα αυτου και την μητερα και προσκολληθησεται προς την γυναικα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1452', '41', '10', '8', 'και εσονται οι δυο εις σαρκα μιαν ωστε ουκετι εισιν δυο αλλα μια σαρξ');
INSERT INTO `bible_verses_tr` VALUES ('1453', '41', '10', '9', 'ο ουν ο θεος συνεζευξεν ανθρωπος μη χωριζετω');
INSERT INTO `bible_verses_tr` VALUES ('1454', '41', '10', '10', 'και εν τη οικια παλιν οι μαθηται αυτου περι του αυτου επηρωτησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1455', '41', '10', '11', 'και λεγει αυτοις ος εαν απολυση την γυναικα αυτου και γαμηση αλλην μοιχαται επ αυτην');
INSERT INTO `bible_verses_tr` VALUES ('1456', '41', '10', '12', 'και εαν γυνη απολυση τον ανδρα αυτης και γαμηθη αλλω μοιχαται');
INSERT INTO `bible_verses_tr` VALUES ('1457', '41', '10', '13', 'και προσεφερον αυτω παιδια ινα αψηται αυτων οι δε μαθηται επετιμων τοις προσφερουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1458', '41', '10', '14', 'ιδων δε ο ιησους ηγανακτησεν και ειπεν αυτοις αφετε τα παιδια ερχεσθαι προς με και μη κωλυετε αυτα των γαρ τοιουτων εστιν η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1459', '41', '10', '15', 'αμην λεγω υμιν ος εαν μη δεξηται την βασιλειαν του θεου ως παιδιον ου μη εισελθη εις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('1460', '41', '10', '16', 'και εναγκαλισαμενος αυτα τιθεις τας χειρας επ αυτα ηυλογει αυτα');
INSERT INTO `bible_verses_tr` VALUES ('1461', '41', '10', '17', 'και εκπορευομενου αυτου εις οδον προσδραμων εις και γονυπετησας αυτον επηρωτα αυτον διδασκαλε αγαθε τι ποιησω ινα ζωην αιωνιον κληρονομησω');
INSERT INTO `bible_verses_tr` VALUES ('1462', '41', '10', '18', 'ο δε ιησους ειπεν αυτω τι με λεγεις αγαθον ουδεις αγαθος ει μη εις ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('1463', '41', '10', '19', 'τας εντολας οιδας μη μοιχευσης μη φονευσης μη κλεψης μη ψευδομαρτυρησης μη αποστερησης τιμα τον πατερα σου και την μητερα');
INSERT INTO `bible_verses_tr` VALUES ('1464', '41', '10', '20', 'ο δε αποκριθεις ειπεν αυτω διδασκαλε ταυτα παντα εφυλαξαμην εκ νεοτητος μου');
INSERT INTO `bible_verses_tr` VALUES ('1465', '41', '10', '21', 'ο δε ιησους εμβλεψας αυτω ηγαπησεν αυτον και ειπεν αυτω εν σοι υστερει υπαγε οσα εχεις πωλησον και δος τοις πτωχοις και εξεις θησαυρον εν ουρανω και δευρο ακολουθει μοι αρας τον σταυρον');
INSERT INTO `bible_verses_tr` VALUES ('1466', '41', '10', '22', 'ο δε στυγνασας επι τω λογω απηλθεν λυπουμενος ην γαρ εχων κτηματα πολλα');
INSERT INTO `bible_verses_tr` VALUES ('1467', '41', '10', '23', 'και περιβλεψαμενος ο ιησους λεγει τοις μαθηταις αυτου πως δυσκολως οι τα χρηματα εχοντες εις την βασιλειαν του θεου εισελευσονται');
INSERT INTO `bible_verses_tr` VALUES ('1468', '41', '10', '24', 'οι δε μαθηται εθαμβουντο επι τοις λογοις αυτου ο δε ιησους παλιν αποκριθεις λεγει αυτοις τεκνα πως δυσκολον εστιν τους πεποιθοτας επι τοις χρημασιν εις την βασιλειαν του θεου εισελθειν');
INSERT INTO `bible_verses_tr` VALUES ('1469', '41', '10', '25', 'ευκοπωτερον εστιν καμηλον δια της τρυμαλιας της ραφιδος  {VAR1: εισελθειν } {VAR2: διελθειν } η πλουσιον εις την βασιλειαν του θεου εισελθειν');
INSERT INTO `bible_verses_tr` VALUES ('1470', '41', '10', '26', 'οι δε περισσως εξεπλησσοντο λεγοντες προς εαυτους και τις δυναται σωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('1471', '41', '10', '27', 'εμβλεψας δε αυτοις ο ιησους λεγει παρα ανθρωποις αδυνατον αλλ ου παρα τω θεω παντα γαρ δυνατα εστιν παρα τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('1472', '41', '10', '28', 'και ηρξατο ο πετρος λεγειν αυτω ιδου ημεις αφηκαμεν παντα και ηκολουθησαμεν σοι');
INSERT INTO `bible_verses_tr` VALUES ('1473', '41', '10', '29', 'αποκριθεις δε ο ιησους ειπεν αμην λεγω υμιν ουδεις εστιν ος αφηκεν οικιαν η αδελφους η αδελφας η πατερα η μητερα η γυναικα η τεκνα η αγρους ενεκεν εμου και του ευαγγελιου');
INSERT INTO `bible_verses_tr` VALUES ('1474', '41', '10', '30', 'εαν μη λαβη εκατονταπλασιονα νυν εν τω καιρω τουτω οικιας και αδελφους και αδελφας και μητερας και τεκνα και αγρους μετα διωγμων και εν τω αιωνι τω ερχομενω ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('1475', '41', '10', '31', 'πολλοι δε εσονται πρωτοι εσχατοι και οι εσχατοι πρωτοι');
INSERT INTO `bible_verses_tr` VALUES ('1476', '41', '10', '32', 'ησαν δε εν τη οδω αναβαινοντες εις ιεροσολυμα και ην προαγων αυτους ο ιησους και εθαμβουντο και ακολουθουντες εφοβουντο και παραλαβων παλιν τους δωδεκα ηρξατο αυτοις λεγειν τα μελλοντα αυτω συμβαινειν');
INSERT INTO `bible_verses_tr` VALUES ('1477', '41', '10', '33', 'οτι ιδου αναβαινομεν εις ιεροσολυμα και ο υιος του ανθρωπου παραδοθησεται τοις αρχιερευσιν και τοις γραμματευσιν και κατακρινουσιν αυτον θανατω και παραδωσουσιν αυτον τοις εθνεσιν');
INSERT INTO `bible_verses_tr` VALUES ('1478', '41', '10', '34', 'και εμπαιξουσιν αυτω και μαστιγωσουσιν αυτον και εμπτυσουσιν αυτω και αποκτενουσιν αυτον και τη τριτη ημερα αναστησεται');
INSERT INTO `bible_verses_tr` VALUES ('1479', '41', '10', '35', 'και προσπορευονται αυτω ιακωβος και ιωαννης οι υιοι ζεβεδαιου λεγοντες διδασκαλε θελομεν ινα ο εαν αιτησωμεν ποιησης ημιν');
INSERT INTO `bible_verses_tr` VALUES ('1480', '41', '10', '36', 'ο δε ειπεν αυτοις τι θελετε ποιησαι με υμιν');
INSERT INTO `bible_verses_tr` VALUES ('1481', '41', '10', '37', 'οι δε ειπον αυτω δος ημιν ινα εις εκ δεξιων σου και εις εξ ευωνυμων σου καθισωμεν εν τη δοξη σου');
INSERT INTO `bible_verses_tr` VALUES ('1482', '41', '10', '38', 'ο δε ιησους ειπεν αυτοις ουκ οιδατε τι αιτεισθε δυνασθε πιειν το ποτηριον ο εγω πινω και το βαπτισμα ο εγω βαπτιζομαι βαπτισθηναι');
INSERT INTO `bible_verses_tr` VALUES ('1483', '41', '10', '39', 'οι δε ειπον αυτω δυναμεθα ο δε ιησους ειπεν αυτοις το μεν ποτηριον ο εγω πινω πιεσθε και το βαπτισμα ο εγω βαπτιζομαι βαπτισθησεσθε');
INSERT INTO `bible_verses_tr` VALUES ('1484', '41', '10', '40', 'το δε καθισαι εκ δεξιων μου και εξ ευωνυμων μου ουκ εστιν εμον δουναι αλλ οις ητοιμασται');
INSERT INTO `bible_verses_tr` VALUES ('1485', '41', '10', '41', 'και ακουσαντες οι δεκα ηρξαντο αγανακτειν περι ιακωβου και ιωαννου');
INSERT INTO `bible_verses_tr` VALUES ('1486', '41', '10', '42', 'ο δε ιησους προσκαλεσαμενος αυτους λεγει αυτοις οιδατε οτι οι δοκουντες αρχειν των εθνων κατακυριευουσιν αυτων και οι μεγαλοι αυτων κατεξουσιαζουσιν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1487', '41', '10', '43', 'ουχ ουτως δε εσται εν υμιν αλλ ος εαν θελη γενεσθαι μεγας εν υμιν εσται διακονος υμων');
INSERT INTO `bible_verses_tr` VALUES ('1488', '41', '10', '44', 'και ος αν θελη υμων γενεσθαι πρωτος εσται παντων δουλος');
INSERT INTO `bible_verses_tr` VALUES ('1489', '41', '10', '45', 'και γαρ ο υιος του ανθρωπου ουκ ηλθεν διακονηθηναι αλλα διακονησαι και δουναι την ψυχην αυτου λυτρον αντι πολλων');
INSERT INTO `bible_verses_tr` VALUES ('1490', '41', '10', '46', 'και ερχονται εις ιεριχω και εκπορευομενου αυτου απο ιεριχω και των μαθητων αυτου και οχλου ικανου υιος τιμαιου βαρτιμαιος ο τυφλος εκαθητο παρα την οδον προσαιτων');
INSERT INTO `bible_verses_tr` VALUES ('1491', '41', '10', '47', 'και ακουσας οτι ιησους ο ναζωραιος εστιν ηρξατο κραζειν και λεγειν ο υιος δαβιδ ιησου ελεησον με');
INSERT INTO `bible_verses_tr` VALUES ('1492', '41', '10', '48', 'και επετιμων αυτω πολλοι ινα σιωπηση ο δε πολλω μαλλον εκραζεν υιε δαβιδ ελεησον με');
INSERT INTO `bible_verses_tr` VALUES ('1493', '41', '10', '49', 'και στας ο ιησους ειπεν αυτον φωνηθηναι και φωνουσιν τον τυφλον λεγοντες αυτω θαρσει εγειραι φωνει σε');
INSERT INTO `bible_verses_tr` VALUES ('1494', '41', '10', '50', 'ο δε αποβαλων το ιματιον αυτου αναστας ηλθεν προς τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('1495', '41', '10', '51', 'και αποκριθεις λεγει αυτω ο ιησους τι θελεις ποιησω σοι ο δε τυφλος ειπεν αυτω ραββονι ινα αναβλεψω');
INSERT INTO `bible_verses_tr` VALUES ('1496', '41', '10', '52', 'ο δε ιησους ειπεν αυτω υπαγε η πιστις σου σεσωκεν σε και ευθεως ανεβλεψεν και ηκολουθει τω ιησου εν τη οδω');
INSERT INTO `bible_verses_tr` VALUES ('1497', '41', '11', '1', 'και οτε εγγιζουσιν εις ιερουσαλημ εις βηθφαγη και βηθανιαν προς το ορος των ελαιων αποστελλει δυο των μαθητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1498', '41', '11', '2', 'και λεγει αυτοις υπαγετε εις την κωμην την κατεναντι υμων και ευθεως εισπορευομενοι εις αυτην ευρησετε πωλον δεδεμενον εφ ον ουδεις ανθρωπων κεκαθικεν λυσαντες αυτον αγαγετε');
INSERT INTO `bible_verses_tr` VALUES ('1499', '41', '11', '3', 'και εαν τις υμιν ειπη τι ποιειτε τουτο ειπατε οτι ο κυριος αυτου χρειαν εχει και ευθεως αυτον αποστελει ωδε');
INSERT INTO `bible_verses_tr` VALUES ('1500', '41', '11', '4', 'απηλθον δε και ευρον τον πωλον δεδεμενον προς την θυραν εξω επι του αμφοδου και λυουσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1501', '41', '11', '5', 'και τινες των εκει εστηκοτων ελεγον αυτοις τι ποιειτε λυοντες τον πωλον');
INSERT INTO `bible_verses_tr` VALUES ('1502', '41', '11', '6', 'οι δε ειπον αυτοις καθως ενετειλατο ο ιησους και αφηκαν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1503', '41', '11', '7', 'και ηγαγον τον πωλον προς τον ιησουν και επεβαλον αυτω τα ιματια αυτων και εκαθισεν επ αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1504', '41', '11', '8', 'πολλοι δε τα ιματια αυτων εστρωσαν εις την οδον αλλοι δε στοιβαδας εκοπτον εκ των δενδρων και εστρωννυον εις την οδον');
INSERT INTO `bible_verses_tr` VALUES ('1505', '41', '11', '9', 'και οι προαγοντες και οι ακολουθουντες εκραζον λεγοντες ωσαννα ευλογημενος ο ερχομενος εν ονοματι κυριου');
INSERT INTO `bible_verses_tr` VALUES ('1506', '41', '11', '10', 'ευλογημενη η ερχομενη βασιλεια εν ονοματι κυριου του πατρος ημων δαβιδ ωσαννα εν τοις υψιστοις');
INSERT INTO `bible_verses_tr` VALUES ('1507', '41', '11', '11', 'και εισηλθεν εις ιεροσολυμα ο ιησους και εις το ιερον και περιβλεψαμενος παντα οψιας ηδη ουσης της ωρας εξηλθεν εις βηθανιαν μετα των δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('1508', '41', '11', '12', 'και τη επαυριον εξελθοντων αυτων απο βηθανιας επεινασεν');
INSERT INTO `bible_verses_tr` VALUES ('1509', '41', '11', '13', 'και ιδων συκην μακροθεν εχουσαν φυλλα ηλθεν ει αρα ευρησει τι εν αυτη και ελθων επ αυτην ουδεν ευρεν ει μη φυλλα ου γαρ ην καιρος συκων');
INSERT INTO `bible_verses_tr` VALUES ('1510', '41', '11', '14', 'και αποκριθεις ο ιησους ειπεν αυτη μηκετι εκ σου εις τον αιωνα μηδεις καρπον φαγοι και ηκουον οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1511', '41', '11', '15', 'και ερχονται εις ιεροσολυμα και εισελθων ο ιησους εις το ιερον ηρξατο εκβαλλειν τους πωλουντας και αγοραζοντας εν τω ιερω και τας τραπεζας των κολλυβιστων και τας καθεδρας των πωλουντων τας περιστερας κατεστρεψεν');
INSERT INTO `bible_verses_tr` VALUES ('1512', '41', '11', '16', 'και ουκ ηφιεν ινα τις διενεγκη σκευος δια του ιερου');
INSERT INTO `bible_verses_tr` VALUES ('1513', '41', '11', '17', 'και εδιδασκεν λεγων αυτοις ου γεγραπται οτι ο οικος μου οικος προσευχης κληθησεται πασιν τοις εθνεσιν υμεις δε εποιησατε αυτον σπηλαιον ληστων');
INSERT INTO `bible_verses_tr` VALUES ('1514', '41', '11', '18', 'και ηκουσαν οι γραμματεις και οι αρχιερεις και εζητουν πως αυτον απολεσουσιν εφοβουντο γαρ αυτον οτι πας ο οχλος εξεπλησσετο επι τη διδαχη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1515', '41', '11', '19', 'και οτε οψε εγενετο εξεπορευετο εξω της πολεως');
INSERT INTO `bible_verses_tr` VALUES ('1516', '41', '11', '20', 'και πρωι παραπορευομενοι ειδον την συκην εξηραμμενην εκ ριζων');
INSERT INTO `bible_verses_tr` VALUES ('1517', '41', '11', '21', 'και αναμνησθεις ο πετρος λεγει αυτω ραββι ιδε η συκη ην κατηρασω εξηρανται');
INSERT INTO `bible_verses_tr` VALUES ('1518', '41', '11', '22', 'και αποκριθεις ιησους λεγει αυτοις εχετε πιστιν θεου');
INSERT INTO `bible_verses_tr` VALUES ('1519', '41', '11', '23', 'αμην γαρ λεγω υμιν οτι ος αν ειπη τω ορει τουτω αρθητι και βληθητι εις την θαλασσαν και μη διακριθη εν τη καρδια αυτου αλλα πιστευση οτι α λεγει γινεται εσται αυτω ο εαν ειπη');
INSERT INTO `bible_verses_tr` VALUES ('1520', '41', '11', '24', 'δια τουτο λεγω υμιν παντα οσα αν προσευχομενοι αιτεισθε πιστευετε οτι λαμβανετε και εσται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('1521', '41', '11', '25', 'και οταν στηκητε προσευχομενοι αφιετε ει τι εχετε κατα τινος ινα και ο πατηρ υμων ο εν τοις ουρανοις αφη υμιν τα παραπτωματα υμων');
INSERT INTO `bible_verses_tr` VALUES ('1522', '41', '11', '26', 'ει δε υμεις ουκ αφιετε ουδε ο πατηρ υμων ο εν τοις ουρανοις αφησει τα παραπτωματα υμων');
INSERT INTO `bible_verses_tr` VALUES ('1523', '41', '11', '27', 'και ερχονται παλιν εις ιεροσολυμα και εν τω ιερω περιπατουντος αυτου ερχονται προς αυτον οι αρχιερεις και οι γραμματεις και οι πρεσβυτεροι');
INSERT INTO `bible_verses_tr` VALUES ('1524', '41', '11', '28', 'και λεγουσιν αυτω εν ποια εξουσια ταυτα ποιεις και τις σοι την εξουσιαν ταυτην εδωκεν ινα ταυτα ποιης');
INSERT INTO `bible_verses_tr` VALUES ('1525', '41', '11', '29', 'ο δε ιησους αποκριθεις ειπεν αυτοις επερωτησω υμας καγω ενα λογον και αποκριθητε μοι και ερω υμιν εν ποια εξουσια ταυτα ποιω');
INSERT INTO `bible_verses_tr` VALUES ('1526', '41', '11', '30', 'το βαπτισμα ιωαννου εξ ουρανου ην η εξ ανθρωπων αποκριθητε μοι');
INSERT INTO `bible_verses_tr` VALUES ('1527', '41', '11', '31', 'και ελογιζοντο προς εαυτους λεγοντες εαν ειπωμεν εξ ουρανου ερει δια τι ουν ουκ επιστευσατε αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1528', '41', '11', '32', 'αλλ εαν ειπωμεν εξ ανθρωπων εφοβουντο τον λαον απαντες γαρ ειχον τον ιωαννην οτι οντως προφητης ην');
INSERT INTO `bible_verses_tr` VALUES ('1529', '41', '11', '33', 'και αποκριθεντες λεγουσιν τω ιησου ουκ οιδαμεν και ο ιησους αποκριθεις λεγει αυτοις ουδε εγω λεγω υμιν εν ποια εξουσια ταυτα ποιω');
INSERT INTO `bible_verses_tr` VALUES ('1530', '41', '12', '1', 'και ηρξατο αυτοις εν παραβολαις λεγειν αμπελωνα εφυτευσεν ανθρωπος και περιεθηκεν φραγμον και ωρυξεν υποληνιον και ωκοδομησεν πυργον και εξεδοτο αυτον γεωργοις και απεδημησεν');
INSERT INTO `bible_verses_tr` VALUES ('1531', '41', '12', '2', 'και απεστειλεν προς τους γεωργους τω καιρω δουλον ινα παρα των γεωργων λαβη απο του καρπου του αμπελωνος');
INSERT INTO `bible_verses_tr` VALUES ('1532', '41', '12', '3', 'οι δε λαβοντες αυτον εδειραν και απεστειλαν κενον');
INSERT INTO `bible_verses_tr` VALUES ('1533', '41', '12', '4', 'και παλιν απεστειλεν προς αυτους αλλον δουλον κακεινον λιθοβολησαντες εκεφαλαιωσαν και απεστειλαν ητιμωμενον');
INSERT INTO `bible_verses_tr` VALUES ('1534', '41', '12', '5', 'και παλιν αλλον απεστειλεν κακεινον απεκτειναν και πολλους αλλους τους μεν δεροντες τους δε αποκτεινοντες');
INSERT INTO `bible_verses_tr` VALUES ('1535', '41', '12', '6', 'ετι ουν ενα υιον εχων αγαπητον αυτου απεστειλεν και αυτον προς αυτους εσχατον λεγων οτι εντραπησονται τον υιον μου');
INSERT INTO `bible_verses_tr` VALUES ('1536', '41', '12', '7', 'εκεινοι δε οι γεωργοι ειπον προς εαυτους οτι ουτος εστιν ο κληρονομος δευτε αποκτεινωμεν αυτον και ημων εσται η κληρονομια');
INSERT INTO `bible_verses_tr` VALUES ('1537', '41', '12', '8', 'και λαβοντες αυτον απεκτειναν και εξεβαλον εξω του αμπελωνος');
INSERT INTO `bible_verses_tr` VALUES ('1538', '41', '12', '9', 'τι ουν ποιησει ο κυριος του αμπελωνος ελευσεται και απολεσει τους γεωργους και δωσει τον αμπελωνα αλλοις');
INSERT INTO `bible_verses_tr` VALUES ('1539', '41', '12', '10', 'ουδε την γραφην ταυτην ανεγνωτε λιθον ον απεδοκιμασαν οι οικοδομουντες ουτος εγενηθη εις κεφαλην γωνιας');
INSERT INTO `bible_verses_tr` VALUES ('1540', '41', '12', '11', 'παρα κυριου εγενετο αυτη και εστιν θαυμαστη εν οφθαλμοις ημων');
INSERT INTO `bible_verses_tr` VALUES ('1541', '41', '12', '12', 'και εζητουν αυτον κρατησαι και εφοβηθησαν τον οχλον εγνωσαν γαρ οτι προς αυτους την παραβολην ειπεν και αφεντες αυτον απηλθον');
INSERT INTO `bible_verses_tr` VALUES ('1542', '41', '12', '13', 'και αποστελλουσιν προς αυτον τινας των φαρισαιων και των ηρωδιανων ινα αυτον αγρευσωσιν λογω');
INSERT INTO `bible_verses_tr` VALUES ('1543', '41', '12', '14', 'οι δε ελθοντες λεγουσιν αυτω διδασκαλε οιδαμεν οτι αληθης ει και ου μελει σοι περι ουδενος ου γαρ βλεπεις εις προσωπον ανθρωπων αλλ επ αληθειας την οδον του θεου διδασκεις εξεστιν κηνσον καισαρι δουναι η ου');
INSERT INTO `bible_verses_tr` VALUES ('1544', '41', '12', '15', 'δωμεν η μη δωμεν ο δε ειδως αυτων την υποκρισιν ειπεν αυτοις τι με πειραζετε φερετε μοι δηναριον ινα ιδω');
INSERT INTO `bible_verses_tr` VALUES ('1545', '41', '12', '16', 'οι δε ηνεγκαν και λεγει αυτοις τινος η εικων αυτη και η επιγραφη οι δε ειπον αυτω καισαρος');
INSERT INTO `bible_verses_tr` VALUES ('1546', '41', '12', '17', 'και αποκριθεις ο ιησους ειπεν αυτοις αποδοτε τα καισαρος καισαρι και τα του θεου τω θεω και εθαυμασαν επ αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1547', '41', '12', '18', 'και ερχονται σαδδουκαιοι προς αυτον οιτινες λεγουσιν αναστασιν μη ειναι και επηρωτησαν αυτον λεγοντες');
INSERT INTO `bible_verses_tr` VALUES ('1548', '41', '12', '19', 'διδασκαλε μωσης εγραψεν ημιν οτι εαν τινος αδελφος αποθανη και καταλιπη γυναικα και τεκνα μη αφη ινα λαβη ο αδελφος αυτου την γυναικα αυτου και εξαναστηση σπερμα τω αδελφω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1549', '41', '12', '20', 'επτα  {VAR2: ουν } αδελφοι ησαν και ο πρωτος ελαβεν γυναικα και αποθνησκων ουκ αφηκεν σπερμα');
INSERT INTO `bible_verses_tr` VALUES ('1550', '41', '12', '21', 'και ο δευτερος ελαβεν αυτην και απεθανεν και ουδε αυτος αφηκεν σπερμα και ο τριτος ωσαυτως');
INSERT INTO `bible_verses_tr` VALUES ('1551', '41', '12', '22', 'και ελαβον αυτην οι επτα και ουκ αφηκαν σπερμα εσχατη παντων απεθανεν και η γυνη');
INSERT INTO `bible_verses_tr` VALUES ('1552', '41', '12', '23', 'εν τη ουν αναστασει οταν αναστωσιν τινος αυτων εσται γυνη οι γαρ επτα εσχον αυτην γυναικα');
INSERT INTO `bible_verses_tr` VALUES ('1553', '41', '12', '24', 'και αποκριθεις ο ιησους ειπεν αυτοις ου δια τουτο πλανασθε μη ειδοτες τας γραφας μηδε την δυναμιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1554', '41', '12', '25', 'οταν γαρ εκ νεκρων αναστωσιν ουτε γαμουσιν ουτε γαμισκονται αλλ εισιν ως αγγελοι οι εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('1555', '41', '12', '26', 'περι δε των νεκρων οτι εγειρονται ουκ ανεγνωτε εν τη βιβλω μωσεως επι της βατου ως ειπεν αυτω ο θεος λεγων εγω ο θεος αβρααμ και ο θεος ισαακ και ο θεος ιακωβ');
INSERT INTO `bible_verses_tr` VALUES ('1556', '41', '12', '27', 'ουκ εστιν ο θεος νεκρων αλλα θεος ζωντων υμεις ουν πολυ πλανασθε');
INSERT INTO `bible_verses_tr` VALUES ('1557', '41', '12', '28', 'και προσελθων εις των γραμματεων ακουσας αυτων συζητουντων ειδως οτι καλως αυτοις απεκριθη επηρωτησεν αυτον ποια εστιν πρωτη πασων εντολη');
INSERT INTO `bible_verses_tr` VALUES ('1558', '41', '12', '29', 'ο δε ιησους απεκριθη αυτω οτι πρωτη πασων των εντολων ακουε ισραηλ κυριος ο θεος ημων κυριος εις εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1559', '41', '12', '30', 'και αγαπησεις κυριον τον θεον σου εξ ολης της καρδιας σου και εξ ολης της ψυχης σου και εξ ολης της διανοιας σου και εξ ολης της ισχυος σου αυτη πρωτη εντολη');
INSERT INTO `bible_verses_tr` VALUES ('1560', '41', '12', '31', 'και δευτερα ομοια αυτη αγαπησεις τον πλησιον σου ως σεαυτον μειζων τουτων αλλη εντολη ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1561', '41', '12', '32', 'και ειπεν αυτω ο γραμματευς καλως διδασκαλε επ αληθειας ειπας οτι εις εστιν θεος και ουκ εστιν αλλος πλην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1562', '41', '12', '33', 'και το αγαπαν αυτον εξ ολης της καρδιας και εξ ολης της συνεσεως και εξ ολης της ψυχης και εξ ολης της ισχυος και το αγαπαν τον πλησιον ως εαυτον πλειον εστιν παντων των ολοκαυτωματων και των θυσιων');
INSERT INTO `bible_verses_tr` VALUES ('1563', '41', '12', '34', 'και ο ιησους ιδων αυτον οτι νουνεχως απεκριθη ειπεν αυτω ου μακραν ει απο της βασιλειας του θεου και ουδεις ουκετι ετολμα αυτον επερωτησαι');
INSERT INTO `bible_verses_tr` VALUES ('1564', '41', '12', '35', 'και αποκριθεις ο ιησους ελεγεν διδασκων εν τω ιερω πως λεγουσιν οι γραμματεις οτι ο χριστος υιος εστιν δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('1565', '41', '12', '36', 'αυτος γαρ δαβιδ ειπεν εν τω πνευματι τω αγιω ειπεν ο κυριος τω κυριω μου καθου εκ δεξιων μου εως αν θω τους εχθρους σου υποποδιον των ποδων σου');
INSERT INTO `bible_verses_tr` VALUES ('1566', '41', '12', '37', 'αυτος ουν δαβιδ λεγει αυτον κυριον και ποθεν υιος αυτου εστιν και ο πολυς οχλος ηκουεν αυτου ηδεως');
INSERT INTO `bible_verses_tr` VALUES ('1567', '41', '12', '38', 'και ελεγεν αυτοις εν τη διδαχη αυτου βλεπετε απο των γραμματεων των θελοντων εν στολαις περιπατειν και ασπασμους εν ταις αγοραις');
INSERT INTO `bible_verses_tr` VALUES ('1568', '41', '12', '39', 'και πρωτοκαθεδριας εν ταις συναγωγαις και πρωτοκλισιας εν τοις δειπνοις');
INSERT INTO `bible_verses_tr` VALUES ('1569', '41', '12', '40', 'οι κατεσθιοντες τας οικιας των χηρων και προφασει μακρα προσευχομενοι ουτοι ληψονται περισσοτερον κριμα');
INSERT INTO `bible_verses_tr` VALUES ('1570', '41', '12', '41', 'και καθισας ο ιησους κατεναντι του γαζοφυλακιου εθεωρει πως ο οχλος βαλλει χαλκον εις το γαζοφυλακιον και πολλοι πλουσιοι εβαλλον πολλα');
INSERT INTO `bible_verses_tr` VALUES ('1571', '41', '12', '42', 'και ελθουσα μια χηρα πτωχη εβαλεν λεπτα δυο ο εστιν κοδραντης');
INSERT INTO `bible_verses_tr` VALUES ('1572', '41', '12', '43', 'και προσκαλεσαμενος τους μαθητας αυτου λεγει αυτοις αμην λεγω υμιν οτι η χηρα αυτη η πτωχη πλειον παντων βεβληκεν των βαλοντων εις το γαζοφυλακιον');
INSERT INTO `bible_verses_tr` VALUES ('1573', '41', '12', '44', 'παντες γαρ εκ του περισσευοντος αυτοις εβαλον αυτη δε εκ της υστερησεως αυτης παντα οσα ειχεν εβαλεν ολον τον βιον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1574', '41', '13', '1', 'και εκπορευομενου αυτου εκ του ιερου λεγει αυτω εις των μαθητων αυτου διδασκαλε ιδε ποταποι λιθοι και ποταπαι οικοδομαι');
INSERT INTO `bible_verses_tr` VALUES ('1575', '41', '13', '2', 'και ο ιησους αποκριθεις ειπεν αυτω βλεπεις ταυτας τας μεγαλας οικοδομας ου μη αφεθη λιθος επι λιθω ος ου μη καταλυθη');
INSERT INTO `bible_verses_tr` VALUES ('1576', '41', '13', '3', 'και καθημενου αυτου εις το ορος των ελαιων κατεναντι του ιερου επηρωτων αυτον κατ ιδιαν πετρος και ιακωβος και ιωαννης και ανδρεας');
INSERT INTO `bible_verses_tr` VALUES ('1577', '41', '13', '4', 'ειπε ημιν ποτε ταυτα εσται και τι το σημειον οταν μελλη παντα ταυτα συντελεισθαι');
INSERT INTO `bible_verses_tr` VALUES ('1578', '41', '13', '5', 'ο δε ιησους αποκριθεις αυτοις ηρξατο λεγειν βλεπετε μη τις υμας πλανηση');
INSERT INTO `bible_verses_tr` VALUES ('1579', '41', '13', '6', 'πολλοι γαρ ελευσονται επι τω ονοματι μου λεγοντες οτι εγω ειμι και πολλους πλανησουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1580', '41', '13', '7', 'οταν δε ακουσητε πολεμους και ακοας πολεμων μη θροεισθε δει γαρ γενεσθαι αλλ ουπω το τελος');
INSERT INTO `bible_verses_tr` VALUES ('1581', '41', '13', '8', 'εγερθησεται γαρ εθνος επι εθνος και βασιλεια επι βασιλειαν και εσονται σεισμοι κατα τοπους και εσονται λιμοι και ταραχαι αρχαι ωδινων ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('1582', '41', '13', '9', 'βλεπετε δε υμεις εαυτους παραδωσουσιν γαρ υμας εις συνεδρια και εις συναγωγας δαρησεσθε και επι ηγεμονων και βασιλεων  {VAR1: σταθησεσθε } {VAR2: αχθησεσθε } ενεκεν εμου εις μαρτυριον αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1583', '41', '13', '10', 'και εις παντα τα εθνη δει πρωτον κηρυχθηναι το ευαγγελιον');
INSERT INTO `bible_verses_tr` VALUES ('1584', '41', '13', '11', 'οταν δε αγαγωσιν υμας παραδιδοντες μη προμεριμνατε τι λαλησητε μηδε μελετατε αλλ ο εαν δοθη υμιν εν εκεινη τη ωρα τουτο λαλειτε ου γαρ εστε υμεις οι λαλουντες αλλα το πνευμα το αγιον');
INSERT INTO `bible_verses_tr` VALUES ('1585', '41', '13', '12', 'παραδωσει δε αδελφος αδελφον εις θανατον και πατηρ τεκνον και επαναστησονται τεκνα επι γονεις και θανατωσουσιν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1586', '41', '13', '13', 'και εσεσθε μισουμενοι υπο παντων δια το ονομα μου ο δε υπομεινας εις τελος ουτος σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('1587', '41', '13', '14', 'οταν δε ιδητε το βδελυγμα της ερημωσεως το ρηθεν υπο δανιηλ του προφητου  {VAR1: εστος } {VAR2: εστως } οπου ου δει ο αναγινωσκων νοειτω τοτε οι εν τη ιουδαια φευγετωσαν εις τα ορη');
INSERT INTO `bible_verses_tr` VALUES ('1588', '41', '13', '15', 'ο δε επι του δωματος μη καταβατω εις την οικιαν μηδε εισελθετω αραι τι εκ της οικιας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1589', '41', '13', '16', 'και ο εις τον αγρον ων μη επιστρεψατω εις τα οπισω αραι το ιματιον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1590', '41', '13', '17', 'ουαι δε ταις εν γαστρι εχουσαις και ταις θηλαζουσαις εν εκειναις ταις ημεραις');
INSERT INTO `bible_verses_tr` VALUES ('1591', '41', '13', '18', 'προσευχεσθε δε ινα μη γενηται η φυγη υμων χειμωνος');
INSERT INTO `bible_verses_tr` VALUES ('1592', '41', '13', '19', 'εσονται γαρ αι ημεραι εκειναι θλιψις οια ου γεγονεν τοιαυτη απ αρχης κτισεως ης εκτισεν ο θεος εως του νυν και ου μη γενηται');
INSERT INTO `bible_verses_tr` VALUES ('1593', '41', '13', '20', 'και ει μη κυριος εκολοβωσεν τας ημερας ουκ αν εσωθη πασα σαρξ αλλα δια τους εκλεκτους ους εξελεξατο εκολοβωσεν τας ημερας');
INSERT INTO `bible_verses_tr` VALUES ('1594', '41', '13', '21', 'και τοτε εαν τις υμιν ειπη ιδου ωδε ο χριστος η ιδου εκει μη πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('1595', '41', '13', '22', 'εγερθησονται γαρ ψευδοχριστοι και ψευδοπροφηται και δωσουσιν σημεια και τερατα προς το αποπλαναν ει δυνατον και τους εκλεκτους');
INSERT INTO `bible_verses_tr` VALUES ('1596', '41', '13', '23', 'υμεις δε βλεπετε ιδου προειρηκα υμιν παντα');
INSERT INTO `bible_verses_tr` VALUES ('1597', '41', '13', '24', 'αλλ εν εκειναις ταις ημεραις μετα την θλιψιν εκεινην ο ηλιος σκοτισθησεται και η σεληνη ου δωσει το φεγγος αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1598', '41', '13', '25', 'και οι αστερες του ουρανου εσονται εκπιπτοντες και αι δυναμεις αι εν τοις ουρανοις σαλευθησονται');
INSERT INTO `bible_verses_tr` VALUES ('1599', '41', '13', '26', 'και τοτε οψονται τον υιον του ανθρωπου ερχομενον εν νεφελαις μετα δυναμεως πολλης και δοξης');
INSERT INTO `bible_verses_tr` VALUES ('1600', '41', '13', '27', 'και τοτε αποστελει τους αγγελους αυτου και επισυναξει τους εκλεκτους αυτου εκ των τεσσαρων ανεμων απ ακρου γης εως ακρου ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('1601', '41', '13', '28', 'απο δε της συκης μαθετε την παραβολην οταν αυτης ηδη ο κλαδος απαλος γενηται και εκφυη τα φυλλα γινωσκετε οτι εγγυς το θερος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1602', '41', '13', '29', 'ουτως και υμεις οταν ταυτα ιδητε γινομενα γινωσκετε οτι εγγυς εστιν επι θυραις');
INSERT INTO `bible_verses_tr` VALUES ('1603', '41', '13', '30', 'αμην λεγω υμιν οτι ου μη παρελθη η γενεα αυτη μεχρις ου παντα ταυτα γενηται');
INSERT INTO `bible_verses_tr` VALUES ('1604', '41', '13', '31', 'ο ουρανος και η γη παρελευσονται οι δε λογοι μου ου μη παρελθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1605', '41', '13', '32', 'περι δε της ημερας εκεινης και της ωρας ουδεις οιδεν ουδε οι αγγελοι οι εν ουρανω ουδε ο υιος ει μη ο πατηρ');
INSERT INTO `bible_verses_tr` VALUES ('1606', '41', '13', '33', 'βλεπετε αγρυπνειτε και προσευχεσθε ουκ οιδατε γαρ ποτε ο καιρος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1607', '41', '13', '34', 'ως ανθρωπος αποδημος αφεις την οικιαν αυτου και δους τοις δουλοις αυτου την εξουσιαν και εκαστω το εργον αυτου και τω θυρωρω ενετειλατο ινα γρηγορη');
INSERT INTO `bible_verses_tr` VALUES ('1608', '41', '13', '35', 'γρηγορειτε ουν ουκ οιδατε γαρ ποτε ο κυριος της οικιας ερχεται οψε η μεσονυκτιου η αλεκτοροφωνιας η πρωι');
INSERT INTO `bible_verses_tr` VALUES ('1609', '41', '13', '36', 'μη ελθων εξαιφνης ευρη υμας καθευδοντας');
INSERT INTO `bible_verses_tr` VALUES ('1610', '41', '13', '37', 'α δε υμιν λεγω πασιν λεγω γρηγορειτε');
INSERT INTO `bible_verses_tr` VALUES ('1611', '41', '14', '1', 'ην δε το πασχα και τα αζυμα μετα δυο ημερας και εζητουν οι αρχιερεις και οι γραμματεις πως αυτον εν δολω κρατησαντες αποκτεινωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1612', '41', '14', '2', 'ελεγον δε μη εν τη εορτη μηποτε θορυβος εσται του λαου');
INSERT INTO `bible_verses_tr` VALUES ('1613', '41', '14', '3', 'και οντος αυτου εν βηθανια εν τη οικια σιμωνος του λεπρου κατακειμενου αυτου ηλθεν γυνη εχουσα αλαβαστρον μυρου ναρδου πιστικης πολυτελους και συντριψασα το αλαβαστρον κατεχεεν αυτου κατα της κεφαλης');
INSERT INTO `bible_verses_tr` VALUES ('1614', '41', '14', '4', 'ησαν δε τινες αγανακτουντες προς εαυτους και λεγοντες εις τι η απωλεια αυτη του μυρου γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('1615', '41', '14', '5', 'ηδυνατο γαρ τουτο πραθηναι επανω τριακοσιων δηναριων και δοθηναι τοις πτωχοις και ενεβριμωντο αυτη');
INSERT INTO `bible_verses_tr` VALUES ('1616', '41', '14', '6', 'ο δε ιησους ειπεν αφετε αυτην τι αυτη κοπους παρεχετε καλον εργον ειργασατο εις εμε');
INSERT INTO `bible_verses_tr` VALUES ('1617', '41', '14', '7', 'παντοτε γαρ τους πτωχους εχετε μεθ εαυτων και οταν θελητε δυνασθε αυτους ευ ποιησαι εμε δε ου παντοτε εχετε');
INSERT INTO `bible_verses_tr` VALUES ('1618', '41', '14', '8', 'ο ειχεν αυτη εποιησεν προελαβεν μυρισαι μου το σωμα εις τον ενταφιασμον');
INSERT INTO `bible_verses_tr` VALUES ('1619', '41', '14', '9', 'αμην λεγω υμιν οπου αν κηρυχθη το ευαγγελιον τουτο εις ολον τον κοσμον και ο εποιησεν αυτη λαληθησεται εις μνημοσυνον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1620', '41', '14', '10', 'και ο ιουδας ο ισκαριωτης εις των δωδεκα απηλθεν προς τους αρχιερεις ινα παραδω αυτον αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1621', '41', '14', '11', 'οι δε ακουσαντες εχαρησαν και επηγγειλαντο αυτω αργυριον δουναι και εζητει πως ευκαιρως αυτον παραδω');
INSERT INTO `bible_verses_tr` VALUES ('1622', '41', '14', '12', 'και τη πρωτη ημερα των αζυμων οτε το πασχα εθυον λεγουσιν αυτω οι μαθηται αυτου που θελεις απελθοντες ετοιμασωμεν ινα φαγης το πασχα');
INSERT INTO `bible_verses_tr` VALUES ('1623', '41', '14', '13', 'και αποστελλει δυο των μαθητων αυτου και λεγει αυτοις υπαγετε εις την πολιν και απαντησει υμιν ανθρωπος κεραμιον υδατος βασταζων ακολουθησατε αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1624', '41', '14', '14', 'και οπου εαν εισελθη ειπατε τω οικοδεσποτη οτι ο διδασκαλος λεγει που εστιν το καταλυμα οπου το πασχα μετα των μαθητων μου φαγω');
INSERT INTO `bible_verses_tr` VALUES ('1625', '41', '14', '15', 'και αυτος υμιν δειξει ανωγεον μεγα εστρωμενον ετοιμον εκει ετοιμασατε ημιν');
INSERT INTO `bible_verses_tr` VALUES ('1626', '41', '14', '16', 'και εξηλθον οι μαθηται αυτου και ηλθον εις την πολιν και ευρον καθως ειπεν αυτοις και ητοιμασαν το πασχα');
INSERT INTO `bible_verses_tr` VALUES ('1627', '41', '14', '17', 'και οψιας γενομενης ερχεται μετα των δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('1628', '41', '14', '18', 'και ανακειμενων αυτων και εσθιοντων ειπεν ο ιησους αμην λεγω υμιν οτι εις εξ υμων παραδωσει με ο εσθιων μετ εμου');
INSERT INTO `bible_verses_tr` VALUES ('1629', '41', '14', '19', 'οι δε ηρξαντο λυπεισθαι και λεγειν αυτω εις καθ εις μητι εγω και αλλος μητι εγω');
INSERT INTO `bible_verses_tr` VALUES ('1630', '41', '14', '20', 'ο δε αποκριθεις ειπεν αυτοις εις εκ των δωδεκα ο εμβαπτομενος μετ εμου εις το τρυβλιον');
INSERT INTO `bible_verses_tr` VALUES ('1631', '41', '14', '21', 'ο μεν υιος του ανθρωπου υπαγει καθως γεγραπται περι αυτου ουαι δε τω ανθρωπω εκεινω δι ου ο υιος του ανθρωπου παραδιδοται καλον ην αυτω ει ουκ εγεννηθη ο ανθρωπος εκεινος');
INSERT INTO `bible_verses_tr` VALUES ('1632', '41', '14', '22', 'και εσθιοντων αυτων λαβων ο ιησους αρτον ευλογησας εκλασεν και εδωκεν αυτοις και ειπεν λαβετε φαγετε τουτο εστιν το σωμα μου');
INSERT INTO `bible_verses_tr` VALUES ('1633', '41', '14', '23', 'και λαβων το ποτηριον ευχαριστησας εδωκεν αυτοις και επιον εξ αυτου παντες');
INSERT INTO `bible_verses_tr` VALUES ('1634', '41', '14', '24', 'και ειπεν αυτοις τουτο εστιν το αιμα μου το της καινης διαθηκης το περι πολλων εκχυνομενον');
INSERT INTO `bible_verses_tr` VALUES ('1635', '41', '14', '25', 'αμην λεγω υμιν οτι ουκετι ου μη πιω εκ του γεννηματος της αμπελου εως της ημερας εκεινης οταν αυτο πινω καινον εν τη βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1636', '41', '14', '26', 'και υμνησαντες εξηλθον εις το ορος των ελαιων');
INSERT INTO `bible_verses_tr` VALUES ('1637', '41', '14', '27', 'και λεγει αυτοις ο ιησους οτι παντες σκανδαλισθησεσθε εν εμοι εν τη νυκτι ταυτη οτι γεγραπται παταξω τον ποιμενα και διασκορπισθησεται τα προβατα');
INSERT INTO `bible_verses_tr` VALUES ('1638', '41', '14', '28', 'αλλα μετα το εγερθηναι με προαξω υμας εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('1639', '41', '14', '29', 'ο δε πετρος εφη αυτω και ει παντες σκανδαλισθησονται αλλ ουκ εγω');
INSERT INTO `bible_verses_tr` VALUES ('1640', '41', '14', '30', 'και λεγει αυτω ο ιησους αμην λεγω σοι οτι σημερον εν τη νυκτι ταυτη πριν η δις αλεκτορα φωνησαι τρις απαρνηση με');
INSERT INTO `bible_verses_tr` VALUES ('1641', '41', '14', '31', 'ο δε εκ περισσου ελεγεν μαλλον εαν με δεη συναποθανειν σοι ου μη σε απαρνησομαι ωσαυτως δε και παντες ελεγον');
INSERT INTO `bible_verses_tr` VALUES ('1642', '41', '14', '32', 'και ερχονται εις χωριον ου το ονομα γεθσημανη και λεγει τοις μαθηταις αυτου καθισατε ωδε εως προσευξωμαι');
INSERT INTO `bible_verses_tr` VALUES ('1643', '41', '14', '33', 'και παραλαμβανει τον πετρον και τον ιακωβον και ιωαννην μεθ εαυτου και ηρξατο εκθαμβεισθαι και αδημονειν');
INSERT INTO `bible_verses_tr` VALUES ('1644', '41', '14', '34', 'και λεγει αυτοις περιλυπος εστιν η ψυχη μου εως θανατου μεινατε ωδε και γρηγορειτε');
INSERT INTO `bible_verses_tr` VALUES ('1645', '41', '14', '35', 'και προελθων μικρον επεσεν επι της γης και προσηυχετο ινα ει δυνατον εστιν παρελθη απ αυτου η ωρα');
INSERT INTO `bible_verses_tr` VALUES ('1646', '41', '14', '36', 'και ελεγεν αββα ο πατηρ παντα δυνατα σοι παρενεγκε το ποτηριον απ εμου τουτο αλλ ου τι εγω θελω αλλα τι συ');
INSERT INTO `bible_verses_tr` VALUES ('1647', '41', '14', '37', 'και ερχεται και ευρισκει αυτους καθευδοντας και λεγει τω πετρω σιμων καθευδεις ουκ ισχυσας μιαν ωραν γρηγορησαι');
INSERT INTO `bible_verses_tr` VALUES ('1648', '41', '14', '38', 'γρηγορειτε και προσευχεσθε ινα μη εισελθητε εις πειρασμον το μεν πνευμα προθυμον η δε σαρξ ασθενης');
INSERT INTO `bible_verses_tr` VALUES ('1649', '41', '14', '39', 'και παλιν απελθων προσηυξατο τον αυτον λογον ειπων');
INSERT INTO `bible_verses_tr` VALUES ('1650', '41', '14', '40', 'και υποστρεψας ευρεν αυτους παλιν καθευδοντας ησαν γαρ οι οφθαλμοι αυτων βεβαρημενοι και ουκ ηδεισαν τι αυτω αποκριθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('1651', '41', '14', '41', 'και ερχεται το τριτον και λεγει αυτοις καθευδετε το λοιπον και αναπαυεσθε απεχει ηλθεν η ωρα ιδου παραδιδοται ο υιος του ανθρωπου εις τας χειρας των αμαρτωλων');
INSERT INTO `bible_verses_tr` VALUES ('1652', '41', '14', '42', 'εγειρεσθε αγωμεν ιδου ο παραδιδους με ηγγικεν');
INSERT INTO `bible_verses_tr` VALUES ('1653', '41', '14', '43', 'και ευθεως ετι αυτου λαλουντος παραγινεται ιουδας εις ων των δωδεκα και μετ αυτου οχλος πολυς μετα μαχαιρων και ξυλων παρα των αρχιερεων και των γραμματεων και των πρεσβυτερων');
INSERT INTO `bible_verses_tr` VALUES ('1654', '41', '14', '44', 'δεδωκει δε ο παραδιδους αυτον συσσημον αυτοις λεγων ον αν φιλησω αυτος εστιν κρατησατε αυτον και απαγαγετε ασφαλως');
INSERT INTO `bible_verses_tr` VALUES ('1655', '41', '14', '45', 'και ελθων ευθεως προσελθων αυτω λεγει ραββι ραββι και κατεφιλησεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1656', '41', '14', '46', 'οι δε επεβαλον επ αυτον τας χειρας αυτων και εκρατησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1657', '41', '14', '47', 'εις δε τις των παρεστηκοτων σπασαμενος την μαχαιραν επαισεν τον δουλον του αρχιερεως και αφειλεν αυτου το ωτιον');
INSERT INTO `bible_verses_tr` VALUES ('1658', '41', '14', '48', 'και αποκριθεις ο ιησους ειπεν αυτοις ως επι ληστην εξηλθετε μετα μαχαιρων και ξυλων συλλαβειν με');
INSERT INTO `bible_verses_tr` VALUES ('1659', '41', '14', '49', 'καθ ημεραν ημην προς υμας εν τω ιερω διδασκων και ουκ εκρατησατε με αλλ ινα πληρωθωσιν αι γραφαι');
INSERT INTO `bible_verses_tr` VALUES ('1660', '41', '14', '50', 'και αφεντες αυτον παντες εφυγον');
INSERT INTO `bible_verses_tr` VALUES ('1661', '41', '14', '51', 'και εις τις νεανισκος ηκολουθει αυτω περιβεβλημενος σινδονα επι γυμνου και κρατουσιν αυτον οι νεανισκοι');
INSERT INTO `bible_verses_tr` VALUES ('1662', '41', '14', '52', 'ο δε καταλιπων την σινδονα γυμνος εφυγεν απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1663', '41', '14', '53', 'και απηγαγον τον ιησουν προς τον αρχιερεα και συνερχονται αυτω παντες οι αρχιερεις και οι πρεσβυτεροι και οι γραμματεις');
INSERT INTO `bible_verses_tr` VALUES ('1664', '41', '14', '54', 'και ο πετρος απο μακροθεν ηκολουθησεν αυτω εως εσω εις την αυλην του αρχιερεως και ην συγκαθημενος μετα των υπηρετων και θερμαινομενος προς το φως');
INSERT INTO `bible_verses_tr` VALUES ('1665', '41', '14', '55', 'οι δε αρχιερεις και ολον το συνεδριον εζητουν κατα του ιησου μαρτυριαν εις το θανατωσαι αυτον και ουχ ευρισκον');
INSERT INTO `bible_verses_tr` VALUES ('1666', '41', '14', '56', 'πολλοι γαρ εψευδομαρτυρουν κατ αυτου και ισαι αι μαρτυριαι ουκ ησαν');
INSERT INTO `bible_verses_tr` VALUES ('1667', '41', '14', '57', 'και τινες ανασταντες εψευδομαρτυρουν κατ αυτου λεγοντες');
INSERT INTO `bible_verses_tr` VALUES ('1668', '41', '14', '58', 'οτι ημεις ηκουσαμεν αυτου λεγοντος οτι εγω καταλυσω τον ναον τουτον τον χειροποιητον και δια τριων ημερων αλλον αχειροποιητον οικοδομησω');
INSERT INTO `bible_verses_tr` VALUES ('1669', '41', '14', '59', 'και ουδε ουτως ιση ην η μαρτυρια αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1670', '41', '14', '60', 'και αναστας ο αρχιερευς εις το μεσον επηρωτησεν τον ιησουν λεγων ουκ αποκρινη ουδεν τι ουτοι σου καταμαρτυρουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1671', '41', '14', '61', 'ο δε εσιωπα και ουδεν απεκρινατο παλιν ο αρχιερευς επηρωτα αυτον και λεγει αυτω συ ει ο χριστος ο υιος του ευλογητου');
INSERT INTO `bible_verses_tr` VALUES ('1672', '41', '14', '62', 'ο δε ιησους ειπεν εγω ειμι και οψεσθε τον υιον του ανθρωπου καθημενον εκ δεξιων της δυναμεως και ερχομενον μετα των νεφελων του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('1673', '41', '14', '63', 'ο δε αρχιερευς διαρρηξας τους χιτωνας αυτου λεγει τι ετι χρειαν εχομεν μαρτυρων');
INSERT INTO `bible_verses_tr` VALUES ('1674', '41', '14', '64', 'ηκουσατε της βλασφημιας τι υμιν φαινεται οι δε παντες κατεκριναν αυτον ειναι ενοχον θανατου');
INSERT INTO `bible_verses_tr` VALUES ('1675', '41', '14', '65', 'και ηρξαντο τινες εμπτυειν αυτω και περικαλυπτειν το προσωπον αυτου και κολαφιζειν αυτον και λεγειν αυτω προφητευσον και οι υπηρεται ραπισμασιν αυτον εβαλλον');
INSERT INTO `bible_verses_tr` VALUES ('1676', '41', '14', '66', 'και οντος του πετρου εν τη αυλη κατω ερχεται μια των παιδισκων του αρχιερεως');
INSERT INTO `bible_verses_tr` VALUES ('1677', '41', '14', '67', 'και ιδουσα τον πετρον θερμαινομενον εμβλεψασα αυτω λεγει και συ μετα του ναζαρηνου ιησου ησθα');
INSERT INTO `bible_verses_tr` VALUES ('1678', '41', '14', '68', 'ο δε ηρνησατο λεγων ουκ οιδα ουδε επισταμαι τι συ λεγεις και εξηλθεν εξω εις το προαυλιον και αλεκτωρ εφωνησεν');
INSERT INTO `bible_verses_tr` VALUES ('1679', '41', '14', '69', 'και η παιδισκη ιδουσα αυτον παλιν ηρξατο λεγειν τοις παρεστηκοσιν οτι ουτος εξ αυτων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('1680', '41', '14', '70', 'ο δε παλιν ηρνειτο και μετα μικρον παλιν οι παρεστωτες ελεγον τω πετρω αληθως εξ αυτων ει και γαρ γαλιλαιος ει και η λαλια σου ομοιαζει');
INSERT INTO `bible_verses_tr` VALUES ('1681', '41', '14', '71', 'ο δε ηρξατο αναθεματιζειν και ομνυειν οτι ουκ οιδα τον ανθρωπον τουτον ον λεγετε');
INSERT INTO `bible_verses_tr` VALUES ('1682', '41', '14', '72', 'και εκ δευτερου αλεκτωρ εφωνησεν και ανεμνησθη ο πετρος του ρηματος ου ειπεν αυτω ο ιησους οτι πριν αλεκτορα φωνησαι δις απαρνηση με τρις και επιβαλων εκλαιεν');
INSERT INTO `bible_verses_tr` VALUES ('1683', '41', '15', '1', 'και ευθεως επι το πρωι συμβουλιον ποιησαντες οι αρχιερεις μετα των πρεσβυτερων και γραμματεων και ολον το συνεδριον δησαντες τον ιησουν απηνεγκαν και παρεδωκαν τω πιλατω');
INSERT INTO `bible_verses_tr` VALUES ('1684', '41', '15', '2', 'και επηρωτησεν αυτον ο πιλατος συ ει ο βασιλευς των ιουδαιων ο δε αποκριθεις ειπεν αυτω συ λεγεις');
INSERT INTO `bible_verses_tr` VALUES ('1685', '41', '15', '3', 'και κατηγορουν αυτου οι αρχιερεις πολλα  {VAR2: αυτος δε ουδεν απεκρινατο }');
INSERT INTO `bible_verses_tr` VALUES ('1686', '41', '15', '4', 'ο δε πιλατος παλιν επηρωτησεν αυτον λεγων ουκ αποκρινη ουδεν ιδε ποσα σου καταμαρτυρουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1687', '41', '15', '5', 'ο δε ιησους ουκετι ουδεν απεκριθη ωστε θαυμαζειν τον πιλατον');
INSERT INTO `bible_verses_tr` VALUES ('1688', '41', '15', '6', 'κατα δε εορτην απελυεν αυτοις ενα δεσμιον ονπερ ητουντο');
INSERT INTO `bible_verses_tr` VALUES ('1689', '41', '15', '7', 'ην δε ο λεγομενος βαραββας μετα των συστασιαστων δεδεμενος οιτινες εν τη στασει φονον πεποιηκεισαν');
INSERT INTO `bible_verses_tr` VALUES ('1690', '41', '15', '8', 'και αναβοησας ο οχλος ηρξατο αιτεισθαι καθως αει εποιει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1691', '41', '15', '9', 'ο δε πιλατος απεκριθη αυτοις λεγων θελετε απολυσω υμιν τον βασιλεα των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1692', '41', '15', '10', 'εγινωσκεν γαρ οτι δια φθονον παραδεδωκεισαν αυτον οι αρχιερεις');
INSERT INTO `bible_verses_tr` VALUES ('1693', '41', '15', '11', 'οι δε αρχιερεις ανεσεισαν τον οχλον ινα μαλλον τον βαραββαν απολυση αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1694', '41', '15', '12', 'ο δε πιλατος αποκριθεις παλιν ειπεν αυτοις τι ουν θελετε ποιησω ον λεγετε βασιλεα των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1695', '41', '15', '13', 'οι δε παλιν εκραξαν σταυρωσον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1696', '41', '15', '14', 'ο δε πιλατος ελεγεν αυτοις τι γαρ κακον εποιησεν οι δε περισσοτερως εκραξαν σταυρωσον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1697', '41', '15', '15', 'ο δε πιλατος βουλομενος τω οχλω το ικανον ποιησαι απελυσεν αυτοις τον βαραββαν και παρεδωκεν τον ιησουν φραγελλωσας ινα σταυρωθη');
INSERT INTO `bible_verses_tr` VALUES ('1698', '41', '15', '16', 'οι δε στρατιωται απηγαγον αυτον εσω της αυλης ο εστιν πραιτωριον και συγκαλουσιν ολην την σπειραν');
INSERT INTO `bible_verses_tr` VALUES ('1699', '41', '15', '17', 'και ενδυουσιν αυτον πορφυραν και περιτιθεασιν αυτω πλεξαντες ακανθινον στεφανον');
INSERT INTO `bible_verses_tr` VALUES ('1700', '41', '15', '18', 'και ηρξαντο ασπαζεσθαι αυτον χαιρε βασιλευ των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1701', '41', '15', '19', 'και ετυπτον αυτου την κεφαλην καλαμω και ενεπτυον αυτω και τιθεντες τα γονατα προσεκυνουν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1702', '41', '15', '20', 'και οτε ενεπαιξαν αυτω εξεδυσαν αυτον την πορφυραν και ενεδυσαν αυτον τα ιματια τα ιδια και εξαγουσιν αυτον ινα σταυρωσωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1703', '41', '15', '21', 'και αγγαρευουσιν παραγοντα τινα σιμωνα κυρηναιον ερχομενον απ αγρου τον πατερα αλεξανδρου και ρουφου ινα αρη τον σταυρον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1704', '41', '15', '22', 'και φερουσιν αυτον επι γολγοθα τοπον ο εστιν μεθερμηνευομενον κρανιου τοπος');
INSERT INTO `bible_verses_tr` VALUES ('1705', '41', '15', '23', 'και εδιδουν αυτω πιειν εσμυρνισμενον οινον ο δε ουκ ελαβεν');
INSERT INTO `bible_verses_tr` VALUES ('1706', '41', '15', '24', 'και σταυρωσαντες αυτον διεμεριζον τα ιματια αυτου βαλλοντες κληρον επ αυτα τις τι αρη');
INSERT INTO `bible_verses_tr` VALUES ('1707', '41', '15', '25', 'ην δε ωρα τριτη και εσταυρωσαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1708', '41', '15', '26', 'και ην η επιγραφη της αιτιας αυτου επιγεγραμμενη ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('1709', '41', '15', '27', 'και συν αυτω σταυρουσιν δυο ληστας ενα εκ δεξιων και ενα εξ ευωνυμων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1710', '41', '15', '28', 'και επληρωθη η γραφη η λεγουσα και μετα ανομων ελογισθη');
INSERT INTO `bible_verses_tr` VALUES ('1711', '41', '15', '29', 'και οι παραπορευομενοι εβλασφημουν αυτον κινουντες τας κεφαλας αυτων και λεγοντες ουα ο καταλυων τον ναον και εν τρισιν ημεραις οικοδομων');
INSERT INTO `bible_verses_tr` VALUES ('1712', '41', '15', '30', 'σωσον σεαυτον και καταβα απο του σταυρου');
INSERT INTO `bible_verses_tr` VALUES ('1713', '41', '15', '31', 'ομοιως δε και οι αρχιερεις εμπαιζοντες προς αλληλους μετα των γραμματεων ελεγον αλλους εσωσεν εαυτον ου δυναται σωσαι');
INSERT INTO `bible_verses_tr` VALUES ('1714', '41', '15', '32', 'ο χριστος ο βασιλευς του ισραηλ καταβατω νυν απο του σταυρου ινα ιδωμεν και πιστευσωμεν και οι συνεσταυρωμενοι αυτω ωνειδιζον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1715', '41', '15', '33', 'γενομενης δε ωρας εκτης σκοτος εγενετο εφ ολην την γην εως ωρας εννατης');
INSERT INTO `bible_verses_tr` VALUES ('1716', '41', '15', '34', 'και τη ωρα τη εννατη εβοησεν ο ιησους φωνη μεγαλη λεγων ελωι ελωι λαμμα σαβαχθανι ο εστιν μεθερμηνευομενον ο θεος μου ο θεος μου εις τι με εγκατελιπες');
INSERT INTO `bible_verses_tr` VALUES ('1717', '41', '15', '35', 'και τινες των παρεστηκοτων ακουσαντες ελεγον ιδου ηλιαν φωνει');
INSERT INTO `bible_verses_tr` VALUES ('1718', '41', '15', '36', 'δραμων δε εις και γεμισας σπογγον οξους περιθεις τε καλαμω εποτιζεν αυτον λεγων αφετε ιδωμεν ει ερχεται ηλιας καθελειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1719', '41', '15', '37', 'ο δε ιησους αφεις φωνην μεγαλην εξεπνευσεν');
INSERT INTO `bible_verses_tr` VALUES ('1720', '41', '15', '38', 'και το καταπετασμα του ναου εσχισθη εις δυο απο ανωθεν εως κατω');
INSERT INTO `bible_verses_tr` VALUES ('1721', '41', '15', '39', 'ιδων δε ο κεντυριων ο παρεστηκως εξ εναντιας αυτου οτι ουτως κραξας εξεπνευσεν ειπεν αληθως ο ανθρωπος ουτος υιος ην θεου');
INSERT INTO `bible_verses_tr` VALUES ('1722', '41', '15', '40', 'ησαν δε και γυναικες απο μακροθεν θεωρουσαι εν αις ην και μαρια η μαγδαληνη και μαρια η του ιακωβου του μικρου και ιωση μητηρ και σαλωμη');
INSERT INTO `bible_verses_tr` VALUES ('1723', '41', '15', '41', 'αι και οτε ην εν τη γαλιλαια ηκολουθουν αυτω και διηκονουν αυτω και αλλαι πολλαι αι συναναβασαι αυτω εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('1724', '41', '15', '42', 'και ηδη οψιας γενομενης επει ην παρασκευη ο εστιν προσαββατον');
INSERT INTO `bible_verses_tr` VALUES ('1725', '41', '15', '43', 'ηλθεν ιωσηφ ο απο αριμαθαιας ευσχημων βουλευτης ος και αυτος ην προσδεχομενος την βασιλειαν του θεου τολμησας εισηλθεν προς πιλατον και ητησατο το σωμα του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('1726', '41', '15', '44', 'ο δε πιλατος εθαυμασεν ει ηδη τεθνηκεν και προσκαλεσαμενος τον κεντυριωνα επηρωτησεν αυτον ει παλαι απεθανεν');
INSERT INTO `bible_verses_tr` VALUES ('1727', '41', '15', '45', 'και γνους απο του κεντυριωνος εδωρησατο το σωμα τω ιωσηφ');
INSERT INTO `bible_verses_tr` VALUES ('1728', '41', '15', '46', 'και αγορασας σινδονα και καθελων αυτον ενειλησεν τη σινδονι και κατεθηκεν αυτον εν μνημειω ο ην λελατομημενον εκ πετρας και προσεκυλισεν λιθον επι την θυραν του μνημειου');
INSERT INTO `bible_verses_tr` VALUES ('1729', '41', '15', '47', 'η δε μαρια η μαγδαληνη και μαρια ιωση εθεωρουν που τιθεται');
INSERT INTO `bible_verses_tr` VALUES ('1730', '41', '16', '1', 'και διαγενομενου του σαββατου μαρια η μαγδαληνη και μαρια η του ιακωβου και σαλωμη ηγορασαν αρωματα ινα ελθουσαι αλειψωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1731', '41', '16', '2', 'και λιαν πρωι της μιας σαββατων ερχονται επι το μνημειον ανατειλαντος του ηλιου');
INSERT INTO `bible_verses_tr` VALUES ('1732', '41', '16', '3', 'και ελεγον προς εαυτας τις αποκυλισει ημιν τον λιθον εκ της θυρας του μνημειου');
INSERT INTO `bible_verses_tr` VALUES ('1733', '41', '16', '4', 'και αναβλεψασαι θεωρουσιν οτι αποκεκυλισται ο λιθος ην γαρ μεγας σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('1734', '41', '16', '5', 'και εισελθουσαι εις το μνημειον ειδον νεανισκον καθημενον εν τοις δεξιοις περιβεβλημενον στολην λευκην και εξεθαμβηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('1735', '41', '16', '6', 'ο δε λεγει αυταις μη εκθαμβεισθε ιησουν ζητειτε τον ναζαρηνον τον εσταυρωμενον ηγερθη ουκ εστιν ωδε ιδε ο τοπος οπου εθηκαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1736', '41', '16', '7', 'αλλ υπαγετε ειπατε τοις μαθηταις αυτου και τω πετρω οτι προαγει υμας εις την γαλιλαιαν εκει αυτον οψεσθε καθως ειπεν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('1737', '41', '16', '8', 'και εξελθουσαι ταχυ εφυγον απο του μνημειου ειχεν δε αυτας τρομος και εκστασις και ουδενι ουδεν ειπον εφοβουντο γαρ');
INSERT INTO `bible_verses_tr` VALUES ('1738', '41', '16', '9', 'αναστας δε πρωι πρωτη σαββατου εφανη πρωτον μαρια τη μαγδαληνη αφ ης εκβεβληκει επτα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('1739', '41', '16', '10', 'εκεινη πορευθεισα απηγγειλεν τοις μετ αυτου γενομενοις πενθουσιν και κλαιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1740', '41', '16', '11', 'κακεινοι ακουσαντες οτι ζη και εθεαθη υπ αυτης ηπιστησαν');
INSERT INTO `bible_verses_tr` VALUES ('1741', '41', '16', '12', 'μετα δε ταυτα δυσιν εξ αυτων περιπατουσιν εφανερωθη εν ετερα μορφη πορευομενοις εις αγρον');
INSERT INTO `bible_verses_tr` VALUES ('1742', '41', '16', '13', 'κακεινοι απελθοντες απηγγειλαν τοις λοιποις ουδε εκεινοις επιστευσαν');
INSERT INTO `bible_verses_tr` VALUES ('1743', '41', '16', '14', 'υστερον ανακειμενοις αυτοις τοις ενδεκα εφανερωθη και ωνειδισεν την απιστιαν αυτων και σκληροκαρδιαν οτι τοις θεασαμενοις αυτον εγηγερμενον ουκ επιστευσαν');
INSERT INTO `bible_verses_tr` VALUES ('1744', '41', '16', '15', 'και ειπεν αυτοις πορευθεντες εις τον κοσμον απαντα κηρυξατε το ευαγγελιον παση τη κτισει');
INSERT INTO `bible_verses_tr` VALUES ('1745', '41', '16', '16', 'ο πιστευσας και βαπτισθεις σωθησεται ο δε απιστησας κατακριθησεται');
INSERT INTO `bible_verses_tr` VALUES ('1746', '41', '16', '17', 'σημεια δε τοις πιστευσασιν ταυτα παρακολουθησει εν τω ονοματι μου δαιμονια εκβαλουσιν γλωσσαις λαλησουσιν καιναις');
INSERT INTO `bible_verses_tr` VALUES ('1747', '41', '16', '18', 'οφεις αρουσιν καν θανασιμον τι πιωσιν ου μη αυτους βλαψει επι αρρωστους χειρας επιθησουσιν και καλως εξουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1748', '41', '16', '19', 'ο μεν ουν κυριος μετα το λαλησαι αυτοις ανεληφθη εις τον ουρανον και εκαθισεν εκ δεξιων του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1749', '41', '16', '20', 'εκεινοι δε εξελθοντες εκηρυξαν πανταχου του κυριου συνεργουντος και τον λογον βεβαιουντος δια των επακολουθουντων σημειων αμην');
INSERT INTO `bible_verses_tr` VALUES ('1750', '42', '1', '1', 'επειδηπερ πολλοι επεχειρησαν αναταξασθαι διηγησιν περι των πεπληροφορημενων εν ημιν πραγματων');
INSERT INTO `bible_verses_tr` VALUES ('1751', '42', '1', '2', 'καθως παρεδοσαν ημιν οι απ αρχης αυτοπται και υπηρεται γενομενοι του λογου');
INSERT INTO `bible_verses_tr` VALUES ('1752', '42', '1', '3', 'εδοξεν καμοι παρηκολουθηκοτι ανωθεν πασιν ακριβως καθεξης σοι γραψαι κρατιστε θεοφιλε');
INSERT INTO `bible_verses_tr` VALUES ('1753', '42', '1', '4', 'ινα επιγνως περι ων κατηχηθης λογων την ασφαλειαν');
INSERT INTO `bible_verses_tr` VALUES ('1754', '42', '1', '5', 'εγενετο εν ταις ημεραις ηρωδου του βασιλεως της ιουδαιας ιερευς τις ονοματι ζαχαριας εξ εφημεριας αβια και η γυνη αυτου εκ των θυγατερων ααρων και το ονομα αυτης ελισαβετ');
INSERT INTO `bible_verses_tr` VALUES ('1755', '42', '1', '6', 'ησαν δε δικαιοι αμφοτεροι ενωπιον του θεου πορευομενοι εν πασαις ταις εντολαις και δικαιωμασιν του κυριου αμεμπτοι');
INSERT INTO `bible_verses_tr` VALUES ('1756', '42', '1', '7', 'και ουκ ην αυτοις τεκνον καθοτι η ελισαβετ ην στειρα και αμφοτεροι προβεβηκοτες εν ταις ημεραις αυτων ησαν');
INSERT INTO `bible_verses_tr` VALUES ('1757', '42', '1', '8', 'εγενετο δε εν τω ιερατευειν αυτον εν τη ταξει της εφημεριας αυτου εναντι του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1758', '42', '1', '9', 'κατα το εθος της ιερατειας ελαχεν του θυμιασαι εισελθων εις τον ναον του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('1759', '42', '1', '10', 'και παν το πληθος του λαου ην προσευχομενον εξω τη ωρα του θυμιαματος');
INSERT INTO `bible_verses_tr` VALUES ('1760', '42', '1', '11', 'ωφθη δε αυτω αγγελος κυριου εστως εκ δεξιων του θυσιαστηριου του θυμιαματος');
INSERT INTO `bible_verses_tr` VALUES ('1761', '42', '1', '12', 'και εταραχθη ζαχαριας ιδων και φοβος επεπεσεν επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1762', '42', '1', '13', 'ειπεν δε προς αυτον ο αγγελος μη φοβου ζαχαρια διοτι εισηκουσθη η δεησις σου και η γυνη σου ελισαβετ γεννησει υιον σοι και καλεσεις το ονομα αυτου ιωαννην');
INSERT INTO `bible_verses_tr` VALUES ('1763', '42', '1', '14', 'και εσται χαρα σοι και αγαλλιασις και πολλοι επι τη γεννησει αυτου χαρησονται');
INSERT INTO `bible_verses_tr` VALUES ('1764', '42', '1', '15', 'εσται γαρ μεγας ενωπιον του κυριου και οινον και σικερα ου μη πιη και πνευματος αγιου πλησθησεται ετι εκ κοιλιας μητρος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1765', '42', '1', '16', 'και πολλους των υιων ισραηλ επιστρεψει επι κυριον τον θεον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1766', '42', '1', '17', 'και αυτος προελευσεται ενωπιον αυτου εν πνευματι και δυναμει ηλιου επιστρεψαι καρδιας πατερων επι τεκνα και απειθεις εν φρονησει δικαιων ετοιμασαι κυριω λαον κατεσκευασμενον');
INSERT INTO `bible_verses_tr` VALUES ('1767', '42', '1', '18', 'και ειπεν ζαχαριας προς τον αγγελον κατα τι γνωσομαι τουτο εγω γαρ ειμι πρεσβυτης και η γυνη μου προβεβηκυια εν ταις ημεραις αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1768', '42', '1', '19', 'και αποκριθεις ο αγγελος ειπεν αυτω εγω ειμι γαβριηλ ο παρεστηκως ενωπιον του θεου και απεσταλην λαλησαι προς σε και ευαγγελισασθαι σοι ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('1769', '42', '1', '20', 'και ιδου εση σιωπων και μη δυναμενος λαλησαι αχρι ης ημερας γενηται ταυτα ανθ ων ουκ επιστευσας τοις λογοις μου οιτινες πληρωθησονται εις τον καιρον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1770', '42', '1', '21', 'και ην ο λαος προσδοκων τον ζαχαριαν και εθαυμαζον εν τω χρονιζειν αυτον εν τω ναω');
INSERT INTO `bible_verses_tr` VALUES ('1771', '42', '1', '22', 'εξελθων δε ουκ ηδυνατο λαλησαι αυτοις και επεγνωσαν οτι οπτασιαν εωρακεν εν τω ναω και αυτος ην διανευων αυτοις και διεμενεν κωφος');
INSERT INTO `bible_verses_tr` VALUES ('1772', '42', '1', '23', 'και εγενετο ως επλησθησαν αι ημεραι της λειτουργιας αυτου απηλθεν εις τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1773', '42', '1', '24', 'μετα δε ταυτας τας ημερας συνελαβεν ελισαβετ η γυνη αυτου και περιεκρυβεν εαυτην μηνας πεντε λεγουσα');
INSERT INTO `bible_verses_tr` VALUES ('1774', '42', '1', '25', 'οτι ουτως μοι πεποιηκεν ο κυριος εν ημεραις αις επειδεν αφελειν το ονειδος μου εν ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('1775', '42', '1', '26', 'εν δε τω μηνι τω εκτω απεσταλη ο αγγελος γαβριηλ υπο του θεου εις πολιν της γαλιλαιας η ονομα  {VAR1: ναζαρετ } {VAR2: ναζαρεθ }');
INSERT INTO `bible_verses_tr` VALUES ('1776', '42', '1', '27', 'προς παρθενον μεμνηστευμενην ανδρι ω ονομα ιωσηφ εξ οικου δαβιδ και το ονομα της παρθενου μαριαμ');
INSERT INTO `bible_verses_tr` VALUES ('1777', '42', '1', '28', 'και εισελθων ο αγγελος προς αυτην ειπεν χαιρε κεχαριτωμενη ο κυριος μετα σου ευλογημενη συ εν γυναιξιν');
INSERT INTO `bible_verses_tr` VALUES ('1778', '42', '1', '29', 'η δε ιδουσα διεταραχθη επι τω λογω αυτου και διελογιζετο ποταπος ειη ο ασπασμος ουτος');
INSERT INTO `bible_verses_tr` VALUES ('1779', '42', '1', '30', 'και ειπεν ο αγγελος αυτη μη φοβου μαριαμ ευρες γαρ χαριν παρα τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('1780', '42', '1', '31', 'και ιδου συλληψη εν γαστρι και τεξη υιον και καλεσεις το ονομα αυτου ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('1781', '42', '1', '32', 'ουτος εσται μεγας και υιος υψιστου κληθησεται και δωσει αυτω κυριος ο θεος τον θρονον δαβιδ του πατρος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1782', '42', '1', '33', 'και βασιλευσει επι τον οικον ιακωβ εις τους αιωνας και της βασιλειας αυτου ουκ εσται τελος');
INSERT INTO `bible_verses_tr` VALUES ('1783', '42', '1', '34', 'ειπεν δε μαριαμ προς τον αγγελον πως εσται τουτο επει ανδρα ου γινωσκω');
INSERT INTO `bible_verses_tr` VALUES ('1784', '42', '1', '35', 'και αποκριθεις ο αγγελος ειπεν αυτη πνευμα αγιον επελευσεται επι σε και δυναμις υψιστου επισκιασει σοι διο και το γεννωμενον  {VAR2: εκ σου } αγιον κληθησεται υιος θεου');
INSERT INTO `bible_verses_tr` VALUES ('1785', '42', '1', '36', 'και ιδου ελισαβετ η συγγενης σου και αυτη συνειληφυια υιον εν γηρα αυτης και ουτος μην εκτος εστιν αυτη τη καλουμενη στειρα');
INSERT INTO `bible_verses_tr` VALUES ('1786', '42', '1', '37', 'οτι ουκ αδυνατησει παρα τω θεω παν ρημα');
INSERT INTO `bible_verses_tr` VALUES ('1787', '42', '1', '38', 'ειπεν δε μαριαμ ιδου η δουλη κυριου γενοιτο μοι κατα το ρημα σου και απηλθεν απ αυτης ο αγγελος');
INSERT INTO `bible_verses_tr` VALUES ('1788', '42', '1', '39', 'αναστασα δε μαριαμ εν ταις ημεραις ταυταις επορευθη εις την ορεινην μετα σπουδης εις πολιν ιουδα');
INSERT INTO `bible_verses_tr` VALUES ('1789', '42', '1', '40', 'και εισηλθεν εις τον οικον ζαχαριου και ησπασατο την ελισαβετ');
INSERT INTO `bible_verses_tr` VALUES ('1790', '42', '1', '41', 'και εγενετο ως ηκουσεν η ελισαβετ τον ασπασμον της μαριας εσκιρτησεν το βρεφος εν τη κοιλια αυτης και επλησθη πνευματος αγιου η ελισαβετ');
INSERT INTO `bible_verses_tr` VALUES ('1791', '42', '1', '42', 'και ανεφωνησεν φωνη μεγαλη και ειπεν ευλογημενη συ εν γυναιξιν και ευλογημενος ο καρπος της κοιλιας σου');
INSERT INTO `bible_verses_tr` VALUES ('1792', '42', '1', '43', 'και ποθεν μοι τουτο ινα ελθη η μητηρ του κυριου μου προς με');
INSERT INTO `bible_verses_tr` VALUES ('1793', '42', '1', '44', 'ιδου γαρ ως εγενετο η φωνη του ασπασμου σου εις τα ωτα μου εσκιρτησεν εν αγαλλιασει το βρεφος εν τη κοιλια μου');
INSERT INTO `bible_verses_tr` VALUES ('1794', '42', '1', '45', 'και μακαρια η πιστευσασα οτι εσται τελειωσις τοις λελαλημενοις αυτη παρα κυριου');
INSERT INTO `bible_verses_tr` VALUES ('1795', '42', '1', '46', 'και ειπεν μαριαμ μεγαλυνει η ψυχη μου τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('1796', '42', '1', '47', 'και ηγαλλιασεν το πνευμα μου επι τω θεω τω σωτηρι μου');
INSERT INTO `bible_verses_tr` VALUES ('1797', '42', '1', '48', 'οτι επεβλεψεν επι την ταπεινωσιν της δουλης αυτου ιδου γαρ απο του νυν μακαριουσιν με πασαι αι γενεαι');
INSERT INTO `bible_verses_tr` VALUES ('1798', '42', '1', '49', 'οτι εποιησεν μοι μεγαλεια ο δυνατος και αγιον το ονομα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1799', '42', '1', '50', 'και το ελεος αυτου εις γενεας γενεων τοις φοβουμενοις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1800', '42', '1', '51', 'εποιησεν κρατος εν βραχιονι αυτου διεσκορπισεν υπερηφανους διανοια καρδιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1801', '42', '1', '52', 'καθειλεν δυναστας απο θρονων και υψωσεν ταπεινους');
INSERT INTO `bible_verses_tr` VALUES ('1802', '42', '1', '53', 'πεινωντας ενεπλησεν αγαθων και πλουτουντας εξαπεστειλεν κενους');
INSERT INTO `bible_verses_tr` VALUES ('1803', '42', '1', '54', 'αντελαβετο ισραηλ παιδος αυτου μνησθηναι ελεους');
INSERT INTO `bible_verses_tr` VALUES ('1804', '42', '1', '55', 'καθως ελαλησεν προς τους πατερας ημων τω αβρααμ και τω σπερματι αυτου εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('1805', '42', '1', '56', 'εμεινεν δε μαριαμ συν αυτη ωσει μηνας τρεις και υπεστρεψεν εις τον οικον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1806', '42', '1', '57', 'τη δε ελισαβετ επλησθη ο χρονος του τεκειν αυτην και εγεννησεν υιον');
INSERT INTO `bible_verses_tr` VALUES ('1807', '42', '1', '58', 'και ηκουσαν οι περιοικοι και οι συγγενεις αυτης οτι εμεγαλυνεν κυριος το ελεος αυτου μετ αυτης και συνεχαιρον αυτη');
INSERT INTO `bible_verses_tr` VALUES ('1808', '42', '1', '59', 'και εγενετο εν τη ογδοη ημερα ηλθον περιτεμειν το παιδιον και εκαλουν αυτο επι τω ονοματι του πατρος αυτου ζαχαριαν');
INSERT INTO `bible_verses_tr` VALUES ('1809', '42', '1', '60', 'και αποκριθεισα η μητηρ αυτου ειπεν ουχι αλλα κληθησεται ιωαννης');
INSERT INTO `bible_verses_tr` VALUES ('1810', '42', '1', '61', 'και ειπον προς αυτην οτι ουδεις εστιν εν τη συγγενεια σου ος καλειται τω ονοματι τουτω');
INSERT INTO `bible_verses_tr` VALUES ('1811', '42', '1', '62', 'ενενευον δε τω πατρι αυτου το τι αν θελοι καλεισθαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1812', '42', '1', '63', 'και αιτησας πινακιδιον εγραψεν λεγων ιωαννης εστιν το ονομα αυτου και εθαυμασαν παντες');
INSERT INTO `bible_verses_tr` VALUES ('1813', '42', '1', '64', 'ανεωχθη δε το στομα αυτου παραχρημα και η γλωσσα αυτου και ελαλει ευλογων τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('1814', '42', '1', '65', 'και εγενετο επι παντας φοβος τους περιοικουντας αυτους και εν ολη τη ορεινη της ιουδαιας διελαλειτο παντα τα ρηματα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('1815', '42', '1', '66', 'και εθεντο παντες οι ακουσαντες εν τη καρδια αυτων λεγοντες τι αρα το παιδιον τουτο εσται και χειρ κυριου ην μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1816', '42', '1', '67', 'και ζαχαριας ο πατηρ αυτου επλησθη πνευματος αγιου και προεφητευσεν λεγων');
INSERT INTO `bible_verses_tr` VALUES ('1817', '42', '1', '68', 'ευλογητος κυριος ο θεος του ισραηλ οτι επεσκεψατο και εποιησεν λυτρωσιν τω λαω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1818', '42', '1', '69', 'και ηγειρεν κερας σωτηριας ημιν εν τω οικω δαβιδ του παιδος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1819', '42', '1', '70', 'καθως ελαλησεν δια στοματος των αγιων των απ αιωνος προφητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1820', '42', '1', '71', 'σωτηριαν εξ εχθρων ημων και εκ χειρος παντων των μισουντων ημας');
INSERT INTO `bible_verses_tr` VALUES ('1821', '42', '1', '72', 'ποιησαι ελεος μετα των πατερων ημων και μνησθηναι διαθηκης αγιας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1822', '42', '1', '73', 'ορκον ον ωμοσεν προς αβρααμ τον πατερα ημων');
INSERT INTO `bible_verses_tr` VALUES ('1823', '42', '1', '74', 'του δουναι ημιν αφοβως εκ χειρος των εχθρων ημων ρυσθεντας λατρευειν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1824', '42', '1', '75', 'εν οσιοτητι και δικαιοσυνη ενωπιον αυτου πασας τας ημερας της ζωης ημων');
INSERT INTO `bible_verses_tr` VALUES ('1825', '42', '1', '76', 'και συ παιδιον προφητης υψιστου κληθηση προπορευση γαρ προ προσωπου κυριου ετοιμασαι οδους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1826', '42', '1', '77', 'του δουναι γνωσιν σωτηριας τω λαω αυτου εν αφεσει αμαρτιων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1827', '42', '1', '78', 'δια σπλαγχνα ελεους θεου ημων εν οις επεσκεψατο ημας ανατολη εξ υψους');
