INSERT INTO `bible_verses_tr` VALUES ('1922', '42', '4', '3', 'και ειπεν αυτω ο διαβολος ει υιος ει του θεου ειπε τω λιθω τουτω ινα γενηται αρτος');
INSERT INTO `bible_verses_tr` VALUES ('1923', '42', '4', '4', 'και απεκριθη ιησους προς αυτον λεγων γεγραπται οτι ουκ επ αρτω μονω ζησεται ο ανθρωπος αλλ επι παντι ρηματι θεου');
INSERT INTO `bible_verses_tr` VALUES ('1924', '42', '4', '5', 'και αναγαγων αυτον ο διαβολος εις ορος υψηλον εδειξεν αυτω πασας τας βασιλειας της οικουμενης εν στιγμη χρονου');
INSERT INTO `bible_verses_tr` VALUES ('1925', '42', '4', '6', 'και ειπεν αυτω ο διαβολος σοι δωσω την εξουσιαν ταυτην απασαν και την δοξαν αυτων οτι εμοι παραδεδοται και ω εαν θελω διδωμι αυτην');
INSERT INTO `bible_verses_tr` VALUES ('1926', '42', '4', '7', 'συ ουν εαν προσκυνησης ενωπιον μου εσται σου παντα');
INSERT INTO `bible_verses_tr` VALUES ('1927', '42', '4', '8', 'και αποκριθεις αυτω ειπεν ο ιησους υπαγε οπισω μου σατανα γεγραπται γαρ προσκυνησεις κυριον τον θεον σου και αυτω μονω λατρευσεις');
INSERT INTO `bible_verses_tr` VALUES ('1928', '42', '4', '9', 'και ηγαγεν αυτον εις ιερουσαλημ και εστησεν αυτον επι το πτερυγιον του ιερου και ειπεν αυτω ει ο υιος ει του θεου βαλε σεαυτον εντευθεν κατω');
INSERT INTO `bible_verses_tr` VALUES ('1929', '42', '4', '10', 'γεγραπται γαρ οτι τοις αγγελοις αυτου εντελειται περι σου του διαφυλαξαι σε');
INSERT INTO `bible_verses_tr` VALUES ('1930', '42', '4', '11', 'και οτι επι χειρων αρουσιν σε μηποτε προσκοψης προς λιθον τον ποδα σου');
INSERT INTO `bible_verses_tr` VALUES ('1931', '42', '4', '12', 'και αποκριθεις ειπεν αυτω ο ιησους οτι ειρηται ουκ εκπειρασεις κυριον τον θεον σου');
INSERT INTO `bible_verses_tr` VALUES ('1932', '42', '4', '13', 'και συντελεσας παντα πειρασμον ο διαβολος απεστη απ αυτου αχρι καιρου');
INSERT INTO `bible_verses_tr` VALUES ('1933', '42', '4', '14', 'και υπεστρεψεν ο ιησους εν τη δυναμει του πνευματος εις την γαλιλαιαν και φημη εξηλθεν καθ ολης της περιχωρου περι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1934', '42', '4', '15', 'και αυτος εδιδασκεν εν ταις συναγωγαις αυτων δοξαζομενος υπο παντων');
INSERT INTO `bible_verses_tr` VALUES ('1935', '42', '4', '16', 'και ηλθεν εις την  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } ου ην τεθραμμενος και εισηλθεν κατα το ειωθος αυτω εν τη ημερα των σαββατων εις την συναγωγην και ανεστη αναγνωναι');
INSERT INTO `bible_verses_tr` VALUES ('1936', '42', '4', '17', 'και επεδοθη αυτω βιβλιον ησαιου του προφητου και αναπτυξας το βιβλιον ευρεν τον τοπον ου ην γεγραμμενον');
INSERT INTO `bible_verses_tr` VALUES ('1937', '42', '4', '18', 'πνευμα κυριου επ εμε ου ενεκεν εχρισεν με ευαγγελιζεσθαι πτωχοις απεσταλκεν με ιασασθαι τους συντετριμμενους την καρδιαν κηρυξαι αιχμαλωτοις αφεσιν και τυφλοις αναβλεψιν αποστειλαι τεθραυσμενους εν αφεσει');
INSERT INTO `bible_verses_tr` VALUES ('1938', '42', '4', '19', 'κηρυξαι ενιαυτον κυριου δεκτον');
INSERT INTO `bible_verses_tr` VALUES ('1939', '42', '4', '20', 'και πτυξας το βιβλιον αποδους τω υπηρετη εκαθισεν και παντων εν τη συναγωγη οι οφθαλμοι ησαν ατενιζοντες αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1940', '42', '4', '21', 'ηρξατο δε λεγειν προς αυτους οτι σημερον πεπληρωται η γραφη αυτη εν τοις ωσιν υμων');
INSERT INTO `bible_verses_tr` VALUES ('1941', '42', '4', '22', 'και παντες εμαρτυρουν αυτω και εθαυμαζον επι τοις λογοις της χαριτος τοις εκπορευομενοις εκ του στοματος αυτου και ελεγον ουχ ουτος εστιν ο υιος ιωσηφ');
INSERT INTO `bible_verses_tr` VALUES ('1942', '42', '4', '23', 'και ειπεν προς αυτους παντως ερειτε μοι την παραβολην ταυτην ιατρε θεραπευσον σεαυτον οσα ηκουσαμεν γενομενα εν τη καπερναουμ ποιησον και ωδε εν τη πατριδι σου');
INSERT INTO `bible_verses_tr` VALUES ('1943', '42', '4', '24', 'ειπεν δε αμην λεγω υμιν οτι ουδεις προφητης δεκτος εστιν εν τη πατριδι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1944', '42', '4', '25', 'επ αληθειας δε λεγω υμιν πολλαι χηραι ησαν εν ταις ημεραις ηλιου εν τω ισραηλ οτε εκλεισθη ο ουρανος επι ετη τρια και μηνας εξ ως εγενετο λιμος μεγας επι πασαν την γην');
INSERT INTO `bible_verses_tr` VALUES ('1945', '42', '4', '26', 'και προς ουδεμιαν αυτων επεμφθη ηλιας ει μη εις σαρεπτα της σιδωνος προς γυναικα χηραν');
INSERT INTO `bible_verses_tr` VALUES ('1946', '42', '4', '27', 'και πολλοι λεπροι ησαν επι ελισσαιου του προφητου εν τω ισραηλ και ουδεις αυτων εκαθαρισθη ει μη νεεμαν ο συρος');
INSERT INTO `bible_verses_tr` VALUES ('1947', '42', '4', '28', 'και επλησθησαν παντες θυμου εν τη συναγωγη ακουοντες ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('1948', '42', '4', '29', 'και ανασταντες εξεβαλον αυτον εξω της πολεως και ηγαγον αυτον εως της οφρυος του ορους εφ ου η πολις αυτων ωκοδομητο εις το κατακρημνισαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1949', '42', '4', '30', 'αυτος δε διελθων δια μεσου αυτων επορευετο');
INSERT INTO `bible_verses_tr` VALUES ('1950', '42', '4', '31', 'και κατηλθεν εις καπερναουμ πολιν της γαλιλαιας και ην διδασκων αυτους εν τοις σαββασιν');
INSERT INTO `bible_verses_tr` VALUES ('1951', '42', '4', '32', 'και εξεπλησσοντο επι τη διδαχη αυτου οτι εν εξουσια ην ο λογος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1952', '42', '4', '33', 'και εν τη συναγωγη ην ανθρωπος εχων πνευμα δαιμονιου ακαθαρτου και ανεκραξεν φωνη μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('1953', '42', '4', '34', 'λεγων εα τι ημιν και σοι ιησου ναζαρηνε ηλθες απολεσαι ημας οιδα σε τις ει ο αγιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('1954', '42', '4', '35', 'και επετιμησεν αυτω ο ιησους λεγων φιμωθητι και εξελθε εξ αυτου και ριψαν αυτον το δαιμονιον εις το μεσον εξηλθεν απ αυτου μηδεν βλαψαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('1955', '42', '4', '36', 'και εγενετο θαμβος επι παντας και συνελαλουν προς αλληλους λεγοντες τις ο λογος ουτος οτι εν εξουσια και δυναμει επιτασσει τοις ακαθαρτοις πνευμασιν και εξερχονται');
INSERT INTO `bible_verses_tr` VALUES ('1956', '42', '4', '37', 'και εξεπορευετο ηχος περι αυτου εις παντα τοπον της περιχωρου');
INSERT INTO `bible_verses_tr` VALUES ('1957', '42', '4', '38', 'αναστας δε εκ της συναγωγης εισηλθεν εις την οικιαν σιμωνος η πενθερα δε του σιμωνος ην συνεχομενη πυρετω μεγαλω και ηρωτησαν αυτον περι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('1958', '42', '4', '39', 'και επιστας επανω αυτης επετιμησεν τω πυρετω και αφηκεν αυτην παραχρημα δε αναστασα διηκονει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1959', '42', '4', '40', 'δυνοντος δε του ηλιου παντες οσοι ειχον ασθενουντας νοσοις ποικιλαις ηγαγον αυτους προς αυτον ο δε ενι εκαστω αυτων τας χειρας επιθεις εθεραπευσεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1960', '42', '4', '41', 'εξηρχετο δε και δαιμονια απο πολλων κραζοντα και λεγοντα οτι συ ει ο χριστος ο υιος του θεου και επιτιμων ουκ εια αυτα λαλειν οτι ηδεισαν τον χριστον αυτον ειναι');
INSERT INTO `bible_verses_tr` VALUES ('1961', '42', '4', '42', 'γενομενης δε ημερας εξελθων επορευθη εις ερημον τοπον και οι οχλοι εζητουν αυτον και ηλθον εως αυτου και κατειχον αυτον του μη πορευεσθαι απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1962', '42', '4', '43', 'ο δε ειπεν προς αυτους οτι και ταις ετεραις πολεσιν ευαγγελισασθαι με δει την βασιλειαν του θεου οτι εις τουτο απεσταλμαι');
INSERT INTO `bible_verses_tr` VALUES ('1963', '42', '4', '44', 'και ην κηρυσσων εν ταις συναγωγαις της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('1964', '42', '5', '1', 'εγενετο δε εν τω τον οχλον επικεισθαι αυτω του ακουειν τον λογον του θεου και αυτος ην εστως παρα την λιμνην γεννησαρετ');
INSERT INTO `bible_verses_tr` VALUES ('1965', '42', '5', '2', 'και ειδεν δυο πλοια εστωτα παρα την λιμνην οι δε αλιεις αποβαντες απ αυτων απεπλυναν τα δικτυα');
INSERT INTO `bible_verses_tr` VALUES ('1966', '42', '5', '3', 'εμβας δε εις εν των πλοιων ο ην του σιμωνος ηρωτησεν αυτον απο της γης επαναγαγειν ολιγον και καθισας εδιδασκεν εκ του πλοιου τους οχλους');
INSERT INTO `bible_verses_tr` VALUES ('1967', '42', '5', '4', 'ως δε επαυσατο λαλων ειπεν προς τον σιμωνα επαναγαγε εις το βαθος και χαλασατε τα δικτυα υμων εις αγραν');
INSERT INTO `bible_verses_tr` VALUES ('1968', '42', '5', '5', 'και αποκριθεις ο σιμων ειπεν αυτω επιστατα δι ολης της νυκτος κοπιασαντες ουδεν ελαβομεν επι δε τω ρηματι σου χαλασω το δικτυον');
INSERT INTO `bible_verses_tr` VALUES ('1969', '42', '5', '6', 'και τουτο ποιησαντες συνεκλεισαν ιχθυων πληθος πολυ διερρηγνυτο δε το δικτυον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1970', '42', '5', '7', 'και κατενευσαν τοις μετοχοις τοις εν τω ετερω πλοιω του ελθοντας συλλαβεσθαι αυτοις και ηλθον και επλησαν αμφοτερα τα πλοια ωστε βυθιζεσθαι αυτα');
INSERT INTO `bible_verses_tr` VALUES ('1971', '42', '5', '8', 'ιδων δε σιμων πετρος προσεπεσεν τοις γονασιν του ιησου λεγων εξελθε απ εμου οτι ανηρ αμαρτωλος ειμι κυριε');
INSERT INTO `bible_verses_tr` VALUES ('1972', '42', '5', '9', 'θαμβος γαρ περιεσχεν αυτον και παντας τους συν αυτω επι τη αγρα των ιχθυων η συνελαβον');
INSERT INTO `bible_verses_tr` VALUES ('1973', '42', '5', '10', 'ομοιως δε και ιακωβον και ιωαννην υιους ζεβεδαιου οι ησαν κοινωνοι τω σιμωνι και ειπεν προς τον σιμωνα ο ιησους μη φοβου απο του νυν ανθρωπους εση ζωγρων');
INSERT INTO `bible_verses_tr` VALUES ('1974', '42', '5', '11', 'και καταγαγοντες τα πλοια επι την γην αφεντες απαντα ηκολουθησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1975', '42', '5', '12', 'και εγενετο εν τω ειναι αυτον εν μια των πολεων και ιδου ανηρ πληρης λεπρας και ιδων τον ιησουν πεσων επι προσωπον εδεηθη αυτου λεγων κυριε εαν θελης δυνασαι με καθαρισαι');
INSERT INTO `bible_verses_tr` VALUES ('1976', '42', '5', '13', 'και εκτεινας την χειρα ηψατο αυτου ειπων θελω καθαρισθητι και ευθεως η λεπρα απηλθεν απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1977', '42', '5', '14', 'και αυτος παρηγγειλεν αυτω μηδενι ειπειν αλλα απελθων δειξον σεαυτον τω ιερει και προσενεγκε περι του καθαρισμου σου καθως προσεταξεν μωσης εις μαρτυριον αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('1978', '42', '5', '15', 'διηρχετο δε μαλλον ο λογος περι αυτου και συνηρχοντο οχλοι πολλοι ακουειν και θεραπευεσθαι υπ αυτου απο των ασθενειων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('1979', '42', '5', '16', 'αυτος δε ην υποχωρων εν ταις ερημοις και προσευχομενος');
INSERT INTO `bible_verses_tr` VALUES ('1980', '42', '5', '17', 'και εγενετο εν μια των ημερων και αυτος ην διδασκων και ησαν καθημενοι φαρισαιοι και νομοδιδασκαλοι οι ησαν εληλυθοτες εκ πασης κωμης της γαλιλαιας και ιουδαιας και ιερουσαλημ και δυναμις κυριου ην εις το ιασθαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('1981', '42', '5', '18', 'και ιδου ανδρες φεροντες επι κλινης ανθρωπον ος ην παραλελυμενος και εζητουν αυτον εισενεγκειν και θειναι ενωπιον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('1982', '42', '5', '19', 'και μη ευροντες δια ποιας εισενεγκωσιν αυτον δια τον οχλον αναβαντες επι το δωμα δια των κεραμων καθηκαν αυτον συν τω κλινιδιω εις το μεσον εμπροσθεν του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('1983', '42', '5', '20', 'και ιδων την πιστιν αυτων ειπεν αυτω ανθρωπε αφεωνται σοι αι αμαρτιαι σου');
INSERT INTO `bible_verses_tr` VALUES ('1984', '42', '5', '21', 'και ηρξαντο διαλογιζεσθαι οι γραμματεις και οι φαρισαιοι λεγοντες τις εστιν ουτος ος λαλει βλασφημιας τις δυναται αφιεναι αμαρτιας ει μη μονος ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('1985', '42', '5', '22', 'επιγνους δε ο ιησους τους διαλογισμους αυτων αποκριθεις ειπεν προς αυτους τι διαλογιζεσθε εν ταις καρδιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('1986', '42', '5', '23', 'τι εστιν ευκοπωτερον ειπειν αφεωνται σοι αι αμαρτιαι σου η ειπειν εγειραι και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('1987', '42', '5', '24', 'ινα δε ειδητε οτι εξουσιαν εχει ο υιος του ανθρωπου επι της γης αφιεναι αμαρτιας ειπεν τω παραλελυμενω σοι λεγω εγειραι και αρας το κλινιδιον σου πορευου εις τον οικον σου');
INSERT INTO `bible_verses_tr` VALUES ('1988', '42', '5', '25', 'και παραχρημα αναστας ενωπιον αυτων αρας εφ ω κατεκειτο απηλθεν εις τον οικον αυτου δοξαζων τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('1989', '42', '5', '26', 'και εκστασις ελαβεν απαντας και εδοξαζον τον θεον και επλησθησαν φοβου λεγοντες οτι ειδομεν παραδοξα σημερον');
INSERT INTO `bible_verses_tr` VALUES ('1990', '42', '5', '27', 'και μετα ταυτα εξηλθεν και εθεασατο τελωνην ονοματι λευιν καθημενον επι το τελωνιον και ειπεν αυτω ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('1991', '42', '5', '28', 'και καταλιπων απαντα αναστας ηκολουθησεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('1992', '42', '5', '29', 'και εποιησεν δοχην μεγαλην ο λευις αυτω εν τη οικια αυτου και ην οχλος τελωνων πολυς και αλλων οι ησαν μετ αυτων κατακειμενοι');
INSERT INTO `bible_verses_tr` VALUES ('1993', '42', '5', '30', 'και εγογγυζον οι γραμματεις αυτων και οι φαρισαιοι προς τους μαθητας αυτου λεγοντες δια τι μετα τελωνων και αμαρτωλων εσθιετε και πινετε');
INSERT INTO `bible_verses_tr` VALUES ('1994', '42', '5', '31', 'και αποκριθεις ο ιησους ειπεν προς αυτους ου χρειαν εχουσιν οι υγιαινοντες ιατρου αλλ οι κακως εχοντες');
INSERT INTO `bible_verses_tr` VALUES ('1995', '42', '5', '32', 'ουκ εληλυθα καλεσαι δικαιους αλλα αμαρτωλους εις μετανοιαν');
INSERT INTO `bible_verses_tr` VALUES ('1996', '42', '5', '33', 'οι δε ειπον προς αυτον δια τι οι μαθηται ιωαννου νηστευουσιν πυκνα και δεησεις ποιουνται ομοιως και οι των φαρισαιων οι δε σοι εσθιουσιν και πινουσιν');
INSERT INTO `bible_verses_tr` VALUES ('1997', '42', '5', '34', 'ο δε ειπεν προς αυτους μη δυνασθε τους υιους του νυμφωνος εν ω ο νυμφιος μετ αυτων εστιν ποιησαι νηστευειν');
INSERT INTO `bible_verses_tr` VALUES ('1998', '42', '5', '35', 'ελευσονται δε ημεραι και οταν απαρθη απ αυτων ο νυμφιος τοτε νηστευσουσιν εν εκειναις ταις ημεραις');
INSERT INTO `bible_verses_tr` VALUES ('1999', '42', '5', '36', 'ελεγεν δε και παραβολην προς αυτους οτι ουδεις επιβλημα ιματιου καινου επιβαλλει επι ιματιον παλαιον ει δε μηγε και το καινον σχιζει και τω παλαιω ου συμφωνει επιβλημα το απο του καινου');
INSERT INTO `bible_verses_tr` VALUES ('2000', '42', '5', '37', 'και ουδεις βαλλει οινον νεον εις ασκους παλαιους ει δε μηγε ρηξει ο νεος οινος τους ασκους και αυτος εκχυθησεται και οι ασκοι απολουνται');
INSERT INTO `bible_verses_tr` VALUES ('2001', '42', '5', '38', 'αλλα οινον νεον εις ασκους καινους βλητεον και αμφοτεροι συντηρουνται');
INSERT INTO `bible_verses_tr` VALUES ('2002', '42', '5', '39', 'και ουδεις πιων παλαιον ευθεως θελει νεον λεγει γαρ ο παλαιος χρηστοτερος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2003', '42', '6', '1', 'εγενετο δε εν σαββατω δευτεροπρωτω διαπορευεσθαι αυτον δια των σποριμων και ετιλλον οι μαθηται αυτου τους σταχυας και ησθιον ψωχοντες ταις χερσιν');
INSERT INTO `bible_verses_tr` VALUES ('2004', '42', '6', '2', 'τινες δε των φαρισαιων ειπον αυτοις τι ποιειτε ο ουκ εξεστιν ποιειν εν τοις σαββασιν');
INSERT INTO `bible_verses_tr` VALUES ('2005', '42', '6', '3', 'και αποκριθεις προς αυτους ειπεν ο ιησους ουδε τουτο ανεγνωτε ο εποιησεν δαβιδ οποτε επεινασεν αυτος και οι μετ αυτου οντες');
INSERT INTO `bible_verses_tr` VALUES ('2006', '42', '6', '4', 'ως εισηλθεν εις τον οικον του θεου και τους αρτους της προθεσεως ελαβεν και εφαγεν και εδωκεν και τοις μετ αυτου ους ουκ εξεστιν φαγειν ει μη μονους τους ιερεις');
INSERT INTO `bible_verses_tr` VALUES ('2007', '42', '6', '5', 'και ελεγεν αυτοις οτι κυριος εστιν ο υιος του ανθρωπου και του σαββατου');
INSERT INTO `bible_verses_tr` VALUES ('2008', '42', '6', '6', 'εγενετο δε και εν ετερω σαββατω εισελθειν αυτον εις την συναγωγην και διδασκειν και ην εκει ανθρωπος και η χειρ αυτου η δεξια ην ξηρα');
INSERT INTO `bible_verses_tr` VALUES ('2009', '42', '6', '7', 'παρετηρουν δε αυτον οι γραμματεις και οι φαρισαιοι ει εν τω σαββατω θεραπευσει ινα ευρωσιν κατηγοριαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2010', '42', '6', '8', 'αυτος δε ηδει τους διαλογισμους αυτων και ειπεν τω ανθρωπω τω ξηραν εχοντι την χειρα εγειραι και στηθι εις το μεσον ο δε αναστας εστη');
INSERT INTO `bible_verses_tr` VALUES ('2011', '42', '6', '9', 'ειπεν ουν ο ιησους προς αυτους επερωτησω υμας τι εξεστιν τοις σαββασιν αγαθοποιησαι η κακοποιησαι ψυχην σωσαι η απολεσαι');
INSERT INTO `bible_verses_tr` VALUES ('2012', '42', '6', '10', 'και περιβλεψαμενος παντας αυτους ειπεν τω ανθρωπω εκτεινον την χειρα σου ο δε εποιησεν ουτως και αποκατεσταθη η χειρ αυτου υγιης ως η αλλη');
INSERT INTO `bible_verses_tr` VALUES ('2013', '42', '6', '11', 'αυτοι δε επλησθησαν ανοιας και διελαλουν προς αλληλους τι αν ποιησειαν τω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('2014', '42', '6', '12', 'εγενετο δε εν ταις ημεραις ταυταις εξηλθεν εις το ορος προσευξασθαι και ην διανυκτερευων εν τη προσευχη του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2015', '42', '6', '13', 'και οτε εγενετο ημερα προσεφωνησεν τους μαθητας αυτου και εκλεξαμενος απ αυτων δωδεκα ους και αποστολους ωνομασεν');
INSERT INTO `bible_verses_tr` VALUES ('2016', '42', '6', '14', 'σιμωνα ον και ωνομασεν πετρον και ανδρεαν τον αδελφον αυτου ιακωβον και ιωαννην φιλιππον και βαρθολομαιον');
INSERT INTO `bible_verses_tr` VALUES ('2017', '42', '6', '15', 'ματθαιον και θωμαν ιακωβον τον του αλφαιου και σιμωνα τον καλουμενον ζηλωτην');
INSERT INTO `bible_verses_tr` VALUES ('2018', '42', '6', '16', 'ιουδαν ιακωβου και ιουδαν ισκαριωτην ος και εγενετο προδοτης');
INSERT INTO `bible_verses_tr` VALUES ('2019', '42', '6', '17', 'και καταβας μετ αυτων εστη επι τοπου πεδινου και οχλος μαθητων αυτου και πληθος πολυ του λαου απο πασης της ιουδαιας και ιερουσαλημ και της παραλιου τυρου και σιδωνος οι ηλθον ακουσαι αυτου και ιαθηναι απο των νοσων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2020', '42', '6', '18', 'και οι οχλουμενοι υπο πνευματων ακαθαρτων και εθεραπευοντο');
INSERT INTO `bible_verses_tr` VALUES ('2021', '42', '6', '19', 'και πας ο οχλος εζητει απτεσθαι αυτου οτι δυναμις παρ αυτου εξηρχετο και ιατο παντας');
INSERT INTO `bible_verses_tr` VALUES ('2022', '42', '6', '20', 'και αυτος επαρας τους οφθαλμους αυτου εις τους μαθητας αυτου ελεγεν μακαριοι οι πτωχοι οτι υμετερα εστιν η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2023', '42', '6', '21', 'μακαριοι οι πεινωντες νυν οτι χορτασθησεσθε μακαριοι οι κλαιοντες νυν οτι γελασετε');
INSERT INTO `bible_verses_tr` VALUES ('2024', '42', '6', '22', 'μακαριοι εστε οταν μισησωσιν υμας οι ανθρωποι και οταν αφορισωσιν υμας και ονειδισωσιν και εκβαλωσιν το ονομα υμων ως πονηρον ενεκα του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2025', '42', '6', '23', 'χαιρετε εν εκεινη τη ημερα και σκιρτησατε ιδου γαρ ο μισθος υμων πολυς εν τω ουρανω κατα ταυτα γαρ εποιουν τοις προφηταις οι πατερες αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2026', '42', '6', '24', 'πλην ουαι υμιν τοις πλουσιοις οτι απεχετε την παρακλησιν υμων');
INSERT INTO `bible_verses_tr` VALUES ('2027', '42', '6', '25', 'ουαι υμιν οι εμπεπλησμενοι οτι πεινασετε ουαι υμιν οι γελωντες νυν οτι πενθησετε και κλαυσετε');
INSERT INTO `bible_verses_tr` VALUES ('2028', '42', '6', '26', 'ουαι υμιν οταν καλως υμας ειπωσιν παντες οι ανθρωποι κατα ταυτα γαρ εποιουν τοις ψευδοπροφηταις οι πατερες αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2029', '42', '6', '27', 'αλλ υμιν λεγω τοις ακουουσιν αγαπατε τους εχθρους υμων καλως ποιειτε τοις μισουσιν υμας');
INSERT INTO `bible_verses_tr` VALUES ('2030', '42', '6', '28', 'ευλογειτε τους καταρωμενους υμιν και προσευχεσθε υπερ των επηρεαζοντων υμας');
INSERT INTO `bible_verses_tr` VALUES ('2031', '42', '6', '29', 'τω τυπτοντι σε επι την σιαγονα παρεχε και την αλλην και απο του αιροντος σου το ιματιον και τον χιτωνα μη κωλυσης');
INSERT INTO `bible_verses_tr` VALUES ('2032', '42', '6', '30', 'παντι δε τω αιτουντι σε διδου και απο του αιροντος τα σα μη απαιτει');
INSERT INTO `bible_verses_tr` VALUES ('2033', '42', '6', '31', 'και καθως θελετε ινα ποιωσιν υμιν οι ανθρωποι και υμεις ποιειτε αυτοις ομοιως');
INSERT INTO `bible_verses_tr` VALUES ('2034', '42', '6', '32', 'και ει αγαπατε τους αγαπωντας υμας ποια υμιν χαρις εστιν και γαρ οι αμαρτωλοι τους αγαπωντας αυτους αγαπωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2035', '42', '6', '33', 'και εαν αγαθοποιητε τους αγαθοποιουντας υμας ποια υμιν χαρις εστιν και γαρ οι αμαρτωλοι το αυτο ποιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2036', '42', '6', '34', 'και εαν δανειζητε παρ ων  {VAR1: ελπιζετε } {VAR2: ελπιζητε } απολαβειν ποια υμιν χαρις εστιν και γαρ οι αμαρτωλοι αμαρτωλοις δανειζουσιν ινα απολαβωσιν τα ισα');
INSERT INTO `bible_verses_tr` VALUES ('2037', '42', '6', '35', 'πλην αγαπατε τους εχθρους υμων και αγαθοποιειτε και δανειζετε μηδεν απελπιζοντες και εσται ο μισθος υμων πολυς και εσεσθε υιοι του υψιστου οτι αυτος χρηστος εστιν επι τους αχαριστους και πονηρους');
INSERT INTO `bible_verses_tr` VALUES ('2038', '42', '6', '36', 'γινεσθε ουν οικτιρμονες καθως και ο πατηρ υμων οικτιρμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2039', '42', '6', '37', '{VAR1: και } μη κρινετε και ου μη κριθητε μη καταδικαζετε και ου μη καταδικασθητε απολυετε και απολυθησεσθε');
INSERT INTO `bible_verses_tr` VALUES ('2040', '42', '6', '38', 'διδοτε και δοθησεται υμιν μετρον καλον πεπιεσμενον και σεσαλευμενον και υπερεκχυνομενον δωσουσιν εις τον κολπον υμων τω γαρ αυτω μετρω ω μετρειτε αντιμετρηθησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2041', '42', '6', '39', 'ειπεν δε παραβολην αυτοις μητι δυναται τυφλος τυφλον οδηγειν ουχι αμφοτεροι εις βοθυνον πεσουνται');
INSERT INTO `bible_verses_tr` VALUES ('2042', '42', '6', '40', 'ουκ εστιν μαθητης υπερ τον διδασκαλον αυτου κατηρτισμενος δε πας εσται ως ο διδασκαλος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2043', '42', '6', '41', 'τι δε βλεπεις το καρφος το εν τω οφθαλμω του αδελφου σου την δε δοκον την εν τω ιδιω οφθαλμω ου κατανοεις');
INSERT INTO `bible_verses_tr` VALUES ('2044', '42', '6', '42', 'η πως δυνασαι λεγειν τω αδελφω σου αδελφε αφες εκβαλω το καρφος το εν τω οφθαλμω σου αυτος την εν τω οφθαλμω σου δοκον ου βλεπων υποκριτα εκβαλε πρωτον την δοκον εκ του οφθαλμου σου και τοτε διαβλεψεις εκβαλειν το καρφος το εν τω οφθαλμω του αδελφου σου');
INSERT INTO `bible_verses_tr` VALUES ('2045', '42', '6', '43', 'ου γαρ εστιν δενδρον καλον ποιουν καρπον σαπρον ουδε δενδρον σαπρον ποιουν καρπον καλον');
INSERT INTO `bible_verses_tr` VALUES ('2046', '42', '6', '44', 'εκαστον γαρ δενδρον εκ του ιδιου καρπου γινωσκεται ου γαρ εξ ακανθων συλλεγουσιν συκα ουδε εκ βατου τρυγωσιν σταφυλην');
INSERT INTO `bible_verses_tr` VALUES ('2047', '42', '6', '45', 'ο αγαθος ανθρωπος εκ του αγαθου θησαυρου της καρδιας αυτου προφερει το αγαθον και ο πονηρος ανθρωπος εκ του πονηρου θησαυρου της καρδιας αυτου προφερει το πονηρον εκ γαρ του περισσευματος της καρδιας λαλει το στομα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2048', '42', '6', '46', 'τι δε με καλειτε κυριε κυριε και ου ποιειτε α λεγω');
INSERT INTO `bible_verses_tr` VALUES ('2049', '42', '6', '47', 'πας ο ερχομενος προς με και ακουων μου των λογων και ποιων αυτους υποδειξω υμιν τινι εστιν ομοιος');
INSERT INTO `bible_verses_tr` VALUES ('2050', '42', '6', '48', 'ομοιος εστιν ανθρωπω οικοδομουντι οικιαν ος εσκαψεν και εβαθυνεν και εθηκεν θεμελιον επι την πετραν πλημμυρας δε γενομενης προσερρηξεν ο ποταμος τη οικια εκεινη και ουκ ισχυσεν σαλευσαι αυτην τεθεμελιωτο γαρ επι την πετραν');
INSERT INTO `bible_verses_tr` VALUES ('2051', '42', '6', '49', 'ο δε ακουσας και μη ποιησας ομοιος εστιν ανθρωπω οικοδομησαντι οικιαν επι την γην χωρις θεμελιου η προσερρηξεν ο ποταμος και ευθεως επεσεν και εγενετο το ρηγμα της οικιας εκεινης μεγα');
INSERT INTO `bible_verses_tr` VALUES ('2052', '42', '7', '1', 'επει δε επληρωσεν παντα τα ρηματα αυτου εις τας ακοας του λαου εισηλθεν εις καπερναουμ');
INSERT INTO `bible_verses_tr` VALUES ('2053', '42', '7', '2', 'εκατονταρχου δε τινος δουλος κακως εχων ημελλεν τελευταν ος ην αυτω εντιμος');
INSERT INTO `bible_verses_tr` VALUES ('2054', '42', '7', '3', 'ακουσας δε περι του ιησου απεστειλεν προς αυτον πρεσβυτερους των ιουδαιων ερωτων αυτον οπως ελθων διασωση τον δουλον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2055', '42', '7', '4', 'οι δε παραγενομενοι προς τον ιησουν παρεκαλουν αυτον σπουδαιως λεγοντες οτι αξιος εστιν ω παρεξει τουτο');
INSERT INTO `bible_verses_tr` VALUES ('2056', '42', '7', '5', 'αγαπα γαρ το εθνος ημων και την συναγωγην αυτος ωκοδομησεν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('2057', '42', '7', '6', 'ο δε ιησους επορευετο συν αυτοις ηδη δε αυτου ου μακραν απεχοντος απο της οικιας επεμψεν προς αυτον ο εκατονταρχος φιλους λεγων αυτω κυριε μη σκυλλου ου γαρ ειμι ικανος ινα υπο την στεγην μου εισελθης');
INSERT INTO `bible_verses_tr` VALUES ('2058', '42', '7', '7', 'διο ουδε εμαυτον ηξιωσα προς σε ελθειν αλλα ειπε λογω και ιαθησεται ο παις μου');
INSERT INTO `bible_verses_tr` VALUES ('2059', '42', '7', '8', 'και γαρ εγω ανθρωπος ειμι υπο εξουσιαν τασσομενος εχων υπ εμαυτον στρατιωτας και λεγω τουτω πορευθητι και πορευεται και αλλω ερχου και ερχεται και τω δουλω μου ποιησον τουτο και ποιει');
INSERT INTO `bible_verses_tr` VALUES ('2060', '42', '7', '9', 'ακουσας δε ταυτα ο ιησους εθαυμασεν αυτον και στραφεις τω ακολουθουντι αυτω οχλω ειπεν λεγω υμιν ουδε εν τω ισραηλ τοσαυτην πιστιν ευρον');
INSERT INTO `bible_verses_tr` VALUES ('2061', '42', '7', '10', 'και υποστρεψαντες οι πεμφθεντες εις τον οικον ευρον τον ασθενουντα δουλον υγιαινοντα');
INSERT INTO `bible_verses_tr` VALUES ('2062', '42', '7', '11', 'και εγενετο εν τη εξης επορευετο εις πολιν καλουμενην ναιν και συνεπορευοντο αυτω οι μαθηται αυτου ικανοι και οχλος πολυς');
INSERT INTO `bible_verses_tr` VALUES ('2063', '42', '7', '12', 'ως δε ηγγισεν τη πυλη της πολεως και ιδου εξεκομιζετο τεθνηκως υιος μονογενης τη μητρι αυτου και αυτη ην χηρα και οχλος της πολεως ικανος  {VAR2: ην } συν αυτη');
INSERT INTO `bible_verses_tr` VALUES ('2064', '42', '7', '13', 'και ιδων αυτην ο κυριος εσπλαγχνισθη επ αυτη και ειπεν αυτη μη κλαιε');
INSERT INTO `bible_verses_tr` VALUES ('2065', '42', '7', '14', 'και προσελθων ηψατο της σορου οι δε βασταζοντες εστησαν και ειπεν νεανισκε σοι λεγω εγερθητι');
INSERT INTO `bible_verses_tr` VALUES ('2066', '42', '7', '15', 'και ανεκαθισεν ο νεκρος και ηρξατο λαλειν και εδωκεν αυτον τη μητρι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2067', '42', '7', '16', 'ελαβεν δε φοβος απαντας και εδοξαζον τον θεον λεγοντες οτι προφητης μεγας εγηγερται εν ημιν και οτι επεσκεψατο ο θεος τον λαον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2068', '42', '7', '17', 'και εξηλθεν ο λογος ουτος εν ολη τη ιουδαια περι αυτου και εν παση τη περιχωρω');
INSERT INTO `bible_verses_tr` VALUES ('2069', '42', '7', '18', 'και απηγγειλαν ιωαννη οι μαθηται αυτου περι παντων τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2070', '42', '7', '19', 'και προσκαλεσαμενος δυο τινας των μαθητων αυτου ο ιωαννης επεμψεν προς τον ιησουν λεγων συ ει ο ερχομενος η αλλον προσδοκωμεν');
INSERT INTO `bible_verses_tr` VALUES ('2071', '42', '7', '20', 'παραγενομενοι δε προς αυτον οι ανδρες ειπον ιωαννης ο βαπτιστης απεσταλκεν ημας προς σε λεγων συ ει ο ερχομενος η αλλον προσδοκωμεν');
INSERT INTO `bible_verses_tr` VALUES ('2072', '42', '7', '21', 'εν αυτη δε τη ωρα εθεραπευσεν πολλους απο νοσων και μαστιγων και πνευματων πονηρων και τυφλοις πολλοις εχαρισατο το βλεπειν');
INSERT INTO `bible_verses_tr` VALUES ('2073', '42', '7', '22', 'και αποκριθεις ο ιησους ειπεν αυτοις πορευθεντες απαγγειλατε ιωαννη α ειδετε και ηκουσατε οτι τυφλοι αναβλεπουσιν χωλοι περιπατουσιν λεπροι καθαριζονται κωφοι ακουουσιν νεκροι εγειρονται πτωχοι ευαγγελιζονται');
INSERT INTO `bible_verses_tr` VALUES ('2074', '42', '7', '23', 'και μακαριος εστιν ος εαν μη σκανδαλισθη εν εμοι');
INSERT INTO `bible_verses_tr` VALUES ('2075', '42', '7', '24', 'απελθοντων δε των αγγελων ιωαννου ηρξατο λεγειν προς τους οχλους περι ιωαννου τι εξεληλυθατε εις την ερημον θεασασθαι καλαμον υπο ανεμου σαλευομενον');
INSERT INTO `bible_verses_tr` VALUES ('2076', '42', '7', '25', 'αλλα τι εξεληλυθατε ιδειν ανθρωπον εν μαλακοις ιματιοις ημφιεσμενον ιδου οι εν ιματισμω ενδοξω και τρυφη υπαρχοντες εν τοις βασιλειοις εισιν');
INSERT INTO `bible_verses_tr` VALUES ('2077', '42', '7', '26', 'αλλα τι εξεληλυθατε ιδειν προφητην ναι λεγω υμιν και περισσοτερον προφητου');
INSERT INTO `bible_verses_tr` VALUES ('2078', '42', '7', '27', 'ουτος εστιν περι ου γεγραπται ιδου εγω αποστελλω τον αγγελον μου προ προσωπου σου ος κατασκευασει την οδον σου εμπροσθεν σου');
INSERT INTO `bible_verses_tr` VALUES ('2079', '42', '7', '28', 'λεγω γαρ υμιν μειζων εν γεννητοις γυναικων προφητης ιωαννου του βαπτιστου ουδεις εστιν ο δε μικροτερος εν τη βασιλεια του θεου μειζων αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2080', '42', '7', '29', 'και πας ο λαος ακουσας και οι τελωναι εδικαιωσαν τον θεον βαπτισθεντες το βαπτισμα ιωαννου');
INSERT INTO `bible_verses_tr` VALUES ('2081', '42', '7', '30', 'οι δε φαρισαιοι και οι νομικοι την βουλην του θεου ηθετησαν εις εαυτους μη βαπτισθεντες υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2082', '42', '7', '31', 'ειπεν δε ο κυριος τινι ουν ομοιωσω τους ανθρωπους της γενεας ταυτης και τινι εισιν ομοιοι');
INSERT INTO `bible_verses_tr` VALUES ('2083', '42', '7', '32', 'ομοιοι εισιν παιδιοις τοις εν αγορα καθημενοις και προσφωνουσιν αλληλοις και λεγουσιν ηυλησαμεν υμιν και ουκ ωρχησασθε εθρηνησαμεν υμιν και ουκ εκλαυσατε');
INSERT INTO `bible_verses_tr` VALUES ('2084', '42', '7', '33', 'εληλυθεν γαρ ιωαννης ο βαπτιστης μητε αρτον εσθιων μητε οινον πινων και λεγετε δαιμονιον εχει');
INSERT INTO `bible_verses_tr` VALUES ('2085', '42', '7', '34', 'εληλυθεν ο υιος του ανθρωπου εσθιων και πινων και λεγετε ιδου ανθρωπος φαγος και οινοποτης τελωνων φιλος και αμαρτωλων');
INSERT INTO `bible_verses_tr` VALUES ('2086', '42', '7', '35', 'και εδικαιωθη η σοφια απο των τεκνων αυτης παντων');
INSERT INTO `bible_verses_tr` VALUES ('2087', '42', '7', '36', 'ηρωτα δε τις αυτον των φαρισαιων ινα φαγη μετ αυτου και εισελθων εις την οικιαν του φαρισαιου ανεκλιθη');
INSERT INTO `bible_verses_tr` VALUES ('2088', '42', '7', '37', 'και ιδου γυνη εν τη πολει ητις ην αμαρτωλος επιγνουσα οτι ανακειται εν τη οικια του φαρισαιου κομισασα αλαβαστρον μυρου');
INSERT INTO `bible_verses_tr` VALUES ('2089', '42', '7', '38', 'και στασα παρα τους ποδας αυτου οπισω κλαιουσα ηρξατο βρεχειν τους ποδας αυτου τοις δακρυσιν και ταις θριξιν της κεφαλης αυτης εξεμασσεν και κατεφιλει τους ποδας αυτου και ηλειφεν τω μυρω');
INSERT INTO `bible_verses_tr` VALUES ('2090', '42', '7', '39', 'ιδων δε ο φαρισαιος ο καλεσας αυτον ειπεν εν εαυτω λεγων ουτος ει ην προφητης εγινωσκεν αν τις και ποταπη η γυνη ητις απτεται αυτου οτι αμαρτωλος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2091', '42', '7', '40', 'και αποκριθεις ο ιησους ειπεν προς αυτον σιμων εχω σοι τι ειπειν ο δε φησιν διδασκαλε ειπε');
INSERT INTO `bible_verses_tr` VALUES ('2092', '42', '7', '41', 'δυο χρεωφειλεται ησαν δανειστη τινι ο εις ωφειλεν δηναρια πεντακοσια ο δε ετερος πεντηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('2093', '42', '7', '42', 'μη εχοντων δε αυτων αποδουναι αμφοτεροις εχαρισατο τις ουν αυτων ειπε πλειον αυτον αγαπησει');
INSERT INTO `bible_verses_tr` VALUES ('2094', '42', '7', '43', 'αποκριθεις δε ο σιμων ειπεν υπολαμβανω οτι ω το πλειον εχαρισατο ο δε ειπεν αυτω ορθως εκρινας');
INSERT INTO `bible_verses_tr` VALUES ('2095', '42', '7', '44', 'και στραφεις προς την γυναικα τω σιμωνι εφη βλεπεις ταυτην την γυναικα εισηλθον σου εις την οικιαν υδωρ επι τους ποδας μου ουκ εδωκας αυτη δε τοις δακρυσιν εβρεξεν μου τους ποδας και ταις θριξιν της κεφαλης αυτης εξεμαξεν');
INSERT INTO `bible_verses_tr` VALUES ('2096', '42', '7', '45', 'φιλημα μοι ουκ εδωκας αυτη δε αφ ης εισηλθον ου διελιπεν καταφιλουσα μου τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('2097', '42', '7', '46', 'ελαιω την κεφαλην μου ουκ ηλειψας αυτη δε μυρω ηλειψεν μου τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('2098', '42', '7', '47', 'ου χαριν λεγω σοι αφεωνται αι αμαρτιαι αυτης αι πολλαι οτι ηγαπησεν πολυ ω δε ολιγον αφιεται ολιγον αγαπα');
INSERT INTO `bible_verses_tr` VALUES ('2099', '42', '7', '48', 'ειπεν δε αυτη αφεωνται σου αι αμαρτιαι');
INSERT INTO `bible_verses_tr` VALUES ('2100', '42', '7', '49', 'και ηρξαντο οι συνανακειμενοι λεγειν εν εαυτοις τις ουτος εστιν ος και αμαρτιας αφιησιν');
INSERT INTO `bible_verses_tr` VALUES ('2101', '42', '7', '50', 'ειπεν δε προς την γυναικα η πιστις σου σεσωκεν σε πορευου εις ειρηνην');
INSERT INTO `bible_verses_tr` VALUES ('2102', '42', '8', '1', 'και εγενετο εν τω καθεξης και αυτος διωδευεν κατα πολιν και κωμην κηρυσσων και ευαγγελιζομενος την βασιλειαν του θεου και οι δωδεκα συν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2103', '42', '8', '2', 'και γυναικες τινες αι ησαν τεθεραπευμεναι απο πνευματων πονηρων και ασθενειων μαρια η καλουμενη μαγδαληνη αφ ης δαιμονια επτα εξεληλυθει');
INSERT INTO `bible_verses_tr` VALUES ('2104', '42', '8', '3', 'και ιωαννα γυνη χουζα επιτροπου ηρωδου και σουσαννα και ετεραι πολλαι αιτινες διηκονουν αυτω απο των υπαρχοντων αυταις');
INSERT INTO `bible_verses_tr` VALUES ('2105', '42', '8', '4', 'συνιοντος δε οχλου πολλου και των κατα πολιν επιπορευομενων προς αυτον ειπεν δια παραβολης');
INSERT INTO `bible_verses_tr` VALUES ('2106', '42', '8', '5', 'εξηλθεν ο σπειρων του σπειραι τον σπορον αυτου και εν τω σπειρειν αυτον ο μεν επεσεν παρα την οδον και κατεπατηθη και τα πετεινα του ουρανου κατεφαγεν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('2107', '42', '8', '6', 'και ετερον επεσεν επι την πετραν και φυεν εξηρανθη δια το μη εχειν ικμαδα');
INSERT INTO `bible_verses_tr` VALUES ('2108', '42', '8', '7', 'και ετερον επεσεν εν μεσω των ακανθων και συμφυεισαι αι ακανθαι απεπνιξαν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('2109', '42', '8', '8', 'και ετερον επεσεν επι την γην την αγαθην και φυεν εποιησεν καρπον εκατονταπλασιονα ταυτα λεγων εφωνει ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('2110', '42', '8', '9', 'επηρωτων δε αυτον οι μαθηται αυτου λεγοντες τις ειη η παραβολη αυτη');
INSERT INTO `bible_verses_tr` VALUES ('2111', '42', '8', '10', 'ο δε ειπεν υμιν δεδοται γνωναι τα μυστηρια της βασιλειας του θεου τοις δε λοιποις εν παραβολαις ινα βλεποντες μη βλεπωσιν και ακουοντες μη συνιωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2112', '42', '8', '11', 'εστιν δε αυτη η παραβολη ο σπορος εστιν ο λογος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2113', '42', '8', '12', 'οι δε παρα την οδον εισιν οι ακουοντες ειτα ερχεται ο διαβολος και αιρει τον λογον απο της καρδιας αυτων ινα μη πιστευσαντες σωθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2114', '42', '8', '13', 'οι δε επι της πετρας οι οταν ακουσωσιν μετα χαρας δεχονται τον λογον και ουτοι ριζαν ουκ εχουσιν οι προς καιρον πιστευουσιν και εν καιρω πειρασμου αφιστανται');
INSERT INTO `bible_verses_tr` VALUES ('2115', '42', '8', '14', 'το δε εις τας ακανθας πεσον ουτοι εισιν οι ακουσαντες και υπο μεριμνων και πλουτου και ηδονων του βιου πορευομενοι συμπνιγονται και ου τελεσφορουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2116', '42', '8', '15', 'το δε εν τη καλη γη ουτοι εισιν οιτινες εν καρδια καλη και αγαθη ακουσαντες τον λογον κατεχουσιν και καρποφορουσιν εν υπομονη');
INSERT INTO `bible_verses_tr` VALUES ('2117', '42', '8', '16', 'ουδεις δε λυχνον αψας καλυπτει αυτον σκευει η υποκατω κλινης τιθησιν αλλ επι λυχνιας επιτιθησιν ινα οι εισπορευομενοι βλεπωσιν το φως');
INSERT INTO `bible_verses_tr` VALUES ('2118', '42', '8', '17', 'ου γαρ εστιν κρυπτον ο ου φανερον γενησεται ουδε αποκρυφον ο ου γνωσθησεται και εις φανερον ελθη');
INSERT INTO `bible_verses_tr` VALUES ('2119', '42', '8', '18', 'βλεπετε ουν πως ακουετε ος γαρ αν εχη δοθησεται αυτω και ος αν μη εχη και ο δοκει εχειν αρθησεται απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2120', '42', '8', '19', 'παρεγενοντο δε προς αυτον η μητηρ και οι αδελφοι αυτου και ουκ ηδυναντο συντυχειν αυτω δια τον οχλον');
INSERT INTO `bible_verses_tr` VALUES ('2121', '42', '8', '20', 'και απηγγελη αυτω λεγοντων η μητηρ σου και οι αδελφοι σου εστηκασιν εξω ιδειν σε θελοντες');
INSERT INTO `bible_verses_tr` VALUES ('2122', '42', '8', '21', 'ο δε αποκριθεις ειπεν προς αυτους μητηρ μου και αδελφοι μου ουτοι εισιν οι τον λογον του θεου ακουοντες και ποιουντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2123', '42', '8', '22', 'και εγενετο εν μια των ημερων και αυτος ενεβη εις πλοιον και οι μαθηται αυτου και ειπεν προς αυτους διελθωμεν εις το περαν της λιμνης και ανηχθησαν');
INSERT INTO `bible_verses_tr` VALUES ('2124', '42', '8', '23', 'πλεοντων δε αυτων αφυπνωσεν και κατεβη λαιλαψ ανεμου εις την λιμνην και συνεπληρουντο και εκινδυνευον');
INSERT INTO `bible_verses_tr` VALUES ('2125', '42', '8', '24', 'προσελθοντες δε διηγειραν αυτον λεγοντες επιστατα επιστατα απολλυμεθα ο δε εγερθεις επετιμησεν τω ανεμω και τω κλυδωνι του υδατος και επαυσαντο και εγενετο γαληνη');
INSERT INTO `bible_verses_tr` VALUES ('2126', '42', '8', '25', 'ειπεν δε αυτοις που εστιν η πιστις υμων φοβηθεντες δε εθαυμασαν λεγοντες προς αλληλους τις αρα ουτος εστιν οτι και τοις ανεμοις επιτασσει και τω υδατι και υπακουουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2127', '42', '8', '26', 'και κατεπλευσαν εις την χωραν των γαδαρηνων ητις εστιν αντιπεραν της γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('2128', '42', '8', '27', 'εξελθοντι δε αυτω επι την γην υπηντησεν αυτω ανηρ τις εκ της πολεως ος ειχεν δαιμονια εκ χρονων ικανων και ιματιον ουκ ενεδιδυσκετο και εν οικια ουκ εμενεν αλλ εν τοις μνημασιν');
INSERT INTO `bible_verses_tr` VALUES ('2129', '42', '8', '28', 'ιδων δε τον ιησουν και ανακραξας προσεπεσεν αυτω και φωνη μεγαλη ειπεν τι εμοι και σοι ιησου υιε του θεου του υψιστου δεομαι σου μη με βασανισης');
INSERT INTO `bible_verses_tr` VALUES ('2130', '42', '8', '29', '{VAR1: παρηγγελλεν } {VAR2: παρηγγειλεν } γαρ τω πνευματι τω ακαθαρτω εξελθειν απο του ανθρωπου πολλοις γαρ χρονοις συνηρπακει αυτον και εδεσμειτο αλυσεσιν και πεδαις φυλασσομενος και διαρρησσων τα δεσμα ηλαυνετο υπο του δαιμονος εις τας ερημους');
INSERT INTO `bible_verses_tr` VALUES ('2131', '42', '8', '30', 'επηρωτησεν δε αυτον ο ιησους λεγων τι σοι εστιν ονομα ο δε ειπεν λεγεων οτι δαιμονια πολλα εισηλθεν εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2132', '42', '8', '31', 'και  {VAR1: παρεκαλει } {VAR2: παρεκαλουν } αυτον ινα μη επιταξη αυτοις εις την αβυσσον απελθειν');
INSERT INTO `bible_verses_tr` VALUES ('2133', '42', '8', '32', 'ην δε εκει αγελη χοιρων ικανων βοσκομενων εν τω ορει και παρεκαλουν αυτον ινα επιτρεψη αυτοις εις εκεινους εισελθειν και επετρεψεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2134', '42', '8', '33', 'εξελθοντα δε τα δαιμονια απο του ανθρωπου εισηλθεν εις τους χοιρους και ωρμησεν η αγελη κατα του κρημνου εις την λιμνην και απεπνιγη');
INSERT INTO `bible_verses_tr` VALUES ('2135', '42', '8', '34', 'ιδοντες δε οι βοσκοντες το γεγενημενον εφυγον και απελθοντες απηγγειλαν εις την πολιν και εις τους αγρους');
INSERT INTO `bible_verses_tr` VALUES ('2136', '42', '8', '35', 'εξηλθον δε ιδειν το γεγονος και ηλθον προς τον ιησουν και ευρον καθημενον τον ανθρωπον αφ ου τα δαιμονια εξεληλυθει ιματισμενον και σωφρονουντα παρα τους ποδας του ιησου και εφοβηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('2137', '42', '8', '36', 'απηγγειλαν δε αυτοις και οι ιδοντες πως εσωθη ο δαιμονισθεις');
INSERT INTO `bible_verses_tr` VALUES ('2138', '42', '8', '37', 'και ηρωτησαν αυτον απαν το πληθος της περιχωρου των γαδαρηνων απελθειν απ αυτων οτι φοβω μεγαλω συνειχοντο αυτος δε εμβας εις το πλοιον υπεστρεψεν');
INSERT INTO `bible_verses_tr` VALUES ('2139', '42', '8', '38', 'εδεετο δε αυτου ο ανηρ αφ ου εξεληλυθει τα δαιμονια ειναι συν αυτω απελυσεν δε αυτον ο ιησους λεγων');
INSERT INTO `bible_verses_tr` VALUES ('2140', '42', '8', '39', 'υποστρεφε εις τον οικον σου και διηγου οσα εποιησεν σοι ο θεος και απηλθεν καθ ολην την πολιν κηρυσσων οσα εποιησεν αυτω ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('2141', '42', '8', '40', 'εγενετο δε εν τω υποστρεψαι τον ιησουν απεδεξατο αυτον ο οχλος ησαν γαρ παντες προσδοκωντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2142', '42', '8', '41', 'και ιδου ηλθεν ανηρ ω ονομα ιαειρος και αυτος αρχων της συναγωγης υπηρχεν και πεσων παρα τους ποδας του ιησου παρεκαλει αυτον εισελθειν εις τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2143', '42', '8', '42', 'οτι θυγατηρ μονογενης ην αυτω ως ετων δωδεκα και αυτη απεθνησκεν εν δε τω υπαγειν αυτον οι οχλοι συνεπνιγον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2144', '42', '8', '43', 'και γυνη ουσα εν ρυσει αιματος απο ετων δωδεκα ητις εις ιατρους προσαναλωσασα ολον τον βιον ουκ ισχυσεν υπ ουδενος θεραπευθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2145', '42', '8', '44', 'προσελθουσα οπισθεν ηψατο του κρασπεδου του ιματιου αυτου και παραχρημα εστη η ρυσις του αιματος αυτης');
INSERT INTO `bible_verses_tr` VALUES ('2146', '42', '8', '45', 'και ειπεν ο ιησους τις ο αψαμενος μου αρνουμενων δε παντων ειπεν ο πετρος και οι μετ αυτου επιστατα οι οχλοι συνεχουσιν σε και αποθλιβουσιν και λεγεις τις ο αψαμενος μου');
INSERT INTO `bible_verses_tr` VALUES ('2147', '42', '8', '46', 'ο δε ιησους ειπεν ηψατο μου τις εγω γαρ εγνων δυναμιν εξελθουσαν απ εμου');
INSERT INTO `bible_verses_tr` VALUES ('2148', '42', '8', '47', 'ιδουσα δε η γυνη οτι ουκ ελαθεν τρεμουσα ηλθεν και προσπεσουσα αυτω δι ην αιτιαν ηψατο αυτου απηγγειλεν αυτω ενωπιον παντος του λαου και ως ιαθη παραχρημα');
INSERT INTO `bible_verses_tr` VALUES ('2149', '42', '8', '48', 'ο δε ειπεν αυτη θαρσει θυγατερ η πιστις σου σεσωκεν σε πορευου εις ειρηνην');
INSERT INTO `bible_verses_tr` VALUES ('2150', '42', '8', '49', 'ετι αυτου λαλουντος ερχεται τις παρα του αρχισυναγωγου λεγων αυτω οτι τεθνηκεν η θυγατηρ σου μη σκυλλε τον διδασκαλον');
INSERT INTO `bible_verses_tr` VALUES ('2151', '42', '8', '50', 'ο δε ιησους ακουσας απεκριθη αυτω λεγων μη φοβου μονον πιστευε και σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2152', '42', '8', '51', 'εισελθων δε εις την οικιαν ουκ αφηκεν εισελθειν ουδενα ει μη πετρον και ιακωβον και ιωαννην και τον πατερα της παιδος και την μητερα');
INSERT INTO `bible_verses_tr` VALUES ('2153', '42', '8', '52', 'εκλαιον δε παντες και εκοπτοντο αυτην ο δε ειπεν μη κλαιετε ουκ απεθανεν αλλα καθευδει');
INSERT INTO `bible_verses_tr` VALUES ('2154', '42', '8', '53', 'και κατεγελων αυτου ειδοτες οτι απεθανεν');
INSERT INTO `bible_verses_tr` VALUES ('2155', '42', '8', '54', 'αυτος δε εκβαλων εξω παντας και κρατησας της χειρος αυτης εφωνησεν λεγων η παις εγειρου');
INSERT INTO `bible_verses_tr` VALUES ('2156', '42', '8', '55', 'και επεστρεψεν το πνευμα αυτης και ανεστη παραχρημα και διεταξεν αυτη δοθηναι φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('2157', '42', '8', '56', 'και εξεστησαν οι γονεις αυτης ο δε παρηγγειλεν αυτοις μηδενι ειπειν το γεγονος');
INSERT INTO `bible_verses_tr` VALUES ('2158', '42', '9', '1', 'συγκαλεσαμενος δε τους δωδεκα μαθητας αυτου εδωκεν αυτοις δυναμιν και εξουσιαν επι παντα τα δαιμονια και νοσους θεραπευειν');
INSERT INTO `bible_verses_tr` VALUES ('2159', '42', '9', '2', 'και απεστειλεν αυτους κηρυσσειν την βασιλειαν του θεου και ιασθαι τους ασθενουντας');
INSERT INTO `bible_verses_tr` VALUES ('2160', '42', '9', '3', 'και ειπεν προς αυτους μηδεν αιρετε εις την οδον μητε ραβδους μητε πηραν μητε αρτον μητε αργυριον μητε ανα δυο χιτωνας εχειν');
INSERT INTO `bible_verses_tr` VALUES ('2161', '42', '9', '4', 'και εις ην αν οικιαν εισελθητε εκει μενετε και εκειθεν εξερχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('2162', '42', '9', '5', 'και οσοι αν μη δεξωνται υμας εξερχομενοι απο της πολεως εκεινης και τον κονιορτον απο των ποδων υμων αποτιναξατε εις μαρτυριον επ αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2163', '42', '9', '6', 'εξερχομενοι δε διηρχοντο κατα τας κωμας ευαγγελιζομενοι και θεραπευοντες πανταχου');
INSERT INTO `bible_verses_tr` VALUES ('2164', '42', '9', '7', 'ηκουσεν δε ηρωδης ο τετραρχης τα γινομενα υπ αυτου παντα και διηπορει δια το λεγεσθαι υπο τινων οτι ιωαννης εγηγερται εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('2165', '42', '9', '8', 'υπο τινων δε οτι ηλιας εφανη αλλων δε οτι προφητης εις των αρχαιων ανεστη');
INSERT INTO `bible_verses_tr` VALUES ('2166', '42', '9', '9', 'και ειπεν ο ηρωδης ιωαννην εγω απεκεφαλισα τις δε εστιν ουτος περι ου εγω ακουω τοιαυτα και εζητει ιδειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2167', '42', '9', '10', 'και υποστρεψαντες οι αποστολοι διηγησαντο αυτω οσα εποιησαν και παραλαβων αυτους υπεχωρησεν κατ ιδιαν εις τοπον ερημον πολεως καλουμενης βηθσαιδα');
INSERT INTO `bible_verses_tr` VALUES ('2168', '42', '9', '11', 'οι δε οχλοι γνοντες ηκολουθησαν αυτω και δεξαμενος αυτους ελαλει αυτοις περι της βασιλειας του θεου και τους χρειαν εχοντας θεραπειας ιατο');
INSERT INTO `bible_verses_tr` VALUES ('2169', '42', '9', '12', 'η δε ημερα ηρξατο κλινειν προσελθοντες δε οι δωδεκα ειπον αυτω απολυσον τον οχλον ινα απελθοντες εις τας κυκλω κωμας και τους αγρους καταλυσωσιν και ευρωσιν επισιτισμον οτι ωδε εν ερημω τοπω εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('2170', '42', '9', '13', 'ειπεν δε προς αυτους δοτε αυτοις υμεις φαγειν οι δε ειπον ουκ εισιν ημιν πλειον η πεντε αρτοι και δυο ιχθυες ει μητι πορευθεντες ημεις αγορασωμεν εις παντα τον λαον τουτον βρωματα');
INSERT INTO `bible_verses_tr` VALUES ('2171', '42', '9', '14', 'ησαν γαρ ωσει ανδρες πεντακισχιλιοι ειπεν δε προς τους μαθητας αυτου κατακλινατε αυτους κλισιας ανα πεντηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('2172', '42', '9', '15', 'και εποιησαν ουτως και ανεκλιναν απαντας');
INSERT INTO `bible_verses_tr` VALUES ('2173', '42', '9', '16', 'λαβων δε τους πεντε αρτους και τους δυο ιχθυας αναβλεψας εις τον ουρανον ευλογησεν αυτους και κατεκλασεν και εδιδου τοις μαθηταις παρατιθεναι τω οχλω');
INSERT INTO `bible_verses_tr` VALUES ('2174', '42', '9', '17', 'και εφαγον και εχορτασθησαν παντες και ηρθη το περισσευσαν αυτοις κλασματων κοφινοι δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('2175', '42', '9', '18', 'και εγενετο εν τω ειναι αυτον προσευχομενον καταμονας συνησαν αυτω οι μαθηται και επηρωτησεν αυτους λεγων τινα με λεγουσιν οι οχλοι ειναι');
INSERT INTO `bible_verses_tr` VALUES ('2176', '42', '9', '19', 'οι δε αποκριθεντες ειπον ιωαννην τον βαπτιστην αλλοι δε ηλιαν αλλοι δε οτι προφητης τις των αρχαιων ανεστη');
INSERT INTO `bible_verses_tr` VALUES ('2177', '42', '9', '20', 'ειπεν δε αυτοις υμεις δε τινα με λεγετε ειναι αποκριθεις δε ο πετρος ειπεν τον χριστον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2178', '42', '9', '21', 'ο δε επιτιμησας αυτοις παρηγγειλεν μηδενι ειπειν τουτο');
INSERT INTO `bible_verses_tr` VALUES ('2179', '42', '9', '22', 'ειπων οτι δει τον υιον του ανθρωπου πολλα παθειν και αποδοκιμασθηναι απο των πρεσβυτερων και αρχιερεων και γραμματεων και αποκτανθηναι και τη τριτη ημερα εγερθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2180', '42', '9', '23', 'ελεγεν δε προς παντας ει τις θελει οπισω μου ελθειν απαρνησασθω εαυτον και αρατω τον σταυρον αυτου καθ ημεραν και ακολουθειτω μοι');
INSERT INTO `bible_verses_tr` VALUES ('2181', '42', '9', '24', 'ος γαρ αν θελη την ψυχην αυτου σωσαι απολεσει αυτην ος δ αν απολεση την ψυχην αυτου ενεκεν εμου ουτος σωσει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2182', '42', '9', '25', 'τι γαρ ωφελειται ανθρωπος κερδησας τον κοσμον ολον εαυτον δε απολεσας η ζημιωθεις');
INSERT INTO `bible_verses_tr` VALUES ('2183', '42', '9', '26', 'ος γαρ αν επαισχυνθη με και τους εμους λογους τουτον ο υιος του ανθρωπου επαισχυνθησεται οταν ελθη εν τη δοξη αυτου και του πατρος και των αγιων αγγελων');
INSERT INTO `bible_verses_tr` VALUES ('2184', '42', '9', '27', 'λεγω δε υμιν αληθως εισιν τινες των ωδε εστηκοτων οι ου μη γευσονται θανατου εως αν ιδωσιν την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2185', '42', '9', '28', 'εγενετο δε μετα τους λογους τουτους ωσει ημεραι οκτω και παραλαβων τον πετρον και ιωαννην και ιακωβον ανεβη εις το ορος προσευξασθαι');
INSERT INTO `bible_verses_tr` VALUES ('2186', '42', '9', '29', 'και εγενετο εν τω προσευχεσθαι αυτον το ειδος του προσωπου αυτου ετερον και ο ιματισμος αυτου λευκος εξαστραπτων');
INSERT INTO `bible_verses_tr` VALUES ('2187', '42', '9', '30', 'και ιδου ανδρες δυο συνελαλουν αυτω οιτινες ησαν μωσης και ηλιας');
INSERT INTO `bible_verses_tr` VALUES ('2188', '42', '9', '31', 'οι οφθεντες εν δοξη ελεγον την εξοδον αυτου ην εμελλεν πληρουν εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2189', '42', '9', '32', 'ο δε πετρος και οι συν αυτω ησαν βεβαρημενοι υπνω διαγρηγορησαντες δε ειδον την δοξαν αυτου και τους δυο ανδρας τους συνεστωτας αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2190', '42', '9', '33', 'και εγενετο εν τω διαχωριζεσθαι αυτους απ αυτου ειπεν ο πετρος προς τον ιησουν επιστατα καλον εστιν ημας ωδε ειναι και ποιησωμεν σκηνας τρεις μιαν σοι και μωσει μιαν και μιαν ηλια μη ειδως ο λεγει');
INSERT INTO `bible_verses_tr` VALUES ('2191', '42', '9', '34', 'ταυτα δε αυτου λεγοντος εγενετο νεφελη και επεσκιασεν αυτους εφοβηθησαν δε εν τω εκεινους εισελθειν εις την νεφελην');
INSERT INTO `bible_verses_tr` VALUES ('2192', '42', '9', '35', 'και φωνη εγενετο εκ της νεφελης λεγουσα ουτος εστιν ο υιος μου ο αγαπητος αυτου ακουετε');
INSERT INTO `bible_verses_tr` VALUES ('2193', '42', '9', '36', 'και εν τω γενεσθαι την φωνην ευρεθη ο ιησους μονος και αυτοι εσιγησαν και ουδενι απηγγειλαν εν εκειναις ταις ημεραις ουδεν ων εωρακασιν');
INSERT INTO `bible_verses_tr` VALUES ('2194', '42', '9', '37', 'εγενετο δε εν τη εξης ημερα κατελθοντων αυτων απο του ορους συνηντησεν αυτω οχλος πολυς');
INSERT INTO `bible_verses_tr` VALUES ('2195', '42', '9', '38', 'και ιδου ανηρ απο του οχλου ανεβοησεν λεγων διδασκαλε δεομαι σου επιβλεψον επι τον υιον μου οτι μονογενης εστιν μοι');
INSERT INTO `bible_verses_tr` VALUES ('2196', '42', '9', '39', 'και ιδου πνευμα λαμβανει αυτον και εξαιφνης κραζει και σπαρασσει αυτον μετα αφρου και μογις αποχωρει απ αυτου συντριβον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2197', '42', '9', '40', 'και εδεηθην των μαθητων σου ινα εκβαλλωσιν αυτο και ουκ ηδυνηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('2198', '42', '9', '41', 'αποκριθεις δε ο ιησους ειπεν ω γενεα απιστος και διεστραμμενη εως ποτε εσομαι προς υμας και ανεξομαι υμων προσαγαγε ωδε τον υιον σου');
INSERT INTO `bible_verses_tr` VALUES ('2199', '42', '9', '42', 'ετι δε προσερχομενου αυτου ερρηξεν αυτον το δαιμονιον και συνεσπαραξεν επετιμησεν δε ο ιησους τω πνευματι τω ακαθαρτω και ιασατο τον παιδα και απεδωκεν αυτον τω πατρι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2200', '42', '9', '43', 'εξεπλησσοντο δε παντες επι τη μεγαλειοτητι του θεου παντων δε θαυμαζοντων επι πασιν οις εποιησεν ο ιησους ειπεν προς τους μαθητας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2201', '42', '9', '44', 'θεσθε υμεις εις τα ωτα υμων τους λογους τουτους ο γαρ υιος του ανθρωπου μελλει παραδιδοσθαι εις χειρας ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('2202', '42', '9', '45', 'οι δε ηγνοουν το ρημα τουτο και ην παρακεκαλυμμενον απ αυτων ινα μη αισθωνται αυτο και εφοβουντο ερωτησαι αυτον περι του ρηματος τουτου');
INSERT INTO `bible_verses_tr` VALUES ('2203', '42', '9', '46', 'εισηλθεν δε διαλογισμος εν αυτοις το τις αν ειη μειζων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2204', '42', '9', '47', 'ο δε ιησους ιδων τον διαλογισμον της καρδιας αυτων επιλαβομενος παιδιου εστησεν αυτο παρ εαυτω');
INSERT INTO `bible_verses_tr` VALUES ('2205', '42', '9', '48', 'και ειπεν αυτοις ος εαν δεξηται τουτο το παιδιον επι τω ονοματι μου εμε δεχεται και ος εαν εμε δεξηται δεχεται τον αποστειλαντα με ο γαρ μικροτερος εν πασιν υμιν υπαρχων ουτος εσται μεγας');
INSERT INTO `bible_verses_tr` VALUES ('2206', '42', '9', '49', 'αποκριθεις δε ο ιωαννης ειπεν επιστατα ειδομεν τινα επι τω ονοματι σου εκβαλλοντα τα δαιμονια και εκωλυσαμεν αυτον οτι ουκ ακολουθει μεθ ημων');
INSERT INTO `bible_verses_tr` VALUES ('2207', '42', '9', '50', 'και ειπεν προς αυτον ο ιησους μη κωλυετε ος γαρ ουκ εστιν καθ ημων υπερ ημων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2208', '42', '9', '51', 'εγενετο δε εν τω συμπληρουσθαι τας ημερας της αναληψεως αυτου και αυτος το προσωπον αυτου εστηριξεν του πορευεσθαι εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2209', '42', '9', '52', 'και απεστειλεν αγγελους προ προσωπου αυτου και πορευθεντες εισηλθον εις κωμην σαμαρειτων ωστε ετοιμασαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2210', '42', '9', '53', 'και ουκ εδεξαντο αυτον οτι το προσωπον αυτου ην πορευομενον εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2211', '42', '9', '54', 'ιδοντες δε οι μαθηται αυτου ιακωβος και ιωαννης ειπον κυριε θελεις ειπωμεν πυρ καταβηναι απο του ουρανου και αναλωσαι αυτους ως και ηλιας εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('2212', '42', '9', '55', 'στραφεις δε επετιμησεν αυτοις και ειπεν ουκ οιδατε οιου πνευματος εστε υμεις');
INSERT INTO `bible_verses_tr` VALUES ('2213', '42', '9', '56', 'ο γαρ υιος του ανθρωπου ουκ ηλθεν ψυχας ανθρωπων απολεσαι αλλα σωσαι και επορευθησαν εις ετεραν κωμην');
INSERT INTO `bible_verses_tr` VALUES ('2214', '42', '9', '57', 'εγενετο δε πορευομενων αυτων εν τη οδω ειπεν τις προς αυτον ακολουθησω σοι οπου αν απερχη κυριε');
INSERT INTO `bible_verses_tr` VALUES ('2215', '42', '9', '58', 'και ειπεν αυτω ο ιησους αι αλωπεκες φωλεους εχουσιν και τα πετεινα του ουρανου κατασκηνωσεις ο δε υιος του ανθρωπου ουκ εχει που την κεφαλην κλινη');
INSERT INTO `bible_verses_tr` VALUES ('2216', '42', '9', '59', 'ειπεν δε προς ετερον ακολουθει μοι ο δε ειπεν κυριε επιτρεψον μοι απελθοντι πρωτον θαψαι τον πατερα μου');
INSERT INTO `bible_verses_tr` VALUES ('2217', '42', '9', '60', 'ειπεν δε αυτω ο ιησους αφες τους νεκρους θαψαι τους εαυτων νεκρους συ δε απελθων διαγγελλε την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2218', '42', '9', '61', 'ειπεν δε και ετερος ακολουθησω σοι κυριε πρωτον δε επιτρεψον μοι αποταξασθαι τοις εις τον οικον μου');
INSERT INTO `bible_verses_tr` VALUES ('2219', '42', '9', '62', 'ειπεν δε προς αυτον ο ιησους ουδεις επιβαλων την χειρα αυτου επ αροτρον και βλεπων εις τα οπισω ευθετος εστιν εις την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2220', '42', '10', '1', 'μετα δε ταυτα ανεδειξεν ο κυριος και ετερους εβδομηκοντα και απεστειλεν αυτους ανα δυο προ προσωπου αυτου εις πασαν πολιν και τοπον ου εμελλεν αυτος ερχεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2221', '42', '10', '2', 'ελεγεν ουν προς αυτους ο μεν θερισμος πολυς οι δε εργαται ολιγοι δεηθητε ουν του κυριου του θερισμου οπως εκβαλλη εργατας εις τον θερισμον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2222', '42', '10', '3', 'υπαγετε ιδου εγω αποστελλω υμας ως αρνας εν μεσω λυκων');
INSERT INTO `bible_verses_tr` VALUES ('2223', '42', '10', '4', 'μη βασταζετε βαλαντιον μη πηραν μηδε υποδηματα και μηδενα κατα την οδον ασπασησθε');
INSERT INTO `bible_verses_tr` VALUES ('2224', '42', '10', '5', 'εις ην δ αν οικιαν εισερχησθε πρωτον λεγετε ειρηνη τω οικω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('2225', '42', '10', '6', 'και εαν μεν η εκει  {VAR2: ο } υιος ειρηνης επαναπαυσεται επ αυτον η ειρηνη υμων ει δε μηγε εφ υμας ανακαμψει');
INSERT INTO `bible_verses_tr` VALUES ('2226', '42', '10', '7', 'εν αυτη δε τη οικια μενετε εσθιοντες και πινοντες τα παρ αυτων αξιος γαρ ο εργατης του μισθου αυτου εστιν μη μεταβαινετε εξ οικιας εις οικιαν');
INSERT INTO `bible_verses_tr` VALUES ('2227', '42', '10', '8', 'και εις ην δ αν πολιν εισερχησθε και δεχωνται υμας εσθιετε τα παρατιθεμενα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2228', '42', '10', '9', 'και θεραπευετε τους εν αυτη ασθενεις και λεγετε αυτοις ηγγικεν εφ υμας η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2229', '42', '10', '10', 'εις ην δ αν πολιν εισερχησθε και μη δεχωνται υμας εξελθοντες εις τας πλατειας αυτης ειπατε');
INSERT INTO `bible_verses_tr` VALUES ('2230', '42', '10', '11', 'και τον κονιορτον τον κολληθεντα ημιν εκ της πολεως υμων απομασσομεθα υμιν πλην τουτο γινωσκετε οτι ηγγικεν εφ υμας η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2231', '42', '10', '12', 'λεγω δε υμιν οτι σοδομοις εν τη ημερα εκεινη ανεκτοτερον εσται η τη πολει εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('2232', '42', '10', '13', 'ουαι σοι χωραζιν ουαι σοι βηθσαιδα οτι ει εν τυρω και σιδωνι εγενοντο αι δυναμεις αι γενομεναι εν υμιν παλαι αν εν σακκω και σποδω καθημεναι μετενοησαν');
INSERT INTO `bible_verses_tr` VALUES ('2233', '42', '10', '14', 'πλην τυρω και σιδωνι ανεκτοτερον εσται εν τη κρισει η υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2234', '42', '10', '15', 'και συ καπερναουμ η εως του ουρανου υψωθεισα εως αδου καταβιβασθηση');
INSERT INTO `bible_verses_tr` VALUES ('2235', '42', '10', '16', 'ο ακουων υμων εμου ακουει και ο αθετων υμας εμε αθετει ο δε εμε αθετων αθετει τον αποστειλαντα με');
INSERT INTO `bible_verses_tr` VALUES ('2236', '42', '10', '17', 'υπεστρεψαν δε οι εβδομηκοντα μετα χαρας λεγοντες κυριε και τα δαιμονια υποτασσεται ημιν εν τω ονοματι σου');
INSERT INTO `bible_verses_tr` VALUES ('2237', '42', '10', '18', 'ειπεν δε αυτοις εθεωρουν τον σαταναν ως αστραπην εκ του ουρανου πεσοντα');
INSERT INTO `bible_verses_tr` VALUES ('2238', '42', '10', '19', 'ιδου διδωμι υμιν την εξουσιαν του πατειν επανω οφεων και σκορπιων και επι πασαν την δυναμιν του εχθρου και ουδεν υμας ου μη  {VAR1: αδικηση } {VAR2: αδικησει }');
INSERT INTO `bible_verses_tr` VALUES ('2239', '42', '10', '20', 'πλην εν τουτω μη χαιρετε οτι τα πνευματα υμιν υποτασσεται χαιρετε δε μαλλον οτι τα ονοματα υμων εγραφη εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('2240', '42', '10', '21', 'εν αυτη τη ωρα ηγαλλιασατο τω πνευματι ο ιησους και ειπεν εξομολογουμαι σοι πατερ κυριε του ουρανου και της γης οτι απεκρυψας ταυτα απο σοφων και συνετων και απεκαλυψας αυτα νηπιοις ναι ο πατηρ οτι ουτως εγενετο ευδοκια εμπροσθεν σου');
INSERT INTO `bible_verses_tr` VALUES ('2241', '42', '10', '22', '{VAR1: και στραφεις προς τους μαθητας ειπεν } παντα παρεδοθη μοι υπο του πατρος μου και ουδεις γινωσκει τις εστιν ο υιος ει μη ο πατηρ και τις εστιν ο πατηρ ει μη ο υιος και ω εαν βουληται ο υιος αποκαλυψαι');
INSERT INTO `bible_verses_tr` VALUES ('2242', '42', '10', '23', 'και στραφεις προς τους μαθητας κατ ιδιαν ειπεν μακαριοι οι οφθαλμοι οι βλεποντες α βλεπετε');
INSERT INTO `bible_verses_tr` VALUES ('2243', '42', '10', '24', 'λεγω γαρ υμιν οτι πολλοι προφηται και βασιλεις ηθελησαν ιδειν α υμεις βλεπετε και ουκ ειδον και ακουσαι α ακουετε και ουκ ηκουσαν');
INSERT INTO `bible_verses_tr` VALUES ('2244', '42', '10', '25', 'και ιδου νομικος τις ανεστη εκπειραζων αυτον και λεγων διδασκαλε τι ποιησας ζωην αιωνιον κληρονομησω');
INSERT INTO `bible_verses_tr` VALUES ('2245', '42', '10', '26', 'ο δε ειπεν προς αυτον εν τω νομω τι γεγραπται πως αναγινωσκεις');
INSERT INTO `bible_verses_tr` VALUES ('2246', '42', '10', '27', 'ο δε αποκριθεις ειπεν αγαπησεις κυριον τον θεον σου εξ ολης της καρδιας σου και εξ ολης της ψυχης σου και εξ ολης της ισχυος σου και εξ ολης της διανοιας σου και τον πλησιον σου ως σεαυτον');
INSERT INTO `bible_verses_tr` VALUES ('2247', '42', '10', '28', 'ειπεν δε αυτω ορθως απεκριθης τουτο ποιει και ζηση');
INSERT INTO `bible_verses_tr` VALUES ('2248', '42', '10', '29', 'ο δε θελων δικαιουν εαυτον ειπεν προς τον ιησουν και τις εστιν μου πλησιον');
INSERT INTO `bible_verses_tr` VALUES ('2249', '42', '10', '30', 'υπολαβων δε ο ιησους ειπεν ανθρωπος τις κατεβαινεν απο ιερουσαλημ εις ιεριχω και λησταις περιεπεσεν οι και εκδυσαντες αυτον και πληγας επιθεντες απηλθον αφεντες ημιθανη τυγχανοντα');
INSERT INTO `bible_verses_tr` VALUES ('2250', '42', '10', '31', 'κατα συγκυριαν δε ιερευς τις κατεβαινεν εν τη οδω εκεινη και ιδων αυτον αντιπαρηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('2251', '42', '10', '32', 'ομοιως δε και λευιτης γενομενος κατα τον τοπον ελθων και ιδων αντιπαρηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('2252', '42', '10', '33', 'σαμαρειτης δε τις οδευων ηλθεν κατ αυτον και ιδων αυτον εσπλαγχνισθη');
INSERT INTO `bible_verses_tr` VALUES ('2253', '42', '10', '34', 'και προσελθων κατεδησεν τα τραυματα αυτου επιχεων ελαιον και οινον επιβιβασας δε αυτον επι το ιδιον κτηνος ηγαγεν αυτον εις πανδοχειον και επεμεληθη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2254', '42', '10', '35', 'και επι την αυριον εξελθων εκβαλων δυο δηναρια εδωκεν τω πανδοχει και ειπεν αυτω επιμεληθητι αυτου και ο τι αν προσδαπανησης εγω εν τω επανερχεσθαι με αποδωσω σοι');
INSERT INTO `bible_verses_tr` VALUES ('2255', '42', '10', '36', 'τις ουν τουτων των τριων δοκει σοι πλησιον γεγονεναι του εμπεσοντος εις τους ληστας');
INSERT INTO `bible_verses_tr` VALUES ('2256', '42', '10', '37', 'ο δε ειπεν ο ποιησας το ελεος μετ αυτου ειπεν ουν αυτω ο ιησους πορευου και συ ποιει ομοιως');
INSERT INTO `bible_verses_tr` VALUES ('2257', '42', '10', '38', 'εγενετο δε εν τω πορευεσθαι αυτους και αυτος εισηλθεν εις κωμην τινα γυνη δε τις ονοματι μαρθα υπεδεξατο αυτον εις τον οικον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('2258', '42', '10', '39', 'και τηδε ην αδελφη καλουμενη μαρια η και παρακαθισασα παρα τους ποδας του ιησου ηκουεν τον λογον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2259', '42', '10', '40', 'η δε μαρθα περιεσπατο περι πολλην διακονιαν επιστασα δε ειπεν κυριε ου μελει σοι οτι η αδελφη μου μονην με κατελιπεν διακονειν ειπε ουν αυτη ινα μοι συναντιλαβηται');
INSERT INTO `bible_verses_tr` VALUES ('2260', '42', '10', '41', 'αποκριθεις δε ειπεν αυτη ο ιησους μαρθα μαρθα μεριμνας και τυρβαζη περι πολλα');
INSERT INTO `bible_verses_tr` VALUES ('2261', '42', '10', '42', 'ενος δε εστιν χρεια μαρια δε την αγαθην μεριδα εξελεξατο ητις ουκ αφαιρεθησεται απ αυτης');
INSERT INTO `bible_verses_tr` VALUES ('2262', '42', '11', '1', 'και εγενετο εν τω ειναι αυτον εν τοπω τινι προσευχομενον ως επαυσατο ειπεν τις των μαθητων αυτου προς αυτον κυριε διδαξον ημας προσευχεσθαι καθως και ιωαννης εδιδαξεν τους μαθητας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2263', '42', '11', '2', 'ειπεν δε αυτοις οταν προσευχησθε λεγετε πατερ ημων ο εν τοις ουρανοις αγιασθητω το ονομα σου ελθετω η βασιλεια σου γενηθητω το θελημα σου ως εν ουρανω και επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('2264', '42', '11', '3', 'τον αρτον ημων τον επιουσιον διδου ημιν το καθ ημεραν');
INSERT INTO `bible_verses_tr` VALUES ('2265', '42', '11', '4', 'και αφες ημιν τας αμαρτιας ημων και γαρ αυτοι αφιεμεν παντι οφειλοντι ημιν και μη εισενεγκης ημας εις πειρασμον αλλα ρυσαι ημας απο του πονηρου');
INSERT INTO `bible_verses_tr` VALUES ('2266', '42', '11', '5', 'και ειπεν προς αυτους τις εξ υμων εξει φιλον και πορευσεται προς αυτον μεσονυκτιου και ειπη αυτω φιλε χρησον μοι τρεις αρτους');
INSERT INTO `bible_verses_tr` VALUES ('2267', '42', '11', '6', 'επειδη φιλος μου παρεγενετο εξ οδου προς με και ουκ εχω ο παραθησω αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2268', '42', '11', '7', 'κακεινος εσωθεν αποκριθεις ειπη μη μοι κοπους παρεχε ηδη η θυρα κεκλεισται και τα παιδια μου μετ εμου εις την κοιτην εισιν ου δυναμαι αναστας δουναι σοι');
INSERT INTO `bible_verses_tr` VALUES ('2269', '42', '11', '8', 'λεγω υμιν ει και ου δωσει αυτω αναστας δια το ειναι αυτου φιλον δια γε την αναιδειαν αυτου εγερθεις δωσει αυτω οσων χρηζει');
INSERT INTO `bible_verses_tr` VALUES ('2270', '42', '11', '9', 'καγω υμιν λεγω αιτειτε και δοθησεται υμιν ζητειτε και ευρησετε κρουετε και ανοιγησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2271', '42', '11', '10', 'πας γαρ ο αιτων λαμβανει και ο ζητων ευρισκει και τω κρουοντι ανοιγησεται');
INSERT INTO `bible_verses_tr` VALUES ('2272', '42', '11', '11', 'τινα δε υμων τον πατερα αιτησει ο υιος αρτον μη λιθον επιδωσει αυτω ει και ιχθυν μη αντι ιχθυος οφιν επιδωσει αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2273', '42', '11', '12', 'η και εαν αιτηση ωον μη επιδωσει αυτω σκορπιον');
INSERT INTO `bible_verses_tr` VALUES ('2274', '42', '11', '13', 'ει ουν υμεις πονηροι υπαρχοντες οιδατε αγαθα δοματα διδοναι τοις τεκνοις υμων ποσω μαλλον ο πατηρ ο εξ ουρανου δωσει πνευμα αγιον τοις αιτουσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2275', '42', '11', '14', 'και ην εκβαλλων δαιμονιον και αυτο ην κωφον εγενετο δε του δαιμονιου εξελθοντος ελαλησεν ο κωφος και εθαυμασαν οι οχλοι');
INSERT INTO `bible_verses_tr` VALUES ('2276', '42', '11', '15', 'τινες δε εξ αυτων ειπον εν βεελζεβουλ αρχοντι των δαιμονιων εκβαλλει τα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('2277', '42', '11', '16', 'ετεροι δε πειραζοντες σημειον παρ αυτου εζητουν εξ ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('2278', '42', '11', '17', 'αυτος δε ειδως αυτων τα διανοηματα ειπεν αυτοις πασα βασιλεια εφ εαυτην διαμερισθεισα ερημουται και οικος επι οικον πιπτει');
INSERT INTO `bible_verses_tr` VALUES ('2279', '42', '11', '18', 'ει δε και ο σατανας εφ εαυτον διεμερισθη πως σταθησεται η βασιλεια αυτου οτι λεγετε εν βεελζεβουλ εκβαλλειν με τα δαιμονια');
INSERT INTO `bible_verses_tr` VALUES ('2280', '42', '11', '19', 'ει δε εγω εν βεελζεβουλ εκβαλλω τα δαιμονια οι υιοι υμων εν τινι εκβαλλουσιν δια τουτο κριται υμων αυτοι εσονται');
INSERT INTO `bible_verses_tr` VALUES ('2281', '42', '11', '20', 'ει δε εν δακτυλω θεου εκβαλλω τα δαιμονια αρα εφθασεν εφ υμας η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2282', '42', '11', '21', 'οταν ο ισχυρος καθωπλισμενος φυλασση την εαυτου αυλην εν ειρηνη εστιν τα υπαρχοντα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2283', '42', '11', '22', 'επαν δε ο ισχυροτερος αυτου επελθων νικηση αυτον την πανοπλιαν αυτου αιρει εφ η επεποιθει και τα σκυλα αυτου διαδιδωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2284', '42', '11', '23', 'ο μη ων μετ εμου κατ εμου εστιν και ο μη συναγων μετ εμου σκορπιζει');
INSERT INTO `bible_verses_tr` VALUES ('2285', '42', '11', '24', 'οταν το ακαθαρτον πνευμα εξελθη απο του ανθρωπου διερχεται δι ανυδρων τοπων ζητουν αναπαυσιν και μη ευρισκον λεγει υποστρεψω εις τον οικον μου οθεν εξηλθον');
INSERT INTO `bible_verses_tr` VALUES ('2286', '42', '11', '25', 'και ελθον ευρισκει σεσαρωμενον και κεκοσμημενον');
INSERT INTO `bible_verses_tr` VALUES ('2287', '42', '11', '26', 'τοτε πορευεται και παραλαμβανει επτα ετερα πνευματα πονηροτερα εαυτου και εισελθοντα κατοικει εκει και γινεται τα εσχατα του ανθρωπου εκεινου χειρονα των πρωτων');
INSERT INTO `bible_verses_tr` VALUES ('2288', '42', '11', '27', 'εγενετο δε εν τω λεγειν αυτον ταυτα επαρασα τις γυνη φωνην εκ του οχλου ειπεν αυτω μακαρια η κοιλια η βαστασασα σε και μαστοι ους εθηλασας');
INSERT INTO `bible_verses_tr` VALUES ('2289', '42', '11', '28', 'αυτος δε ειπεν μενουνγε μακαριοι οι ακουοντες τον λογον του θεου και φυλασσοντες αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2290', '42', '11', '29', 'των δε οχλων επαθροιζομενων ηρξατο λεγειν η γενεα αυτη πονηρα εστιν σημειον επιζητει και σημειον ου δοθησεται αυτη ει μη το σημειον ιωνα του προφητου');
INSERT INTO `bible_verses_tr` VALUES ('2291', '42', '11', '30', 'καθως γαρ εγενετο ιωνας σημειον τοις νινευιταις ουτως εσται και ο υιος του ανθρωπου τη γενεα ταυτη');
INSERT INTO `bible_verses_tr` VALUES ('2292', '42', '11', '31', 'βασιλισσα νοτου εγερθησεται εν τη κρισει μετα των ανδρων της γενεας ταυτης και κατακρινει αυτους οτι ηλθεν εκ των περατων της γης ακουσαι την σοφιαν σολομωντος και ιδου πλειον σολομωντος ωδε');
INSERT INTO `bible_verses_tr` VALUES ('2293', '42', '11', '32', 'ανδρες νινευι αναστησονται εν τη κρισει μετα της γενεας ταυτης και κατακρινουσιν αυτην οτι μετενοησαν εις το κηρυγμα ιωνα και ιδου πλειον ιωνα ωδε');
INSERT INTO `bible_verses_tr` VALUES ('2294', '42', '11', '33', 'ουδεις δε λυχνον αψας εις κρυπτον τιθησιν ουδε υπο τον μοδιον αλλ επι την λυχνιαν ινα οι εισπορευομενοι το φεγγος βλεπωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2295', '42', '11', '34', 'ο λυχνος του σωματος εστιν ο οφθαλμος οταν ουν ο οφθαλμος σου απλους η και ολον το σωμα σου φωτεινον εστιν επαν δε πονηρος η και το σωμα σου σκοτεινον');
INSERT INTO `bible_verses_tr` VALUES ('2296', '42', '11', '35', 'σκοπει ουν μη το φως το εν σοι σκοτος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2297', '42', '11', '36', 'ει ουν το σωμα σου ολον φωτεινον μη εχον τι μερος σκοτεινον εσται φωτεινον ολον ως οταν ο λυχνος τη αστραπη φωτιζη σε');
INSERT INTO `bible_verses_tr` VALUES ('2298', '42', '11', '37', 'εν δε τω λαλησαι ηρωτα αυτον φαρισαιος τις οπως αριστηση παρ αυτω εισελθων δε ανεπεσεν');
INSERT INTO `bible_verses_tr` VALUES ('2299', '42', '11', '38', 'ο δε φαρισαιος ιδων εθαυμασεν οτι ου πρωτον εβαπτισθη προ του αριστου');
INSERT INTO `bible_verses_tr` VALUES ('2300', '42', '11', '39', 'ειπεν δε ο κυριος προς αυτον νυν υμεις οι φαρισαιοι το εξωθεν του ποτηριου και του πινακος καθαριζετε το δε εσωθεν υμων γεμει αρπαγης και πονηριας');
INSERT INTO `bible_verses_tr` VALUES ('2301', '42', '11', '40', 'αφρονες ουχ ο ποιησας το εξωθεν και το εσωθεν εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('2302', '42', '11', '41', 'πλην τα ενοντα δοτε ελεημοσυνην και ιδου παντα καθαρα υμιν εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2303', '42', '11', '42', 'αλλ ουαι υμιν τοις φαρισαιοις οτι αποδεκατουτε το ηδυοσμον και το πηγανον και παν λαχανον και παρερχεσθε την κρισιν και την αγαπην του θεου ταυτα εδει ποιησαι κακεινα μη αφιεναι');
INSERT INTO `bible_verses_tr` VALUES ('2304', '42', '11', '43', 'ουαι υμιν τοις φαρισαιοις οτι αγαπατε την πρωτοκαθεδριαν εν ταις συναγωγαις και τους ασπασμους εν ταις αγοραις');
INSERT INTO `bible_verses_tr` VALUES ('2305', '42', '11', '44', 'ουαι υμιν γραμματεις και φαρισαιοι υποκριται οτι εστε ως τα μνημεια τα αδηλα και οι ανθρωποι οι περιπατουντες επανω ουκ οιδασιν');
INSERT INTO `bible_verses_tr` VALUES ('2306', '42', '11', '45', 'αποκριθεις δε τις των νομικων λεγει αυτω διδασκαλε ταυτα λεγων και ημας υβριζεις');
INSERT INTO `bible_verses_tr` VALUES ('2307', '42', '11', '46', 'ο δε ειπεν και υμιν τοις νομικοις ουαι οτι φορτιζετε τους ανθρωπους φορτια δυσβαστακτα και αυτοι ενι των δακτυλων υμων ου προσψαυετε τοις φορτιοις');
INSERT INTO `bible_verses_tr` VALUES ('2308', '42', '11', '47', 'ουαι υμιν οτι οικοδομειτε τα μνημεια των προφητων οι δε πατερες υμων απεκτειναν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2309', '42', '11', '48', 'αρα μαρτυρειτε και συνευδοκειτε τοις εργοις των πατερων υμων οτι αυτοι μεν απεκτειναν αυτους υμεις δε οικοδομειτε αυτων τα μνημεια');
INSERT INTO `bible_verses_tr` VALUES ('2310', '42', '11', '49', 'δια τουτο και η σοφια του θεου ειπεν αποστελω εις αυτους προφητας και αποστολους και εξ αυτων αποκτενουσιν και εκδιωξουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2311', '42', '11', '50', 'ινα εκζητηθη το αιμα παντων των προφητων το εκχυνομενον απο καταβολης κοσμου απο της γενεας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('2312', '42', '11', '51', 'απο του αιματος αβελ εως του αιματος ζαχαριου του απολομενου μεταξυ του θυσιαστηριου και του οικου ναι λεγω υμιν εκζητηθησεται απο της γενεας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('2313', '42', '11', '52', 'ουαι υμιν τοις νομικοις οτι ηρατε την κλειδα της γνωσεως αυτοι ουκ εισηλθετε και τους εισερχομενους εκωλυσατε');
INSERT INTO `bible_verses_tr` VALUES ('2314', '42', '11', '53', 'λεγοντος δε αυτου ταυτα προς αυτους ηρξαντο οι γραμματεις και οι φαρισαιοι δεινως ενεχειν και αποστοματιζειν αυτον περι πλειονων');
INSERT INTO `bible_verses_tr` VALUES ('2315', '42', '11', '54', 'ενεδρευοντες αυτον και ζητουντες θηρευσαι τι εκ του στοματος αυτου ινα κατηγορησωσιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2316', '42', '12', '1', 'εν οις επισυναχθεισων των μυριαδων του οχλου ωστε καταπατειν αλληλους ηρξατο λεγειν προς τους μαθητας αυτου πρωτον προσεχετε εαυτοις απο της ζυμης των φαρισαιων ητις εστιν υποκρισις');
INSERT INTO `bible_verses_tr` VALUES ('2317', '42', '12', '2', 'ουδεν δε συγκεκαλυμμενον εστιν ο ουκ αποκαλυφθησεται και κρυπτον ο ου γνωσθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2318', '42', '12', '3', 'ανθ ων οσα εν τη σκοτια ειπατε εν τω φωτι ακουσθησεται και ο προς το ους ελαλησατε εν τοις ταμειοις κηρυχθησεται επι των δωματων');
INSERT INTO `bible_verses_tr` VALUES ('2319', '42', '12', '4', 'λεγω δε υμιν τοις φιλοις μου μη φοβηθητε απο των αποκτεινοντων το σωμα και μετα ταυτα μη εχοντων περισσοτερον τι ποιησαι');
INSERT INTO `bible_verses_tr` VALUES ('2320', '42', '12', '5', 'υποδειξω δε υμιν τινα φοβηθητε φοβηθητε τον μετα το αποκτειναι εξουσιαν εχοντα εμβαλειν εις την γεενναν ναι λεγω υμιν τουτον φοβηθητε');
INSERT INTO `bible_verses_tr` VALUES ('2321', '42', '12', '6', 'ουχι πεντε στρουθια πωλειται ασσαριων δυο και εν εξ αυτων ουκ εστιν επιλελησμενον ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2322', '42', '12', '7', 'αλλα και αι τριχες της κεφαλης υμων πασαι ηριθμηνται μη ουν φοβεισθε πολλων στρουθιων διαφερετε');
INSERT INTO `bible_verses_tr` VALUES ('2323', '42', '12', '8', 'λεγω δε υμιν πας ος αν ομολογηση εν εμοι εμπροσθεν των ανθρωπων και ο υιος του ανθρωπου ομολογησει εν αυτω εμπροσθεν των αγγελων του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2324', '42', '12', '9', 'ο δε αρνησαμενος με ενωπιον των ανθρωπων απαρνηθησεται ενωπιον των αγγελων του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2325', '42', '12', '10', 'και πας ος ερει λογον εις τον υιον του ανθρωπου αφεθησεται αυτω τω δε εις το αγιον πνευμα βλασφημησαντι ουκ αφεθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2326', '42', '12', '11', 'οταν δε προσφερωσιν υμας επι τας συναγωγας και τας αρχας και τας εξουσιας μη μεριμνατε πως η τι απολογησησθε η τι ειπητε');
INSERT INTO `bible_verses_tr` VALUES ('2327', '42', '12', '12', 'το γαρ αγιον πνευμα διδαξει υμας εν αυτη τη ωρα α δει ειπειν');
INSERT INTO `bible_verses_tr` VALUES ('2328', '42', '12', '13', 'ειπεν δε τις αυτω εκ του οχλου διδασκαλε ειπε τω αδελφω μου μερισασθαι μετ εμου την κληρονομιαν');
INSERT INTO `bible_verses_tr` VALUES ('2329', '42', '12', '14', 'ο δε ειπεν αυτω ανθρωπε τις με κατεστησεν δικαστην η μεριστην εφ υμας');
INSERT INTO `bible_verses_tr` VALUES ('2330', '42', '12', '15', 'ειπεν δε προς αυτους ορατε και φυλασσεσθε απο της πλεονεξιας οτι ουκ εν τω περισσευειν τινι η ζωη αυτου εστιν εκ των υπαρχοντων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2331', '42', '12', '16', 'ειπεν δε παραβολην προς αυτους λεγων ανθρωπου τινος πλουσιου ευφορησεν η χωρα');
INSERT INTO `bible_verses_tr` VALUES ('2332', '42', '12', '17', 'και διελογιζετο εν εαυτω λεγων τι ποιησω οτι ουκ εχω που συναξω τους καρπους μου');
INSERT INTO `bible_verses_tr` VALUES ('2333', '42', '12', '18', 'και ειπεν τουτο ποιησω καθελω μου τας αποθηκας και μειζονας οικοδομησω και συναξω εκει παντα τα γενηματα μου και τα αγαθα μου');
INSERT INTO `bible_verses_tr` VALUES ('2334', '42', '12', '19', 'και ερω τη ψυχη μου ψυχη εχεις πολλα αγαθα κειμενα εις ετη πολλα αναπαυου φαγε πιε ευφραινου');
INSERT INTO `bible_verses_tr` VALUES ('2335', '42', '12', '20', 'ειπεν δε αυτω ο θεος  {VAR1: αφρων } {VAR2: αφρον } ταυτη τη νυκτι την ψυχην σου απαιτουσιν απο σου α δε ητοιμασας τινι εσται');
INSERT INTO `bible_verses_tr` VALUES ('2336', '42', '12', '21', 'ουτως ο θησαυριζων εαυτω και μη εις θεον πλουτων');
INSERT INTO `bible_verses_tr` VALUES ('2337', '42', '12', '22', 'ειπεν δε προς τους μαθητας αυτου δια τουτο υμιν λεγω μη μεριμνατε τη ψυχη υμων τι φαγητε μηδε τω σωματι τι ενδυσησθε');
INSERT INTO `bible_verses_tr` VALUES ('2338', '42', '12', '23', 'η ψυχη πλειον εστιν της τροφης και το σωμα του ενδυματος');
INSERT INTO `bible_verses_tr` VALUES ('2339', '42', '12', '24', 'κατανοησατε τους κορακας οτι ου σπειρουσιν ουδε θεριζουσιν οις ουκ εστιν ταμειον ουδε αποθηκη και ο θεος τρεφει αυτους ποσω μαλλον υμεις διαφερετε των πετεινων');
INSERT INTO `bible_verses_tr` VALUES ('2340', '42', '12', '25', 'τις δε εξ υμων μεριμνων δυναται προσθειναι επι την ηλικιαν αυτου πηχυν ενα');
INSERT INTO `bible_verses_tr` VALUES ('2341', '42', '12', '26', 'ει ουν ουτε ελαχιστον δυνασθε τι περι των λοιπων μεριμνατε');
INSERT INTO `bible_verses_tr` VALUES ('2342', '42', '12', '27', 'κατανοησατε τα κρινα πως αυξανει ου κοπια ουδε νηθει λεγω δε υμιν ουδε σολομων εν παση τη δοξη αυτου περιεβαλετο ως εν τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2343', '42', '12', '28', 'ει δε τον χορτον εν τω αγρω σημερον οντα και αυριον εις κλιβανον βαλλομενον ο θεος ουτως αμφιεννυσιν ποσω μαλλον υμας ολιγοπιστοι');
INSERT INTO `bible_verses_tr` VALUES ('2344', '42', '12', '29', 'και υμεις μη ζητειτε τι φαγητε η τι πιητε και μη μετεωριζεσθε');
INSERT INTO `bible_verses_tr` VALUES ('2345', '42', '12', '30', 'ταυτα γαρ παντα τα εθνη του κοσμου επιζητει υμων δε ο πατηρ οιδεν οτι χρηζετε τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2346', '42', '12', '31', 'πλην ζητειτε την βασιλειαν του θεου και ταυτα παντα προστεθησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2347', '42', '12', '32', 'μη φοβου το μικρον ποιμνιον οτι ευδοκησεν ο πατηρ υμων δουναι υμιν την βασιλειαν');
INSERT INTO `bible_verses_tr` VALUES ('2348', '42', '12', '33', 'πωλησατε τα υπαρχοντα υμων και δοτε ελεημοσυνην ποιησατε εαυτοις βαλαντια μη παλαιουμενα θησαυρον ανεκλειπτον εν τοις ουρανοις οπου κλεπτης ουκ εγγιζει ουδε σης διαφθειρει');
INSERT INTO `bible_verses_tr` VALUES ('2349', '42', '12', '34', 'οπου γαρ εστιν ο θησαυρος υμων εκει και η καρδια υμων εσται');
INSERT INTO `bible_verses_tr` VALUES ('2350', '42', '12', '35', 'εστωσαν υμων αι οσφυες περιεζωσμεναι και οι λυχνοι καιομενοι');
INSERT INTO `bible_verses_tr` VALUES ('2351', '42', '12', '36', 'και υμεις ομοιοι ανθρωποις προσδεχομενοις τον κυριον εαυτων ποτε αναλυσει εκ των γαμων ινα ελθοντος και κρουσαντος ευθεως ανοιξωσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2352', '42', '12', '37', 'μακαριοι οι δουλοι εκεινοι ους ελθων ο κυριος ευρησει γρηγορουντας αμην λεγω υμιν οτι περιζωσεται και ανακλινει αυτους και παρελθων διακονησει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2353', '42', '12', '38', 'και εαν ελθη εν τη δευτερα φυλακη και εν τη τριτη φυλακη ελθη και ευρη ουτως μακαριοι εισιν οι δουλοι εκεινοι');
INSERT INTO `bible_verses_tr` VALUES ('2354', '42', '12', '39', 'τουτο δε γινωσκετε οτι ει ηδει ο οικοδεσποτης ποια ωρα ο κλεπτης ερχεται εγρηγορησεν αν και ουκ αν αφηκεν διορυγηναι τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2355', '42', '12', '40', 'και υμεις ουν γινεσθε ετοιμοι οτι η ωρα ου δοκειτε ο υιος του ανθρωπου ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('2356', '42', '12', '41', 'ειπεν δε αυτω ο πετρος κυριε προς ημας την παραβολην ταυτην λεγεις η και προς παντας');
INSERT INTO `bible_verses_tr` VALUES ('2357', '42', '12', '42', 'ειπεν δε ο κυριος τις αρα εστιν ο πιστος οικονομος και φρονιμος ον καταστησει ο κυριος επι της θεραπειας αυτου του διδοναι εν καιρω το σιτομετριον');
INSERT INTO `bible_verses_tr` VALUES ('2358', '42', '12', '43', 'μακαριος ο δουλος εκεινος ον ελθων ο κυριος αυτου ευρησει ποιουντα ουτως');
INSERT INTO `bible_verses_tr` VALUES ('2359', '42', '12', '44', 'αληθως λεγω υμιν οτι επι πασιν τοις υπαρχουσιν αυτου καταστησει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2360', '42', '12', '45', 'εαν δε ειπη ο δουλος εκεινος εν τη καρδια αυτου χρονιζει ο κυριος μου ερχεσθαι και αρξηται τυπτειν τους παιδας και τας παιδισκας εσθιειν τε και πινειν και μεθυσκεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2361', '42', '12', '46', 'ηξει ο κυριος του δουλου εκεινου εν ημερα η ου προσδοκα και εν ωρα η ου γινωσκει και διχοτομησει αυτον και το μερος αυτου μετα των απιστων θησει');
INSERT INTO `bible_verses_tr` VALUES ('2362', '42', '12', '47', 'εκεινος δε ο δουλος ο γνους το θελημα του κυριου εαυτου και μη ετοιμασας μηδε ποιησας προς το θελημα αυτου δαρησεται πολλας');
INSERT INTO `bible_verses_tr` VALUES ('2363', '42', '12', '48', 'ο δε μη γνους ποιησας δε αξια πληγων δαρησεται ολιγας παντι δε ω εδοθη πολυ πολυ ζητηθησεται παρ αυτου και ω παρεθεντο πολυ περισσοτερον αιτησουσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2364', '42', '12', '49', 'πυρ ηλθον βαλειν εις την γην και τι θελω ει ηδη ανηφθη');
INSERT INTO `bible_verses_tr` VALUES ('2365', '42', '12', '50', 'βαπτισμα δε εχω βαπτισθηναι και πως συνεχομαι εως ου τελεσθη');
INSERT INTO `bible_verses_tr` VALUES ('2366', '42', '12', '51', 'δοκειτε οτι ειρηνην παρεγενομην δουναι εν τη γη ουχι λεγω υμιν αλλ η διαμερισμον');
INSERT INTO `bible_verses_tr` VALUES ('2367', '42', '12', '52', 'εσονται γαρ απο του νυν πεντε εν οικω ενι διαμεμερισμενοι τρεις επι δυσιν και δυο επι τρισιν');
INSERT INTO `bible_verses_tr` VALUES ('2368', '42', '12', '53', 'διαμερισθησεται πατηρ εφ υιω και υιος επι πατρι μητηρ επι θυγατρι και θυγατηρ επι μητρι πενθερα επι την νυμφην αυτης και νυμφη επι την πενθεραν αυτης');
INSERT INTO `bible_verses_tr` VALUES ('2369', '42', '12', '54', 'ελεγεν δε και τοις οχλοις οταν ιδητε την νεφελην ανατελλουσαν απο δυσμων ευθεως λεγετε ομβρος ερχεται και γινεται ουτως');
INSERT INTO `bible_verses_tr` VALUES ('2370', '42', '12', '55', 'και οταν νοτον πνεοντα λεγετε οτι καυσων εσται και γινεται');
INSERT INTO `bible_verses_tr` VALUES ('2371', '42', '12', '56', 'υποκριται το προσωπον  {VAR1: της γης και του ουρανου } {VAR2: του ουρανου και της γης } οιδατε δοκιμαζειν τον δε καιρον τουτον πως ου δοκιμαζετε');
INSERT INTO `bible_verses_tr` VALUES ('2372', '42', '12', '57', 'τι δε και αφ εαυτων ου κρινετε το δικαιον');
INSERT INTO `bible_verses_tr` VALUES ('2373', '42', '12', '58', 'ως γαρ υπαγεις μετα του αντιδικου σου επ αρχοντα εν τη οδω δος εργασιαν απηλλαχθαι απ αυτου μηποτε κατασυρη σε προς τον κριτην και ο κριτης σε παραδω τω πρακτορι και ο πρακτωρ σε βαλλη εις φυλακην');
INSERT INTO `bible_verses_tr` VALUES ('2374', '42', '12', '59', 'λεγω σοι ου μη εξελθης εκειθεν εως ου και το εσχατον λεπτον αποδως');
INSERT INTO `bible_verses_tr` VALUES ('2375', '42', '13', '1', 'παρησαν δε τινες εν αυτω τω καιρω απαγγελλοντες αυτω περι των γαλιλαιων ων το αιμα πιλατος εμιξεν μετα των θυσιων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2376', '42', '13', '2', 'και αποκριθεις ο ιησους ειπεν αυτοις δοκειτε οτι οι γαλιλαιοι ουτοι αμαρτωλοι παρα παντας τους γαλιλαιους εγενοντο οτι τοιαυτα πεπονθασιν');
INSERT INTO `bible_verses_tr` VALUES ('2377', '42', '13', '3', 'ουχι λεγω υμιν αλλ εαν μη μετανοητε παντες ωσαυτως απολεισθε');
INSERT INTO `bible_verses_tr` VALUES ('2378', '42', '13', '4', 'η εκεινοι οι δεκα και οκτω εφ ους επεσεν ο πυργος εν τω σιλωαμ και απεκτεινεν αυτους δοκειτε οτι ουτοι οφειλεται εγενοντο παρα παντας ανθρωπους τους κατοικουντας εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2379', '42', '13', '5', 'ουχι λεγω υμιν αλλ εαν μη μετανοητε παντες ομοιως απολεισθε');
INSERT INTO `bible_verses_tr` VALUES ('2380', '42', '13', '6', 'ελεγεν δε ταυτην την παραβολην συκην ειχεν τις εν τω αμπελωνι αυτου πεφυτευμενην και ηλθεν καρπον ζητων εν αυτη και ουχ ευρεν');
INSERT INTO `bible_verses_tr` VALUES ('2381', '42', '13', '7', 'ειπεν δε προς τον αμπελουργον ιδου τρια ετη ερχομαι ζητων καρπον εν τη συκη ταυτη και ουχ ευρισκω εκκοψον αυτην ινα τι και την γην καταργει');
INSERT INTO `bible_verses_tr` VALUES ('2382', '42', '13', '8', 'ο δε αποκριθεις λεγει αυτω κυριε αφες αυτην και τουτο το ετος εως οτου σκαψω περι αυτην και βαλω κοπριαν');
INSERT INTO `bible_verses_tr` VALUES ('2383', '42', '13', '9', 'καν μεν ποιηση καρπον ει δε μηγε εις το μελλον εκκοψεις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2384', '42', '13', '10', 'ην δε διδασκων εν μια των συναγωγων εν τοις σαββασιν');
INSERT INTO `bible_verses_tr` VALUES ('2385', '42', '13', '11', 'και ιδου γυνη ην πνευμα εχουσα ασθενειας ετη δεκα και οκτω και ην συγκυπτουσα και μη δυναμενη ανακυψαι εις το παντελες');
INSERT INTO `bible_verses_tr` VALUES ('2386', '42', '13', '12', 'ιδων δε αυτην ο ιησους προσεφωνησεν και ειπεν αυτη γυναι απολελυσαι της ασθενειας σου');
INSERT INTO `bible_verses_tr` VALUES ('2387', '42', '13', '13', 'και επεθηκεν αυτη τας χειρας και παραχρημα ανωρθωθη και εδοξαζεν τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('2388', '42', '13', '14', 'αποκριθεις δε ο αρχισυναγωγος αγανακτων οτι τω σαββατω εθεραπευσεν ο ιησους ελεγεν τω οχλω εξ ημεραι εισιν εν αις δει εργαζεσθαι εν ταυταις ουν ερχομενοι θεραπευεσθε και μη τη ημερα του σαββατου');
INSERT INTO `bible_verses_tr` VALUES ('2389', '42', '13', '15', 'απεκριθη ουν αυτω ο κυριος και ειπεν υποκριτα εκαστος υμων τω σαββατω ου λυει τον βουν αυτου η τον ονον απο της φατνης και απαγαγων ποτιζει');
INSERT INTO `bible_verses_tr` VALUES ('2390', '42', '13', '16', 'ταυτην δε θυγατερα αβρααμ ουσαν ην εδησεν ο σατανας ιδου δεκα και οκτω ετη ουκ εδει λυθηναι απο του δεσμου τουτου τη ημερα του σαββατου');
INSERT INTO `bible_verses_tr` VALUES ('2391', '42', '13', '17', 'και ταυτα λεγοντος αυτου κατησχυνοντο παντες οι αντικειμενοι αυτω και πας ο οχλος εχαιρεν επι πασιν τοις ενδοξοις τοις γινομενοις υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2392', '42', '13', '18', 'ελεγεν δε τινι ομοια εστιν η βασιλεια του θεου και τινι ομοιωσω αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2393', '42', '13', '19', 'ομοια εστιν κοκκω σιναπεως ον λαβων ανθρωπος εβαλεν εις κηπον εαυτου και ηυξησεν και εγενετο εις δενδρον μεγα και τα πετεινα του ουρανου κατεσκηνωσεν εν τοις κλαδοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2394', '42', '13', '20', 'και παλιν ειπεν τινι ομοιωσω την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2395', '42', '13', '21', 'ομοια εστιν ζυμη ην λαβουσα γυνη ενεκρυψεν εις αλευρου σατα τρια εως ου εζυμωθη ολον');
INSERT INTO `bible_verses_tr` VALUES ('2396', '42', '13', '22', 'και διεπορευετο κατα πολεις και κωμας διδασκων και πορειαν ποιουμενος εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2397', '42', '13', '23', 'ειπεν δε τις αυτω κυριε ει ολιγοι οι σωζομενοι ο δε ειπεν προς αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2398', '42', '13', '24', 'αγωνιζεσθε εισελθειν δια της στενης πυλης οτι πολλοι λεγω υμιν ζητησουσιν εισελθειν και ουκ ισχυσουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2399', '42', '13', '25', 'αφ ου αν εγερθη ο οικοδεσποτης και αποκλειση την θυραν και αρξησθε εξω εσταναι και κρουειν την θυραν λεγοντες κυριε κυριε ανοιξον ημιν και αποκριθεις ερει υμιν ουκ οιδα υμας ποθεν εστε');
INSERT INTO `bible_verses_tr` VALUES ('2400', '42', '13', '26', 'τοτε αρξεσθε λεγειν εφαγομεν ενωπιον σου και επιομεν και εν ταις πλατειαις ημων εδιδαξας');
INSERT INTO `bible_verses_tr` VALUES ('2401', '42', '13', '27', 'και ερει λεγω υμιν ουκ οιδα υμας ποθεν εστε αποστητε απ εμου παντες οι εργαται της αδικιας');
INSERT INTO `bible_verses_tr` VALUES ('2402', '42', '13', '28', 'εκει εσται ο κλαυθμος και ο βρυγμος των οδοντων οταν οψησθε αβρααμ και ισαακ και ιακωβ και παντας τους προφητας εν τη βασιλεια του θεου υμας δε εκβαλλομενους εξω');
INSERT INTO `bible_verses_tr` VALUES ('2403', '42', '13', '29', 'και ηξουσιν απο ανατολων και δυσμων και απο βορρα και νοτου και ανακλιθησονται εν τη βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2404', '42', '13', '30', 'και ιδου εισιν εσχατοι οι εσονται πρωτοι και εισιν πρωτοι οι εσονται εσχατοι');
INSERT INTO `bible_verses_tr` VALUES ('2405', '42', '13', '31', 'εν αυτη τη ημερα προσηλθον τινες φαρισαιοι λεγοντες αυτω εξελθε και πορευου εντευθεν οτι ηρωδης θελει σε αποκτειναι');
INSERT INTO `bible_verses_tr` VALUES ('2406', '42', '13', '32', 'και ειπεν αυτοις πορευθεντες ειπατε τη αλωπεκι ταυτη ιδου εκβαλλω δαιμονια και ιασεις επιτελω σημερον και αυριον και τη τριτη τελειουμαι');
INSERT INTO `bible_verses_tr` VALUES ('2407', '42', '13', '33', 'πλην δει με σημερον και αυριον και τη εχομενη πορευεσθαι οτι ουκ ενδεχεται προφητην απολεσθαι εξω ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2408', '42', '13', '34', 'ιερουσαλημ ιερουσαλημ η αποκτεινουσα τους προφητας και λιθοβολουσα τους απεσταλμενους προς αυτην ποσακις ηθελησα επισυναξαι τα τεκνα σου ον τροπον ορνις την εαυτης νοσσιαν υπο τας πτερυγας και ουκ ηθελησατε');
INSERT INTO `bible_verses_tr` VALUES ('2409', '42', '13', '35', 'ιδου αφιεται υμιν ο οικος υμων ερημος αμην δε λεγω υμιν οτι ου μη με ιδητε εως αν ηξη οτε ειπητε ευλογημενος ο ερχομενος εν ονοματι κυριου');
INSERT INTO `bible_verses_tr` VALUES ('2410', '42', '14', '1', 'και εγενετο εν τω ελθειν αυτον εις οικον τινος των αρχοντων των φαρισαιων σαββατω φαγειν αρτον και αυτοι ησαν παρατηρουμενοι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2411', '42', '14', '2', 'και ιδου ανθρωπος τις ην υδρωπικος εμπροσθεν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2412', '42', '14', '3', 'και αποκριθεις ο ιησους ειπεν προς τους νομικους και φαρισαιους λεγων ει εξεστιν τω σαββατω θεραπευειν');
INSERT INTO `bible_verses_tr` VALUES ('2413', '42', '14', '4', 'οι δε ησυχασαν και επιλαβομενος ιασατο αυτον και απελυσεν');
INSERT INTO `bible_verses_tr` VALUES ('2414', '42', '14', '5', 'και αποκριθεις προς αυτους ειπεν τινος υμων ονος η βους εις φρεαρ εμπεσειται και ουκ ευθεως ανασπασει αυτον εν τη ημερα του σαββατου');
INSERT INTO `bible_verses_tr` VALUES ('2415', '42', '14', '6', 'και ουκ ισχυσαν ανταποκριθηναι αυτω προς ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('2416', '42', '14', '7', 'ελεγεν δε προς τους κεκλημενους παραβολην επεχων πως τας πρωτοκλισιας εξελεγοντο λεγων προς αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2417', '42', '14', '8', 'οταν κληθης υπο τινος εις γαμους μη κατακλιθης εις την πρωτοκλισιαν μηποτε εντιμοτερος σου η κεκλημενος υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2418', '42', '14', '9', 'και ελθων ο σε και αυτον καλεσας ερει σοι δος τουτω τοπον και τοτε αρξη μετ αισχυνης τον εσχατον τοπον κατεχειν');
INSERT INTO `bible_verses_tr` VALUES ('2419', '42', '14', '10', 'αλλ οταν κληθης πορευθεις αναπεσον εις τον εσχατον τοπον ινα οταν ελθη ο κεκληκως σε ειπη σοι φιλε προσαναβηθι ανωτερον τοτε εσται σοι δοξα ενωπιον των συνανακειμενων σοι');
INSERT INTO `bible_verses_tr` VALUES ('2420', '42', '14', '11', 'οτι πας ο υψων εαυτον ταπεινωθησεται και ο ταπεινων εαυτον υψωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2421', '42', '14', '12', 'ελεγεν δε και τω κεκληκοτι αυτον οταν ποιης αριστον η δειπνον μη φωνει τους φιλους σου μηδε τους αδελφους σου μηδε τους συγγενεις σου μηδε γειτονας πλουσιους μηποτε και αυτοι σε αντικαλεσωσιν και γενηται σοι ανταποδομα');
INSERT INTO `bible_verses_tr` VALUES ('2422', '42', '14', '13', 'αλλ οταν ποιης δοχην καλει πτωχους αναπηρους χωλους τυφλους');
INSERT INTO `bible_verses_tr` VALUES ('2423', '42', '14', '14', 'και μακαριος εση οτι ουκ εχουσιν ανταποδουναι σοι ανταποδοθησεται γαρ σοι εν τη αναστασει των δικαιων');
INSERT INTO `bible_verses_tr` VALUES ('2424', '42', '14', '15', 'ακουσας δε τις των συνανακειμενων ταυτα ειπεν αυτω μακαριος ος φαγεται αρτον εν τη βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2425', '42', '14', '16', 'ο δε ειπεν αυτω ανθρωπος τις εποιησεν δειπνον μεγα και εκαλεσεν πολλους');
INSERT INTO `bible_verses_tr` VALUES ('2426', '42', '14', '17', 'και απεστειλεν τον δουλον αυτου τη ωρα του δειπνου ειπειν τοις κεκλημενοις ερχεσθε οτι ηδη ετοιμα εστιν παντα');
INSERT INTO `bible_verses_tr` VALUES ('2427', '42', '14', '18', 'και ηρξαντο απο μιας παραιτεισθαι παντες ο πρωτος ειπεν αυτω αγρον ηγορασα και εχω αναγκην εξελθειν και ιδειν αυτον ερωτω σε εχε με παρητημενον');
INSERT INTO `bible_verses_tr` VALUES ('2428', '42', '14', '19', 'και ετερος ειπεν ζευγη βοων ηγορασα πεντε και πορευομαι δοκιμασαι αυτα ερωτω σε εχε με παρητημενον');
INSERT INTO `bible_verses_tr` VALUES ('2429', '42', '14', '20', 'και ετερος ειπεν γυναικα εγημα και δια τουτο ου δυναμαι ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('2430', '42', '14', '21', 'και παραγενομενος ο δουλος εκεινος απηγγειλεν τω κυριω αυτου ταυτα τοτε οργισθεις ο οικοδεσποτης ειπεν τω δουλω αυτου εξελθε ταχεως εις τας πλατειας και ρυμας της πολεως και τους πτωχους και αναπηρους και χωλους και τυφλους εισαγαγε ωδε');
INSERT INTO `bible_verses_tr` VALUES ('2431', '42', '14', '22', 'και ειπεν ο δουλος κυριε γεγονεν ως επεταξας και ετι τοπος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2432', '42', '14', '23', 'και ειπεν ο κυριος προς τον δουλον εξελθε εις τας οδους και φραγμους και αναγκασον εισελθειν ινα γεμισθη ο οικος μου');
INSERT INTO `bible_verses_tr` VALUES ('2433', '42', '14', '24', 'λεγω γαρ υμιν οτι ουδεις των ανδρων εκεινων των κεκλημενων γευσεται μου του δειπνου');
INSERT INTO `bible_verses_tr` VALUES ('2434', '42', '14', '25', 'συνεπορευοντο δε αυτω οχλοι πολλοι και στραφεις ειπεν προς αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2435', '42', '14', '26', 'ει τις ερχεται προς με και ου μισει τον πατερα εαυτου και την μητερα και την γυναικα και τα τεκνα και τους αδελφους και τας αδελφας ετι δε και την εαυτου ψυχην ου δυναται μου μαθητης ειναι');
INSERT INTO `bible_verses_tr` VALUES ('2436', '42', '14', '27', 'και οστις ου βασταζει τον σταυρον αυτου και ερχεται οπισω μου ου δυναται μου ειναι μαθητης');
INSERT INTO `bible_verses_tr` VALUES ('2437', '42', '14', '28', 'τις γαρ εξ υμων θελων πυργον οικοδομησαι ουχι πρωτον καθισας ψηφιζει την δαπανην ει εχει τα προς απαρτισμον');
INSERT INTO `bible_verses_tr` VALUES ('2438', '42', '14', '29', 'ινα μηποτε θεντος αυτου θεμελιον και μη ισχυοντος εκτελεσαι παντες οι θεωρουντες αρξωνται εμπαιζειν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2439', '42', '14', '30', 'λεγοντες οτι ουτος ο ανθρωπος ηρξατο οικοδομειν και ουκ ισχυσεν εκτελεσαι');
INSERT INTO `bible_verses_tr` VALUES ('2440', '42', '14', '31', 'η τις βασιλευς πορευομενος συμβαλειν ετερω βασιλει εις πολεμον ουχι καθισας πρωτον βουλευεται ει δυνατος εστιν εν δεκα χιλιασιν απαντησαι τω μετα εικοσι χιλιαδων ερχομενω επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2441', '42', '14', '32', 'ει δε μηγε ετι αυτου πορρω οντος πρεσβειαν αποστειλας ερωτα τα προς ειρηνην');
INSERT INTO `bible_verses_tr` VALUES ('2442', '42', '14', '33', 'ουτως ουν πας εξ υμων ος ουκ αποτασσεται πασιν τοις εαυτου υπαρχουσιν ου δυναται μου ειναι μαθητης');
INSERT INTO `bible_verses_tr` VALUES ('2443', '42', '14', '34', 'καλον το αλας εαν δε το αλας μωρανθη εν τινι αρτυθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2444', '42', '14', '35', 'ουτε εις γην ουτε εις κοπριαν ευθετον εστιν εξω βαλλουσιν αυτο ο εχων ωτα ακουειν ακουετω');
INSERT INTO `bible_verses_tr` VALUES ('2445', '42', '15', '1', 'ησαν δε εγγιζοντες αυτω παντες οι τελωναι και οι αμαρτωλοι ακουειν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2446', '42', '15', '2', 'και διεγογγυζον οι φαρισαιοι και οι γραμματεις λεγοντες οτι ουτος αμαρτωλους προσδεχεται και συνεσθιει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2447', '42', '15', '3', 'ειπεν δε προς αυτους την παραβολην ταυτην λεγων');
INSERT INTO `bible_verses_tr` VALUES ('2448', '42', '15', '4', 'τις ανθρωπος εξ υμων εχων εκατον προβατα και απολεσας εν εξ αυτων ου καταλειπει τα εννενηκονταεννεα εν τη ερημω και πορευεται επι το απολωλος εως ευρη αυτο');
INSERT INTO `bible_verses_tr` VALUES ('2449', '42', '15', '5', 'και ευρων επιτιθησιν επι τους ωμους εαυτου χαιρων');
INSERT INTO `bible_verses_tr` VALUES ('2450', '42', '15', '6', 'και ελθων εις τον οικον συγκαλει τους φιλους και τους γειτονας λεγων αυτοις συγχαρητε μοι οτι ευρον το προβατον μου το απολωλος');
INSERT INTO `bible_verses_tr` VALUES ('2451', '42', '15', '7', 'λεγω υμιν οτι ουτως χαρα εσται εν τω ουρανω επι ενι αμαρτωλω μετανοουντι η επι εννενηκονταεννεα δικαιοις οιτινες ου χρειαν εχουσιν μετανοιας');
INSERT INTO `bible_verses_tr` VALUES ('2452', '42', '15', '8', 'η τις γυνη δραχμας εχουσα δεκα εαν απολεση δραχμην μιαν ουχι απτει λυχνον και σαροι την οικιαν και ζητει επιμελως εως οτου ευρη');
INSERT INTO `bible_verses_tr` VALUES ('2453', '42', '15', '9', 'και ευρουσα συγκαλειται τας φιλας και τας γειτονας λεγουσα συγχαρητε μοι οτι ευρον την δραχμην ην απωλεσα');
INSERT INTO `bible_verses_tr` VALUES ('2454', '42', '15', '10', 'ουτως λεγω υμιν χαρα γινεται ενωπιον των αγγελων του θεου επι ενι αμαρτωλω μετανοουντι');
INSERT INTO `bible_verses_tr` VALUES ('2455', '42', '15', '11', 'ειπεν δε ανθρωπος τις ειχεν δυο υιους');
INSERT INTO `bible_verses_tr` VALUES ('2456', '42', '15', '12', 'και ειπεν ο νεωτερος αυτων τω πατρι πατερ δος μοι το επιβαλλον μερος της ουσιας και διειλεν αυτοις τον βιον');
INSERT INTO `bible_verses_tr` VALUES ('2457', '42', '15', '13', 'και μετ ου πολλας ημερας συναγαγων απαντα ο νεωτερος υιος απεδημησεν εις χωραν μακραν και εκει διεσκορπισεν την ουσιαν αυτου ζων ασωτως');
INSERT INTO `bible_verses_tr` VALUES ('2458', '42', '15', '14', 'δαπανησαντος δε αυτου παντα εγενετο λιμος ισχυρος κατα την χωραν εκεινην και αυτος ηρξατο υστερεισθαι');
INSERT INTO `bible_verses_tr` VALUES ('2459', '42', '15', '15', 'και πορευθεις εκολληθη ενι των πολιτων της χωρας εκεινης και επεμψεν αυτον εις τους αγρους αυτου βοσκειν χοιρους');
INSERT INTO `bible_verses_tr` VALUES ('2460', '42', '15', '16', 'και επεθυμει γεμισαι την κοιλιαν αυτου απο των κερατιων ων ησθιον οι χοιροι και ουδεις εδιδου αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2461', '42', '15', '17', 'εις εαυτον δε ελθων ειπεν ποσοι μισθιοι του πατρος μου περισσευουσιν αρτων εγω δε λιμω απολλυμαι');
INSERT INTO `bible_verses_tr` VALUES ('2462', '42', '15', '18', 'αναστας πορευσομαι προς τον πατερα μου και ερω αυτω πατερ ημαρτον εις τον ουρανον και ενωπιον σου');
INSERT INTO `bible_verses_tr` VALUES ('2463', '42', '15', '19', 'και ουκετι ειμι αξιος κληθηναι υιος σου ποιησον με ως ενα των μισθιων σου');
INSERT INTO `bible_verses_tr` VALUES ('2464', '42', '15', '20', 'και αναστας ηλθεν προς τον πατερα εαυτου ετι δε αυτου μακραν απεχοντος ειδεν αυτον ο πατηρ αυτου και εσπλαγχνισθη και δραμων επεπεσεν επι τον τραχηλον αυτου και κατεφιλησεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2465', '42', '15', '21', 'ειπεν δε αυτω ο υιος πατερ ημαρτον εις τον ουρανον και ενωπιον σου και ουκετι ειμι αξιος κληθηναι υιος σου');
INSERT INTO `bible_verses_tr` VALUES ('2466', '42', '15', '22', 'ειπεν δε ο πατηρ προς τους δουλους αυτου εξενεγκατε την στολην την πρωτην και ενδυσατε αυτον και δοτε δακτυλιον εις την χειρα αυτου και υποδηματα εις τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('2467', '42', '15', '23', 'και ενεγκαντες τον μοσχον τον σιτευτον θυσατε και φαγοντες ευφρανθωμεν');
INSERT INTO `bible_verses_tr` VALUES ('2468', '42', '15', '24', 'οτι ουτος ο υιος μου νεκρος ην και ανεζησεν και απολωλως ην και ευρεθη και ηρξαντο ευφραινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2469', '42', '15', '25', 'ην δε ο υιος αυτου ο πρεσβυτερος εν αγρω και ως ερχομενος ηγγισεν τη οικια ηκουσεν συμφωνιας και χορων');
INSERT INTO `bible_verses_tr` VALUES ('2470', '42', '15', '26', 'και προσκαλεσαμενος ενα των παιδων  {VAR1: αυτου } επυνθανετο τι ειη ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('2471', '42', '15', '27', 'ο δε ειπεν αυτω οτι ο αδελφος σου ηκει και εθυσεν ο πατηρ σου τον μοσχον τον σιτευτον οτι υγιαινοντα αυτον απελαβεν');
INSERT INTO `bible_verses_tr` VALUES ('2472', '42', '15', '28', 'ωργισθη δε και ουκ ηθελεν εισελθειν ο ουν πατηρ αυτου εξελθων παρεκαλει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2473', '42', '15', '29', 'ο δε αποκριθεις ειπεν τω πατρι ιδου τοσαυτα ετη δουλευω σοι και ουδεποτε εντολην σου παρηλθον και εμοι ουδεποτε εδωκας εριφον ινα μετα των φιλων μου ευφρανθω');
INSERT INTO `bible_verses_tr` VALUES ('2474', '42', '15', '30', 'οτε δε ο υιος σου ουτος ο καταφαγων σου τον βιον μετα πορνων ηλθεν εθυσας αυτω τον μοσχον τον σιτευτον');
INSERT INTO `bible_verses_tr` VALUES ('2475', '42', '15', '31', 'ο δε ειπεν αυτω τεκνον συ παντοτε μετ εμου ει και παντα τα εμα σα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2476', '42', '15', '32', 'ευφρανθηναι δε και χαρηναι εδει οτι ο αδελφος σου ουτος νεκρος ην και ανεζησεν και απολωλως ην και ευρεθη');
INSERT INTO `bible_verses_tr` VALUES ('2477', '42', '16', '1', 'ελεγεν δε και προς τους μαθητας αυτου ανθρωπος τις ην πλουσιος ος ειχεν οικονομον και ουτος διεβληθη αυτω ως διασκορπιζων τα υπαρχοντα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2478', '42', '16', '2', 'και φωνησας αυτον ειπεν αυτω τι τουτο ακουω περι σου αποδος τον λογον της οικονομιας σου ου γαρ δυνηση ετι οικονομειν');
INSERT INTO `bible_verses_tr` VALUES ('2479', '42', '16', '3', 'ειπεν δε εν εαυτω ο οικονομος τι ποιησω οτι ο κυριος μου αφαιρειται την οικονομιαν απ εμου σκαπτειν ουκ ισχυω επαιτειν αισχυνομαι');
INSERT INTO `bible_verses_tr` VALUES ('2480', '42', '16', '4', 'εγνων τι ποιησω ινα οταν μετασταθω της οικονομιας δεξωνται με εις τους οικους αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2481', '42', '16', '5', 'και προσκαλεσαμενος ενα εκαστον των χρεωφειλετων του κυριου εαυτου ελεγεν τω πρωτω ποσον οφειλεις τω κυριω μου');
INSERT INTO `bible_verses_tr` VALUES ('2482', '42', '16', '6', 'ο δε ειπεν εκατον βατους ελαιου και ειπεν αυτω δεξαι σου το γραμμα και καθισας ταχεως γραψον πεντηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('2483', '42', '16', '7', 'επειτα ετερω ειπεν συ δε ποσον οφειλεις ο δε ειπεν εκατον κορους σιτου και λεγει αυτω δεξαι σου το γραμμα και γραψον ογδοηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('2484', '42', '16', '8', 'και επηνεσεν ο κυριος τον οικονομον της αδικιας οτι φρονιμως εποιησεν οτι οι υιοι του αιωνος τουτου φρονιμωτεροι υπερ τους υιους του φωτος εις την γενεαν  {VAR1: την } εαυτων εισιν');
INSERT INTO `bible_verses_tr` VALUES ('2485', '42', '16', '9', 'καγω υμιν λεγω ποιησατε εαυτοις φιλους εκ του μαμωνα της αδικιας ινα οταν εκλιπητε δεξωνται υμας εις τας αιωνιους σκηνας');
INSERT INTO `bible_verses_tr` VALUES ('2486', '42', '16', '10', 'ο πιστος εν ελαχιστω και εν πολλω πιστος εστιν και ο εν ελαχιστω αδικος και εν πολλω αδικος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2487', '42', '16', '11', 'ει ουν εν τω αδικω μαμωνα πιστοι ουκ εγενεσθε το αληθινον τις υμιν πιστευσει');
INSERT INTO `bible_verses_tr` VALUES ('2488', '42', '16', '12', 'και ει εν τω αλλοτριω πιστοι ουκ εγενεσθε το υμετερον τις υμιν δωσει');
INSERT INTO `bible_verses_tr` VALUES ('2489', '42', '16', '13', 'ουδεις οικετης δυναται δυσι κυριοις δουλευειν η γαρ τον ενα μισησει και τον ετερον αγαπησει η ενος ανθεξεται και του ετερου καταφρονησει ου δυνασθε θεω δουλευειν και μαμωνα');
INSERT INTO `bible_verses_tr` VALUES ('2490', '42', '16', '14', 'ηκουον δε ταυτα παντα και οι φαρισαιοι φιλαργυροι υπαρχοντες και εξεμυκτηριζον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2491', '42', '16', '15', 'και ειπεν αυτοις υμεις εστε οι δικαιουντες εαυτους ενωπιον των ανθρωπων ο δε θεος γινωσκει τας καρδιας υμων οτι το εν ανθρωποις υψηλον βδελυγμα ενωπιον του θεου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2492', '42', '16', '16', 'ο νομος και οι προφηται εως ιωαννου απο τοτε η βασιλεια του θεου ευαγγελιζεται και πας εις αυτην βιαζεται');
INSERT INTO `bible_verses_tr` VALUES ('2493', '42', '16', '17', 'ευκοπωτερον δε εστιν τον ουρανον και την γην παρελθειν η του νομου μιαν κεραιαν πεσειν');
INSERT INTO `bible_verses_tr` VALUES ('2494', '42', '16', '18', 'πας ο απολυων την γυναικα αυτου και γαμων ετεραν μοιχευει και πας ο απολελυμενην απο ανδρος γαμων μοιχευει');
INSERT INTO `bible_verses_tr` VALUES ('2495', '42', '16', '19', 'ανθρωπος δε τις ην πλουσιος και ενεδιδυσκετο πορφυραν και βυσσον ευφραινομενος καθ ημεραν λαμπρως');
INSERT INTO `bible_verses_tr` VALUES ('2496', '42', '16', '20', 'πτωχος δε τις ην ονοματι λαζαρος ος εβεβλητο προς τον πυλωνα αυτου ηλκωμενος');
INSERT INTO `bible_verses_tr` VALUES ('2497', '42', '16', '21', 'και επιθυμων χορτασθηναι απο των ψιχιων των πιπτοντων απο της τραπεζης του πλουσιου αλλα και οι κυνες ερχομενοι απελειχον τα ελκη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2498', '42', '16', '22', 'εγενετο δε αποθανειν τον πτωχον και απενεχθηναι αυτον υπο των αγγελων εις τον κολπον του αβρααμ απεθανεν δε και ο πλουσιος και εταφη');
INSERT INTO `bible_verses_tr` VALUES ('2499', '42', '16', '23', 'και εν τω αδη επαρας τους οφθαλμους αυτου υπαρχων εν βασανοις ορα τον αβρααμ απο μακροθεν και λαζαρον εν τοις κολποις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2500', '42', '16', '24', 'και αυτος φωνησας ειπεν πατερ αβρααμ ελεησον με και πεμψον λαζαρον ινα βαψη το ακρον του δακτυλου αυτου υδατος και καταψυξη την γλωσσαν μου οτι οδυνωμαι εν τη φλογι ταυτη');
INSERT INTO `bible_verses_tr` VALUES ('2501', '42', '16', '25', 'ειπεν δε αβρααμ τεκνον μνησθητι οτι απελαβες συ τα αγαθα σου εν τη ζωη σου και λαζαρος ομοιως τα κακα νυν δε οδε παρακαλειται συ δε οδυνασαι');
INSERT INTO `bible_verses_tr` VALUES ('2502', '42', '16', '26', 'και επι πασιν τουτοις μεταξυ ημων και υμων χασμα μεγα εστηρικται οπως οι θελοντες διαβηναι εντευθεν προς υμας μη δυνωνται μηδε οι εκειθεν προς ημας διαπερωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2503', '42', '16', '27', 'ειπεν δε ερωτω ουν σε πατερ ινα πεμψης αυτον εις τον οικον του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('2504', '42', '16', '28', 'εχω γαρ πεντε αδελφους οπως διαμαρτυρηται αυτοις ινα μη και αυτοι ελθωσιν εις τον τοπον τουτον της βασανου');
INSERT INTO `bible_verses_tr` VALUES ('2505', '42', '16', '29', 'λεγει αυτω αβρααμ εχουσιν μωσεα και τους προφητας ακουσατωσαν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2506', '42', '16', '30', 'ο δε ειπεν ουχι πατερ αβρααμ αλλ εαν τις απο νεκρων πορευθη προς αυτους μετανοησουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2507', '42', '16', '31', 'ειπεν δε αυτω ει μωσεως και των προφητων ουκ ακουουσιν ουδε εαν τις εκ νεκρων αναστη πεισθησονται');
INSERT INTO `bible_verses_tr` VALUES ('2508', '42', '17', '1', 'ειπεν δε προς τους μαθητας ανενδεκτον εστιν του μη ελθειν τα σκανδαλα ουαι δε δι ου ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('2509', '42', '17', '2', 'λυσιτελει αυτω ει μυλος ονικος περικειται περι τον τραχηλον αυτου και ερριπται εις την θαλασσαν η ινα σκανδαλιση ενα των μικρων τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2510', '42', '17', '3', 'προσεχετε εαυτοις εαν δε αμαρτη εις σε ο αδελφος σου επιτιμησον αυτω και εαν μετανοηση αφες αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2511', '42', '17', '4', 'και εαν επτακις της ημερας αμαρτη εις σε και επτακις της ημερας επιστρεψη επι σε λεγων μετανοω αφησεις αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2512', '42', '17', '5', 'και ειπον οι αποστολοι τω κυριω προσθες ημιν πιστιν');
INSERT INTO `bible_verses_tr` VALUES ('2513', '42', '17', '6', 'ειπεν δε ο κυριος ει ειχετε πιστιν ως κοκκον σιναπεως ελεγετε αν τη συκαμινω ταυτη εκριζωθητι και φυτευθητι εν τη θαλασση και υπηκουσεν αν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2514', '42', '17', '7', 'τις δε εξ υμων δουλον εχων αροτριωντα η ποιμαινοντα ος εισελθοντι εκ του αγρου ερει ευθεως παρελθων αναπεσαι');
INSERT INTO `bible_verses_tr` VALUES ('2515', '42', '17', '8', 'αλλ ουχι ερει αυτω ετοιμασον τι δειπνησω και περιζωσαμενος διακονει μοι εως φαγω και πιω και μετα ταυτα φαγεσαι και πιεσαι συ');
INSERT INTO `bible_verses_tr` VALUES ('2516', '42', '17', '9', 'μη χαριν εχει τω δουλω εκεινω οτι εποιησεν τα διαταχθεντα αυτω ου δοκω');
INSERT INTO `bible_verses_tr` VALUES ('2517', '42', '17', '10', 'ουτως και υμεις οταν ποιησητε παντα τα διαταχθεντα υμιν λεγετε οτι δουλοι αχρειοι εσμεν οτι ο ωφειλομεν ποιησαι πεποιηκαμεν');
INSERT INTO `bible_verses_tr` VALUES ('2518', '42', '17', '11', 'και εγενετο εν τω πορευεσθαι αυτον εις ιερουσαλημ και αυτος διηρχετο δια μεσου σαμαρειας και γαλιλαιας');
INSERT INTO `bible_verses_tr` VALUES ('2519', '42', '17', '12', 'και εισερχομενου αυτου εις τινα κωμην απηντησαν αυτω δεκα λεπροι ανδρες οι εστησαν πορρωθεν');
INSERT INTO `bible_verses_tr` VALUES ('2520', '42', '17', '13', 'και αυτοι ηραν φωνην λεγοντες ιησου επιστατα ελεησον ημας');
INSERT INTO `bible_verses_tr` VALUES ('2521', '42', '17', '14', 'και ιδων ειπεν αυτοις πορευθεντες επιδειξατε εαυτους τοις ιερευσιν και εγενετο εν τω υπαγειν αυτους εκαθαρισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('2522', '42', '17', '15', 'εις δε εξ αυτων ιδων οτι ιαθη υπεστρεψεν μετα φωνης μεγαλης δοξαζων τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('2523', '42', '17', '16', 'και επεσεν επι προσωπον παρα τους ποδας αυτου ευχαριστων αυτω και αυτος ην σαμαρειτης');
INSERT INTO `bible_verses_tr` VALUES ('2524', '42', '17', '17', 'αποκριθεις δε ο ιησους ειπεν ουχι οι δεκα εκαθαρισθησαν οι δε εννεα που');
INSERT INTO `bible_verses_tr` VALUES ('2525', '42', '17', '18', 'ουχ ευρεθησαν υποστρεψαντες δουναι δοξαν τω θεω ει μη ο αλλογενης ουτος');
INSERT INTO `bible_verses_tr` VALUES ('2526', '42', '17', '19', 'και ειπεν αυτω αναστας πορευου η πιστις σου σεσωκεν σε');
INSERT INTO `bible_verses_tr` VALUES ('2527', '42', '17', '20', 'επερωτηθεις δε υπο των φαρισαιων ποτε ερχεται η βασιλεια του θεου απεκριθη αυτοις και ειπεν ουκ ερχεται η βασιλεια του θεου μετα παρατηρησεως');
INSERT INTO `bible_verses_tr` VALUES ('2528', '42', '17', '21', 'ουδε ερουσιν ιδου ωδε η ιδου εκει ιδου γαρ η βασιλεια του θεου εντος υμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2529', '42', '17', '22', 'ειπεν δε προς τους μαθητας ελευσονται ημεραι οτε επιθυμησετε μιαν των ημερων του υιου του ανθρωπου ιδειν και ουκ οψεσθε');
INSERT INTO `bible_verses_tr` VALUES ('2530', '42', '17', '23', 'και ερουσιν υμιν ιδου ωδε η ιδου εκει μη απελθητε μηδε διωξητε');
INSERT INTO `bible_verses_tr` VALUES ('2531', '42', '17', '24', 'ωσπερ γαρ η αστραπη η αστραπτουσα εκ της υπ ουρανον εις την υπ ουρανον λαμπει ουτως εσται και ο υιος του ανθρωπου εν τη ημερα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2532', '42', '17', '25', 'πρωτον δε δει αυτον πολλα παθειν και αποδοκιμασθηναι απο της γενεας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('2533', '42', '17', '26', 'και καθως εγενετο εν ταις ημεραις του νωε ουτως εσται και εν ταις ημεραις του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2534', '42', '17', '27', 'ησθιον επινον εγαμουν εξεγαμιζοντο αχρι ης ημερας εισηλθεν νωε εις την κιβωτον και ηλθεν ο κατακλυσμος και απωλεσεν απαντας');
INSERT INTO `bible_verses_tr` VALUES ('2535', '42', '17', '28', 'ομοιως και ως εγενετο εν ταις ημεραις λωτ ησθιον επινον ηγοραζον επωλουν εφυτευον ωκοδομουν');
INSERT INTO `bible_verses_tr` VALUES ('2536', '42', '17', '29', 'η δε ημερα εξηλθεν λωτ απο σοδομων εβρεξεν πυρ και θειον απ ουρανου και απωλεσεν απαντας');
INSERT INTO `bible_verses_tr` VALUES ('2537', '42', '17', '30', 'κατα ταυτα εσται η ημερα ο υιος του ανθρωπου αποκαλυπτεται');
INSERT INTO `bible_verses_tr` VALUES ('2538', '42', '17', '31', 'εν εκεινη τη ημερα ος εσται επι του δωματος και τα σκευη αυτου εν τη οικια μη καταβατω αραι αυτα και ο εν τω αγρω ομοιως μη επιστρεψατω εις τα οπισω');
INSERT INTO `bible_verses_tr` VALUES ('2539', '42', '17', '32', 'μνημονευετε της γυναικος λωτ');
INSERT INTO `bible_verses_tr` VALUES ('2540', '42', '17', '33', 'ος εαν ζητηση την ψυχην αυτου σωσαι απολεσει αυτην και ος εαν απολεση αυτην ζωογονησει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2541', '42', '17', '34', 'λεγω υμιν ταυτη τη νυκτι εσονται δυο επι κλινης μιας ο εις παραληφθησεται και ο ετερος αφεθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2542', '42', '17', '35', 'δυο εσονται αληθουσαι επι το αυτο  {VAR2: η } μια παραληφθησεται και η ετερα αφεθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2543', '42', '17', '36', '{VAR2: δυο εσονται εν τω αγρω ο εις παραληφθησεται και ο ετερος αφεθησεται }');
INSERT INTO `bible_verses_tr` VALUES ('2544', '42', '17', '37', 'και αποκριθεντες λεγουσιν αυτω που κυριε ο δε ειπεν αυτοις οπου το σωμα εκει συναχθησονται οι αετοι');
INSERT INTO `bible_verses_tr` VALUES ('2545', '42', '18', '1', 'ελεγεν δε και παραβολην αυτοις προς το δειν παντοτε προσευχεσθαι και μη εκκακειν');
INSERT INTO `bible_verses_tr` VALUES ('2546', '42', '18', '2', 'λεγων κριτης τις ην εν τινι πολει τον θεον μη φοβουμενος και ανθρωπον μη εντρεπομενος');
INSERT INTO `bible_verses_tr` VALUES ('2547', '42', '18', '3', 'χηρα δε ην εν τη πολει εκεινη και ηρχετο προς αυτον λεγουσα εκδικησον με απο του αντιδικου μου');
INSERT INTO `bible_verses_tr` VALUES ('2548', '42', '18', '4', 'και ουκ ηθελησεν επι χρονον μετα δε ταυτα ειπεν εν εαυτω ει και τον θεον ου φοβουμαι και ανθρωπον ουκ εντρεπομαι');
INSERT INTO `bible_verses_tr` VALUES ('2549', '42', '18', '5', 'δια γε το παρεχειν μοι κοπον την χηραν ταυτην εκδικησω αυτην ινα μη εις τελος ερχομενη υπωπιαζη με');
INSERT INTO `bible_verses_tr` VALUES ('2550', '42', '18', '6', 'ειπεν δε ο κυριος ακουσατε τι ο κριτης της αδικιας λεγει');
INSERT INTO `bible_verses_tr` VALUES ('2551', '42', '18', '7', 'ο δε θεος ου μη ποιησει την εκδικησιν των εκλεκτων αυτου των βοωντων προς αυτον ημερας και νυκτος και μακροθυμων επ αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2552', '42', '18', '8', 'λεγω υμιν οτι ποιησει την εκδικησιν αυτων εν ταχει πλην ο υιος του ανθρωπου ελθων αρα ευρησει την πιστιν επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('2553', '42', '18', '9', 'ειπεν δε και προς τινας τους πεποιθοτας εφ εαυτοις οτι εισιν δικαιοι και εξουθενουντας τους λοιπους την παραβολην ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('2554', '42', '18', '10', 'ανθρωποι δυο ανεβησαν εις το ιερον προσευξασθαι ο εις φαρισαιος και ο ετερος τελωνης');
INSERT INTO `bible_verses_tr` VALUES ('2555', '42', '18', '11', 'ο φαρισαιος σταθεις προς εαυτον ταυτα προσηυχετο ο θεος ευχαριστω σοι οτι ουκ ειμι ωσπερ οι λοιποι των ανθρωπων αρπαγες αδικοι μοιχοι η και ως ουτος ο τελωνης');
INSERT INTO `bible_verses_tr` VALUES ('2556', '42', '18', '12', 'νηστευω δις του σαββατου αποδεκατω παντα οσα κτωμαι');
INSERT INTO `bible_verses_tr` VALUES ('2557', '42', '18', '13', 'και ο τελωνης μακροθεν εστως ουκ ηθελεν ουδε τους οφθαλμους εις τον ουρανον επαραι αλλ ετυπτεν εις το στηθος αυτου λεγων ο θεος ιλασθητι μοι τω αμαρτωλω');
INSERT INTO `bible_verses_tr` VALUES ('2558', '42', '18', '14', 'λεγω υμιν κατεβη ουτος δεδικαιωμενος εις τον οικον αυτου η εκεινος οτι πας ο υψων εαυτον ταπεινωθησεται ο δε ταπεινων εαυτον υψωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2559', '42', '18', '15', 'προσεφερον δε αυτω και τα βρεφη ινα αυτων απτηται ιδοντες δε οι μαθηται επετιμησαν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2560', '42', '18', '16', 'ο δε ιησους προσκαλεσαμενος αυτα ειπεν αφετε τα παιδια ερχεσθαι προς με και μη κωλυετε αυτα των γαρ τοιουτων εστιν η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2561', '42', '18', '17', 'αμην λεγω υμιν ος εαν μη δεξηται την βασιλειαν του θεου ως παιδιον ου μη εισελθη εις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2562', '42', '18', '18', 'και επηρωτησεν τις αυτον αρχων λεγων διδασκαλε αγαθε τι ποιησας ζωην αιωνιον κληρονομησω');
INSERT INTO `bible_verses_tr` VALUES ('2563', '42', '18', '19', 'ειπεν δε αυτω ο ιησους τι με λεγεις αγαθον ουδεις αγαθος ει μη εις ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('2564', '42', '18', '20', 'τας εντολας οιδας μη μοιχευσης μη φονευσης μη κλεψης μη ψευδομαρτυρησης τιμα τον πατερα σου και την μητερα σου');
INSERT INTO `bible_verses_tr` VALUES ('2565', '42', '18', '21', 'ο δε ειπεν ταυτα παντα εφυλαξαμην εκ νεοτητος μου');
INSERT INTO `bible_verses_tr` VALUES ('2566', '42', '18', '22', 'ακουσας δε ταυτα ο ιησους ειπεν αυτω ετι εν σοι λειπει παντα οσα εχεις πωλησον και διαδος πτωχοις και εξεις θησαυρον εν ουρανω και δευρο ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('2567', '42', '18', '23', 'ο δε ακουσας ταυτα περιλυπος εγενετο ην γαρ πλουσιος σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('2568', '42', '18', '24', 'ιδων δε αυτον ο ιησους περιλυπον γενομενον ειπεν πως δυσκολως οι τα χρηματα εχοντες εισελευσονται εις την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2569', '42', '18', '25', 'ευκοπωτερον γαρ εστιν καμηλον δια τρυμαλιας ραφιδος εισελθειν η πλουσιον εις την βασιλειαν του θεου εισελθειν');
INSERT INTO `bible_verses_tr` VALUES ('2570', '42', '18', '26', 'ειπον δε οι ακουσαντες και τις δυναται σωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2571', '42', '18', '27', 'ο δε ειπεν τα αδυνατα παρα ανθρωποις δυνατα εστιν παρα τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('2572', '42', '18', '28', 'ειπεν δε ο πετρος ιδου ημεις αφηκαμεν παντα και ηκολουθησαμεν σοι');
INSERT INTO `bible_verses_tr` VALUES ('2573', '42', '18', '29', 'ο δε ειπεν αυτοις αμην λεγω υμιν οτι ουδεις εστιν ος αφηκεν οικιαν η γονεις η αδελφους η γυναικα η τεκνα ενεκεν της βασιλειας του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2574', '42', '18', '30', 'ος ου μη απολαβη πολλαπλασιονα εν τω καιρω τουτω και εν τω αιωνι τω ερχομενω ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('2575', '42', '18', '31', 'παραλαβων δε τους δωδεκα ειπεν προς αυτους ιδου αναβαινομεν εις ιεροσολυμα και τελεσθησεται παντα τα γεγραμμενα δια των προφητων τω υιω του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2576', '42', '18', '32', 'παραδοθησεται γαρ τοις εθνεσιν και εμπαιχθησεται και υβρισθησεται και εμπτυσθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2577', '42', '18', '33', 'και μαστιγωσαντες αποκτενουσιν αυτον και τη ημερα τη τριτη αναστησεται');
INSERT INTO `bible_verses_tr` VALUES ('2578', '42', '18', '34', 'και αυτοι ουδεν τουτων συνηκαν και ην το ρημα τουτο κεκρυμμενον απ αυτων και ουκ εγινωσκον τα λεγομενα');
INSERT INTO `bible_verses_tr` VALUES ('2579', '42', '18', '35', 'εγενετο δε εν τω εγγιζειν αυτον εις ιεριχω τυφλος τις εκαθητο παρα την οδον προσαιτων');
INSERT INTO `bible_verses_tr` VALUES ('2580', '42', '18', '36', 'ακουσας δε οχλου διαπορευομενου επυνθανετο τι ειη τουτο');
INSERT INTO `bible_verses_tr` VALUES ('2581', '42', '18', '37', 'απηγγειλαν δε αυτω οτι ιησους ο ναζωραιος παρερχεται');
INSERT INTO `bible_verses_tr` VALUES ('2582', '42', '18', '38', 'και εβοησεν λεγων ιησου υιε δαβιδ ελεησον με');
INSERT INTO `bible_verses_tr` VALUES ('2583', '42', '18', '39', 'και οι προαγοντες επετιμων αυτω ινα σιωπηση αυτος δε πολλω μαλλον εκραζεν υιε δαβιδ ελεησον με');
INSERT INTO `bible_verses_tr` VALUES ('2584', '42', '18', '40', 'σταθεις δε ο ιησους εκελευσεν αυτον αχθηναι προς αυτον εγγισαντος δε αυτου επηρωτησεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2585', '42', '18', '41', 'λεγων τι σοι θελεις ποιησω ο δε ειπεν κυριε ινα αναβλεψω');
INSERT INTO `bible_verses_tr` VALUES ('2586', '42', '18', '42', 'και ο ιησους ειπεν αυτω αναβλεψον η πιστις σου σεσωκεν σε');
INSERT INTO `bible_verses_tr` VALUES ('2587', '42', '18', '43', 'και παραχρημα ανεβλεψεν και ηκολουθει αυτω δοξαζων τον θεον και πας ο λαος ιδων εδωκεν αινον τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('2588', '42', '19', '1', 'και εισελθων διηρχετο την ιεριχω');
INSERT INTO `bible_verses_tr` VALUES ('2589', '42', '19', '2', 'και ιδου ανηρ ονοματι καλουμενος ζακχαιος και αυτος ην αρχιτελωνης και ουτος ην πλουσιος');
INSERT INTO `bible_verses_tr` VALUES ('2590', '42', '19', '3', 'και εζητει ιδειν τον ιησουν τις εστιν και ουκ ηδυνατο απο του οχλου οτι τη ηλικια μικρος ην');
INSERT INTO `bible_verses_tr` VALUES ('2591', '42', '19', '4', 'και προδραμων εμπροσθεν ανεβη επι συκομωραιαν ινα ιδη αυτον οτι δι εκεινης ημελλεν διερχεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2592', '42', '19', '5', 'και ως ηλθεν επι τον τοπον αναβλεψας ο ιησους ειδεν αυτον και ειπεν προς αυτον ζακχαιε σπευσας καταβηθι σημερον γαρ εν τω οικω σου δει με μειναι');
INSERT INTO `bible_verses_tr` VALUES ('2593', '42', '19', '6', 'και σπευσας κατεβη και υπεδεξατο αυτον χαιρων');
INSERT INTO `bible_verses_tr` VALUES ('2594', '42', '19', '7', 'και ιδοντες απαντες διεγογγυζον λεγοντες οτι παρα αμαρτωλω ανδρι εισηλθεν καταλυσαι');
INSERT INTO `bible_verses_tr` VALUES ('2595', '42', '19', '8', 'σταθεις δε ζακχαιος ειπεν προς τον κυριον ιδου τα ημιση των υπαρχοντων μου κυριε διδωμι τοις πτωχοις και ει τινος τι εσυκοφαντησα αποδιδωμι τετραπλουν');
INSERT INTO `bible_verses_tr` VALUES ('2596', '42', '19', '9', 'ειπεν δε προς αυτον ο ιησους οτι σημερον σωτηρια τω οικω τουτω εγενετο καθοτι και αυτος υιος αβρααμ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2597', '42', '19', '10', 'ηλθεν γαρ ο υιος του ανθρωπου ζητησαι και σωσαι το απολωλος');
INSERT INTO `bible_verses_tr` VALUES ('2598', '42', '19', '11', 'ακουοντων δε αυτων ταυτα προσθεις ειπεν παραβολην δια το εγγυς αυτον ειναι ιερουσαλημ και δοκειν αυτους οτι παραχρημα μελλει η βασιλεια του θεου αναφαινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2599', '42', '19', '12', 'ειπεν ουν ανθρωπος τις ευγενης επορευθη εις χωραν μακραν λαβειν εαυτω βασιλειαν και υποστρεψαι');
INSERT INTO `bible_verses_tr` VALUES ('2600', '42', '19', '13', 'καλεσας δε δεκα δουλους εαυτου εδωκεν αυτοις δεκα μνας και ειπεν προς αυτους πραγματευσασθε εως ερχομαι');
INSERT INTO `bible_verses_tr` VALUES ('2601', '42', '19', '14', 'οι δε πολιται αυτου εμισουν αυτον και απεστειλαν πρεσβειαν οπισω αυτου λεγοντες ου θελομεν τουτον βασιλευσαι εφ ημας');
INSERT INTO `bible_verses_tr` VALUES ('2602', '42', '19', '15', 'και εγενετο εν τω επανελθειν αυτον λαβοντα την βασιλειαν και ειπεν φωνηθηναι αυτω τους δουλους τουτους οις εδωκεν το αργυριον ινα γνω τις τι διεπραγματευσατο');
INSERT INTO `bible_verses_tr` VALUES ('2603', '42', '19', '16', 'παρεγενετο δε ο πρωτος λεγων κυριε η μνα σου προσειργασατο δεκα μνας');
INSERT INTO `bible_verses_tr` VALUES ('2604', '42', '19', '17', 'και ειπεν αυτω ευ αγαθε δουλε οτι εν ελαχιστω πιστος εγενου ισθι εξουσιαν εχων επανω δεκα πολεων');
INSERT INTO `bible_verses_tr` VALUES ('2605', '42', '19', '18', 'και ηλθεν ο δευτερος λεγων κυριε η μνα σου εποιησεν πεντε μνας');
INSERT INTO `bible_verses_tr` VALUES ('2606', '42', '19', '19', 'ειπεν δε και τουτω και συ γινου επανω πεντε πολεων');
INSERT INTO `bible_verses_tr` VALUES ('2607', '42', '19', '20', 'και ετερος ηλθεν λεγων κυριε ιδου η μνα σου ην ειχον αποκειμενην εν σουδαριω');
INSERT INTO `bible_verses_tr` VALUES ('2608', '42', '19', '21', 'εφοβουμην γαρ σε οτι ανθρωπος αυστηρος ει αιρεις ο ουκ εθηκας και θεριζεις ο ουκ εσπειρας');
INSERT INTO `bible_verses_tr` VALUES ('2609', '42', '19', '22', 'λεγει δε αυτω εκ του στοματος σου κρινω σε πονηρε δουλε ηδεις οτι εγω ανθρωπος αυστηρος ειμι αιρων ο ουκ εθηκα και θεριζων ο ουκ εσπειρα');
INSERT INTO `bible_verses_tr` VALUES ('2610', '42', '19', '23', 'και δια τι ουκ εδωκας το αργυριον μου επι την τραπεζαν και εγω ελθων συν τοκω αν επραξα αυτο');
INSERT INTO `bible_verses_tr` VALUES ('2611', '42', '19', '24', 'και τοις παρεστωσιν ειπεν αρατε απ αυτου την μναν και δοτε τω τας δεκα μνας εχοντι');
INSERT INTO `bible_verses_tr` VALUES ('2612', '42', '19', '25', 'και ειπον αυτω κυριε εχει δεκα μνας');
INSERT INTO `bible_verses_tr` VALUES ('2613', '42', '19', '26', 'λεγω γαρ υμιν οτι παντι τω εχοντι δοθησεται απο δε του μη εχοντος και ο εχει αρθησεται απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2614', '42', '19', '27', 'πλην τους εχθρους μου εκεινους τους μη θελησαντας με βασιλευσαι επ αυτους αγαγετε ωδε και κατασφαξατε εμπροσθεν μου');
INSERT INTO `bible_verses_tr` VALUES ('2615', '42', '19', '28', 'και ειπων ταυτα επορευετο εμπροσθεν αναβαινων εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('2616', '42', '19', '29', 'και εγενετο ως ηγγισεν εις βηθφαγη και βηθανιαν προς το ορος το καλουμενον ελαιων απεστειλεν δυο των μαθητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2617', '42', '19', '30', 'ειπων υπαγετε εις την κατεναντι κωμην εν η εισπορευομενοι ευρησετε πωλον δεδεμενον εφ ον ουδεις πωποτε ανθρωπων εκαθισεν λυσαντες αυτον αγαγετε');
INSERT INTO `bible_verses_tr` VALUES ('2618', '42', '19', '31', 'και εαν τις υμας ερωτα δια τι λυετε ουτως ερειτε αυτω οτι ο κυριος αυτου χρειαν εχει');
INSERT INTO `bible_verses_tr` VALUES ('2619', '42', '19', '32', 'απελθοντες δε οι απεσταλμενοι ευρον καθως ειπεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2620', '42', '19', '33', 'λυοντων δε αυτων τον πωλον ειπον οι κυριοι αυτου προς αυτους τι λυετε τον πωλον');
INSERT INTO `bible_verses_tr` VALUES ('2621', '42', '19', '34', 'οι δε ειπον ο κυριος αυτου χρειαν εχει');
INSERT INTO `bible_verses_tr` VALUES ('2622', '42', '19', '35', 'και ηγαγον αυτον προς τον ιησουν και επιρριψαντες εαυτων τα ιματια επι τον πωλον επεβιβασαν τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('2623', '42', '19', '36', 'πορευομενου δε αυτου υπεστρωννυον τα ιματια αυτων εν τη οδω');
INSERT INTO `bible_verses_tr` VALUES ('2624', '42', '19', '37', 'εγγιζοντος δε αυτου ηδη προς τη καταβασει του ορους των ελαιων ηρξαντο απαν το πληθος των μαθητων χαιροντες αινειν τον θεον φωνη μεγαλη περι πασων ων ειδον δυναμεων');
INSERT INTO `bible_verses_tr` VALUES ('2625', '42', '19', '38', 'λεγοντες ευλογημενος ο ερχομενος βασιλευς εν ονοματι κυριου ειρηνη εν ουρανω και δοξα εν υψιστοις');
INSERT INTO `bible_verses_tr` VALUES ('2626', '42', '19', '39', 'και τινες των φαρισαιων απο του οχλου ειπον προς αυτον διδασκαλε επιτιμησον τοις μαθηταις σου');
INSERT INTO `bible_verses_tr` VALUES ('2627', '42', '19', '40', 'και αποκριθεις ειπεν αυτοις λεγω υμιν οτι εαν ουτοι σιωπησωσιν οι λιθοι κεκραξονται');
INSERT INTO `bible_verses_tr` VALUES ('2628', '42', '19', '41', 'και ως ηγγισεν ιδων την πολιν εκλαυσεν επ αυτη');
INSERT INTO `bible_verses_tr` VALUES ('2629', '42', '19', '42', 'λεγων οτι ει εγνως και συ και γε εν τη ημερα σου ταυτη τα προς ειρηνην σου νυν δε εκρυβη απο οφθαλμων σου');
INSERT INTO `bible_verses_tr` VALUES ('2630', '42', '19', '43', 'οτι ηξουσιν ημεραι επι σε και περιβαλουσιν οι εχθροι σου χαρακα σοι και περικυκλωσουσιν σε και συνεξουσιν σε παντοθεν');
INSERT INTO `bible_verses_tr` VALUES ('2631', '42', '19', '44', 'και εδαφιουσιν σε και τα τεκνα σου εν σοι και ουκ αφησουσιν εν σοι λιθον επι λιθω ανθ ων ουκ εγνως τον καιρον της επισκοπης σου');
INSERT INTO `bible_verses_tr` VALUES ('2632', '42', '19', '45', 'και εισελθων εις το ιερον ηρξατο εκβαλλειν τους πωλουντας εν αυτω και αγοραζοντας');
INSERT INTO `bible_verses_tr` VALUES ('2633', '42', '19', '46', 'λεγων αυτοις γεγραπται ο οικος μου οικος προσευχης εστιν υμεις δε αυτον εποιησατε σπηλαιον ληστων');
INSERT INTO `bible_verses_tr` VALUES ('2634', '42', '19', '47', 'και ην διδασκων το καθ ημεραν εν τω ιερω οι δε αρχιερεις και οι γραμματεις εζητουν αυτον απολεσαι και οι πρωτοι του λαου');
INSERT INTO `bible_verses_tr` VALUES ('2635', '42', '19', '48', 'και ουχ ευρισκον το τι ποιησωσιν ο λαος γαρ απας εξεκρεματο αυτου ακουων');
INSERT INTO `bible_verses_tr` VALUES ('2636', '42', '20', '1', 'και εγενετο εν μια των ημερων εκεινων διδασκοντος αυτου τον λαον εν τω ιερω και ευαγγελιζομενου επεστησαν οι αρχιερεις και οι γραμματεις συν τοις πρεσβυτεροις');
INSERT INTO `bible_verses_tr` VALUES ('2637', '42', '20', '2', 'και ειπον προς αυτον λεγοντες ειπε ημιν εν ποια εξουσια ταυτα ποιεις η τις εστιν ο δους σοι την εξουσιαν ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('2638', '42', '20', '3', 'αποκριθεις δε ειπεν προς αυτους ερωτησω υμας καγω ενα λογον και ειπατε μοι');
INSERT INTO `bible_verses_tr` VALUES ('2639', '42', '20', '4', 'το βαπτισμα ιωαννου εξ ουρανου ην η εξ ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('2640', '42', '20', '5', 'οι δε συνελογισαντο προς εαυτους λεγοντες οτι εαν ειπωμεν εξ ουρανου ερει δια τι ουν ουκ επιστευσατε αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2641', '42', '20', '6', 'εαν δε ειπωμεν εξ ανθρωπων πας ο λαος καταλιθασει ημας πεπεισμενος γαρ εστιν ιωαννην προφητην ειναι');
INSERT INTO `bible_verses_tr` VALUES ('2642', '42', '20', '7', 'και απεκριθησαν μη ειδεναι ποθεν');
INSERT INTO `bible_verses_tr` VALUES ('2643', '42', '20', '8', 'και ο ιησους ειπεν αυτοις ουδε εγω λεγω υμιν εν ποια εξουσια ταυτα ποιω');
INSERT INTO `bible_verses_tr` VALUES ('2644', '42', '20', '9', 'ηρξατο δε προς τον λαον λεγειν την παραβολην ταυτην ανθρωπος τις εφυτευσεν αμπελωνα και εξεδοτο αυτον γεωργοις και απεδημησεν χρονους ικανους');
INSERT INTO `bible_verses_tr` VALUES ('2645', '42', '20', '10', 'και εν καιρω απεστειλεν προς τους γεωργους δουλον ινα απο του καρπου του αμπελωνος δωσιν αυτω οι δε γεωργοι δειραντες αυτον εξαπεστειλαν κενον');
INSERT INTO `bible_verses_tr` VALUES ('2646', '42', '20', '11', 'και προσεθετο πεμψαι ετερον δουλον οι δε κακεινον δειραντες και ατιμασαντες εξαπεστειλαν κενον');
INSERT INTO `bible_verses_tr` VALUES ('2647', '42', '20', '12', 'και προσεθετο πεμψαι τριτον οι δε και τουτον τραυματισαντες εξεβαλον');
INSERT INTO `bible_verses_tr` VALUES ('2648', '42', '20', '13', 'ειπεν δε ο κυριος του αμπελωνος τι ποιησω πεμψω τον υιον μου τον αγαπητον ισως τουτον ιδοντες εντραπησονται');
INSERT INTO `bible_verses_tr` VALUES ('2649', '42', '20', '14', 'ιδοντες δε αυτον οι γεωργοι διελογιζοντο προς εαυτους λεγοντες ουτος εστιν ο κληρονομος δευτε αποκτεινωμεν αυτον ινα ημων γενηται η κληρονομια');
INSERT INTO `bible_verses_tr` VALUES ('2650', '42', '20', '15', 'και εκβαλοντες αυτον εξω του αμπελωνος απεκτειναν τι ουν ποιησει αυτοις ο κυριος του αμπελωνος');
INSERT INTO `bible_verses_tr` VALUES ('2651', '42', '20', '16', 'ελευσεται και απολεσει τους γεωργους τουτους και δωσει τον αμπελωνα αλλοις ακουσαντες δε ειπον μη γενοιτο');
INSERT INTO `bible_verses_tr` VALUES ('2652', '42', '20', '17', 'ο δε εμβλεψας αυτοις ειπεν τι ουν εστιν το γεγραμμενον τουτο λιθον ον απεδοκιμασαν οι οικοδομουντες ουτος εγενηθη εις κεφαλην γωνιας');
INSERT INTO `bible_verses_tr` VALUES ('2653', '42', '20', '18', 'πας ο πεσων επ εκεινον τον λιθον συνθλασθησεται εφ ον δ αν πεση λικμησει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2654', '42', '20', '19', 'και εζητησαν οι αρχιερεις και οι γραμματεις επιβαλειν επ αυτον τας χειρας εν αυτη τη ωρα και εφοβηθησαν τον λαον εγνωσαν γαρ οτι προς αυτους την παραβολην ταυτην ειπεν');
INSERT INTO `bible_verses_tr` VALUES ('2655', '42', '20', '20', 'και παρατηρησαντες απεστειλαν εγκαθετους υποκρινομενους εαυτους δικαιους ειναι ινα επιλαβωνται αυτου λογου εις το παραδουναι αυτον τη αρχη και τη εξουσια του ηγεμονος');
INSERT INTO `bible_verses_tr` VALUES ('2656', '42', '20', '21', 'και επηρωτησαν αυτον λεγοντες διδασκαλε οιδαμεν οτι ορθως λεγεις και διδασκεις και ου λαμβανεις προσωπον αλλ επ αληθειας την οδον του θεου διδασκεις');
INSERT INTO `bible_verses_tr` VALUES ('2657', '42', '20', '22', 'εξεστιν ημιν καισαρι φορον δουναι η ου');
INSERT INTO `bible_verses_tr` VALUES ('2658', '42', '20', '23', 'κατανοησας δε αυτων την πανουργιαν ειπεν προς αυτους τι με πειραζετε');
INSERT INTO `bible_verses_tr` VALUES ('2659', '42', '20', '24', 'επιδειξατε μοι δηναριον τινος εχει εικονα και επιγραφην αποκριθεντες δε ειπον καισαρος');
INSERT INTO `bible_verses_tr` VALUES ('2660', '42', '20', '25', 'ο δε ειπεν αυτοις αποδοτε τοινυν τα καισαρος καισαρι και τα του θεου τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('2661', '42', '20', '26', 'και ουκ ισχυσαν επιλαβεσθαι αυτου ρηματος εναντιον του λαου και θαυμασαντες επι τη αποκρισει αυτου εσιγησαν');
INSERT INTO `bible_verses_tr` VALUES ('2662', '42', '20', '27', 'προσελθοντες δε τινες των σαδδουκαιων οι αντιλεγοντες αναστασιν μη ειναι επηρωτησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2663', '42', '20', '28', 'λεγοντες διδασκαλε μωσης εγραψεν ημιν εαν τινος αδελφος αποθανη εχων γυναικα και ουτος ατεκνος αποθανη ινα λαβη ο αδελφος αυτου την γυναικα και εξαναστηση σπερμα τω αδελφω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2664', '42', '20', '29', 'επτα ουν αδελφοι ησαν και ο πρωτος λαβων γυναικα απεθανεν ατεκνος');
INSERT INTO `bible_verses_tr` VALUES ('2665', '42', '20', '30', 'και ελαβεν ο δευτερος την γυναικα και ουτος απεθανεν ατεκνος');
INSERT INTO `bible_verses_tr` VALUES ('2666', '42', '20', '31', 'και ο τριτος ελαβεν αυτην ωσαυτως δε και οι επτα  {VAR2: και } ου κατελιπον τεκνα και απεθανον');
INSERT INTO `bible_verses_tr` VALUES ('2667', '42', '20', '32', 'υστερον  {VAR1: δε } παντων απεθανεν και η γυνη');
INSERT INTO `bible_verses_tr` VALUES ('2668', '42', '20', '33', 'εν τη ουν αναστασει τινος αυτων γινεται γυνη οι γαρ επτα εσχον αυτην γυναικα');
INSERT INTO `bible_verses_tr` VALUES ('2669', '42', '20', '34', 'και αποκριθεις ειπεν αυτοις ο ιησους οι υιοι του αιωνος τουτου γαμουσιν και εκγαμισκονται');
INSERT INTO `bible_verses_tr` VALUES ('2670', '42', '20', '35', 'οι δε καταξιωθεντες του αιωνος εκεινου τυχειν και της αναστασεως της εκ νεκρων ουτε γαμουσιν ουτε εκγαμισκονται');
INSERT INTO `bible_verses_tr` VALUES ('2671', '42', '20', '36', 'ουτε γαρ αποθανειν ετι δυνανται ισαγγελοι γαρ εισιν και υιοι εισιν του θεου της αναστασεως υιοι οντες');
INSERT INTO `bible_verses_tr` VALUES ('2672', '42', '20', '37', 'οτι δε εγειρονται οι νεκροι και μωσης εμηνυσεν επι της βατου ως λεγει κυριον τον θεον αβρααμ και τον θεον ισαακ και τον θεον ιακωβ');
INSERT INTO `bible_verses_tr` VALUES ('2673', '42', '20', '38', 'θεος δε ουκ εστιν νεκρων αλλα ζωντων παντες γαρ αυτω ζωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2674', '42', '20', '39', 'αποκριθεντες δε τινες των γραμματεων ειπον διδασκαλε καλως ειπας');
INSERT INTO `bible_verses_tr` VALUES ('2675', '42', '20', '40', 'ουκετι δε ετολμων επερωταν αυτον ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('2676', '42', '20', '41', 'ειπεν δε προς αυτους πως λεγουσιν τον χριστον υιον δαβιδ ειναι');
INSERT INTO `bible_verses_tr` VALUES ('2677', '42', '20', '42', 'και αυτος δαβιδ λεγει εν βιβλω ψαλμων ειπεν ο κυριος τω κυριω μου καθου εκ δεξιων μου');
INSERT INTO `bible_verses_tr` VALUES ('2678', '42', '20', '43', 'εως αν θω τους εχθρους σου υποποδιον των ποδων σου');
INSERT INTO `bible_verses_tr` VALUES ('2679', '42', '20', '44', 'δαβιδ ουν κυριον αυτον καλει και πως υιος αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2680', '42', '20', '45', 'ακουοντος δε παντος του λαου ειπεν τοις μαθηταις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2681', '42', '20', '46', 'προσεχετε απο των γραμματεων των θελοντων περιπατειν εν στολαις και φιλουντων ασπασμους εν ταις αγοραις και πρωτοκαθεδριας εν ταις συναγωγαις και πρωτοκλισιας εν τοις δειπνοις');
INSERT INTO `bible_verses_tr` VALUES ('2682', '42', '20', '47', 'οι κατεσθιουσιν τας οικιας των χηρων και προφασει μακρα προσευχονται ουτοι ληψονται περισσοτερον κριμα');
INSERT INTO `bible_verses_tr` VALUES ('2683', '42', '21', '1', 'αναβλεψας δε ειδεν τους βαλλοντας τα δωρα αυτων εις το γαζοφυλακιον πλουσιους');
INSERT INTO `bible_verses_tr` VALUES ('2684', '42', '21', '2', 'ειδεν δε και τινα χηραν πενιχραν βαλλουσαν εκει δυο λεπτα');
INSERT INTO `bible_verses_tr` VALUES ('2685', '42', '21', '3', 'και ειπεν αληθως λεγω υμιν οτι η χηρα η πτωχη αυτη πλειον παντων εβαλεν');
INSERT INTO `bible_verses_tr` VALUES ('2686', '42', '21', '4', 'απαντες γαρ ουτοι εκ του περισσευοντος αυτοις εβαλον εις τα δωρα του θεου αυτη δε εκ του υστερηματος αυτης απαντα τον βιον ον ειχεν εβαλεν');
INSERT INTO `bible_verses_tr` VALUES ('2687', '42', '21', '5', 'και τινων λεγοντων περι του ιερου οτι λιθοις καλοις και αναθημασιν κεκοσμηται ειπεν');
INSERT INTO `bible_verses_tr` VALUES ('2688', '42', '21', '6', 'ταυτα α θεωρειτε ελευσονται ημεραι εν αις ουκ αφεθησεται λιθος επι λιθω ος ου καταλυθησεται');
INSERT INTO `bible_verses_tr` VALUES ('2689', '42', '21', '7', 'επηρωτησαν δε αυτον λεγοντες διδασκαλε ποτε ουν ταυτα εσται και τι το σημειον οταν μελλη ταυτα γινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2690', '42', '21', '8', 'ο δε ειπεν βλεπετε μη πλανηθητε πολλοι γαρ ελευσονται επι τω ονοματι μου λεγοντες οτι εγω ειμι και ο καιρος ηγγικεν μη ουν πορευθητε οπισω αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2691', '42', '21', '9', 'οταν δε ακουσητε πολεμους και ακαταστασιας μη πτοηθητε δει γαρ ταυτα γενεσθαι πρωτον αλλ ουκ ευθεως το τελος');
INSERT INTO `bible_verses_tr` VALUES ('2692', '42', '21', '10', 'τοτε ελεγεν αυτοις εγερθησεται εθνος επι εθνος και βασιλεια επι βασιλειαν');
INSERT INTO `bible_verses_tr` VALUES ('2693', '42', '21', '11', 'σεισμοι τε μεγαλοι κατα τοπους και λιμοι και λοιμοι εσονται φοβητρα τε και σημεια απ ουρανου μεγαλα εσται');
INSERT INTO `bible_verses_tr` VALUES ('2694', '42', '21', '12', 'προ δε τουτων απαντων επιβαλουσιν εφ υμας τας χειρας αυτων και διωξουσιν παραδιδοντες εις συναγωγας και φυλακας αγομενους επι βασιλεις και ηγεμονας ενεκεν του ονοματος μου');
INSERT INTO `bible_verses_tr` VALUES ('2695', '42', '21', '13', 'αποβησεται δε υμιν εις μαρτυριον');
INSERT INTO `bible_verses_tr` VALUES ('2696', '42', '21', '14', 'θεσθε ουν εις τας καρδιας υμων μη προμελεταν απολογηθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2697', '42', '21', '15', 'εγω γαρ δωσω υμιν στομα και σοφιαν η ου δυνησονται αντειπειν ουδε αντιστηναι παντες οι αντικειμενοι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2698', '42', '21', '16', 'παραδοθησεσθε δε και υπο γονεων και αδελφων και συγγενων και φιλων και θανατωσουσιν εξ υμων');
INSERT INTO `bible_verses_tr` VALUES ('2699', '42', '21', '17', 'και εσεσθε μισουμενοι υπο παντων δια το ονομα μου');
INSERT INTO `bible_verses_tr` VALUES ('2700', '42', '21', '18', 'και θριξ εκ της κεφαλης υμων ου μη αποληται');
INSERT INTO `bible_verses_tr` VALUES ('2701', '42', '21', '19', 'εν τη υπομονη υμων κτησασθε τας ψυχας υμων');
INSERT INTO `bible_verses_tr` VALUES ('2702', '42', '21', '20', 'οταν δε ιδητε κυκλουμενην υπο στρατοπεδων την ιερουσαλημ τοτε γνωτε οτι ηγγικεν η ερημωσις αυτης');
INSERT INTO `bible_verses_tr` VALUES ('2703', '42', '21', '21', 'τοτε οι εν τη ιουδαια φευγετωσαν εις τα ορη και οι εν μεσω αυτης εκχωρειτωσαν και οι εν ταις χωραις μη εισερχεσθωσαν εις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('2704', '42', '21', '22', 'οτι ημεραι εκδικησεως αυται εισιν του πληρωθηναι παντα τα γεγραμμενα');
INSERT INTO `bible_verses_tr` VALUES ('2705', '42', '21', '23', 'ουαι δε ταις εν γαστρι εχουσαις και ταις θηλαζουσαις εν εκειναις ταις ημεραις εσται γαρ αναγκη μεγαλη επι της γης και οργη εν τω λαω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('2706', '42', '21', '24', 'και πεσουνται στοματι μαχαιρας και αιχμαλωτισθησονται εις παντα τα εθνη και ιερουσαλημ εσται πατουμενη υπο εθνων αχρι πληρωθωσιν καιροι εθνων');
INSERT INTO `bible_verses_tr` VALUES ('2707', '42', '21', '25', 'και εσται σημεια εν ηλιω και σεληνη και αστροις και επι της γης συνοχη εθνων εν απορια ηχουσης θαλασσης και σαλου');
INSERT INTO `bible_verses_tr` VALUES ('2708', '42', '21', '26', 'αποψυχοντων ανθρωπων απο φοβου και προσδοκιας των επερχομενων τη οικουμενη αι γαρ δυναμεις των ουρανων σαλευθησονται');
INSERT INTO `bible_verses_tr` VALUES ('2709', '42', '21', '27', 'και τοτε οψονται τον υιον του ανθρωπου ερχομενον εν νεφελη μετα δυναμεως και δοξης πολλης');
INSERT INTO `bible_verses_tr` VALUES ('2710', '42', '21', '28', 'αρχομενων δε τουτων γινεσθαι ανακυψατε και επαρατε τας κεφαλας υμων διοτι εγγιζει η απολυτρωσις υμων');
INSERT INTO `bible_verses_tr` VALUES ('2711', '42', '21', '29', 'και ειπεν παραβολην αυτοις ιδετε την συκην και παντα τα δενδρα');
INSERT INTO `bible_verses_tr` VALUES ('2712', '42', '21', '30', 'οταν προβαλωσιν ηδη βλεποντες αφ εαυτων γινωσκετε οτι ηδη εγγυς το θερος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2713', '42', '21', '31', 'ουτως και υμεις οταν ιδητε ταυτα γινομενα γινωσκετε οτι εγγυς εστιν η βασιλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2714', '42', '21', '32', 'αμην λεγω υμιν οτι ου μη παρελθη η γενεα αυτη εως αν παντα γενηται');
INSERT INTO `bible_verses_tr` VALUES ('2715', '42', '21', '33', 'ο ουρανος και η γη παρελευσονται οι δε λογοι μου ου μη παρελθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('2716', '42', '21', '34', 'προσεχετε δε εαυτοις μηποτε βαρυνθωσιν υμων αι καρδιαι εν κραιπαλη και μεθη και μεριμναις βιωτικαις και αιφνιδιος εφ υμας επιστη η ημερα εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('2717', '42', '21', '35', 'ως παγις γαρ επελευσεται επι παντας τους καθημενους επι προσωπον πασης της γης');
INSERT INTO `bible_verses_tr` VALUES ('2718', '42', '21', '36', 'αγρυπνειτε ουν εν παντι καιρω δεομενοι ινα καταξιωθητε εκφυγειν ταυτα παντα τα μελλοντα γινεσθαι και σταθηναι εμπροσθεν του υιου του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2719', '42', '21', '37', 'ην δε τας ημερας εν τω ιερω διδασκων τας δε νυκτας εξερχομενος ηυλιζετο εις το ορος το καλουμενον ελαιων');
