INSERT INTO `bible_verses_tr` VALUES ('2728', '42', '22', '8', 'και απεστειλεν πετρον και ιωαννην ειπων πορευθεντες ετοιμασατε ημιν το πασχα ινα φαγωμεν');
INSERT INTO `bible_verses_tr` VALUES ('2729', '42', '22', '9', 'οι δε ειπον αυτω που θελεις ετοιμασωμεν');
INSERT INTO `bible_verses_tr` VALUES ('2730', '42', '22', '10', 'ο δε ειπεν αυτοις ιδου εισελθοντων υμων εις την πολιν συναντησει υμιν ανθρωπος κεραμιον υδατος βασταζων ακολουθησατε αυτω εις την οικιαν ου εισπορευεται');
INSERT INTO `bible_verses_tr` VALUES ('2731', '42', '22', '11', 'και ερειτε τω οικοδεσποτη της οικιας λεγει σοι ο διδασκαλος που εστιν το καταλυμα οπου το πασχα μετα των μαθητων μου φαγω');
