INSERT INTO `bible_verses_tr` VALUES ('2742', '42', '22', '22', 'και ο μεν υιος του ανθρωπου πορευεται κατα το ωρισμενον πλην ουαι τω ανθρωπω εκεινω δι ου παραδιδοται');
INSERT INTO `bible_verses_tr` VALUES ('2743', '42', '22', '23', 'και αυτοι ηρξαντο συζητειν προς εαυτους το τις αρα ειη εξ αυτων ο τουτο μελλων πρασσειν');
INSERT INTO `bible_verses_tr` VALUES ('2744', '42', '22', '24', 'εγενετο δε και φιλονεικια εν αυτοις το τις αυτων δοκει ειναι μειζων');
