INSERT INTO `bible_verses_tr` VALUES ('2747', '42', '22', '27', 'τις γαρ μειζων ο ανακειμενος η ο διακονων ουχι ο ανακειμενος εγω δε ειμι εν μεσω υμων ως ο διακονων');
INSERT INTO `bible_verses_tr` VALUES ('2748', '42', '22', '28', 'υμεις δε εστε οι διαμεμενηκοτες μετ εμου εν τοις πειρασμοις μου');
INSERT INTO `bible_verses_tr` VALUES ('2749', '42', '22', '29', 'καγω διατιθεμαι υμιν καθως διεθετο μοι ο πατηρ μου βασιλειαν');
INSERT INTO `bible_verses_tr` VALUES ('2750', '42', '22', '30', 'ινα εσθιητε και πινητε επι της τραπεζης μου εν τη βασιλεια μου και καθισησθε επι θρονων κρινοντες τας δωδεκα φυλας του ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('2751', '42', '22', '31', 'ειπεν δε ο κυριος σιμων σιμων ιδου ο σατανας εξητησατο υμας του σινιασαι ως τον σιτον');
