INSERT INTO `bible_verses_tr` VALUES ('2755', '42', '22', '35', 'και ειπεν αυτοις οτε απεστειλα υμας ατερ βαλαντιου και πηρας και υποδηματων μη τινος υστερησατε οι δε ειπον ουδενος');
