INSERT INTO `bible_verses_tr` VALUES ('2816', '42', '23', '25', 'απελυσεν δε αυτοις τον δια στασιν και φονον βεβλημενον εις την φυλακην ον ητουντο τον δε ιησουν παρεδωκεν τω θεληματι αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2817', '42', '23', '26', 'και ως απηγαγον αυτον επιλαβομενοι σιμωνος τινος κυρηναιου του ερχομενου απ αγρου επεθηκαν αυτω τον σταυρον φερειν οπισθεν του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('2818', '42', '23', '27', 'ηκολουθει δε αυτω πολυ πληθος του λαου και γυναικων αι και εκοπτοντο και εθρηνουν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2819', '42', '23', '28', 'στραφεις δε προς αυτας ο ιησους ειπεν θυγατερες ιερουσαλημ μη κλαιετε επ εμε πλην εφ εαυτας κλαιετε και επι τα τεκνα υμων');
INSERT INTO `bible_verses_tr` VALUES ('2820', '42', '23', '29', 'οτι ιδου ερχονται ημεραι εν αις ερουσιν μακαριαι αι στειραι και κοιλιαι αι ουκ εγεννησαν και μαστοι οι ουκ εθηλασαν');
INSERT INTO `bible_verses_tr` VALUES ('2821', '42', '23', '30', 'τοτε αρξονται λεγειν τοις ορεσιν πεσετε εφ ημας και τοις βουνοις καλυψατε ημας');
INSERT INTO `bible_verses_tr` VALUES ('2822', '42', '23', '31', 'οτι ει εν τω υγρω ξυλω ταυτα ποιουσιν εν τω ξηρω τι γενηται');
INSERT INTO `bible_verses_tr` VALUES ('2823', '42', '23', '32', 'ηγοντο δε και ετεροι δυο κακουργοι συν αυτω αναιρεθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2824', '42', '23', '33', 'και οτε απηλθον επι τον τοπον τον καλουμενον κρανιον εκει εσταυρωσαν αυτον και τους κακουργους ον μεν εκ δεξιων ον δε εξ αριστερων');
INSERT INTO `bible_verses_tr` VALUES ('2825', '42', '23', '34', 'ο δε ιησους ελεγεν πατερ αφες αυτοις ου γαρ οιδασιν τι ποιουσιν διαμεριζομενοι δε τα ιματια αυτου εβαλον κληρον');
INSERT INTO `bible_verses_tr` VALUES ('2826', '42', '23', '35', 'και ειστηκει ο λαος θεωρων εξεμυκτηριζον δε και οι αρχοντες συν αυτοις λεγοντες αλλους εσωσεν σωσατω εαυτον ει ουτος εστιν ο χριστος ο του θεου εκλεκτος');
INSERT INTO `bible_verses_tr` VALUES ('2827', '42', '23', '36', 'ενεπαιζον δε αυτω και οι στρατιωται προσερχομενοι και οξος προσφεροντες αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2828', '42', '23', '37', 'και λεγοντες ει συ ει ο βασιλευς των ιουδαιων σωσον σεαυτον');
INSERT INTO `bible_verses_tr` VALUES ('2829', '42', '23', '38', 'ην δε και επιγραφη γεγραμμενη επ αυτω γραμμασιν ελληνικοις και ρωμαικοις και εβραικοις ουτος εστιν ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('2830', '42', '23', '39', 'εις δε των κρεμασθεντων κακουργων εβλασφημει αυτον λεγων ει συ ει ο χριστος σωσον σεαυτον και ημας');
INSERT INTO `bible_verses_tr` VALUES ('2831', '42', '23', '40', 'αποκριθεις δε ο ετερος επετιμα αυτω λεγων ουδε φοβη συ τον θεον οτι εν τω αυτω κριματι ει');
INSERT INTO `bible_verses_tr` VALUES ('2832', '42', '23', '41', 'και ημεις μεν δικαιως αξια γαρ ων επραξαμεν απολαμβανομεν ουτος δε ουδεν ατοπον επραξεν');
INSERT INTO `bible_verses_tr` VALUES ('2833', '42', '23', '42', 'και ελεγεν τω ιησου μνησθητι μου κυριε οταν ελθης εν τη βασιλεια σου');
INSERT INTO `bible_verses_tr` VALUES ('2834', '42', '23', '43', 'και ειπεν αυτω ο ιησους αμην λεγω σοι σημερον μετ εμου εση εν τω παραδεισω');
INSERT INTO `bible_verses_tr` VALUES ('2835', '42', '23', '44', 'ην δε ωσει ωρα εκτη και σκοτος εγενετο εφ ολην την γην εως ωρας εννατης');
INSERT INTO `bible_verses_tr` VALUES ('2836', '42', '23', '45', 'και εσκοτισθη ο ηλιος και εσχισθη το καταπετασμα του ναου μεσον');
INSERT INTO `bible_verses_tr` VALUES ('2837', '42', '23', '46', 'και φωνησας φωνη μεγαλη ο ιησους ειπεν πατερ εις χειρας σου παραθησομαι το πνευμα μου και ταυτα ειπων εξεπνευσεν');
INSERT INTO `bible_verses_tr` VALUES ('2838', '42', '23', '47', 'ιδων δε ο εκατονταρχος το γενομενον εδοξασεν τον θεον λεγων οντως ο ανθρωπος ουτος δικαιος ην');
INSERT INTO `bible_verses_tr` VALUES ('2839', '42', '23', '48', 'και παντες οι συμπαραγενομενοι οχλοι επι την θεωριαν ταυτην θεωρουντες τα γενομενα τυπτοντες εαυτων τα στηθη υπεστρεφον');
INSERT INTO `bible_verses_tr` VALUES ('2840', '42', '23', '49', 'ειστηκεισαν δε παντες οι γνωστοι αυτου μακροθεν και γυναικες αι συνακολουθησασαι αυτω απο της γαλιλαιας ορωσαι ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('2841', '42', '23', '50', 'και ιδου ανηρ ονοματι ιωσηφ βουλευτης υπαρχων ανηρ αγαθος και δικαιος');
INSERT INTO `bible_verses_tr` VALUES ('2842', '42', '23', '51', 'ουτος ουκ ην συγκατατεθειμενος τη βουλη και τη πραξει αυτων απο αριμαθαιας πολεως των ιουδαιων ος και προσεδεχετο και αυτος την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2843', '42', '23', '52', 'ουτος προσελθων τω πιλατω ητησατο το σωμα του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('2844', '42', '23', '53', 'και καθελων αυτο ενετυλιξεν αυτο σινδονι και εθηκεν αυτο εν μνηματι λαξευτω ου ουκ ην ουδεπω ουδεις κειμενος');
INSERT INTO `bible_verses_tr` VALUES ('2845', '42', '23', '54', 'και ημερα ην παρασκευη και σαββατον επεφωσκεν');
INSERT INTO `bible_verses_tr` VALUES ('2846', '42', '23', '55', 'κατακολουθησασαι δε και γυναικες αιτινες ησαν συνεληλυθυιαι αυτω εκ της γαλιλαιας εθεασαντο το μνημειον και ως ετεθη το σωμα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2847', '42', '23', '56', 'υποστρεψασαι δε ητοιμασαν αρωματα και μυρα και το μεν σαββατον ησυχασαν κατα την εντολην');
INSERT INTO `bible_verses_tr` VALUES ('2848', '42', '24', '1', 'τη δε μια των σαββατων ορθρου βαθεος ηλθον επι το μνημα φερουσαι α ητοιμασαν αρωματα και τινες συν αυταις');
INSERT INTO `bible_verses_tr` VALUES ('2849', '42', '24', '2', 'ευρον δε τον λιθον αποκεκυλισμενον απο του μνημειου');
INSERT INTO `bible_verses_tr` VALUES ('2850', '42', '24', '3', 'και εισελθουσαι ουχ ευρον το σωμα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('2851', '42', '24', '4', 'και εγενετο εν τω διαπορεισθαι αυτας περι τουτου και ιδου δυο ανδρες επεστησαν αυταις εν εσθησεσιν αστραπτουσαις');
INSERT INTO `bible_verses_tr` VALUES ('2852', '42', '24', '5', 'εμφοβων δε γενομενων αυτων και κλινουσων το προσωπον εις την γην ειπον προς αυτας τι ζητειτε τον ζωντα μετα των νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('2853', '42', '24', '6', 'ουκ εστιν ωδε αλλ ηγερθη μνησθητε ως ελαλησεν υμιν ετι ων εν τη γαλιλαια');
INSERT INTO `bible_verses_tr` VALUES ('2854', '42', '24', '7', 'λεγων οτι δει τον υιον του ανθρωπου παραδοθηναι εις χειρας ανθρωπων αμαρτωλων και σταυρωθηναι και τη τριτη ημερα αναστηναι');
INSERT INTO `bible_verses_tr` VALUES ('2855', '42', '24', '8', 'και εμνησθησαν των ρηματων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2856', '42', '24', '9', 'και υποστρεψασαι απο του μνημειου απηγγειλαν ταυτα παντα τοις ενδεκα και πασιν τοις λοιποις');
INSERT INTO `bible_verses_tr` VALUES ('2857', '42', '24', '10', 'ησαν δε η μαγδαληνη μαρια και ιωαννα και μαρια ιακωβου και αι λοιπαι συν αυταις αι ελεγον προς τους αποστολους ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('2858', '42', '24', '11', 'και εφανησαν ενωπιον αυτων ωσει ληρος τα ρηματα αυτων και ηπιστουν αυταις');
INSERT INTO `bible_verses_tr` VALUES ('2859', '42', '24', '12', 'ο δε πετρος αναστας εδραμεν επι το μνημειον και παρακυψας βλεπει τα οθονια κειμενα μονα και απηλθεν προς εαυτον θαυμαζων το γεγονος');
INSERT INTO `bible_verses_tr` VALUES ('2860', '42', '24', '13', 'και ιδου δυο εξ αυτων ησαν πορευομενοι εν αυτη τη ημερα εις κωμην απεχουσαν σταδιους εξηκοντα απο ιερουσαλημ η ονομα εμμαους');
INSERT INTO `bible_verses_tr` VALUES ('2861', '42', '24', '14', 'και αυτοι ωμιλουν προς αλληλους περι παντων των συμβεβηκοτων τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2862', '42', '24', '15', 'και εγενετο εν τω ομιλειν αυτους και συζητειν και αυτος ο ιησους εγγισας συνεπορευετο αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2863', '42', '24', '16', 'οι δε οφθαλμοι αυτων εκρατουντο του μη επιγνωναι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2864', '42', '24', '17', 'ειπεν δε προς αυτους τινες οι λογοι ουτοι ους αντιβαλλετε προς αλληλους περιπατουντες και εστε σκυθρωποι');
INSERT INTO `bible_verses_tr` VALUES ('2865', '42', '24', '18', 'αποκριθεις δε ο εις ω ονομα κλεοπας ειπεν προς αυτον συ μονος παροικεις εν ιερουσαλημ και ουκ εγνως τα γενομενα εν αυτη εν ταις ημεραις ταυταις');
INSERT INTO `bible_verses_tr` VALUES ('2866', '42', '24', '19', 'και ειπεν αυτοις ποια οι δε ειπον αυτω τα περι ιησου του ναζωραιου ος εγενετο ανηρ προφητης δυνατος εν εργω και λογω εναντιον του θεου και παντος του λαου');
INSERT INTO `bible_verses_tr` VALUES ('2867', '42', '24', '20', 'οπως τε παρεδωκαν αυτον οι αρχιερεις και οι αρχοντες ημων εις κριμα θανατου και εσταυρωσαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2868', '42', '24', '21', 'ημεις δε ηλπιζομεν οτι αυτος εστιν ο μελλων λυτρουσθαι τον ισραηλ αλλα γε συν πασιν τουτοις τριτην ταυτην ημεραν αγει σημερον αφ ου ταυτα εγενετο');
INSERT INTO `bible_verses_tr` VALUES ('2869', '42', '24', '22', 'αλλα και γυναικες τινες εξ ημων εξεστησαν ημας γενομεναι ορθριαι επι το μνημειον');
INSERT INTO `bible_verses_tr` VALUES ('2870', '42', '24', '23', 'και μη ευρουσαι το σωμα αυτου ηλθον λεγουσαι και οπτασιαν αγγελων εωρακεναι οι λεγουσιν αυτον ζην');
INSERT INTO `bible_verses_tr` VALUES ('2871', '42', '24', '24', 'και απηλθον τινες των συν ημιν επι το μνημειον και ευρον ουτως καθως και αι γυναικες ειπον αυτον δε ουκ ειδον');
INSERT INTO `bible_verses_tr` VALUES ('2872', '42', '24', '25', 'και αυτος ειπεν προς αυτους ω ανοητοι και βραδεις τη καρδια του πιστευειν επι πασιν οις ελαλησαν οι προφηται');
INSERT INTO `bible_verses_tr` VALUES ('2873', '42', '24', '26', 'ουχι ταυτα εδει παθειν τον χριστον και εισελθειν εις την δοξαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2874', '42', '24', '27', 'και αρξαμενος απο μωσεως και απο παντων των προφητων διηρμηνευεν αυτοις εν πασαις ταις γραφαις τα περι εαυτου');
INSERT INTO `bible_verses_tr` VALUES ('2875', '42', '24', '28', 'και ηγγισαν εις την κωμην ου επορευοντο και αυτος προσεποιειτο πορρωτερω πορευεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2876', '42', '24', '29', 'και παρεβιασαντο αυτον λεγοντες μεινον μεθ ημων οτι προς εσπεραν εστιν και κεκλικεν η ημερα και εισηλθεν του μειναι συν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2877', '42', '24', '30', 'και εγενετο εν τω κατακλιθηναι αυτον μετ αυτων λαβων τον αρτον ευλογησεν και κλασας επεδιδου αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2878', '42', '24', '31', 'αυτων δε διηνοιχθησαν οι οφθαλμοι και επεγνωσαν αυτον και αυτος αφαντος εγενετο απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('2879', '42', '24', '32', 'και ειπον προς αλληλους ουχι η καρδια ημων καιομενη ην εν ημιν ως ελαλει ημιν εν τη οδω και ως διηνοιγεν ημιν τας γραφας');
INSERT INTO `bible_verses_tr` VALUES ('2880', '42', '24', '33', 'και ανασταντες αυτη τη ωρα υπεστρεψαν εις ιερουσαλημ και ευρον συνηθροισμενους τους ενδεκα και τους συν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('2881', '42', '24', '34', 'λεγοντας οτι ηγερθη ο κυριος οντως και ωφθη σιμωνι');
INSERT INTO `bible_verses_tr` VALUES ('2882', '42', '24', '35', 'και αυτοι εξηγουντο τα εν τη οδω και ως εγνωσθη αυτοις εν τη κλασει του αρτου');
INSERT INTO `bible_verses_tr` VALUES ('2883', '42', '24', '36', 'ταυτα δε αυτων λαλουντων αυτος ο ιησους εστη εν μεσω αυτων και λεγει αυτοις ειρηνη υμιν');
INSERT INTO `bible_verses_tr` VALUES ('2884', '42', '24', '37', 'πτοηθεντες δε και εμφοβοι γενομενοι εδοκουν πνευμα θεωρειν');
INSERT INTO `bible_verses_tr` VALUES ('2885', '42', '24', '38', 'και ειπεν αυτοις τι τεταραγμενοι εστε και δια τι διαλογισμοι αναβαινουσιν εν ταις καρδιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('2886', '42', '24', '39', 'ιδετε τας χειρας μου και τους ποδας μου οτι αυτος εγω ειμι ψηλαφησατε με και ιδετε οτι πνευμα σαρκα και οστεα ουκ εχει καθως εμε θεωρειτε εχοντα');
INSERT INTO `bible_verses_tr` VALUES ('2887', '42', '24', '40', 'και τουτο ειπων επεδειξεν αυτοις τας χειρας και τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('2888', '42', '24', '41', 'ετι δε απιστουντων αυτων απο της χαρας και θαυμαζοντων ειπεν αυτοις εχετε τι βρωσιμον ενθαδε');
INSERT INTO `bible_verses_tr` VALUES ('2889', '42', '24', '42', 'οι δε επεδωκαν αυτω ιχθυος οπτου μερος και απο μελισσιου κηριου');
INSERT INTO `bible_verses_tr` VALUES ('2890', '42', '24', '43', 'και λαβων ενωπιον αυτων εφαγεν');
INSERT INTO `bible_verses_tr` VALUES ('2891', '42', '24', '44', 'ειπεν δε αυτοις ουτοι οι λογοι ους ελαλησα προς υμας ετι ων συν υμιν οτι δει πληρωθηναι παντα τα γεγραμμενα εν τω νομω μωσεως και προφηταις και ψαλμοις περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('2892', '42', '24', '45', 'τοτε διηνοιξεν αυτων τον νουν του συνιεναι τας γραφας');
INSERT INTO `bible_verses_tr` VALUES ('2893', '42', '24', '46', 'και ειπεν αυτοις οτι ουτως γεγραπται και ουτως εδει παθειν τον χριστον και αναστηναι εκ νεκρων τη τριτη ημερα');
INSERT INTO `bible_verses_tr` VALUES ('2894', '42', '24', '47', 'και κηρυχθηναι επι τω ονοματι αυτου μετανοιαν και αφεσιν αμαρτιων εις παντα τα εθνη αρξαμενον απο ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('2895', '42', '24', '48', 'υμεις δε εστε μαρτυρες τουτων');
INSERT INTO `bible_verses_tr` VALUES ('2896', '42', '24', '49', 'και ιδου εγω αποστελλω την επαγγελιαν του πατρος μου εφ υμας υμεις δε καθισατε εν τη πολει ιερουσαλημ εως ου ενδυσησθε δυναμιν εξ υψους');
INSERT INTO `bible_verses_tr` VALUES ('2897', '42', '24', '50', 'εξηγαγεν δε αυτους εξω εως εις βηθανιαν και επαρας τας χειρας αυτου ευλογησεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('2898', '42', '24', '51', 'και εγενετο εν τω ευλογειν αυτον αυτους διεστη απ αυτων και ανεφερετο εις τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('2899', '42', '24', '52', 'και αυτοι προσκυνησαντες αυτον υπεστρεψαν εις ιερουσαλημ μετα χαρας μεγαλης');
INSERT INTO `bible_verses_tr` VALUES ('2900', '42', '24', '53', 'και ησαν διαπαντος εν τω ιερω αινουντες και ευλογουντες τον θεον αμην');
INSERT INTO `bible_verses_tr` VALUES ('2901', '43', '1', '1', 'εν αρχη ην ο λογος και ο λογος ην προς τον θεον και θεος ην ο λογος');
INSERT INTO `bible_verses_tr` VALUES ('2902', '43', '1', '2', 'ουτος ην εν αρχη προς τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('2903', '43', '1', '3', 'παντα δι αυτου εγενετο και χωρις αυτου εγενετο ουδε εν ο γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('2904', '43', '1', '4', 'εν αυτω ζωη ην και η ζωη ην το φως των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('2905', '43', '1', '5', 'και το φως εν τη σκοτια φαινει και η σκοτια αυτο ου κατελαβεν');
INSERT INTO `bible_verses_tr` VALUES ('2906', '43', '1', '6', 'εγενετο ανθρωπος απεσταλμενος παρα θεου ονομα αυτω ιωαννης');
INSERT INTO `bible_verses_tr` VALUES ('2907', '43', '1', '7', 'ουτος ηλθεν εις μαρτυριαν ινα μαρτυρηση περι του φωτος ινα παντες πιστευσωσιν δι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2908', '43', '1', '8', 'ουκ ην εκεινος το φως αλλ ινα μαρτυρηση περι του φωτος');
INSERT INTO `bible_verses_tr` VALUES ('2909', '43', '1', '9', 'ην το φως το αληθινον ο φωτιζει παντα ανθρωπον ερχομενον εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('2910', '43', '1', '10', 'εν τω κοσμω ην και ο κοσμος δι αυτου εγενετο και ο κοσμος αυτον ουκ εγνω');
INSERT INTO `bible_verses_tr` VALUES ('2911', '43', '1', '11', 'εις τα ιδια ηλθεν και οι ιδιοι αυτον ου παρελαβον');
INSERT INTO `bible_verses_tr` VALUES ('2912', '43', '1', '12', 'οσοι δε ελαβον αυτον εδωκεν αυτοις εξουσιαν τεκνα θεου γενεσθαι τοις πιστευουσιν εις το ονομα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2913', '43', '1', '13', 'οι ουκ εξ αιματων ουδε εκ θεληματος σαρκος ουδε εκ θεληματος ανδρος αλλ εκ θεου εγεννηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('2914', '43', '1', '14', 'και ο λογος σαρξ εγενετο και εσκηνωσεν εν ημιν και εθεασαμεθα την δοξαν αυτου δοξαν ως μονογενους παρα πατρος πληρης χαριτος και αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('2915', '43', '1', '15', 'ιωαννης μαρτυρει περι αυτου και κεκραγεν λεγων ουτος ην ον ειπον ο οπισω μου ερχομενος εμπροσθεν μου γεγονεν οτι πρωτος μου ην');
INSERT INTO `bible_verses_tr` VALUES ('2916', '43', '1', '16', 'και εκ του πληρωματος αυτου ημεις παντες ελαβομεν και χαριν αντι χαριτος');
INSERT INTO `bible_verses_tr` VALUES ('2917', '43', '1', '17', 'οτι ο νομος δια μωσεως εδοθη η χαρις και η αληθεια δια ιησου χριστου εγενετο');
INSERT INTO `bible_verses_tr` VALUES ('2918', '43', '1', '18', 'θεον ουδεις εωρακεν πωποτε ο μονογενης υιος ο ων εις τον κολπον του πατρος εκεινος εξηγησατο');
INSERT INTO `bible_verses_tr` VALUES ('2919', '43', '1', '19', 'και αυτη εστιν η μαρτυρια του ιωαννου οτε απεστειλαν οι ιουδαιοι εξ ιεροσολυμων ιερεις και λευιτας ινα ερωτησωσιν αυτον συ τις ει');
INSERT INTO `bible_verses_tr` VALUES ('2920', '43', '1', '20', 'και ωμολογησεν και ουκ ηρνησατο και ωμολογησεν οτι ουκ ειμι εγω ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('2921', '43', '1', '21', 'και ηρωτησαν αυτον τι ουν ηλιας ει συ και λεγει ουκ ειμι ο προφητης ει συ και απεκριθη ου');
INSERT INTO `bible_verses_tr` VALUES ('2922', '43', '1', '22', 'ειπον ουν αυτω τις ει ινα αποκρισιν δωμεν τοις πεμψασιν ημας τι λεγεις περι σεαυτου');
INSERT INTO `bible_verses_tr` VALUES ('2923', '43', '1', '23', 'εφη εγω φωνη βοωντος εν τη ερημω ευθυνατε την οδον κυριου καθως ειπεν ησαιας ο προφητης');
INSERT INTO `bible_verses_tr` VALUES ('2924', '43', '1', '24', 'και οι απεσταλμενοι ησαν εκ των φαρισαιων');
INSERT INTO `bible_verses_tr` VALUES ('2925', '43', '1', '25', 'και ηρωτησαν αυτον και ειπον αυτω τι ουν βαπτιζεις ει συ ουκ ει ο χριστος ουτε ηλιας ουτε ο προφητης');
INSERT INTO `bible_verses_tr` VALUES ('2926', '43', '1', '26', 'απεκριθη αυτοις ο ιωαννης λεγων εγω βαπτιζω εν υδατι μεσος δε υμων εστηκεν ον υμεις ουκ οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('2927', '43', '1', '27', 'αυτος εστιν ο οπισω μου ερχομενος ος εμπροσθεν μου γεγονεν ου εγω ουκ ειμι αξιος ινα λυσω αυτου τον ιμαντα του υποδηματος');
INSERT INTO `bible_verses_tr` VALUES ('2928', '43', '1', '28', 'ταυτα εν βηθαβαρα εγενετο περαν του ιορδανου οπου ην ιωαννης βαπτιζων');
INSERT INTO `bible_verses_tr` VALUES ('2929', '43', '1', '29', 'τη επαυριον βλεπει ο ιωαννης τον ιησουν ερχομενον προς αυτον και λεγει ιδε ο αμνος του θεου ο αιρων την αμαρτιαν του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('2930', '43', '1', '30', 'ουτος εστιν περι ου εγω ειπον οπισω μου ερχεται ανηρ ος εμπροσθεν μου γεγονεν οτι πρωτος μου ην');
INSERT INTO `bible_verses_tr` VALUES ('2931', '43', '1', '31', 'καγω ουκ ηδειν αυτον αλλ ινα φανερωθη τω ισραηλ δια τουτο ηλθον εγω εν τω υδατι βαπτιζων');
INSERT INTO `bible_verses_tr` VALUES ('2932', '43', '1', '32', 'και εμαρτυρησεν ιωαννης λεγων οτι τεθεαμαι το πνευμα καταβαινον ωσει περιστεραν εξ ουρανου και εμεινεν επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2933', '43', '1', '33', 'καγω ουκ ηδειν αυτον αλλ ο πεμψας με βαπτιζειν εν υδατι εκεινος μοι ειπεν εφ ον αν ιδης το πνευμα καταβαινον και μενον επ αυτον ουτος εστιν ο βαπτιζων εν πνευματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('2934', '43', '1', '34', 'καγω εωρακα και μεμαρτυρηκα οτι ουτος εστιν ο υιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2935', '43', '1', '35', 'τη επαυριον παλιν ειστηκει ο ιωαννης και εκ των μαθητων αυτου δυο');
INSERT INTO `bible_verses_tr` VALUES ('2936', '43', '1', '36', 'και εμβλεψας τω ιησου περιπατουντι λεγει ιδε ο αμνος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2937', '43', '1', '37', 'και ηκουσαν αυτου οι δυο μαθηται λαλουντος και ηκολουθησαν τω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('2938', '43', '1', '38', 'στραφεις δε ο ιησους και θεασαμενος αυτους ακολουθουντας λεγει αυτοις τι ζητειτε οι δε ειπον αυτω ραββι ο λεγεται ερμηνευομενον διδασκαλε που μενεις');
INSERT INTO `bible_verses_tr` VALUES ('2939', '43', '1', '39', 'λεγει αυτοις ερχεσθε και ιδετε ηλθον και ειδον που μενει και παρ αυτω εμειναν την ημεραν εκεινην ωρα δε ην ως δεκατη');
INSERT INTO `bible_verses_tr` VALUES ('2940', '43', '1', '40', 'ην ανδρεας ο αδελφος σιμωνος πετρου εις εκ των δυο των ακουσαντων παρα ιωαννου και ακολουθησαντων αυτω');
INSERT INTO `bible_verses_tr` VALUES ('2941', '43', '1', '41', 'ευρισκει ουτος πρωτος τον αδελφον τον ιδιον σιμωνα και λεγει αυτω ευρηκαμεν τον μεσσιαν ο εστιν μεθερμηνευομενον ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('2942', '43', '1', '42', 'και ηγαγεν αυτον προς τον ιησουν εμβλεψας δε αυτω ο ιησους ειπεν συ ει σιμων ο υιος ιωνα συ κληθηση κηφας ο ερμηνευεται πετρος');
INSERT INTO `bible_verses_tr` VALUES ('2943', '43', '1', '43', 'τη επαυριον ηθελησεν ο ιησους εξελθειν εις την γαλιλαιαν και ευρισκει φιλιππον και λεγει αυτω ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('2944', '43', '1', '44', 'ην δε ο φιλιππος απο βηθσαιδα εκ της πολεως ανδρεου και πετρου');
INSERT INTO `bible_verses_tr` VALUES ('2945', '43', '1', '45', 'ευρισκει φιλιππος τον ναθαναηλ και λεγει αυτω ον εγραψεν μωσης εν τω νομω και οι προφηται ευρηκαμεν ιησουν τον υιον του ιωσηφ τον απο  {VAR1: ναζαρετ } {VAR2: ναζαρεθ }');
INSERT INTO `bible_verses_tr` VALUES ('2946', '43', '1', '46', 'και ειπεν αυτω ναθαναηλ εκ  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } δυναται τι αγαθον ειναι λεγει αυτω φιλιππος ερχου και ιδε');
INSERT INTO `bible_verses_tr` VALUES ('2947', '43', '1', '47', 'ειδεν ο ιησους τον ναθαναηλ ερχομενον προς αυτον και λεγει περι αυτου ιδε αληθως ισραηλιτης εν ω δολος ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2948', '43', '1', '48', 'λεγει αυτω ναθαναηλ ποθεν με γινωσκεις απεκριθη ο ιησους και ειπεν αυτω προ του σε φιλιππον φωνησαι οντα υπο την συκην ειδον σε');
INSERT INTO `bible_verses_tr` VALUES ('2949', '43', '1', '49', 'απεκριθη ναθαναηλ και λεγει αυτω ραββι συ ει ο υιος του θεου συ ει ο βασιλευς του ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('2950', '43', '1', '50', 'απεκριθη ιησους και ειπεν αυτω οτι ειπον σοι ειδον σε υποκατω της συκης πιστευεις μειζω τουτων οψει');
INSERT INTO `bible_verses_tr` VALUES ('2951', '43', '1', '51', 'και λεγει αυτω αμην αμην λεγω υμιν απ αρτι οψεσθε τον ουρανον ανεωγοτα και τους αγγελους του θεου αναβαινοντας και καταβαινοντας επι τον υιον του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2952', '43', '2', '1', 'και τη ημερα τη τριτη γαμος εγενετο εν κανα της γαλιλαιας και ην η μητηρ του ιησου εκει');
INSERT INTO `bible_verses_tr` VALUES ('2953', '43', '2', '2', 'εκληθη δε και ο ιησους και οι μαθηται αυτου εις τον γαμον');
INSERT INTO `bible_verses_tr` VALUES ('2954', '43', '2', '3', 'και υστερησαντος οινου λεγει η μητηρ του ιησου προς αυτον οινον ουκ εχουσιν');
INSERT INTO `bible_verses_tr` VALUES ('2955', '43', '2', '4', 'λεγει αυτη ο ιησους τι εμοι και σοι γυναι ουπω ηκει η ωρα μου');
INSERT INTO `bible_verses_tr` VALUES ('2956', '43', '2', '5', 'λεγει η μητηρ αυτου τοις διακονοις ο τι αν λεγη υμιν ποιησατε');
INSERT INTO `bible_verses_tr` VALUES ('2957', '43', '2', '6', 'ησαν δε εκει υδριαι λιθιναι εξ κειμεναι κατα τον καθαρισμον των ιουδαιων χωρουσαι ανα μετρητας δυο η τρεις');
INSERT INTO `bible_verses_tr` VALUES ('2958', '43', '2', '7', 'λεγει αυτοις ο ιησους γεμισατε τας υδριας υδατος και εγεμισαν αυτας εως ανω');
INSERT INTO `bible_verses_tr` VALUES ('2959', '43', '2', '8', 'και λεγει αυτοις αντλησατε νυν και φερετε τω αρχιτρικλινω και ηνεγκαν');
INSERT INTO `bible_verses_tr` VALUES ('2960', '43', '2', '9', 'ως δε εγευσατο ο αρχιτρικλινος το υδωρ οινον γεγενημενον και ουκ ηδει ποθεν εστιν οι δε διακονοι ηδεισαν οι ηντληκοτες το υδωρ φωνει τον νυμφιον ο αρχιτρικλινος');
INSERT INTO `bible_verses_tr` VALUES ('2961', '43', '2', '10', 'και λεγει αυτω πας ανθρωπος πρωτον τον καλον οινον τιθησιν και οταν μεθυσθωσιν τοτε τον ελασσω συ τετηρηκας τον καλον οινον εως αρτι');
INSERT INTO `bible_verses_tr` VALUES ('2962', '43', '2', '11', 'ταυτην εποιησεν την αρχην των σημειων ο ιησους εν κανα της γαλιλαιας και εφανερωσεν την δοξαν αυτου και επιστευσαν εις αυτον οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2963', '43', '2', '12', 'μετα τουτο κατεβη εις καπερναουμ αυτος και η μητηρ αυτου και οι αδελφοι αυτου και οι μαθηται αυτου και εκει εμειναν ου πολλας ημερας');
INSERT INTO `bible_verses_tr` VALUES ('2964', '43', '2', '13', 'και εγγυς ην το πασχα των ιουδαιων και ανεβη εις ιεροσολυμα ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('2965', '43', '2', '14', 'και ευρεν εν τω ιερω τους πωλουντας βοας και προβατα και περιστερας και τους κερματιστας καθημενους');
INSERT INTO `bible_verses_tr` VALUES ('2966', '43', '2', '15', 'και ποιησας φραγελλιον εκ σχοινιων παντας εξεβαλεν εκ του ιερου τα τε προβατα και τους βοας και των κολλυβιστων εξεχεεν το κερμα και τας τραπεζας ανεστρεψεν');
INSERT INTO `bible_verses_tr` VALUES ('2967', '43', '2', '16', 'και τοις τας περιστερας πωλουσιν ειπεν αρατε ταυτα εντευθεν μη ποιειτε τον οικον του πατρος μου οικον εμποριου');
INSERT INTO `bible_verses_tr` VALUES ('2968', '43', '2', '17', 'εμνησθησαν δε οι μαθηται αυτου οτι γεγραμμενον εστιν ο ζηλος του οικου σου κατεφαγεν με');
INSERT INTO `bible_verses_tr` VALUES ('2969', '43', '2', '18', 'απεκριθησαν ουν οι ιουδαιοι και ειπον αυτω τι σημειον δεικνυεις ημιν οτι ταυτα ποιεις');
INSERT INTO `bible_verses_tr` VALUES ('2970', '43', '2', '19', 'απεκριθη ο ιησους και ειπεν αυτοις λυσατε τον ναον τουτον και εν τρισιν ημεραις εγερω αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2971', '43', '2', '20', 'ειπον ουν οι ιουδαιοι τεσσαρακοντα και εξ ετεσιν ωκοδομηθη ο ναος ουτος και συ εν τρισιν ημεραις εγερεις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('2972', '43', '2', '21', 'εκεινος δε ελεγεν περι του ναου του σωματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2973', '43', '2', '22', 'οτε ουν ηγερθη εκ νεκρων εμνησθησαν οι μαθηται αυτου οτι τουτο ελεγεν αυτοις και επιστευσαν τη γραφη και τω λογω ω ειπεν ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('2974', '43', '2', '23', 'ως δε ην εν ιεροσολυμοις εν τω πασχα εν τη εορτη πολλοι επιστευσαν εις το ονομα αυτου θεωρουντες αυτου τα σημεια α εποιει');
INSERT INTO `bible_verses_tr` VALUES ('2975', '43', '2', '24', 'αυτος δε ο ιησους ουκ επιστευεν εαυτον αυτοις δια το αυτον γινωσκειν παντας');
INSERT INTO `bible_verses_tr` VALUES ('2976', '43', '2', '25', 'και οτι ου χρειαν ειχεν ινα τις μαρτυρηση περι του ανθρωπου αυτος γαρ εγινωσκεν τι ην εν τω ανθρωπω');
INSERT INTO `bible_verses_tr` VALUES ('2977', '43', '3', '1', 'ην δε ανθρωπος εκ των φαρισαιων νικοδημος ονομα αυτω αρχων των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('2978', '43', '3', '2', 'ουτος ηλθεν προς τον ιησουν νυκτος και ειπεν αυτω ραββι οιδαμεν οτι απο θεου εληλυθας διδασκαλος ουδεις γαρ ταυτα τα σημεια δυναται ποιειν α συ ποιεις εαν μη η ο θεος μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2979', '43', '3', '3', 'απεκριθη ο ιησους και ειπεν αυτω αμην αμην λεγω σοι εαν μη τις γεννηθη ανωθεν ου δυναται ιδειν την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2980', '43', '3', '4', 'λεγει προς αυτον ο νικοδημος πως δυναται ανθρωπος γεννηθηναι γερων ων μη δυναται εις την κοιλιαν της μητρος αυτου δευτερον εισελθειν και γεννηθηναι');
INSERT INTO `bible_verses_tr` VALUES ('2981', '43', '3', '5', 'απεκριθη ο ιησους αμην αμην λεγω σοι εαν μη τις γεννηθη εξ υδατος και πνευματος ου δυναται εισελθειν εις την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2982', '43', '3', '6', 'το γεγεννημενον εκ της σαρκος σαρξ εστιν και το γεγεννημενον εκ του πνευματος πνευμα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('2983', '43', '3', '7', 'μη θαυμασης οτι ειπον σοι δει υμας γεννηθηναι ανωθεν');
INSERT INTO `bible_verses_tr` VALUES ('2984', '43', '3', '8', 'το πνευμα οπου θελει πνει και την φωνην αυτου ακουεις αλλ ουκ οιδας ποθεν ερχεται και που υπαγει ουτως εστιν πας ο γεγεννημενος εκ του πνευματος');
INSERT INTO `bible_verses_tr` VALUES ('2985', '43', '3', '9', 'απεκριθη νικοδημος και ειπεν αυτω πως δυναται ταυτα γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('2986', '43', '3', '10', 'απεκριθη ο ιησους και ειπεν αυτω συ ει ο διδασκαλος του ισραηλ και ταυτα ου γινωσκεις');
INSERT INTO `bible_verses_tr` VALUES ('2987', '43', '3', '11', 'αμην αμην λεγω σοι οτι ο οιδαμεν λαλουμεν και ο εωρακαμεν μαρτυρουμεν και την μαρτυριαν ημων ου λαμβανετε');
INSERT INTO `bible_verses_tr` VALUES ('2988', '43', '3', '12', 'ει τα επιγεια ειπον υμιν και ου πιστευετε πως εαν ειπω υμιν τα επουρανια πιστευσετε');
INSERT INTO `bible_verses_tr` VALUES ('2989', '43', '3', '13', 'και ουδεις αναβεβηκεν εις τον ουρανον ει μη ο εκ του ουρανου καταβας ο υιος του ανθρωπου ο ων εν τω ουρανω');
INSERT INTO `bible_verses_tr` VALUES ('2990', '43', '3', '14', 'και καθως μωσης υψωσεν τον οφιν εν τη ερημω ουτως υψωθηναι δει τον υιον του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('2991', '43', '3', '15', 'ινα πας ο πιστευων εις αυτον μη αποληται αλλ εχη ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('2992', '43', '3', '16', 'ουτως γαρ ηγαπησεν ο θεος τον κοσμον ωστε τον υιον αυτου τον μονογενη εδωκεν ινα πας ο πιστευων εις αυτον μη αποληται αλλ εχη ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('2993', '43', '3', '17', 'ου γαρ απεστειλεν ο θεος τον υιον αυτου εις τον κοσμον ινα κρινη τον κοσμον αλλ ινα σωθη ο κοσμος δι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2994', '43', '3', '18', 'ο πιστευων εις αυτον ου κρινεται ο δε μη πιστευων ηδη κεκριται οτι μη πεπιστευκεν εις το ονομα του μονογενους υιου του θεου');
INSERT INTO `bible_verses_tr` VALUES ('2995', '43', '3', '19', 'αυτη δε εστιν η κρισις οτι το φως εληλυθεν εις τον κοσμον και ηγαπησαν οι ανθρωποι μαλλον το σκοτος η το φως ην γαρ πονηρα αυτων τα εργα');
INSERT INTO `bible_verses_tr` VALUES ('2996', '43', '3', '20', 'πας γαρ ο φαυλα πρασσων μισει το φως και ουκ ερχεται προς το φως ινα μη ελεγχθη τα εργα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('2997', '43', '3', '21', 'ο δε ποιων την αληθειαν ερχεται προς το φως ινα φανερωθη αυτου τα εργα οτι εν θεω εστιν ειργασμενα');
INSERT INTO `bible_verses_tr` VALUES ('2998', '43', '3', '22', 'μετα ταυτα ηλθεν ο ιησους και οι μαθηται αυτου εις την ιουδαιαν γην και εκει διετριβεν μετ αυτων και εβαπτιζεν');
INSERT INTO `bible_verses_tr` VALUES ('2999', '43', '3', '23', 'ην δε και ιωαννης βαπτιζων εν αινων εγγυς του σαλειμ οτι υδατα πολλα ην εκει και παρεγινοντο και εβαπτιζοντο');
INSERT INTO `bible_verses_tr` VALUES ('3000', '43', '3', '24', 'ουπω γαρ ην βεβλημενος εις την φυλακην ο ιωαννης');
INSERT INTO `bible_verses_tr` VALUES ('3001', '43', '3', '25', 'εγενετο ουν ζητησις εκ των μαθητων ιωαννου μετα ιουδαιων περι καθαρισμου');
INSERT INTO `bible_verses_tr` VALUES ('3002', '43', '3', '26', 'και ηλθον προς τον ιωαννην και ειπον αυτω ραββι ος ην μετα σου περαν του ιορδανου ω συ μεμαρτυρηκας ιδε ουτος βαπτιζει και παντες ερχονται προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3003', '43', '3', '27', 'απεκριθη ιωαννης και ειπεν ου δυναται ανθρωπος λαμβανειν ουδεν εαν μη η δεδομενον αυτω εκ του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('3004', '43', '3', '28', 'αυτοι υμεις μοι μαρτυρειτε οτι ειπον ουκ ειμι εγω ο χριστος αλλ οτι απεσταλμενος ειμι εμπροσθεν εκεινου');
INSERT INTO `bible_verses_tr` VALUES ('3005', '43', '3', '29', 'ο εχων την νυμφην νυμφιος εστιν ο δε φιλος του νυμφιου ο εστηκως και ακουων αυτου χαρα χαιρει δια την φωνην του νυμφιου αυτη ουν η χαρα η εμη πεπληρωται');
INSERT INTO `bible_verses_tr` VALUES ('3006', '43', '3', '30', 'εκεινον δει αυξανειν εμε δε ελαττουσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3007', '43', '3', '31', 'ο ανωθεν ερχομενος επανω παντων εστιν ο ων εκ της γης εκ της γης εστιν και εκ της γης λαλει ο εκ του ουρανου ερχομενος επανω παντων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3008', '43', '3', '32', 'και ο εωρακεν και ηκουσεν τουτο μαρτυρει και την μαρτυριαν αυτου ουδεις λαμβανει');
INSERT INTO `bible_verses_tr` VALUES ('3009', '43', '3', '33', 'ο λαβων αυτου την μαρτυριαν εσφραγισεν οτι ο θεος αληθης εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3010', '43', '3', '34', 'ον γαρ απεστειλεν ο θεος τα ρηματα του θεου λαλει ου γαρ εκ μετρου διδωσιν ο θεος το πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('3011', '43', '3', '35', 'ο πατηρ αγαπα τον υιον και παντα δεδωκεν εν τη χειρι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3012', '43', '3', '36', 'ο πιστευων εις τον υιον εχει ζωην αιωνιον ο δε απειθων τω υιω ουκ οψεται ζωην αλλ η οργη του θεου μενει επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3013', '43', '4', '1', 'ως ουν εγνω ο κυριος οτι ηκουσαν οι φαρισαιοι οτι ιησους πλειονας μαθητας ποιει και βαπτιζει η ιωαννης');
INSERT INTO `bible_verses_tr` VALUES ('3014', '43', '4', '2', 'καιτοιγε ιησους αυτος ουκ εβαπτιζεν αλλ οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3015', '43', '4', '3', 'αφηκεν την ιουδαιαν και απηλθεν παλιν εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('3016', '43', '4', '4', 'εδει δε αυτον διερχεσθαι δια της σαμαρειας');
INSERT INTO `bible_verses_tr` VALUES ('3017', '43', '4', '5', 'ερχεται ουν εις πολιν της σαμαρειας λεγομενην συχαρ πλησιον του χωριου ο εδωκεν ιακωβ ιωσηφ τω υιω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3018', '43', '4', '6', 'ην δε εκει πηγη του ιακωβ ο ουν ιησους κεκοπιακως εκ της οδοιποριας εκαθεζετο ουτως επι τη πηγη ωρα ην ωσει εκτη');
INSERT INTO `bible_verses_tr` VALUES ('3019', '43', '4', '7', 'ερχεται γυνη εκ της σαμαρειας αντλησαι υδωρ λεγει αυτη ο ιησους δος μοι πιειν');
INSERT INTO `bible_verses_tr` VALUES ('3020', '43', '4', '8', 'οι γαρ μαθηται αυτου απεληλυθεισαν εις την πολιν ινα τροφας αγορασωσιν');
INSERT INTO `bible_verses_tr` VALUES ('3021', '43', '4', '9', 'λεγει ουν αυτω η γυνη η σαμαρειτις πως συ ιουδαιος ων παρ εμου πιειν αιτεις ουσης γυναικος σαμαρειτιδος ου γαρ συγχρωνται ιουδαιοι σαμαρειταις');
INSERT INTO `bible_verses_tr` VALUES ('3022', '43', '4', '10', 'απεκριθη ιησους και ειπεν αυτη ει ηδεις την δωρεαν του θεου και τις εστιν ο λεγων σοι δος μοι πιειν συ αν ητησας αυτον και εδωκεν αν σοι υδωρ ζων');
INSERT INTO `bible_verses_tr` VALUES ('3023', '43', '4', '11', 'λεγει αυτω η γυνη κυριε ουτε αντλημα εχεις και το φρεαρ εστιν βαθυ ποθεν ουν εχεις το υδωρ το ζων');
INSERT INTO `bible_verses_tr` VALUES ('3024', '43', '4', '12', 'μη συ μειζων ει του πατρος ημων ιακωβ ος εδωκεν ημιν το φρεαρ και αυτος εξ αυτου επιεν και οι υιοι αυτου και τα θρεμματα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3025', '43', '4', '13', 'απεκριθη ο ιησους και ειπεν αυτη πας ο πινων εκ του υδατος τουτου διψησει παλιν');
INSERT INTO `bible_verses_tr` VALUES ('3026', '43', '4', '14', 'ος δ αν πιη εκ του υδατος ου εγω δωσω αυτω ου μη διψηση εις τον αιωνα αλλα το υδωρ ο δωσω αυτω γενησεται εν αυτω πηγη υδατος αλλομενου εις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('3027', '43', '4', '15', 'λεγει προς αυτον η γυνη κυριε δος μοι τουτο το υδωρ ινα μη διψω μηδε ερχωμαι ενθαδε αντλειν');
INSERT INTO `bible_verses_tr` VALUES ('3028', '43', '4', '16', 'λεγει αυτη ο ιησους υπαγε φωνησον τον ανδρα σου και ελθε ενθαδε');
INSERT INTO `bible_verses_tr` VALUES ('3029', '43', '4', '17', 'απεκριθη η γυνη και ειπεν ουκ εχω ανδρα λεγει αυτη ο ιησους καλως ειπας οτι ανδρα ουκ εχω');
INSERT INTO `bible_verses_tr` VALUES ('3030', '43', '4', '18', 'πεντε γαρ ανδρας εσχες και νυν ον εχεις ουκ εστιν σου ανηρ τουτο αληθες ειρηκας');
INSERT INTO `bible_verses_tr` VALUES ('3031', '43', '4', '19', 'λεγει αυτω η γυνη κυριε θεωρω οτι προφητης ει συ');
INSERT INTO `bible_verses_tr` VALUES ('3032', '43', '4', '20', 'οι πατερες ημων εν τουτω τω ορει προσεκυνησαν και υμεις λεγετε οτι εν ιεροσολυμοις εστιν ο τοπος οπου δει προσκυνειν');
INSERT INTO `bible_verses_tr` VALUES ('3033', '43', '4', '21', 'λεγει αυτη ο ιησους γυναι πιστευσον μοι οτι ερχεται ωρα οτε ουτε εν τω ορει τουτω ουτε εν ιεροσολυμοις προσκυνησετε τω πατρι');
INSERT INTO `bible_verses_tr` VALUES ('3034', '43', '4', '22', 'υμεις προσκυνειτε ο ουκ οιδατε ημεις προσκυνουμεν ο οιδαμεν οτι η σωτηρια εκ των ιουδαιων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3035', '43', '4', '23', 'αλλ ερχεται ωρα και νυν εστιν οτε οι αληθινοι προσκυνηται προσκυνησουσιν τω πατρι εν πνευματι και αληθεια και γαρ ο πατηρ τοιουτους ζητει τους προσκυνουντας αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3036', '43', '4', '24', 'πνευμα ο θεος και τους προσκυνουντας αυτον εν πνευματι και αληθεια δει προσκυνειν');
INSERT INTO `bible_verses_tr` VALUES ('3037', '43', '4', '25', 'λεγει αυτω η γυνη οιδα οτι μεσσιας ερχεται ο λεγομενος χριστος οταν ελθη εκεινος αναγγελει ημιν παντα');
INSERT INTO `bible_verses_tr` VALUES ('3038', '43', '4', '26', 'λεγει αυτη ο ιησους εγω ειμι ο λαλων σοι');
INSERT INTO `bible_verses_tr` VALUES ('3039', '43', '4', '27', 'και επι τουτω ηλθον οι μαθηται αυτου και εθαυμασαν οτι μετα γυναικος ελαλει ουδεις μεντοι ειπεν τι ζητεις η τι λαλεις μετ αυτης');
INSERT INTO `bible_verses_tr` VALUES ('3040', '43', '4', '28', 'αφηκεν ουν την υδριαν αυτης η γυνη και απηλθεν εις την πολιν και λεγει τοις ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('3041', '43', '4', '29', 'δευτε ιδετε ανθρωπον ος ειπεν μοι παντα οσα εποιησα μητι ουτος εστιν ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('3042', '43', '4', '30', 'εξηλθον ουν εκ της πολεως και ηρχοντο προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3043', '43', '4', '31', 'εν δε τω μεταξυ ηρωτων αυτον οι μαθηται λεγοντες ραββι φαγε');
INSERT INTO `bible_verses_tr` VALUES ('3044', '43', '4', '32', 'ο δε ειπεν αυτοις εγω βρωσιν εχω φαγειν ην υμεις ουκ οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('3045', '43', '4', '33', 'ελεγον ουν οι μαθηται προς αλληλους μη τις ηνεγκεν αυτω φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('3046', '43', '4', '34', 'λεγει αυτοις ο ιησους εμον βρωμα εστιν ινα ποιω το θελημα του πεμψαντος με και τελειωσω αυτου το εργον');
INSERT INTO `bible_verses_tr` VALUES ('3047', '43', '4', '35', 'ουχ υμεις λεγετε οτι ετι τετραμηνον εστιν και ο θερισμος ερχεται ιδου λεγω υμιν επαρατε τους οφθαλμους υμων και θεασασθε τας χωρας οτι λευκαι εισιν προς θερισμον ηδη');
INSERT INTO `bible_verses_tr` VALUES ('3048', '43', '4', '36', 'και ο θεριζων μισθον λαμβανει και συναγει καρπον εις ζωην αιωνιον ινα και ο σπειρων ομου χαιρη και ο θεριζων');
INSERT INTO `bible_verses_tr` VALUES ('3049', '43', '4', '37', 'εν γαρ τουτω ο λογος εστιν  {VAR1: ο } αληθινος οτι αλλος εστιν ο σπειρων και αλλος ο θεριζων');
INSERT INTO `bible_verses_tr` VALUES ('3050', '43', '4', '38', 'εγω απεστειλα υμας θεριζειν ο ουχ υμεις κεκοπιακατε αλλοι κεκοπιακασιν και υμεις εις τον κοπον αυτων εισεληλυθατε');
INSERT INTO `bible_verses_tr` VALUES ('3051', '43', '4', '39', 'εκ δε της πολεως εκεινης πολλοι επιστευσαν εις αυτον των σαμαρειτων δια τον λογον της γυναικος μαρτυρουσης οτι ειπεν μοι παντα οσα εποιησα');
INSERT INTO `bible_verses_tr` VALUES ('3052', '43', '4', '40', 'ως ουν ηλθον προς αυτον οι σαμαρειται ηρωτων αυτον μειναι παρ αυτοις και εμεινεν εκει δυο ημερας');
INSERT INTO `bible_verses_tr` VALUES ('3053', '43', '4', '41', 'και πολλω πλειους επιστευσαν δια τον λογον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3054', '43', '4', '42', 'τη τε γυναικι ελεγον οτι ουκετι δια την σην λαλιαν πιστευομεν αυτοι γαρ ακηκοαμεν και οιδαμεν οτι ουτος εστιν αληθως ο σωτηρ του κοσμου ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('3055', '43', '4', '43', 'μετα δε τας δυο ημερας εξηλθεν εκειθεν και απηλθεν εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('3056', '43', '4', '44', 'αυτος γαρ ο ιησους εμαρτυρησεν οτι προφητης εν τη ιδια πατριδι τιμην ουκ εχει');
INSERT INTO `bible_verses_tr` VALUES ('3057', '43', '4', '45', 'οτε ουν ηλθεν εις την γαλιλαιαν εδεξαντο αυτον οι γαλιλαιοι παντα εωρακοτες α εποιησεν εν ιεροσολυμοις εν τη εορτη και αυτοι γαρ ηλθον εις την εορτην');
INSERT INTO `bible_verses_tr` VALUES ('3058', '43', '4', '46', 'ηλθεν ουν ο ιησους παλιν εις την κανα της γαλιλαιας οπου εποιησεν το υδωρ οινον και ην τις βασιλικος ου ο υιος ησθενει εν καπερναουμ');
INSERT INTO `bible_verses_tr` VALUES ('3059', '43', '4', '47', 'ουτος ακουσας οτι ιησους ηκει εκ της ιουδαιας εις την γαλιλαιαν απηλθεν προς αυτον και ηρωτα αυτον ινα καταβη και ιασηται αυτου τον υιον ημελλεν γαρ αποθνησκειν');
INSERT INTO `bible_verses_tr` VALUES ('3060', '43', '4', '48', 'ειπεν ουν ο ιησους προς αυτον εαν μη σημεια και τερατα ιδητε ου μη πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('3061', '43', '4', '49', 'λεγει προς αυτον ο βασιλικος κυριε καταβηθι πριν αποθανειν το παιδιον μου');
INSERT INTO `bible_verses_tr` VALUES ('3062', '43', '4', '50', 'λεγει αυτω ο ιησους πορευου ο υιος σου ζη και επιστευσεν ο ανθρωπος τω λογω ω ειπεν αυτω ιησους και επορευετο');
INSERT INTO `bible_verses_tr` VALUES ('3063', '43', '4', '51', 'ηδη δε αυτου καταβαινοντος οι δουλοι αυτου απηντησαν αυτω και απηγγειλαν λεγοντες οτι ο παις σου ζη');
INSERT INTO `bible_verses_tr` VALUES ('3064', '43', '4', '52', 'επυθετο ουν παρ αυτων την ωραν εν η κομψοτερον εσχεν και ειπον αυτω οτι χθες ωραν εβδομην αφηκεν αυτον ο πυρετος');
INSERT INTO `bible_verses_tr` VALUES ('3065', '43', '4', '53', 'εγνω ουν ο πατηρ οτι εν εκεινη τη ωρα εν η ειπεν αυτω ο ιησους οτι ο υιος σου ζη και επιστευσεν αυτος και η οικια αυτου ολη');
INSERT INTO `bible_verses_tr` VALUES ('3066', '43', '4', '54', 'τουτο παλιν δευτερον σημειον εποιησεν ο ιησους ελθων εκ της ιουδαιας εις την γαλιλαιαν');
INSERT INTO `bible_verses_tr` VALUES ('3067', '43', '5', '1', 'μετα ταυτα ην εορτη των ιουδαιων και ανεβη ο ιησους εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('3068', '43', '5', '2', 'εστιν δε εν τοις ιεροσολυμοις επι τη προβατικη κολυμβηθρα η επιλεγομενη εβραιστι βηθεσδα πεντε στοας εχουσα');
INSERT INTO `bible_verses_tr` VALUES ('3069', '43', '5', '3', 'εν ταυταις κατεκειτο πληθος πολυ των ασθενουντων τυφλων χωλων ξηρων εκδεχομενων την του υδατος κινησιν');
INSERT INTO `bible_verses_tr` VALUES ('3070', '43', '5', '4', 'αγγελος γαρ κατα καιρον κατεβαινεν εν τη κολυμβηθρα και εταρασσεν το υδωρ ο ουν πρωτος εμβας μετα την ταραχην του υδατος υγιης εγινετο ω δηποτε κατειχετο νοσηματι');
INSERT INTO `bible_verses_tr` VALUES ('3071', '43', '5', '5', 'ην δε τις ανθρωπος εκει  {VAR1: τριακονταοκτω } {VAR2: τριακοντα και οκτω } ετη εχων εν τη ασθενεια');
INSERT INTO `bible_verses_tr` VALUES ('3072', '43', '5', '6', 'τουτον ιδων ο ιησους κατακειμενον και γνους οτι πολυν ηδη χρονον εχει λεγει αυτω θελεις υγιης γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3073', '43', '5', '7', 'απεκριθη αυτω ο ασθενων κυριε ανθρωπον ουκ εχω ινα οταν ταραχθη το υδωρ βαλλη με εις την κολυμβηθραν εν ω δε ερχομαι εγω αλλος προ εμου καταβαινει');
INSERT INTO `bible_verses_tr` VALUES ('3074', '43', '5', '8', 'λεγει αυτω ο ιησους εγειραι αρον τον κραββατον σου και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('3075', '43', '5', '9', 'και ευθεως εγενετο υγιης ο ανθρωπος και ηρεν τον κραββατον αυτου και περιεπατει ην δε σαββατον εν εκεινη τη ημερα');
INSERT INTO `bible_verses_tr` VALUES ('3076', '43', '5', '10', 'ελεγον ουν οι ιουδαιοι τω τεθεραπευμενω σαββατον εστιν ουκ εξεστιν σοι αραι τον κραββατον');
INSERT INTO `bible_verses_tr` VALUES ('3077', '43', '5', '11', 'απεκριθη αυτοις ο ποιησας με υγιη εκεινος μοι ειπεν αρον τον κραββατον σου και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('3078', '43', '5', '12', 'ηρωτησαν ουν αυτον τις εστιν ο ανθρωπος ο ειπων σοι αρον τον κραββατον σου και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('3079', '43', '5', '13', 'ο δε ιαθεις ουκ ηδει τις εστιν ο γαρ ιησους εξενευσεν οχλου οντος εν τω τοπω');
INSERT INTO `bible_verses_tr` VALUES ('3080', '43', '5', '14', 'μετα ταυτα ευρισκει αυτον ο ιησους εν τω ιερω και ειπεν αυτω ιδε υγιης γεγονας μηκετι αμαρτανε ινα μη χειρον τι σοι γενηται');
INSERT INTO `bible_verses_tr` VALUES ('3081', '43', '5', '15', 'απηλθεν ο ανθρωπος και ανηγγειλεν τοις ιουδαιοις οτι ιησους εστιν ο ποιησας αυτον υγιη');
INSERT INTO `bible_verses_tr` VALUES ('3082', '43', '5', '16', 'και δια τουτο εδιωκον τον ιησουν οι ιουδαιοι και εζητουν αυτον αποκτειναι οτι ταυτα εποιει εν σαββατω');
INSERT INTO `bible_verses_tr` VALUES ('3083', '43', '5', '17', 'ο δε ιησους απεκρινατο αυτοις ο πατηρ μου εως αρτι εργαζεται καγω εργαζομαι');
INSERT INTO `bible_verses_tr` VALUES ('3084', '43', '5', '18', 'δια τουτο ουν μαλλον εζητουν αυτον οι ιουδαιοι αποκτειναι οτι ου μονον ελυεν το σαββατον αλλα και πατερα ιδιον ελεγεν τον θεον ισον εαυτον ποιων τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('3085', '43', '5', '19', 'απεκρινατο ουν ο ιησους και ειπεν αυτοις αμην αμην λεγω υμιν ου δυναται ο υιος ποιειν αφ εαυτου ουδεν εαν μη τι βλεπη τον πατερα ποιουντα α γαρ αν εκεινος ποιη ταυτα και ο υιος ομοιως ποιει');
INSERT INTO `bible_verses_tr` VALUES ('3086', '43', '5', '20', 'ο γαρ πατηρ φιλει τον υιον και παντα δεικνυσιν αυτω α αυτος ποιει και μειζονα τουτων δειξει αυτω εργα ινα υμεις θαυμαζητε');
INSERT INTO `bible_verses_tr` VALUES ('3087', '43', '5', '21', 'ωσπερ γαρ ο πατηρ εγειρει τους νεκρους και ζωοποιει ουτως και ο υιος ους θελει ζωοποιει');
INSERT INTO `bible_verses_tr` VALUES ('3088', '43', '5', '22', 'ουδε γαρ ο πατηρ κρινει ουδενα αλλα την κρισιν πασαν δεδωκεν τω υιω');
INSERT INTO `bible_verses_tr` VALUES ('3089', '43', '5', '23', 'ινα παντες τιμωσιν τον υιον καθως τιμωσιν τον πατερα ο μη τιμων τον υιον ου τιμα τον πατερα τον πεμψαντα αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3090', '43', '5', '24', 'αμην αμην λεγω υμιν οτι ο τον λογον μου ακουων και πιστευων τω πεμψαντι με εχει ζωην αιωνιον και εις κρισιν ουκ ερχεται αλλα μεταβεβηκεν εκ του θανατου εις την ζωην');
INSERT INTO `bible_verses_tr` VALUES ('3091', '43', '5', '25', 'αμην αμην λεγω υμιν οτι ερχεται ωρα και νυν εστιν οτε οι νεκροι ακουσονται της φωνης του υιου του θεου και οι ακουσαντες ζησονται');
INSERT INTO `bible_verses_tr` VALUES ('3092', '43', '5', '26', 'ωσπερ γαρ ο πατηρ εχει ζωην εν εαυτω ουτως εδωκεν και τω υιω ζωην εχειν εν εαυτω');
INSERT INTO `bible_verses_tr` VALUES ('3093', '43', '5', '27', 'και εξουσιαν εδωκεν αυτω και κρισιν ποιειν οτι υιος ανθρωπου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3094', '43', '5', '28', 'μη θαυμαζετε τουτο οτι ερχεται ωρα εν η παντες οι εν τοις μνημειοις ακουσονται της φωνης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3095', '43', '5', '29', 'και εκπορευσονται οι τα αγαθα ποιησαντες εις αναστασιν ζωης οι δε τα φαυλα πραξαντες εις αναστασιν κρισεως');
INSERT INTO `bible_verses_tr` VALUES ('3096', '43', '5', '30', 'ου δυναμαι εγω ποιειν απ εμαυτου ουδεν καθως ακουω κρινω και η κρισις η εμη δικαια εστιν οτι ου ζητω το θελημα το εμον αλλα το θελημα του πεμψαντος με πατρος');
INSERT INTO `bible_verses_tr` VALUES ('3097', '43', '5', '31', 'εαν εγω μαρτυρω περι εμαυτου η μαρτυρια μου ουκ εστιν αληθης');
INSERT INTO `bible_verses_tr` VALUES ('3098', '43', '5', '32', 'αλλος εστιν ο μαρτυρων περι εμου και οιδα οτι αληθης εστιν η μαρτυρια ην μαρτυρει περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('3099', '43', '5', '33', 'υμεις απεσταλκατε προς ιωαννην και μεμαρτυρηκεν τη αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('3100', '43', '5', '34', 'εγω δε ου παρα ανθρωπου την μαρτυριαν λαμβανω αλλα ταυτα λεγω ινα υμεις σωθητε');
INSERT INTO `bible_verses_tr` VALUES ('3101', '43', '5', '35', 'εκεινος ην ο λυχνος ο καιομενος και φαινων υμεις δε ηθελησατε αγαλλιασθηναι προς ωραν εν τω φωτι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3102', '43', '5', '36', 'εγω δε εχω την μαρτυριαν μειζω του ιωαννου τα γαρ εργα α εδωκεν μοι ο πατηρ ινα τελειωσω αυτα αυτα τα εργα α εγω ποιω μαρτυρει περι εμου οτι ο πατηρ με απεσταλκεν');
INSERT INTO `bible_verses_tr` VALUES ('3103', '43', '5', '37', 'και ο πεμψας με πατηρ αυτος μεμαρτυρηκεν περι εμου ουτε φωνην αυτου ακηκοατε πωποτε ουτε ειδος αυτου εωρακατε');
INSERT INTO `bible_verses_tr` VALUES ('3104', '43', '5', '38', 'και τον λογον αυτου ουκ εχετε μενοντα εν υμιν οτι ον απεστειλεν εκεινος τουτω υμεις ου πιστευετε');
