INSERT INTO `bible_verses_tr` VALUES ('3109', '43', '5', '43', 'εγω εληλυθα εν τω ονοματι του πατρος μου και ου λαμβανετε με εαν αλλος ελθη εν τω ονοματι τω ιδιω εκεινον ληψεσθε');
INSERT INTO `bible_verses_tr` VALUES ('3110', '43', '5', '44', 'πως δυνασθε υμεις πιστευσαι δοξαν παρα αλληλων λαμβανοντες και την δοξαν την παρα του μονου θεου ου ζητειτε');
INSERT INTO `bible_verses_tr` VALUES ('3111', '43', '5', '45', 'μη δοκειτε οτι εγω κατηγορησω υμων προς τον πατερα εστιν ο κατηγορων υμων μωσης εις ον υμεις ηλπικατε');
INSERT INTO `bible_verses_tr` VALUES ('3112', '43', '5', '46', 'ει γαρ επιστευετε μωση επιστευετε αν εμοι περι γαρ εμου εκεινος εγραψεν');
INSERT INTO `bible_verses_tr` VALUES ('3113', '43', '5', '47', 'ει δε τοις εκεινου γραμμασιν ου πιστευετε πως τοις εμοις ρημασιν πιστευσετε');
INSERT INTO `bible_verses_tr` VALUES ('3114', '43', '6', '1', 'μετα ταυτα απηλθεν ο ιησους περαν της θαλασσης της γαλιλαιας της τιβεριαδος');
