INSERT INTO `bible_verses_tr` VALUES ('3116', '43', '6', '3', 'ανηλθεν δε εις το ορος ο ιησους και εκει εκαθητο μετα των μαθητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3117', '43', '6', '4', 'ην δε εγγυς το πασχα η εορτη των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3118', '43', '6', '5', 'επαρας ουν ο ιησους τους οφθαλμους και θεασαμενος οτι πολυς οχλος ερχεται προς αυτον λεγει προς τον φιλιππον ποθεν αγορασομεν αρτους ινα φαγωσιν ουτοι');
