INSERT INTO `bible_verses_tr` VALUES ('3121', '43', '6', '8', 'λεγει αυτω εις εκ των μαθητων αυτου ανδρεας ο αδελφος σιμωνος πετρου');
INSERT INTO `bible_verses_tr` VALUES ('3122', '43', '6', '9', 'εστιν παιδαριον εν ωδε ο εχει πεντε αρτους κριθινους και δυο οψαρια αλλα ταυτα τι εστιν εις τοσουτους');
INSERT INTO `bible_verses_tr` VALUES ('3123', '43', '6', '10', 'ειπεν δε ο ιησους ποιησατε τους ανθρωπους αναπεσειν ην δε χορτος πολυς εν τω τοπω ανεπεσον ουν οι ανδρες τον αριθμον ωσει πεντακισχιλιοι');
INSERT INTO `bible_verses_tr` VALUES ('3124', '43', '6', '11', 'ελαβεν δε τους αρτους ο ιησους και ευχαριστησας διεδωκεν τοις μαθηταις οι δε μαθηται τοις ανακειμενοις ομοιως και εκ των οψαριων οσον ηθελον');
INSERT INTO `bible_verses_tr` VALUES ('3125', '43', '6', '12', 'ως δε ενεπλησθησαν λεγει τοις μαθηταις αυτου συναγαγετε τα περισσευσαντα κλασματα ινα μη τι αποληται');
INSERT INTO `bible_verses_tr` VALUES ('3126', '43', '6', '13', 'συνηγαγον ουν και εγεμισαν δωδεκα κοφινους κλασματων εκ των πεντε αρτων των κριθινων α επερισσευσεν τοις βεβρωκοσιν');
INSERT INTO `bible_verses_tr` VALUES ('3127', '43', '6', '14', 'οι ουν ανθρωποι ιδοντες ο εποιησεν σημειον ο ιησους ελεγον οτι ουτος εστιν αληθως ο προφητης ο ερχομενος εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3128', '43', '6', '15', 'ιησους ουν γνους οτι μελλουσιν ερχεσθαι και αρπαζειν αυτον ινα ποιησωσιν αυτον βασιλεα ανεχωρησεν παλιν εις το ορος αυτος μονος');
