INSERT INTO `bible_verses_tr` VALUES ('3130', '43', '6', '17', 'και εμβαντες εις το πλοιον ηρχοντο περαν της θαλασσης εις καπερναουμ και σκοτια ηδη εγεγονει και ουκ εληλυθει προς αυτους ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('3131', '43', '6', '18', 'η τε θαλασσα ανεμου μεγαλου πνεοντος διηγειρετο');
