INSERT INTO `bible_verses_tr` VALUES ('3133', '43', '6', '20', 'ο δε λεγει αυτοις εγω ειμι μη φοβεισθε');
INSERT INTO `bible_verses_tr` VALUES ('3134', '43', '6', '21', 'ηθελον ουν λαβειν αυτον εις το πλοιον και ευθεως το πλοιον εγενετο επι της γης εις ην υπηγον');
INSERT INTO `bible_verses_tr` VALUES ('3135', '43', '6', '22', 'τη επαυριον ο οχλος ο εστηκως περαν της θαλασσης ιδων οτι πλοιαριον αλλο ουκ ην εκει ει μη εν εκεινο εις ο ενεβησαν οι μαθηται αυτου και οτι ου συνεισηλθεν τοις μαθηταις αυτου ο ιησους εις το πλοιαριον αλλα μονοι οι μαθηται αυτου απηλθον');
INSERT INTO `bible_verses_tr` VALUES ('3136', '43', '6', '23', 'αλλα δε ηλθεν πλοιαρια εκ τιβεριαδος εγγυς του τοπου οπου εφαγον τον αρτον ευχαριστησαντος του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('3137', '43', '6', '24', 'οτε ουν ειδεν ο οχλος οτι ιησους ουκ εστιν εκει ουδε οι μαθηται αυτου ενεβησαν και αυτοι εις τα πλοια και ηλθον εις καπερναουμ ζητουντες τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('3138', '43', '6', '25', 'και ευροντες αυτον περαν της θαλασσης ειπον αυτω ραββι ποτε ωδε γεγονας');
INSERT INTO `bible_verses_tr` VALUES ('3139', '43', '6', '26', 'απεκριθη αυτοις ο ιησους και ειπεν αμην αμην λεγω υμιν ζητειτε με ουχ οτι ειδετε σημεια αλλ οτι εφαγετε εκ των αρτων και εχορτασθητε');
INSERT INTO `bible_verses_tr` VALUES ('3140', '43', '6', '27', 'εργαζεσθε μη την βρωσιν την απολλυμενην αλλα την βρωσιν την μενουσαν εις ζωην αιωνιον ην ο υιος του ανθρωπου υμιν δωσει τουτον γαρ ο πατηρ εσφραγισεν ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('3141', '43', '6', '28', 'ειπον ουν προς αυτον τι  {VAR1: ποιουμεν } {VAR2: ποιωμεν } ινα εργαζωμεθα τα εργα του θεου');
INSERT INTO `bible_verses_tr` VALUES ('3142', '43', '6', '29', 'απεκριθη ο ιησους και ειπεν αυτοις τουτο εστιν το εργον του θεου ινα πιστευσητε εις ον απεστειλεν εκεινος');
