INSERT INTO `bible_verses_tr` VALUES ('3144', '43', '6', '31', 'οι πατερες ημων το μαννα εφαγον εν τη ερημω καθως εστιν γεγραμμενον αρτον εκ του ουρανου εδωκεν αυτοις φαγειν');
