INSERT INTO `bible_verses_tr` VALUES ('3148', '43', '6', '35', 'ειπεν δε αυτοις ο ιησους εγω ειμι ο αρτος της ζωης ο ερχομενος προς με ου μη πειναση και ο πιστευων εις εμε ου μη διψηση πωποτε');
