INSERT INTO `bible_verses_tr` VALUES ('3167', '43', '6', '54', 'ο τρωγων μου την σαρκα και πινων μου το αιμα εχει ζωην αιωνιον και εγω αναστησω αυτον τη εσχατη ημερα');
