INSERT INTO `bible_verses_tr` VALUES ('3174', '43', '6', '61', 'ειδως δε ο ιησους εν εαυτω οτι γογγυζουσιν περι τουτου οι μαθηται αυτου ειπεν αυτοις τουτο υμας σκανδαλιζει');
INSERT INTO `bible_verses_tr` VALUES ('3175', '43', '6', '62', 'εαν ουν θεωρητε τον υιον του ανθρωπου αναβαινοντα οπου ην το προτερον');
INSERT INTO `bible_verses_tr` VALUES ('3176', '43', '6', '63', 'το πνευμα εστιν το ζωοποιουν η σαρξ ουκ ωφελει ουδεν τα ρηματα α εγω λαλω υμιν πνευμα εστιν και ζωη εστιν');
