INSERT INTO `bible_verses_tr` VALUES ('3184', '43', '6', '71', 'ελεγεν δε τον ιουδαν σιμωνος ισκαριωτην ουτος γαρ ημελλεν αυτον παραδιδοναι εις ων εκ των δωδεκα');
