INSERT INTO `bible_verses_tr` VALUES ('3188', '43', '7', '4', 'ουδεις γαρ εν κρυπτω τι ποιει και ζητει αυτος εν παρρησια ειναι ει ταυτα ποιεις φανερωσον σεαυτον τω κοσμω');
INSERT INTO `bible_verses_tr` VALUES ('3189', '43', '7', '5', 'ουδε γαρ οι αδελφοι αυτου επιστευον εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3190', '43', '7', '6', 'λεγει ουν αυτοις ο ιησους ο καιρος ο εμος ουπω παρεστιν ο δε καιρος ο υμετερος παντοτε εστιν ετοιμος');
INSERT INTO `bible_verses_tr` VALUES ('3191', '43', '7', '7', 'ου δυναται ο κοσμος μισειν υμας εμε δε μισει οτι εγω μαρτυρω περι αυτου οτι τα εργα αυτου πονηρα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3192', '43', '7', '8', 'υμεις αναβητε εις την εορτην ταυτην εγω ουπω αναβαινω εις την εορτην ταυτην οτι ο καιρος ο εμος ουπω πεπληρωται');
INSERT INTO `bible_verses_tr` VALUES ('3193', '43', '7', '9', 'ταυτα δε ειπων αυτοις εμεινεν εν τη γαλιλαια');
INSERT INTO `bible_verses_tr` VALUES ('3194', '43', '7', '10', 'ως δε ανεβησαν οι αδελφοι αυτου τοτε και αυτος ανεβη εις την εορτην ου φανερως αλλ ως εν κρυπτω');
INSERT INTO `bible_verses_tr` VALUES ('3195', '43', '7', '11', 'οι ουν ιουδαιοι εζητουν αυτον εν τη εορτη και ελεγον που εστιν εκεινος');
INSERT INTO `bible_verses_tr` VALUES ('3196', '43', '7', '12', 'και γογγυσμος πολυς περι αυτου ην εν τοις οχλοις οι μεν ελεγον οτι αγαθος εστιν αλλοι δε ελεγον ου αλλα πλανα τον οχλον');
INSERT INTO `bible_verses_tr` VALUES ('3197', '43', '7', '13', 'ουδεις μεντοι παρρησια ελαλει περι αυτου δια τον φοβον των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3198', '43', '7', '14', 'ηδη δε της εορτης μεσουσης ανεβη ο ιησους εις το ιερον και εδιδασκεν');
INSERT INTO `bible_verses_tr` VALUES ('3199', '43', '7', '15', 'και εθαυμαζον οι ιουδαιοι λεγοντες πως ουτος γραμματα οιδεν μη μεμαθηκως');
INSERT INTO `bible_verses_tr` VALUES ('3200', '43', '7', '16', 'απεκριθη αυτοις ο ιησους και ειπεν η εμη διδαχη ουκ εστιν εμη αλλα του πεμψαντος με');
INSERT INTO `bible_verses_tr` VALUES ('3201', '43', '7', '17', 'εαν τις θελη το θελημα αυτου ποιειν γνωσεται περι της διδαχης ποτερον εκ του θεου εστιν η εγω απ εμαυτου λαλω');
INSERT INTO `bible_verses_tr` VALUES ('3202', '43', '7', '18', 'ο αφ εαυτου λαλων την δοξαν την ιδιαν ζητει ο δε ζητων την δοξαν του πεμψαντος αυτον ουτος αληθης εστιν και αδικια εν αυτω ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3203', '43', '7', '19', 'ου μωσης δεδωκεν υμιν τον νομον και ουδεις εξ υμων ποιει τον νομον τι με ζητειτε αποκτειναι');
INSERT INTO `bible_verses_tr` VALUES ('3204', '43', '7', '20', 'απεκριθη ο οχλος και ειπεν δαιμονιον εχεις τις σε ζητει αποκτειναι');
INSERT INTO `bible_verses_tr` VALUES ('3205', '43', '7', '21', 'απεκριθη ο ιησους και ειπεν αυτοις εν εργον εποιησα και παντες θαυμαζετε');
INSERT INTO `bible_verses_tr` VALUES ('3206', '43', '7', '22', 'δια τουτο μωσης δεδωκεν υμιν την περιτομην ουχ οτι εκ του μωσεως εστιν αλλ εκ των πατερων και εν σαββατω περιτεμνετε ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('3207', '43', '7', '23', 'ει περιτομην λαμβανει ανθρωπος εν σαββατω ινα μη λυθη ο νομος μωσεως εμοι χολατε οτι ολον ανθρωπον υγιη εποιησα εν σαββατω');
INSERT INTO `bible_verses_tr` VALUES ('3208', '43', '7', '24', 'μη κρινετε κατ οψιν αλλα την δικαιαν κρισιν κρινατε');
INSERT INTO `bible_verses_tr` VALUES ('3209', '43', '7', '25', 'ελεγον ουν τινες εκ των ιεροσολυμιτων ουχ ουτος εστιν ον ζητουσιν αποκτειναι');
INSERT INTO `bible_verses_tr` VALUES ('3210', '43', '7', '26', 'και ιδε παρρησια λαλει και ουδεν αυτω λεγουσιν μηποτε αληθως εγνωσαν οι αρχοντες οτι ουτος εστιν αληθως ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('3211', '43', '7', '27', 'αλλα τουτον οιδαμεν ποθεν εστιν ο δε χριστος οταν ερχηται ουδεις γινωσκει ποθεν εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3212', '43', '7', '28', 'εκραξεν ουν εν τω ιερω διδασκων ο ιησους και λεγων καμε οιδατε και οιδατε ποθεν ειμι και απ εμαυτου ουκ εληλυθα αλλ εστιν αληθινος ο πεμψας με ον υμεις ουκ οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('3213', '43', '7', '29', 'εγω δε οιδα αυτον οτι παρ αυτου ειμι κακεινος με απεστειλεν');
INSERT INTO `bible_verses_tr` VALUES ('3214', '43', '7', '30', 'εζητουν ουν αυτον πιασαι και ουδεις επεβαλεν επ αυτον την χειρα οτι ουπω εληλυθει η ωρα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3215', '43', '7', '31', 'πολλοι δε εκ του οχλου επιστευσαν εις αυτον και ελεγον οτι ο χριστος οταν ελθη μητι πλειονα σημεια τουτων ποιησει ων ουτος εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('3216', '43', '7', '32', 'ηκουσαν οι φαρισαιοι του οχλου γογγυζοντος περι αυτου ταυτα και απεστειλαν οι φαρισαιοι και οι αρχιερεις υπηρετας ινα πιασωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3217', '43', '7', '33', 'ειπεν ουν αυτοις ο ιησους ετι μικρον χρονον μεθ υμων ειμι και υπαγω προς τον πεμψαντα με');
INSERT INTO `bible_verses_tr` VALUES ('3218', '43', '7', '34', 'ζητησετε με και ουχ ευρησετε και οπου ειμι εγω υμεις ου δυνασθε ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('3219', '43', '7', '35', 'ειπον ουν οι ιουδαιοι προς εαυτους που ουτος μελλει πορευεσθαι οτι ημεις ουχ ευρησομεν αυτον μη εις την διασποραν των ελληνων μελλει πορευεσθαι και διδασκειν τους ελληνας');
INSERT INTO `bible_verses_tr` VALUES ('3220', '43', '7', '36', 'τις εστιν ουτος ο λογος ον ειπεν ζητησετε με και ουχ ευρησετε και οπου ειμι εγω υμεις ου δυνασθε ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('3221', '43', '7', '37', 'εν δε τη εσχατη ημερα τη μεγαλη της εορτης ειστηκει ο ιησους και εκραξεν λεγων εαν τις διψα ερχεσθω προς με και πινετω');
INSERT INTO `bible_verses_tr` VALUES ('3222', '43', '7', '38', 'ο πιστευων εις εμε καθως ειπεν η γραφη ποταμοι εκ της κοιλιας αυτου ρευσουσιν υδατος ζωντος');
INSERT INTO `bible_verses_tr` VALUES ('3223', '43', '7', '39', 'τουτο δε ειπεν περι του πνευματος ου εμελλον λαμβανειν οι πιστευοντες εις αυτον ουπω γαρ ην πνευμα αγιον οτι ο ιησους ουδεπω εδοξασθη');
INSERT INTO `bible_verses_tr` VALUES ('3224', '43', '7', '40', 'πολλοι ουν εκ του οχλου ακουσαντες τον λογον ελεγον ουτος εστιν αληθως ο προφητης');
INSERT INTO `bible_verses_tr` VALUES ('3225', '43', '7', '41', 'αλλοι ελεγον ουτος εστιν ο χριστος αλλοι δε ελεγον μη γαρ εκ της γαλιλαιας ο χριστος ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('3226', '43', '7', '42', 'ουχι η γραφη ειπεν οτι εκ του σπερματος δαβιδ και απο βηθλεεμ της κωμης οπου ην δαβιδ ο χριστος ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('3227', '43', '7', '43', 'σχισμα ουν εν τω οχλω εγενετο δι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3228', '43', '7', '44', 'τινες δε ηθελον εξ αυτων πιασαι αυτον αλλ ουδεις επεβαλεν επ αυτον τας χειρας');
INSERT INTO `bible_verses_tr` VALUES ('3229', '43', '7', '45', 'ηλθον ουν οι υπηρεται προς τους αρχιερεις και φαρισαιους και ειπον αυτοις εκεινοι δια τι ουκ ηγαγετε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3230', '43', '7', '46', 'απεκριθησαν οι υπηρεται ουδεποτε ουτως ελαλησεν ανθρωπος ως ουτος ο ανθρωπος');
INSERT INTO `bible_verses_tr` VALUES ('3231', '43', '7', '47', 'απεκριθησαν ουν αυτοις οι φαρισαιοι μη και υμεις πεπλανησθε');
INSERT INTO `bible_verses_tr` VALUES ('3232', '43', '7', '48', 'μη τις εκ των αρχοντων επιστευσεν εις αυτον η εκ των φαρισαιων');
INSERT INTO `bible_verses_tr` VALUES ('3233', '43', '7', '49', 'αλλ ο οχλος ουτος ο μη γινωσκων τον νομον επικαταρατοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('3234', '43', '7', '50', 'λεγει νικοδημος προς αυτους ο ελθων νυκτος προς αυτον εις ων εξ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3235', '43', '7', '51', 'μη ο νομος ημων κρινει τον ανθρωπον εαν μη ακουση παρ αυτου προτερον και γνω τι ποιει');
INSERT INTO `bible_verses_tr` VALUES ('3236', '43', '7', '52', 'απεκριθησαν και ειπον αυτω μη και συ εκ της γαλιλαιας ει ερευνησον και ιδε οτι προφητης εκ της γαλιλαιας ουκ εγηγερται');
INSERT INTO `bible_verses_tr` VALUES ('3237', '43', '7', '53', 'και επορευθη εκαστος εις τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3238', '43', '8', '1', 'ιησους δε επορευθη εις το ορος των ελαιων');
INSERT INTO `bible_verses_tr` VALUES ('3239', '43', '8', '2', 'ορθρου δε παλιν παρεγενετο εις το ιερον και πας ο λαος ηρχετο προς αυτον και καθισας εδιδασκεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3240', '43', '8', '3', 'αγουσιν δε οι γραμματεις και οι φαρισαιοι προς αυτον γυναικα εν μοιχεια κατειλημμενην και στησαντες αυτην εν μεσω');
INSERT INTO `bible_verses_tr` VALUES ('3241', '43', '8', '4', 'λεγουσιν αυτω διδασκαλε αυτη η γυνη κατειληφθη επαυτοφωρω μοιχευομενη');
INSERT INTO `bible_verses_tr` VALUES ('3242', '43', '8', '5', 'εν δε τω νομω μωσης ημιν ενετειλατο τας τοιαυτας λιθοβολεισθαι συ ουν τι λεγεις');
INSERT INTO `bible_verses_tr` VALUES ('3243', '43', '8', '6', 'τουτο δε ελεγον πειραζοντες αυτον ινα εχωσιν κατηγορειν αυτου ο δε ιησους κατω κυψας τω δακτυλω εγραφεν εις την γην  {VAR2: μη προσποιουμενος }');
INSERT INTO `bible_verses_tr` VALUES ('3244', '43', '8', '7', 'ως δε επεμενον ερωτωντες αυτον ανακυψας ειπεν προς αυτους ο αναμαρτητος υμων πρωτος τον λιθον επ αυτη βαλετω');
INSERT INTO `bible_verses_tr` VALUES ('3245', '43', '8', '8', 'και παλιν κατω κυψας εγραφεν εις την γην');
INSERT INTO `bible_verses_tr` VALUES ('3246', '43', '8', '9', 'οι δε ακουσαντες και υπο της συνειδησεως ελεγχομενοι εξηρχοντο εις καθ εις αρξαμενοι απο των πρεσβυτερων εως των εσχατων και κατελειφθη μονος ο ιησους και η γυνη εν μεσω εστωσα');
INSERT INTO `bible_verses_tr` VALUES ('3247', '43', '8', '10', 'ανακυψας δε ο ιησους και μηδενα θεασαμενος πλην της γυναικος ειπεν αυτη η γυνη που εισιν εκεινοι οι κατηγοροι σου ουδεις σε κατεκρινεν');
INSERT INTO `bible_verses_tr` VALUES ('3248', '43', '8', '11', 'η δε ειπεν ουδεις κυριε ειπεν δε αυτη ο ιησους ουδε εγω σε κατακρινω πορευου και μηκετι αμαρτανε');
INSERT INTO `bible_verses_tr` VALUES ('3249', '43', '8', '12', 'παλιν ουν ο ιησους αυτοις ελαλησεν λεγων εγω ειμι το φως του κοσμου ο ακολουθων εμοι ου μη περιπατησει εν τη σκοτια αλλ εξει το φως της ζωης');
INSERT INTO `bible_verses_tr` VALUES ('3250', '43', '8', '13', 'ειπον ουν αυτω οι φαρισαιοι συ περι σεαυτου μαρτυρεις η μαρτυρια σου ουκ εστιν αληθης');
INSERT INTO `bible_verses_tr` VALUES ('3251', '43', '8', '14', 'απεκριθη ιησους και ειπεν αυτοις καν εγω μαρτυρω περι εμαυτου αληθης εστιν η μαρτυρια μου οτι οιδα ποθεν ηλθον και που υπαγω υμεις δε ουκ οιδατε ποθεν ερχομαι και που υπαγω');
INSERT INTO `bible_verses_tr` VALUES ('3252', '43', '8', '15', 'υμεις κατα την σαρκα κρινετε εγω ου κρινω ουδενα');
INSERT INTO `bible_verses_tr` VALUES ('3253', '43', '8', '16', 'και εαν κρινω δε εγω η κρισις η εμη αληθης εστιν οτι μονος ουκ ειμι αλλ εγω και ο πεμψας με πατηρ');
INSERT INTO `bible_verses_tr` VALUES ('3254', '43', '8', '17', 'και εν τω νομω δε τω υμετερω γεγραπται οτι δυο ανθρωπων η μαρτυρια αληθης εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3255', '43', '8', '18', 'εγω ειμι ο μαρτυρων περι εμαυτου και μαρτυρει περι εμου ο πεμψας με πατηρ');
INSERT INTO `bible_verses_tr` VALUES ('3256', '43', '8', '19', 'ελεγον ουν αυτω που εστιν ο πατηρ σου απεκριθη ο ιησους ουτε εμε οιδατε ουτε τον πατερα μου ει εμε ηδειτε και τον πατερα μου ηδειτε αν');
INSERT INTO `bible_verses_tr` VALUES ('3257', '43', '8', '20', 'ταυτα τα ρηματα ελαλησεν ο ιησους εν τω γαζοφυλακιω διδασκων εν τω ιερω και ουδεις επιασεν αυτον οτι ουπω εληλυθει η ωρα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3258', '43', '8', '21', 'ειπεν ουν παλιν αυτοις ο ιησους εγω υπαγω και ζητησετε με και εν τη αμαρτια υμων αποθανεισθε οπου εγω υπαγω υμεις ου δυνασθε ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('3259', '43', '8', '22', 'ελεγον ουν οι ιουδαιοι μητι αποκτενει εαυτον οτι λεγει οπου εγω υπαγω υμεις ου δυνασθε ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('3260', '43', '8', '23', 'και ειπεν αυτοις υμεις εκ των κατω εστε εγω εκ των ανω ειμι υμεις εκ του κοσμου τουτου εστε εγω ουκ ειμι εκ του κοσμου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('3261', '43', '8', '24', 'ειπον ουν υμιν οτι αποθανεισθε εν ταις αμαρτιαις υμων εαν γαρ μη πιστευσητε οτι εγω ειμι αποθανεισθε εν ταις αμαρτιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('3262', '43', '8', '25', 'ελεγον ουν αυτω συ τις ει και ειπεν αυτοις ο ιησους την αρχην ο τι και λαλω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3263', '43', '8', '26', 'πολλα εχω περι υμων λαλειν και κρινειν αλλ ο πεμψας με αληθης εστιν καγω α ηκουσα παρ αυτου ταυτα λεγω εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3264', '43', '8', '27', 'ουκ εγνωσαν οτι τον πατερα αυτοις ελεγεν');
INSERT INTO `bible_verses_tr` VALUES ('3265', '43', '8', '28', 'ειπεν ουν αυτοις ο ιησους οταν υψωσητε τον υιον του ανθρωπου τοτε γνωσεσθε οτι εγω ειμι και απ εμαυτου ποιω ουδεν αλλα καθως εδιδαξεν με ο πατηρ μου ταυτα λαλω');
INSERT INTO `bible_verses_tr` VALUES ('3266', '43', '8', '29', 'και ο πεμψας με μετ εμου εστιν ουκ αφηκεν με μονον ο πατηρ οτι εγω τα αρεστα αυτω ποιω παντοτε');
INSERT INTO `bible_verses_tr` VALUES ('3267', '43', '8', '30', 'ταυτα αυτου λαλουντος πολλοι επιστευσαν εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3268', '43', '8', '31', 'ελεγεν ουν ο ιησους προς τους πεπιστευκοτας αυτω ιουδαιους εαν υμεις μεινητε εν τω λογω τω εμω αληθως μαθηται μου εστε');
INSERT INTO `bible_verses_tr` VALUES ('3269', '43', '8', '32', 'και γνωσεσθε την αληθειαν και η αληθεια ελευθερωσει υμας');
INSERT INTO `bible_verses_tr` VALUES ('3270', '43', '8', '33', 'απεκριθησαν αυτω σπερμα αβρααμ εσμεν και ουδενι δεδουλευκαμεν πωποτε πως συ λεγεις οτι ελευθεροι γενησεσθε');
INSERT INTO `bible_verses_tr` VALUES ('3271', '43', '8', '34', 'απεκριθη αυτοις ο ιησους αμην αμην λεγω υμιν οτι πας ο ποιων την αμαρτιαν δουλος εστιν της αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('3272', '43', '8', '35', 'ο δε δουλος ου μενει εν τη οικια εις τον αιωνα ο υιος μενει εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('3273', '43', '8', '36', 'εαν ουν ο υιος υμας ελευθερωση οντως ελευθεροι εσεσθε');
INSERT INTO `bible_verses_tr` VALUES ('3274', '43', '8', '37', 'οιδα οτι σπερμα αβρααμ εστε αλλα ζητειτε με αποκτειναι οτι ο λογος ο εμος ου χωρει εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3275', '43', '8', '38', 'εγω ο εωρακα παρα τω πατρι μου λαλω και υμεις ουν ο εωρακατε παρα τω πατρι υμων ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('3276', '43', '8', '39', 'απεκριθησαν και ειπον αυτω ο πατηρ ημων αβρααμ εστιν λεγει αυτοις ο ιησους ει τεκνα του αβρααμ ητε τα εργα του αβρααμ εποιειτε αν');
INSERT INTO `bible_verses_tr` VALUES ('3277', '43', '8', '40', 'νυν δε ζητειτε με αποκτειναι ανθρωπον ος την αληθειαν υμιν λελαληκα ην ηκουσα παρα του θεου τουτο αβρααμ ουκ εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('3278', '43', '8', '41', 'υμεις ποιειτε τα εργα του πατρος υμων ειπον ουν αυτω ημεις εκ πορνειας ου γεγεννημεθα ενα πατερα εχομεν τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('3279', '43', '8', '42', 'ειπεν  {VAR1: ουν } αυτοις ο ιησους ει ο θεος πατηρ υμων ην ηγαπατε αν εμε εγω γαρ εκ του θεου εξηλθον και ηκω ουδε γαρ απ εμαυτου εληλυθα αλλ εκεινος με απεστειλεν');
INSERT INTO `bible_verses_tr` VALUES ('3280', '43', '8', '43', 'δια τι την λαλιαν την εμην ου γινωσκετε οτι ου δυνασθε ακουειν τον λογον τον εμον');
INSERT INTO `bible_verses_tr` VALUES ('3281', '43', '8', '44', 'υμεις εκ πατρος του διαβολου εστε και τας επιθυμιας του πατρος υμων θελετε ποιειν εκεινος ανθρωποκτονος ην απ αρχης και εν τη αληθεια ουχ εστηκεν οτι ουκ εστιν αληθεια εν αυτω οταν λαλη το ψευδος εκ των ιδιων λαλει οτι ψευστης εστιν και ο πατηρ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3282', '43', '8', '45', 'εγω δε οτι την αληθειαν λεγω ου πιστευετε μοι');
INSERT INTO `bible_verses_tr` VALUES ('3283', '43', '8', '46', 'τις εξ υμων ελεγχει με περι αμαρτιας ει δε αληθειαν λεγω δια τι υμεις ου πιστευετε μοι');
INSERT INTO `bible_verses_tr` VALUES ('3284', '43', '8', '47', 'ο ων εκ του θεου τα ρηματα του θεου ακουει δια τουτο υμεις ουκ ακουετε οτι εκ του θεου ουκ εστε');
INSERT INTO `bible_verses_tr` VALUES ('3285', '43', '8', '48', 'απεκριθησαν ουν οι ιουδαιοι και ειπον αυτω ου καλως λεγομεν ημεις οτι σαμαρειτης ει συ και δαιμονιον εχεις');
INSERT INTO `bible_verses_tr` VALUES ('3286', '43', '8', '49', 'απεκριθη ιησους εγω δαιμονιον ουκ εχω αλλα τιμω τον πατερα μου και υμεις ατιμαζετε με');
INSERT INTO `bible_verses_tr` VALUES ('3287', '43', '8', '50', 'εγω δε ου ζητω την δοξαν μου εστιν ο ζητων και κρινων');
INSERT INTO `bible_verses_tr` VALUES ('3288', '43', '8', '51', 'αμην αμην λεγω υμιν εαν τις τον λογον τον εμον τηρηση θανατον ου μη θεωρηση εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('3289', '43', '8', '52', 'ειπον ουν αυτω οι ιουδαιοι νυν εγνωκαμεν οτι δαιμονιον εχεις αβρααμ απεθανεν και οι προφηται και συ λεγεις εαν τις τον λογον μου τηρηση ου μη γευσεται θανατου εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('3290', '43', '8', '53', 'μη συ μειζων ει του πατρος ημων αβρααμ οστις απεθανεν και οι προφηται απεθανον τινα σεαυτον συ ποιεις');
INSERT INTO `bible_verses_tr` VALUES ('3291', '43', '8', '54', 'απεκριθη ιησους εαν εγω δοξαζω εμαυτον η δοξα μου ουδεν εστιν εστιν ο πατηρ μου ο δοξαζων με ον υμεις λεγετε οτι θεος υμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3292', '43', '8', '55', 'και ουκ εγνωκατε αυτον εγω δε οιδα αυτον και εαν ειπω οτι ουκ οιδα αυτον εσομαι ομοιος υμων ψευστης αλλ οιδα αυτον και τον λογον αυτου τηρω');
INSERT INTO `bible_verses_tr` VALUES ('3293', '43', '8', '56', 'αβρααμ ο πατηρ υμων ηγαλλιασατο ινα ιδη την ημεραν την εμην και ειδεν και εχαρη');
INSERT INTO `bible_verses_tr` VALUES ('3294', '43', '8', '57', 'ειπον ουν οι ιουδαιοι προς αυτον πεντηκοντα ετη ουπω εχεις και αβρααμ εωρακας');
INSERT INTO `bible_verses_tr` VALUES ('3295', '43', '8', '58', 'ειπεν αυτοις ο ιησους αμην αμην λεγω υμιν πριν αβρααμ γενεσθαι εγω ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3296', '43', '8', '59', 'ηραν ουν λιθους ινα βαλωσιν επ αυτον ιησους δε εκρυβη και εξηλθεν εκ του ιερου διελθων δια μεσου αυτων και παρηγεν ουτως');
INSERT INTO `bible_verses_tr` VALUES ('3297', '43', '9', '1', 'και παραγων ειδεν ανθρωπον τυφλον εκ γενετης');
INSERT INTO `bible_verses_tr` VALUES ('3298', '43', '9', '2', 'και ηρωτησαν αυτον οι μαθηται αυτου λεγοντες ραββι τις ημαρτεν ουτος η οι γονεις αυτου ινα τυφλος γεννηθη');
INSERT INTO `bible_verses_tr` VALUES ('3299', '43', '9', '3', 'απεκριθη ο ιησους ουτε ουτος ημαρτεν ουτε οι γονεις αυτου αλλ ινα φανερωθη τα εργα του θεου εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3300', '43', '9', '4', 'εμε δει εργαζεσθαι τα εργα του πεμψαντος με εως ημερα εστιν ερχεται νυξ οτε ουδεις δυναται εργαζεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3301', '43', '9', '5', 'οταν εν τω κοσμω ω φως ειμι του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('3302', '43', '9', '6', 'ταυτα ειπων επτυσεν χαμαι και εποιησεν πηλον εκ του πτυσματος και επεχρισεν τον πηλον επι τους οφθαλμους του τυφλου');
INSERT INTO `bible_verses_tr` VALUES ('3303', '43', '9', '7', 'και ειπεν αυτω υπαγε νιψαι εις την κολυμβηθραν του σιλωαμ ο ερμηνευεται απεσταλμενος απηλθεν ουν και ενιψατο και ηλθεν βλεπων');
INSERT INTO `bible_verses_tr` VALUES ('3304', '43', '9', '8', 'οι ουν γειτονες και οι θεωρουντες αυτον το προτερον οτι τυφλος ην ελεγον ουχ ουτος εστιν ο καθημενος και προσαιτων');
INSERT INTO `bible_verses_tr` VALUES ('3305', '43', '9', '9', 'αλλοι ελεγον οτι ουτος εστιν αλλοι δε οτι ομοιος αυτω εστιν εκεινος ελεγεν οτι εγω ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3306', '43', '9', '10', 'ελεγον ουν αυτω πως ανεωχθησαν σου οι οφθαλμοι');
INSERT INTO `bible_verses_tr` VALUES ('3307', '43', '9', '11', 'απεκριθη εκεινος και ειπεν ανθρωπος λεγομενος ιησους πηλον εποιησεν και επεχρισεν μου τους οφθαλμους και ειπεν μοι υπαγε εις την κολυμβηθραν του σιλωαμ και νιψαι απελθων δε και νιψαμενος ανεβλεψα');
INSERT INTO `bible_verses_tr` VALUES ('3308', '43', '9', '12', 'ειπον ουν αυτω που εστιν εκεινος λεγει ουκ οιδα');
INSERT INTO `bible_verses_tr` VALUES ('3309', '43', '9', '13', 'αγουσιν αυτον προς τους φαρισαιους τον ποτε τυφλον');
INSERT INTO `bible_verses_tr` VALUES ('3310', '43', '9', '14', 'ην δε σαββατον οτε τον πηλον εποιησεν ο ιησους και ανεωξεν αυτου τους οφθαλμους');
INSERT INTO `bible_verses_tr` VALUES ('3311', '43', '9', '15', 'παλιν ουν ηρωτων αυτον και οι φαρισαιοι πως ανεβλεψεν ο δε ειπεν αυτοις πηλον επεθηκεν επι τους οφθαλμους μου και ενιψαμην και βλεπω');
INSERT INTO `bible_verses_tr` VALUES ('3312', '43', '9', '16', 'ελεγον ουν εκ των φαρισαιων τινες ουτος ο ανθρωπος ουκ εστιν παρα του θεου οτι το σαββατον ου τηρει αλλοι ελεγον πως δυναται ανθρωπος αμαρτωλος τοιαυτα σημεια ποιειν και σχισμα ην εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3313', '43', '9', '17', 'λεγουσιν τω τυφλω παλιν συ τι λεγεις περι αυτου οτι ηνοιξεν σου τους οφθαλμους ο δε ειπεν οτι προφητης εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3314', '43', '9', '18', 'ουκ επιστευσαν ουν οι ιουδαιοι περι αυτου οτι τυφλος ην και ανεβλεψεν εως οτου εφωνησαν τους γονεις αυτου του αναβλεψαντος');
INSERT INTO `bible_verses_tr` VALUES ('3315', '43', '9', '19', 'και ηρωτησαν αυτους λεγοντες ουτος εστιν ο υιος υμων ον υμεις λεγετε οτι τυφλος εγεννηθη πως ουν αρτι βλεπει');
INSERT INTO `bible_verses_tr` VALUES ('3316', '43', '9', '20', 'απεκριθησαν αυτοις οι γονεις αυτου και ειπον οιδαμεν οτι ουτος εστιν ο υιος ημων και οτι τυφλος εγεννηθη');
INSERT INTO `bible_verses_tr` VALUES ('3317', '43', '9', '21', 'πως δε νυν βλεπει ουκ οιδαμεν η τις ηνοιξεν αυτου τους οφθαλμους ημεις ουκ οιδαμεν αυτος ηλικιαν εχει αυτον ερωτησατε αυτος περι αυτου λαλησει');
INSERT INTO `bible_verses_tr` VALUES ('3318', '43', '9', '22', 'ταυτα ειπον οι γονεις αυτου οτι εφοβουντο τους ιουδαιους ηδη γαρ συνετεθειντο οι ιουδαιοι ινα εαν τις αυτον ομολογηση χριστον αποσυναγωγος γενηται');
INSERT INTO `bible_verses_tr` VALUES ('3319', '43', '9', '23', 'δια τουτο οι γονεις αυτου ειπον οτι ηλικιαν εχει αυτον ερωτησατε');
INSERT INTO `bible_verses_tr` VALUES ('3320', '43', '9', '24', 'εφωνησαν ουν εκ δευτερου τον ανθρωπον ος ην τυφλος και ειπον αυτω δος δοξαν τω θεω ημεις οιδαμεν οτι ο ανθρωπος ουτος αμαρτωλος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3321', '43', '9', '25', 'απεκριθη ουν εκεινος και ειπεν ει αμαρτωλος εστιν ουκ οιδα εν οιδα οτι τυφλος ων αρτι βλεπω');
INSERT INTO `bible_verses_tr` VALUES ('3322', '43', '9', '26', 'ειπον δε αυτω παλιν τι εποιησεν σοι πως ηνοιξεν σου τους οφθαλμους');
INSERT INTO `bible_verses_tr` VALUES ('3323', '43', '9', '27', 'απεκριθη αυτοις ειπον υμιν ηδη και ουκ ηκουσατε τι παλιν θελετε ακουειν μη και υμεις θελετε αυτου μαθηται γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3324', '43', '9', '28', 'ελοιδορησαν ουν αυτον και ειπον συ ει μαθητης εκεινου ημεις δε του μωσεως εσμεν μαθηται');
INSERT INTO `bible_verses_tr` VALUES ('3325', '43', '9', '29', 'ημεις οιδαμεν οτι μωση λελαληκεν ο θεος τουτον δε ουκ οιδαμεν ποθεν εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3326', '43', '9', '30', 'απεκριθη ο ανθρωπος και ειπεν αυτοις εν γαρ τουτω θαυμαστον εστιν οτι υμεις ουκ οιδατε ποθεν εστιν και ανεωξεν μου τους οφθαλμους');
INSERT INTO `bible_verses_tr` VALUES ('3327', '43', '9', '31', 'οιδαμεν δε οτι αμαρτωλων ο θεος ουκ ακουει αλλ εαν τις θεοσεβης η και το θελημα αυτου ποιη τουτου ακουει');
INSERT INTO `bible_verses_tr` VALUES ('3328', '43', '9', '32', 'εκ του αιωνος ουκ ηκουσθη οτι ηνοιξεν τις οφθαλμους τυφλου γεγεννημενου');
INSERT INTO `bible_verses_tr` VALUES ('3329', '43', '9', '33', 'ει μη ην ουτος παρα θεου ουκ ηδυνατο ποιειν ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3330', '43', '9', '34', 'απεκριθησαν και ειπον αυτω εν αμαρτιαις συ εγεννηθης ολος και συ διδασκεις ημας και εξεβαλον αυτον εξω');
INSERT INTO `bible_verses_tr` VALUES ('3331', '43', '9', '35', 'ηκουσεν ο ιησους οτι εξεβαλον αυτον εξω και ευρων αυτον ειπεν αυτω συ πιστευεις εις τον υιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('3332', '43', '9', '36', 'απεκριθη εκεινος και ειπεν τις εστιν κυριε ινα πιστευσω εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3333', '43', '9', '37', 'ειπεν δε αυτω ο ιησους και εωρακας αυτον και ο λαλων μετα σου εκεινος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3334', '43', '9', '38', 'ο δε εφη πιστευω κυριε και προσεκυνησεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3335', '43', '9', '39', 'και ειπεν ο ιησους εις κριμα εγω εις τον κοσμον τουτον ηλθον ινα οι μη βλεποντες βλεπωσιν και οι βλεποντες τυφλοι γενωνται');
INSERT INTO `bible_verses_tr` VALUES ('3336', '43', '9', '40', 'και ηκουσαν εκ των φαρισαιων ταυτα οι οντες μετ αυτου και ειπον αυτω μη και ημεις τυφλοι εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('3337', '43', '9', '41', 'ειπεν αυτοις ο ιησους ει τυφλοι ητε ουκ αν ειχετε αμαρτιαν νυν δε λεγετε οτι βλεπομεν η ουν αμαρτια υμων μενει');
INSERT INTO `bible_verses_tr` VALUES ('3338', '43', '10', '1', 'αμην αμην λεγω υμιν ο μη εισερχομενος δια της θυρας εις την αυλην των προβατων αλλα αναβαινων αλλαχοθεν εκεινος κλεπτης εστιν και ληστης');
INSERT INTO `bible_verses_tr` VALUES ('3339', '43', '10', '2', 'ο δε εισερχομενος δια της θυρας ποιμην εστιν των προβατων');
INSERT INTO `bible_verses_tr` VALUES ('3340', '43', '10', '3', 'τουτω ο θυρωρος ανοιγει και τα προβατα της φωνης αυτου ακουει και τα ιδια προβατα καλει κατ ονομα και εξαγει αυτα');
INSERT INTO `bible_verses_tr` VALUES ('3341', '43', '10', '4', 'και οταν τα ιδια προβατα εκβαλη εμπροσθεν αυτων πορευεται και τα προβατα αυτω ακολουθει οτι οιδασιν την φωνην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3342', '43', '10', '5', 'αλλοτριω δε ου μη ακολουθησωσιν αλλα φευξονται απ αυτου οτι ουκ οιδασιν των αλλοτριων την φωνην');
INSERT INTO `bible_verses_tr` VALUES ('3343', '43', '10', '6', 'ταυτην την παροιμιαν ειπεν αυτοις ο ιησους εκεινοι δε ουκ εγνωσαν τινα ην α ελαλει αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3344', '43', '10', '7', 'ειπεν ουν παλιν αυτοις ο ιησους αμην αμην λεγω υμιν οτι εγω ειμι η θυρα των προβατων');
INSERT INTO `bible_verses_tr` VALUES ('3345', '43', '10', '8', 'παντες οσοι προ εμου ηλθον κλεπται εισιν και λησται αλλ ουκ ηκουσαν αυτων τα προβατα');
INSERT INTO `bible_verses_tr` VALUES ('3346', '43', '10', '9', 'εγω ειμι η θυρα δι εμου εαν τις εισελθη σωθησεται και εισελευσεται και εξελευσεται και νομην ευρησει');
INSERT INTO `bible_verses_tr` VALUES ('3347', '43', '10', '10', 'ο κλεπτης ουκ ερχεται ει μη ινα κλεψη και θυση και απολεση εγω ηλθον ινα ζωην εχωσιν και περισσον εχωσιν');
INSERT INTO `bible_verses_tr` VALUES ('3348', '43', '10', '11', 'εγω ειμι ο ποιμην ο καλος ο ποιμην ο καλος την ψυχην αυτου τιθησιν υπερ των προβατων');
INSERT INTO `bible_verses_tr` VALUES ('3349', '43', '10', '12', 'ο μισθωτος δε και ουκ ων ποιμην ου ουκ εισιν τα προβατα ιδια θεωρει τον λυκον ερχομενον και αφιησιν τα προβατα και φευγει και ο λυκος αρπαζει αυτα και σκορπιζει τα προβατα');
INSERT INTO `bible_verses_tr` VALUES ('3350', '43', '10', '13', 'ο δε μισθωτος φευγει οτι μισθωτος εστιν και ου μελει αυτω περι των προβατων');
INSERT INTO `bible_verses_tr` VALUES ('3351', '43', '10', '14', 'εγω ειμι ο ποιμην ο καλος και γινωσκω τα εμα και γινωσκομαι υπο των εμων');
INSERT INTO `bible_verses_tr` VALUES ('3352', '43', '10', '15', 'καθως γινωσκει με ο πατηρ καγω γινωσκω τον πατερα και την ψυχην μου τιθημι υπερ των προβατων');
INSERT INTO `bible_verses_tr` VALUES ('3353', '43', '10', '16', 'και αλλα προβατα εχω α ουκ εστιν εκ της αυλης ταυτης κακεινα με δει αγαγειν και της φωνης μου ακουσουσιν και γενησεται μια ποιμνη εις ποιμην');
INSERT INTO `bible_verses_tr` VALUES ('3354', '43', '10', '17', 'δια τουτο ο πατηρ με αγαπα οτι εγω τιθημι την ψυχην μου ινα παλιν λαβω αυτην');
INSERT INTO `bible_verses_tr` VALUES ('3355', '43', '10', '18', 'ουδεις αιρει αυτην απ εμου αλλ εγω τιθημι αυτην απ εμαυτου εξουσιαν εχω θειναι αυτην και εξουσιαν εχω παλιν λαβειν αυτην ταυτην την εντολην ελαβον παρα του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('3356', '43', '10', '19', 'σχισμα ουν παλιν εγενετο εν τοις ιουδαιοις δια τους λογους τουτους');
INSERT INTO `bible_verses_tr` VALUES ('3357', '43', '10', '20', 'ελεγον δε πολλοι εξ αυτων δαιμονιον εχει και μαινεται τι αυτου ακουετε');
INSERT INTO `bible_verses_tr` VALUES ('3358', '43', '10', '21', 'αλλοι ελεγον ταυτα τα ρηματα ουκ εστιν δαιμονιζομενου μη δαιμονιον δυναται τυφλων οφθαλμους ανοιγειν');
INSERT INTO `bible_verses_tr` VALUES ('3359', '43', '10', '22', 'εγενετο δε τα εγκαινια εν τοις ιεροσολυμοις και χειμων ην');
INSERT INTO `bible_verses_tr` VALUES ('3360', '43', '10', '23', 'και περιεπατει ο ιησους εν τω ιερω εν τη στοα του σολομωντος');
INSERT INTO `bible_verses_tr` VALUES ('3361', '43', '10', '24', 'εκυκλωσαν ουν αυτον οι ιουδαιοι και ελεγον αυτω εως ποτε την ψυχην ημων αιρεις ει συ ει ο χριστος ειπε ημιν παρρησια');
INSERT INTO `bible_verses_tr` VALUES ('3362', '43', '10', '25', 'απεκριθη αυτοις ο ιησους ειπον υμιν και ου πιστευετε τα εργα α εγω ποιω εν τω ονοματι του πατρος μου ταυτα μαρτυρει περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('3363', '43', '10', '26', 'αλλ υμεις ου πιστευετε ου γαρ εστε εκ των προβατων των εμων καθως ειπον υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3364', '43', '10', '27', 'τα προβατα τα εμα της φωνης μου ακουει καγω γινωσκω αυτα και ακολουθουσιν μοι');
INSERT INTO `bible_verses_tr` VALUES ('3365', '43', '10', '28', 'καγω ζωην αιωνιον διδωμι αυτοις και ου μη απολωνται εις τον αιωνα και ουχ αρπασει τις αυτα εκ της χειρος μου');
INSERT INTO `bible_verses_tr` VALUES ('3366', '43', '10', '29', 'ο πατηρ μου ος δεδωκεν μοι μειζων παντων εστιν και ουδεις δυναται αρπαζειν εκ της χειρος του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('3367', '43', '10', '30', 'εγω και ο πατηρ εν εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('3368', '43', '10', '31', 'εβαστασαν ουν παλιν λιθους οι ιουδαιοι ινα λιθασωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3369', '43', '10', '32', 'απεκριθη αυτοις ο ιησους πολλα καλα εργα εδειξα υμιν εκ του πατρος μου δια ποιον αυτων εργον λιθαζετε με');
INSERT INTO `bible_verses_tr` VALUES ('3370', '43', '10', '33', 'απεκριθησαν αυτω οι ιουδαιοι λεγοντες περι καλου εργου ου λιθαζομεν σε αλλα περι βλασφημιας και οτι συ ανθρωπος ων ποιεις σεαυτον θεον');
INSERT INTO `bible_verses_tr` VALUES ('3371', '43', '10', '34', 'απεκριθη αυτοις ο ιησους ουκ εστιν γεγραμμενον εν τω νομω υμων εγω ειπα θεοι εστε');
INSERT INTO `bible_verses_tr` VALUES ('3372', '43', '10', '35', 'ει εκεινους ειπεν θεους προς ους ο λογος του θεου εγενετο και ου δυναται λυθηναι η γραφη');
INSERT INTO `bible_verses_tr` VALUES ('3373', '43', '10', '36', 'ον ο πατηρ ηγιασεν και απεστειλεν εις τον κοσμον υμεις λεγετε οτι βλασφημεις οτι ειπον υιος του θεου ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3374', '43', '10', '37', 'ει ου ποιω τα εργα του πατρος μου μη πιστευετε μοι');
INSERT INTO `bible_verses_tr` VALUES ('3375', '43', '10', '38', 'ει δε ποιω καν εμοι μη πιστευητε τοις εργοις πιστευσατε ινα γνωτε και πιστευσητε οτι εν εμοι ο πατηρ καγω εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3376', '43', '10', '39', 'εζητουν ουν παλιν αυτον πιασαι και εξηλθεν εκ της χειρος αυτων');
