INSERT INTO `bible_verses_tr` VALUES ('3379', '43', '10', '42', 'και επιστευσαν πολλοι εκει εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3380', '43', '11', '1', 'ην δε τις ασθενων λαζαρος απο βηθανιας εκ της κωμης μαριας και μαρθας της αδελφης αυτης');
INSERT INTO `bible_verses_tr` VALUES ('3381', '43', '11', '2', 'ην δε μαρια η αλειψασα τον κυριον μυρω και εκμαξασα τους ποδας αυτου ταις θριξιν αυτης ης ο αδελφος λαζαρος ησθενει');
INSERT INTO `bible_verses_tr` VALUES ('3382', '43', '11', '3', 'απεστειλαν ουν αι αδελφαι προς αυτον λεγουσαι κυριε ιδε ον φιλεις ασθενει');
INSERT INTO `bible_verses_tr` VALUES ('3383', '43', '11', '4', 'ακουσας δε ο ιησους ειπεν αυτη η ασθενεια ουκ εστιν προς θανατον αλλ υπερ της δοξης του θεου ινα δοξασθη ο υιος του θεου δι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('3384', '43', '11', '5', 'ηγαπα δε ο ιησους την μαρθαν και την αδελφην αυτης και τον λαζαρον');
INSERT INTO `bible_verses_tr` VALUES ('3385', '43', '11', '6', 'ως ουν ηκουσεν οτι ασθενει τοτε μεν εμεινεν εν ω ην τοπω δυο ημερας');
INSERT INTO `bible_verses_tr` VALUES ('3386', '43', '11', '7', 'επειτα μετα τουτο λεγει τοις μαθηταις αγωμεν εις την ιουδαιαν παλιν');
INSERT INTO `bible_verses_tr` VALUES ('3387', '43', '11', '8', 'λεγουσιν αυτω οι μαθηται ραββι νυν εζητουν σε λιθασαι οι ιουδαιοι και παλιν υπαγεις εκει');
INSERT INTO `bible_verses_tr` VALUES ('3388', '43', '11', '9', 'απεκριθη ο ιησους ουχι δωδεκα εισιν ωραι της ημερας εαν τις περιπατη εν τη ημερα ου προσκοπτει οτι το φως του κοσμου τουτου βλεπει');
INSERT INTO `bible_verses_tr` VALUES ('3389', '43', '11', '10', 'εαν δε τις περιπατη εν τη νυκτι προσκοπτει οτι το φως ουκ εστιν εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3390', '43', '11', '11', 'ταυτα ειπεν και μετα τουτο λεγει αυτοις λαζαρος ο φιλος ημων κεκοιμηται αλλα πορευομαι ινα εξυπνισω αυτον');
