INSERT INTO `bible_verses_tr` VALUES ('3393', '43', '11', '14', 'τοτε ουν ειπεν αυτοις ο ιησους παρρησια λαζαρος απεθανεν');
INSERT INTO `bible_verses_tr` VALUES ('3394', '43', '11', '15', 'και χαιρω δι υμας ινα πιστευσητε οτι ουκ ημην εκει αλλ αγωμεν προς αυτον');
