INSERT INTO `bible_verses_tr` VALUES ('3396', '43', '11', '17', 'ελθων ουν ο ιησους ευρεν αυτον τεσσαρας ημερας ηδη εχοντα εν τω μνημειω');
INSERT INTO `bible_verses_tr` VALUES ('3397', '43', '11', '18', 'ην δε η βηθανια εγγυς των ιεροσολυμων ως απο σταδιων δεκαπεντε');
INSERT INTO `bible_verses_tr` VALUES ('3398', '43', '11', '19', 'και πολλοι εκ των ιουδαιων εληλυθεισαν προς τας περι μαρθαν και μαριαν ινα παραμυθησωνται αυτας περι του αδελφου αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3399', '43', '11', '20', 'η ουν μαρθα ως ηκουσεν οτι ο ιησους ερχεται υπηντησεν αυτω μαρια δε εν τω οικω εκαθεζετο');
INSERT INTO `bible_verses_tr` VALUES ('3400', '43', '11', '21', 'ειπεν ουν η μαρθα προς τον ιησουν κυριε ει ης ωδε ο αδελφος μου ουκ αν ετεθνηκει');
