INSERT INTO `bible_verses_tr` VALUES ('3402', '43', '11', '23', 'λεγει αυτη ο ιησους αναστησεται ο αδελφος σου');
INSERT INTO `bible_verses_tr` VALUES ('3403', '43', '11', '24', 'λεγει αυτω μαρθα οιδα οτι αναστησεται εν τη αναστασει εν τη εσχατη ημερα');
INSERT INTO `bible_verses_tr` VALUES ('3404', '43', '11', '25', 'ειπεν αυτη ο ιησους εγω ειμι η αναστασις και η ζωη ο πιστευων εις εμε καν αποθανη ζησεται');
INSERT INTO `bible_verses_tr` VALUES ('3405', '43', '11', '26', 'και πας ο ζων και πιστευων εις εμε ου μη αποθανη εις τον αιωνα πιστευεις τουτο');
INSERT INTO `bible_verses_tr` VALUES ('3406', '43', '11', '27', 'λεγει αυτω ναι κυριε εγω πεπιστευκα οτι συ ει ο χριστος ο υιος του θεου ο εις τον κοσμον ερχομενος');
INSERT INTO `bible_verses_tr` VALUES ('3407', '43', '11', '28', 'και ταυτα ειπουσα απηλθεν και εφωνησεν μαριαν την αδελφην αυτης λαθρα ειπουσα ο διδασκαλος παρεστιν και φωνει σε');
INSERT INTO `bible_verses_tr` VALUES ('3408', '43', '11', '29', 'εκεινη ως ηκουσεν εγειρεται ταχυ και ερχεται προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3409', '43', '11', '30', 'ουπω δε εληλυθει ο ιησους εις την κωμην αλλ ην εν τω τοπω οπου υπηντησεν αυτω η μαρθα');
INSERT INTO `bible_verses_tr` VALUES ('3410', '43', '11', '31', 'οι ουν ιουδαιοι οι οντες μετ αυτης εν τη οικια και παραμυθουμενοι αυτην ιδοντες την μαριαν οτι ταχεως ανεστη και εξηλθεν ηκολουθησαν αυτη λεγοντες οτι υπαγει εις το μνημειον ινα κλαυση εκει');
INSERT INTO `bible_verses_tr` VALUES ('3411', '43', '11', '32', 'η ουν μαρια ως ηλθεν οπου ην ο ιησους ιδουσα αυτον επεσεν εις τους ποδας αυτου λεγουσα αυτω κυριε ει ης ωδε ουκ αν απεθανεν μου ο αδελφος');
INSERT INTO `bible_verses_tr` VALUES ('3412', '43', '11', '33', 'ιησους ουν ως ειδεν αυτην κλαιουσαν και τους συνελθοντας αυτη ιουδαιους κλαιοντας ενεβριμησατο τω πνευματι και εταραξεν εαυτον');
INSERT INTO `bible_verses_tr` VALUES ('3413', '43', '11', '34', 'και ειπεν που τεθεικατε αυτον λεγουσιν αυτω κυριε ερχου και ιδε');
INSERT INTO `bible_verses_tr` VALUES ('3414', '43', '11', '35', 'εδακρυσεν ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('3415', '43', '11', '36', 'ελεγον ουν οι ιουδαιοι ιδε πως εφιλει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3416', '43', '11', '37', 'τινες δε εξ αυτων ειπον ουκ ηδυνατο ουτος ο ανοιξας τους οφθαλμους του τυφλου ποιησαι ινα και ουτος μη αποθανη');
