INSERT INTO `bible_verses_tr` VALUES ('3418', '43', '11', '39', 'λεγει ο ιησους αρατε τον λιθον λεγει αυτω η αδελφη του τεθνηκοτος μαρθα κυριε ηδη οζει τεταρταιος γαρ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3419', '43', '11', '40', 'λεγει αυτη ο ιησους ουκ ειπον σοι οτι εαν πιστευσης οψει την δοξαν του θεου');
