INSERT INTO `bible_verses_tr` VALUES ('3426', '43', '11', '47', 'συνηγαγον ουν οι αρχιερεις και οι φαρισαιοι συνεδριον και ελεγον τι ποιουμεν οτι ουτος ο ανθρωπος πολλα σημεια ποιει');
