INSERT INTO `bible_verses_tr` VALUES ('3430', '43', '11', '51', 'τουτο δε αφ εαυτου ουκ ειπεν αλλα αρχιερευς ων του ενιαυτου εκεινου προεφητευσεν οτι εμελλεν ο ιησους αποθνησκειν υπερ του εθνους');
