INSERT INTO `bible_verses_tr` VALUES ('3434', '43', '11', '55', 'ην δε εγγυς το πασχα των ιουδαιων και ανεβησαν πολλοι εις ιεροσολυμα εκ της χωρας προ του πασχα ινα αγνισωσιν εαυτους');
INSERT INTO `bible_verses_tr` VALUES ('3435', '43', '11', '56', 'εζητουν ουν τον ιησουν και ελεγον μετ αλληλων εν τω ιερω εστηκοτες τι δοκει υμιν οτι ου μη ελθη εις την εορτην');
INSERT INTO `bible_verses_tr` VALUES ('3436', '43', '11', '57', 'δεδωκεισαν δε και οι αρχιερεις και οι φαρισαιοι εντολην ινα εαν τις γνω που εστιν μηνυση οπως πιασωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3437', '43', '12', '1', 'ο ουν ιησους προ εξ ημερων του πασχα ηλθεν εις βηθανιαν οπου ην λαζαρος ο τεθνηκως ον ηγειρεν εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('3438', '43', '12', '2', 'εποιησαν ουν αυτω δειπνον εκει και η μαρθα διηκονει ο δε λαζαρος εις ην των συνανακειμενων αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3439', '43', '12', '3', 'η ουν μαρια λαβουσα λιτραν μυρου ναρδου πιστικης πολυτιμου ηλειψεν τους ποδας του ιησου και εξεμαξεν ταις θριξιν αυτης τους ποδας αυτου η δε οικια επληρωθη εκ της οσμης του μυρου');
INSERT INTO `bible_verses_tr` VALUES ('3440', '43', '12', '4', 'λεγει ουν εις εκ των μαθητων αυτου ιουδας σιμωνος ισκαριωτης ο μελλων αυτον παραδιδοναι');
INSERT INTO `bible_verses_tr` VALUES ('3441', '43', '12', '5', 'δια τι τουτο το μυρον ουκ επραθη τριακοσιων δηναριων και εδοθη πτωχοις');
