INSERT INTO `bible_verses_tr` VALUES ('3447', '43', '12', '11', 'οτι πολλοι δι αυτον υπηγον των ιουδαιων και επιστευον εις τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('3448', '43', '12', '12', 'τη επαυριον οχλος πολυς ο ελθων εις την εορτην ακουσαντες οτι ερχεται ο ιησους εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('3449', '43', '12', '13', 'ελαβον τα βαια των φοινικων και εξηλθον εις υπαντησιν αυτω και εκραζον ωσαννα ευλογημενος ο ερχομενος εν ονοματι κυριου ο βασιλευς του ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('3450', '43', '12', '14', 'ευρων δε ο ιησους οναριον εκαθισεν επ αυτο καθως εστιν γεγραμμενον');
INSERT INTO `bible_verses_tr` VALUES ('3451', '43', '12', '15', 'μη φοβου θυγατερ σιων ιδου ο βασιλευς σου ερχεται καθημενος επι πωλον ονου');
INSERT INTO `bible_verses_tr` VALUES ('3452', '43', '12', '16', 'ταυτα δε ουκ εγνωσαν οι μαθηται αυτου το πρωτον αλλ οτε εδοξασθη ο ιησους τοτε εμνησθησαν οτι ταυτα ην επ αυτω γεγραμμενα και ταυτα εποιησαν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3453', '43', '12', '17', 'εμαρτυρει ουν ο οχλος ο ων μετ αυτου οτε τον λαζαρον εφωνησεν εκ του μνημειου και ηγειρεν αυτον εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('3454', '43', '12', '18', 'δια τουτο και υπηντησεν αυτω ο οχλος οτι ηκουσεν τουτο αυτον πεποιηκεναι το σημειον');
INSERT INTO `bible_verses_tr` VALUES ('3455', '43', '12', '19', 'οι ουν φαρισαιοι ειπον προς εαυτους θεωρειτε οτι ουκ ωφελειτε ουδεν ιδε ο κοσμος οπισω αυτου απηλθεν');
