INSERT INTO `bible_verses_tr` VALUES ('3459', '43', '12', '23', 'ο δε ιησους απεκρινατο αυτοις λεγων εληλυθεν η ωρα ινα δοξασθη ο υιος του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('3460', '43', '12', '24', 'αμην αμην λεγω υμιν εαν μη ο κοκκος του σιτου πεσων εις την γην αποθανη αυτος μονος μενει εαν δε αποθανη πολυν καρπον φερει');
INSERT INTO `bible_verses_tr` VALUES ('3461', '43', '12', '25', 'ο φιλων την ψυχην αυτου απολεσει αυτην και ο μισων την ψυχην αυτου εν τω κοσμω τουτω εις ζωην αιωνιον φυλαξει αυτην');
INSERT INTO `bible_verses_tr` VALUES ('3462', '43', '12', '26', 'εαν εμοι διακονη τις εμοι ακολουθειτω και οπου ειμι εγω εκει και ο διακονος ο εμος εσται και εαν τις εμοι διακονη τιμησει αυτον ο πατηρ');
INSERT INTO `bible_verses_tr` VALUES ('3463', '43', '12', '27', 'νυν η ψυχη μου τεταρακται και τι ειπω πατερ σωσον με εκ της ωρας ταυτης αλλα δια τουτο ηλθον εις την ωραν ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('3464', '43', '12', '28', 'πατερ δοξασον σου το ονομα ηλθεν ουν φωνη εκ του ουρανου και εδοξασα και παλιν δοξασω');
INSERT INTO `bible_verses_tr` VALUES ('3465', '43', '12', '29', 'ο ουν οχλος ο εστως και ακουσας ελεγεν βροντην γεγονεναι αλλοι ελεγον αγγελος αυτω λελαληκεν');
