INSERT INTO `bible_verses_tr` VALUES ('3468', '43', '12', '32', 'καγω εαν υψωθω εκ της γης παντας ελκυσω προς εμαυτον');
INSERT INTO `bible_verses_tr` VALUES ('3469', '43', '12', '33', 'τουτο δε ελεγεν σημαινων ποιω θανατω ημελλεν αποθνησκειν');
INSERT INTO `bible_verses_tr` VALUES ('3470', '43', '12', '34', 'απεκριθη αυτω ο οχλος ημεις ηκουσαμεν εκ του νομου οτι ο χριστος μενει εις τον αιωνα και πως συ λεγεις οτι δει υψωθηναι τον υιον του ανθρωπου τις εστιν ουτος ο υιος του ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('3471', '43', '12', '35', 'ειπεν ουν αυτοις ο ιησους ετι μικρον χρονον το φως μεθ υμων εστιν περιπατειτε εως το φως εχετε ινα μη σκοτια υμας καταλαβη και ο περιπατων εν τη σκοτια ουκ οιδεν που υπαγει');
INSERT INTO `bible_verses_tr` VALUES ('3472', '43', '12', '36', 'εως το φως εχετε πιστευετε εις το φως ινα υιοι φωτος γενησθε ταυτα ελαλησεν ο ιησους και απελθων εκρυβη απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3473', '43', '12', '37', 'τοσαυτα δε αυτου σημεια πεποιηκοτος εμπροσθεν αυτων ουκ επιστευον εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3474', '43', '12', '38', 'ινα ο λογος ησαιου του προφητου πληρωθη ον ειπεν κυριε τις επιστευσεν τη ακοη ημων και ο βραχιων κυριου τινι απεκαλυφθη');
INSERT INTO `bible_verses_tr` VALUES ('3475', '43', '12', '39', 'δια τουτο ουκ ηδυναντο πιστευειν οτι παλιν ειπεν ησαιας');
INSERT INTO `bible_verses_tr` VALUES ('3476', '43', '12', '40', 'τετυφλωκεν αυτων τους οφθαλμους και πεπωρωκεν αυτων την καρδιαν ινα μη ιδωσιν τοις οφθαλμοις και νοησωσιν τη καρδια και επιστραφωσιν και ιασωμαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3477', '43', '12', '41', 'ταυτα ειπεν ησαιας οτε ειδεν την δοξαν αυτου και ελαλησεν περι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3478', '43', '12', '42', 'ομως μεντοι και εκ των αρχοντων πολλοι επιστευσαν εις αυτον αλλα δια τους φαρισαιους ουχ ωμολογουν ινα μη αποσυναγωγοι γενωνται');
INSERT INTO `bible_verses_tr` VALUES ('3479', '43', '12', '43', 'ηγαπησαν γαρ την δοξαν των ανθρωπων μαλλον ηπερ την δοξαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('3480', '43', '12', '44', 'ιησους δε εκραξεν και ειπεν ο πιστευων εις εμε ου πιστευει εις εμε αλλ εις τον πεμψαντα με');
INSERT INTO `bible_verses_tr` VALUES ('3481', '43', '12', '45', 'και ο θεωρων εμε θεωρει τον πεμψαντα με');
INSERT INTO `bible_verses_tr` VALUES ('3482', '43', '12', '46', 'εγω φως εις τον κοσμον εληλυθα ινα πας ο πιστευων εις εμε εν τη σκοτια μη μεινη');
INSERT INTO `bible_verses_tr` VALUES ('3483', '43', '12', '47', 'και εαν τις μου ακουση των ρηματων και μη πιστευση εγω ου κρινω αυτον ου γαρ ηλθον ινα κρινω τον κοσμον αλλ ινα σωσω τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3484', '43', '12', '48', 'ο αθετων εμε και μη λαμβανων τα ρηματα μου εχει τον κρινοντα αυτον ο λογος ον ελαλησα εκεινος κρινει αυτον εν τη εσχατη ημερα');
INSERT INTO `bible_verses_tr` VALUES ('3485', '43', '12', '49', 'οτι εγω εξ εμαυτου ουκ ελαλησα αλλ ο πεμψας με πατηρ αυτος μοι εντολην εδωκεν τι ειπω και τι λαλησω');
INSERT INTO `bible_verses_tr` VALUES ('3486', '43', '12', '50', 'και οιδα οτι η εντολη αυτου ζωη αιωνιος εστιν α ουν λαλω εγω καθως ειρηκεν μοι ο πατηρ ουτως λαλω');
INSERT INTO `bible_verses_tr` VALUES ('3487', '43', '13', '1', 'προ δε της εορτης του πασχα ειδως ο ιησους οτι εληλυθεν αυτου η ωρα ινα μεταβη εκ του κοσμου τουτου προς τον πατερα αγαπησας τους ιδιους τους εν τω κοσμω εις τελος ηγαπησεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3488', '43', '13', '2', 'και δειπνου γενομενου του διαβολου ηδη βεβληκοτος εις την καρδιαν ιουδα σιμωνος ισκαριωτου ινα αυτον παραδω');
INSERT INTO `bible_verses_tr` VALUES ('3489', '43', '13', '3', 'ειδως ο ιησους οτι παντα δεδωκεν αυτω ο πατηρ εις τας χειρας και οτι απο θεου εξηλθεν και προς τον θεον υπαγει');
INSERT INTO `bible_verses_tr` VALUES ('3490', '43', '13', '4', 'εγειρεται εκ του δειπνου και τιθησιν τα ιματια και λαβων λεντιον διεζωσεν εαυτον');
INSERT INTO `bible_verses_tr` VALUES ('3491', '43', '13', '5', 'ειτα βαλλει υδωρ εις τον νιπτηρα και ηρξατο νιπτειν τους ποδας των μαθητων και εκμασσειν τω λεντιω ω ην διεζωσμενος');
INSERT INTO `bible_verses_tr` VALUES ('3492', '43', '13', '6', 'ερχεται ουν προς σιμωνα πετρον και λεγει αυτω εκεινος κυριε συ μου νιπτεις τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('3493', '43', '13', '7', 'απεκριθη ιησους και ειπεν αυτω ο εγω ποιω συ ουκ οιδας αρτι γνωση δε μετα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('3494', '43', '13', '8', 'λεγει αυτω πετρος ου μη νιψης τους ποδας μου εις τον αιωνα απεκριθη αυτω ο ιησους εαν μη νιψω σε ουκ εχεις μερος μετ εμου');
INSERT INTO `bible_verses_tr` VALUES ('3495', '43', '13', '9', 'λεγει αυτω σιμων πετρος κυριε μη τους ποδας μου μονον αλλα και τας χειρας και την κεφαλην');
INSERT INTO `bible_verses_tr` VALUES ('3496', '43', '13', '10', 'λεγει αυτω ο ιησους ο λελουμενος ου χρειαν εχει η τους ποδας νιψασθαι αλλ εστιν καθαρος ολος και υμεις καθαροι εστε αλλ ουχι παντες');
INSERT INTO `bible_verses_tr` VALUES ('3497', '43', '13', '11', 'ηδει γαρ τον παραδιδοντα αυτον δια τουτο ειπεν ουχι παντες καθαροι εστε');
INSERT INTO `bible_verses_tr` VALUES ('3498', '43', '13', '12', 'οτε ουν ενιψεν τους ποδας αυτων και ελαβεν τα ιματια αυτου αναπεσων παλιν ειπεν αυτοις γινωσκετε τι πεποιηκα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3499', '43', '13', '13', 'υμεις φωνειτε με ο διδασκαλος και ο κυριος και καλως λεγετε ειμι γαρ');
INSERT INTO `bible_verses_tr` VALUES ('3500', '43', '13', '14', 'ει ουν εγω ενιψα υμων τους ποδας ο κυριος και ο διδασκαλος και υμεις οφειλετε αλληλων νιπτειν τους ποδας');
INSERT INTO `bible_verses_tr` VALUES ('3501', '43', '13', '15', 'υποδειγμα γαρ εδωκα υμιν ινα καθως εγω εποιησα υμιν και υμεις ποιητε');
INSERT INTO `bible_verses_tr` VALUES ('3502', '43', '13', '16', 'αμην αμην λεγω υμιν ουκ εστιν δουλος μειζων του κυριου αυτου ουδε αποστολος μειζων του πεμψαντος αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3503', '43', '13', '17', 'ει ταυτα οιδατε μακαριοι εστε εαν ποιητε αυτα');
INSERT INTO `bible_verses_tr` VALUES ('3504', '43', '13', '18', 'ου περι παντων υμων λεγω εγω οιδα ους εξελεξαμην αλλ ινα η γραφη πληρωθη ο τρωγων μετ εμου τον αρτον επηρεν επ εμε την πτερναν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3505', '43', '13', '19', 'απ αρτι λεγω υμιν προ του γενεσθαι ινα οταν γενηται πιστευσητε οτι εγω ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3506', '43', '13', '20', 'αμην αμην λεγω υμιν ο λαμβανων εαν τινα πεμψω εμε λαμβανει ο δε εμε λαμβανων λαμβανει τον πεμψαντα με');
INSERT INTO `bible_verses_tr` VALUES ('3507', '43', '13', '21', 'ταυτα ειπων ο ιησους εταραχθη τω πνευματι και εμαρτυρησεν και ειπεν αμην αμην λεγω υμιν οτι εις εξ υμων παραδωσει με');
INSERT INTO `bible_verses_tr` VALUES ('3508', '43', '13', '22', 'εβλεπον ουν εις αλληλους οι μαθηται απορουμενοι περι τινος λεγει');
INSERT INTO `bible_verses_tr` VALUES ('3509', '43', '13', '23', 'ην δε ανακειμενος εις των μαθητων αυτου εν τω κολπω του ιησου ον ηγαπα ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('3510', '43', '13', '24', 'νευει ουν τουτω σιμων πετρος πυθεσθαι τις αν ειη περι ου λεγει');
INSERT INTO `bible_verses_tr` VALUES ('3511', '43', '13', '25', 'επιπεσων δε εκεινος επι το στηθος του ιησου λεγει αυτω κυριε τις εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3512', '43', '13', '26', 'αποκρινεται ο ιησους εκεινος εστιν ω εγω βαψας το ψωμιον επιδωσω και εμβαψας το ψωμιον διδωσιν ιουδα σιμωνος ισκαριωτη');
INSERT INTO `bible_verses_tr` VALUES ('3513', '43', '13', '27', 'και μετα το ψωμιον τοτε εισηλθεν εις εκεινον ο σατανας λεγει ουν αυτω ο ιησους ο ποιεις ποιησον ταχιον');
INSERT INTO `bible_verses_tr` VALUES ('3514', '43', '13', '28', 'τουτο δε ουδεις εγνω των ανακειμενων προς τι ειπεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3515', '43', '13', '29', 'τινες γαρ εδοκουν επει το γλωσσοκομον ειχεν ο ιουδας οτι λεγει αυτω ο ιησους αγορασον ων χρειαν εχομεν εις την εορτην η τοις πτωχοις ινα τι δω');
INSERT INTO `bible_verses_tr` VALUES ('3516', '43', '13', '30', 'λαβων ουν το ψωμιον εκεινος ευθεως εξηλθεν ην δε νυξ');
INSERT INTO `bible_verses_tr` VALUES ('3517', '43', '13', '31', 'οτε  {VAR2: ουν } εξηλθεν λεγει ο ιησους νυν εδοξασθη ο υιος του ανθρωπου και ο θεος εδοξασθη εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3518', '43', '13', '32', 'ει ο θεος εδοξασθη εν αυτω και ο θεος δοξασει αυτον εν εαυτω και ευθυς δοξασει αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3519', '43', '13', '33', 'τεκνια ετι μικρον μεθ υμων ειμι ζητησετε με και καθως ειπον τοις ιουδαιοις οτι οπου υπαγω εγω υμεις ου δυνασθε ελθειν και υμιν λεγω αρτι');
INSERT INTO `bible_verses_tr` VALUES ('3520', '43', '13', '34', 'εντολην καινην διδωμι υμιν ινα αγαπατε αλληλους καθως ηγαπησα υμας ινα και υμεις αγαπατε αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('3521', '43', '13', '35', 'εν τουτω γνωσονται παντες οτι εμοι μαθηται εστε εαν αγαπην εχητε εν αλληλοις');
INSERT INTO `bible_verses_tr` VALUES ('3522', '43', '13', '36', 'λεγει αυτω σιμων πετρος κυριε που υπαγεις απεκριθη αυτω ο ιησους οπου υπαγω ου δυνασαι μοι νυν ακολουθησαι υστερον δε ακολουθησεις μοι');
INSERT INTO `bible_verses_tr` VALUES ('3523', '43', '13', '37', 'λεγει αυτω ο πετρος κυριε δια τι ου δυναμαι σοι ακολουθησαι αρτι την ψυχην μου υπερ σου θησω');
INSERT INTO `bible_verses_tr` VALUES ('3524', '43', '13', '38', 'απεκριθη αυτω ο ιησους την ψυχην σου υπερ εμου θησεις αμην αμην λεγω σοι ου μη αλεκτωρ φωνησει εως ου απαρνηση με τρις');
INSERT INTO `bible_verses_tr` VALUES ('3525', '43', '14', '1', 'μη ταρασσεσθω υμων η καρδια πιστευετε εις τον θεον και εις εμε πιστευετε');
INSERT INTO `bible_verses_tr` VALUES ('3526', '43', '14', '2', 'εν τη οικια του πατρος μου μοναι πολλαι εισιν ει δε μη ειπον αν υμιν πορευομαι ετοιμασαι τοπον υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3527', '43', '14', '3', 'και εαν πορευθω και ετοιμασω υμιν τοπον παλιν ερχομαι και παραληψομαι υμας προς εμαυτον ινα οπου ειμι εγω και υμεις ητε');
INSERT INTO `bible_verses_tr` VALUES ('3528', '43', '14', '4', 'και οπου εγω υπαγω οιδατε και την οδον οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('3529', '43', '14', '5', 'λεγει αυτω θωμας κυριε ουκ οιδαμεν που υπαγεις και πως δυναμεθα την οδον ειδεναι');
INSERT INTO `bible_verses_tr` VALUES ('3530', '43', '14', '6', 'λεγει αυτω ο ιησους εγω ειμι η οδος και η αληθεια και η ζωη ουδεις ερχεται προς τον πατερα ει μη δι εμου');
INSERT INTO `bible_verses_tr` VALUES ('3531', '43', '14', '7', 'ει εγνωκειτε με και τον πατερα μου εγνωκειτε αν και απ αρτι γινωσκετε αυτον και εωρακατε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3532', '43', '14', '8', 'λεγει αυτω φιλιππος κυριε δειξον ημιν τον πατερα και αρκει ημιν');
INSERT INTO `bible_verses_tr` VALUES ('3533', '43', '14', '9', 'λεγει αυτω ο ιησους τοσουτον χρονον μεθ υμων ειμι και ουκ εγνωκας με φιλιππε ο εωρακως εμε εωρακεν τον πατερα και πως συ λεγεις δειξον ημιν τον πατερα');
INSERT INTO `bible_verses_tr` VALUES ('3534', '43', '14', '10', 'ου πιστευεις οτι εγω εν τω πατρι και ο πατηρ εν εμοι εστιν τα ρηματα α εγω λαλω υμιν απ εμαυτου ου λαλω ο δε πατηρ ο εν εμοι μενων αυτος ποιει τα εργα');
INSERT INTO `bible_verses_tr` VALUES ('3535', '43', '14', '11', 'πιστευετε μοι οτι εγω εν τω πατρι και ο πατηρ εν εμοι ει δε μη δια τα εργα αυτα πιστευετε μοι');
INSERT INTO `bible_verses_tr` VALUES ('3536', '43', '14', '12', 'αμην αμην λεγω υμιν ο πιστευων εις εμε τα εργα α εγω ποιω κακεινος ποιησει και μειζονα τουτων ποιησει οτι εγω προς τον πατερα μου πορευομαι');
INSERT INTO `bible_verses_tr` VALUES ('3537', '43', '14', '13', 'και ο τι αν αιτησητε εν τω ονοματι μου τουτο ποιησω ινα δοξασθη ο πατηρ εν τω υιω');
INSERT INTO `bible_verses_tr` VALUES ('3538', '43', '14', '14', 'εαν τι αιτησητε εν τω ονοματι μου εγω ποιησω');
INSERT INTO `bible_verses_tr` VALUES ('3539', '43', '14', '15', 'εαν αγαπατε με τας εντολας τας εμας τηρησατε');
INSERT INTO `bible_verses_tr` VALUES ('3540', '43', '14', '16', 'και εγω ερωτησω τον πατερα και αλλον παρακλητον δωσει υμιν ινα μενη μεθ υμων εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('3541', '43', '14', '17', 'το πνευμα της αληθειας ο ο κοσμος ου δυναται λαβειν οτι ου θεωρει αυτο ουδε γινωσκει αυτο υμεις δε γινωσκετε αυτο οτι παρ υμιν μενει και εν υμιν εσται');
INSERT INTO `bible_verses_tr` VALUES ('3542', '43', '14', '18', 'ουκ αφησω υμας ορφανους ερχομαι προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('3543', '43', '14', '19', 'ετι μικρον και ο κοσμος με ουκ ετι θεωρει υμεις δε θεωρειτε με οτι εγω ζω και υμεις ζησεσθε');
INSERT INTO `bible_verses_tr` VALUES ('3544', '43', '14', '20', 'εν εκεινη τη ημερα γνωσεσθε υμεις οτι εγω εν τω πατρι μου και υμεις εν εμοι καγω εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3545', '43', '14', '21', 'ο εχων τας εντολας μου και τηρων αυτας εκεινος εστιν ο αγαπων με ο δε αγαπων με αγαπηθησεται υπο του πατρος μου και εγω αγαπησω αυτον και εμφανισω αυτω εμαυτον');
INSERT INTO `bible_verses_tr` VALUES ('3546', '43', '14', '22', 'λεγει αυτω ιουδας ουχ ο ισκαριωτης κυριε τι γεγονεν οτι ημιν μελλεις εμφανιζειν σεαυτον και ουχι τω κοσμω');
INSERT INTO `bible_verses_tr` VALUES ('3547', '43', '14', '23', 'απεκριθη ο ιησους και ειπεν αυτω εαν τις αγαπα με τον λογον μου τηρησει και ο πατηρ μου αγαπησει αυτον και προς αυτον ελευσομεθα και μονην παρ αυτω ποιησομεν');
INSERT INTO `bible_verses_tr` VALUES ('3548', '43', '14', '24', 'ο μη αγαπων με τους λογους μου ου τηρει και ο λογος ον ακουετε ουκ εστιν εμος αλλα του πεμψαντος με πατρος');
INSERT INTO `bible_verses_tr` VALUES ('3549', '43', '14', '25', 'ταυτα λελαληκα υμιν παρ υμιν μενων');
INSERT INTO `bible_verses_tr` VALUES ('3550', '43', '14', '26', 'ο δε παρακλητος το πνευμα το αγιον ο πεμψει ο πατηρ εν τω ονοματι μου εκεινος υμας διδαξει παντα και υπομνησει υμας παντα α ειπον υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3551', '43', '14', '27', 'ειρηνην αφιημι υμιν ειρηνην την εμην διδωμι υμιν ου καθως ο κοσμος διδωσιν εγω διδωμι υμιν μη ταρασσεσθω υμων η καρδια μηδε δειλιατω');
INSERT INTO `bible_verses_tr` VALUES ('3552', '43', '14', '28', 'ηκουσατε οτι εγω ειπον υμιν υπαγω και ερχομαι προς υμας ει ηγαπατε με εχαρητε αν οτι ειπον πορευομαι προς τον πατερα οτι ο πατηρ μου μειζων μου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3553', '43', '14', '29', 'και νυν ειρηκα υμιν πριν γενεσθαι ινα οταν γενηται πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('3554', '43', '14', '30', 'ουκ ετι πολλα λαλησω μεθ υμων ερχεται γαρ ο του κοσμου τουτου αρχων και εν εμοι ουκ εχει ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3555', '43', '14', '31', 'αλλ ινα γνω ο κοσμος οτι αγαπω τον πατερα και καθως ενετειλατο μοι ο πατηρ ουτως ποιω εγειρεσθε αγωμεν εντευθεν');
INSERT INTO `bible_verses_tr` VALUES ('3556', '43', '15', '1', 'εγω ειμι η αμπελος η αληθινη και ο πατηρ μου ο γεωργος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3557', '43', '15', '2', 'παν κλημα εν εμοι μη φερον καρπον αιρει αυτο και παν το καρπον φερον καθαιρει αυτο ινα πλειονα καρπον φερη');
INSERT INTO `bible_verses_tr` VALUES ('3558', '43', '15', '3', 'ηδη υμεις καθαροι εστε δια τον λογον ον λελαληκα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3559', '43', '15', '4', 'μεινατε εν εμοι καγω εν υμιν καθως το κλημα ου δυναται καρπον φερειν αφ εαυτου εαν μη μεινη εν τη αμπελω ουτως ουδε υμεις εαν μη εν εμοι μεινητε');
INSERT INTO `bible_verses_tr` VALUES ('3560', '43', '15', '5', 'εγω ειμι η αμπελος υμεις τα κληματα ο μενων εν εμοι καγω εν αυτω ουτος φερει καρπον πολυν οτι χωρις εμου ου δυνασθε ποιειν ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3561', '43', '15', '6', 'εαν μη τις μεινη εν εμοι εβληθη εξω ως το κλημα και εξηρανθη και συναγουσιν αυτα και εις πυρ βαλλουσιν και καιεται');
INSERT INTO `bible_verses_tr` VALUES ('3562', '43', '15', '7', 'εαν μεινητε εν εμοι και τα ρηματα μου εν υμιν μεινη ο εαν θελητε αιτησεσθε και γενησεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3563', '43', '15', '8', 'εν τουτω εδοξασθη ο πατηρ μου ινα καρπον πολυν φερητε και γενησεσθε εμοι μαθηται');
INSERT INTO `bible_verses_tr` VALUES ('3564', '43', '15', '9', 'καθως ηγαπησεν με ο πατηρ καγω ηγαπησα υμας μεινατε εν τη αγαπη τη εμη');
INSERT INTO `bible_verses_tr` VALUES ('3565', '43', '15', '10', 'εαν τας εντολας μου τηρησητε μενειτε εν τη αγαπη μου καθως εγω τας εντολας του πατρος μου τετηρηκα και μενω αυτου εν τη αγαπη');
INSERT INTO `bible_verses_tr` VALUES ('3566', '43', '15', '11', 'ταυτα λελαληκα υμιν ινα η χαρα η εμη εν υμιν μεινη και η χαρα υμων πληρωθη');
INSERT INTO `bible_verses_tr` VALUES ('3567', '43', '15', '12', 'αυτη εστιν η εντολη η εμη ινα αγαπατε αλληλους καθως ηγαπησα υμας');
INSERT INTO `bible_verses_tr` VALUES ('3568', '43', '15', '13', 'μειζονα ταυτης αγαπην ουδεις εχει ινα τις την ψυχην αυτου θη υπερ των φιλων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3569', '43', '15', '14', 'υμεις φιλοι μου εστε εαν ποιητε οσα εγω εντελλομαι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3570', '43', '15', '15', 'ουκετι υμας λεγω δουλους οτι ο δουλος ουκ οιδεν τι ποιει αυτου ο κυριος υμας δε ειρηκα φιλους οτι παντα α ηκουσα παρα του πατρος μου εγνωρισα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3571', '43', '15', '16', 'ουχ υμεις με εξελεξασθε αλλ εγω εξελεξαμην υμας και εθηκα υμας ινα υμεις υπαγητε και καρπον φερητε και ο καρπος υμων μενη ινα ο τι αν αιτησητε τον πατερα εν τω ονοματι μου δω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3572', '43', '15', '17', 'ταυτα εντελλομαι υμιν ινα αγαπατε αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('3573', '43', '15', '18', 'ει ο κοσμος υμας μισει γινωσκετε οτι εμε πρωτον υμων μεμισηκεν');
INSERT INTO `bible_verses_tr` VALUES ('3574', '43', '15', '19', 'ει εκ του κοσμου ητε ο κοσμος αν το ιδιον εφιλει οτι δε εκ του κοσμου ουκ εστε αλλ εγω εξελεξαμην υμας εκ του κοσμου δια τουτο μισει υμας ο κοσμος');
INSERT INTO `bible_verses_tr` VALUES ('3575', '43', '15', '20', 'μνημονευετε του λογου ου εγω ειπον υμιν ουκ εστιν δουλος μειζων του κυριου αυτου ει εμε εδιωξαν και υμας διωξουσιν ει τον λογον μου ετηρησαν και τον υμετερον τηρησουσιν');
INSERT INTO `bible_verses_tr` VALUES ('3576', '43', '15', '21', 'αλλα ταυτα παντα ποιησουσιν υμιν δια το ονομα μου οτι ουκ οιδασιν τον πεμψαντα με');
INSERT INTO `bible_verses_tr` VALUES ('3577', '43', '15', '22', 'ει μη ηλθον και ελαλησα αυτοις αμαρτιαν ουκ ειχον νυν δε προφασιν ουκ εχουσιν περι της αμαρτιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3578', '43', '15', '23', 'ο εμε μισων και τον πατερα μου μισει');
INSERT INTO `bible_verses_tr` VALUES ('3579', '43', '15', '24', 'ει τα εργα μη εποιησα εν αυτοις α ουδεις αλλος πεποιηκεν αμαρτιαν ουκ ειχον νυν δε και εωρακασιν και μεμισηκασιν και εμε και τον πατερα μου');
INSERT INTO `bible_verses_tr` VALUES ('3580', '43', '15', '25', 'αλλ ινα πληρωθη ο λογος ο γεγραμμενος εν τω νομω αυτων οτι εμισησαν με δωρεαν');
INSERT INTO `bible_verses_tr` VALUES ('3581', '43', '15', '26', 'οταν δε ελθη ο παρακλητος ον εγω πεμψω υμιν παρα του πατρος το πνευμα της αληθειας ο παρα του πατρος εκπορευεται εκεινος μαρτυρησει περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('3582', '43', '15', '27', 'και υμεις δε μαρτυρειτε οτι απ αρχης μετ εμου εστε');
INSERT INTO `bible_verses_tr` VALUES ('3583', '43', '16', '1', 'ταυτα λελαληκα υμιν ινα μη σκανδαλισθητε');
INSERT INTO `bible_verses_tr` VALUES ('3584', '43', '16', '2', 'αποσυναγωγους ποιησουσιν υμας αλλ ερχεται ωρα ινα πας ο αποκτεινας υμας δοξη λατρειαν προσφερειν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('3585', '43', '16', '3', 'και ταυτα ποιησουσιν υμιν οτι ουκ εγνωσαν τον πατερα ουδε εμε');
INSERT INTO `bible_verses_tr` VALUES ('3586', '43', '16', '4', 'αλλα ταυτα λελαληκα υμιν ινα οταν ελθη η ωρα μνημονευητε αυτων οτι εγω ειπον υμιν ταυτα δε υμιν εξ αρχης ουκ ειπον οτι μεθ υμων ημην');
INSERT INTO `bible_verses_tr` VALUES ('3587', '43', '16', '5', 'νυν δε υπαγω προς τον πεμψαντα με και ουδεις εξ υμων ερωτα με που υπαγεις');
INSERT INTO `bible_verses_tr` VALUES ('3588', '43', '16', '6', 'αλλ οτι ταυτα λελαληκα υμιν η λυπη πεπληρωκεν υμων την καρδιαν');
INSERT INTO `bible_verses_tr` VALUES ('3589', '43', '16', '7', 'αλλ εγω την αληθειαν λεγω υμιν συμφερει υμιν ινα εγω απελθω εαν γαρ μη απελθω ο παρακλητος ουκ ελευσεται προς υμας εαν δε πορευθω πεμψω αυτον προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('3590', '43', '16', '8', 'και ελθων εκεινος ελεγξει τον κοσμον περι αμαρτιας και περι δικαιοσυνης και περι κρισεως');
INSERT INTO `bible_verses_tr` VALUES ('3591', '43', '16', '9', 'περι αμαρτιας μεν οτι ου πιστευουσιν εις εμε');
INSERT INTO `bible_verses_tr` VALUES ('3592', '43', '16', '10', 'περι δικαιοσυνης δε οτι προς τον πατερα μου υπαγω και ουκ ετι θεωρειτε με');
INSERT INTO `bible_verses_tr` VALUES ('3593', '43', '16', '11', 'περι δε κρισεως οτι ο αρχων του κοσμου τουτου κεκριται');
INSERT INTO `bible_verses_tr` VALUES ('3594', '43', '16', '12', 'ετι πολλα εχω λεγειν υμιν αλλ ου δυνασθε βασταζειν αρτι');
INSERT INTO `bible_verses_tr` VALUES ('3595', '43', '16', '13', 'οταν δε ελθη εκεινος το πνευμα της αληθειας οδηγησει υμας εις πασαν την αληθειαν ου γαρ λαλησει αφ εαυτου αλλ οσα αν ακουση λαλησει και τα ερχομενα αναγγελει υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3596', '43', '16', '14', 'εκεινος εμε δοξασει οτι εκ του εμου ληψεται και αναγγελει υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3597', '43', '16', '15', 'παντα οσα εχει ο πατηρ εμα εστιν δια τουτο ειπον οτι εκ του εμου ληψεται και αναγγελει υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3598', '43', '16', '16', 'μικρον και ου θεωρειτε με και παλιν μικρον και οψεσθε με οτι εγω υπαγω προς τον πατερα');
INSERT INTO `bible_verses_tr` VALUES ('3599', '43', '16', '17', 'ειπον ουν εκ των μαθητων αυτου προς αλληλους τι εστιν τουτο ο λεγει ημιν μικρον και ου θεωρειτε με και παλιν μικρον και οψεσθε με και οτι εγω υπαγω προς τον πατερα');
INSERT INTO `bible_verses_tr` VALUES ('3600', '43', '16', '18', 'ελεγον ουν τουτο τι εστιν ο λεγει το μικρον ουκ οιδαμεν τι λαλει');
INSERT INTO `bible_verses_tr` VALUES ('3601', '43', '16', '19', 'εγνω ουν ο ιησους οτι ηθελον αυτον ερωταν και ειπεν αυτοις περι τουτου ζητειτε μετ αλληλων οτι ειπον μικρον και ου θεωρειτε με και παλιν μικρον και οψεσθε με');
INSERT INTO `bible_verses_tr` VALUES ('3602', '43', '16', '20', 'αμην αμην λεγω υμιν οτι κλαυσετε και θρηνησετε υμεις ο δε κοσμος χαρησεται υμεις δε λυπηθησεσθε αλλ η λυπη υμων εις χαραν γενησεται');
INSERT INTO `bible_verses_tr` VALUES ('3603', '43', '16', '21', 'η γυνη οταν τικτη λυπην εχει οτι ηλθεν η ωρα αυτης οταν δε γεννηση το παιδιον ουκ ετι μνημονευει της θλιψεως δια την χαραν οτι εγεννηθη ανθρωπος εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3604', '43', '16', '22', 'και υμεις ουν λυπην μεν νυν εχετε παλιν δε οψομαι υμας και χαρησεται υμων η καρδια και την χαραν υμων ουδεις αιρει αφ υμων');
INSERT INTO `bible_verses_tr` VALUES ('3605', '43', '16', '23', 'και εν εκεινη τη ημερα εμε ουκ ερωτησετε ουδεν αμην αμην λεγω υμιν οτι οσα αν αιτησητε τον πατερα εν τω ονοματι μου δωσει υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3606', '43', '16', '24', 'εως αρτι ουκ ητησατε ουδεν εν τω ονοματι μου αιτειτε και ληψεσθε ινα η χαρα υμων η πεπληρωμενη');
INSERT INTO `bible_verses_tr` VALUES ('3607', '43', '16', '25', 'ταυτα εν παροιμιαις λελαληκα υμιν  {VAR1: αλλ } ερχεται ωρα οτε ουκ ετι εν παροιμιαις λαλησω υμιν αλλα παρρησια περι του πατρος αναγγελω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3608', '43', '16', '26', 'εν εκεινη τη ημερα εν τω ονοματι μου αιτησεσθε και ου λεγω υμιν οτι εγω ερωτησω τον πατερα περι υμων');
INSERT INTO `bible_verses_tr` VALUES ('3609', '43', '16', '27', 'αυτος γαρ ο πατηρ φιλει υμας οτι υμεις εμε πεφιληκατε και πεπιστευκατε οτι εγω παρα του θεου εξηλθον');
INSERT INTO `bible_verses_tr` VALUES ('3610', '43', '16', '28', 'εξηλθον παρα του πατρος και εληλυθα εις τον κοσμον παλιν αφιημι τον κοσμον και πορευομαι προς τον πατερα');
INSERT INTO `bible_verses_tr` VALUES ('3611', '43', '16', '29', 'λεγουσιν αυτω οι μαθηται αυτου ιδε νυν παρρησια λαλεις και παροιμιαν ουδεμιαν λεγεις');
INSERT INTO `bible_verses_tr` VALUES ('3612', '43', '16', '30', 'νυν οιδαμεν οτι οιδας παντα και ου χρειαν εχεις ινα τις σε ερωτα εν τουτω πιστευομεν οτι απο θεου εξηλθες');
INSERT INTO `bible_verses_tr` VALUES ('3613', '43', '16', '31', 'απεκριθη αυτοις ο ιησους αρτι πιστευετε');
INSERT INTO `bible_verses_tr` VALUES ('3614', '43', '16', '32', 'ιδου ερχεται ωρα και νυν εληλυθεν ινα σκορπισθητε εκαστος εις τα ιδια και εμε μονον αφητε και ουκ ειμι μονος οτι ο πατηρ μετ εμου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3615', '43', '16', '33', 'ταυτα λελαληκα υμιν ινα εν εμοι ειρηνην εχητε εν τω κοσμω θλιψιν  {VAR1: εχετε } {VAR2: εξετε } αλλα θαρσειτε εγω νενικηκα τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3616', '43', '17', '1', 'ταυτα ελαλησεν ο ιησους και επηρεν τους οφθαλμους αυτου εις τον ουρανον και ειπεν πατερ εληλυθεν η ωρα δοξασον σου τον υιον ινα και ο υιος σου δοξαση σε');
INSERT INTO `bible_verses_tr` VALUES ('3617', '43', '17', '2', 'καθως εδωκας αυτω εξουσιαν πασης σαρκος ινα παν ο δεδωκας αυτω δωση αυτοις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('3618', '43', '17', '3', 'αυτη δε εστιν η αιωνιος ζωη ινα γινωσκωσιν σε τον μονον αληθινον θεον και ον απεστειλας ιησουν χριστον');
INSERT INTO `bible_verses_tr` VALUES ('3619', '43', '17', '4', 'εγω σε εδοξασα επι της γης το εργον ετελειωσα ο δεδωκας μοι ινα ποιησω');
INSERT INTO `bible_verses_tr` VALUES ('3620', '43', '17', '5', 'και νυν δοξασον με συ πατερ παρα σεαυτω τη δοξη η ειχον προ του τον κοσμον ειναι παρα σοι');
INSERT INTO `bible_verses_tr` VALUES ('3621', '43', '17', '6', 'εφανερωσα σου το ονομα τοις ανθρωποις ους δεδωκας μοι εκ του κοσμου σοι ησαν και εμοι αυτους δεδωκας και τον λογον σου τετηρηκασιν');
INSERT INTO `bible_verses_tr` VALUES ('3622', '43', '17', '7', 'νυν εγνωκαν οτι παντα οσα δεδωκας μοι παρα σου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3623', '43', '17', '8', 'οτι τα ρηματα α δεδωκας μοι δεδωκα αυτοις και αυτοι ελαβον και εγνωσαν αληθως οτι παρα σου εξηλθον και επιστευσαν οτι συ με απεστειλας');
INSERT INTO `bible_verses_tr` VALUES ('3624', '43', '17', '9', 'εγω περι αυτων ερωτω ου περι του κοσμου ερωτω αλλα περι ων δεδωκας μοι οτι σοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('3625', '43', '17', '10', 'και τα εμα παντα σα εστιν και τα σα εμα και δεδοξασμαι εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3626', '43', '17', '11', 'και ουκ ετι ειμι εν τω κοσμω και ουτοι εν τω κοσμω εισιν και εγω προς σε ερχομαι πατερ αγιε τηρησον αυτους εν τω ονοματι σου ους δεδωκας μοι ινα ωσιν εν καθως ημεις');
INSERT INTO `bible_verses_tr` VALUES ('3627', '43', '17', '12', 'οτε ημην μετ αυτων εν τω κοσμω εγω ετηρουν αυτους εν τω ονοματι σου ους δεδωκας μοι εφυλαξα και ουδεις εξ αυτων απωλετο ει μη ο υιος της απωλειας ινα η γραφη πληρωθη');
INSERT INTO `bible_verses_tr` VALUES ('3628', '43', '17', '13', 'νυν δε προς σε ερχομαι και ταυτα λαλω εν τω κοσμω ινα εχωσιν την χαραν την εμην πεπληρωμενην εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3629', '43', '17', '14', 'εγω δεδωκα αυτοις τον λογον σου και ο κοσμος εμισησεν αυτους οτι ουκ εισιν εκ του κοσμου καθως εγω ουκ ειμι εκ του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('3630', '43', '17', '15', 'ουκ ερωτω ινα αρης αυτους εκ του κοσμου αλλ ινα τηρησης αυτους εκ του πονηρου');
INSERT INTO `bible_verses_tr` VALUES ('3631', '43', '17', '16', 'εκ του κοσμου ουκ εισιν καθως εγω εκ του κοσμου ουκ ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3632', '43', '17', '17', 'αγιασον αυτους εν τη αληθεια σου ο λογος ο σος αληθεια εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3633', '43', '17', '18', 'καθως εμε απεστειλας εις τον κοσμον καγω απεστειλα αυτους εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('3634', '43', '17', '19', 'και υπερ αυτων εγω αγιαζω εμαυτον ινα και αυτοι ωσιν ηγιασμενοι εν αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('3635', '43', '17', '20', 'ου περι τουτων δε ερωτω μονον αλλα και περι των πιστευσοντων δια του λογου αυτων εις εμε');
INSERT INTO `bible_verses_tr` VALUES ('3636', '43', '17', '21', 'ινα παντες εν ωσιν καθως συ πατερ εν εμοι καγω εν σοι ινα και αυτοι εν ημιν εν ωσιν ινα ο κοσμος πιστευση οτι συ με απεστειλας');
INSERT INTO `bible_verses_tr` VALUES ('3637', '43', '17', '22', 'και εγω την δοξαν ην δεδωκας μοι δεδωκα αυτοις ινα ωσιν εν καθως ημεις εν εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('3638', '43', '17', '23', 'εγω εν αυτοις και συ εν εμοι ινα ωσιν τετελειωμενοι εις εν και ινα γινωσκη ο κοσμος οτι συ με απεστειλας και ηγαπησας αυτους καθως εμε ηγαπησας');
INSERT INTO `bible_verses_tr` VALUES ('3639', '43', '17', '24', 'πατερ ους δεδωκας μοι θελω ινα οπου ειμι εγω κακεινοι ωσιν μετ εμου ινα θεωρωσιν την δοξαν την εμην ην εδωκας μοι οτι ηγαπησας με προ καταβολης κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('3640', '43', '17', '25', 'πατερ δικαιε και ο κοσμος σε ουκ εγνω εγω δε σε εγνων και ουτοι εγνωσαν οτι συ με απεστειλας');
INSERT INTO `bible_verses_tr` VALUES ('3641', '43', '17', '26', 'και εγνωρισα αυτοις το ονομα σου και γνωρισω ινα η αγαπη ην ηγαπησας με εν αυτοις η καγω εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3642', '43', '18', '1', 'ταυτα ειπων ο ιησους εξηλθεν συν τοις μαθηταις αυτου περαν του χειμαρρου των κεδρων οπου ην κηπος εις ον εισηλθεν αυτος και οι μαθηται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3643', '43', '18', '2', 'ηδει δε και ιουδας ο παραδιδους αυτον τον τοπον οτι πολλακις συνηχθη ο ιησους εκει μετα των μαθητων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3644', '43', '18', '3', 'ο ουν ιουδας λαβων την σπειραν και εκ των αρχιερεων και φαρισαιων υπηρετας ερχεται εκει μετα φανων και λαμπαδων και οπλων');
INSERT INTO `bible_verses_tr` VALUES ('3645', '43', '18', '4', 'ιησους ουν ειδως παντα τα ερχομενα επ αυτον εξελθων ειπεν αυτοις τινα ζητειτε');
INSERT INTO `bible_verses_tr` VALUES ('3646', '43', '18', '5', 'απεκριθησαν αυτω ιησουν τον ναζωραιον λεγει αυτοις ο ιησους εγω ειμι ειστηκει δε και ιουδας ο παραδιδους αυτον μετ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3647', '43', '18', '6', 'ως ουν ειπεν αυτοις οτι εγω ειμι απηλθον εις τα οπισω και επεσον χαμαι');
INSERT INTO `bible_verses_tr` VALUES ('3648', '43', '18', '7', 'παλιν ουν αυτους επηρωτησεν τινα ζητειτε οι δε ειπον ιησουν τον ναζωραιον');
INSERT INTO `bible_verses_tr` VALUES ('3649', '43', '18', '8', 'απεκριθη ο ιησους ειπον υμιν οτι εγω ειμι ει ουν εμε ζητειτε αφετε τουτους υπαγειν');
INSERT INTO `bible_verses_tr` VALUES ('3650', '43', '18', '9', 'ινα πληρωθη ο λογος ον ειπεν οτι ους δεδωκας μοι ουκ απωλεσα εξ αυτων ουδενα');
INSERT INTO `bible_verses_tr` VALUES ('3651', '43', '18', '10', 'σιμων ουν πετρος εχων μαχαιραν ειλκυσεν αυτην και επαισεν τον του αρχιερεως δουλον και απεκοψεν αυτου το ωτιον το δεξιον ην δε ονομα τω δουλω μαλχος');
INSERT INTO `bible_verses_tr` VALUES ('3652', '43', '18', '11', 'ειπεν ουν ο ιησους τω πετρω βαλε την μαχαιραν σου εις την θηκην το ποτηριον ο δεδωκεν μοι ο πατηρ ου μη πιω αυτο');
INSERT INTO `bible_verses_tr` VALUES ('3653', '43', '18', '12', 'η ουν σπειρα και ο χιλιαρχος και οι υπηρεται των ιουδαιων συνελαβον τον ιησουν και εδησαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3654', '43', '18', '13', 'και απηγαγον αυτον προς ανναν πρωτον ην γαρ πενθερος του καιαφα ος ην αρχιερευς του ενιαυτου εκεινου');
INSERT INTO `bible_verses_tr` VALUES ('3655', '43', '18', '14', 'ην δε καιαφας ο συμβουλευσας τοις ιουδαιοις οτι συμφερει ενα ανθρωπον απολεσθαι υπερ του λαου');
INSERT INTO `bible_verses_tr` VALUES ('3656', '43', '18', '15', 'ηκολουθει δε τω ιησου σιμων πετρος και  {VAR1: ο } αλλος μαθητης ο δε μαθητης εκεινος ην γνωστος τω αρχιερει και συνεισηλθεν τω ιησου εις την αυλην του αρχιερεως');
INSERT INTO `bible_verses_tr` VALUES ('3657', '43', '18', '16', 'ο δε πετρος ειστηκει προς τη θυρα εξω εξηλθεν ουν ο μαθητης ο αλλος ος ην γνωστος τω αρχιερει και ειπεν τη θυρωρω και εισηγαγεν τον πετρον');
INSERT INTO `bible_verses_tr` VALUES ('3658', '43', '18', '17', 'λεγει ουν η παιδισκη η θυρωρος τω πετρω μη και συ εκ των μαθητων ει του ανθρωπου τουτου λεγει εκεινος ουκ ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3659', '43', '18', '18', 'ειστηκεισαν δε οι δουλοι και οι υπηρεται ανθρακιαν πεποιηκοτες οτι ψυχος ην και εθερμαινοντο ην δε μετ αυτων ο πετρος εστως και θερμαινομενος');
INSERT INTO `bible_verses_tr` VALUES ('3660', '43', '18', '19', 'ο ουν αρχιερευς ηρωτησεν τον ιησουν περι των μαθητων αυτου και περι της διδαχης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3661', '43', '18', '20', 'απεκριθη αυτω ο ιησους εγω παρρησια ελαλησα τω κοσμω εγω παντοτε εδιδαξα εν τη συναγωγη και εν τω ιερω οπου παντοτε οι ιουδαιοι συνερχονται και εν κρυπτω ελαλησα ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3662', '43', '18', '21', 'τι με επερωτας επερωτησον τους ακηκοοτας τι ελαλησα αυτοις ιδε ουτοι οιδασιν α ειπον εγω');
INSERT INTO `bible_verses_tr` VALUES ('3663', '43', '18', '22', 'ταυτα δε αυτου ειποντος εις των υπηρετων παρεστηκως εδωκεν ραπισμα τω ιησου ειπων ουτως αποκρινη τω αρχιερει');
INSERT INTO `bible_verses_tr` VALUES ('3664', '43', '18', '23', 'απεκριθη αυτω ο ιησους ει κακως ελαλησα μαρτυρησον περι του κακου ει δε καλως τι με δερεις');
INSERT INTO `bible_verses_tr` VALUES ('3665', '43', '18', '24', 'απεστειλεν  {VAR2: ουν } αυτον ο αννας δεδεμενον προς καιαφαν τον αρχιερεα');
INSERT INTO `bible_verses_tr` VALUES ('3666', '43', '18', '25', 'ην δε σιμων πετρος εστως και θερμαινομενος ειπον ουν αυτω μη και συ εκ των μαθητων αυτου ει ηρνησατο εκεινος και ειπεν ουκ ειμι');
INSERT INTO `bible_verses_tr` VALUES ('3667', '43', '18', '26', 'λεγει εις εκ των δουλων του αρχιερεως συγγενης ων ου απεκοψεν πετρος το ωτιον ουκ εγω σε ειδον εν τω κηπω μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3668', '43', '18', '27', 'παλιν ουν ηρνησατο ο πετρος και ευθεως αλεκτωρ εφωνησεν');
INSERT INTO `bible_verses_tr` VALUES ('3669', '43', '18', '28', 'αγουσιν ουν τον ιησουν απο του καιαφα εις το πραιτωριον ην δε πρωια και αυτοι ουκ εισηλθον εις το πραιτωριον ινα μη μιανθωσιν αλλ ινα φαγωσιν το πασχα');
INSERT INTO `bible_verses_tr` VALUES ('3670', '43', '18', '29', 'εξηλθεν ουν ο πιλατος προς αυτους και ειπεν τινα κατηγοριαν φερετε κατα του ανθρωπου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('3671', '43', '18', '30', 'απεκριθησαν και ειπον αυτω ει μη ην ουτος κακοποιος ουκ αν σοι παρεδωκαμεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3672', '43', '18', '31', 'ειπεν ουν αυτοις ο πιλατος λαβετε αυτον υμεις και κατα τον νομον υμων κρινατε αυτον ειπον ουν αυτω οι ιουδαιοι ημιν ουκ εξεστιν αποκτειναι ουδενα');
INSERT INTO `bible_verses_tr` VALUES ('3673', '43', '18', '32', 'ινα ο λογος του ιησου πληρωθη ον ειπεν σημαινων ποιω θανατω ημελλεν αποθνησκειν');
INSERT INTO `bible_verses_tr` VALUES ('3674', '43', '18', '33', 'εισηλθεν ουν εις το πραιτωριον παλιν ο πιλατος και εφωνησεν τον ιησουν και ειπεν αυτω συ ει ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3675', '43', '18', '34', 'απεκριθη αυτω ο ιησους αφ εαυτου συ τουτο λεγεις η αλλοι σοι ειπον περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('3676', '43', '18', '35', 'απεκριθη ο πιλατος μητι εγω ιουδαιος ειμι το εθνος το σον και οι αρχιερεις παρεδωκαν σε εμοι τι εποιησας');
INSERT INTO `bible_verses_tr` VALUES ('3677', '43', '18', '36', 'απεκριθη ο ιησους η βασιλεια η εμη ουκ εστιν εκ του κοσμου τουτου ει εκ του κοσμου τουτου ην η βασιλεια η εμη οι υπηρεται αν οι εμοι ηγωνιζοντο ινα μη παραδοθω τοις ιουδαιοις νυν δε η βασιλεια η εμη ουκ εστιν εντευθεν');
INSERT INTO `bible_verses_tr` VALUES ('3678', '43', '18', '37', 'ειπεν ουν αυτω ο πιλατος ουκουν βασιλευς ει συ απεκριθη ο ιησους συ λεγεις οτι βασιλευς ειμι εγω εγω εις τουτο γεγεννημαι και εις τουτο εληλυθα εις τον κοσμον ινα μαρτυρησω τη αληθεια πας ο ων εκ της αληθειας ακουει μου της φωνης');
INSERT INTO `bible_verses_tr` VALUES ('3679', '43', '18', '38', 'λεγει αυτω ο πιλατος τι εστιν αληθεια και τουτο ειπων παλιν εξηλθεν προς τους ιουδαιους και λεγει αυτοις εγω ουδεμιαν αιτιαν ευρισκω εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3680', '43', '18', '39', 'εστιν δε συνηθεια υμιν ινα ενα υμιν απολυσω εν τω πασχα βουλεσθε ουν υμιν απολυσω τον βασιλεα των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3681', '43', '18', '40', 'εκραυγασαν ουν παλιν παντες λεγοντες μη τουτον αλλα τον βαραββαν ην δε ο βαραββας ληστης');
INSERT INTO `bible_verses_tr` VALUES ('3682', '43', '19', '1', 'τοτε ουν ελαβεν ο πιλατος τον ιησουν και εμαστιγωσεν');
INSERT INTO `bible_verses_tr` VALUES ('3683', '43', '19', '2', 'και οι στρατιωται πλεξαντες στεφανον εξ ακανθων επεθηκαν αυτου τη κεφαλη και ιματιον πορφυρουν περιεβαλον αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3684', '43', '19', '3', 'και ελεγον χαιρε ο βασιλευς των ιουδαιων και εδιδουν αυτω ραπισματα');
INSERT INTO `bible_verses_tr` VALUES ('3685', '43', '19', '4', 'εξηλθεν ουν παλιν εξω ο πιλατος και λεγει αυτοις ιδε αγω υμιν αυτον εξω ινα γνωτε οτι εν αυτω ουδεμιαν αιτιαν ευρισκω');
INSERT INTO `bible_verses_tr` VALUES ('3686', '43', '19', '5', 'εξηλθεν ουν ο ιησους εξω φορων τον ακανθινον στεφανον και το πορφυρουν ιματιον και λεγει αυτοις ιδε ο ανθρωπος');
INSERT INTO `bible_verses_tr` VALUES ('3687', '43', '19', '6', 'οτε ουν ειδον αυτον οι αρχιερεις και οι υπηρεται εκραυγασαν λεγοντες σταυρωσον σταυρωσον λεγει αυτοις ο πιλατος λαβετε αυτον υμεις και σταυρωσατε εγω γαρ ουχ ευρισκω εν αυτω αιτιαν');
INSERT INTO `bible_verses_tr` VALUES ('3688', '43', '19', '7', 'απεκριθησαν αυτω οι ιουδαιοι ημεις νομον εχομεν και κατα τον νομον ημων οφειλει αποθανειν οτι εαυτον υιον  {VAR2: του } θεου εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('3689', '43', '19', '8', 'οτε ουν ηκουσεν ο πιλατος τουτον τον λογον μαλλον εφοβηθη');
INSERT INTO `bible_verses_tr` VALUES ('3690', '43', '19', '9', 'και εισηλθεν εις το πραιτωριον παλιν και λεγει τω ιησου ποθεν ει συ ο δε ιησους αποκρισιν ουκ εδωκεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3691', '43', '19', '10', 'λεγει ουν αυτω ο πιλατος εμοι ου λαλεις ουκ οιδας οτι εξουσιαν εχω σταυρωσαι σε και εξουσιαν εχω απολυσαι σε');
INSERT INTO `bible_verses_tr` VALUES ('3692', '43', '19', '11', 'απεκριθη ο ιησους ουκ ειχες εξουσιαν ουδεμιαν κατ εμου ει μη ην σοι δεδομενον ανωθεν δια τουτο ο παραδιδους με σοι μειζονα αμαρτιαν εχει');
INSERT INTO `bible_verses_tr` VALUES ('3693', '43', '19', '12', 'εκ τουτου εζητει ο πιλατος απολυσαι αυτον οι δε ιουδαιοι εκραζον λεγοντες εαν τουτον απολυσης ουκ ει φιλος του καισαρος πας ο βασιλεα αυτον ποιων αντιλεγει τω καισαρι');
INSERT INTO `bible_verses_tr` VALUES ('3694', '43', '19', '13', 'ο ουν πιλατος ακουσας τουτον τον λογον ηγαγεν εξω τον ιησουν και εκαθισεν επι του βηματος εις τοπον λεγομενον λιθοστρωτον εβραιστι δε γαββαθα');
INSERT INTO `bible_verses_tr` VALUES ('3695', '43', '19', '14', 'ην δε παρασκευη του πασχα ωρα δε ωσει εκτη και λεγει τοις ιουδαιοις ιδε ο βασιλευς υμων');
INSERT INTO `bible_verses_tr` VALUES ('3696', '43', '19', '15', 'οι δε εκραυγασαν αρον αρον σταυρωσον αυτον λεγει αυτοις ο πιλατος τον βασιλεα υμων σταυρωσω απεκριθησαν οι αρχιερεις ουκ εχομεν βασιλεα ει μη καισαρα');
INSERT INTO `bible_verses_tr` VALUES ('3697', '43', '19', '16', 'τοτε ουν παρεδωκεν αυτον αυτοις ινα σταυρωθη παρελαβον δε τον ιησουν και απηγαγον');
INSERT INTO `bible_verses_tr` VALUES ('3698', '43', '19', '17', 'και βασταζων τον σταυρον αυτου εξηλθεν εις τον λεγομενον κρανιου τοπον ος λεγεται εβραιστι γολγοθα');
INSERT INTO `bible_verses_tr` VALUES ('3699', '43', '19', '18', 'οπου αυτον εσταυρωσαν και μετ αυτου αλλους δυο εντευθεν και εντευθεν μεσον δε τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('3700', '43', '19', '19', 'εγραψεν δε και τιτλον ο πιλατος και εθηκεν επι του σταυρου ην δε γεγραμμενον ιησους ο ναζωραιος ο βασιλευς των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3701', '43', '19', '20', 'τουτον ουν τον τιτλον πολλοι ανεγνωσαν των ιουδαιων οτι εγγυς ην της πολεως ο τοπος οπου εσταυρωθη ο ιησους και ην γεγραμμενον εβραιστι ελληνιστι ρωμαιστι');
INSERT INTO `bible_verses_tr` VALUES ('3702', '43', '19', '21', 'ελεγον ουν τω πιλατω οι αρχιερεις των ιουδαιων μη γραφε ο βασιλευς των ιουδαιων αλλ οτι εκεινος ειπεν βασιλευς ειμι των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('3703', '43', '19', '22', 'απεκριθη ο πιλατος ο γεγραφα γεγραφα');
INSERT INTO `bible_verses_tr` VALUES ('3704', '43', '19', '23', 'οι ουν στρατιωται οτε εσταυρωσαν τον ιησουν ελαβον τα ιματια αυτου και εποιησαν τεσσαρα μερη εκαστω στρατιωτη μερος και τον χιτωνα ην δε ο χιτων αρραφος εκ των ανωθεν υφαντος δι ολου');
INSERT INTO `bible_verses_tr` VALUES ('3705', '43', '19', '24', 'ειπον ουν προς αλληλους μη σχισωμεν αυτον αλλα λαχωμεν περι αυτου τινος εσται ινα η γραφη πληρωθη η λεγουσα διεμερισαντο τα ιματια μου εαυτοις και επι τον ιματισμον μου εβαλον κληρον οι μεν ουν στρατιωται ταυτα εποιησαν');
INSERT INTO `bible_verses_tr` VALUES ('3706', '43', '19', '25', 'ειστηκεισαν δε παρα τω σταυρω του ιησου η μητηρ αυτου και η αδελφη της μητρος αυτου μαρια η του κλωπα και μαρια η μαγδαληνη');
INSERT INTO `bible_verses_tr` VALUES ('3707', '43', '19', '26', 'ιησους ουν ιδων την μητερα και τον μαθητην παρεστωτα ον ηγαπα λεγει τη μητρι αυτου γυναι ιδου ο υιος σου');
INSERT INTO `bible_verses_tr` VALUES ('3708', '43', '19', '27', 'ειτα λεγει τω μαθητη ιδου η μητηρ σου και απ εκεινης της ωρας ελαβεν αυτην ο μαθητης εις τα ιδια');
INSERT INTO `bible_verses_tr` VALUES ('3709', '43', '19', '28', 'μετα τουτο ειδως ο ιησους οτι παντα ηδη τετελεσται ινα τελειωθη η γραφη λεγει διψω');
INSERT INTO `bible_verses_tr` VALUES ('3710', '43', '19', '29', 'σκευος ουν εκειτο οξους μεστον οι δε πλησαντες σπογγον οξους και υσσωπω περιθεντες προσηνεγκαν αυτου τω στοματι');
INSERT INTO `bible_verses_tr` VALUES ('3711', '43', '19', '30', 'οτε ουν ελαβεν το οξος ο ιησους ειπεν τετελεσται και κλινας την κεφαλην παρεδωκεν το πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('3712', '43', '19', '31', 'οι ουν ιουδαιοι  {VAR2: επει παρασκευη ην } ινα μη μεινη επι του σταυρου τα σωματα εν τω σαββατω  {VAR1: επει παρασκευη ην } ην γαρ μεγαλη η ημερα εκεινου του σαββατου ηρωτησαν τον πιλατον ινα κατεαγωσιν αυτων τα σκελη και αρθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('3713', '43', '19', '32', 'ηλθον ουν οι στρατιωται και του μεν πρωτου κατεαξαν τα σκελη και του αλλου του συσταυρωθεντος αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3714', '43', '19', '33', 'επι δε τον ιησουν ελθοντες ως ειδον αυτον ηδη τεθνηκοτα ου κατεαξαν αυτου τα σκελη');
INSERT INTO `bible_verses_tr` VALUES ('3715', '43', '19', '34', 'αλλ εις των στρατιωτων λογχη αυτου την πλευραν ενυξεν και ευθυς εξηλθεν αιμα και υδωρ');
INSERT INTO `bible_verses_tr` VALUES ('3716', '43', '19', '35', 'και ο εωρακως μεμαρτυρηκεν και αληθινη αυτου εστιν η μαρτυρια κακεινος οιδεν οτι αληθη λεγει ινα υμεις πιστευσητε');
INSERT INTO `bible_verses_tr` VALUES ('3717', '43', '19', '36', 'εγενετο γαρ ταυτα ινα η γραφη πληρωθη οστουν ου συντριβησεται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3718', '43', '19', '37', 'και παλιν ετερα γραφη λεγει οψονται εις ον εξεκεντησαν');
INSERT INTO `bible_verses_tr` VALUES ('3719', '43', '19', '38', 'μετα δε ταυτα ηρωτησεν τον πιλατον ο ιωσηφ ο απο αριμαθαιας ων μαθητης του ιησου κεκρυμμενος δε δια τον φοβον των ιουδαιων ινα αρη το σωμα του ιησου και επετρεψεν ο πιλατος ηλθεν ουν και ηρεν το σωμα του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('3720', '43', '19', '39', 'ηλθεν δε και νικοδημος ο ελθων προς τον ιησουν νυκτος το πρωτον φερων μιγμα σμυρνης και αλοης ωσει λιτρας εκατον');
INSERT INTO `bible_verses_tr` VALUES ('3721', '43', '19', '40', 'ελαβον ουν το σωμα του ιησου και εδησαν αυτο οθονιοις μετα των αρωματων καθως εθος εστιν τοις ιουδαιοις ενταφιαζειν');
INSERT INTO `bible_verses_tr` VALUES ('3722', '43', '19', '41', 'ην δε εν τω τοπω οπου εσταυρωθη κηπος και εν τω κηπω μνημειον καινον εν ω ουδεπω ουδεις ετεθη');
INSERT INTO `bible_verses_tr` VALUES ('3723', '43', '19', '42', 'εκει ουν δια την παρασκευην των ιουδαιων οτι εγγυς ην το μνημειον εθηκαν τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('3724', '43', '20', '1', 'τη δε μια των σαββατων μαρια η μαγδαληνη ερχεται πρωι σκοτιας ετι ουσης εις το μνημειον και βλεπει τον λιθον ηρμενον εκ του μνημειου');
INSERT INTO `bible_verses_tr` VALUES ('3725', '43', '20', '2', 'τρεχει ουν και ερχεται προς σιμωνα πετρον και προς τον αλλον μαθητην ον εφιλει ο ιησους και λεγει αυτοις ηραν τον κυριον εκ του μνημειου και ουκ οιδαμεν που εθηκαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3726', '43', '20', '3', 'εξηλθεν ουν ο πετρος και ο αλλος μαθητης και ηρχοντο εις το μνημειον');
INSERT INTO `bible_verses_tr` VALUES ('3727', '43', '20', '4', 'ετρεχον δε οι δυο ομου και ο αλλος μαθητης προεδραμεν ταχιον του πετρου και ηλθεν πρωτος εις το μνημειον');
INSERT INTO `bible_verses_tr` VALUES ('3728', '43', '20', '5', 'και παρακυψας βλεπει κειμενα τα οθονια ου μεντοι εισηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('3729', '43', '20', '6', 'ερχεται ουν σιμων πετρος ακολουθων αυτω και εισηλθεν εις το μνημειον και θεωρει τα οθονια κειμενα');
INSERT INTO `bible_verses_tr` VALUES ('3730', '43', '20', '7', 'και το σουδαριον ο ην επι της κεφαλης αυτου ου μετα των οθονιων κειμενον αλλα χωρις εντετυλιγμενον εις ενα τοπον');
INSERT INTO `bible_verses_tr` VALUES ('3731', '43', '20', '8', 'τοτε ουν εισηλθεν και ο αλλος μαθητης ο ελθων πρωτος εις το μνημειον και ειδεν και επιστευσεν');
INSERT INTO `bible_verses_tr` VALUES ('3732', '43', '20', '9', 'ουδεπω γαρ ηδεισαν την γραφην οτι δει αυτον εκ νεκρων αναστηναι');
INSERT INTO `bible_verses_tr` VALUES ('3733', '43', '20', '10', 'απηλθον ουν παλιν προς εαυτους οι μαθηται');
INSERT INTO `bible_verses_tr` VALUES ('3734', '43', '20', '11', 'μαρια δε ειστηκει προς το μνημειον κλαιουσα εξω ως ουν εκλαιεν παρεκυψεν εις το μνημειον');
INSERT INTO `bible_verses_tr` VALUES ('3735', '43', '20', '12', 'και θεωρει δυο αγγελους εν λευκοις καθεζομενους ενα προς τη κεφαλη και ενα προς τοις ποσιν οπου εκειτο το σωμα του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('3736', '43', '20', '13', 'και λεγουσιν αυτη εκεινοι γυναι τι κλαιεις λεγει αυτοις οτι ηραν τον κυριον μου και ουκ οιδα που εθηκαν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3737', '43', '20', '14', 'και ταυτα ειπουσα εστραφη εις τα οπισω και θεωρει τον ιησουν εστωτα και ουκ ηδει οτι ο ιησους εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3738', '43', '20', '15', 'λεγει αυτη ο ιησους γυναι τι κλαιεις τινα ζητεις εκεινη δοκουσα οτι ο κηπουρος εστιν λεγει αυτω κυριε ει συ εβαστασας αυτον ειπε μοι που αυτον εθηκας καγω αυτον αρω');
INSERT INTO `bible_verses_tr` VALUES ('3739', '43', '20', '16', 'λεγει αυτη ο ιησους μαρια στραφεισα εκεινη λεγει αυτω ραββουνι ο λεγεται διδασκαλε');
INSERT INTO `bible_verses_tr` VALUES ('3740', '43', '20', '17', 'λεγει αυτη ο ιησους μη μου απτου ουπω γαρ αναβεβηκα προς τον πατερα μου πορευου δε προς τους αδελφους μου και ειπε αυτοις αναβαινω προς τον πατερα μου και πατερα υμων και θεον μου και θεον υμων');
INSERT INTO `bible_verses_tr` VALUES ('3741', '43', '20', '18', 'ερχεται μαρια η μαγδαληνη απαγγελλουσα τοις μαθηταις οτι εωρακεν τον κυριον και ταυτα ειπεν αυτη');
INSERT INTO `bible_verses_tr` VALUES ('3742', '43', '20', '19', 'ουσης ουν οψιας τη ημερα εκεινη τη μια των σαββατων και των θυρων κεκλεισμενων οπου ησαν οι μαθηται συνηγμενοι δια τον φοβον των ιουδαιων ηλθεν ο ιησους και εστη εις το μεσον και λεγει αυτοις ειρηνη υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3743', '43', '20', '20', 'και τουτο ειπων εδειξεν αυτοις τας χειρας και την πλευραν αυτου εχαρησαν ουν οι μαθηται ιδοντες τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('3744', '43', '20', '21', 'ειπεν ουν αυτοις ο ιησους παλιν ειρηνη υμιν καθως απεσταλκεν με ο πατηρ καγω πεμπω υμας');
INSERT INTO `bible_verses_tr` VALUES ('3745', '43', '20', '22', 'και τουτο ειπων ενεφυσησεν και λεγει αυτοις λαβετε πνευμα αγιον');
INSERT INTO `bible_verses_tr` VALUES ('3746', '43', '20', '23', 'αν τινων αφητε τας αμαρτιας αφιενται αυτοις αν τινων κρατητε κεκρατηνται');
INSERT INTO `bible_verses_tr` VALUES ('3747', '43', '20', '24', 'θωμας δε εις εκ των δωδεκα ο λεγομενος διδυμος ουκ ην μετ αυτων οτε ηλθεν ο ιησους');
INSERT INTO `bible_verses_tr` VALUES ('3748', '43', '20', '25', 'ελεγον ουν αυτω οι αλλοι μαθηται εωρακαμεν τον κυριον ο δε ειπεν αυτοις εαν μη ιδω εν ταις χερσιν αυτου τον τυπον των ηλων και βαλω τον δακτυλον μου εις τον τυπον των ηλων και βαλω την χειρα μου εις την πλευραν αυτου ου μη πιστευσω');
INSERT INTO `bible_verses_tr` VALUES ('3749', '43', '20', '26', 'και μεθ ημερας οκτω παλιν ησαν εσω οι μαθηται αυτου και θωμας μετ αυτων ερχεται ο ιησους των θυρων κεκλεισμενων και εστη εις το μεσον και ειπεν ειρηνη υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3750', '43', '20', '27', 'ειτα λεγει τω θωμα φερε τον δακτυλον σου ωδε και ιδε τας χειρας μου και φερε την χειρα σου και βαλε εις την πλευραν μου και μη γινου απιστος αλλα πιστος');
INSERT INTO `bible_verses_tr` VALUES ('3751', '43', '20', '28', 'και απεκριθη ο θωμας και ειπεν αυτω ο κυριος μου και ο θεος μου');
INSERT INTO `bible_verses_tr` VALUES ('3752', '43', '20', '29', 'λεγει αυτω ο ιησους οτι εωρακας με θωμα πεπιστευκας μακαριοι οι μη ιδοντες και πιστευσαντες');
INSERT INTO `bible_verses_tr` VALUES ('3753', '43', '20', '30', 'πολλα μεν ουν και αλλα σημεια εποιησεν ο ιησους ενωπιον των μαθητων αυτου α ουκ εστιν γεγραμμενα εν τω βιβλιω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('3754', '43', '20', '31', 'ταυτα δε γεγραπται ινα πιστευσητε οτι ο ιησους εστιν ο χριστος ο υιος του θεου και ινα πιστευοντες ζωην εχητε εν τω ονοματι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3755', '43', '21', '1', 'μετα ταυτα εφανερωσεν εαυτον παλιν ο ιησους τοις μαθηταις επι της θαλασσης της τιβεριαδος εφανερωσεν δε ουτως');
INSERT INTO `bible_verses_tr` VALUES ('3756', '43', '21', '2', 'ησαν ομου σιμων πετρος και θωμας ο λεγομενος διδυμος και ναθαναηλ ο απο κανα της γαλιλαιας και οι του ζεβεδαιου και αλλοι εκ των μαθητων αυτου δυο');
INSERT INTO `bible_verses_tr` VALUES ('3757', '43', '21', '3', 'λεγει αυτοις σιμων πετρος υπαγω αλιευειν λεγουσιν αυτω ερχομεθα και ημεις συν σοι εξηλθον και ανεβησαν εις το πλοιον ευθυς και εν εκεινη τη νυκτι επιασαν ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3758', '43', '21', '4', 'πρωιας δε ηδη γενομενης εστη ο ιησους εις τον αιγιαλον ου μεντοι ηδεισαν οι μαθηται οτι ιησους εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3759', '43', '21', '5', 'λεγει ουν αυτοις ο ιησους παιδια μη τι προσφαγιον εχετε απεκριθησαν αυτω ου');
INSERT INTO `bible_verses_tr` VALUES ('3760', '43', '21', '6', 'ο δε ειπεν αυτοις βαλετε εις τα δεξια μερη του πλοιου το δικτυον και ευρησετε εβαλον ουν και ουκ ετι αυτο ελκυσαι ισχυσαν απο του πληθους των ιχθυων');
INSERT INTO `bible_verses_tr` VALUES ('3761', '43', '21', '7', 'λεγει ουν ο μαθητης εκεινος ον ηγαπα ο ιησους τω πετρω ο κυριος εστιν σιμων ουν πετρος ακουσας οτι ο κυριος εστιν τον επενδυτην διεζωσατο ην γαρ γυμνος και εβαλεν εαυτον εις την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('3762', '43', '21', '8', 'οι δε αλλοι μαθηται τω πλοιαριω ηλθον ου γαρ ησαν μακραν απο της γης αλλ ως απο πηχων διακοσιων συροντες το δικτυον των ιχθυων');
INSERT INTO `bible_verses_tr` VALUES ('3763', '43', '21', '9', 'ως ουν απεβησαν εις την γην βλεπουσιν ανθρακιαν κειμενην και οψαριον επικειμενον και αρτον');
INSERT INTO `bible_verses_tr` VALUES ('3764', '43', '21', '10', 'λεγει αυτοις ο ιησους ενεγκατε απο των οψαριων ων επιασατε νυν');
INSERT INTO `bible_verses_tr` VALUES ('3765', '43', '21', '11', 'ανεβη σιμων πετρος και ειλκυσεν το δικτυον επι της γης μεστον ιχθυων μεγαλων εκατον πεντηκοντατριων και τοσουτων οντων ουκ εσχισθη το δικτυον');
INSERT INTO `bible_verses_tr` VALUES ('3766', '43', '21', '12', 'λεγει αυτοις ο ιησους δευτε αριστησατε ουδεις δε ετολμα των μαθητων εξετασαι αυτον συ τις ει ειδοτες οτι ο κυριος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('3767', '43', '21', '13', 'ερχεται ουν ο ιησους και λαμβανει τον αρτον και διδωσιν αυτοις και το οψαριον ομοιως');
INSERT INTO `bible_verses_tr` VALUES ('3768', '43', '21', '14', 'τουτο ηδη τριτον εφανερωθη ο ιησους τοις μαθηταις αυτου εγερθεις εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('3769', '43', '21', '15', 'οτε ουν ηριστησαν λεγει τω σιμωνι πετρω ο ιησους σιμων ιωνα αγαπας με πλειον τουτων λεγει αυτω ναι κυριε συ οιδας οτι φιλω σε λεγει αυτω βοσκε τα αρνια μου');
INSERT INTO `bible_verses_tr` VALUES ('3770', '43', '21', '16', 'λεγει αυτω παλιν δευτερον σιμων ιωνα αγαπας με λεγει αυτω ναι κυριε συ οιδας οτι φιλω σε λεγει αυτω ποιμαινε τα προβατα μου');
INSERT INTO `bible_verses_tr` VALUES ('3771', '43', '21', '17', 'λεγει αυτω το τριτον σιμων ιωνα φιλεις με ελυπηθη ο πετρος οτι ειπεν αυτω το τριτον φιλεις με και ειπεν αυτω κυριε συ παντα οιδας συ γινωσκεις οτι φιλω σε λεγει αυτω ο ιησους βοσκε τα προβατα μου');
INSERT INTO `bible_verses_tr` VALUES ('3772', '43', '21', '18', 'αμην αμην λεγω σοι οτε ης νεωτερος εζωννυες σεαυτον και περιεπατεις οπου ηθελες οταν δε γηρασης εκτενεις τας χειρας σου και αλλος σε ζωσει και οισει οπου ου θελεις');
INSERT INTO `bible_verses_tr` VALUES ('3773', '43', '21', '19', 'τουτο δε ειπεν σημαινων ποιω θανατω δοξασει τον θεον και τουτο ειπων λεγει αυτω ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('3774', '43', '21', '20', 'επιστραφεις δε ο πετρος βλεπει τον μαθητην ον ηγαπα ο ιησους ακολουθουντα ος και ανεπεσεν εν τω δειπνω επι το στηθος αυτου και ειπεν κυριε τις εστιν ο παραδιδους σε');
INSERT INTO `bible_verses_tr` VALUES ('3775', '43', '21', '21', 'τουτον ιδων ο πετρος λεγει τω ιησου κυριε ουτος δε τι');
INSERT INTO `bible_verses_tr` VALUES ('3776', '43', '21', '22', 'λεγει αυτω ο ιησους εαν αυτον θελω μενειν εως ερχομαι τι προς σε συ ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('3777', '43', '21', '23', 'εξηλθεν ουν ο λογος ουτος εις τους αδελφους οτι ο μαθητης εκεινος ουκ αποθνησκει και ουκ ειπεν αυτω ο ιησους οτι ουκ αποθνησκει αλλ εαν αυτον θελω μενειν εως ερχομαι τι προς σε');
INSERT INTO `bible_verses_tr` VALUES ('3778', '43', '21', '24', 'ουτος εστιν ο μαθητης ο μαρτυρων περι τουτων και γραψας ταυτα και οιδαμεν οτι αληθης εστιν η μαρτυρια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3779', '43', '21', '25', 'εστιν δε και αλλα πολλα οσα εποιησεν ο ιησους ατινα εαν γραφηται καθ εν ουδε αυτον οιμαι τον κοσμον χωρησαι τα γραφομενα βιβλια αμην');
INSERT INTO `bible_verses_tr` VALUES ('3780', '44', '1', '1', 'τον μεν πρωτον λογον εποιησαμην περι παντων ω θεοφιλε ων ηρξατο ο ιησους ποιειν τε και διδασκειν');
INSERT INTO `bible_verses_tr` VALUES ('3781', '44', '1', '2', 'αχρι ης ημερας εντειλαμενος τοις αποστολοις δια πνευματος αγιου ους εξελεξατο ανεληφθη');
INSERT INTO `bible_verses_tr` VALUES ('3782', '44', '1', '3', 'οις και παρεστησεν εαυτον ζωντα μετα το παθειν αυτον εν πολλοις τεκμηριοις δι ημερων τεσσαρακοντα οπτανομενος αυτοις και λεγων τα περι της βασιλειας του θεου');
INSERT INTO `bible_verses_tr` VALUES ('3783', '44', '1', '4', 'και συναλιζομενος  {VAR2: μετ αυτων } παρηγγειλεν αυτοις απο ιεροσολυμων μη χωριζεσθαι αλλα περιμενειν την επαγγελιαν του πατρος ην ηκουσατε μου');
INSERT INTO `bible_verses_tr` VALUES ('3784', '44', '1', '5', 'οτι ιωαννης μεν εβαπτισεν υδατι υμεις δε βαπτισθησεσθε εν πνευματι αγιω ου μετα πολλας ταυτας ημερας');
INSERT INTO `bible_verses_tr` VALUES ('3785', '44', '1', '6', 'οι μεν ουν συνελθοντες επηρωτων αυτον λεγοντες κυριε ει εν τω χρονω τουτω αποκαθιστανεις την βασιλειαν τω ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('3786', '44', '1', '7', 'ειπεν δε προς αυτους ουχ υμων εστιν γνωναι χρονους η καιρους ους ο πατηρ εθετο εν τη ιδια εξουσια');
INSERT INTO `bible_verses_tr` VALUES ('3787', '44', '1', '8', 'αλλα ληψεσθε δυναμιν επελθοντος του αγιου πνευματος εφ υμας και εσεσθε μοι μαρτυρες εν τε ιερουσαλημ και εν παση τη ιουδαια και σαμαρεια και εως εσχατου της γης');
INSERT INTO `bible_verses_tr` VALUES ('3788', '44', '1', '9', 'και ταυτα ειπων βλεποντων αυτων επηρθη και νεφελη υπελαβεν αυτον απο των οφθαλμων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3789', '44', '1', '10', 'και ως ατενιζοντες ησαν εις τον ουρανον πορευομενου αυτου και ιδου ανδρες δυο παρειστηκεισαν αυτοις εν εσθητι λευκη');
INSERT INTO `bible_verses_tr` VALUES ('3790', '44', '1', '11', 'οι και ειπον ανδρες γαλιλαιοι τι εστηκατε εμβλεποντες εις τον ουρανον ουτος ο ιησους ο αναληφθεις αφ υμων εις τον ουρανον ουτως ελευσεται ον τροπον εθεασασθε αυτον πορευομενον εις τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('3791', '44', '1', '12', 'τοτε υπεστρεψαν εις ιερουσαλημ απο ορους του καλουμενου ελαιωνος ο εστιν εγγυς ιερουσαλημ σαββατου εχον οδον');
INSERT INTO `bible_verses_tr` VALUES ('3792', '44', '1', '13', 'και οτε εισηλθον ανεβησαν εις το υπερωον ου ησαν καταμενοντες ο τε πετρος και ιακωβος και ιωαννης και ανδρεας φιλιππος και θωμας βαρθολομαιος και ματθαιος ιακωβος αλφαιου και σιμων ο ζηλωτης και ιουδας ιακωβου');
INSERT INTO `bible_verses_tr` VALUES ('3793', '44', '1', '14', 'ουτοι παντες ησαν προσκαρτερουντες ομοθυμαδον τη προσευχη και τη δεησει συν γυναιξιν και μαρια τη μητρι του ιησου και συν τοις αδελφοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3794', '44', '1', '15', 'και εν ταις ημεραις ταυταις αναστας πετρος εν μεσω των μαθητων ειπεν ην τε οχλος ονοματων επι το αυτο ως εκατον εικοσιν');
INSERT INTO `bible_verses_tr` VALUES ('3795', '44', '1', '16', 'ανδρες αδελφοι εδει πληρωθηναι την γραφην ταυτην ην προειπεν το πνευμα το αγιον δια στοματος δαβιδ περι ιουδα του γενομενου οδηγου τοις συλλαβουσιν τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('3796', '44', '1', '17', 'οτι κατηριθμημενος ην συν ημιν και ελαχεν τον κληρον της διακονιας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('3797', '44', '1', '18', 'ουτος μεν ουν εκτησατο χωριον εκ του μισθου της αδικιας και πρηνης γενομενος ελακησεν μεσος και εξεχυθη παντα τα σπλαγχνα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3798', '44', '1', '19', 'και γνωστον εγενετο πασιν τοις κατοικουσιν ιερουσαλημ ωστε κληθηναι το χωριον εκεινο τη ιδια διαλεκτω αυτων ακελδαμα τουτ εστιν χωριον αιματος');
INSERT INTO `bible_verses_tr` VALUES ('3799', '44', '1', '20', 'γεγραπται γαρ εν βιβλω ψαλμων γενηθητω η επαυλις αυτου ερημος και μη εστω ο κατοικων εν αυτη και την επισκοπην αυτου λαβοι ετερος');
INSERT INTO `bible_verses_tr` VALUES ('3800', '44', '1', '21', 'δει ουν των συνελθοντων ημιν ανδρων εν παντι χρονω εν ω εισηλθεν και εξηλθεν εφ ημας ο κυριος ιησους');
INSERT INTO `bible_verses_tr` VALUES ('3801', '44', '1', '22', 'αρξαμενος απο του βαπτισματος ιωαννου εως της ημερας ης ανεληφθη αφ ημων μαρτυρα της αναστασεως αυτου γενεσθαι συν ημιν ενα τουτων');
INSERT INTO `bible_verses_tr` VALUES ('3802', '44', '1', '23', 'και εστησαν δυο ιωσηφ τον καλουμενον βαρσαβαν ος επεκληθη ιουστος και ματθιαν');
INSERT INTO `bible_verses_tr` VALUES ('3803', '44', '1', '24', 'και προσευξαμενοι ειπον συ κυριε καρδιογνωστα παντων αναδειξον εκ τουτων των δυο  {VAR1: ενα ον } {VAR2: ον ενα } εξελεξω');
INSERT INTO `bible_verses_tr` VALUES ('3804', '44', '1', '25', 'λαβειν τον κληρον της διακονιας ταυτης και αποστολης εξ ης παρεβη ιουδας πορευθηναι εις τον τοπον τον ιδιον');
INSERT INTO `bible_verses_tr` VALUES ('3805', '44', '1', '26', 'και εδωκαν κληρους αυτων και επεσεν ο κληρος επι ματθιαν και συγκατεψηφισθη μετα των ενδεκα αποστολων');
INSERT INTO `bible_verses_tr` VALUES ('3806', '44', '2', '1', 'και εν τω συμπληρουσθαι την ημεραν της πεντηκοστης ησαν απαντες ομοθυμαδον επι το αυτο');
INSERT INTO `bible_verses_tr` VALUES ('3807', '44', '2', '2', 'και εγενετο αφνω εκ του ουρανου ηχος ωσπερ φερομενης πνοης βιαιας και επληρωσεν ολον τον οικον ου ησαν καθημενοι');
INSERT INTO `bible_verses_tr` VALUES ('3808', '44', '2', '3', 'και ωφθησαν αυτοις διαμεριζομεναι γλωσσαι ωσει πυρος εκαθισεν τε εφ ενα εκαστον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3809', '44', '2', '4', 'και επλησθησαν απαντες πνευματος αγιου και ηρξαντο λαλειν ετεραις γλωσσαις καθως το πνευμα εδιδου αυτοις αποφθεγγεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3810', '44', '2', '5', 'ησαν δε εν ιερουσαλημ κατοικουντες ιουδαιοι ανδρες ευλαβεις απο παντος εθνους των υπο τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('3811', '44', '2', '6', 'γενομενης δε της φωνης ταυτης συνηλθεν το πληθος και συνεχυθη οτι ηκουον εις εκαστος τη ιδια διαλεκτω λαλουντων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3812', '44', '2', '7', 'εξισταντο δε παντες και εθαυμαζον λεγοντες προς αλληλους ουκ ιδου παντες ουτοι εισιν οι λαλουντες γαλιλαιοι');
INSERT INTO `bible_verses_tr` VALUES ('3813', '44', '2', '8', 'και πως ημεις ακουομεν εκαστος τη ιδια διαλεκτω ημων εν η εγεννηθημεν');
INSERT INTO `bible_verses_tr` VALUES ('3814', '44', '2', '9', 'παρθοι και μηδοι και ελαμιται και οι κατοικουντες την μεσοποταμιαν ιουδαιαν τε και καππαδοκιαν ποντον και την ασιαν');
INSERT INTO `bible_verses_tr` VALUES ('3815', '44', '2', '10', 'φρυγιαν τε και παμφυλιαν αιγυπτον και τα μερη της λιβυης της κατα κυρηνην και οι επιδημουντες ρωμαιοι ιουδαιοι τε και προσηλυτοι');
INSERT INTO `bible_verses_tr` VALUES ('3816', '44', '2', '11', 'κρητες και αραβες ακουομεν λαλουντων αυτων ταις ημετεραις γλωσσαις τα μεγαλεια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('3817', '44', '2', '12', 'εξισταντο δε παντες και διηπορουν αλλος προς αλλον λεγοντες τι αν θελοι τουτο ειναι');
INSERT INTO `bible_verses_tr` VALUES ('3818', '44', '2', '13', 'ετεροι δε χλευαζοντες ελεγον οτι γλευκους μεμεστωμενοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('3819', '44', '2', '14', 'σταθεις δε πετρος συν τοις ενδεκα επηρεν την φωνην αυτου και απεφθεγξατο αυτοις ανδρες ιουδαιοι και οι κατοικουντες ιερουσαλημ απαντες τουτο υμιν γνωστον εστω και ενωτισασθε τα ρηματα μου');
INSERT INTO `bible_verses_tr` VALUES ('3820', '44', '2', '15', 'ου γαρ ως υμεις υπολαμβανετε ουτοι μεθυουσιν εστιν γαρ ωρα τριτη της ημερας');
INSERT INTO `bible_verses_tr` VALUES ('3821', '44', '2', '16', 'αλλα τουτο εστιν το ειρημενον δια του προφητου ιωηλ');
INSERT INTO `bible_verses_tr` VALUES ('3822', '44', '2', '17', 'και εσται εν ταις εσχαταις ημεραις λεγει ο θεος εκχεω απο του πνευματος μου επι πασαν σαρκα και προφητευσουσιν οι υιοι υμων και αι θυγατερες υμων και οι νεανισκοι υμων ορασεις οψονται και οι πρεσβυτεροι υμων ενυπνια ενυπνιασθησονται');
INSERT INTO `bible_verses_tr` VALUES ('3823', '44', '2', '18', 'και γε επι τους δουλους μου και επι τας δουλας μου εν ταις ημεραις εκειναις εκχεω απο του πνευματος μου και προφητευσουσιν');
INSERT INTO `bible_verses_tr` VALUES ('3824', '44', '2', '19', 'και δωσω τερατα εν τω ουρανω ανω και σημεια επι της γης κατω αιμα και πυρ και ατμιδα καπνου');
INSERT INTO `bible_verses_tr` VALUES ('3825', '44', '2', '20', 'ο ηλιος μεταστραφησεται εις σκοτος και η σεληνη εις αιμα πριν η ελθειν την ημεραν κυριου την μεγαλην και επιφανη');
INSERT INTO `bible_verses_tr` VALUES ('3826', '44', '2', '21', 'και εσται πας ος αν επικαλεσηται το ονομα κυριου σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('3827', '44', '2', '22', 'ανδρες ισραηλιται ακουσατε τους λογους τουτους ιησουν τον ναζωραιον ανδρα απο του θεου αποδεδειγμενον εις υμας δυναμεσιν και τερασιν και σημειοις οις εποιησεν δι αυτου ο θεος εν μεσω υμων καθως και αυτοι οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('3828', '44', '2', '23', 'τουτον τη ωρισμενη βουλη και προγνωσει του θεου εκδοτον λαβοντες δια χειρων ανομων προσπηξαντες ανειλετε');
INSERT INTO `bible_verses_tr` VALUES ('3829', '44', '2', '24', 'ον ο θεος ανεστησεν λυσας τας ωδινας του θανατου καθοτι ουκ ην δυνατον κρατεισθαι αυτον υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3830', '44', '2', '25', 'δαβιδ γαρ λεγει εις αυτον προωρωμην τον κυριον ενωπιον μου δια παντος οτι εκ δεξιων μου εστιν ινα μη σαλευθω');
INSERT INTO `bible_verses_tr` VALUES ('3831', '44', '2', '26', 'δια τουτο ευφρανθη η καρδια μου και ηγαλλιασατο η γλωσσα μου ετι δε και η σαρξ μου κατασκηνωσει επ ελπιδι');
INSERT INTO `bible_verses_tr` VALUES ('3832', '44', '2', '27', 'οτι ουκ εγκαταλειψεις την ψυχην μου εις αδου ουδε δωσεις τον οσιον σου ιδειν διαφθοραν');
INSERT INTO `bible_verses_tr` VALUES ('3833', '44', '2', '28', 'εγνωρισας μοι οδους ζωης πληρωσεις με ευφροσυνης μετα του προσωπου σου');
INSERT INTO `bible_verses_tr` VALUES ('3834', '44', '2', '29', 'ανδρες αδελφοι εξον ειπειν μετα παρρησιας προς υμας περι του πατριαρχου δαβιδ οτι και ετελευτησεν και εταφη και το μνημα αυτου εστιν εν ημιν αχρι της ημερας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('3835', '44', '2', '30', 'προφητης ουν υπαρχων και ειδως οτι ορκω ωμοσεν αυτω ο θεος εκ καρπου της οσφυος αυτου το κατα σαρκα αναστησειν τον χριστον καθισαι επι του θρονου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3836', '44', '2', '31', 'προιδων ελαλησεν περι της αναστασεως του χριστου οτι ου κατελειφθη η ψυχη αυτου εις αδου ουδε η σαρξ αυτου ειδεν διαφθοραν');
INSERT INTO `bible_verses_tr` VALUES ('3837', '44', '2', '32', 'τουτον τον ιησουν ανεστησεν ο θεος ου παντες ημεις εσμεν μαρτυρες');
INSERT INTO `bible_verses_tr` VALUES ('3838', '44', '2', '33', 'τη δεξια ουν του θεου υψωθεις την τε επαγγελιαν του αγιου πνευματος λαβων παρα του πατρος εξεχεεν τουτο ο νυν υμεις βλεπετε και ακουετε');
INSERT INTO `bible_verses_tr` VALUES ('3839', '44', '2', '34', 'ου γαρ δαβιδ ανεβη εις τους ουρανους λεγει δε αυτος ειπεν ο κυριος τω κυριω μου καθου εκ δεξιων μου');
INSERT INTO `bible_verses_tr` VALUES ('3840', '44', '2', '35', 'εως αν θω τους εχθρους σου υποποδιον των ποδων σου');
INSERT INTO `bible_verses_tr` VALUES ('3841', '44', '2', '36', 'ασφαλως ουν γινωσκετω πας οικος ισραηλ οτι και κυριον και χριστον αυτον ο θεος εποιησεν τουτον τον ιησουν ον υμεις εσταυρωσατε');
INSERT INTO `bible_verses_tr` VALUES ('3842', '44', '2', '37', 'ακουσαντες δε κατενυγησαν τη καρδια ειπον τε προς τον πετρον και τους λοιπους αποστολους τι ποιησομεν ανδρες αδελφοι');
INSERT INTO `bible_verses_tr` VALUES ('3843', '44', '2', '38', 'πετρος δε εφη προς αυτους μετανοησατε και βαπτισθητω εκαστος υμων επι τω ονοματι ιησου χριστου εις αφεσιν αμαρτιων και ληψεσθε την δωρεαν του αγιου πνευματος');
INSERT INTO `bible_verses_tr` VALUES ('3844', '44', '2', '39', 'υμιν γαρ εστιν η επαγγελια και τοις τεκνοις υμων και πασιν τοις εις μακραν οσους αν προσκαλεσηται κυριος ο θεος ημων');
INSERT INTO `bible_verses_tr` VALUES ('3845', '44', '2', '40', 'ετεροις τε λογοις πλειοσιν διεμαρτυρετο και παρεκαλει λεγων σωθητε απο της γενεας της σκολιας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('3846', '44', '2', '41', 'οι μεν ουν ασμενως αποδεξαμενοι τον λογον αυτου εβαπτισθησαν και προσετεθησαν τη ημερα εκεινη ψυχαι ωσει τρισχιλιαι');
INSERT INTO `bible_verses_tr` VALUES ('3847', '44', '2', '42', 'ησαν δε προσκαρτερουντες τη διδαχη των αποστολων και τη κοινωνια και τη κλασει του αρτου και ταις προσευχαις');
INSERT INTO `bible_verses_tr` VALUES ('3848', '44', '2', '43', 'εγενετο δε παση ψυχη φοβος πολλα τε τερατα και σημεια δια των αποστολων εγινετο');
INSERT INTO `bible_verses_tr` VALUES ('3849', '44', '2', '44', 'παντες δε οι πιστευοντες ησαν επι το αυτο και ειχον απαντα κοινα');
INSERT INTO `bible_verses_tr` VALUES ('3850', '44', '2', '45', 'και τα κτηματα και τας υπαρξεις επιπρασκον και διεμεριζον αυτα πασιν καθοτι αν τις χρειαν ειχεν');
INSERT INTO `bible_verses_tr` VALUES ('3851', '44', '2', '46', 'καθ ημεραν τε προσκαρτερουντες ομοθυμαδον εν τω ιερω κλωντες τε κατ οικον αρτον μετελαμβανον τροφης εν αγαλλιασει και αφελοτητι καρδιας');
INSERT INTO `bible_verses_tr` VALUES ('3852', '44', '2', '47', 'αινουντες τον θεον και εχοντες χαριν προς ολον τον λαον ο δε κυριος προσετιθει τους σωζομενους καθ ημεραν τη εκκλησια');
INSERT INTO `bible_verses_tr` VALUES ('3853', '44', '3', '1', 'επι το αυτο δε πετρος και ιωαννης ανεβαινον εις το ιερον επι την ωραν της προσευχης την εννατην');
INSERT INTO `bible_verses_tr` VALUES ('3854', '44', '3', '2', 'και τις ανηρ χωλος εκ κοιλιας μητρος αυτου υπαρχων εβασταζετο ον ετιθουν καθ ημεραν προς την θυραν του ιερου την λεγομενην ωραιαν του αιτειν ελεημοσυνην παρα των εισπορευομενων εις το ιερον');
INSERT INTO `bible_verses_tr` VALUES ('3855', '44', '3', '3', 'ος ιδων πετρον και ιωαννην μελλοντας εισιεναι εις το ιερον ηρωτα ελεημοσυνην  {VAR1: λαβειν }');
INSERT INTO `bible_verses_tr` VALUES ('3856', '44', '3', '4', 'ατενισας δε πετρος εις αυτον συν τω ιωαννη ειπεν βλεψον εις ημας');
INSERT INTO `bible_verses_tr` VALUES ('3857', '44', '3', '5', 'ο δε επειχεν αυτοις προσδοκων τι παρ αυτων λαβειν');
INSERT INTO `bible_verses_tr` VALUES ('3858', '44', '3', '6', 'ειπεν δε πετρος αργυριον και χρυσιον ουχ υπαρχει μοι ο δε εχω τουτο σοι διδωμι εν τω ονοματι ιησου χριστου του ναζωραιου εγειραι και περιπατει');
INSERT INTO `bible_verses_tr` VALUES ('3859', '44', '3', '7', 'και πιασας αυτον της δεξιας χειρος ηγειρεν παραχρημα δε εστερεωθησαν αυτου αι βασεις και τα σφυρα');
INSERT INTO `bible_verses_tr` VALUES ('3860', '44', '3', '8', 'και εξαλλομενος εστη και περιεπατει και εισηλθεν συν αυτοις εις το ιερον περιπατων και αλλομενος και αινων τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('3861', '44', '3', '9', 'και ειδεν αυτον πας ο λαος περιπατουντα και αινουντα τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('3862', '44', '3', '10', 'επεγινωσκον τε αυτον οτι ουτος ην ο προς την ελεημοσυνην καθημενος επι τη ωραια πυλη του ιερου και επλησθησαν θαμβους και εκστασεως επι τω συμβεβηκοτι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3863', '44', '3', '11', 'κρατουντος δε του ιαθεντος χωλου τον πετρον και ιωαννην συνεδραμεν προς αυτους πας ο λαος επι τη στοα τη καλουμενη σολομωντος εκθαμβοι');
INSERT INTO `bible_verses_tr` VALUES ('3864', '44', '3', '12', 'ιδων δε πετρος απεκρινατο προς τον λαον ανδρες ισραηλιται τι θαυμαζετε επι τουτω η ημιν τι ατενιζετε ως ιδια δυναμει η ευσεβεια πεποιηκοσιν του περιπατειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('3865', '44', '3', '13', 'ο θεος αβρααμ και ισαακ και ιακωβ ο θεος των πατερων ημων εδοξασεν τον παιδα αυτου ιησουν ον υμεις παρεδωκατε και ηρνησασθε αυτον κατα προσωπον πιλατου κριναντος εκεινου απολυειν');
INSERT INTO `bible_verses_tr` VALUES ('3866', '44', '3', '14', 'υμεις δε τον αγιον και δικαιον ηρνησασθε και ητησασθε ανδρα φονεα χαρισθηναι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('3867', '44', '3', '15', 'τον δε αρχηγον της ζωης απεκτεινατε ον ο θεος ηγειρεν εκ νεκρων ου ημεις μαρτυρες εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('3868', '44', '3', '16', 'και επι τη πιστει του ονοματος αυτου τουτον ον θεωρειτε και οιδατε εστερεωσεν το ονομα αυτου και η πιστις η δι αυτου εδωκεν αυτω την ολοκληριαν ταυτην απεναντι παντων υμων');
INSERT INTO `bible_verses_tr` VALUES ('3869', '44', '3', '17', 'και νυν αδελφοι οιδα οτι κατα αγνοιαν επραξατε ωσπερ και οι αρχοντες υμων');
INSERT INTO `bible_verses_tr` VALUES ('3870', '44', '3', '18', 'ο δε θεος α προκατηγγειλεν δια στοματος παντων των προφητων αυτου παθειν τον χριστον επληρωσεν ουτως');
INSERT INTO `bible_verses_tr` VALUES ('3871', '44', '3', '19', 'μετανοησατε ουν και επιστρεψατε εις το εξαλειφθηναι υμων τας αμαρτιας οπως αν ελθωσιν καιροι αναψυξεως απο προσωπου του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('3872', '44', '3', '20', 'και αποστειλη τον προκεκηρυγμενον υμιν ιησουν χριστον');
INSERT INTO `bible_verses_tr` VALUES ('3873', '44', '3', '21', 'ον δει ουρανον μεν δεξασθαι αχρι χρονων αποκαταστασεως παντων ων ελαλησεν ο θεος δια στοματος παντων αγιων αυτου προφητων απ αιωνος');
INSERT INTO `bible_verses_tr` VALUES ('3874', '44', '3', '22', 'μωσης μεν γαρ προς τους πατερας ειπεν οτι προφητην υμιν αναστησει κυριος ο θεος υμων εκ των αδελφων υμων ως εμε αυτου ακουσεσθε κατα παντα οσα αν λαληση προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('3875', '44', '3', '23', 'εσται δε πασα ψυχη ητις αν μη ακουση του προφητου εκεινου εξολοθρευθησεται εκ του λαου');
INSERT INTO `bible_verses_tr` VALUES ('3876', '44', '3', '24', 'και παντες δε οι προφηται απο σαμουηλ και των καθεξης οσοι ελαλησαν και προκατηγγειλαν τας ημερας ταυτας');
INSERT INTO `bible_verses_tr` VALUES ('3877', '44', '3', '25', 'υμεις εστε υιοι των προφητων και της διαθηκης ης διεθετο ο θεος προς τους πατερας ημων λεγων προς αβρααμ και τω σπερματι σου ενευλογηθησονται πασαι αι πατριαι της γης');
INSERT INTO `bible_verses_tr` VALUES ('3878', '44', '3', '26', 'υμιν πρωτον ο θεος αναστησας τον παιδα αυτου ιησουν απεστειλεν αυτον ευλογουντα υμας εν τω αποστρεφειν εκαστον απο των πονηριων υμων');
INSERT INTO `bible_verses_tr` VALUES ('3879', '44', '4', '1', 'λαλουντων δε αυτων προς τον λαον επεστησαν αυτοις οι ιερεις και ο στρατηγος του ιερου και οι σαδδουκαιοι');
INSERT INTO `bible_verses_tr` VALUES ('3880', '44', '4', '2', 'διαπονουμενοι δια το διδασκειν αυτους τον λαον και καταγγελλειν εν τω ιησου την αναστασιν την εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('3881', '44', '4', '3', 'και επεβαλον αυτοις τας χειρας και εθεντο εις τηρησιν εις την αυριον ην γαρ εσπερα ηδη');
INSERT INTO `bible_verses_tr` VALUES ('3882', '44', '4', '4', 'πολλοι δε των ακουσαντων τον λογον επιστευσαν και εγενηθη ο αριθμος των ανδρων ωσει χιλιαδες πεντε');
INSERT INTO `bible_verses_tr` VALUES ('3883', '44', '4', '5', 'εγενετο δε επι την αυριον συναχθηναι αυτων τους αρχοντας και πρεσβυτερους και γραμματεις');
INSERT INTO `bible_verses_tr` VALUES ('3884', '44', '4', '6', 'εις ιερουσαλημ και ανναν τον αρχιερεα και καιαφαν και ιωαννην και αλεξανδρον και οσοι ησαν εκ γενους αρχιερατικου');
INSERT INTO `bible_verses_tr` VALUES ('3885', '44', '4', '7', 'και στησαντες αυτους εν τω μεσω επυνθανοντο εν ποια δυναμει η εν ποιω ονοματι εποιησατε τουτο υμεις');
INSERT INTO `bible_verses_tr` VALUES ('3886', '44', '4', '8', 'τοτε πετρος πλησθεις πνευματος αγιου ειπεν προς αυτους αρχοντες του λαου και πρεσβυτεροι του ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('3887', '44', '4', '9', 'ει ημεις σημερον ανακρινομεθα επι ευεργεσια ανθρωπου ασθενους εν τινι ουτος σεσωσται');
INSERT INTO `bible_verses_tr` VALUES ('3888', '44', '4', '10', 'γνωστον εστω πασιν υμιν και παντι τω λαω ισραηλ οτι εν τω ονοματι ιησου χριστου του ναζωραιου ον υμεις εσταυρωσατε ον ο θεος ηγειρεν εκ νεκρων εν τουτω ουτος παρεστηκεν ενωπιον υμων υγιης');
INSERT INTO `bible_verses_tr` VALUES ('3889', '44', '4', '11', 'ουτος εστιν ο λιθος ο εξουθενηθεις υφ υμων των οικοδομουντων ο γενομενος εις κεφαλην γωνιας');
INSERT INTO `bible_verses_tr` VALUES ('3890', '44', '4', '12', 'και ουκ εστιν εν αλλω ουδενι η σωτηρια ουτε γαρ ονομα εστιν ετερον υπο τον ουρανον το δεδομενον εν ανθρωποις εν ω δει σωθηναι ημας');
INSERT INTO `bible_verses_tr` VALUES ('3891', '44', '4', '13', 'θεωρουντες δε την του πετρου παρρησιαν και ιωαννου και καταλαβομενοι οτι ανθρωποι αγραμματοι εισιν και ιδιωται εθαυμαζον επεγινωσκον τε αυτους οτι συν τω ιησου ησαν');
INSERT INTO `bible_verses_tr` VALUES ('3892', '44', '4', '14', 'τον δε ανθρωπον βλεποντες συν αυτοις εστωτα τον τεθεραπευμενον ουδεν ειχον αντειπειν');
INSERT INTO `bible_verses_tr` VALUES ('3893', '44', '4', '15', 'κελευσαντες δε αυτους εξω του συνεδριου απελθειν συνεβαλον προς αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('3894', '44', '4', '16', 'λεγοντες τι ποιησομεν τοις ανθρωποις τουτοις οτι μεν γαρ γνωστον σημειον γεγονεν δι αυτων πασιν τοις κατοικουσιν ιερουσαλημ φανερον και ου δυναμεθα αρνησασθαι');
INSERT INTO `bible_verses_tr` VALUES ('3895', '44', '4', '17', 'αλλ ινα μη επι πλειον διανεμηθη εις τον λαον απειλη απειλησωμεθα αυτοις μηκετι λαλειν επι τω ονοματι τουτω μηδενι ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('3896', '44', '4', '18', 'και καλεσαντες αυτους παρηγγειλαν αυτοις το καθολου μη φθεγγεσθαι μηδε διδασκειν επι τω ονοματι του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('3897', '44', '4', '19', 'ο δε πετρος και ιωαννης αποκριθεντες προς αυτους ειπον ει δικαιον εστιν ενωπιον του θεου υμων ακουειν μαλλον η του θεου κρινατε');
INSERT INTO `bible_verses_tr` VALUES ('3898', '44', '4', '20', 'ου δυναμεθα γαρ ημεις α ειδομεν και ηκουσαμεν μη λαλειν');
INSERT INTO `bible_verses_tr` VALUES ('3899', '44', '4', '21', 'οι δε προσαπειλησαμενοι απελυσαν αυτους μηδεν ευρισκοντες το πως κολασωνται αυτους δια τον λαον οτι παντες εδοξαζον τον θεον επι τω γεγονοτι');
INSERT INTO `bible_verses_tr` VALUES ('3900', '44', '4', '22', 'ετων γαρ ην πλειονων τεσσαρακοντα ο ανθρωπος εφ ον εγεγονει το σημειον τουτο της ιασεως');
INSERT INTO `bible_verses_tr` VALUES ('3901', '44', '4', '23', 'απολυθεντες δε ηλθον προς τους ιδιους και απηγγειλαν οσα προς αυτους οι αρχιερεις και οι πρεσβυτεροι ειπον');
INSERT INTO `bible_verses_tr` VALUES ('3902', '44', '4', '24', 'οι δε ακουσαντες ομοθυμαδον ηραν φωνην προς τον θεον και ειπον δεσποτα συ ο θεος ο ποιησας τον ουρανον και την γην και την θαλασσαν και παντα τα εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('3903', '44', '4', '25', 'ο δια στοματος δαβιδ του παιδος σου ειπων ινα τι εφρυαξαν εθνη και λαοι εμελετησαν κενα');
INSERT INTO `bible_verses_tr` VALUES ('3904', '44', '4', '26', 'παρεστησαν οι βασιλεις της γης και οι αρχοντες συνηχθησαν επι το αυτο κατα του κυριου και κατα του χριστου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3905', '44', '4', '27', 'συνηχθησαν γαρ επ αληθειας επι τον αγιον παιδα σου ιησουν ον εχρισας ηρωδης τε και ποντιος πιλατος συν εθνεσιν και λαοις ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('3906', '44', '4', '28', 'ποιησαι οσα η χειρ σου και η βουλη σου προωρισεν γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('3907', '44', '4', '29', 'και τα νυν κυριε επιδε επι τας απειλας αυτων και δος τοις δουλοις σου μετα παρρησιας πασης λαλειν τον λογον σου');
INSERT INTO `bible_verses_tr` VALUES ('3908', '44', '4', '30', 'εν τω την χειρα σου εκτεινειν σε εις ιασιν και σημεια και τερατα γινεσθαι δια του ονοματος του αγιου παιδος σου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('3909', '44', '4', '31', 'και δεηθεντων αυτων εσαλευθη ο τοπος εν ω ησαν συνηγμενοι και επλησθησαν απαντες πνευματος αγιου και ελαλουν τον λογον του θεου μετα παρρησιας');
INSERT INTO `bible_verses_tr` VALUES ('3910', '44', '4', '32', 'του δε πληθους των πιστευσαντων ην η καρδια και η ψυχη μια και  {VAR1: ουδε } {VAR2: ουδ } εις τι των υπαρχοντων αυτω ελεγεν ιδιον ειναι αλλ ην αυτοις απαντα κοινα');
INSERT INTO `bible_verses_tr` VALUES ('3911', '44', '4', '33', 'και μεγαλη δυναμει απεδιδουν το μαρτυριον οι αποστολοι της αναστασεως του κυριου ιησου χαρις τε μεγαλη ην επι παντας αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3912', '44', '4', '34', 'ουδε γαρ ενδεης τις υπηρχεν εν αυτοις οσοι γαρ κτητορες χωριων η οικιων υπηρχον πωλουντες εφερον τας τιμας των πιπρασκομενων');
INSERT INTO `bible_verses_tr` VALUES ('3913', '44', '4', '35', 'και ετιθουν παρα τους ποδας των αποστολων διεδιδοτο δε εκαστω καθοτι αν τις χρειαν ειχεν');
INSERT INTO `bible_verses_tr` VALUES ('3914', '44', '4', '36', 'ιωσης δε ο επικληθεις βαρναβας υπο των αποστολων ο εστιν μεθερμηνευομενον υιος παρακλησεως λευιτης κυπριος τω γενει');
INSERT INTO `bible_verses_tr` VALUES ('3915', '44', '4', '37', 'υπαρχοντος αυτω αγρου πωλησας ηνεγκεν το χρημα και εθηκεν παρα τους ποδας των αποστολων');
INSERT INTO `bible_verses_tr` VALUES ('3916', '44', '5', '1', 'ανηρ δε τις ανανιας ονοματι συν σαπφειρη τη γυναικι αυτου επωλησεν κτημα');
INSERT INTO `bible_verses_tr` VALUES ('3917', '44', '5', '2', 'και ενοσφισατο απο της τιμης συνειδυιας και της γυναικος αυτου και ενεγκας μερος τι παρα τους ποδας των αποστολων εθηκεν');
INSERT INTO `bible_verses_tr` VALUES ('3918', '44', '5', '3', 'ειπεν δε πετρος ανανια δια τι επληρωσεν ο σατανας την καρδιαν σου ψευσασθαι σε το πνευμα το αγιον και νοσφισασθαι απο της τιμης του χωριου');
INSERT INTO `bible_verses_tr` VALUES ('3919', '44', '5', '4', 'ουχι μενον σοι εμενεν και πραθεν εν τη ση εξουσια υπηρχεν τι οτι εθου εν τη καρδια σου το πραγμα τουτο ουκ εψευσω ανθρωποις αλλα τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('3920', '44', '5', '5', 'ακουων δε ανανιας τους λογους τουτους πεσων εξεψυξεν και εγενετο φοβος μεγας επι παντας τους ακουοντας ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('3921', '44', '5', '6', 'ανασταντες δε οι νεωτεροι συνεστειλαν αυτον και εξενεγκαντες εθαψαν');
INSERT INTO `bible_verses_tr` VALUES ('3922', '44', '5', '7', 'εγενετο δε ως ωρων τριων διαστημα και η γυνη αυτου μη ειδυια το γεγονος εισηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('3923', '44', '5', '8', 'απεκριθη δε αυτη ο πετρος ειπε μοι ει τοσουτου το χωριον απεδοσθε η δε ειπεν ναι τοσουτου');
INSERT INTO `bible_verses_tr` VALUES ('3924', '44', '5', '9', 'ο δε πετρος ειπεν προς αυτην τι οτι συνεφωνηθη υμιν πειρασαι το πνευμα κυριου ιδου οι ποδες των θαψαντων τον ανδρα σου επι τη θυρα και εξοισουσιν σε');
INSERT INTO `bible_verses_tr` VALUES ('3925', '44', '5', '10', 'επεσεν δε παραχρημα παρα τους ποδας αυτου και εξεψυξεν εισελθοντες δε οι νεανισκοι ευρον αυτην νεκραν και εξενεγκαντες εθαψαν προς τον ανδρα αυτης');
INSERT INTO `bible_verses_tr` VALUES ('3926', '44', '5', '11', 'και εγενετο φοβος μεγας εφ ολην την εκκλησιαν και επι παντας τους ακουοντας ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('3927', '44', '5', '12', 'δια δε των χειρων των αποστολων  {VAR1: εγενετο } {VAR2: εγινετο } σημεια και τερατα εν τω λαω πολλα και ησαν ομοθυμαδον απαντες εν τη στοα σολομωντος');
INSERT INTO `bible_verses_tr` VALUES ('3928', '44', '5', '13', 'των δε λοιπων ουδεις ετολμα κολλασθαι αυτοις αλλ εμεγαλυνεν αυτους ο λαος');
INSERT INTO `bible_verses_tr` VALUES ('3929', '44', '5', '14', 'μαλλον δε προσετιθεντο πιστευοντες τω κυριω πληθη ανδρων τε και γυναικων');
INSERT INTO `bible_verses_tr` VALUES ('3930', '44', '5', '15', 'ωστε κατα τας πλατειας εκφερειν τους ασθενεις και τιθεναι επι κλινων και κραββατων ινα ερχομενου πετρου καν η σκια επισκιαση τινι αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3931', '44', '5', '16', 'συνηρχετο δε και το πληθος των περιξ πολεων εις ιερουσαλημ φεροντες ασθενεις και οχλουμενους υπο πνευματων ακαθαρτων οιτινες εθεραπευοντο απαντες');
INSERT INTO `bible_verses_tr` VALUES ('3932', '44', '5', '17', 'αναστας δε ο αρχιερευς και παντες οι συν αυτω η ουσα αιρεσις των σαδδουκαιων επλησθησαν ζηλου');
INSERT INTO `bible_verses_tr` VALUES ('3933', '44', '5', '18', 'και επεβαλον τας χειρας αυτων επι τους αποστολους και εθεντο αυτους εν τηρησει δημοσια');
INSERT INTO `bible_verses_tr` VALUES ('3934', '44', '5', '19', 'αγγελος δε κυριου δια της νυκτος ηνοιξεν τας θυρας της φυλακης εξαγαγων τε αυτους ειπεν');
INSERT INTO `bible_verses_tr` VALUES ('3935', '44', '5', '20', 'πορευεσθε και σταθεντες λαλειτε εν τω ιερω τω λαω παντα τα ρηματα της ζωης ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('3936', '44', '5', '21', 'ακουσαντες δε εισηλθον υπο τον ορθρον εις το ιερον και εδιδασκον παραγενομενος δε ο αρχιερευς και οι συν αυτω συνεκαλεσαν το συνεδριον και πασαν την γερουσιαν των υιων ισραηλ και απεστειλαν εις το δεσμωτηριον αχθηναι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3937', '44', '5', '22', 'οι δε υπηρεται παραγενομενοι ουχ ευρον αυτους εν τη φυλακη αναστρεψαντες δε απηγγειλαν');
INSERT INTO `bible_verses_tr` VALUES ('3938', '44', '5', '23', 'λεγοντες οτι το μεν δεσμωτηριον ευρομεν κεκλεισμενον εν παση ασφαλεια και τους φυλακας εξω εστωτας προ των θυρων ανοιξαντες δε εσω ουδενα ευρομεν');
INSERT INTO `bible_verses_tr` VALUES ('3939', '44', '5', '24', 'ως δε ηκουσαν τους λογους τουτους ο τε ιερευς και ο στρατηγος του ιερου και οι αρχιερεις διηπορουν περι αυτων τι αν γενοιτο τουτο');
INSERT INTO `bible_verses_tr` VALUES ('3940', '44', '5', '25', 'παραγενομενος δε τις απηγγειλεν αυτοις λεγων οτι ιδου οι ανδρες ους εθεσθε εν τη φυλακη εισιν εν τω ιερω εστωτες και διδασκοντες τον λαον');
INSERT INTO `bible_verses_tr` VALUES ('3941', '44', '5', '26', 'τοτε απελθων ο στρατηγος συν τοις υπηρεταις ηγαγεν αυτους ου μετα βιας εφοβουντο γαρ τον λαον ινα μη λιθασθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('3942', '44', '5', '27', 'αγαγοντες δε αυτους εστησαν εν τω συνεδριω και επηρωτησεν αυτους ο αρχιερευς');
INSERT INTO `bible_verses_tr` VALUES ('3943', '44', '5', '28', 'λεγων ου παραγγελια παρηγγειλαμεν υμιν μη διδασκειν επι τω ονοματι τουτω και ιδου πεπληρωκατε την ιερουσαλημ της διδαχης υμων και βουλεσθε επαγαγειν εφ ημας το αιμα του ανθρωπου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('3944', '44', '5', '29', 'αποκριθεις δε ο πετρος και οι αποστολοι ειπον πειθαρχειν δει θεω μαλλον η ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('3945', '44', '5', '30', 'ο θεος των πατερων ημων ηγειρεν ιησουν ον υμεις διεχειρισασθε κρεμασαντες επι ξυλου');
INSERT INTO `bible_verses_tr` VALUES ('3946', '44', '5', '31', 'τουτον ο θεος αρχηγον και σωτηρα υψωσεν τη δεξια αυτου δουναι μετανοιαν τω ισραηλ και αφεσιν αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('3947', '44', '5', '32', 'και ημεις εσμεν αυτου μαρτυρες των ρηματων τουτων και το πνευμα δε το αγιον ο εδωκεν ο θεος τοις πειθαρχουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('3948', '44', '5', '33', 'οι δε ακουσαντες διεπριοντο και εβουλευοντο ανελειν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3949', '44', '5', '34', 'αναστας δε τις εν τω συνεδριω φαρισαιος ονοματι γαμαλιηλ νομοδιδασκαλος τιμιος παντι τω λαω εκελευσεν εξω βραχυ τι τους αποστολους ποιησαι');
INSERT INTO `bible_verses_tr` VALUES ('3950', '44', '5', '35', 'ειπεν τε προς αυτους ανδρες ισραηλιται προσεχετε εαυτοις επι τοις ανθρωποις τουτοις τι μελλετε πρασσειν');
INSERT INTO `bible_verses_tr` VALUES ('3951', '44', '5', '36', 'προ γαρ τουτων των ημερων ανεστη θευδας λεγων ειναι τινα εαυτον ω προσεκολληθη αριθμος ανδρων ωσει τετρακοσιων ος ανηρεθη και παντες οσοι επειθοντο αυτω διελυθησαν και εγενοντο εις ουδεν');
INSERT INTO `bible_verses_tr` VALUES ('3952', '44', '5', '37', 'μετα τουτον ανεστη ιουδας ο γαλιλαιος εν ταις ημεραις της απογραφης και απεστησεν λαον ικανον οπισω αυτου κακεινος απωλετο και παντες οσοι επειθοντο αυτω διεσκορπισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('3953', '44', '5', '38', 'και τα νυν λεγω υμιν αποστητε απο των ανθρωπων τουτων και εασατε αυτους οτι εαν η εξ ανθρωπων η βουλη αυτη η το εργον τουτο καταλυθησεται');
INSERT INTO `bible_verses_tr` VALUES ('3954', '44', '5', '39', 'ει δε εκ θεου εστιν ου δυνασθε καταλυσαι αυτο μηποτε και θεομαχοι ευρεθητε');
INSERT INTO `bible_verses_tr` VALUES ('3955', '44', '5', '40', 'επεισθησαν δε αυτω και προσκαλεσαμενοι τους αποστολους δειραντες παρηγγειλαν μη λαλειν επι τω ονοματι του ιησου και απελυσαν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('3956', '44', '5', '41', 'οι μεν ουν επορευοντο χαιροντες απο προσωπου του συνεδριου οτι υπερ του ονοματος αυτου κατηξιωθησαν ατιμασθηναι');
INSERT INTO `bible_verses_tr` VALUES ('3957', '44', '5', '42', 'πασαν τε ημεραν εν τω ιερω και κατ οικον ουκ επαυοντο διδασκοντες και ευαγγελιζομενοι ιησουν τον χριστον');
INSERT INTO `bible_verses_tr` VALUES ('3958', '44', '6', '1', 'εν δε ταις ημεραις ταυταις πληθυνοντων των μαθητων εγενετο γογγυσμος των ελληνιστων προς τους εβραιους οτι παρεθεωρουντο εν τη διακονια τη καθημερινη αι χηραι αυτων');
INSERT INTO `bible_verses_tr` VALUES ('3959', '44', '6', '2', 'προσκαλεσαμενοι δε οι δωδεκα το πληθος των μαθητων ειπον ουκ αρεστον εστιν ημας καταλειψαντας τον λογον του θεου διακονειν τραπεζαις');
INSERT INTO `bible_verses_tr` VALUES ('3960', '44', '6', '3', 'επισκεψασθε ουν αδελφοι ανδρας εξ υμων μαρτυρουμενους επτα πληρεις πνευματος αγιου και σοφιας ους καταστησομεν επι της χρειας ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('3961', '44', '6', '4', 'ημεις δε τη προσευχη και τη διακονια του λογου προσκαρτερησομεν');
INSERT INTO `bible_verses_tr` VALUES ('3962', '44', '6', '5', 'και ηρεσεν ο λογος ενωπιον παντος του πληθους και εξελεξαντο στεφανον ανδρα πληρη πιστεως και πνευματος αγιου και φιλιππον και προχορον και νικανορα και τιμωνα και παρμεναν και νικολαον προσηλυτον αντιοχεα');
INSERT INTO `bible_verses_tr` VALUES ('3963', '44', '6', '6', 'ους εστησαν ενωπιον των αποστολων και προσευξαμενοι επεθηκαν αυτοις τας χειρας');
INSERT INTO `bible_verses_tr` VALUES ('3964', '44', '6', '7', 'και ο λογος του θεου ηυξανεν και επληθυνετο ο αριθμος των μαθητων εν ιερουσαλημ σφοδρα πολυς τε οχλος των ιερεων υπηκουον τη πιστει');
INSERT INTO `bible_verses_tr` VALUES ('3965', '44', '6', '8', 'στεφανος δε πληρης πιστεως και δυναμεως εποιει τερατα και σημεια μεγαλα εν τω λαω');
INSERT INTO `bible_verses_tr` VALUES ('3966', '44', '6', '9', 'ανεστησαν δε τινες των εκ της συναγωγης της λεγομενης λιβερτινων και κυρηναιων και αλεξανδρεων και των απο κιλικιας και ασιας συζητουντες τω στεφανω');
INSERT INTO `bible_verses_tr` VALUES ('3967', '44', '6', '10', 'και ουκ ισχυον αντιστηναι τη σοφια και τω πνευματι ω ελαλει');
INSERT INTO `bible_verses_tr` VALUES ('3968', '44', '6', '11', 'τοτε υπεβαλον ανδρας λεγοντας οτι ακηκοαμεν αυτου λαλουντος ρηματα βλασφημα εις μωσην και τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('3969', '44', '6', '12', 'συνεκινησαν τε τον λαον και τους πρεσβυτερους και τους γραμματεις και επισταντες συνηρπασαν αυτον και ηγαγον εις το συνεδριον');
INSERT INTO `bible_verses_tr` VALUES ('3970', '44', '6', '13', 'εστησαν τε μαρτυρας ψευδεις λεγοντας ο ανθρωπος ουτος ου παυεται ρηματα βλασφημα λαλων κατα του τοπου του αγιου τουτου και του νομου');
INSERT INTO `bible_verses_tr` VALUES ('3971', '44', '6', '14', 'ακηκοαμεν γαρ αυτου λεγοντος οτι ιησους ο ναζωραιος ουτος καταλυσει τον τοπον τουτον και αλλαξει τα εθη α παρεδωκεν ημιν μωυσης');
INSERT INTO `bible_verses_tr` VALUES ('3972', '44', '6', '15', 'και ατενισαντες εις αυτον απαντες οι καθεζομενοι εν τω συνεδριω ειδον το προσωπον αυτου ωσει προσωπον αγγελου');
INSERT INTO `bible_verses_tr` VALUES ('3973', '44', '7', '1', 'ειπεν δε ο αρχιερευς ει αρα ταυτα ουτως εχει');
INSERT INTO `bible_verses_tr` VALUES ('3974', '44', '7', '2', 'ο δε εφη ανδρες αδελφοι και πατερες ακουσατε ο θεος της δοξης ωφθη τω πατρι ημων αβρααμ οντι εν τη μεσοποταμια πριν η κατοικησαι αυτον εν χαρραν');
INSERT INTO `bible_verses_tr` VALUES ('3975', '44', '7', '3', 'και ειπεν προς αυτον εξελθε εκ της γης σου και εκ της συγγενειας σου και δευρο εις γην ην αν σοι δειξω');
INSERT INTO `bible_verses_tr` VALUES ('3976', '44', '7', '4', 'τοτε εξελθων εκ γης χαλδαιων κατωκησεν εν χαρραν κακειθεν μετα το αποθανειν τον πατερα αυτου μετωκισεν αυτον εις την γην ταυτην εις ην υμεις νυν κατοικειτε');
INSERT INTO `bible_verses_tr` VALUES ('3977', '44', '7', '5', 'και ουκ εδωκεν αυτω κληρονομιαν εν αυτη ουδε βημα ποδος και επηγγειλατο αυτω δουναι εις κατασχεσιν αυτην και τω σπερματι αυτου μετ αυτον ουκ οντος αυτω τεκνου');
INSERT INTO `bible_verses_tr` VALUES ('3978', '44', '7', '6', 'ελαλησεν δε ουτως ο θεος οτι εσται το σπερμα αυτου παροικον εν γη αλλοτρια και δουλωσουσιν αυτο και κακωσουσιν ετη τετρακοσια');
INSERT INTO `bible_verses_tr` VALUES ('3979', '44', '7', '7', 'και το εθνος ω εαν δουλευσωσιν κρινω εγω ειπεν ο θεος και μετα ταυτα εξελευσονται και λατρευσουσιν μοι εν τω τοπω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('3980', '44', '7', '8', 'και εδωκεν αυτω διαθηκην περιτομης και ουτως εγεννησεν τον ισαακ και περιετεμεν αυτον τη ημερα τη ογδοη και ο ισαακ τον ιακωβ και ο ιακωβ τους δωδεκα πατριαρχας');
INSERT INTO `bible_verses_tr` VALUES ('3981', '44', '7', '9', 'και οι πατριαρχαι ζηλωσαντες τον ιωσηφ απεδοντο εις αιγυπτον και ην ο θεος μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3982', '44', '7', '10', 'και εξειλετο αυτον εκ πασων των θλιψεων αυτου και εδωκεν αυτω χαριν και σοφιαν εναντιον φαραω βασιλεως αιγυπτου και κατεστησεν αυτον ηγουμενον επ αιγυπτον και ολον τον οικον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3983', '44', '7', '11', 'ηλθεν δε λιμος εφ ολην την γην αιγυπτου και χανααν και θλιψις μεγαλη και ουχ ευρισκον χορτασματα οι πατερες ημων');
INSERT INTO `bible_verses_tr` VALUES ('3984', '44', '7', '12', 'ακουσας δε ιακωβ οντα σιτα εν αιγυπτω εξαπεστειλεν τους πατερας ημων πρωτον');
INSERT INTO `bible_verses_tr` VALUES ('3985', '44', '7', '13', 'και εν τω δευτερω ανεγνωρισθη ιωσηφ τοις αδελφοις αυτου και φανερον εγενετο τω φαραω το γενος του ιωσηφ');
INSERT INTO `bible_verses_tr` VALUES ('3986', '44', '7', '14', 'αποστειλας δε ιωσηφ μετεκαλεσατο τον πατερα αυτου ιακωβ και πασαν την συγγενειαν αυτου εν ψυχαις εβδομηκοντα πεντε');
INSERT INTO `bible_verses_tr` VALUES ('3987', '44', '7', '15', 'κατεβη δε ιακωβ εις αιγυπτον και ετελευτησεν αυτος και οι πατερες ημων');
INSERT INTO `bible_verses_tr` VALUES ('3988', '44', '7', '16', 'και μετετεθησαν εις  {VAR1: συχεμ } {VAR2: σιχεμ } και ετεθησαν εν τω μνηματι ο ωνησατο αβρααμ τιμης αργυριου παρα των υιων  {VAR1: εμμορ  {VAR1: εμορ } {VAR2: του } συχεμ } {VAR2: σιχεμ }');
INSERT INTO `bible_verses_tr` VALUES ('3989', '44', '7', '17', 'καθως δε ηγγιζεν ο χρονος της επαγγελιας ης ωμοσεν ο θεος τω αβρααμ ηυξησεν ο λαος και επληθυνθη εν αιγυπτω');
INSERT INTO `bible_verses_tr` VALUES ('3990', '44', '7', '18', 'αχρις ου ανεστη βασιλευς ετερος ος ουκ ηδει τον ιωσηφ');
INSERT INTO `bible_verses_tr` VALUES ('3991', '44', '7', '19', 'ουτος κατασοφισαμενος το γενος ημων εκακωσεν τους πατερας ημων του ποιειν εκθετα τα βρεφη αυτων εις το μη ζωογονεισθαι');
INSERT INTO `bible_verses_tr` VALUES ('3992', '44', '7', '20', 'εν ω καιρω εγεννηθη μωσης και ην αστειος τω θεω ος ανετραφη μηνας τρεις εν τω οικω του πατρος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('3993', '44', '7', '21', 'εκτεθεντα δε αυτον ανειλετο αυτον η θυγατηρ φαραω και ανεθρεψατο αυτον εαυτη εις υιον');
INSERT INTO `bible_verses_tr` VALUES ('3994', '44', '7', '22', 'και επαιδευθη μωσης παση σοφια αιγυπτιων ην δε δυνατος εν λογοις και εν εργοις');
INSERT INTO `bible_verses_tr` VALUES ('3995', '44', '7', '23', 'ως δε επληρουτο αυτω τεσσαρακονταετης χρονος ανεβη επι την καρδιαν αυτου επισκεψασθαι τους αδελφους αυτου τους υιους ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('3996', '44', '7', '24', 'και ιδων τινα αδικουμενον ημυνατο και εποιησεν εκδικησιν τω καταπονουμενω παταξας τον αιγυπτιον');
INSERT INTO `bible_verses_tr` VALUES ('3997', '44', '7', '25', 'ενομιζεν δε συνιεναι τους αδελφους αυτου οτι ο θεος δια χειρος αυτου διδωσιν αυτοις σωτηριαν οι δε ου συνηκαν');
INSERT INTO `bible_verses_tr` VALUES ('3998', '44', '7', '26', 'τη  {VAR1: τε } {VAR2: δε } επιουση ημερα ωφθη αυτοις μαχομενοις και συνηλασεν αυτους εις ειρηνην ειπων ανδρες αδελφοι εστε υμεις ινα τι αδικειτε αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('3999', '44', '7', '27', 'ο δε αδικων τον πλησιον απωσατο αυτον ειπων τις σε κατεστησεν αρχοντα και δικαστην εφ ημας');
INSERT INTO `bible_verses_tr` VALUES ('4000', '44', '7', '28', 'μη ανελειν με συ θελεις ον τροπον ανειλες χθες τον αιγυπτιον');
INSERT INTO `bible_verses_tr` VALUES ('4001', '44', '7', '29', 'εφυγεν δε μωσης εν τω λογω τουτω και εγενετο παροικος εν γη μαδιαμ ου εγεννησεν υιους δυο');
INSERT INTO `bible_verses_tr` VALUES ('4002', '44', '7', '30', 'και πληρωθεντων ετων τεσσαρακοντα ωφθη αυτω εν τη ερημω του ορους σινα αγγελος κυριου εν φλογι πυρος βατου');
INSERT INTO `bible_verses_tr` VALUES ('4003', '44', '7', '31', 'ο δε μωσης ιδων εθαυμασεν το οραμα προσερχομενου δε αυτου κατανοησαι εγενετο φωνη κυριου προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4004', '44', '7', '32', 'εγω ο θεος των πατερων σου ο θεος αβρααμ και ο θεος ισαακ και ο θεος ιακωβ εντρομος δε γενομενος μωσης ουκ ετολμα κατανοησαι');
INSERT INTO `bible_verses_tr` VALUES ('4005', '44', '7', '33', 'ειπεν δε αυτω ο κυριος λυσον το υποδημα των ποδων σου ο γαρ τοπος εν ω εστηκας γη αγια εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4006', '44', '7', '34', 'ιδων ειδον την κακωσιν του λαου μου του εν αιγυπτω και του στεναγμου αυτων ηκουσα και κατεβην εξελεσθαι αυτους και νυν δευρο αποστελω σε εις αιγυπτον');
INSERT INTO `bible_verses_tr` VALUES ('4007', '44', '7', '35', 'τουτον τον μωυσην ον ηρνησαντο ειποντες τις σε κατεστησεν αρχοντα και δικαστην τουτον ο θεος αρχοντα και λυτρωτην απεστειλεν εν χειρι αγγελου του οφθεντος αυτω εν τη βατω');
INSERT INTO `bible_verses_tr` VALUES ('4008', '44', '7', '36', 'ουτος εξηγαγεν αυτους ποιησας τερατα και σημεια εν γη αιγυπτου και εν ερυθρα θαλασση και εν τη ερημω ετη τεσσαρακοντα');
INSERT INTO `bible_verses_tr` VALUES ('4009', '44', '7', '37', 'ουτος εστιν ο μωυσης ο ειπων τοις υιοις ισραηλ προφητην υμιν αναστησει κυριος ο θεος υμων εκ των αδελφων υμων ως εμε αυτου ακουσεσθε');
INSERT INTO `bible_verses_tr` VALUES ('4010', '44', '7', '38', 'ουτος εστιν ο γενομενος εν τη εκκλησια εν τη ερημω μετα του αγγελου του λαλουντος αυτω εν τω ορει σινα και των πατερων ημων ος εδεξατο λογια ζωντα δουναι ημιν');
INSERT INTO `bible_verses_tr` VALUES ('4011', '44', '7', '39', 'ω ουκ ηθελησαν υπηκοοι γενεσθαι οι πατερες ημων αλλ απωσαντο και εστραφησαν ταις καρδιαις αυτων εις αιγυπτον');
INSERT INTO `bible_verses_tr` VALUES ('4012', '44', '7', '40', 'ειποντες τω ααρων ποιησον ημιν θεους οι προπορευσονται ημων ο γαρ μωσης ουτος ος εξηγαγεν ημας εκ γης αιγυπτου ουκ οιδαμεν τι γεγονεν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4013', '44', '7', '41', 'και εμοσχοποιησαν εν ταις ημεραις εκειναις και ανηγαγον θυσιαν τω ειδωλω και ευφραινοντο εν τοις εργοις των χειρων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4014', '44', '7', '42', 'εστρεψεν δε ο θεος και παρεδωκεν αυτους λατρευειν τη στρατια του ουρανου καθως γεγραπται εν βιβλω των προφητων μη σφαγια και θυσιας προσηνεγκατε μοι ετη τεσσαρακοντα εν τη ερημω οικος ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('4015', '44', '7', '43', 'και ανελαβετε την σκηνην του μολοχ και το αστρον του θεου υμων ρεμφαν τους τυπους ους εποιησατε προσκυνειν αυτοις και μετοικιω υμας επεκεινα βαβυλωνος');
INSERT INTO `bible_verses_tr` VALUES ('4016', '44', '7', '44', 'η σκηνη του μαρτυριου ην  {VAR1: εν } τοις πατρασιν ημων εν τη ερημω καθως διεταξατο ο λαλων τω μωση ποιησαι αυτην κατα τον τυπον ον εωρακει');
INSERT INTO `bible_verses_tr` VALUES ('4017', '44', '7', '45', 'ην και εισηγαγον διαδεξαμενοι οι πατερες ημων μετα ιησου εν τη κατασχεσει των εθνων ων εξωσεν ο θεος απο προσωπου των πατερων ημων εως των ημερων δαβιδ');
INSERT INTO `bible_verses_tr` VALUES ('4018', '44', '7', '46', 'ος ευρεν χαριν ενωπιον του θεου και ητησατο ευρειν σκηνωμα τω θεω ιακωβ');
INSERT INTO `bible_verses_tr` VALUES ('4019', '44', '7', '47', 'σολομων δε ωκοδομησεν αυτω οικον');
INSERT INTO `bible_verses_tr` VALUES ('4020', '44', '7', '48', 'αλλ ουχ ο υψιστος εν χειροποιητοις ναοις κατοικει καθως ο προφητης λεγει');
INSERT INTO `bible_verses_tr` VALUES ('4021', '44', '7', '49', 'ο ουρανος μοι θρονος η δε γη υποποδιον των ποδων μου ποιον οικον οικοδομησετε μοι λεγει κυριος η τις τοπος της καταπαυσεως μου');
INSERT INTO `bible_verses_tr` VALUES ('4022', '44', '7', '50', 'ουχι η χειρ μου εποιησεν ταυτα παντα');
INSERT INTO `bible_verses_tr` VALUES ('4023', '44', '7', '51', 'σκληροτραχηλοι και απεριτμητοι τη καρδια και τοις ωσιν υμεις αει τω πνευματι τω αγιω αντιπιπτετε ως οι πατερες υμων και υμεις');
INSERT INTO `bible_verses_tr` VALUES ('4024', '44', '7', '52', 'τινα των προφητων ουκ εδιωξαν οι πατερες υμων και απεκτειναν τους προκαταγγειλαντας περι της ελευσεως του δικαιου ου νυν υμεις προδοται και φονεις γεγενησθε');
INSERT INTO `bible_verses_tr` VALUES ('4025', '44', '7', '53', 'οιτινες ελαβετε τον νομον εις διαταγας αγγελων και ουκ εφυλαξατε');
INSERT INTO `bible_verses_tr` VALUES ('4026', '44', '7', '54', 'ακουοντες δε ταυτα διεπριοντο ταις καρδιαις αυτων και εβρυχον τους οδοντας επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4027', '44', '7', '55', 'υπαρχων δε πληρης πνευματος αγιου ατενισας εις τον ουρανον ειδεν δοξαν θεου και ιησουν εστωτα εκ δεξιων του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4028', '44', '7', '56', 'και ειπεν ιδου θεωρω τους ουρανους ανεωγμενους και τον υιον του ανθρωπου εκ δεξιων εστωτα του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4029', '44', '7', '57', 'κραξαντες δε φωνη μεγαλη συνεσχον τα ωτα αυτων και ωρμησαν ομοθυμαδον επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4030', '44', '7', '58', 'και εκβαλοντες εξω της πολεως ελιθοβολουν και οι μαρτυρες απεθεντο τα ιματια αυτων παρα τους ποδας νεανιου καλουμενου σαυλου');
INSERT INTO `bible_verses_tr` VALUES ('4031', '44', '7', '59', 'και ελιθοβολουν τον στεφανον επικαλουμενον και λεγοντα κυριε ιησου δεξαι το πνευμα μου');
INSERT INTO `bible_verses_tr` VALUES ('4032', '44', '7', '60', 'θεις δε τα γονατα εκραξεν φωνη μεγαλη κυριε μη στησης αυτοις την αμαρτιαν ταυτην και τουτο ειπων εκοιμηθη');
INSERT INTO `bible_verses_tr` VALUES ('4033', '44', '8', '1', 'σαυλος δε ην συνευδοκων τη αναιρεσει αυτου εγενετο δε εν εκεινη τη ημερα διωγμος μεγας επι την εκκλησιαν την εν ιεροσολυμοις παντες τε διεσπαρησαν κατα τας χωρας της ιουδαιας και σαμαρειας πλην των αποστολων');
INSERT INTO `bible_verses_tr` VALUES ('4034', '44', '8', '2', 'συνεκομισαν δε τον στεφανον ανδρες ευλαβεις και εποιησαντο κοπετον μεγαν επ αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4035', '44', '8', '3', 'σαυλος δε ελυμαινετο την εκκλησιαν κατα τους οικους εισπορευομενος συρων τε ανδρας και γυναικας παρεδιδου εις φυλακην');
INSERT INTO `bible_verses_tr` VALUES ('4036', '44', '8', '4', 'οι μεν ουν διασπαρεντες διηλθον ευαγγελιζομενοι τον λογον');
INSERT INTO `bible_verses_tr` VALUES ('4037', '44', '8', '5', 'φιλιππος δε κατελθων εις πολιν της σαμαρειας εκηρυσσεν αυτοις τον χριστον');
INSERT INTO `bible_verses_tr` VALUES ('4038', '44', '8', '6', 'προσειχον τε οι οχλοι τοις λεγομενοις υπο του φιλιππου ομοθυμαδον εν τω ακουειν αυτους και βλεπειν τα σημεια α εποιει');
INSERT INTO `bible_verses_tr` VALUES ('4039', '44', '8', '7', 'πολλων γαρ των εχοντων πνευματα ακαθαρτα βοωντα μεγαλη φωνη εξηρχετο πολλοι δε παραλελυμενοι και χωλοι εθεραπευθησαν');
INSERT INTO `bible_verses_tr` VALUES ('4040', '44', '8', '8', 'και εγενετο χαρα μεγαλη εν τη πολει εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('4041', '44', '8', '9', 'ανηρ δε τις ονοματι σιμων προυπηρχεν εν τη πολει μαγευων και εξιστων το εθνος της σαμαρειας λεγων ειναι τινα εαυτον μεγαν');
INSERT INTO `bible_verses_tr` VALUES ('4042', '44', '8', '10', 'ω προσειχον παντες απο μικρου εως μεγαλου λεγοντες ουτος εστιν η δυναμις του θεου η μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('4043', '44', '8', '11', 'προσειχον δε αυτω δια το ικανω χρονω ταις μαγειαις εξεστακεναι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4044', '44', '8', '12', 'οτε δε επιστευσαν τω φιλιππω ευαγγελιζομενω τα περι της βασιλειας του θεου και του ονοματος του ιησου χριστου εβαπτιζοντο ανδρες τε και γυναικες');
INSERT INTO `bible_verses_tr` VALUES ('4045', '44', '8', '13', 'ο δε σιμων και αυτος επιστευσεν και βαπτισθεις ην προσκαρτερων τω φιλιππω θεωρων τε  {VAR1: σημεια και δυναμεις μεγαλας γινομενας } {VAR2: δυναμεις και σημεια γινομενα } εξιστατο');
INSERT INTO `bible_verses_tr` VALUES ('4046', '44', '8', '14', 'ακουσαντες δε οι εν ιεροσολυμοις αποστολοι οτι δεδεκται η σαμαρεια τον λογον του θεου απεστειλαν προς αυτους τον πετρον και ιωαννην');
INSERT INTO `bible_verses_tr` VALUES ('4047', '44', '8', '15', 'οιτινες καταβαντες προσηυξαντο περι αυτων οπως λαβωσιν πνευμα αγιον');
INSERT INTO `bible_verses_tr` VALUES ('4048', '44', '8', '16', 'ουπω γαρ ην επ ουδενι αυτων επιπεπτωκος μονον δε βεβαπτισμενοι υπηρχον εις το ονομα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('4049', '44', '8', '17', 'τοτε επετιθουν τας χειρας επ αυτους και ελαμβανον πνευμα αγιον');
INSERT INTO `bible_verses_tr` VALUES ('4050', '44', '8', '18', 'θεασαμενος δε ο σιμων οτι δια της επιθεσεως των χειρων των αποστολων διδοται το πνευμα το αγιον προσηνεγκεν αυτοις χρηματα');
INSERT INTO `bible_verses_tr` VALUES ('4051', '44', '8', '19', 'λεγων δοτε καμοι την εξουσιαν ταυτην ινα ω  {VAR1: αν } {VAR2: εαν } επιθω τας χειρας λαμβανη πνευμα αγιον');
INSERT INTO `bible_verses_tr` VALUES ('4052', '44', '8', '20', 'πετρος δε ειπεν προς αυτον το αργυριον σου συν σοι ειη εις απωλειαν οτι την δωρεαν του θεου ενομισας δια χρηματων κτασθαι');
INSERT INTO `bible_verses_tr` VALUES ('4053', '44', '8', '21', 'ουκ εστιν σοι μερις ουδε κληρος εν τω λογω τουτω η γαρ καρδια σου ουκ εστιν ευθεια ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4054', '44', '8', '22', 'μετανοησον ουν απο της κακιας σου ταυτης και δεηθητι του θεου ει αρα αφεθησεται σοι η επινοια της καρδιας σου');
INSERT INTO `bible_verses_tr` VALUES ('4055', '44', '8', '23', 'εις γαρ χολην πικριας και συνδεσμον αδικιας ορω σε οντα');
INSERT INTO `bible_verses_tr` VALUES ('4056', '44', '8', '24', 'αποκριθεις δε ο σιμων ειπεν δεηθητε υμεις υπερ εμου προς τον κυριον οπως μηδεν επελθη επ εμε ων ειρηκατε');
INSERT INTO `bible_verses_tr` VALUES ('4057', '44', '8', '25', 'οι μεν ουν διαμαρτυραμενοι και λαλησαντες τον λογον του κυριου υπεστρεψαν εις ιερουσαλημ πολλας τε κωμας των σαμαρειτων ευηγγελισαντο');
INSERT INTO `bible_verses_tr` VALUES ('4058', '44', '8', '26', 'αγγελος δε κυριου ελαλησεν προς φιλιππον λεγων αναστηθι και πορευου κατα μεσημβριαν επι την οδον την καταβαινουσαν απο ιερουσαλημ εις γαζαν αυτη εστιν ερημος');
INSERT INTO `bible_verses_tr` VALUES ('4059', '44', '8', '27', 'και αναστας επορευθη και ιδου ανηρ αιθιοψ ευνουχος δυναστης κανδακης της βασιλισσης αιθιοπων ος ην επι πασης της γαζης αυτης ος εληλυθει προσκυνησων εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4060', '44', '8', '28', 'ην τε υποστρεφων και καθημενος επι του αρματος αυτου  {VAR1: και } ανεγινωσκεν τον προφητην ησαιαν');
INSERT INTO `bible_verses_tr` VALUES ('4061', '44', '8', '29', 'ειπεν δε το πνευμα τω φιλιππω προσελθε και κολληθητι τω αρματι τουτω');
INSERT INTO `bible_verses_tr` VALUES ('4062', '44', '8', '30', 'προσδραμων δε ο φιλιππος ηκουσεν αυτου αναγινωσκοντος τον προφητην ησαιαν και ειπεν αρα γε γινωσκεις α αναγινωσκεις');
INSERT INTO `bible_verses_tr` VALUES ('4063', '44', '8', '31', 'ο δε ειπεν πως γαρ αν δυναιμην εαν μη τις οδηγηση με παρεκαλεσεν τε τον φιλιππον αναβαντα καθισαι συν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4064', '44', '8', '32', 'η δε περιοχη της γραφης ην ανεγινωσκεν ην αυτη ως προβατον επι σφαγην ηχθη και ως αμνος εναντιον του κειροντος αυτον αφωνος ουτως ουκ ανοιγει το στομα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4065', '44', '8', '33', 'εν τη ταπεινωσει αυτου η κρισις αυτου ηρθη την δε γενεαν αυτου τις διηγησεται οτι αιρεται απο της γης η ζωη αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4066', '44', '8', '34', 'αποκριθεις δε ο ευνουχος τω φιλιππω ειπεν δεομαι σου περι τινος ο προφητης λεγει τουτο περι εαυτου η περι ετερου τινος');
INSERT INTO `bible_verses_tr` VALUES ('4067', '44', '8', '35', 'ανοιξας δε ο φιλιππος το στομα αυτου και αρξαμενος απο της γραφης ταυτης ευηγγελισατο αυτω τον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4068', '44', '8', '36', 'ως δε επορευοντο κατα την οδον ηλθον επι τι υδωρ και φησιν ο ευνουχος ιδου υδωρ τι κωλυει με βαπτισθηναι');
INSERT INTO `bible_verses_tr` VALUES ('4069', '44', '8', '37', 'ειπεν δε ο φιλιππος ει πιστευεις εξ ολης της καρδιας εξεστιν αποκριθεις δε ειπεν πιστευω τον υιον του θεου ειναι τον ιησουν χριστον');
INSERT INTO `bible_verses_tr` VALUES ('4070', '44', '8', '38', 'και εκελευσεν στηναι το αρμα και κατεβησαν αμφοτεροι εις το υδωρ ο τε φιλιππος και ο ευνουχος και εβαπτισεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4071', '44', '8', '39', 'οτε δε ανεβησαν εκ του υδατος πνευμα κυριου ηρπασεν τον φιλιππον και ουκ ειδεν αυτον ουκετι ο ευνουχος επορευετο γαρ την οδον αυτου χαιρων');
INSERT INTO `bible_verses_tr` VALUES ('4072', '44', '8', '40', 'φιλιππος δε ευρεθη εις αζωτον και διερχομενος ευηγγελιζετο τας πολεις πασας εως του ελθειν αυτον εις καισαρειαν');
INSERT INTO `bible_verses_tr` VALUES ('4073', '44', '9', '1', 'ο δε σαυλος ετι εμπνεων απειλης και φονου εις τους μαθητας του κυριου προσελθων τω αρχιερει');
INSERT INTO `bible_verses_tr` VALUES ('4074', '44', '9', '2', 'ητησατο παρ αυτου επιστολας εις δαμασκον προς τας συναγωγας οπως εαν τινας ευρη της οδου οντας ανδρας τε και γυναικας δεδεμενους αγαγη εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4075', '44', '9', '3', 'εν δε τω πορευεσθαι εγενετο αυτον εγγιζειν τη δαμασκω και εξαιφνης περιηστραψεν αυτον φως απο του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('4076', '44', '9', '4', 'και πεσων επι την γην ηκουσεν φωνην λεγουσαν αυτω σαουλ σαουλ τι με διωκεις');
INSERT INTO `bible_verses_tr` VALUES ('4077', '44', '9', '5', 'ειπεν δε τις ει κυριε ο δε κυριος ειπεν εγω ειμι ιησους ον συ διωκεις σκληρον σοι προς κεντρα λακτιζειν');
INSERT INTO `bible_verses_tr` VALUES ('4078', '44', '9', '6', 'τρεμων τε και θαμβων ειπεν κυριε τι με θελεις ποιησαι και ο κυριος προς αυτον αναστηθι και εισελθε εις την πολιν και λαληθησεται σοι τι σε δει ποιειν');
INSERT INTO `bible_verses_tr` VALUES ('4079', '44', '9', '7', 'οι δε ανδρες οι συνοδευοντες αυτω ειστηκεισαν εννεοι ακουοντες μεν της φωνης μηδενα δε θεωρουντες');
INSERT INTO `bible_verses_tr` VALUES ('4080', '44', '9', '8', 'ηγερθη δε ο σαυλος απο της γης ανεωγμενων δε των οφθαλμων αυτου ουδενα εβλεπεν χειραγωγουντες δε αυτον εισηγαγον εις δαμασκον');
INSERT INTO `bible_verses_tr` VALUES ('4081', '44', '9', '9', 'και ην ημερας τρεις μη βλεπων και ουκ εφαγεν ουδε επιεν');
INSERT INTO `bible_verses_tr` VALUES ('4082', '44', '9', '10', 'ην δε τις μαθητης εν δαμασκω ονοματι ανανιας και ειπεν προς αυτον ο κυριος εν οραματι ανανια ο δε ειπεν ιδου εγω κυριε');
INSERT INTO `bible_verses_tr` VALUES ('4083', '44', '9', '11', 'ο δε κυριος προς αυτον αναστας πορευθητι επι την ρυμην την καλουμενην ευθειαν και ζητησον εν οικια ιουδα σαυλον ονοματι ταρσεα ιδου γαρ προσευχεται');
INSERT INTO `bible_verses_tr` VALUES ('4084', '44', '9', '12', 'και ειδεν εν οραματι ανδρα ονοματι ανανιαν εισελθοντα και επιθεντα αυτω χειρα οπως αναβλεψη');
INSERT INTO `bible_verses_tr` VALUES ('4085', '44', '9', '13', 'απεκριθη δε ο ανανιας κυριε ακηκοα απο πολλων περι του ανδρος τουτου οσα κακα εποιησεν τοις αγιοις σου εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4086', '44', '9', '14', 'και ωδε εχει εξουσιαν παρα των αρχιερεων δησαι παντας τους επικαλουμενους το ονομα σου');
INSERT INTO `bible_verses_tr` VALUES ('4087', '44', '9', '15', 'ειπεν δε προς αυτον ο κυριος πορευου οτι σκευος εκλογης μοι εστιν ουτος του βαστασαι το ονομα μου ενωπιον εθνων και βασιλεων υιων τε ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('4088', '44', '9', '16', 'εγω γαρ υποδειξω αυτω οσα δει αυτον υπερ του ονοματος μου παθειν');
INSERT INTO `bible_verses_tr` VALUES ('4089', '44', '9', '17', 'απηλθεν δε ανανιας και εισηλθεν εις την οικιαν και επιθεις επ αυτον τας χειρας ειπεν σαουλ αδελφε ο κυριος απεσταλκεν με ιησους ο οφθεις σοι εν τη οδω η ηρχου οπως αναβλεψης και πλησθης πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('4090', '44', '9', '18', 'και ευθεως απεπεσον απο των οφθαλμων αυτου ωσει λεπιδες ανεβλεψεν τε παραχρημα και αναστας εβαπτισθη');
INSERT INTO `bible_verses_tr` VALUES ('4091', '44', '9', '19', 'και λαβων τροφην ενισχυσεν εγενετο δε ο σαυλος μετα των εν δαμασκω μαθητων ημερας τινας');
INSERT INTO `bible_verses_tr` VALUES ('4092', '44', '9', '20', 'και ευθεως εν ταις συναγωγαις εκηρυσσεν τον χριστον οτι ουτος εστιν ο υιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4093', '44', '9', '21', 'εξισταντο δε παντες οι ακουοντες και ελεγον ουχ ουτος εστιν ο πορθησας εν ιερουσαλημ τους επικαλουμενους το ονομα τουτο και ωδε εις τουτο εληλυθει ινα δεδεμενους αυτους αγαγη επι τους αρχιερεις');
INSERT INTO `bible_verses_tr` VALUES ('4094', '44', '9', '22', 'σαυλος δε μαλλον ενεδυναμουτο και συνεχυνεν τους ιουδαιους τους κατοικουντας εν δαμασκω συμβιβαζων οτι ουτος εστιν ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('4095', '44', '9', '23', 'ως δε επληρουντο ημεραι ικαναι συνεβουλευσαντο οι ιουδαιοι ανελειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4096', '44', '9', '24', 'εγνωσθη δε τω σαυλω η επιβουλη αυτων παρετηρουν τε τας πυλας ημερας τε και νυκτος οπως αυτον ανελωσιν');
INSERT INTO `bible_verses_tr` VALUES ('4097', '44', '9', '25', 'λαβοντες δε αυτον οι μαθηται νυκτος καθηκαν δια του τειχους χαλασαντες εν σπυριδι');
INSERT INTO `bible_verses_tr` VALUES ('4098', '44', '9', '26', 'παραγενομενος δε ο σαυλος εις ιερουσαλημ επειρατο κολλασθαι τοις μαθηταις και παντες εφοβουντο αυτον μη πιστευοντες οτι εστιν μαθητης');
INSERT INTO `bible_verses_tr` VALUES ('4099', '44', '9', '27', 'βαρναβας δε επιλαβομενος αυτον ηγαγεν προς τους αποστολους και διηγησατο αυτοις πως εν τη οδω ειδεν τον κυριον και οτι ελαλησεν αυτω και πως εν δαμασκω επαρρησιασατο εν τω ονοματι του ιησου');
INSERT INTO `bible_verses_tr` VALUES ('4100', '44', '9', '28', 'και ην μετ αυτων εισπορευομενος και εκπορευομενος εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4101', '44', '9', '29', 'και παρρησιαζομενος εν τω ονοματι του κυριου ιησου ελαλει τε και συνεζητει προς τους ελληνιστας οι δε επεχειρουν αυτον ανελειν');
INSERT INTO `bible_verses_tr` VALUES ('4102', '44', '9', '30', 'επιγνοντες δε οι αδελφοι κατηγαγον αυτον εις καισαρειαν και εξαπεστειλαν αυτον εις ταρσον');
INSERT INTO `bible_verses_tr` VALUES ('4103', '44', '9', '31', 'αι μεν ουν εκκλησιαι καθ ολης της ιουδαιας και γαλιλαιας και σαμαρειας ειχον ειρηνην οικοδομουμεναι και πορευομεναι τω φοβω του κυριου και τη παρακλησει του αγιου πνευματος επληθυνοντο');
INSERT INTO `bible_verses_tr` VALUES ('4104', '44', '9', '32', 'εγενετο δε πετρον διερχομενον δια παντων κατελθειν και προς τους αγιους τους κατοικουντας λυδδαν');
INSERT INTO `bible_verses_tr` VALUES ('4105', '44', '9', '33', 'ευρεν δε εκει ανθρωπον τινα αινεαν ονοματι εξ ετων οκτω κατακειμενον επι κραββατω ος ην παραλελυμενος');
INSERT INTO `bible_verses_tr` VALUES ('4106', '44', '9', '34', 'και ειπεν αυτω ο πετρος αινεα ιαται σε ιησους ο χριστος αναστηθι και στρωσον σεαυτω και ευθεως ανεστη');
INSERT INTO `bible_verses_tr` VALUES ('4107', '44', '9', '35', 'και ειδον αυτον παντες οι κατοικουντες λυδδαν και τον  {VAR1: σαρωναν } {VAR2: σαρωνα } οιτινες επεστρεψαν επι τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('4108', '44', '9', '36', 'εν ιοππη δε τις ην μαθητρια ονοματι ταβιθα η διερμηνευομενη λεγεται δορκας αυτη ην πληρης αγαθων εργων και ελεημοσυνων ων εποιει');
INSERT INTO `bible_verses_tr` VALUES ('4109', '44', '9', '37', 'εγενετο δε εν ταις ημεραις εκειναις ασθενησασαν αυτην αποθανειν λουσαντες δε αυτην εθηκαν εν υπερωω');
INSERT INTO `bible_verses_tr` VALUES ('4110', '44', '9', '38', 'εγγυς δε ουσης λυδδης τη ιοππη οι μαθηται ακουσαντες οτι πετρος εστιν εν αυτη απεστειλαν δυο ανδρας προς αυτον παρακαλουντες μη οκνησαι διελθειν εως αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4111', '44', '9', '39', 'αναστας δε πετρος συνηλθεν αυτοις ον παραγενομενον ανηγαγον εις το υπερωον και παρεστησαν αυτω πασαι αι χηραι κλαιουσαι και επιδεικνυμεναι χιτωνας και ιματια οσα εποιει μετ αυτων ουσα η δορκας');
INSERT INTO `bible_verses_tr` VALUES ('4112', '44', '9', '40', 'εκβαλων δε εξω παντας ο πετρος θεις τα γονατα προσηυξατο και επιστρεψας προς το σωμα ειπεν ταβιθα αναστηθι η δε ηνοιξεν τους οφθαλμους αυτης και ιδουσα τον πετρον ανεκαθισεν');
INSERT INTO `bible_verses_tr` VALUES ('4113', '44', '9', '41', 'δους δε αυτη χειρα ανεστησεν αυτην φωνησας δε τους αγιους και τας χηρας παρεστησεν αυτην ζωσαν');
INSERT INTO `bible_verses_tr` VALUES ('4114', '44', '9', '42', 'γνωστον δε εγενετο καθ ολης της ιοππης και πολλοι επιστευσαν επι τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('4115', '44', '9', '43', 'εγενετο δε ημερας ικανας μειναι αυτον εν ιοππη παρα τινι σιμωνι βυρσει');
INSERT INTO `bible_verses_tr` VALUES ('4116', '44', '10', '1', 'ανηρ δε τις ην εν καισαρεια ονοματι κορνηλιος εκατονταρχης εκ σπειρης της καλουμενης ιταλικης');
INSERT INTO `bible_verses_tr` VALUES ('4117', '44', '10', '2', 'ευσεβης και φοβουμενος τον θεον συν παντι τω οικω αυτου ποιων τε ελεημοσυνας πολλας τω λαω και δεομενος του θεου διαπαντος');
INSERT INTO `bible_verses_tr` VALUES ('4118', '44', '10', '3', 'ειδεν εν οραματι φανερως ωσει ωραν εννατην της ημερας αγγελον του θεου εισελθοντα προς αυτον και ειποντα αυτω κορνηλιε');
INSERT INTO `bible_verses_tr` VALUES ('4119', '44', '10', '4', 'ο δε ατενισας αυτω και εμφοβος γενομενος ειπεν τι εστιν κυριε ειπεν δε αυτω αι προσευχαι σου και αι ελεημοσυναι σου ανεβησαν εις μνημοσυνον ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4120', '44', '10', '5', 'και νυν πεμψον εις ιοππην ανδρας και μεταπεμψαι σιμωνα ος επικαλειται πετρος');
INSERT INTO `bible_verses_tr` VALUES ('4121', '44', '10', '6', 'ουτος ξενιζεται παρα τινι σιμωνι βυρσει ω εστιν οικια παρα θαλασσαν ουτος λαλησει σοι τι σε δει ποιειν');
INSERT INTO `bible_verses_tr` VALUES ('4122', '44', '10', '7', 'ως δε απηλθεν ο αγγελος ο λαλων τω κορνηλιω φωνησας δυο των οικετων αυτου και στρατιωτην ευσεβη των προσκαρτερουντων αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4123', '44', '10', '8', 'και εξηγησαμενος αυτοις απαντα απεστειλεν αυτους εις την ιοππην');
INSERT INTO `bible_verses_tr` VALUES ('4124', '44', '10', '9', 'τη δε επαυριον οδοιπορουντων εκεινων και τη πολει εγγιζοντων ανεβη πετρος επι το δωμα προσευξασθαι περι ωραν εκτην');
INSERT INTO `bible_verses_tr` VALUES ('4125', '44', '10', '10', 'εγενετο δε προσπεινος και ηθελεν γευσασθαι παρασκευαζοντων δε εκεινων επεπεσεν επ αυτον εκστασις');
INSERT INTO `bible_verses_tr` VALUES ('4126', '44', '10', '11', 'και θεωρει τον ουρανον ανεωγμενον και καταβαινον επ αυτον σκευος τι ως οθονην μεγαλην τεσσαρσιν αρχαις δεδεμενον και καθιεμενον επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('4127', '44', '10', '12', 'εν ω υπηρχεν παντα τα τετραποδα της γης και τα θηρια και τα ερπετα και τα πετεινα του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('4128', '44', '10', '13', 'και εγενετο φωνη προς αυτον αναστας πετρε θυσον και φαγε');
INSERT INTO `bible_verses_tr` VALUES ('4129', '44', '10', '14', 'ο δε πετρος ειπεν μηδαμως κυριε οτι ουδεποτε εφαγον παν κοινον η ακαθαρτον');
INSERT INTO `bible_verses_tr` VALUES ('4130', '44', '10', '15', 'και φωνη παλιν εκ δευτερου προς αυτον α ο θεος εκαθαρισεν συ μη κοινου');
INSERT INTO `bible_verses_tr` VALUES ('4131', '44', '10', '16', 'τουτο δε εγενετο επι τρις και παλιν ανεληφθη το σκευος εις τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('4132', '44', '10', '17', 'ως δε εν εαυτω διηπορει ο πετρος τι αν ειη το οραμα ο ειδεν και ιδου οι ανδρες οι απεσταλμενοι απο του κορνηλιου διερωτησαντες την οικιαν σιμωνος επεστησαν επι τον πυλωνα');
INSERT INTO `bible_verses_tr` VALUES ('4133', '44', '10', '18', 'και φωνησαντες επυνθανοντο ει σιμων ο επικαλουμενος πετρος ενθαδε ξενιζεται');
INSERT INTO `bible_verses_tr` VALUES ('4134', '44', '10', '19', 'του δε πετρου ενθυμουμενου περι του οραματος ειπεν αυτω το πνευμα ιδου ανδρες τρεις ζητουσιν σε');
INSERT INTO `bible_verses_tr` VALUES ('4135', '44', '10', '20', 'αλλα αναστας καταβηθι και πορευου συν αυτοις μηδεν διακρινομενος διοτι εγω απεσταλκα αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4136', '44', '10', '21', 'καταβας δε πετρος προς τους ανδρας τους απεσταλμενους απο του κορνηλιου προς αυτον ειπεν ιδου εγω ειμι ον ζητειτε τις η αιτια δι ην παρεστε');
INSERT INTO `bible_verses_tr` VALUES ('4137', '44', '10', '22', 'οι δε ειπον κορνηλιος εκατονταρχης ανηρ δικαιος και φοβουμενος τον θεον μαρτυρουμενος τε υπο ολου του εθνους των ιουδαιων εχρηματισθη υπο αγγελου αγιου μεταπεμψασθαι σε εις τον οικον αυτου και ακουσαι ρηματα παρα σου');
INSERT INTO `bible_verses_tr` VALUES ('4138', '44', '10', '23', 'εισκαλεσαμενος ουν αυτους εξενισεν τη δε επαυριον ο πετρος εξηλθεν συν αυτοις και τινες των αδελφων των απο της ιοππης συνηλθον αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4139', '44', '10', '24', 'και τη επαυριον εισηλθον εις την καισαρειαν ο δε κορνηλιος ην προσδοκων αυτους συγκαλεσαμενος τους συγγενεις αυτου και τους αναγκαιους φιλους');
INSERT INTO `bible_verses_tr` VALUES ('4140', '44', '10', '25', 'ως δε εγενετο εισελθειν τον πετρον συναντησας αυτω ο κορνηλιος πεσων επι τους ποδας προσεκυνησεν');
INSERT INTO `bible_verses_tr` VALUES ('4141', '44', '10', '26', 'ο δε πετρος αυτον ηγειρεν λεγων αναστηθι καγω αυτος ανθρωπος ειμι');
INSERT INTO `bible_verses_tr` VALUES ('4142', '44', '10', '27', 'και συνομιλων αυτω εισηλθεν και ευρισκει συνεληλυθοτας πολλους');
INSERT INTO `bible_verses_tr` VALUES ('4143', '44', '10', '28', 'εφη τε προς αυτους υμεις επιστασθε ως αθεμιτον εστιν ανδρι ιουδαιω κολλασθαι η προσερχεσθαι αλλοφυλω και εμοι ο θεος εδειξεν μηδενα κοινον η ακαθαρτον λεγειν ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('4144', '44', '10', '29', 'διο και αναντιρρητως ηλθον μεταπεμφθεις πυνθανομαι ουν τινι λογω μετεπεμψασθε με');
INSERT INTO `bible_verses_tr` VALUES ('4145', '44', '10', '30', 'και ο κορνηλιος εφη απο τεταρτης ημερας μεχρι ταυτης της ωρας ημην νηστευων και την εννατην ωραν προσευχομενος εν τω οικω μου και ιδου ανηρ εστη ενωπιον μου εν εσθητι λαμπρα');
INSERT INTO `bible_verses_tr` VALUES ('4146', '44', '10', '31', 'και φησιν κορνηλιε εισηκουσθη σου η προσευχη και αι ελεημοσυναι σου εμνησθησαν ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4147', '44', '10', '32', 'πεμψον ουν εις ιοππην και μετακαλεσαι σιμωνα ος επικαλειται πετρος ουτος ξενιζεται εν οικια σιμωνος βυρσεως παρα θαλασσαν ος παραγενομενος λαλησει σοι');
INSERT INTO `bible_verses_tr` VALUES ('4148', '44', '10', '33', 'εξαυτης ουν επεμψα προς σε συ τε καλως εποιησας παραγενομενος νυν ουν παντες ημεις ενωπιον του θεου παρεσμεν ακουσαι παντα τα προστεταγμενα σοι υπο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4149', '44', '10', '34', 'ανοιξας δε πετρος το στομα ειπεν επ αληθειας καταλαμβανομαι οτι ουκ εστιν προσωποληπτης ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('4150', '44', '10', '35', 'αλλ εν παντι εθνει ο φοβουμενος αυτον και εργαζομενος δικαιοσυνην δεκτος αυτω εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4151', '44', '10', '36', 'τον λογον ον απεστειλεν τοις υιοις ισραηλ ευαγγελιζομενος ειρηνην δια ιησου χριστου ουτος εστιν παντων κυριος');
INSERT INTO `bible_verses_tr` VALUES ('4152', '44', '10', '37', 'υμεις οιδατε το γενομενον ρημα καθ ολης της ιουδαιας αρξαμενον απο της γαλιλαιας μετα το βαπτισμα ο εκηρυξεν ιωαννης');
INSERT INTO `bible_verses_tr` VALUES ('4153', '44', '10', '38', 'ιησουν τον απο  {VAR1: ναζαρετ } {VAR2: ναζαρεθ } ως εχρισεν αυτον ο θεος πνευματι αγιω και δυναμει ος διηλθεν ευεργετων και ιωμενος παντας τους καταδυναστευομενους υπο του διαβολου οτι ο θεος ην μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4154', '44', '10', '39', 'και ημεις εσμεν μαρτυρες παντων ων εποιησεν εν τε τη χωρα των ιουδαιων και εν ιερουσαλημ ον ανειλον κρεμασαντες επι ξυλου');
INSERT INTO `bible_verses_tr` VALUES ('4155', '44', '10', '40', 'τουτον ο θεος ηγειρεν τη τριτη ημερα και εδωκεν αυτον εμφανη γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4156', '44', '10', '41', 'ου παντι τω λαω αλλα μαρτυσιν τοις προκεχειροτονημενοις υπο του θεου ημιν οιτινες συνεφαγομεν και συνεπιομεν αυτω μετα το αναστηναι αυτον εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('4157', '44', '10', '42', 'και παρηγγειλεν ημιν κηρυξαι τω λαω και διαμαρτυρασθαι οτι αυτος εστιν ο ωρισμενος υπο του θεου κριτης ζωντων και νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('4158', '44', '10', '43', 'τουτω παντες οι προφηται μαρτυρουσιν αφεσιν αμαρτιων λαβειν δια του ονοματος αυτου παντα τον πιστευοντα εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4159', '44', '10', '44', 'ετι λαλουντος του πετρου τα ρηματα ταυτα επεπεσεν το πνευμα το αγιον επι παντας τους ακουοντας τον λογον');
INSERT INTO `bible_verses_tr` VALUES ('4160', '44', '10', '45', 'και εξεστησαν οι εκ περιτομης πιστοι οσοι συνηλθον τω πετρω οτι και επι τα εθνη η δωρεα του αγιου πνευματος εκκεχυται');
INSERT INTO `bible_verses_tr` VALUES ('4161', '44', '10', '46', 'ηκουον γαρ αυτων λαλουντων γλωσσαις και μεγαλυνοντων τον θεον τοτε απεκριθη ο πετρος');
INSERT INTO `bible_verses_tr` VALUES ('4162', '44', '10', '47', 'μητι το υδωρ κωλυσαι δυναται τις του μη βαπτισθηναι τουτους οιτινες το πνευμα το αγιον ελαβον καθως και ημεις');
INSERT INTO `bible_verses_tr` VALUES ('4163', '44', '10', '48', 'προσεταξεν τε αυτους βαπτισθηναι εν τω ονοματι του κυριου τοτε ηρωτησαν αυτον επιμειναι ημερας τινας');
INSERT INTO `bible_verses_tr` VALUES ('4164', '44', '11', '1', 'ηκουσαν δε οι αποστολοι και οι αδελφοι οι οντες κατα την ιουδαιαν οτι και τα εθνη εδεξαντο τον λογον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4165', '44', '11', '2', 'και οτε ανεβη πετρος εις ιεροσολυμα διεκρινοντο προς αυτον οι εκ περιτομης');
INSERT INTO `bible_verses_tr` VALUES ('4166', '44', '11', '3', 'λεγοντες οτι προς ανδρας ακροβυστιαν εχοντας εισηλθες και συνεφαγες αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4167', '44', '11', '4', 'αρξαμενος δε ο πετρος εξετιθετο αυτοις καθεξης λεγων');
INSERT INTO `bible_verses_tr` VALUES ('4168', '44', '11', '5', 'εγω ημην εν πολει ιοππη προσευχομενος και ειδον εν εκστασει οραμα καταβαινον σκευος τι ως οθονην μεγαλην τεσσαρσιν αρχαις καθιεμενην εκ του ουρανου και ηλθεν αχρις εμου');
INSERT INTO `bible_verses_tr` VALUES ('4169', '44', '11', '6', 'εις ην ατενισας κατενοουν και ειδον τα τετραποδα της γης και τα θηρια και τα ερπετα και τα πετεινα του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('4170', '44', '11', '7', 'ηκουσα δε φωνης λεγουσης μοι αναστας πετρε θυσον και φαγε');
INSERT INTO `bible_verses_tr` VALUES ('4171', '44', '11', '8', 'ειπον δε μηδαμως κυριε οτι παν κοινον η ακαθαρτον ουδεποτε εισηλθεν εις το στομα μου');
INSERT INTO `bible_verses_tr` VALUES ('4172', '44', '11', '9', 'απεκριθη δε μοι φωνη εκ δευτερου εκ του ουρανου α ο θεος εκαθαρισεν συ μη κοινου');
INSERT INTO `bible_verses_tr` VALUES ('4173', '44', '11', '10', 'τουτο δε εγενετο επι τρις και παλιν ανεσπασθη απαντα εις τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('4174', '44', '11', '11', 'και ιδου εξαυτης τρεις ανδρες επεστησαν επι την οικιαν εν η ημην απεσταλμενοι απο καισαρειας προς με');
INSERT INTO `bible_verses_tr` VALUES ('4175', '44', '11', '12', 'ειπεν δε μοι το πνευμα συνελθειν αυτοις μηδεν διακρινομενον ηλθον δε συν εμοι και οι εξ αδελφοι ουτοι και εισηλθομεν εις τον οικον του ανδρος');
INSERT INTO `bible_verses_tr` VALUES ('4176', '44', '11', '13', 'απηγγειλεν τε ημιν πως ειδεν τον αγγελον εν τω οικω αυτου σταθεντα και ειποντα αυτω αποστειλον εις ιοππην ανδρας και μεταπεμψαι σιμωνα τον επικαλουμενον πετρον');
INSERT INTO `bible_verses_tr` VALUES ('4177', '44', '11', '14', 'ος λαλησει ρηματα προς σε εν οις σωθηση συ και πας ο οικος σου');
INSERT INTO `bible_verses_tr` VALUES ('4178', '44', '11', '15', 'εν δε τω αρξασθαι με λαλειν επεπεσεν το πνευμα το αγιον επ αυτους ωσπερ και εφ ημας εν αρχη');
INSERT INTO `bible_verses_tr` VALUES ('4179', '44', '11', '16', 'εμνησθην δε του ρηματος κυριου ως ελεγεν ιωαννης μεν εβαπτισεν υδατι υμεις δε βαπτισθησεσθε εν πνευματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('4180', '44', '11', '17', 'ει ουν την ισην δωρεαν εδωκεν αυτοις ο θεος ως και ημιν πιστευσασιν επι τον κυριον ιησουν χριστον εγω δε τις ημην δυνατος κωλυσαι τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('4181', '44', '11', '18', 'ακουσαντες δε ταυτα ησυχασαν και εδοξαζον τον θεον λεγοντες αραγε και τοις εθνεσιν ο θεος την μετανοιαν εδωκεν εις ζωην');
INSERT INTO `bible_verses_tr` VALUES ('4182', '44', '11', '19', 'οι μεν ουν διασπαρεντες απο της θλιψεως της γενομενης επι στεφανω διηλθον εως φοινικης και κυπρου και αντιοχειας μηδενι λαλουντες τον λογον ει μη μονον ιουδαιοις');
INSERT INTO `bible_verses_tr` VALUES ('4183', '44', '11', '20', 'ησαν δε τινες εξ αυτων ανδρες κυπριοι και κυρηναιοι οιτινες εισελθοντες εις αντιοχειαν ελαλουν προς τους ελληνιστας ευαγγελιζομενοι τον κυριον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4184', '44', '11', '21', 'και ην χειρ κυριου μετ αυτων πολυς τε αριθμος πιστευσας επεστρεψεν επι τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('4185', '44', '11', '22', 'ηκουσθη δε ο λογος εις τα ωτα της εκκλησιας της εν ιεροσολυμοις περι αυτων και εξαπεστειλαν βαρναβαν διελθειν εως αντιοχειας');
INSERT INTO `bible_verses_tr` VALUES ('4186', '44', '11', '23', 'ος παραγενομενος και ιδων την χαριν του θεου εχαρη και παρεκαλει παντας τη προθεσει της καρδιας προσμενειν τω κυριω');
INSERT INTO `bible_verses_tr` VALUES ('4187', '44', '11', '24', 'οτι ην ανηρ αγαθος και πληρης πνευματος αγιου και πιστεως και προσετεθη οχλος ικανος τω κυριω');
INSERT INTO `bible_verses_tr` VALUES ('4188', '44', '11', '25', 'εξηλθεν δε εις ταρσον ο βαρναβας αναζητησαι σαυλον');
INSERT INTO `bible_verses_tr` VALUES ('4189', '44', '11', '26', 'και ευρων αυτον ηγαγεν αυτον εις αντιοχειαν εγενετο δε αυτους ενιαυτον ολον συναχθηναι εν τη εκκλησια και διδαξαι οχλον ικανον χρηματισαι τε πρωτον εν αντιοχεια τους μαθητας χριστιανους');
INSERT INTO `bible_verses_tr` VALUES ('4190', '44', '11', '27', 'εν ταυταις δε ταις ημεραις κατηλθον απο ιεροσολυμων προφηται εις αντιοχειαν');
INSERT INTO `bible_verses_tr` VALUES ('4191', '44', '11', '28', 'αναστας δε εις εξ αυτων ονοματι αγαβος εσημανεν δια του πνευματος λιμον μεγαν μελλειν εσεσθαι εφ ολην την οικουμενην οστις και εγενετο επι κλαυδιου καισαρος');
INSERT INTO `bible_verses_tr` VALUES ('4192', '44', '11', '29', 'των δε μαθητων καθως ηυπορειτο τις ωρισαν εκαστος αυτων εις διακονιαν πεμψαι τοις κατοικουσιν εν τη ιουδαια αδελφοις');
INSERT INTO `bible_verses_tr` VALUES ('4193', '44', '11', '30', 'ο και εποιησαν αποστειλαντες προς τους πρεσβυτερους δια χειρος βαρναβα και σαυλου');
INSERT INTO `bible_verses_tr` VALUES ('4194', '44', '12', '1', 'κατ εκεινον δε τον καιρον επεβαλεν ηρωδης ο βασιλευς τας χειρας κακωσαι τινας των απο της εκκλησιας');
INSERT INTO `bible_verses_tr` VALUES ('4195', '44', '12', '2', 'ανειλεν δε ιακωβον τον αδελφον ιωαννου μαχαιρα');
INSERT INTO `bible_verses_tr` VALUES ('4196', '44', '12', '3', 'και ιδων οτι αρεστον εστιν τοις ιουδαιοις προσεθετο συλλαβειν και πετρον ησαν δε ημεραι των αζυμων');
INSERT INTO `bible_verses_tr` VALUES ('4197', '44', '12', '4', 'ον και πιασας εθετο εις φυλακην παραδους τεσσαρσιν τετραδιοις στρατιωτων φυλασσειν αυτον βουλομενος μετα το πασχα αναγαγειν αυτον τω λαω');
INSERT INTO `bible_verses_tr` VALUES ('4198', '44', '12', '5', 'ο μεν ουν πετρος ετηρειτο εν τη φυλακη προσευχη δε ην εκτενης γινομενη υπο της εκκλησιας προς τον θεον υπερ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4199', '44', '12', '6', 'οτε δε εμελλεν αυτον προαγειν ο ηρωδης τη νυκτι εκεινη ην ο πετρος κοιμωμενος μεταξυ δυο στρατιωτων δεδεμενος αλυσεσιν δυσιν φυλακες τε προ της θυρας ετηρουν την φυλακην');
INSERT INTO `bible_verses_tr` VALUES ('4200', '44', '12', '7', 'και ιδου αγγελος κυριου επεστη και φως ελαμψεν εν τω οικηματι παταξας δε την πλευραν του πετρου ηγειρεν αυτον λεγων αναστα εν ταχει και εξεπεσον αυτου αι αλυσεις εκ των χειρων');
INSERT INTO `bible_verses_tr` VALUES ('4201', '44', '12', '8', 'ειπεν τε ο αγγελος προς αυτον περιζωσαι και υποδησαι τα σανδαλια σου εποιησεν δε ουτως και λεγει αυτω περιβαλου το ιματιον σου και ακολουθει μοι');
INSERT INTO `bible_verses_tr` VALUES ('4202', '44', '12', '9', 'και εξελθων ηκολουθει αυτω και ουκ ηδει οτι αληθες εστιν το γινομενον δια του αγγελου εδοκει δε οραμα βλεπειν');
INSERT INTO `bible_verses_tr` VALUES ('4203', '44', '12', '10', 'διελθοντες δε πρωτην φυλακην και δευτεραν ηλθον επι την πυλην την σιδηραν την φερουσαν εις την πολιν ητις αυτοματη ηνοιχθη αυτοις και εξελθοντες προηλθον ρυμην μιαν και ευθεως απεστη ο αγγελος απ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4204', '44', '12', '11', 'και ο πετρος γενομενος εν εαυτω ειπεν νυν οιδα αληθως οτι εξαπεστειλεν κυριος τον αγγελον αυτου και εξειλετο με εκ χειρος ηρωδου και πασης της προσδοκιας του λαου των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('4205', '44', '12', '12', 'συνιδων τε ηλθεν επι την οικιαν μαριας της μητρος ιωαννου του επικαλουμενου μαρκου ου ησαν ικανοι συνηθροισμενοι και προσευχομενοι');
INSERT INTO `bible_verses_tr` VALUES ('4206', '44', '12', '13', 'κρουσαντος δε του πετρου την θυραν του πυλωνος προσηλθεν παιδισκη υπακουσαι ονοματι ροδη');
INSERT INTO `bible_verses_tr` VALUES ('4207', '44', '12', '14', 'και επιγνουσα την φωνην του πετρου απο της χαρας ουκ ηνοιξεν τον πυλωνα εισδραμουσα δε απηγγειλεν εσταναι τον πετρον προ του πυλωνος');
INSERT INTO `bible_verses_tr` VALUES ('4208', '44', '12', '15', 'οι δε προς αυτην ειπον μαινη η δε διισχυριζετο ουτως εχειν οι δ ελεγον ο αγγελος αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4209', '44', '12', '16', 'ο δε πετρος επεμενεν κρουων ανοιξαντες δε ειδον αυτον και εξεστησαν');
INSERT INTO `bible_verses_tr` VALUES ('4210', '44', '12', '17', 'κατασεισας δε αυτοις τη χειρι σιγαν διηγησατο αυτοις πως ο κυριος αυτον εξηγαγεν εκ της φυλακης ειπεν δε απαγγειλατε ιακωβω και τοις αδελφοις ταυτα και εξελθων επορευθη εις ετερον τοπον');
INSERT INTO `bible_verses_tr` VALUES ('4211', '44', '12', '18', 'γενομενης δε ημερας ην ταραχος ουκ ολιγος εν τοις στρατιωταις τι αρα ο πετρος εγενετο');
INSERT INTO `bible_verses_tr` VALUES ('4212', '44', '12', '19', 'ηρωδης δε επιζητησας αυτον και μη ευρων ανακρινας τους φυλακας εκελευσεν απαχθηναι και κατελθων απο της ιουδαιας εις την καισαρειαν διετριβεν');
INSERT INTO `bible_verses_tr` VALUES ('4213', '44', '12', '20', 'ην δε ο ηρωδης θυμομαχων τυριοις και σιδωνιοις ομοθυμαδον δε παρησαν προς αυτον και πεισαντες βλαστον τον επι του κοιτωνος του βασιλεως ητουντο ειρηνην δια το τρεφεσθαι αυτων την χωραν απο της βασιλικης');
INSERT INTO `bible_verses_tr` VALUES ('4214', '44', '12', '21', 'τακτη δε ημερα ο ηρωδης ενδυσαμενος εσθητα βασιλικην και καθισας επι του βηματος εδημηγορει προς αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4215', '44', '12', '22', 'ο δε δημος επεφωνει θεου φωνη και ουκ ανθρωπου');
INSERT INTO `bible_verses_tr` VALUES ('4216', '44', '12', '23', 'παραχρημα δε επαταξεν αυτον αγγελος κυριου ανθ ων ουκ εδωκεν την δοξαν τω θεω και γενομενος σκωληκοβρωτος εξεψυξεν');
INSERT INTO `bible_verses_tr` VALUES ('4217', '44', '12', '24', 'ο δε λογος του θεου ηυξανεν και επληθυνετο');
INSERT INTO `bible_verses_tr` VALUES ('4218', '44', '12', '25', 'βαρναβας δε και σαυλος υπεστρεψαν εξ ιερουσαλημ πληρωσαντες την διακονιαν συμπαραλαβοντες και ιωαννην τον επικληθεντα μαρκον');
INSERT INTO `bible_verses_tr` VALUES ('4219', '44', '13', '1', 'ησαν δε τινες εν αντιοχεια κατα την ουσαν εκκλησιαν προφηται και διδασκαλοι ο τε βαρναβας και συμεων ο καλουμενος νιγερ και λουκιος ο κυρηναιος μαναην τε ηρωδου του τετραρχου συντροφος και σαυλος');
INSERT INTO `bible_verses_tr` VALUES ('4220', '44', '13', '2', 'λειτουργουντων δε αυτων τω κυριω και νηστευοντων ειπεν το πνευμα το αγιον αφορισατε δη μοι τον τε βαρναβαν και τον σαυλον εις το εργον ο προσκεκλημαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4221', '44', '13', '3', 'τοτε νηστευσαντες και προσευξαμενοι και επιθεντες τας χειρας αυτοις απελυσαν');
INSERT INTO `bible_verses_tr` VALUES ('4222', '44', '13', '4', 'ουτοι μεν ουν εκπεμφθεντες υπο του πνευματος του αγιου κατηλθον εις την σελευκειαν εκειθεν τε απεπλευσαν εις την κυπρον');
INSERT INTO `bible_verses_tr` VALUES ('4223', '44', '13', '5', 'και γενομενοι εν σαλαμινι κατηγγελλον τον λογον του θεου εν ταις συναγωγαις των ιουδαιων ειχον δε και ιωαννην υπηρετην');
INSERT INTO `bible_verses_tr` VALUES ('4224', '44', '13', '6', 'διελθοντες δε την νησον αχρι παφου ευρον τινα μαγον ψευδοπροφητην ιουδαιον ω ονομα βαριησους');
INSERT INTO `bible_verses_tr` VALUES ('4225', '44', '13', '7', 'ος ην συν τω ανθυπατω σεργιω παυλω ανδρι συνετω ουτος προσκαλεσαμενος βαρναβαν και σαυλον επεζητησεν ακουσαι τον λογον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4226', '44', '13', '8', 'ανθιστατο δε αυτοις ελυμας ο μαγος ουτως γαρ μεθερμηνευεται το ονομα αυτου ζητων διαστρεψαι τον ανθυπατον απο της πιστεως');
INSERT INTO `bible_verses_tr` VALUES ('4227', '44', '13', '9', 'σαυλος δε ο και παυλος πλησθεις πνευματος αγιου και ατενισας εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4228', '44', '13', '10', 'ειπεν ω πληρης παντος δολου και πασης ραδιουργιας υιε διαβολου εχθρε πασης δικαιοσυνης ου παυση διαστρεφων τας οδους κυριου τας ευθειας');
INSERT INTO `bible_verses_tr` VALUES ('4229', '44', '13', '11', 'και νυν ιδου χειρ του κυριου επι σε και εση τυφλος μη βλεπων τον ηλιον αχρι καιρου παραχρημα δε επεπεσεν επ αυτον αχλυς και σκοτος και περιαγων εζητει χειραγωγους');
INSERT INTO `bible_verses_tr` VALUES ('4230', '44', '13', '12', 'τοτε ιδων ο ανθυπατος το γεγονος επιστευσεν εκπλησσομενος επι τη διδαχη του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('4231', '44', '13', '13', 'αναχθεντες δε απο της παφου οι περι τον παυλον ηλθον εις περγην της παμφυλιας ιωαννης δε αποχωρησας απ αυτων υπεστρεψεν εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('4232', '44', '13', '14', 'αυτοι δε διελθοντες απο της περγης παρεγενοντο εις αντιοχειαν της πισιδιας και εισελθοντες εις την συναγωγην τη ημερα των σαββατων εκαθισαν');
INSERT INTO `bible_verses_tr` VALUES ('4233', '44', '13', '15', 'μετα δε την αναγνωσιν του νομου και των προφητων απεστειλαν οι αρχισυναγωγοι προς αυτους λεγοντες ανδρες αδελφοι ει εστιν λογος εν υμιν παρακλησεως προς τον λαον λεγετε');
INSERT INTO `bible_verses_tr` VALUES ('4234', '44', '13', '16', 'αναστας δε παυλος και κατασεισας τη χειρι ειπεν ανδρες ισραηλιται και οι φοβουμενοι τον θεον ακουσατε');
INSERT INTO `bible_verses_tr` VALUES ('4235', '44', '13', '17', 'ο θεος του λαου τουτου ισραηλ εξελεξατο τους πατερας ημων και τον λαον υψωσεν εν τη παροικια εν γη αιγυπτω και μετα βραχιονος υψηλου εξηγαγεν αυτους εξ αυτης');
INSERT INTO `bible_verses_tr` VALUES ('4236', '44', '13', '18', 'και ως τεσσαρακονταετη χρονον ετροποφορησεν αυτους εν τη ερημω');
INSERT INTO `bible_verses_tr` VALUES ('4237', '44', '13', '19', 'και καθελων εθνη επτα εν γη χανααν κατεκληροδοτησεν αυτοις την γην αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4238', '44', '13', '20', 'και μετα ταυτα ως ετεσιν τετρακοσιοις και πεντηκοντα εδωκεν κριτας εως σαμουηλ του προφητου');
INSERT INTO `bible_verses_tr` VALUES ('4239', '44', '13', '21', 'κακειθεν ητησαντο βασιλεα και εδωκεν αυτοις ο θεος τον σαουλ υιον κις ανδρα εκ φυλης βενιαμιν ετη τεσσαρακοντα');
INSERT INTO `bible_verses_tr` VALUES ('4240', '44', '13', '22', 'και μεταστησας αυτον ηγειρεν αυτοις τον δαβιδ εις βασιλεα ω και ειπεν μαρτυρησας ευρον δαβιδ τον του ιεσσαι ανδρα κατα την καρδιαν μου ος ποιησει παντα τα θεληματα μου');
INSERT INTO `bible_verses_tr` VALUES ('4241', '44', '13', '23', 'τουτου ο θεος απο του σπερματος κατ επαγγελιαν ηγειρεν τω ισραηλ σωτηρα ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4242', '44', '13', '24', 'προκηρυξαντος ιωαννου προ προσωπου της εισοδου αυτου βαπτισμα μετανοιας παντι τω λαω ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('4243', '44', '13', '25', 'ως δε επληρου ο ιωαννης τον δρομον ελεγεν τινα με υπονοειτε ειναι ουκ ειμι εγω αλλ ιδου ερχεται μετ εμε ου ουκ ειμι αξιος το υποδημα των ποδων λυσαι');
INSERT INTO `bible_verses_tr` VALUES ('4244', '44', '13', '26', 'ανδρες αδελφοι υιοι γενους αβρααμ και οι εν υμιν φοβουμενοι τον θεον υμιν ο λογος της σωτηριας ταυτης απεσταλη');
INSERT INTO `bible_verses_tr` VALUES ('4245', '44', '13', '27', 'οι γαρ κατοικουντες εν ιερουσαλημ και οι αρχοντες αυτων τουτον αγνοησαντες και τας φωνας των προφητων τας κατα παν σαββατον αναγινωσκομενας κριναντες επληρωσαν');
INSERT INTO `bible_verses_tr` VALUES ('4246', '44', '13', '28', 'και μηδεμιαν αιτιαν θανατου ευροντες ητησαντο πιλατον αναιρεθηναι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4247', '44', '13', '29', 'ως δε ετελεσαν απαντα τα περι αυτου γεγραμμενα καθελοντες απο του ξυλου εθηκαν εις μνημειον');
INSERT INTO `bible_verses_tr` VALUES ('4248', '44', '13', '30', 'ο δε θεος ηγειρεν αυτον εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('4249', '44', '13', '31', 'ος ωφθη επι ημερας πλειους τοις συναναβασιν αυτω απο της γαλιλαιας εις ιερουσαλημ οιτινες εισιν μαρτυρες αυτου προς τον λαον');
INSERT INTO `bible_verses_tr` VALUES ('4250', '44', '13', '32', 'και ημεις υμας ευαγγελιζομεθα την προς τους πατερας επαγγελιαν γενομενην');
INSERT INTO `bible_verses_tr` VALUES ('4251', '44', '13', '33', 'οτι ταυτην ο θεος εκπεπληρωκεν τοις τεκνοις αυτων ημιν αναστησας ιησουν ως και εν τω ψαλμω τω δευτερω γεγραπται υιος μου ει συ εγω σημερον γεγεννηκα σε');
INSERT INTO `bible_verses_tr` VALUES ('4252', '44', '13', '34', 'οτι δε ανεστησεν αυτον εκ νεκρων μηκετι μελλοντα υποστρεφειν εις διαφθοραν ουτως ειρηκεν οτι δωσω υμιν τα οσια δαβιδ τα πιστα');
INSERT INTO `bible_verses_tr` VALUES ('4253', '44', '13', '35', 'διο και εν ετερω λεγει ου δωσεις τον οσιον σου ιδειν διαφθοραν');
INSERT INTO `bible_verses_tr` VALUES ('4254', '44', '13', '36', 'δαβιδ μεν γαρ ιδια γενεα υπηρετησας τη του θεου βουλη εκοιμηθη και προσετεθη προς τους πατερας αυτου και ειδεν διαφθοραν');
INSERT INTO `bible_verses_tr` VALUES ('4255', '44', '13', '37', 'ον δε ο θεος ηγειρεν ουκ ειδεν διαφθοραν');
INSERT INTO `bible_verses_tr` VALUES ('4256', '44', '13', '38', 'γνωστον ουν εστω υμιν ανδρες αδελφοι οτι δια τουτου υμιν αφεσις αμαρτιων καταγγελλεται');
INSERT INTO `bible_verses_tr` VALUES ('4257', '44', '13', '39', 'και απο παντων ων ουκ ηδυνηθητε εν τω νομω μωσεως δικαιωθηναι εν τουτω πας ο πιστευων δικαιουται');
INSERT INTO `bible_verses_tr` VALUES ('4258', '44', '13', '40', 'βλεπετε ουν μη επελθη εφ υμας το ειρημενον εν τοις προφηταις');
INSERT INTO `bible_verses_tr` VALUES ('4259', '44', '13', '41', 'ιδετε οι καταφρονηται και θαυμασατε και αφανισθητε οτι εργον εγω εργαζομαι εν ταις ημεραις υμων εργον ω ου μη πιστευσητε εαν τις εκδιηγηται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('4260', '44', '13', '42', 'εξιοντων δε εκ της συναγωγης των ιουδαιων παρεκαλουν τα εθνη εις το μεταξυ σαββατον λαληθηναι αυτοις τα ρηματα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('4261', '44', '13', '43', 'λυθεισης δε της συναγωγης ηκολουθησαν πολλοι των ιουδαιων και των σεβομενων προσηλυτων τω παυλω και τω βαρναβα οιτινες προσλαλουντες αυτοις επειθον αυτους επιμενειν τη χαριτι του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4262', '44', '13', '44', 'τω δε ερχομενω σαββατω σχεδον πασα η πολις συνηχθη ακουσαι τον λογον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4263', '44', '13', '45', 'ιδοντες δε οι ιουδαιοι τους οχλους επλησθησαν ζηλου και αντελεγον τοις υπο του παυλου λεγομενοις αντιλεγοντες και βλασφημουντες');
INSERT INTO `bible_verses_tr` VALUES ('4264', '44', '13', '46', 'παρρησιασαμενοι δε ο παυλος και ο βαρναβας ειπον υμιν ην αναγκαιον πρωτον λαληθηναι τον λογον του θεου επειδη δε απωθεισθε αυτον και ουκ αξιους κρινετε εαυτους της αιωνιου ζωης ιδου στρεφομεθα εις τα εθνη');
INSERT INTO `bible_verses_tr` VALUES ('4265', '44', '13', '47', 'ουτως γαρ εντεταλται ημιν ο κυριος τεθεικα σε εις φως εθνων του ειναι σε εις σωτηριαν εως εσχατου της γης');
INSERT INTO `bible_verses_tr` VALUES ('4266', '44', '13', '48', 'ακουοντα δε τα εθνη εχαιρον και εδοξαζον τον λογον του κυριου και επιστευσαν οσοι ησαν τεταγμενοι εις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('4267', '44', '13', '49', 'διεφερετο δε ο λογος του κυριου δι ολης της χωρας');
INSERT INTO `bible_verses_tr` VALUES ('4268', '44', '13', '50', 'οι δε ιουδαιοι παρωτρυναν τας σεβομενας γυναικας και τας ευσχημονας και τους πρωτους της πολεως και επηγειραν διωγμον επι τον παυλον και τον βαρναβαν και εξεβαλον αυτους απο των οριων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4269', '44', '13', '51', 'οι δε εκτιναξαμενοι τον κονιορτον των ποδων αυτων επ αυτους ηλθον εις ικονιον');
INSERT INTO `bible_verses_tr` VALUES ('4270', '44', '13', '52', 'οι δε μαθηται επληρουντο χαρας και πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('4271', '44', '14', '1', 'εγενετο δε εν ικονιω κατα το αυτο εισελθειν αυτους εις την συναγωγην των ιουδαιων και λαλησαι ουτως ωστε πιστευσαι ιουδαιων τε και ελληνων πολυ πληθος');
INSERT INTO `bible_verses_tr` VALUES ('4272', '44', '14', '2', 'οι δε απειθουντες ιουδαιοι επηγειραν και εκακωσαν τας ψυχας των εθνων κατα των αδελφων');
INSERT INTO `bible_verses_tr` VALUES ('4273', '44', '14', '3', 'ικανον μεν ουν χρονον διετριψαν παρρησιαζομενοι επι τω κυριω τω μαρτυρουντι τω λογω της χαριτος αυτου και διδοντι σημεια και τερατα γινεσθαι δια των χειρων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4274', '44', '14', '4', 'εσχισθη δε το πληθος της πολεως και οι μεν ησαν συν τοις ιουδαιοις οι δε συν τοις αποστολοις');
INSERT INTO `bible_verses_tr` VALUES ('4275', '44', '14', '5', 'ως δε εγενετο ορμη των εθνων τε και ιουδαιων συν τοις αρχουσιν αυτων υβρισαι και λιθοβολησαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4276', '44', '14', '6', 'συνιδοντες κατεφυγον εις τας πολεις της λυκαονιας λυστραν και δερβην και την περιχωρον');
INSERT INTO `bible_verses_tr` VALUES ('4277', '44', '14', '7', 'κακει ησαν ευαγγελιζομενοι');
INSERT INTO `bible_verses_tr` VALUES ('4278', '44', '14', '8', 'και τις ανηρ εν λυστροις αδυνατος τοις ποσιν εκαθητο χωλος εκ κοιλιας μητρος αυτου υπαρχων ος ουδεποτε  {VAR1: περιπεπατηκει } {VAR2: περιεπεπατηκει }');
INSERT INTO `bible_verses_tr` VALUES ('4279', '44', '14', '9', 'ουτος ηκουεν του παυλου λαλουντος ος ατενισας αυτω και ιδων οτι πιστιν εχει του σωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('4280', '44', '14', '10', 'ειπεν μεγαλη τη φωνη αναστηθι επι τους ποδας σου ορθος και ηλλετο και περιεπατει');
INSERT INTO `bible_verses_tr` VALUES ('4281', '44', '14', '11', 'οι δε οχλοι ιδοντες ο εποιησεν ο παυλος επηραν την φωνην αυτων λυκαονιστι λεγοντες οι θεοι ομοιωθεντες ανθρωποις κατεβησαν προς ημας');
INSERT INTO `bible_verses_tr` VALUES ('4282', '44', '14', '12', 'εκαλουν τε τον μεν βαρναβαν δια τον δε παυλον ερμην επειδη αυτος ην ο ηγουμενος του λογου');
INSERT INTO `bible_verses_tr` VALUES ('4283', '44', '14', '13', 'ο δε ιερευς του διος του οντος προ της πολεως αυτων ταυρους και στεμματα επι τους πυλωνας ενεγκας συν τοις οχλοις ηθελεν θυειν');
INSERT INTO `bible_verses_tr` VALUES ('4284', '44', '14', '14', 'ακουσαντες δε οι αποστολοι βαρναβας και παυλος διαρρηξαντες τα ιματια αυτων εισεπηδησαν εις τον οχλον κραζοντες');
INSERT INTO `bible_verses_tr` VALUES ('4285', '44', '14', '15', 'και λεγοντες ανδρες τι ταυτα ποιειτε και ημεις ομοιοπαθεις εσμεν υμιν ανθρωποι ευαγγελιζομενοι υμας απο τουτων των ματαιων επιστρεφειν επι τον θεον τον ζωντα ος εποιησεν τον ουρανον και την γην και την θαλασσαν και παντα τα εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4286', '44', '14', '16', 'ος εν ταις παρωχημεναις γενεαις ειασεν παντα τα εθνη πορευεσθαι ταις οδοις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4287', '44', '14', '17', 'και τοι γε ουκ αμαρτυρον εαυτον αφηκεν αγαθοποιων ουρανοθεν ημιν υετους διδους και καιρους καρποφορους εμπιπλων τροφης και ευφροσυνης τας καρδιας ημων');
INSERT INTO `bible_verses_tr` VALUES ('4288', '44', '14', '18', 'και ταυτα λεγοντες μολις κατεπαυσαν τους οχλους του μη θυειν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4289', '44', '14', '19', 'επηλθον δε απο αντιοχειας και ικονιου ιουδαιοι και πεισαντες τους οχλους και λιθασαντες τον παυλον εσυρον εξω της πολεως νομισαντες αυτον τεθναναι');
INSERT INTO `bible_verses_tr` VALUES ('4290', '44', '14', '20', 'κυκλωσαντων δε αυτον των μαθητων αναστας εισηλθεν εις την πολιν και τη επαυριον εξηλθεν συν τω βαρναβα εις δερβην');
INSERT INTO `bible_verses_tr` VALUES ('4291', '44', '14', '21', 'ευαγγελισαμενοι τε την πολιν εκεινην και μαθητευσαντες ικανους υπεστρεψαν εις την λυστραν και ικονιον και αντιοχειαν');
INSERT INTO `bible_verses_tr` VALUES ('4292', '44', '14', '22', 'επιστηριζοντες τας ψυχας των μαθητων παρακαλουντες εμμενειν τη πιστει και οτι δια πολλων θλιψεων δει ημας εισελθειν εις την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4293', '44', '14', '23', 'χειροτονησαντες δε αυτοις πρεσβυτερους κατ εκκλησιαν προσευξαμενοι μετα νηστειων παρεθεντο αυτους τω κυριω εις ον πεπιστευκεισαν');
INSERT INTO `bible_verses_tr` VALUES ('4294', '44', '14', '24', 'και διελθοντες την πισιδιαν ηλθον εις παμφυλιαν');
INSERT INTO `bible_verses_tr` VALUES ('4295', '44', '14', '25', 'και λαλησαντες εν περγη τον λογον κατεβησαν εις ατταλειαν');
INSERT INTO `bible_verses_tr` VALUES ('4296', '44', '14', '26', 'κακειθεν απεπλευσαν εις αντιοχειαν οθεν ησαν παραδεδομενοι τη χαριτι του θεου εις το εργον ο επληρωσαν');
INSERT INTO `bible_verses_tr` VALUES ('4297', '44', '14', '27', 'παραγενομενοι δε και συναγαγοντες την εκκλησιαν ανηγγειλαν οσα εποιησεν ο θεος μετ αυτων και οτι ηνοιξεν τοις εθνεσιν θυραν πιστεως');
INSERT INTO `bible_verses_tr` VALUES ('4298', '44', '14', '28', 'διετριβον δε εκει χρονον ουκ ολιγον συν τοις μαθηταις');
INSERT INTO `bible_verses_tr` VALUES ('4299', '44', '15', '1', 'και τινες κατελθοντες απο της ιουδαιας εδιδασκον τους αδελφους οτι εαν μη περιτεμνησθε τω εθει μωυσεως ου δυνασθε σωθηναι');
INSERT INTO `bible_verses_tr` VALUES ('4300', '44', '15', '2', 'γενομενης ουν στασεως και συζητησεως ουκ ολιγης τω παυλω και τω βαρναβα προς αυτους εταξαν αναβαινειν παυλον και βαρναβαν και τινας αλλους εξ αυτων προς τους αποστολους και πρεσβυτερους εις ιερουσαλημ περι του ζητηματος τουτου');
INSERT INTO `bible_verses_tr` VALUES ('4301', '44', '15', '3', 'οι μεν ουν προπεμφθεντες υπο της εκκλησιας διηρχοντο την φοινικην και σαμαρειαν εκδιηγουμενοι την επιστροφην των εθνων και εποιουν χαραν μεγαλην πασιν τοις αδελφοις');
INSERT INTO `bible_verses_tr` VALUES ('4302', '44', '15', '4', 'παραγενομενοι δε εις ιερουσαλημ απεδεχθησαν υπο της εκκλησιας και των αποστολων και των πρεσβυτερων ανηγγειλαν τε οσα ο θεος εποιησεν μετ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4303', '44', '15', '5', 'εξανεστησαν δε τινες των απο της αιρεσεως των φαρισαιων πεπιστευκοτες λεγοντες οτι δει περιτεμνειν αυτους παραγγελλειν τε τηρειν τον νομον μωυσεως');
INSERT INTO `bible_verses_tr` VALUES ('4304', '44', '15', '6', 'συνηχθησαν δε οι αποστολοι και οι πρεσβυτεροι ιδειν περι του λογου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('4305', '44', '15', '7', 'πολλης δε συζητησεως γενομενης αναστας πετρος ειπεν προς αυτους ανδρες αδελφοι υμεις επιστασθε οτι αφ ημερων αρχαιων ο θεος εν ημιν εξελεξατο δια του στοματος μου ακουσαι τα εθνη τον λογον του ευαγγελιου και πιστευσαι');
INSERT INTO `bible_verses_tr` VALUES ('4306', '44', '15', '8', 'και ο καρδιογνωστης θεος εμαρτυρησεν αυτοις δους αυτοις το πνευμα το αγιον καθως και ημιν');
INSERT INTO `bible_verses_tr` VALUES ('4307', '44', '15', '9', 'και ουδεν διεκρινεν μεταξυ ημων τε και αυτων τη πιστει καθαρισας τας καρδιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4308', '44', '15', '10', 'νυν ουν τι πειραζετε τον θεον επιθειναι ζυγον επι τον τραχηλον των μαθητων ον ουτε οι πατερες ημων ουτε ημεις ισχυσαμεν βαστασαι');
INSERT INTO `bible_verses_tr` VALUES ('4309', '44', '15', '11', 'αλλα δια της χαριτος κυριου ιησου χριστου πιστευομεν σωθηναι καθ ον τροπον κακεινοι');
INSERT INTO `bible_verses_tr` VALUES ('4310', '44', '15', '12', 'εσιγησεν δε παν το πληθος και ηκουον βαρναβα και παυλου εξηγουμενων οσα εποιησεν ο θεος σημεια και τερατα εν τοις εθνεσιν δι αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4311', '44', '15', '13', 'μετα δε το σιγησαι αυτους απεκριθη ιακωβος λεγων ανδρες αδελφοι ακουσατε μου');
INSERT INTO `bible_verses_tr` VALUES ('4312', '44', '15', '14', 'συμεων εξηγησατο καθως πρωτον ο θεος επεσκεψατο λαβειν εξ εθνων λαον επι τω ονοματι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4313', '44', '15', '15', 'και τουτω συμφωνουσιν οι λογοι των προφητων καθως γεγραπται');
INSERT INTO `bible_verses_tr` VALUES ('4314', '44', '15', '16', 'μετα ταυτα αναστρεψω και ανοικοδομησω την σκηνην δαβιδ την πεπτωκυιαν και τα κατεσκαμμενα αυτης ανοικοδομησω και ανορθωσω αυτην');
INSERT INTO `bible_verses_tr` VALUES ('4315', '44', '15', '17', 'οπως αν εκζητησωσιν οι καταλοιποι των ανθρωπων τον κυριον και παντα τα εθνη εφ ους επικεκληται το ονομα μου επ αυτους λεγει κυριος ο ποιων ταυτα παντα');
INSERT INTO `bible_verses_tr` VALUES ('4316', '44', '15', '18', 'γνωστα απ αιωνος εστιν τω θεω παντα τα εργα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4317', '44', '15', '19', 'διο εγω κρινω μη παρενοχλειν τοις απο των εθνων επιστρεφουσιν επι τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('4318', '44', '15', '20', 'αλλα επιστειλαι αυτοις του απεχεσθαι απο των αλισγηματων των ειδωλων και της πορνειας και του πνικτου και του αιματος');
INSERT INTO `bible_verses_tr` VALUES ('4319', '44', '15', '21', 'μωσης γαρ εκ γενεων αρχαιων κατα πολιν τους κηρυσσοντας αυτον εχει εν ταις συναγωγαις κατα παν σαββατον αναγινωσκομενος');
INSERT INTO `bible_verses_tr` VALUES ('4320', '44', '15', '22', 'τοτε εδοξεν τοις αποστολοις και τοις πρεσβυτεροις συν ολη τη εκκλησια εκλεξαμενους ανδρας εξ αυτων πεμψαι εις αντιοχειαν συν τω παυλω και βαρναβα ιουδαν τον επικαλουμενον βαρσαβαν και σιλαν ανδρας ηγουμενους εν τοις αδελφοις');
INSERT INTO `bible_verses_tr` VALUES ('4321', '44', '15', '23', 'γραψαντες δια χειρος αυτων ταδε οι αποστολοι και οι πρεσβυτεροι και οι αδελφοι τοις κατα την αντιοχειαν και συριαν και κιλικιαν αδελφοις τοις εξ εθνων χαιρειν');
INSERT INTO `bible_verses_tr` VALUES ('4322', '44', '15', '24', 'επειδη ηκουσαμεν οτι τινες εξ ημων εξελθοντες εταραξαν υμας λογοις ανασκευαζοντες τας ψυχας υμων λεγοντες περιτεμνεσθαι και τηρειν τον νομον οις ου διεστειλαμεθα');
INSERT INTO `bible_verses_tr` VALUES ('4323', '44', '15', '25', 'εδοξεν ημιν γενομενοις ομοθυμαδον εκλεξαμενους ανδρας πεμψαι προς υμας συν τοις αγαπητοις ημων βαρναβα και παυλω');
INSERT INTO `bible_verses_tr` VALUES ('4324', '44', '15', '26', 'ανθρωποις παραδεδωκοσιν τας ψυχας αυτων υπερ του ονοματος του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('4325', '44', '15', '27', 'απεσταλκαμεν ουν ιουδαν και σιλαν και αυτους δια λογου απαγγελλοντας τα αυτα');
INSERT INTO `bible_verses_tr` VALUES ('4326', '44', '15', '28', 'εδοξεν γαρ τω αγιω πνευματι και ημιν μηδεν πλεον επιτιθεσθαι υμιν βαρος πλην των επαναγκες τουτων');
INSERT INTO `bible_verses_tr` VALUES ('4327', '44', '15', '29', 'απεχεσθαι ειδωλοθυτων και αιματος και πνικτου και πορνειας εξ ων διατηρουντες εαυτους ευ πραξετε ερρωσθε');
INSERT INTO `bible_verses_tr` VALUES ('4328', '44', '15', '30', 'οι μεν ουν απολυθεντες ηλθον εις αντιοχειαν και συναγαγοντες το πληθος επεδωκαν την επιστολην');
INSERT INTO `bible_verses_tr` VALUES ('4329', '44', '15', '31', 'αναγνοντες δε εχαρησαν επι τη παρακλησει');
INSERT INTO `bible_verses_tr` VALUES ('4330', '44', '15', '32', 'ιουδας  {VAR1: τε } {VAR2: δε } και σιλας και αυτοι προφηται οντες δια λογου πολλου παρεκαλεσαν τους αδελφους και επεστηριξαν');
INSERT INTO `bible_verses_tr` VALUES ('4331', '44', '15', '33', 'ποιησαντες δε χρονον απελυθησαν μετ ειρηνης απο των αδελφων προς τους αποστολους');
INSERT INTO `bible_verses_tr` VALUES ('4332', '44', '15', '34', 'εδοξεν δε τω σιλα επιμειναι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4333', '44', '15', '35', 'παυλος δε και βαρναβας διετριβον εν αντιοχεια διδασκοντες και ευαγγελιζομενοι μετα και ετερων πολλων τον λογον του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('4334', '44', '15', '36', 'μετα δε τινας ημερας ειπεν παυλος προς βαρναβαν επιστρεψαντες δη επισκεψωμεθα τους αδελφους ημων κατα πασαν πολιν εν αις κατηγγειλαμεν τον λογον του κυριου πως εχουσιν');
INSERT INTO `bible_verses_tr` VALUES ('4335', '44', '15', '37', 'βαρναβας δε εβουλευσατο συμπαραλαβειν τον ιωαννην τον καλουμενον μαρκον');
INSERT INTO `bible_verses_tr` VALUES ('4336', '44', '15', '38', 'παυλος δε ηξιου τον αποσταντα απ αυτων απο παμφυλιας και μη συνελθοντα αυτοις εις το εργον μη συμπαραλαβειν τουτον');
INSERT INTO `bible_verses_tr` VALUES ('4337', '44', '15', '39', 'εγενετο ουν παροξυσμος ωστε αποχωρισθηναι αυτους απ αλληλων τον τε βαρναβαν παραλαβοντα τον μαρκον εκπλευσαι εις κυπρον');
INSERT INTO `bible_verses_tr` VALUES ('4338', '44', '15', '40', 'παυλος δε επιλεξαμενος σιλαν εξηλθεν παραδοθεις τη χαριτι του θεου υπο των αδελφων');
INSERT INTO `bible_verses_tr` VALUES ('4339', '44', '15', '41', 'διηρχετο δε την συριαν και κιλικιαν επιστηριζων τας εκκλησιας');
INSERT INTO `bible_verses_tr` VALUES ('4340', '44', '16', '1', 'κατηντησεν δε εις δερβην και λυστραν και ιδου μαθητης τις ην εκει ονοματι τιμοθεος υιος γυναικος τινος ιουδαιας πιστης πατρος δε ελληνος');
INSERT INTO `bible_verses_tr` VALUES ('4341', '44', '16', '2', 'ος εμαρτυρειτο υπο των εν λυστροις και ικονιω αδελφων');
INSERT INTO `bible_verses_tr` VALUES ('4342', '44', '16', '3', 'τουτον ηθελησεν ο παυλος συν αυτω εξελθειν και λαβων περιετεμεν αυτον δια τους ιουδαιους τους οντας εν τοις τοποις εκεινοις ηδεισαν γαρ απαντες τον πατερα αυτου οτι ελλην υπηρχεν');
INSERT INTO `bible_verses_tr` VALUES ('4343', '44', '16', '4', 'ως δε διεπορευοντο τας πολεις παρεδιδουν αυτοις φυλασσειν τα δογματα τα κεκριμενα υπο των αποστολων και των πρεσβυτερων των εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4344', '44', '16', '5', 'αι μεν ουν εκκλησιαι εστερεουντο τη πιστει και επερισσευον τω αριθμω καθ ημεραν');
INSERT INTO `bible_verses_tr` VALUES ('4345', '44', '16', '6', 'διελθοντες δε την φρυγιαν και την γαλατικην χωραν κωλυθεντες υπο του αγιου πνευματος λαλησαι τον λογον εν τη ασια');
INSERT INTO `bible_verses_tr` VALUES ('4346', '44', '16', '7', 'ελθοντες κατα την μυσιαν επειραζον κατα την βιθυνιαν πορευεσθαι και ουκ ειασεν αυτους το πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('4347', '44', '16', '8', 'παρελθοντες δε την μυσιαν κατεβησαν εις τρωαδα');
INSERT INTO `bible_verses_tr` VALUES ('4348', '44', '16', '9', 'και οραμα δια της νυκτος ωφθη τω παυλω ανηρ τις ην μακεδων εστως παρακαλων αυτον και λεγων διαβας εις μακεδονιαν βοηθησον ημιν');
INSERT INTO `bible_verses_tr` VALUES ('4349', '44', '16', '10', 'ως δε το οραμα ειδεν ευθεως εζητησαμεν εξελθειν εις την μακεδονιαν συμβιβαζοντες οτι προσκεκληται ημας ο κυριος ευαγγελισασθαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4350', '44', '16', '11', 'αναχθεντες ουν απο της τρωαδος ευθυδρομησαμεν εις σαμοθρακην τη τε επιουση εις νεαπολιν');
INSERT INTO `bible_verses_tr` VALUES ('4351', '44', '16', '12', 'εκειθεν τε εις φιλιππους ητις εστιν πρωτη της μεριδος της μακεδονιας πολις κολωνια ημεν δε εν ταυτη τη πολει διατριβοντες ημερας τινας');
INSERT INTO `bible_verses_tr` VALUES ('4352', '44', '16', '13', 'τη τε ημερα των σαββατων εξηλθομεν εξω της πολεως παρα ποταμον ου ενομιζετο προσευχη ειναι και καθισαντες ελαλουμεν ταις συνελθουσαις γυναιξιν');
INSERT INTO `bible_verses_tr` VALUES ('4353', '44', '16', '14', 'και τις γυνη ονοματι λυδια πορφυροπωλις πολεως θυατειρων σεβομενη τον θεον ηκουεν ης ο κυριος διηνοιξεν την καρδιαν προσεχειν τοις λαλουμενοις υπο του παυλου');
INSERT INTO `bible_verses_tr` VALUES ('4354', '44', '16', '15', 'ως δε εβαπτισθη και ο οικος αυτης παρεκαλεσεν λεγουσα ει κεκρικατε με πιστην τω κυριω ειναι εισελθοντες εις τον οικον μου μεινατε και παρεβιασατο ημας');
INSERT INTO `bible_verses_tr` VALUES ('4355', '44', '16', '16', 'εγενετο δε πορευομενων ημων εις προσευχην παιδισκην τινα εχουσαν πνευμα πυθωνος απαντησαι ημιν ητις εργασιαν πολλην παρειχεν τοις κυριοις αυτης μαντευομενη');
INSERT INTO `bible_verses_tr` VALUES ('4356', '44', '16', '17', 'αυτη κατακολουθησασα τω παυλω και ημιν εκραζεν λεγουσα ουτοι οι ανθρωποι δουλοι του θεου του υψιστου εισιν οιτινες καταγγελλουσιν ημιν οδον σωτηριας');
INSERT INTO `bible_verses_tr` VALUES ('4357', '44', '16', '18', 'τουτο δε εποιει επι πολλας ημερας διαπονηθεις δε ο παυλος και επιστρεψας τω πνευματι ειπεν παραγγελλω σοι εν τω ονοματι ιησου χριστου εξελθειν απ αυτης και εξηλθεν αυτη τη ωρα');
INSERT INTO `bible_verses_tr` VALUES ('4358', '44', '16', '19', 'ιδοντες δε οι κυριοι αυτης οτι εξηλθεν η ελπις της εργασιας αυτων επιλαβομενοι τον παυλον και τον σιλαν ειλκυσαν εις την αγοραν επι τους αρχοντας');
INSERT INTO `bible_verses_tr` VALUES ('4359', '44', '16', '20', 'και προσαγαγοντες αυτους τοις στρατηγοις ειπον ουτοι οι ανθρωποι εκταρασσουσιν ημων την πολιν ιουδαιοι υπαρχοντες');
INSERT INTO `bible_verses_tr` VALUES ('4360', '44', '16', '21', 'και καταγγελλουσιν εθη α ουκ εξεστιν ημιν παραδεχεσθαι ουδε ποιειν ρωμαιοις ουσιν');
INSERT INTO `bible_verses_tr` VALUES ('4361', '44', '16', '22', 'και συνεπεστη ο οχλος κατ αυτων και οι στρατηγοι περιρρηξαντες αυτων τα ιματια εκελευον ραβδιζειν');
INSERT INTO `bible_verses_tr` VALUES ('4362', '44', '16', '23', 'πολλας τε επιθεντες αυτοις πληγας εβαλον εις φυλακην παραγγειλαντες τω δεσμοφυλακι ασφαλως τηρειν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4363', '44', '16', '24', 'ος παραγγελιαν τοιαυτην ειληφως εβαλεν αυτους εις την εσωτεραν φυλακην και τους ποδας αυτων ησφαλισατο εις το ξυλον');
INSERT INTO `bible_verses_tr` VALUES ('4364', '44', '16', '25', 'κατα δε το μεσονυκτιον παυλος και σιλας προσευχομενοι υμνουν τον θεον επηκροωντο δε αυτων οι δεσμιοι');
INSERT INTO `bible_verses_tr` VALUES ('4365', '44', '16', '26', 'αφνω δε σεισμος εγενετο μεγας ωστε σαλευθηναι τα θεμελια του δεσμωτηριου ανεωχθησαν τε παραχρημα αι θυραι πασαι και παντων τα δεσμα ανεθη');
INSERT INTO `bible_verses_tr` VALUES ('4366', '44', '16', '27', 'εξυπνος δε γενομενος ο δεσμοφυλαξ και ιδων ανεωγμενας τας θυρας της φυλακης σπασαμενος μαχαιραν εμελλεν εαυτον αναιρειν νομιζων εκπεφευγεναι τους δεσμιους');
INSERT INTO `bible_verses_tr` VALUES ('4367', '44', '16', '28', 'εφωνησεν δε φωνη μεγαλη ο παυλος λεγων μηδεν πραξης σεαυτω κακον απαντες γαρ εσμεν ενθαδε');
INSERT INTO `bible_verses_tr` VALUES ('4368', '44', '16', '29', 'αιτησας δε φωτα εισεπηδησεν και εντρομος γενομενος προσεπεσεν τω παυλω και τω σιλα');
INSERT INTO `bible_verses_tr` VALUES ('4369', '44', '16', '30', 'και προαγαγων αυτους εξω εφη κυριοι τι με δει ποιειν ινα σωθω');
INSERT INTO `bible_verses_tr` VALUES ('4370', '44', '16', '31', 'οι δε ειπον πιστευσον επι τον κυριον ιησουν χριστον και σωθηση συ και ο οικος σου');
INSERT INTO `bible_verses_tr` VALUES ('4371', '44', '16', '32', 'και ελαλησαν αυτω τον λογον του κυριου και πασιν τοις εν τη οικια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4372', '44', '16', '33', 'και παραλαβων αυτους εν εκεινη τη ωρα της νυκτος ελουσεν απο των πληγων και εβαπτισθη αυτος και οι αυτου παντες παραχρημα');
INSERT INTO `bible_verses_tr` VALUES ('4373', '44', '16', '34', 'αναγαγων τε αυτους εις τον οικον αυτου παρεθηκεν τραπεζαν και ηγαλλιασατο πανοικι πεπιστευκως τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('4374', '44', '16', '35', 'ημερας δε γενομενης απεστειλαν οι στρατηγοι τους ραβδουχους λεγοντες απολυσον τους ανθρωπους εκεινους');
INSERT INTO `bible_verses_tr` VALUES ('4375', '44', '16', '36', 'απηγγειλεν δε ο δεσμοφυλαξ τους λογους τουτους προς τον παυλον οτι απεσταλκασιν οι στρατηγοι ινα απολυθητε νυν ουν εξελθοντες πορευεσθε εν ειρηνη');
INSERT INTO `bible_verses_tr` VALUES ('4376', '44', '16', '37', 'ο δε παυλος εφη προς αυτους δειραντες ημας δημοσια ακατακριτους ανθρωπους ρωμαιους υπαρχοντας εβαλον εις φυλακην και νυν λαθρα ημας εκβαλλουσιν ου γαρ αλλα ελθοντες αυτοι ημας εξαγαγετωσαν');
INSERT INTO `bible_verses_tr` VALUES ('4377', '44', '16', '38', 'ανηγγειλαν δε τοις στρατηγοις οι ραβδουχοι τα ρηματα ταυτα και εφοβηθησαν ακουσαντες οτι ρωμαιοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('4378', '44', '16', '39', 'και ελθοντες παρεκαλεσαν αυτους και εξαγαγοντες ηρωτων εξελθειν της πολεως');
INSERT INTO `bible_verses_tr` VALUES ('4379', '44', '16', '40', 'εξελθοντες δε εκ της φυλακης εισηλθον εις την λυδιαν και ιδοντες τους αδελφους παρεκαλεσαν αυτους και εξηλθον');
INSERT INTO `bible_verses_tr` VALUES ('4380', '44', '17', '1', 'διοδευσαντες δε την αμφιπολιν και απολλωνιαν ηλθον εις θεσσαλονικην οπου ην η συναγωγη των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('4381', '44', '17', '2', 'κατα δε το ειωθος τω παυλω εισηλθεν προς αυτους και επι σαββατα τρια διελεγετο αυτοις απο των γραφων');
INSERT INTO `bible_verses_tr` VALUES ('4382', '44', '17', '3', 'διανοιγων και παρατιθεμενος οτι τον χριστον εδει παθειν και αναστηναι εκ νεκρων και οτι ουτος εστιν ο χριστος ιησους ον εγω καταγγελλω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('4383', '44', '17', '4', 'και τινες εξ αυτων επεισθησαν και προσεκληρωθησαν τω παυλω και τω σιλα των τε σεβομενων ελληνων πολυ πληθος γυναικων τε των πρωτων ουκ ολιγαι');
INSERT INTO `bible_verses_tr` VALUES ('4384', '44', '17', '5', 'ζηλωσαντες δε οι απειθουντες ιουδαιοι και προσλαβομενοι των αγοραιων τινας ανδρας πονηρους και οχλοποιησαντες εθορυβουν την πολιν επισταντες τε τη οικια ιασονος εζητουν αυτους αγαγειν εις τον δημον');
INSERT INTO `bible_verses_tr` VALUES ('4385', '44', '17', '6', 'μη ευροντες δε αυτους εσυρον τον ιασονα και τινας αδελφους επι τους πολιταρχας βοωντες οτι οι την οικουμενην αναστατωσαντες ουτοι και ενθαδε παρεισιν');
INSERT INTO `bible_verses_tr` VALUES ('4386', '44', '17', '7', 'ους υποδεδεκται ιασων και ουτοι παντες απεναντι των δογματων καισαρος πραττουσιν βασιλεα λεγοντες ετερον ειναι ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4387', '44', '17', '8', 'εταραξαν δε τον οχλον και τους πολιταρχας ακουοντας ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('4388', '44', '17', '9', 'και λαβοντες το ικανον παρα του ιασονος και των λοιπων απελυσαν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4389', '44', '17', '10', 'οι δε αδελφοι ευθεως δια της νυκτος εξεπεμψαν τον τε παυλον και τον σιλαν εις βεροιαν οιτινες παραγενομενοι εις την συναγωγην των ιουδαιων απηεσαν');
INSERT INTO `bible_verses_tr` VALUES ('4390', '44', '17', '11', 'ουτοι δε ησαν ευγενεστεροι των εν θεσσαλονικη οιτινες εδεξαντο τον λογον μετα πασης προθυμιας το καθ ημεραν ανακρινοντες τας γραφας ει εχοι ταυτα ουτως');
INSERT INTO `bible_verses_tr` VALUES ('4391', '44', '17', '12', 'πολλοι μεν ουν εξ αυτων επιστευσαν και των ελληνιδων γυναικων των ευσχημονων και ανδρων ουκ ολιγοι');
INSERT INTO `bible_verses_tr` VALUES ('4392', '44', '17', '13', 'ως δε εγνωσαν οι απο της θεσσαλονικης ιουδαιοι οτι και εν τη βεροια κατηγγελη υπο του παυλου ο λογος του θεου ηλθον κακει σαλευοντες τους οχλους');
INSERT INTO `bible_verses_tr` VALUES ('4393', '44', '17', '14', 'ευθεως δε τοτε τον παυλον εξαπεστειλαν οι αδελφοι πορευεσθαι ως επι την θαλασσαν υπεμενον δε ο τε σιλας και ο τιμοθεος εκει');
INSERT INTO `bible_verses_tr` VALUES ('4394', '44', '17', '15', 'οι δε καθιστωντες τον παυλον ηγαγον αυτον εως αθηνων και λαβοντες εντολην προς τον σιλαν και τιμοθεον ινα ως ταχιστα ελθωσιν προς αυτον εξηεσαν');
INSERT INTO `bible_verses_tr` VALUES ('4395', '44', '17', '16', 'εν δε ταις αθηναις εκδεχομενου αυτους του παυλου παρωξυνετο το πνευμα αυτου εν αυτω θεωρουντι κατειδωλον ουσαν την πολιν');
INSERT INTO `bible_verses_tr` VALUES ('4396', '44', '17', '17', 'διελεγετο μεν ουν εν τη συναγωγη τοις ιουδαιοις και τοις σεβομενοις και εν τη αγορα κατα πασαν ημεραν προς τους παρατυγχανοντας');
INSERT INTO `bible_verses_tr` VALUES ('4397', '44', '17', '18', 'τινες δε των επικουρειων και των στωικων φιλοσοφων συνεβαλλον αυτω και τινες ελεγον τι αν θελοι ο σπερμολογος ουτος λεγειν οι δε ξενων δαιμονιων δοκει καταγγελευς ειναι οτι τον ιησουν και την αναστασιν αυτοις ευηγγελιζετο');
INSERT INTO `bible_verses_tr` VALUES ('4398', '44', '17', '19', 'επιλαβομενοι τε αυτου επι τον αρειον παγον ηγαγον λεγοντες δυναμεθα γνωναι τις η καινη αυτη η υπο σου λαλουμενη διδαχη');
INSERT INTO `bible_verses_tr` VALUES ('4399', '44', '17', '20', 'ξενιζοντα γαρ τινα εισφερεις εις τας ακοας ημων βουλομεθα ουν γνωναι τι αν θελοι ταυτα ειναι');
INSERT INTO `bible_verses_tr` VALUES ('4400', '44', '17', '21', 'αθηναιοι δε παντες και οι επιδημουντες ξενοι εις ουδεν ετερον ευκαιρουν η λεγειν τι και ακουειν καινοτερον');
INSERT INTO `bible_verses_tr` VALUES ('4401', '44', '17', '22', 'σταθεις δε ο παυλος εν μεσω του αρειου παγου εφη ανδρες αθηναιοι κατα παντα ως δεισιδαιμονεστερους υμας θεωρω');
INSERT INTO `bible_verses_tr` VALUES ('4402', '44', '17', '23', 'διερχομενος γαρ και αναθεωρων τα σεβασματα υμων ευρον και βωμον εν ω επεγεγραπτο αγνωστω θεω ον ουν αγνοουντες ευσεβειτε τουτον εγω καταγγελλω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('4403', '44', '17', '24', 'ο θεος ο ποιησας τον κοσμον και παντα τα εν αυτω ουτος ουρανου και γης κυριος υπαρχων ουκ εν χειροποιητοις ναοις κατοικει');
INSERT INTO `bible_verses_tr` VALUES ('4404', '44', '17', '25', 'ουδε υπο χειρων ανθρωπων θεραπευεται προσδεομενος τινος αυτος διδους πασιν ζωην και πνοην  {VAR1: κατα } {VAR2: και τα } παντα');
INSERT INTO `bible_verses_tr` VALUES ('4405', '44', '17', '26', 'εποιησεν τε εξ ενος αιματος παν εθνος ανθρωπων κατοικειν επι παν το προσωπον της γης ορισας προτεταγμενους καιρους και τας οροθεσιας της κατοικιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4406', '44', '17', '27', 'ζητειν τον κυριον ει αρα γε ψηλαφησειαν αυτον και ευροιεν καιτοιγε ου μακραν απο ενος εκαστου ημων υπαρχοντα');
INSERT INTO `bible_verses_tr` VALUES ('4407', '44', '17', '28', 'εν αυτω γαρ ζωμεν και κινουμεθα και εσμεν ως και τινες των καθ υμας ποιητων ειρηκασιν του γαρ και γενος εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('4408', '44', '17', '29', 'γενος ουν υπαρχοντες του θεου ουκ οφειλομεν νομιζειν χρυσω η αργυρω η λιθω χαραγματι τεχνης και ενθυμησεως ανθρωπου το θειον ειναι ομοιον');
INSERT INTO `bible_verses_tr` VALUES ('4409', '44', '17', '30', 'τους μεν ουν χρονους της αγνοιας υπεριδων ο θεος τα νυν παραγγελλει τοις ανθρωποις πασιν πανταχου μετανοειν');
INSERT INTO `bible_verses_tr` VALUES ('4410', '44', '17', '31', 'διοτι εστησεν ημεραν εν η μελλει κρινειν την οικουμενην εν δικαιοσυνη εν ανδρι ω ωρισεν πιστιν παρασχων πασιν αναστησας αυτον εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('4411', '44', '17', '32', 'ακουσαντες δε αναστασιν νεκρων οι μεν εχλευαζον οι δε ειπον ακουσομεθα σου παλιν περι τουτου');
INSERT INTO `bible_verses_tr` VALUES ('4412', '44', '17', '33', 'και ουτως ο παυλος εξηλθεν εκ μεσου αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4413', '44', '17', '34', 'τινες δε ανδρες κολληθεντες αυτω επιστευσαν εν οις και διονυσιος ο αρεοπαγιτης και γυνη ονοματι δαμαρις και ετεροι συν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4414', '44', '18', '1', 'μετα δε ταυτα χωρισθεις ο παυλος εκ των αθηνων ηλθεν εις κορινθον');
INSERT INTO `bible_verses_tr` VALUES ('4415', '44', '18', '2', 'και ευρων τινα ιουδαιον ονοματι ακυλαν ποντικον τω γενει προσφατως εληλυθοτα απο της ιταλιας και πρισκιλλαν γυναικα αυτου δια το διατεταχεναι κλαυδιον χωριζεσθαι παντας τους ιουδαιους εκ της ρωμης προσηλθεν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4416', '44', '18', '3', 'και δια το ομοτεχνον ειναι εμενεν παρ αυτοις και ειργαζετο ησαν γαρ σκηνοποιοι την τεχνην');
INSERT INTO `bible_verses_tr` VALUES ('4417', '44', '18', '4', 'διελεγετο δε εν τη συναγωγη κατα παν σαββατον επειθεν τε ιουδαιους και ελληνας');
INSERT INTO `bible_verses_tr` VALUES ('4418', '44', '18', '5', 'ως δε κατηλθον απο της μακεδονιας ο τε σιλας και ο τιμοθεος συνειχετο τω πνευματι ο παυλος διαμαρτυρομενος τοις ιουδαιοις τον χριστον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4419', '44', '18', '6', 'αντιτασσομενων δε αυτων και βλασφημουντων εκτιναξαμενος τα ιματια ειπεν προς αυτους το αιμα υμων επι την κεφαλην υμων καθαρος εγω απο του νυν εις τα εθνη πορευσομαι');
INSERT INTO `bible_verses_tr` VALUES ('4420', '44', '18', '7', 'και μεταβας εκειθεν ηλθεν εις οικιαν τινος ονοματι ιουστου σεβομενου τον θεον ου η οικια ην συνομορουσα τη συναγωγη');
INSERT INTO `bible_verses_tr` VALUES ('4421', '44', '18', '8', 'κρισπος δε ο αρχισυναγωγος επιστευσεν τω κυριω συν ολω τω οικω αυτου και πολλοι των κορινθιων ακουοντες επιστευον και εβαπτιζοντο');
INSERT INTO `bible_verses_tr` VALUES ('4422', '44', '18', '9', 'ειπεν δε ο κυριος δι οραματος εν νυκτι τω παυλω μη φοβου αλλα λαλει και μη σιωπησης');
INSERT INTO `bible_verses_tr` VALUES ('4423', '44', '18', '10', 'διοτι εγω ειμι μετα σου και ουδεις επιθησεται σοι του κακωσαι σε διοτι λαος εστιν μοι πολυς εν τη πολει ταυτη');
INSERT INTO `bible_verses_tr` VALUES ('4424', '44', '18', '11', 'εκαθισεν τε ενιαυτον και μηνας εξ διδασκων εν αυτοις τον λογον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4425', '44', '18', '12', 'γαλλιωνος δε ανθυπατευοντος της αχαιας κατεπεστησαν ομοθυμαδον οι ιουδαιοι τω παυλω και ηγαγον αυτον επι το βημα');
INSERT INTO `bible_verses_tr` VALUES ('4426', '44', '18', '13', 'λεγοντες οτι παρα τον νομον ουτος αναπειθει τους ανθρωπους σεβεσθαι τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('4427', '44', '18', '14', 'μελλοντος δε του παυλου ανοιγειν το στομα ειπεν ο γαλλιων προς τους ιουδαιους ει μεν ουν ην αδικημα τι η ραδιουργημα πονηρον ω ιουδαιοι κατα λογον αν ηνεσχομην υμων');
INSERT INTO `bible_verses_tr` VALUES ('4428', '44', '18', '15', 'ει δε ζητημα εστιν περι λογου και ονοματων και νομου του καθ υμας οψεσθε αυτοι κριτης γαρ εγω τουτων ου βουλομαι ειναι');
INSERT INTO `bible_verses_tr` VALUES ('4429', '44', '18', '16', 'και απηλασεν αυτους απο του βηματος');
INSERT INTO `bible_verses_tr` VALUES ('4430', '44', '18', '17', 'επιλαβομενοι δε παντες οι ελληνες σωσθενην τον αρχισυναγωγον ετυπτον εμπροσθεν του βηματος και ουδεν τουτων τω γαλλιωνι εμελεν');
INSERT INTO `bible_verses_tr` VALUES ('4431', '44', '18', '18', 'ο δε παυλος ετι προσμεινας ημερας ικανας τοις αδελφοις αποταξαμενος εξεπλει εις την συριαν και συν αυτω πρισκιλλα και ακυλας κειραμενος την κεφαλην εν κεγχρεαις ειχεν γαρ ευχην');
INSERT INTO `bible_verses_tr` VALUES ('4432', '44', '18', '19', 'κατηντησεν δε εις εφεσον κακεινους κατελιπεν αυτου αυτος δε εισελθων εις την συναγωγην διελεχθη τοις ιουδαιοις');
INSERT INTO `bible_verses_tr` VALUES ('4433', '44', '18', '20', 'ερωτωντων δε αυτων επι πλειονα χρονον μειναι παρ αυτοις ουκ επενευσεν');
INSERT INTO `bible_verses_tr` VALUES ('4434', '44', '18', '21', 'αλλ απεταξατο αυτοις ειπων δει με παντως την εορτην την ερχομενην ποιησαι εις ιεροσολυμα παλιν δε ανακαμψω προς υμας του θεου θελοντος και ανηχθη απο της εφεσου');
INSERT INTO `bible_verses_tr` VALUES ('4435', '44', '18', '22', 'και κατελθων εις καισαρειαν αναβας και ασπασαμενος την εκκλησιαν κατεβη εις αντιοχειαν');
INSERT INTO `bible_verses_tr` VALUES ('4436', '44', '18', '23', 'και ποιησας χρονον τινα εξηλθεν διερχομενος καθεξης την γαλατικην χωραν και φρυγιαν επιστηριζων παντας τους μαθητας');
INSERT INTO `bible_verses_tr` VALUES ('4437', '44', '18', '24', 'ιουδαιος δε τις απολλως ονοματι αλεξανδρευς τω γενει ανηρ λογιος κατηντησεν εις εφεσον δυνατος ων εν ταις γραφαις');
INSERT INTO `bible_verses_tr` VALUES ('4438', '44', '18', '25', 'ουτος ην κατηχημενος την οδον του κυριου και ζεων τω πνευματι ελαλει και εδιδασκεν ακριβως τα περι του κυριου επισταμενος μονον το βαπτισμα ιωαννου');
INSERT INTO `bible_verses_tr` VALUES ('4439', '44', '18', '26', 'ουτος τε ηρξατο παρρησιαζεσθαι εν τη συναγωγη ακουσαντες δε αυτου ακυλας και πρισκιλλα προσελαβοντο αυτον και ακριβεστερον αυτω εξεθεντο την του θεου οδον');
INSERT INTO `bible_verses_tr` VALUES ('4440', '44', '18', '27', 'βουλομενου δε αυτου διελθειν εις την αχαιαν προτρεψαμενοι οι αδελφοι εγραψαν τοις μαθηταις αποδεξασθαι αυτον ος παραγενομενος συνεβαλετο πολυ τοις πεπιστευκοσιν δια της χαριτος');
INSERT INTO `bible_verses_tr` VALUES ('4441', '44', '18', '28', 'ευτονως γαρ τοις ιουδαιοις διακατηλεγχετο δημοσια επιδεικνυς δια των γραφων ειναι τον χριστον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4442', '44', '19', '1', 'εγενετο δε εν τω τον απολλω ειναι εν κορινθω παυλον διελθοντα τα ανωτερικα μερη ελθειν εις εφεσον και ευρων τινας μαθητας');
INSERT INTO `bible_verses_tr` VALUES ('4443', '44', '19', '2', 'ειπεν προς αυτους ει πνευμα αγιον ελαβετε πιστευσαντες οι δε ειπον προς αυτον αλλ ουδε ει πνευμα αγιον εστιν ηκουσαμεν');
INSERT INTO `bible_verses_tr` VALUES ('4444', '44', '19', '3', 'ειπεν τε προς αυτους εις τι ουν εβαπτισθητε οι δε ειπον εις το ιωαννου βαπτισμα');
INSERT INTO `bible_verses_tr` VALUES ('4445', '44', '19', '4', 'ειπεν δε παυλος ιωαννης μεν εβαπτισεν βαπτισμα μετανοιας τω λαω λεγων εις τον ερχομενον μετ αυτον ινα πιστευσωσιν τουτ εστιν εις τον χριστον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('4446', '44', '19', '5', 'ακουσαντες δε εβαπτισθησαν εις το ονομα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('4447', '44', '19', '6', 'και επιθεντος αυτοις του παυλου τας χειρας ηλθεν το πνευμα το αγιον επ αυτους ελαλουν τε γλωσσαις και προεφητευον');
INSERT INTO `bible_verses_tr` VALUES ('4448', '44', '19', '7', 'ησαν δε οι παντες ανδρες ωσει δεκαδυο');
INSERT INTO `bible_verses_tr` VALUES ('4449', '44', '19', '8', 'εισελθων δε εις την συναγωγην επαρρησιαζετο επι μηνας τρεις διαλεγομενος και πειθων τα περι της βασιλειας του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4450', '44', '19', '9', 'ως δε τινες εσκληρυνοντο και ηπειθουν κακολογουντες την οδον ενωπιον του πληθους αποστας απ αυτων αφωρισεν τους μαθητας καθ ημεραν διαλεγομενος εν τη σχολη τυραννου τινος');
INSERT INTO `bible_verses_tr` VALUES ('4451', '44', '19', '10', 'τουτο δε εγενετο επι ετη δυο ωστε παντας τους κατοικουντας την ασιαν ακουσαι τον λογον του κυριου ιησου ιουδαιους τε και ελληνας');
INSERT INTO `bible_verses_tr` VALUES ('4452', '44', '19', '11', 'δυναμεις τε ου τας τυχουσας εποιει ο θεος δια των χειρων παυλου');
INSERT INTO `bible_verses_tr` VALUES ('4453', '44', '19', '12', 'ωστε και επι τους ασθενουντας επιφερεσθαι απο του χρωτος αυτου σουδαρια η σιμικινθια και απαλλασσεσθαι απ αυτων τας νοσους τα τε πνευματα τα πονηρα εξερχεσθαι απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4454', '44', '19', '13', 'επεχειρησαν δε τινες απο των περιερχομενων ιουδαιων εξορκιστων ονομαζειν επι τους εχοντας τα πνευματα τα πονηρα το ονομα του κυριου ιησου λεγοντες ορκιζομεν υμας τον ιησουν ον ο παυλος κηρυσσει');
INSERT INTO `bible_verses_tr` VALUES ('4455', '44', '19', '14', 'ησαν δε τινες υιοι σκευα ιουδαιου αρχιερεως επτα οι τουτο ποιουντες');
INSERT INTO `bible_verses_tr` VALUES ('4456', '44', '19', '15', 'αποκριθεν δε το πνευμα το πονηρον ειπεν τον ιησουν γινωσκω και τον παυλον επισταμαι υμεις δε τινες εστε');
INSERT INTO `bible_verses_tr` VALUES ('4457', '44', '19', '16', 'και εφαλλομενος επ αυτους ο ανθρωπος εν ω ην το πνευμα το πονηρον και κατακυριευσας αυτων ισχυσεν κατ αυτων ωστε γυμνους και τετραυματισμενους εκφυγειν εκ του οικου εκεινου');
INSERT INTO `bible_verses_tr` VALUES ('4458', '44', '19', '17', 'τουτο δε εγενετο γνωστον πασιν ιουδαιοις τε και ελλησιν τοις κατοικουσιν την εφεσον και επεπεσεν φοβος επι παντας αυτους και εμεγαλυνετο το ονομα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('4459', '44', '19', '18', 'πολλοι τε των πεπιστευκοτων ηρχοντο εξομολογουμενοι και αναγγελλοντες τας πραξεις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4460', '44', '19', '19', 'ικανοι δε των τα περιεργα πραξαντων συνενεγκαντες τας βιβλους κατεκαιον ενωπιον παντων και συνεψηφισαν τας τιμας αυτων και ευρον αργυριου μυριαδας πεντε');
INSERT INTO `bible_verses_tr` VALUES ('4461', '44', '19', '20', 'ουτως κατα κρατος ο λογος του κυριου ηυξανεν και ισχυεν');
INSERT INTO `bible_verses_tr` VALUES ('4462', '44', '19', '21', 'ως δε επληρωθη ταυτα εθετο ο παυλος εν τω πνευματι διελθων την μακεδονιαν και αχαιαν πορευεσθαι εις ιερουσαλημ ειπων οτι μετα το γενεσθαι με εκει δει με και ρωμην ιδειν');
INSERT INTO `bible_verses_tr` VALUES ('4463', '44', '19', '22', 'αποστειλας δε εις την μακεδονιαν δυο των διακονουντων αυτω τιμοθεον και εραστον αυτος επεσχεν χρονον εις την ασιαν');
INSERT INTO `bible_verses_tr` VALUES ('4464', '44', '19', '23', 'εγενετο δε κατα τον καιρον εκεινον ταραχος ουκ ολιγος περι της οδου');
INSERT INTO `bible_verses_tr` VALUES ('4465', '44', '19', '24', 'δημητριος γαρ τις ονοματι αργυροκοπος ποιων ναους αργυρους αρτεμιδος παρειχετο τοις τεχνιταις εργασιαν ουκ ολιγην');
INSERT INTO `bible_verses_tr` VALUES ('4466', '44', '19', '25', 'ους συναθροισας και τους περι τα τοιαυτα εργατας ειπεν ανδρες επιστασθε οτι εκ ταυτης της εργασιας η ευπορια ημων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4467', '44', '19', '26', 'και θεωρειτε και ακουετε οτι ου μονον εφεσου αλλα σχεδον πασης της ασιας ο παυλος ουτος πεισας μετεστησεν ικανον οχλον λεγων οτι ουκ εισιν θεοι οι δια χειρων γινομενοι');
INSERT INTO `bible_verses_tr` VALUES ('4468', '44', '19', '27', 'ου μονον δε τουτο κινδυνευει ημιν το μερος εις απελεγμον ελθειν αλλα και το της μεγαλης θεας αρτεμιδος ιερον εις ουδεν λογισθηναι μελλειν  {VAR1: δε } {VAR2: τε } και καθαιρεισθαι την μεγαλειοτητα αυτης ην ολη η ασια και η οικουμενη σεβεται');
INSERT INTO `bible_verses_tr` VALUES ('4469', '44', '19', '28', 'ακουσαντες δε και γενομενοι πληρεις θυμου εκραζον λεγοντες μεγαλη η αρτεμις εφεσιων');
INSERT INTO `bible_verses_tr` VALUES ('4470', '44', '19', '29', 'και επλησθη η πολις ολη συγχυσεως ωρμησαν τε ομοθυμαδον εις το θεατρον συναρπασαντες γαιον και αρισταρχον μακεδονας συνεκδημους του παυλου');
INSERT INTO `bible_verses_tr` VALUES ('4471', '44', '19', '30', 'του δε παυλου βουλομενου εισελθειν εις τον δημον ουκ ειων αυτον οι μαθηται');
INSERT INTO `bible_verses_tr` VALUES ('4472', '44', '19', '31', 'τινες δε και των ασιαρχων οντες αυτω φιλοι πεμψαντες προς αυτον παρεκαλουν μη δουναι εαυτον εις το θεατρον');
INSERT INTO `bible_verses_tr` VALUES ('4473', '44', '19', '32', 'αλλοι μεν ουν αλλο τι εκραζον ην γαρ η εκκλησια συγκεχυμενη και οι πλειους ουκ ηδεισαν τινος ενεκεν συνεληλυθεισαν');
INSERT INTO `bible_verses_tr` VALUES ('4474', '44', '19', '33', 'εκ δε του οχλου προεβιβασαν αλεξανδρον  {VAR1: προβαλοντων } {VAR2: προβαλλοντων } αυτον των ιουδαιων ο δε αλεξανδρος κατασεισας την χειρα ηθελεν απολογεισθαι τω δημω');
INSERT INTO `bible_verses_tr` VALUES ('4475', '44', '19', '34', 'επιγνοντων δε οτι ιουδαιος εστιν φωνη εγενετο μια εκ παντων ως επι ωρας δυο κραζοντων μεγαλη η αρτεμις εφεσιων');
INSERT INTO `bible_verses_tr` VALUES ('4476', '44', '19', '35', 'καταστειλας δε ο γραμματευς τον οχλον φησιν ανδρες εφεσιοι τις γαρ εστιν ανθρωπος ος ου γινωσκει την εφεσιων πολιν νεωκορον ουσαν της μεγαλης θεας αρτεμιδος και του διοπετους');
INSERT INTO `bible_verses_tr` VALUES ('4477', '44', '19', '36', 'αναντιρρητων ουν οντων τουτων δεον εστιν υμας κατεσταλμενους υπαρχειν και μηδεν προπετες πραττειν');
INSERT INTO `bible_verses_tr` VALUES ('4478', '44', '19', '37', 'ηγαγετε γαρ τους ανδρας τουτους ουτε ιεροσυλους ουτε βλασφημουντας την θεαν υμων');
INSERT INTO `bible_verses_tr` VALUES ('4479', '44', '19', '38', 'ει μεν ουν δημητριος και οι συν αυτω τεχνιται προς τινα λογον εχουσιν αγοραιοι αγονται και ανθυπατοι εισιν εγκαλειτωσαν αλληλοις');
INSERT INTO `bible_verses_tr` VALUES ('4480', '44', '19', '39', 'ει δε τι περι ετερων επιζητειτε εν τη εννομω εκκλησια επιλυθησεται');
INSERT INTO `bible_verses_tr` VALUES ('4481', '44', '19', '40', 'και γαρ κινδυνευομεν εγκαλεισθαι στασεως περι της σημερον μηδενος αιτιου υπαρχοντος περι ου δυνησομεθα αποδουναι λογον της συστροφης ταυτης');
INSERT INTO `bible_verses_tr` VALUES ('4482', '44', '19', '41', 'και ταυτα ειπων απελυσεν την εκκλησιαν');
INSERT INTO `bible_verses_tr` VALUES ('4483', '44', '20', '1', 'μετα δε το παυσασθαι τον θορυβον προσκαλεσαμενος ο παυλος τους μαθητας και ασπασαμενος εξηλθεν πορευθηναι εις την μακεδονιαν');
INSERT INTO `bible_verses_tr` VALUES ('4484', '44', '20', '2', 'διελθων δε τα μερη εκεινα και παρακαλεσας αυτους λογω πολλω ηλθεν εις την ελλαδα');
INSERT INTO `bible_verses_tr` VALUES ('4485', '44', '20', '3', 'ποιησας τε μηνας τρεις γενομενης αυτω επιβουλης υπο των ιουδαιων μελλοντι αναγεσθαι εις την συριαν εγενετο γνωμη του υποστρεφειν δια μακεδονιας');
INSERT INTO `bible_verses_tr` VALUES ('4486', '44', '20', '4', 'συνειπετο δε αυτω αχρι της ασιας σωπατρος βεροιαιος θεσσαλονικεων δε αρισταρχος και σεκουνδος και γαιος δερβαιος και τιμοθεος ασιανοι δε τυχικος και τροφιμος');
INSERT INTO `bible_verses_tr` VALUES ('4487', '44', '20', '5', 'ουτοι προελθοντες εμενον ημας εν τρωαδι');
INSERT INTO `bible_verses_tr` VALUES ('4488', '44', '20', '6', 'ημεις δε εξεπλευσαμεν μετα τας ημερας των αζυμων απο φιλιππων και ηλθομεν προς αυτους εις την τρωαδα αχρις ημερων πεντε ου διετριψαμεν ημερας επτα');
INSERT INTO `bible_verses_tr` VALUES ('4489', '44', '20', '7', 'εν δε τη μια των σαββατων συνηγμενων των μαθητων του κλασαι αρτον ο παυλος διελεγετο αυτοις μελλων εξιεναι τη επαυριον παρετεινεν τε τον λογον μεχρι μεσονυκτιου');
INSERT INTO `bible_verses_tr` VALUES ('4490', '44', '20', '8', 'ησαν δε λαμπαδες ικαναι εν τω υπερωω ου ησαν συνηγμενοι');
INSERT INTO `bible_verses_tr` VALUES ('4491', '44', '20', '9', 'καθημενος δε τις νεανιας ονοματι ευτυχος επι της θυριδος καταφερομενος υπνω βαθει διαλεγομενου του παυλου επι πλειον κατενεχθεις απο του υπνου επεσεν απο του τριστεγου κατω και ηρθη νεκρος');
INSERT INTO `bible_verses_tr` VALUES ('4492', '44', '20', '10', 'καταβας δε ο παυλος επεπεσεν αυτω και συμπεριλαβων ειπεν μη θορυβεισθε η γαρ ψυχη αυτου εν αυτω εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4493', '44', '20', '11', 'αναβας δε και κλασας αρτον και γευσαμενος εφ ικανον τε ομιλησας αχρις αυγης ουτως εξηλθεν');
INSERT INTO `bible_verses_tr` VALUES ('4494', '44', '20', '12', 'ηγαγον δε τον παιδα ζωντα και παρεκληθησαν ου μετριως');
INSERT INTO `bible_verses_tr` VALUES ('4495', '44', '20', '13', 'ημεις δε προελθοντες επι το πλοιον ανηχθημεν εις την ασσον εκειθεν μελλοντες αναλαμβανειν τον παυλον ουτως γαρ ην διατεταγμενος μελλων αυτος πεζευειν');
INSERT INTO `bible_verses_tr` VALUES ('4496', '44', '20', '14', 'ως δε συνεβαλεν ημιν εις την ασσον αναλαβοντες αυτον ηλθομεν εις μιτυληνην');
INSERT INTO `bible_verses_tr` VALUES ('4497', '44', '20', '15', 'κακειθεν αποπλευσαντες τη επιουση κατηντησαμεν αντικρυ χιου τη δε ετερα παρεβαλομεν εις σαμον και μειναντες εν τρωγυλλιω τη εχομενη ηλθομεν εις μιλητον');
INSERT INTO `bible_verses_tr` VALUES ('4498', '44', '20', '16', 'εκρινεν γαρ ο παυλος παραπλευσαι την εφεσον οπως μη γενηται αυτω χρονοτριβησαι εν τη ασια εσπευδεν γαρ ει δυνατον ην αυτω την ημεραν της πεντηκοστης γενεσθαι εις ιεροσολυμα');
INSERT INTO `bible_verses_tr` VALUES ('4499', '44', '20', '17', 'απο δε της μιλητου πεμψας εις εφεσον μετεκαλεσατο τους πρεσβυτερους της εκκλησιας');
INSERT INTO `bible_verses_tr` VALUES ('4500', '44', '20', '18', 'ως δε παρεγενοντο προς αυτον ειπεν αυτοις υμεις επιστασθε απο πρωτης ημερας αφ ης επεβην εις την ασιαν πως μεθ υμων τον παντα χρονον εγενομην');
INSERT INTO `bible_verses_tr` VALUES ('4501', '44', '20', '19', 'δουλευων τω κυριω μετα πασης ταπεινοφροσυνης και πολλων δακρυων και πειρασμων των συμβαντων μοι εν ταις επιβουλαις των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('4502', '44', '20', '20', 'ως ουδεν υπεστειλαμην των συμφεροντων του μη αναγγειλαι υμιν και διδαξαι υμας δημοσια και κατ οικους');
INSERT INTO `bible_verses_tr` VALUES ('4503', '44', '20', '21', 'διαμαρτυρομενος ιουδαιοις τε και ελλησιν την εις τον θεον μετανοιαν και πιστιν την εις τον κυριον ημων ιησουν χριστον');
INSERT INTO `bible_verses_tr` VALUES ('4504', '44', '20', '22', 'και νυν ιδου εγω δεδεμενος τω πνευματι πορευομαι εις ιερουσαλημ τα εν αυτη συναντησοντα μοι μη ειδως');
INSERT INTO `bible_verses_tr` VALUES ('4505', '44', '20', '23', 'πλην οτι το πνευμα το αγιον κατα πολιν διαμαρτυρεται λεγον οτι δεσμα με και θλιψεις μενουσιν');
INSERT INTO `bible_verses_tr` VALUES ('4506', '44', '20', '24', 'αλλ ουδενος λογον ποιουμαι ουδε εχω την ψυχην μου τιμιαν εμαυτω ως τελειωσαι τον δρομον μου μετα χαρας και την διακονιαν ην ελαβον παρα του κυριου ιησου διαμαρτυρασθαι το ευαγγελιον της χαριτος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4507', '44', '20', '25', 'και νυν ιδου εγω οιδα οτι ουκετι οψεσθε το προσωπον μου υμεις παντες εν οις διηλθον κηρυσσων την βασιλειαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4508', '44', '20', '26', 'διο μαρτυρομαι υμιν εν τη σημερον ημερα οτι καθαρος εγω απο του αιματος παντων');
INSERT INTO `bible_verses_tr` VALUES ('4509', '44', '20', '27', 'ου γαρ υπεστειλαμην του μη αναγγειλαι υμιν πασαν την βουλην του θεου');
INSERT INTO `bible_verses_tr` VALUES ('4510', '44', '20', '28', 'προσεχετε ουν εαυτοις και παντι τω ποιμνιω εν ω υμας το πνευμα το αγιον εθετο επισκοπους ποιμαινειν την εκκλησιαν του θεου ην περιεποιησατο δια του ιδιου αιματος');
INSERT INTO `bible_verses_tr` VALUES ('4511', '44', '20', '29', 'εγω γαρ οιδα τουτο οτι εισελευσονται μετα την αφιξιν μου λυκοι βαρεις εις υμας μη φειδομενοι του ποιμνιου');
INSERT INTO `bible_verses_tr` VALUES ('4512', '44', '20', '30', 'και εξ υμων αυτων αναστησονται ανδρες λαλουντες διεστραμμενα του αποσπαν τους μαθητας οπισω αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4513', '44', '20', '31', 'διο γρηγορειτε μνημονευοντες οτι τριετιαν νυκτα και ημεραν ουκ επαυσαμην μετα δακρυων νουθετων ενα εκαστον');
INSERT INTO `bible_verses_tr` VALUES ('4514', '44', '20', '32', 'και  {VAR1: τανυν } {VAR2: τα νυν } παρατιθεμαι υμας αδελφοι τω θεω και τω λογω της χαριτος αυτου τω δυναμενω εποικοδομησαι και δουναι υμιν κληρονομιαν εν τοις ηγιασμενοις πασιν');
INSERT INTO `bible_verses_tr` VALUES ('4515', '44', '20', '33', 'αργυριου η χρυσιου η ιματισμου ουδενος επεθυμησα');
INSERT INTO `bible_verses_tr` VALUES ('4516', '44', '20', '34', 'αυτοι δε γινωσκετε οτι ταις χρειαις μου και τοις ουσιν μετ εμου υπηρετησαν αι χειρες αυται');
INSERT INTO `bible_verses_tr` VALUES ('4517', '44', '20', '35', 'παντα υπεδειξα υμιν οτι ουτως κοπιωντας δει αντιλαμβανεσθαι των ασθενουντων μνημονευειν τε των λογων του κυριου ιησου οτι αυτος ειπεν μακαριον εστιν διδοναι μαλλον η λαμβανειν');
INSERT INTO `bible_verses_tr` VALUES ('4518', '44', '20', '36', 'και ταυτα ειπων θεις τα γονατα αυτου συν πασιν αυτοις προσηυξατο');
INSERT INTO `bible_verses_tr` VALUES ('4519', '44', '20', '37', 'ικανος δε εγενετο κλαυθμος παντων και επιπεσοντες επι τον τραχηλον του παυλου κατεφιλουν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4520', '44', '20', '38', 'οδυνωμενοι μαλιστα επι τω λογω ω ειρηκει οτι ουκετι μελλουσιν το προσωπον αυτου θεωρειν προεπεμπον δε αυτον εις το πλοιον');
INSERT INTO `bible_verses_tr` VALUES ('4521', '44', '21', '1', 'ως δε εγενετο αναχθηναι ημας αποσπασθεντας απ αυτων ευθυδρομησαντες ηλθομεν εις την κων τη δε εξης εις την ροδον κακειθεν εις παταρα');
INSERT INTO `bible_verses_tr` VALUES ('4522', '44', '21', '2', 'και ευροντες πλοιον διαπερων εις φοινικην επιβαντες ανηχθημεν');
INSERT INTO `bible_verses_tr` VALUES ('4523', '44', '21', '3', 'αναφαναντες δε την κυπρον και καταλιποντες αυτην ευωνυμον επλεομεν εις συριαν και κατηχθημεν εις τυρον εκεισε γαρ ην το πλοιον αποφορτιζομενον τον γομον');
INSERT INTO `bible_verses_tr` VALUES ('4524', '44', '21', '4', 'και ανευροντες  {VAR1: τους } μαθητας επεμειναμεν αυτου ημερας επτα οιτινες τω παυλω ελεγον δια του πνευματος μη αναβαινειν εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4525', '44', '21', '5', 'οτε δε εγενετο ημας εξαρτισαι τας ημερας εξελθοντες επορευομεθα προπεμποντων ημας παντων συν γυναιξιν και τεκνοις εως εξω της πολεως και θεντες τα γονατα επι τον αιγιαλον προσηυξαμεθα');
INSERT INTO `bible_verses_tr` VALUES ('4526', '44', '21', '6', 'και ασπασαμενοι αλληλους επεβημεν εις το πλοιον εκεινοι δε υπεστρεψαν εις τα ιδια');
INSERT INTO `bible_verses_tr` VALUES ('4527', '44', '21', '7', 'ημεις δε τον πλουν διανυσαντες απο τυρου κατηντησαμεν εις πτολεμαιδα και ασπασαμενοι τους αδελφους εμειναμεν ημεραν μιαν παρ αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4528', '44', '21', '8', 'τη δε επαυριον εξελθοντες οι περι τον παυλον  {VAR1: ηλθον } {VAR2: ηλθομεν } εις καισαρειαν και εισελθοντες εις τον οικον φιλιππου του ευαγγελιστου του οντος εκ των επτα εμειναμεν παρ αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4529', '44', '21', '9', 'τουτω δε ησαν θυγατερες παρθενοι τεσσαρες προφητευουσαι');
INSERT INTO `bible_verses_tr` VALUES ('4530', '44', '21', '10', 'επιμενοντων δε ημων ημερας πλειους κατηλθεν τις απο της ιουδαιας προφητης ονοματι αγαβος');
INSERT INTO `bible_verses_tr` VALUES ('4531', '44', '21', '11', 'και ελθων προς ημας και αρας την ζωνην του παυλου δησας τε αυτου τας χειρας και τους ποδας ειπεν ταδε λεγει το πνευμα το αγιον τον ανδρα ου εστιν η ζωνη αυτη ουτως δησουσιν εν ιερουσαλημ οι ιουδαιοι και παραδωσουσιν εις χειρας εθνων');
INSERT INTO `bible_verses_tr` VALUES ('4532', '44', '21', '12', 'ως δε ηκουσαμεν ταυτα παρεκαλουμεν ημεις τε και οι εντοπιοι του μη αναβαινειν αυτον εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4533', '44', '21', '13', 'απεκριθη δε ο παυλος τι ποιειτε κλαιοντες και συνθρυπτοντες μου την καρδιαν εγω γαρ ου μονον δεθηναι αλλα και αποθανειν εις ιερουσαλημ ετοιμως εχω υπερ του ονοματος του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('4534', '44', '21', '14', 'μη πειθομενου δε αυτου ησυχασαμεν ειποντες το θελημα του κυριου γενεσθω');
INSERT INTO `bible_verses_tr` VALUES ('4535', '44', '21', '15', 'μετα δε τας ημερας ταυτας αποσκευασαμενοι ανεβαινομεν εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4536', '44', '21', '16', 'συνηλθον δε και των μαθητων απο καισαρειας συν ημιν αγοντες παρ ω ξενισθωμεν μνασωνι τινι κυπριω αρχαιω μαθητη');
INSERT INTO `bible_verses_tr` VALUES ('4537', '44', '21', '17', 'γενομενων δε ημων εις ιεροσολυμα ασμενως εδεξαντο ημας οι αδελφοι');
INSERT INTO `bible_verses_tr` VALUES ('4538', '44', '21', '18', 'τη δε επιουση εισηει ο παυλος συν ημιν προς ιακωβον παντες τε παρεγενοντο οι πρεσβυτεροι');
INSERT INTO `bible_verses_tr` VALUES ('4539', '44', '21', '19', 'και ασπασαμενος αυτους εξηγειτο καθ εν εκαστον ων εποιησεν ο θεος εν τοις εθνεσιν δια της διακονιας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4540', '44', '21', '20', 'οι δε ακουσαντες εδοξαζον τον κυριον ειπον τε αυτω θεωρεις αδελφε ποσαι μυριαδες εισιν ιουδαιων των πεπιστευκοτων και παντες ζηλωται του νομου υπαρχουσιν');
INSERT INTO `bible_verses_tr` VALUES ('4541', '44', '21', '21', 'κατηχηθησαν δε περι σου οτι αποστασιαν διδασκεις απο μωσεως τους κατα τα εθνη παντας ιουδαιους λεγων μη περιτεμνειν αυτους τα τεκνα μηδε τοις εθεσιν περιπατειν');
INSERT INTO `bible_verses_tr` VALUES ('4542', '44', '21', '22', 'τι ουν εστιν παντως δει πληθος συνελθειν ακουσονται γαρ οτι εληλυθας');
INSERT INTO `bible_verses_tr` VALUES ('4543', '44', '21', '23', 'τουτο ουν ποιησον ο σοι λεγομεν εισιν ημιν ανδρες τεσσαρες ευχην εχοντες εφ εαυτων');
INSERT INTO `bible_verses_tr` VALUES ('4544', '44', '21', '24', 'τουτους παραλαβων αγνισθητι συν αυτοις και δαπανησον επ αυτοις ινα ξυρησωνται την κεφαλην και γνωσιν παντες οτι ων κατηχηνται περι σου ουδεν εστιν αλλα στοιχεις και αυτος τον νομον φυλασσων');
INSERT INTO `bible_verses_tr` VALUES ('4545', '44', '21', '25', 'περι δε των πεπιστευκοτων εθνων ημεις επεστειλαμεν κριναντες μηδεν τοιουτον τηρειν αυτους ει μη φυλασσεσθαι αυτους το τε ειδωλοθυτον και το αιμα και πνικτον και πορνειαν');
INSERT INTO `bible_verses_tr` VALUES ('4546', '44', '21', '26', 'τοτε ο παυλος παραλαβων τους ανδρας τη εχομενη ημερα συν αυτοις αγνισθεις εισηει εις το ιερον διαγγελλων την εκπληρωσιν των ημερων του αγνισμου εως ου προσηνεχθη υπερ ενος εκαστου αυτων η προσφορα');
INSERT INTO `bible_verses_tr` VALUES ('4547', '44', '21', '27', 'ως δε εμελλον αι επτα ημεραι συντελεισθαι οι απο της ασιας ιουδαιοι θεασαμενοι αυτον εν τω ιερω συνεχεον παντα τον οχλον και επεβαλον τας χειρας επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4548', '44', '21', '28', 'κραζοντες ανδρες ισραηλιται βοηθειτε ουτος εστιν ο ανθρωπος ο κατα του λαου και του νομου και του τοπου τουτου παντας πανταχου διδασκων ετι τε και ελληνας εισηγαγεν εις το ιερον και κεκοινωκεν τον αγιον τοπον τουτον');
INSERT INTO `bible_verses_tr` VALUES ('4549', '44', '21', '29', 'ησαν γαρ προεωρακοτες τροφιμον τον εφεσιον εν τη πολει συν αυτω ον ενομιζον οτι εις το ιερον εισηγαγεν ο παυλος');
INSERT INTO `bible_verses_tr` VALUES ('4550', '44', '21', '30', 'εκινηθη τε η πολις ολη και εγενετο συνδρομη του λαου και επιλαβομενοι του παυλου ειλκον αυτον εξω του ιερου και ευθεως εκλεισθησαν αι θυραι');
INSERT INTO `bible_verses_tr` VALUES ('4551', '44', '21', '31', 'ζητουντων δε αυτον αποκτειναι ανεβη φασις τω χιλιαρχω της σπειρης οτι ολη συγκεχυται ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4552', '44', '21', '32', 'ος εξαυτης παραλαβων στρατιωτας και εκατονταρχους κατεδραμεν επ αυτους οι δε ιδοντες τον χιλιαρχον και τους στρατιωτας επαυσαντο τυπτοντες τον παυλον');
INSERT INTO `bible_verses_tr` VALUES ('4553', '44', '21', '33', 'τοτε εγγισας ο χιλιαρχος επελαβετο αυτου και εκελευσεν δεθηναι αλυσεσιν δυσιν και επυνθανετο τις αν ειη και τι εστιν πεποιηκως');
INSERT INTO `bible_verses_tr` VALUES ('4554', '44', '21', '34', 'αλλοι δε αλλο τι εβοων εν τω οχλω μη δυναμενος δε γνωναι το ασφαλες δια τον θορυβον εκελευσεν αγεσθαι αυτον εις την παρεμβολην');
INSERT INTO `bible_verses_tr` VALUES ('4555', '44', '21', '35', 'οτε δε εγενετο επι τους αναβαθμους συνεβη βασταζεσθαι αυτον υπο των στρατιωτων δια την βιαν του οχλου');
INSERT INTO `bible_verses_tr` VALUES ('4556', '44', '21', '36', 'ηκολουθει γαρ το πληθος του λαου κραζον αιρε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4557', '44', '21', '37', 'μελλων τε εισαγεσθαι εις την παρεμβολην ο παυλος λεγει τω χιλιαρχω ει εξεστιν μοι ειπειν τι προς σε ο δε εφη ελληνιστι γινωσκεις');
INSERT INTO `bible_verses_tr` VALUES ('4558', '44', '21', '38', 'ουκ αρα συ ει ο αιγυπτιος ο προ τουτων των ημερων αναστατωσας και εξαγαγων εις την ερημον τους τετρακισχιλιους ανδρας των σικαριων');
INSERT INTO `bible_verses_tr` VALUES ('4559', '44', '21', '39', 'ειπεν δε ο παυλος εγω ανθρωπος μεν ειμι ιουδαιος ταρσευς της κιλικιας ουκ ασημου πολεως πολιτης δεομαι δε σου επιτρεψον μοι λαλησαι προς τον λαον');
INSERT INTO `bible_verses_tr` VALUES ('4560', '44', '21', '40', 'επιτρεψαντος δε αυτου ο παυλος εστως επι των αναβαθμων κατεσεισεν τη χειρι τω λαω πολλης δε σιγης γενομενης προσεφωνησεν τη εβραιδι διαλεκτω λεγων');
INSERT INTO `bible_verses_tr` VALUES ('4561', '44', '22', '1', 'ανδρες αδελφοι και πατερες ακουσατε μου της προς υμας νυν απολογιας');
INSERT INTO `bible_verses_tr` VALUES ('4562', '44', '22', '2', 'ακουσαντες δε οτι τη εβραιδι διαλεκτω προσεφωνει αυτοις μαλλον παρεσχον ησυχιαν και φησιν');
INSERT INTO `bible_verses_tr` VALUES ('4563', '44', '22', '3', 'εγω μεν ειμι ανηρ ιουδαιος γεγεννημενος εν ταρσω της κιλικιας ανατεθραμμενος δε εν τη πολει ταυτη παρα τους ποδας γαμαλιηλ πεπαιδευμενος κατα ακριβειαν του πατρωου νομου ζηλωτης υπαρχων του θεου καθως παντες υμεις εστε σημερον');
INSERT INTO `bible_verses_tr` VALUES ('4564', '44', '22', '4', 'ος ταυτην την οδον εδιωξα αχρι θανατου δεσμευων και παραδιδους εις φυλακας ανδρας τε και γυναικας');
INSERT INTO `bible_verses_tr` VALUES ('4565', '44', '22', '5', 'ως και ο αρχιερευς μαρτυρει μοι και παν το πρεσβυτεριον παρ ων και επιστολας δεξαμενος προς τους αδελφους εις δαμασκον επορευομην αξων και τους εκεισε οντας δεδεμενους εις ιερουσαλημ ινα τιμωρηθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('4566', '44', '22', '6', 'εγενετο δε μοι πορευομενω και εγγιζοντι τη δαμασκω περι μεσημβριαν εξαιφνης εκ του ουρανου περιαστραψαι φως ικανον περι εμε');
INSERT INTO `bible_verses_tr` VALUES ('4567', '44', '22', '7', 'επεσον τε εις το εδαφος και ηκουσα φωνης λεγουσης μοι σαουλ σαουλ τι με διωκεις');
INSERT INTO `bible_verses_tr` VALUES ('4568', '44', '22', '8', 'εγω δε απεκριθην τις ει κυριε ειπεν τε προς με εγω ειμι ιησους ο ναζωραιος ον συ διωκεις');
INSERT INTO `bible_verses_tr` VALUES ('4569', '44', '22', '9', 'οι δε συν εμοι οντες το μεν φως εθεασαντο και εμφοβοι εγενοντο την δε φωνην ουκ ηκουσαν του λαλουντος μοι');
INSERT INTO `bible_verses_tr` VALUES ('4570', '44', '22', '10', 'ειπον δε τι ποιησω κυριε ο δε κυριος ειπεν προς με αναστας πορευου εις δαμασκον κακει σοι λαληθησεται περι παντων ων τετακται σοι ποιησαι');
INSERT INTO `bible_verses_tr` VALUES ('4571', '44', '22', '11', 'ως δε ουκ ενεβλεπον απο της δοξης του φωτος εκεινου χειραγωγουμενος υπο των συνοντων μοι ηλθον εις δαμασκον');
INSERT INTO `bible_verses_tr` VALUES ('4572', '44', '22', '12', 'ανανιας δε τις ανηρ ευσεβης κατα τον νομον μαρτυρουμενος υπο παντων των κατοικουντων ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('4573', '44', '22', '13', 'ελθων προς με και επιστας ειπεν μοι σαουλ αδελφε αναβλεψον καγω αυτη τη ωρα ανεβλεψα εις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4574', '44', '22', '14', 'ο δε ειπεν ο θεος των πατερων ημων προεχειρισατο σε γνωναι το θελημα αυτου και ιδειν τον δικαιον και ακουσαι φωνην εκ του στοματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4575', '44', '22', '15', 'οτι εση μαρτυς αυτω προς παντας ανθρωπους ων εωρακας και ηκουσας');
INSERT INTO `bible_verses_tr` VALUES ('4576', '44', '22', '16', 'και νυν τι μελλεις αναστας βαπτισαι και απολουσαι τας αμαρτιας σου επικαλεσαμενος το ονομα του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('4577', '44', '22', '17', 'εγενετο δε μοι υποστρεψαντι εις ιερουσαλημ και προσευχομενου μου εν τω ιερω γενεσθαι με εν εκστασει');
INSERT INTO `bible_verses_tr` VALUES ('4578', '44', '22', '18', 'και ιδειν αυτον λεγοντα μοι σπευσον και εξελθε εν ταχει εξ ιερουσαλημ διοτι ου παραδεξονται σου την μαρτυριαν περι εμου');
INSERT INTO `bible_verses_tr` VALUES ('4579', '44', '22', '19', 'καγω ειπον κυριε αυτοι επιστανται οτι εγω ημην φυλακιζων και δερων κατα τας συναγωγας τους πιστευοντας επι σε');
INSERT INTO `bible_verses_tr` VALUES ('4580', '44', '22', '20', 'και οτε εξεχειτο το αιμα στεφανου του μαρτυρος σου και αυτος ημην εφεστως και συνευδοκων τη αναιρεσει αυτου και φυλασσων τα ιματια των αναιρουντων αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4581', '44', '22', '21', 'και ειπεν προς με πορευου οτι εγω εις εθνη μακραν εξαποστελω σε');
INSERT INTO `bible_verses_tr` VALUES ('4582', '44', '22', '22', 'ηκουον δε αυτου αχρι τουτου του λογου και επηραν την φωνην αυτων λεγοντες αιρε απο της γης τον τοιουτον ου γαρ καθηκον αυτον ζην');
INSERT INTO `bible_verses_tr` VALUES ('4583', '44', '22', '23', 'κραυγαζοντων δε αυτων και ριπτουντων τα ιματια και κονιορτον βαλλοντων εις τον αερα');
INSERT INTO `bible_verses_tr` VALUES ('4584', '44', '22', '24', 'εκελευσεν αυτον ο χιλιαρχος αγεσθαι εις την παρεμβολην ειπων μαστιξιν ανεταζεσθαι αυτον ινα επιγνω δι ην αιτιαν ουτως επεφωνουν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4585', '44', '22', '25', 'ως δε  {VAR1: προετεινεν } {VAR2: προετειναν } αυτον τοις ιμασιν ειπεν προς τον εστωτα εκατονταρχον ο παυλος ει ανθρωπον ρωμαιον και ακατακριτον εξεστιν υμιν μαστιζειν');
INSERT INTO `bible_verses_tr` VALUES ('4586', '44', '22', '26', 'ακουσας δε ο εκατονταρχος προσελθων απηγγειλεν τω χιλιαρχω λεγων ορα τι μελλεις ποιειν ο γαρ ανθρωπος ουτος ρωμαιος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4587', '44', '22', '27', 'προσελθων δε ο χιλιαρχος ειπεν αυτω λεγε μοι ει συ ρωμαιος ει ο δε εφη ναι');
INSERT INTO `bible_verses_tr` VALUES ('4588', '44', '22', '28', 'απεκριθη τε ο χιλιαρχος εγω πολλου κεφαλαιου την πολιτειαν ταυτην εκτησαμην ο δε παυλος εφη εγω δε και γεγεννημαι');
INSERT INTO `bible_verses_tr` VALUES ('4589', '44', '22', '29', 'ευθεως ουν απεστησαν απ αυτου οι μελλοντες αυτον ανεταζειν και ο χιλιαρχος δε εφοβηθη επιγνους οτι ρωμαιος εστιν και οτι ην αυτον δεδεκως');
INSERT INTO `bible_verses_tr` VALUES ('4590', '44', '22', '30', 'τη δε επαυριον βουλομενος γνωναι το ασφαλες το τι κατηγορειται παρα των ιουδαιων ελυσεν αυτον απο των δεσμων και εκελευσεν ελθειν τους αρχιερεις και ολον το συνεδριον αυτων και καταγαγων τον παυλον εστησεν εις αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4591', '44', '23', '1', 'ατενισας δε ο παυλος τω συνεδριω ειπεν ανδρες αδελφοι εγω παση συνειδησει αγαθη πεπολιτευμαι τω θεω αχρι ταυτης της ημερας');
INSERT INTO `bible_verses_tr` VALUES ('4592', '44', '23', '2', 'ο δε αρχιερευς ανανιας επεταξεν τοις παρεστωσιν αυτω τυπτειν αυτου το στομα');
INSERT INTO `bible_verses_tr` VALUES ('4593', '44', '23', '3', 'τοτε ο παυλος προς αυτον ειπεν τυπτειν σε μελλει ο θεος τοιχε κεκονιαμενε και συ καθη κρινων με κατα τον νομον και παρανομων κελευεις με τυπτεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4594', '44', '23', '4', 'οι δε παρεστωτες ειπον τον αρχιερεα του θεου λοιδορεις');
INSERT INTO `bible_verses_tr` VALUES ('4595', '44', '23', '5', 'εφη τε ο παυλος ουκ ηδειν αδελφοι οτι εστιν αρχιερευς γεγραπται γαρ αρχοντα του λαου σου ουκ ερεις κακως');
INSERT INTO `bible_verses_tr` VALUES ('4596', '44', '23', '6', 'γνους δε ο παυλος οτι το εν μερος εστιν σαδδουκαιων το δε ετερον φαρισαιων εκραξεν εν τω συνεδριω ανδρες αδελφοι εγω φαρισαιος ειμι υιος φαρισαιου περι ελπιδος και αναστασεως νεκρων εγω κρινομαι');
INSERT INTO `bible_verses_tr` VALUES ('4597', '44', '23', '7', 'τουτο δε αυτου λαλησαντος εγενετο στασις των φαρισαιων και των σαδδουκαιων και εσχισθη το πληθος');
INSERT INTO `bible_verses_tr` VALUES ('4598', '44', '23', '8', 'σαδδουκαιοι μεν γαρ λεγουσιν μη ειναι αναστασιν μηδε αγγελον μητε πνευμα φαρισαιοι δε ομολογουσιν τα αμφοτερα');
INSERT INTO `bible_verses_tr` VALUES ('4599', '44', '23', '9', 'εγενετο δε κραυγη μεγαλη και ανασταντες οι γραμματεις του μερους των φαρισαιων διεμαχοντο λεγοντες ουδεν κακον ευρισκομεν εν τω ανθρωπω τουτω ει δε πνευμα ελαλησεν αυτω η αγγελος μη θεομαχωμεν');
INSERT INTO `bible_verses_tr` VALUES ('4600', '44', '23', '10', 'πολλης δε γενομενης στασεως ευλαβηθεις ο χιλιαρχος μη διασπασθη ο παυλος υπ αυτων εκελευσεν το στρατευμα καταβαν αρπασαι αυτον εκ μεσου αυτων αγειν τε εις την παρεμβολην');
INSERT INTO `bible_verses_tr` VALUES ('4601', '44', '23', '11', 'τη δε επιουση νυκτι επιστας αυτω ο κυριος ειπεν θαρσει παυλε ως γαρ διεμαρτυρω τα περι εμου εις ιερουσαλημ ουτως σε δει και εις ρωμην μαρτυρησαι');
INSERT INTO `bible_verses_tr` VALUES ('4602', '44', '23', '12', 'γενομενης δε ημερας ποιησαντες τινες των ιουδαιων συστροφην ανεθεματισαν εαυτους λεγοντες μητε φαγειν μητε πιειν εως ου αποκτεινωσιν τον παυλον');
INSERT INTO `bible_verses_tr` VALUES ('4603', '44', '23', '13', 'ησαν δε πλειους τεσσαρακοντα οι ταυτην την συνωμοσιαν πεποιηκοτες');
INSERT INTO `bible_verses_tr` VALUES ('4604', '44', '23', '14', 'οιτινες προσελθοντες τοις αρχιερευσιν και τοις πρεσβυτεροις ειπον αναθεματι ανεθεματισαμεν εαυτους μηδενος γευσασθαι εως ου αποκτεινωμεν τον παυλον');
INSERT INTO `bible_verses_tr` VALUES ('4605', '44', '23', '15', 'νυν ουν υμεις εμφανισατε τω χιλιαρχω συν τω συνεδριω οπως αυριον αυτον καταγαγη προς υμας ως μελλοντας διαγινωσκειν ακριβεστερον τα περι αυτου ημεις δε προ του εγγισαι αυτον ετοιμοι εσμεν του ανελειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4606', '44', '23', '16', 'ακουσας δε ο υιος της αδελφης παυλου  {VAR1: το ενεδρον } {VAR2: την ενεδραν } παραγενομενος και εισελθων εις την παρεμβολην απηγγειλεν τω παυλω');
INSERT INTO `bible_verses_tr` VALUES ('4607', '44', '23', '17', 'προσκαλεσαμενος δε ο παυλος ενα των εκατονταρχων εφη τον νεανιαν τουτον απαγαγε προς τον χιλιαρχον εχει γαρ τι απαγγειλαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4608', '44', '23', '18', 'ο μεν ουν παραλαβων αυτον ηγαγεν προς τον χιλιαρχον και φησιν ο δεσμιος παυλος προσκαλεσαμενος με ηρωτησεν τουτον τον νεανιαν αγαγειν προς σε εχοντα τι λαλησαι σοι');
INSERT INTO `bible_verses_tr` VALUES ('4609', '44', '23', '19', 'επιλαβομενος δε της χειρος αυτου ο χιλιαρχος και αναχωρησας κατ ιδιαν επυνθανετο τι εστιν ο εχεις απαγγειλαι μοι');
INSERT INTO `bible_verses_tr` VALUES ('4610', '44', '23', '20', 'ειπεν δε οτι οι ιουδαιοι συνεθεντο του ερωτησαι σε οπως αυριον εις το συνεδριον καταγαγης τον παυλον ως μελλοντες τι ακριβεστερον πυνθανεσθαι περι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4611', '44', '23', '21', 'συ ουν μη πεισθης αυτοις ενεδρευουσιν γαρ αυτον εξ αυτων ανδρες πλειους τεσσαρακοντα οιτινες ανεθεματισαν εαυτους μητε φαγειν μητε πιειν εως ου ανελωσιν αυτον και νυν ετοιμοι εισιν προσδεχομενοι την απο σου επαγγελιαν');
INSERT INTO `bible_verses_tr` VALUES ('4612', '44', '23', '22', 'ο μεν ουν χιλιαρχος απελυσεν τον νεανιαν παραγγειλας μηδενι εκλαλησαι οτι ταυτα ενεφανισας προς με');
INSERT INTO `bible_verses_tr` VALUES ('4613', '44', '23', '23', 'και προσκαλεσαμενος δυο τινας των εκατονταρχων ειπεν ετοιμασατε στρατιωτας διακοσιους οπως πορευθωσιν εως καισαρειας και ιππεις εβδομηκοντα και δεξιολαβους διακοσιους απο τριτης ωρας της νυκτος');
INSERT INTO `bible_verses_tr` VALUES ('4614', '44', '23', '24', 'κτηνη τε παραστησαι ινα επιβιβασαντες τον παυλον διασωσωσιν προς φηλικα τον ηγεμονα');
INSERT INTO `bible_verses_tr` VALUES ('4615', '44', '23', '25', 'γραψας επιστολην περιεχουσαν τον τυπον τουτον');
INSERT INTO `bible_verses_tr` VALUES ('4616', '44', '23', '26', 'κλαυδιος λυσιας τω κρατιστω ηγεμονι φηλικι χαιρειν');
INSERT INTO `bible_verses_tr` VALUES ('4617', '44', '23', '27', 'τον ανδρα τουτον συλληφθεντα υπο των ιουδαιων και μελλοντα αναιρεισθαι υπ αυτων επιστας συν τω στρατευματι εξειλομην αυτον μαθων οτι ρωμαιος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('4618', '44', '23', '28', 'βουλομενος δε γνωναι την αιτιαν δι ην ενεκαλουν αυτω κατηγαγον αυτον εις το συνεδριον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('4619', '44', '23', '29', 'ον ευρον εγκαλουμενον περι ζητηματων του νομου αυτων μηδεν δε αξιον θανατου η δεσμων εγκλημα εχοντα');
INSERT INTO `bible_verses_tr` VALUES ('4620', '44', '23', '30', 'μηνυθεισης δε μοι επιβουλης εις τον ανδρα μελλειν εσεσθαι υπο των ιουδαιων εξαυτης επεμψα προς σε παραγγειλας και τοις κατηγοροις λεγειν τα προς αυτον επι σου ερρωσο');
INSERT INTO `bible_verses_tr` VALUES ('4621', '44', '23', '31', 'οι μεν ουν στρατιωται κατα το διατεταγμενον αυτοις αναλαβοντες τον παυλον ηγαγον δια της νυκτος εις την αντιπατριδα');
INSERT INTO `bible_verses_tr` VALUES ('4622', '44', '23', '32', 'τη δε επαυριον εασαντες τους ιππεις πορευεσθαι συν αυτω υπεστρεψαν εις την παρεμβολην');
INSERT INTO `bible_verses_tr` VALUES ('4623', '44', '23', '33', 'οιτινες εισελθοντες εις την καισαρειαν και αναδοντες την επιστολην τω ηγεμονι παρεστησαν και τον παυλον αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4624', '44', '23', '34', 'αναγνους δε ο ηγεμων και επερωτησας εκ ποιας επαρχιας εστιν και πυθομενος οτι απο κιλικιας');
INSERT INTO `bible_verses_tr` VALUES ('4625', '44', '23', '35', 'διακουσομαι σου εφη οταν και οι κατηγοροι σου παραγενωνται εκελευσεν τε αυτον εν τω πραιτωριω του ηρωδου φυλασσεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4626', '44', '24', '1', 'μετα δε πεντε ημερας κατεβη ο αρχιερευς ανανιας μετα των πρεσβυτερων και ρητορος τερτυλλου τινος οιτινες ενεφανισαν τω ηγεμονι κατα του παυλου');
INSERT INTO `bible_verses_tr` VALUES ('4627', '44', '24', '2', 'κληθεντος δε αυτου ηρξατο κατηγορειν ο τερτυλλος λεγων');
INSERT INTO `bible_verses_tr` VALUES ('4628', '44', '24', '3', 'πολλης ειρηνης τυγχανοντες δια σου και κατορθωματων γινομενων τω εθνει τουτω δια της σης προνοιας παντη τε και πανταχου αποδεχομεθα κρατιστε φηλιξ μετα πασης ευχαριστιας');
INSERT INTO `bible_verses_tr` VALUES ('4629', '44', '24', '4', 'ινα δε μη επι πλειον σε εγκοπτω παρακαλω ακουσαι σε ημων συντομως τη ση επιεικεια');
INSERT INTO `bible_verses_tr` VALUES ('4630', '44', '24', '5', 'ευροντες γαρ τον ανδρα τουτον λοιμον και κινουντα στασιν πασιν τοις ιουδαιοις τοις κατα την οικουμενην πρωτοστατην τε της των ναζωραιων αιρεσεως');
INSERT INTO `bible_verses_tr` VALUES ('4631', '44', '24', '6', 'ος και το ιερον επειρασεν βεβηλωσαι ον και εκρατησαμεν και κατα τον ημετερον νομον ηθελησαμεν κρινειν');
INSERT INTO `bible_verses_tr` VALUES ('4632', '44', '24', '7', 'παρελθων δε λυσιας ο χιλιαρχος μετα πολλης βιας εκ των χειρων ημων απηγαγεν');
INSERT INTO `bible_verses_tr` VALUES ('4633', '44', '24', '8', 'κελευσας τους κατηγορους αυτου ερχεσθαι επι σε παρ ου δυνηση αυτος ανακρινας περι παντων τουτων επιγνωναι ων ημεις κατηγορουμεν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4634', '44', '24', '9', 'συνεθεντο δε και οι ιουδαιοι φασκοντες ταυτα ουτως εχειν');
INSERT INTO `bible_verses_tr` VALUES ('4635', '44', '24', '10', 'απεκριθη δε ο παυλος νευσαντος αυτω του ηγεμονος λεγειν εκ πολλων ετων οντα σε κριτην τω εθνει τουτω επισταμενος ευθυμοτερον τα περι εμαυτου απολογουμαι');
INSERT INTO `bible_verses_tr` VALUES ('4636', '44', '24', '11', 'δυναμενου σου γνωναι οτι ου πλειους εισιν μοι ημεραι η δεκαδυο αφ ης ανεβην προσκυνησων εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('4637', '44', '24', '12', 'και ουτε εν τω ιερω ευρον με προς τινα διαλεγομενον η επισυστασιν ποιουντα οχλου ουτε εν ταις συναγωγαις ουτε κατα την πολιν');
INSERT INTO `bible_verses_tr` VALUES ('4638', '44', '24', '13', 'ουτε παραστησαι  {VAR1: με } δυνανται περι ων νυν κατηγορουσιν μου');
INSERT INTO `bible_verses_tr` VALUES ('4639', '44', '24', '14', 'ομολογω δε τουτο σοι οτι κατα την οδον ην λεγουσιν αιρεσιν ουτως λατρευω τω πατρωω θεω πιστευων πασιν τοις κατα τον νομον και τοις προφηταις γεγραμμενοις');
INSERT INTO `bible_verses_tr` VALUES ('4640', '44', '24', '15', 'ελπιδα εχων εις τον θεον ην και αυτοι ουτοι προσδεχονται αναστασιν μελλειν εσεσθαι νεκρων δικαιων τε και αδικων');
INSERT INTO `bible_verses_tr` VALUES ('4641', '44', '24', '16', 'εν τουτω δε αυτος ασκω απροσκοπον συνειδησιν εχειν προς τον θεον και τους ανθρωπους διαπαντος');
INSERT INTO `bible_verses_tr` VALUES ('4642', '44', '24', '17', 'δι ετων δε πλειονων παρεγενομην ελεημοσυνας ποιησων εις το εθνος μου και προσφορας');
INSERT INTO `bible_verses_tr` VALUES ('4643', '44', '24', '18', 'εν οις ευρον με ηγνισμενον εν τω ιερω ου μετα οχλου ουδε μετα θορυβου τινες  {VAR1: δε } απο της ασιας ιουδαιοι');
INSERT INTO `bible_verses_tr` VALUES ('4644', '44', '24', '19', 'ους  {VAR1: δει } {VAR2: εδει } επι σου παρειναι και κατηγορειν ει τι εχοιεν προς με');
INSERT INTO `bible_verses_tr` VALUES ('4645', '44', '24', '20', 'η αυτοι ουτοι ειπατωσαν ει τι ευρον εν εμοι αδικημα σταντος μου επι του συνεδριου');
INSERT INTO `bible_verses_tr` VALUES ('4646', '44', '24', '21', 'η περι μιας ταυτης φωνης ης εκραξα εστως εν αυτοις οτι περι αναστασεως νεκρων εγω κρινομαι σημερον υφ υμων');
INSERT INTO `bible_verses_tr` VALUES ('4647', '44', '24', '22', 'ακουσας δε ταυτα ο φηλιξ ανεβαλετο αυτους ακριβεστερον ειδως τα περι της οδου ειπων οταν λυσιας ο χιλιαρχος καταβη διαγνωσομαι τα καθ υμας');
INSERT INTO `bible_verses_tr` VALUES ('4648', '44', '24', '23', 'διαταξαμενος τε τω εκατονταρχη τηρεισθαι τον παυλον εχειν τε ανεσιν και μηδενα κωλυειν των ιδιων αυτου υπηρετειν η προσερχεσθαι αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4649', '44', '24', '24', 'μετα δε ημερας τινας παραγενομενος ο φηλιξ συν δρουσιλλη τη γυναικι αυτου ουση ιουδαια μετεπεμψατο τον παυλον και ηκουσεν αυτου περι της εις χριστον πιστεως');
INSERT INTO `bible_verses_tr` VALUES ('4650', '44', '24', '25', 'διαλεγομενου δε αυτου περι δικαιοσυνης και εγκρατειας και του κριματος του μελλοντος εσεσθαι εμφοβος γενομενος ο φηλιξ απεκριθη το νυν εχον πορευου καιρον δε μεταλαβων μετακαλεσομαι σε');
INSERT INTO `bible_verses_tr` VALUES ('4651', '44', '24', '26', 'αμα δε και ελπιζων οτι χρηματα δοθησεται αυτω υπο του παυλου οπως λυση αυτον διο και πυκνοτερον αυτον μεταπεμπομενος ωμιλει αυτω');
INSERT INTO `bible_verses_tr` VALUES ('4652', '44', '24', '27', 'διετιας δε πληρωθεισης ελαβεν διαδοχον ο φηλιξ πορκιον φηστον θελων τε χαριτας καταθεσθαι τοις ιουδαιοις ο φηλιξ κατελιπεν τον παυλον δεδεμενον');
INSERT INTO `bible_verses_tr` VALUES ('4653', '44', '25', '1', 'φηστος ουν επιβας τη επαρχια μετα τρεις ημερας ανεβη εις ιεροσολυμα απο καισαρειας');
INSERT INTO `bible_verses_tr` VALUES ('4654', '44', '25', '2', 'ενεφανισαν δε αυτω ο αρχιερευς και οι πρωτοι των ιουδαιων κατα του παυλου και παρεκαλουν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4655', '44', '25', '3', 'αιτουμενοι χαριν κατ αυτου οπως μεταπεμψηται αυτον εις ιερουσαλημ ενεδραν ποιουντες ανελειν αυτον κατα την οδον');
INSERT INTO `bible_verses_tr` VALUES ('4656', '44', '25', '4', 'ο μεν ουν φηστος απεκριθη τηρεισθαι τον παυλον εν καισαρεια εαυτον δε μελλειν εν ταχει εκπορευεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4657', '44', '25', '5', 'οι ουν δυνατοι εν υμιν φησιν συγκαταβαντες ει τι εστιν  {VAR2: ατοπον } εν τω ανδρι τουτω κατηγορειτωσαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4658', '44', '25', '6', 'διατριψας δε εν αυτοις ημερας πλειους η δεκα καταβας εις καισαρειαν τη επαυριον καθισας επι του βηματος εκελευσεν τον παυλον αχθηναι');
INSERT INTO `bible_verses_tr` VALUES ('4659', '44', '25', '7', 'παραγενομενου δε αυτου περιεστησαν οι απο ιεροσολυμων καταβεβηκοτες ιουδαιοι πολλα και βαρεα αιτιαματα φεροντες κατα του παυλου α ουκ ισχυον αποδειξαι');
INSERT INTO `bible_verses_tr` VALUES ('4660', '44', '25', '8', 'απολογουμενου αυτου οτι ουτε εις τον νομον των ιουδαιων ουτε εις το ιερον ουτε εις καισαρα τι ημαρτον');
INSERT INTO `bible_verses_tr` VALUES ('4661', '44', '25', '9', 'ο φηστος δε τοις ιουδαιοις θελων χαριν καταθεσθαι αποκριθεις τω παυλω ειπεν θελεις εις ιεροσολυμα αναβας εκει περι τουτων κρινεσθαι επ εμου');
INSERT INTO `bible_verses_tr` VALUES ('4662', '44', '25', '10', 'ειπεν δε ο παυλος επι του βηματος καισαρος εστως ειμι ου με δει κρινεσθαι ιουδαιους ουδεν ηδικησα ως και συ καλλιον επιγινωσκεις');
INSERT INTO `bible_verses_tr` VALUES ('4663', '44', '25', '11', 'ει μεν γαρ αδικω και αξιον θανατου πεπραχα τι ου παραιτουμαι το αποθανειν ει δε ουδεν εστιν ων ουτοι κατηγορουσιν μου ουδεις με δυναται αυτοις χαρισασθαι καισαρα επικαλουμαι');
INSERT INTO `bible_verses_tr` VALUES ('4664', '44', '25', '12', 'τοτε ο φηστος συλλαλησας μετα του συμβουλιου απεκριθη καισαρα επικεκλησαι επι καισαρα πορευση');
INSERT INTO `bible_verses_tr` VALUES ('4665', '44', '25', '13', 'ημερων δε διαγενομενων τινων αγριππας ο βασιλευς και βερνικη κατηντησαν εις καισαρειαν ασπασομενοι τον φηστον');
INSERT INTO `bible_verses_tr` VALUES ('4666', '44', '25', '14', 'ως δε πλειους ημερας διετριβον εκει ο φηστος τω βασιλει ανεθετο τα κατα τον παυλον λεγων ανηρ τις εστιν καταλελειμμενος υπο φηλικος δεσμιος');
INSERT INTO `bible_verses_tr` VALUES ('4667', '44', '25', '15', 'περι ου γενομενου μου εις ιεροσολυμα ενεφανισαν οι αρχιερεις και οι πρεσβυτεροι των ιουδαιων αιτουμενοι κατ αυτου δικην');
INSERT INTO `bible_verses_tr` VALUES ('4668', '44', '25', '16', 'προς ους απεκριθην οτι ουκ εστιν εθος ρωμαιοις χαριζεσθαι τινα ανθρωπον εις απωλειαν πριν η ο κατηγορουμενος κατα προσωπον εχοι τους κατηγορους τοπον τε απολογιας λαβοι περι του εγκληματος');
INSERT INTO `bible_verses_tr` VALUES ('4669', '44', '25', '17', 'συνελθοντων ουν αυτων ενθαδε αναβολην μηδεμιαν ποιησαμενος τη εξης καθισας επι του βηματος εκελευσα αχθηναι τον ανδρα');
INSERT INTO `bible_verses_tr` VALUES ('4670', '44', '25', '18', 'περι ου σταθεντες οι κατηγοροι ουδεμιαν αιτιαν επεφερον ων υπενοουν εγω');
INSERT INTO `bible_verses_tr` VALUES ('4671', '44', '25', '19', 'ζητηματα δε τινα περι της ιδιας δεισιδαιμονιας ειχον προς αυτον και περι τινος ιησου τεθνηκοτος ον εφασκεν ο παυλος ζην');
INSERT INTO `bible_verses_tr` VALUES ('4672', '44', '25', '20', 'απορουμενος δε εγω εις την περι τουτου ζητησιν ελεγον ει βουλοιτο πορευεσθαι εις ιερουσαλημ κακει κρινεσθαι περι τουτων');
INSERT INTO `bible_verses_tr` VALUES ('4673', '44', '25', '21', 'του δε παυλου επικαλεσαμενου τηρηθηναι αυτον εις την του σεβαστου διαγνωσιν εκελευσα τηρεισθαι αυτον εως ου πεμψω αυτον προς καισαρα');
INSERT INTO `bible_verses_tr` VALUES ('4674', '44', '25', '22', 'αγριππας δε προς τον φηστον εφη εβουλομην και αυτος του ανθρωπου ακουσαι ο δε αυριον φησιν ακουση αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4675', '44', '25', '23', 'τη ουν επαυριον ελθοντος του αγριππα και της βερνικης μετα πολλης φαντασιας και εισελθοντων εις το ακροατηριον συν τε τοις χιλιαρχοις και ανδρασιν τοις κατ εξοχην ουσιν της πολεως και κελευσαντος του φηστου ηχθη ο παυλος');
INSERT INTO `bible_verses_tr` VALUES ('4676', '44', '25', '24', 'και φησιν ο φηστος αγριππα βασιλευ και παντες οι συμπαροντες ημιν ανδρες θεωρειτε τουτον περι ου παν το πληθος των ιουδαιων ενετυχον μοι εν τε ιεροσολυμοις και ενθαδε επιβοωντες μη δειν ζην αυτον μηκετι');
INSERT INTO `bible_verses_tr` VALUES ('4677', '44', '25', '25', 'εγω δε καταλαβομενος μηδεν αξιον θανατου αυτον πεπραχεναι και αυτου δε τουτου επικαλεσαμενου τον σεβαστον εκρινα πεμπειν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4678', '44', '25', '26', 'περι ου ασφαλες τι γραψαι τω κυριω ουκ εχω διο προηγαγον αυτον εφ υμων και μαλιστα επι σου βασιλευ αγριππα οπως της ανακρισεως γενομενης σχω τι γραψαι');
INSERT INTO `bible_verses_tr` VALUES ('4679', '44', '25', '27', 'αλογον γαρ μοι δοκει πεμποντα δεσμιον μη και τας κατ αυτου αιτιας σημαναι');
INSERT INTO `bible_verses_tr` VALUES ('4680', '44', '26', '1', 'αγριππας δε προς τον παυλον εφη επιτρεπεται σοι υπερ σεαυτου λεγειν τοτε ο παυλος απελογειτο εκτεινας την χειρα');
INSERT INTO `bible_verses_tr` VALUES ('4681', '44', '26', '2', 'περι παντων ων εγκαλουμαι υπο ιουδαιων βασιλευ αγριππα ηγημαι εμαυτον μακαριον μελλων απολογεισθαι επι σου σημερον');
INSERT INTO `bible_verses_tr` VALUES ('4682', '44', '26', '3', 'μαλιστα γνωστην οντα σε  {VAR2: ειδως } παντων των κατα ιουδαιους εθων τε και ζητηματων διο δεομαι σου μακροθυμως ακουσαι μου');
INSERT INTO `bible_verses_tr` VALUES ('4683', '44', '26', '4', 'την μεν ουν βιωσιν μου την εκ νεοτητος την απ αρχης γενομενην εν τω εθνει μου εν ιεροσολυμοις ισασιν παντες οι ιουδαιοι');
INSERT INTO `bible_verses_tr` VALUES ('4684', '44', '26', '5', 'προγινωσκοντες με ανωθεν εαν θελωσιν μαρτυρειν οτι κατα την ακριβεστατην αιρεσιν της ημετερας θρησκειας εζησα φαρισαιος');
INSERT INTO `bible_verses_tr` VALUES ('4685', '44', '26', '6', 'και νυν επ ελπιδι της προς τους πατερας επαγγελιας γενομενης υπο του θεου εστηκα κρινομενος');
INSERT INTO `bible_verses_tr` VALUES ('4686', '44', '26', '7', 'εις ην το δωδεκαφυλον ημων εν εκτενεια νυκτα και ημεραν λατρευον ελπιζει καταντησαι περι ης ελπιδος εγκαλουμαι βασιλευ αγριππα υπο των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('4687', '44', '26', '8', 'τι απιστον κρινεται παρ υμιν ει ο θεος νεκρους εγειρει');
INSERT INTO `bible_verses_tr` VALUES ('4688', '44', '26', '9', 'εγω μεν ουν εδοξα εμαυτω προς το ονομα ιησου του ναζωραιου δειν πολλα εναντια πραξαι');
INSERT INTO `bible_verses_tr` VALUES ('4689', '44', '26', '10', 'ο και εποιησα εν ιεροσολυμοις και πολλους των αγιων εγω φυλακαις κατεκλεισα την παρα των αρχιερεων εξουσιαν λαβων αναιρουμενων τε αυτων κατηνεγκα ψηφον');
INSERT INTO `bible_verses_tr` VALUES ('4690', '44', '26', '11', 'και κατα πασας τας συναγωγας πολλακις τιμωρων αυτους ηναγκαζον βλασφημειν περισσως τε εμμαινομενος αυτοις εδιωκον εως και εις τας εξω πολεις');
INSERT INTO `bible_verses_tr` VALUES ('4691', '44', '26', '12', 'εν οις και πορευομενος εις την δαμασκον μετ εξουσιας και επιτροπης της παρα των αρχιερεων');
INSERT INTO `bible_verses_tr` VALUES ('4692', '44', '26', '13', 'ημερας μεσης κατα την οδον ειδον βασιλευ ουρανοθεν υπερ την λαμπροτητα του ηλιου περιλαμψαν με φως και τους συν εμοι πορευομενους');
INSERT INTO `bible_verses_tr` VALUES ('4693', '44', '26', '14', 'παντων δε καταπεσοντων ημων εις την γην ηκουσα φωνην λαλουσαν προς με και λεγουσαν τη εβραιδι διαλεκτω σαουλ σαουλ τι με διωκεις σκληρον σοι προς κεντρα λακτιζειν');
INSERT INTO `bible_verses_tr` VALUES ('4694', '44', '26', '15', 'εγω δε ειπον τις ει κυριε ο δε ειπεν εγω ειμι ιησους ον συ διωκεις');
INSERT INTO `bible_verses_tr` VALUES ('4695', '44', '26', '16', 'αλλα αναστηθι και στηθι επι τους ποδας σου εις τουτο γαρ ωφθην σοι προχειρισασθαι σε υπηρετην και μαρτυρα ων τε ειδες ων τε οφθησομαι σοι');
INSERT INTO `bible_verses_tr` VALUES ('4696', '44', '26', '17', 'εξαιρουμενος σε εκ του λαου και των εθνων εις ους νυν σε αποστελλω');
INSERT INTO `bible_verses_tr` VALUES ('4697', '44', '26', '18', 'ανοιξαι οφθαλμους αυτων  {VAR1: του } {VAR2: και } επιστρεψαι απο σκοτους εις φως και της εξουσιας του σατανα επι τον θεον του λαβειν αυτους αφεσιν αμαρτιων και κληρον εν τοις ηγιασμενοις πιστει τη εις εμε');
INSERT INTO `bible_verses_tr` VALUES ('4698', '44', '26', '19', 'οθεν βασιλευ αγριππα ουκ εγενομην απειθης τη ουρανιω οπτασια');
INSERT INTO `bible_verses_tr` VALUES ('4699', '44', '26', '20', 'αλλα τοις εν δαμασκω πρωτον και ιεροσολυμοις εις πασαν τε την χωραν της ιουδαιας και τοις εθνεσιν  {VAR1: απαγγελλων } {VAR2: απηγγελλον } μετανοειν και επιστρεφειν επι τον θεον αξια της μετανοιας εργα πρασσοντας');
INSERT INTO `bible_verses_tr` VALUES ('4700', '44', '26', '21', 'ενεκα τουτων με οι ιουδαιοι συλλαβομενοι εν τω ιερω επειρωντο διαχειρισασθαι');
INSERT INTO `bible_verses_tr` VALUES ('4701', '44', '26', '22', 'επικουριας ουν τυχων της παρα του θεου αχρι της ημερας ταυτης εστηκα μαρτυρουμενος μικρω τε και μεγαλω ουδεν εκτος λεγων ων τε οι προφηται ελαλησαν μελλοντων γινεσθαι και μωσης');
INSERT INTO `bible_verses_tr` VALUES ('4702', '44', '26', '23', 'ει παθητος ο χριστος ει πρωτος εξ αναστασεως νεκρων φως μελλει καταγγελλειν τω λαω και τοις εθνεσιν');
INSERT INTO `bible_verses_tr` VALUES ('4703', '44', '26', '24', 'ταυτα δε αυτου απολογουμενου ο φηστος μεγαλη τη φωνη εφη μαινη παυλε τα πολλα σε γραμματα εις μανιαν περιτρεπει');
INSERT INTO `bible_verses_tr` VALUES ('4704', '44', '26', '25', 'ο δε ου μαινομαι φησιν κρατιστε φηστε αλλ αληθειας και σωφροσυνης ρηματα αποφθεγγομαι');
INSERT INTO `bible_verses_tr` VALUES ('4705', '44', '26', '26', 'επισταται γαρ περι τουτων ο βασιλευς προς ον και παρρησιαζομενος λαλω λανθανειν γαρ αυτον τι τουτων ου πειθομαι ουδεν ου γαρ εστιν εν γωνια πεπραγμενον τουτο');
INSERT INTO `bible_verses_tr` VALUES ('4706', '44', '26', '27', 'πιστευεις βασιλευ αγριππα τοις προφηταις οιδα οτι πιστευεις');
INSERT INTO `bible_verses_tr` VALUES ('4707', '44', '26', '28', 'ο δε αγριππας προς τον παυλον εφη εν ολιγω με πειθεις χριστιανον γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4708', '44', '26', '29', 'ο δε παυλος ειπεν ευξαιμην αν τω θεω και εν ολιγω και εν πολλω ου μονον σε αλλα και παντας τους ακουοντας μου σημερον γενεσθαι τοιουτους οποιος καγω ειμι παρεκτος των δεσμων τουτων');
INSERT INTO `bible_verses_tr` VALUES ('4709', '44', '26', '30', 'και ταυτα ειποντος αυτου ανεστη ο βασιλευς και ο ηγεμων η τε βερνικη και οι συγκαθημενοι αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4710', '44', '26', '31', 'και αναχωρησαντες ελαλουν προς αλληλους λεγοντες οτι ουδεν θανατου αξιον η δεσμων πρασσει ο ανθρωπος ουτος');
INSERT INTO `bible_verses_tr` VALUES ('4711', '44', '26', '32', 'αγριππας δε τω φηστω εφη απολελυσθαι εδυνατο ο ανθρωπος ουτος ει μη επεκεκλητο καισαρα');
INSERT INTO `bible_verses_tr` VALUES ('4712', '44', '27', '1', 'ως δε εκριθη του αποπλειν ημας εις την ιταλιαν παρεδιδουν τον τε παυλον και τινας ετερους δεσμωτας εκατονταρχη ονοματι ιουλιω σπειρης σεβαστης');
INSERT INTO `bible_verses_tr` VALUES ('4713', '44', '27', '2', 'επιβαντες δε πλοιω αδραμυττηνω μελλοντες πλειν τους κατα την ασιαν τοπους ανηχθημεν οντος συν ημιν αρισταρχου μακεδονος θεσσαλονικεως');
INSERT INTO `bible_verses_tr` VALUES ('4714', '44', '27', '3', 'τη τε ετερα κατηχθημεν εις σιδωνα φιλανθρωπως τε ο ιουλιος τω παυλω χρησαμενος επετρεψεν προς  {VAR2: τους } φιλους πορευθεντα επιμελειας τυχειν');
INSERT INTO `bible_verses_tr` VALUES ('4715', '44', '27', '4', 'κακειθεν αναχθεντες υπεπλευσαμεν την κυπρον δια το τους ανεμους ειναι εναντιους');
INSERT INTO `bible_verses_tr` VALUES ('4716', '44', '27', '5', 'το τε πελαγος το κατα την κιλικιαν και παμφυλιαν διαπλευσαντες κατηλθομεν εις μυρα της λυκιας');
INSERT INTO `bible_verses_tr` VALUES ('4717', '44', '27', '6', 'κακει ευρων ο εκατονταρχος πλοιον αλεξανδρινον πλεον εις την ιταλιαν ενεβιβασεν ημας εις αυτο');
INSERT INTO `bible_verses_tr` VALUES ('4718', '44', '27', '7', 'εν ικαναις δε ημεραις βραδυπλοουντες και μολις γενομενοι κατα την κνιδον μη προσεωντος ημας του ανεμου υπεπλευσαμεν την κρητην κατα σαλμωνην');
INSERT INTO `bible_verses_tr` VALUES ('4719', '44', '27', '8', 'μολις τε παραλεγομενοι αυτην ηλθομεν εις τοπον τινα καλουμενον καλους λιμενας ω εγγυς ην πολις λασαια');
INSERT INTO `bible_verses_tr` VALUES ('4720', '44', '27', '9', 'ικανου δε χρονου διαγενομενου και οντος ηδη επισφαλους του πλοος δια το και την νηστειαν ηδη παρεληλυθεναι παρηνει ο παυλος');
INSERT INTO `bible_verses_tr` VALUES ('4721', '44', '27', '10', 'λεγων αυτοις ανδρες θεωρω οτι μετα υβρεως και πολλης ζημιας ου μονον του φορτου και του πλοιου αλλα και των ψυχων ημων μελλειν εσεσθαι τον πλουν');
INSERT INTO `bible_verses_tr` VALUES ('4722', '44', '27', '11', 'ο δε εκατονταρχος τω κυβερνητη και τω ναυκληρω επειθετο μαλλον η τοις υπο του παυλου λεγομενοις');
INSERT INTO `bible_verses_tr` VALUES ('4723', '44', '27', '12', 'ανευθετου δε του λιμενος υπαρχοντος προς παραχειμασιαν οι πλειους εθεντο βουλην αναχθηναι κακειθεν ειπως δυναιντο καταντησαντες εις φοινικα παραχειμασαι λιμενα της κρητης βλεποντα κατα λιβα και κατα χωρον');
INSERT INTO `bible_verses_tr` VALUES ('4724', '44', '27', '13', 'υποπνευσαντος δε νοτου δοξαντες της προθεσεως κεκρατηκεναι αραντες ασσον παρελεγοντο την κρητην');
INSERT INTO `bible_verses_tr` VALUES ('4725', '44', '27', '14', 'μετ ου πολυ δε εβαλεν κατ αυτης ανεμος τυφωνικος ο καλουμενος ευροκλυδων');
INSERT INTO `bible_verses_tr` VALUES ('4726', '44', '27', '15', 'συναρπασθεντος δε του πλοιου και μη δυναμενου αντοφθαλμειν τω ανεμω επιδοντες εφερομεθα');
INSERT INTO `bible_verses_tr` VALUES ('4727', '44', '27', '16', 'νησιον δε τι υποδραμοντες καλουμενον κλαυδην μολις ισχυσαμεν περικρατεις γενεσθαι της σκαφης');
INSERT INTO `bible_verses_tr` VALUES ('4728', '44', '27', '17', 'ην αραντες βοηθειαις εχρωντο υποζωννυντες το πλοιον φοβουμενοι τε μη εις την συρτιν εκπεσωσιν χαλασαντες το σκευος ουτως εφεροντο');
INSERT INTO `bible_verses_tr` VALUES ('4729', '44', '27', '18', 'σφοδρως δε χειμαζομενων ημων τη εξης εκβολην εποιουντο');
INSERT INTO `bible_verses_tr` VALUES ('4730', '44', '27', '19', 'και τη τριτη αυτοχειρες την σκευην του πλοιου ερριψαμεν');
INSERT INTO `bible_verses_tr` VALUES ('4731', '44', '27', '20', 'μητε δε ηλιου μητε αστρων επιφαινοντων επι πλειονας ημερας χειμωνος τε ουκ ολιγου επικειμενου λοιπον περιηρειτο πασα ελπις του σωζεσθαι ημας');
INSERT INTO `bible_verses_tr` VALUES ('4732', '44', '27', '21', 'πολλης δε ασιτιας υπαρχουσης τοτε σταθεις ο παυλος εν μεσω αυτων ειπεν εδει μεν ω ανδρες πειθαρχησαντας μοι μη αναγεσθαι απο της κρητης κερδησαι τε την υβριν ταυτην και την ζημιαν');
INSERT INTO `bible_verses_tr` VALUES ('4733', '44', '27', '22', 'και  {VAR1: τανυν } {VAR2: τα νυν } παραινω υμας ευθυμειν αποβολη γαρ ψυχης ουδεμια εσται εξ υμων πλην του πλοιου');
INSERT INTO `bible_verses_tr` VALUES ('4734', '44', '27', '23', 'παρεστη γαρ μοι τη νυκτι ταυτη αγγελος του θεου ου ειμι ω και λατρευω');
INSERT INTO `bible_verses_tr` VALUES ('4735', '44', '27', '24', 'λεγων μη φοβου παυλε καισαρι σε δει παραστηναι και ιδου κεχαρισται σοι ο θεος παντας τους πλεοντας μετα σου');
INSERT INTO `bible_verses_tr` VALUES ('4736', '44', '27', '25', 'διο ευθυμειτε ανδρες πιστευω γαρ τω θεω οτι ουτως εσται καθ ον τροπον λελαληται μοι');
INSERT INTO `bible_verses_tr` VALUES ('4737', '44', '27', '26', 'εις νησον δε τινα δει ημας εκπεσειν');
INSERT INTO `bible_verses_tr` VALUES ('4738', '44', '27', '27', 'ως δε τεσσαρεσκαιδεκατη νυξ εγενετο διαφερομενων ημων εν τω αδρια κατα μεσον της νυκτος υπενοουν οι ναυται προσαγειν τινα αυτοις χωραν');
INSERT INTO `bible_verses_tr` VALUES ('4739', '44', '27', '28', 'και βολισαντες ευρον οργυιας εικοσι βραχυ δε διαστησαντες και παλιν βολισαντες ευρον οργυιας δεκαπεντε');
INSERT INTO `bible_verses_tr` VALUES ('4740', '44', '27', '29', 'φοβουμενοι τε μηπως εις τραχεις τοπους  {VAR1: εκπεσωσιν } {VAR2: εκπεσωμεν } εκ πρυμνης ριψαντες αγκυρας τεσσαρας ηυχοντο ημεραν γενεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('4741', '44', '27', '30', 'των δε ναυτων ζητουντων φυγειν εκ του πλοιου και χαλασαντων την σκαφην εις την θαλασσαν προφασει ως εκ πρωρας μελλοντων αγκυρας εκτεινειν');
INSERT INTO `bible_verses_tr` VALUES ('4742', '44', '27', '31', 'ειπεν ο παυλος τω εκατονταρχη και τοις στρατιωταις εαν μη ουτοι μεινωσιν εν τω πλοιω υμεις σωθηναι ου δυνασθε');
INSERT INTO `bible_verses_tr` VALUES ('4743', '44', '27', '32', 'τοτε οι στρατιωται απεκοψαν τα σχοινια της σκαφης και ειασαν αυτην εκπεσειν');
INSERT INTO `bible_verses_tr` VALUES ('4744', '44', '27', '33', 'αχρι δε ου εμελλεν ημερα γινεσθαι παρεκαλει ο παυλος απαντας μεταλαβειν τροφης λεγων τεσσαρεσκαιδεκατην σημερον ημεραν προσδοκωντες ασιτοι διατελειτε μηδεν προσλαβομενοι');
INSERT INTO `bible_verses_tr` VALUES ('4745', '44', '27', '34', 'διο παρακαλω υμας προσλαβειν τροφης τουτο γαρ προς της υμετερας σωτηριας υπαρχει ουδενος γαρ υμων θριξ εκ της κεφαλης πεσειται');
INSERT INTO `bible_verses_tr` VALUES ('4746', '44', '27', '35', 'ειπων δε ταυτα και λαβων αρτον ευχαριστησεν τω θεω ενωπιον παντων και κλασας ηρξατο εσθιειν');
INSERT INTO `bible_verses_tr` VALUES ('4747', '44', '27', '36', 'ευθυμοι δε γενομενοι παντες και αυτοι προσελαβοντο τροφης');
INSERT INTO `bible_verses_tr` VALUES ('4748', '44', '27', '37', 'ημεν δε εν τω πλοιω αι πασαι ψυχαι διακοσιαι εβδομηκονταεξ');
INSERT INTO `bible_verses_tr` VALUES ('4749', '44', '27', '38', 'κορεσθεντες δε τροφης εκουφιζον το πλοιον εκβαλλομενοι τον σιτον εις την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('4750', '44', '27', '39', 'οτε δε ημερα εγενετο την γην ουκ επεγινωσκον κολπον δε τινα κατενοουν εχοντα αιγιαλον εις ον εβουλευσαντο ει δυναιντο εξωσαι το πλοιον');
INSERT INTO `bible_verses_tr` VALUES ('4751', '44', '27', '40', 'και τας αγκυρας περιελοντες ειων εις την θαλασσαν αμα ανεντες τας ζευκτηριας των πηδαλιων και επαραντες τον αρτεμονα τη πνεουση κατειχον εις τον αιγιαλον');
INSERT INTO `bible_verses_tr` VALUES ('4752', '44', '27', '41', 'περιπεσοντες δε εις τοπον διθαλασσον επωκειλαν την ναυν και η μεν πρωρα ερεισασα εμεινεν ασαλευτος η δε πρυμνα ελυετο υπο της βιας των κυματων');
INSERT INTO `bible_verses_tr` VALUES ('4753', '44', '27', '42', 'των δε στρατιωτων βουλη εγενετο ινα τους δεσμωτας αποκτεινωσιν μη τις εκκολυμβησας διαφυγοι');
INSERT INTO `bible_verses_tr` VALUES ('4754', '44', '27', '43', 'ο δε εκατονταρχος βουλομενος διασωσαι τον παυλον εκωλυσεν αυτους του βουληματος εκελευσεν τε τους δυναμενους κολυμβαν απορριψαντας πρωτους επι την γην εξιεναι');
INSERT INTO `bible_verses_tr` VALUES ('4755', '44', '27', '44', 'και τους λοιπους ους μεν επι σανισιν ους δε επι τινων των απο του πλοιου και ουτως εγενετο παντας διασωθηναι επι την γην');
INSERT INTO `bible_verses_tr` VALUES ('4756', '44', '28', '1', 'και διασωθεντες τοτε επεγνωσαν οτι μελιτη η νησος καλειται');
INSERT INTO `bible_verses_tr` VALUES ('4757', '44', '28', '2', 'οι δε βαρβαροι παρειχον ου την τυχουσαν φιλανθρωπιαν ημιν αναψαντες γαρ πυραν προσελαβοντο παντας ημας δια τον υετον τον εφεστωτα και δια το ψυχος');
INSERT INTO `bible_verses_tr` VALUES ('4758', '44', '28', '3', 'συστρεψαντος δε του παυλου φρυγανων πληθος και επιθεντος επι την πυραν εχιδνα εκ της θερμης εξελθουσα καθηψεν της χειρος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4759', '44', '28', '4', 'ως δε ειδον οι βαρβαροι κρεμαμενον το θηριον εκ της χειρος αυτου ελεγον προς αλληλους παντως φονευς εστιν ο ανθρωπος ουτος ον διασωθεντα εκ της θαλασσης η δικη ζην ουκ ειασεν');
INSERT INTO `bible_verses_tr` VALUES ('4760', '44', '28', '5', 'ο μεν ουν αποτιναξας το θηριον εις το πυρ επαθεν ουδεν κακον');
INSERT INTO `bible_verses_tr` VALUES ('4761', '44', '28', '6', 'οι δε προσεδοκων αυτον μελλειν πιμπρασθαι η καταπιπτειν αφνω νεκρον επι πολυ δε αυτων προσδοκωντων και θεωρουντων μηδεν ατοπον εις αυτον γινομενον μεταβαλλομενοι ελεγον θεον αυτον ειναι');
INSERT INTO `bible_verses_tr` VALUES ('4762', '44', '28', '7', 'εν δε τοις περι τον τοπον εκεινον υπηρχεν χωρια τω πρωτω της νησου ονοματι ποπλιω ος αναδεξαμενος ημας τρεις ημερας φιλοφρονως εξενισεν');
INSERT INTO `bible_verses_tr` VALUES ('4763', '44', '28', '8', 'εγενετο δε τον πατερα του ποπλιου πυρετοις και δυσεντερια συνεχομενον κατακεισθαι προς ον ο παυλος εισελθων και προσευξαμενος επιθεις τας χειρας αυτω ιασατο αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4764', '44', '28', '9', 'τουτου ουν γενομενου και οι λοιποι οι εχοντες ασθενειας εν τη νησω προσηρχοντο και εθεραπευοντο');
INSERT INTO `bible_verses_tr` VALUES ('4765', '44', '28', '10', 'οι και πολλαις τιμαις ετιμησαν ημας και αναγομενοις επεθεντο τα προς την χρειαν');
INSERT INTO `bible_verses_tr` VALUES ('4766', '44', '28', '11', 'μετα δε τρεις μηνας ανηχθημεν εν πλοιω παρακεχειμακοτι εν τη νησω αλεξανδρινω παρασημω διοσκουροις');
INSERT INTO `bible_verses_tr` VALUES ('4767', '44', '28', '12', 'και καταχθεντες εις συρακουσας επεμειναμεν ημερας τρεις');
INSERT INTO `bible_verses_tr` VALUES ('4768', '44', '28', '13', 'οθεν περιελθοντες κατηντησαμεν εις ρηγιον και μετα μιαν ημεραν επιγενομενου νοτου δευτεραιοι ηλθομεν εις ποτιολους');
INSERT INTO `bible_verses_tr` VALUES ('4769', '44', '28', '14', 'ου ευροντες αδελφους παρεκληθημεν επ αυτοις επιμειναι ημερας επτα και ουτως εις την ρωμην ηλθομεν');
INSERT INTO `bible_verses_tr` VALUES ('4770', '44', '28', '15', 'κακειθεν οι αδελφοι ακουσαντες τα περι ημων εξηλθον εις απαντησιν ημιν αχρις αππιου φορου και τριων ταβερνων ους ιδων ο παυλος ευχαριστησας τω θεω ελαβεν θαρσος');
INSERT INTO `bible_verses_tr` VALUES ('4771', '44', '28', '16', 'οτε δε ηλθομεν εις ρωμην ο εκατονταρχος παρεδωκεν τους δεσμιους τω στρατοπεδαρχη τω δε παυλω επετραπη μενειν καθ εαυτον συν τω φυλασσοντι αυτον στρατιωτη');
INSERT INTO `bible_verses_tr` VALUES ('4772', '44', '28', '17', 'εγενετο δε μετα ημερας τρεις συγκαλεσασθαι τον παυλον τους οντας των ιουδαιων πρωτους συνελθοντων δε αυτων ελεγεν προς αυτους ανδρες αδελφοι εγω ουδεν εναντιον ποιησας τω λαω η τοις εθεσιν τοις πατρωοις δεσμιος εξ ιεροσολυμων παρεδοθην εις τας χειρας των ρωμαιων');
INSERT INTO `bible_verses_tr` VALUES ('4773', '44', '28', '18', 'οιτινες ανακριναντες με εβουλοντο απολυσαι δια το μηδεμιαν αιτιαν θανατου υπαρχειν εν εμοι');
INSERT INTO `bible_verses_tr` VALUES ('4774', '44', '28', '19', 'αντιλεγοντων δε των ιουδαιων ηναγκασθην επικαλεσασθαι καισαρα ουχ ως του εθνους μου εχων τι κατηγορησαι');
INSERT INTO `bible_verses_tr` VALUES ('4775', '44', '28', '20', 'δια ταυτην ουν την αιτιαν παρεκαλεσα υμας ιδειν και προσλαλησαι ενεκεν γαρ της ελπιδος του ισραηλ την αλυσιν ταυτην περικειμαι');
INSERT INTO `bible_verses_tr` VALUES ('4776', '44', '28', '21', 'οι δε προς αυτον ειπον ημεις ουτε γραμματα περι σου εδεξαμεθα απο της ιουδαιας ουτε παραγενομενος τις των αδελφων απηγγειλεν η ελαλησεν τι περι σου πονηρον');
INSERT INTO `bible_verses_tr` VALUES ('4777', '44', '28', '22', 'αξιουμεν δε παρα σου ακουσαι α φρονεις περι μεν γαρ της αιρεσεως ταυτης γνωστον εστιν ημιν οτι πανταχου αντιλεγεται');
INSERT INTO `bible_verses_tr` VALUES ('4778', '44', '28', '23', 'ταξαμενοι δε αυτω ημεραν ηκον προς αυτον εις την ξενιαν πλειονες οις εξετιθετο διαμαρτυρομενος την βασιλειαν του θεου πειθων τε αυτους τα περι του ιησου απο τε του νομου μωσεως και των προφητων απο πρωι εως εσπερας');
INSERT INTO `bible_verses_tr` VALUES ('4779', '44', '28', '24', 'και οι μεν επειθοντο τοις λεγομενοις οι δε ηπιστουν');
INSERT INTO `bible_verses_tr` VALUES ('4780', '44', '28', '25', 'ασυμφωνοι δε οντες προς αλληλους απελυοντο ειποντος του παυλου ρημα εν οτι καλως το πνευμα το αγιον ελαλησεν δια ησαιου του προφητου προς τους πατερας ημων');
INSERT INTO `bible_verses_tr` VALUES ('4781', '44', '28', '26', 'λεγον πορευθητι προς τον λαον τουτον και ειπε ακοη ακουσετε και ου μη συνητε και βλεποντες βλεψετε και ου μη ιδητε');
INSERT INTO `bible_verses_tr` VALUES ('4782', '44', '28', '27', 'επαχυνθη γαρ η καρδια του λαου τουτου και τοις ωσιν βαρεως ηκουσαν και τους οφθαλμους αυτων εκαμμυσαν μηποτε ιδωσιν τοις οφθαλμοις και τοις ωσιν ακουσωσιν και τη καρδια συνωσιν και επιστρεψωσιν και ιασωμαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('4783', '44', '28', '28', 'γνωστον ουν εστω υμιν οτι τοις εθνεσιν απεσταλη το σωτηριον του θεου αυτοι και ακουσονται');
INSERT INTO `bible_verses_tr` VALUES ('4784', '44', '28', '29', 'και ταυτα αυτου ειποντος απηλθον οι ιουδαιοι πολλην εχοντες εν εαυτοις συζητησιν');
INSERT INTO `bible_verses_tr` VALUES ('4785', '44', '28', '30', 'εμεινεν δε ο παυλος διετιαν ολην εν ιδιω μισθωματι και απεδεχετο παντας τους εισπορευομενους προς αυτον');
INSERT INTO `bible_verses_tr` VALUES ('4786', '44', '28', '31', 'κηρυσσων την βασιλειαν του θεου και διδασκων τα περι του κυριου ιησου χριστου μετα πασης παρρησιας ακωλυτως');
INSERT INTO `bible_verses_tr` VALUES ('4787', '45', '1', '1', 'παυλος δουλος ιησου χριστου κλητος αποστολος αφωρισμενος εις ευαγγελιον θεου');
INSERT INTO `bible_verses_tr` VALUES ('4788', '45', '1', '2', 'ο προεπηγγειλατο δια των προφητων αυτου εν γραφαις αγιαις');
INSERT INTO `bible_verses_tr` VALUES ('4789', '45', '1', '3', 'περι του υιου αυτου του γενομενου εκ σπερματος δαβιδ κατα σαρκα');
INSERT INTO `bible_verses_tr` VALUES ('4790', '45', '1', '4', 'του ορισθεντος υιου θεου εν δυναμει κατα πνευμα αγιωσυνης εξ αναστασεως νεκρων ιησου χριστου του κυριου ημων');
INSERT INTO `bible_verses_tr` VALUES ('4791', '45', '1', '5', 'δι ου ελαβομεν χαριν και αποστολην εις υπακοην πιστεως εν πασιν τοις εθνεσιν υπερ του ονοματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('4792', '45', '1', '6', 'εν οις εστε και υμεις κλητοι ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('4793', '45', '1', '7', 'πασιν τοις ουσιν εν ρωμη αγαπητοις θεου κλητοις αγιοις χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('4794', '45', '1', '8', 'πρωτον μεν ευχαριστω τω θεω μου δια ιησου χριστου υπερ παντων υμων οτι η πιστις υμων καταγγελλεται εν ολω τω κοσμω');
INSERT INTO `bible_verses_tr` VALUES ('4795', '45', '1', '9', 'μαρτυς γαρ μου εστιν ο θεος ω λατρευω εν τω πνευματι μου εν τω ευαγγελιω του υιου αυτου ως αδιαλειπτως μνειαν υμων ποιουμαι');
INSERT INTO `bible_verses_tr` VALUES ('4796', '45', '1', '10', 'παντοτε επι των προσευχων μου δεομενος ειπως ηδη ποτε ευοδωθησομαι εν τω θεληματι του θεου ελθειν προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('4797', '45', '1', '11', 'επιποθω γαρ ιδειν υμας ινα τι μεταδω χαρισμα υμιν πνευματικον εις το στηριχθηναι υμας');
INSERT INTO `bible_verses_tr` VALUES ('4798', '45', '1', '12', 'τουτο δε εστιν συμπαρακληθηναι εν υμιν δια της εν αλληλοις πιστεως υμων τε και εμου');
INSERT INTO `bible_verses_tr` VALUES ('4799', '45', '1', '13', 'ου θελω δε υμας αγνοειν αδελφοι οτι πολλακις προεθεμην ελθειν προς υμας και εκωλυθην αχρι του δευρο ινα καρπον τινα σχω και εν υμιν καθως και εν τοις λοιποις εθνεσιν');
INSERT INTO `bible_verses_tr` VALUES ('4800', '45', '1', '14', 'ελλησιν τε και βαρβαροις σοφοις τε και ανοητοις οφειλετης ειμι');
INSERT INTO `bible_verses_tr` VALUES ('4801', '45', '1', '15', 'ουτως το κατ εμε προθυμον και υμιν τοις εν ρωμη ευαγγελισασθαι');
INSERT INTO `bible_verses_tr` VALUES ('4802', '45', '1', '16', 'ου γαρ επαισχυνομαι το ευαγγελιον του χριστου δυναμις γαρ θεου εστιν εις σωτηριαν παντι τω πιστευοντι ιουδαιω τε πρωτον και ελληνι');
INSERT INTO `bible_verses_tr` VALUES ('4803', '45', '1', '17', 'δικαιοσυνη γαρ θεου εν αυτω αποκαλυπτεται εκ πιστεως εις πιστιν καθως γεγραπται ο δε δικαιος εκ πιστεως ζησεται');
INSERT INTO `bible_verses_tr` VALUES ('4804', '45', '1', '18', 'αποκαλυπτεται γαρ οργη θεου απ ουρανου επι πασαν ασεβειαν και αδικιαν ανθρωπων των την αληθειαν εν αδικια κατεχοντων');
INSERT INTO `bible_verses_tr` VALUES ('4805', '45', '1', '19', 'διοτι το γνωστον του θεου φανερον εστιν εν αυτοις ο γαρ θεος αυτοις εφανερωσεν');
INSERT INTO `bible_verses_tr` VALUES ('4806', '45', '1', '20', 'τα γαρ αορατα αυτου απο κτισεως κοσμου τοις ποιημασιν νοουμενα καθοραται η τε αιδιος αυτου δυναμις και θειοτης εις το ειναι αυτους αναπολογητους');
INSERT INTO `bible_verses_tr` VALUES ('4807', '45', '1', '21', 'διοτι γνοντες τον θεον ουχ ως θεον εδοξασαν η ευχαριστησαν αλλ εματαιωθησαν εν τοις διαλογισμοις αυτων και εσκοτισθη η ασυνετος αυτων καρδια');
INSERT INTO `bible_verses_tr` VALUES ('4808', '45', '1', '22', 'φασκοντες ειναι σοφοι εμωρανθησαν');
INSERT INTO `bible_verses_tr` VALUES ('4809', '45', '1', '23', 'και ηλλαξαν την δοξαν του αφθαρτου θεου εν ομοιωματι εικονος φθαρτου ανθρωπου και πετεινων και τετραποδων και ερπετων');
INSERT INTO `bible_verses_tr` VALUES ('4810', '45', '1', '24', 'διο και παρεδωκεν αυτους ο θεος εν ταις επιθυμιαις των καρδιων αυτων εις ακαθαρσιαν του ατιμαζεσθαι τα σωματα αυτων εν εαυτοις');
INSERT INTO `bible_verses_tr` VALUES ('4811', '45', '1', '25', 'οιτινες μετηλλαξαν την αληθειαν του θεου εν τω ψευδει και εσεβασθησαν και ελατρευσαν τη κτισει παρα τον κτισαντα ος εστιν ευλογητος εις τους αιωνας αμην');
INSERT INTO `bible_verses_tr` VALUES ('4812', '45', '1', '26', 'δια τουτο παρεδωκεν αυτους ο θεος εις παθη ατιμιας αι τε γαρ θηλειαι αυτων μετηλλαξαν την φυσικην χρησιν εις την παρα φυσιν');
INSERT INTO `bible_verses_tr` VALUES ('4813', '45', '1', '27', 'ομοιως τε και οι  {VAR1: αρρενες } {VAR2: αρσενες } αφεντες την φυσικην χρησιν της θηλειας εξεκαυθησαν εν τη ορεξει αυτων εις αλληλους αρσενες εν αρσεσιν την ασχημοσυνην κατεργαζομενοι και την αντιμισθιαν ην εδει της πλανης αυτων εν εαυτοις απολαμβανοντες');
INSERT INTO `bible_verses_tr` VALUES ('4814', '45', '1', '28', 'και καθως ουκ εδοκιμασαν τον θεον εχειν εν επιγνωσει παρεδωκεν αυτους ο θεος εις αδοκιμον νουν ποιειν τα μη καθηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('4815', '45', '1', '29', 'πεπληρωμενους παση αδικια πορνεια πονηρια πλεονεξια κακια μεστους φθονου φονου εριδος δολου κακοηθειας ψιθυριστας');
