INSERT INTO `bible_verses_tr` VALUES ('5028', '45', '9', '17', 'λεγει γαρ η γραφη τω φαραω οτι εις αυτο τουτο εξηγειρα σε οπως ενδειξωμαι εν σοι την δυναμιν μου και οπως διαγγελη το ονομα μου εν παση τη γη');
INSERT INTO `bible_verses_tr` VALUES ('5029', '45', '9', '18', 'αρα ουν ον θελει ελεει ον δε θελει σκληρυνει');
INSERT INTO `bible_verses_tr` VALUES ('5030', '45', '9', '19', 'ερεις ουν μοι τι ετι μεμφεται τω γαρ βουληματι αυτου τις ανθεστηκεν');
INSERT INTO `bible_verses_tr` VALUES ('5031', '45', '9', '20', 'μενουνγε ω ανθρωπε συ τις ει ο ανταποκρινομενος τω θεω μη ερει το πλασμα τω πλασαντι τι με εποιησας ουτως');
INSERT INTO `bible_verses_tr` VALUES ('5032', '45', '9', '21', 'η ουκ εχει εξουσιαν ο κεραμευς του πηλου εκ του αυτου φυραματος ποιησαι ο μεν εις τιμην σκευος ο δε εις ατιμιαν');
INSERT INTO `bible_verses_tr` VALUES ('5033', '45', '9', '22', 'ει δε θελων ο θεος ενδειξασθαι την οργην και γνωρισαι το δυνατον αυτου ηνεγκεν εν πολλη μακροθυμια σκευη οργης κατηρτισμενα εις απωλειαν');
INSERT INTO `bible_verses_tr` VALUES ('5034', '45', '9', '23', 'και ινα γνωριση τον πλουτον της δοξης αυτου επι σκευη ελεους α προητοιμασεν εις δοξαν');
INSERT INTO `bible_verses_tr` VALUES ('5035', '45', '9', '24', 'ους και εκαλεσεν ημας ου μονον εξ ιουδαιων αλλα και εξ εθνων');
INSERT INTO `bible_verses_tr` VALUES ('5036', '45', '9', '25', 'ως και εν τω ωσηε λεγει καλεσω τον ου λαον μου λαον μου και την ουκ ηγαπημενην ηγαπημενην');
INSERT INTO `bible_verses_tr` VALUES ('5037', '45', '9', '26', 'και εσται εν τω τοπω ου ερρηθη αυτοις ου λαος μου υμεις εκει κληθησονται υιοι θεου ζωντος');
INSERT INTO `bible_verses_tr` VALUES ('5038', '45', '9', '27', 'ησαιας δε κραζει υπερ του ισραηλ εαν η ο αριθμος των υιων ισραηλ ως η αμμος της θαλασσης το καταλειμμα σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5039', '45', '9', '28', 'λογον γαρ συντελων και συντεμνων εν δικαιοσυνη οτι λογον συντετμημενον ποιησει κυριος επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('5040', '45', '9', '29', 'και καθως προειρηκεν ησαιας ει μη κυριος σαβαωθ εγκατελιπεν ημιν σπερμα ως σοδομα αν εγενηθημεν και ως γομορρα αν ωμοιωθημεν');
INSERT INTO `bible_verses_tr` VALUES ('5041', '45', '9', '30', 'τι ουν ερουμεν οτι εθνη τα μη διωκοντα δικαιοσυνην κατελαβεν δικαιοσυνην δικαιοσυνην δε την εκ πιστεως');
INSERT INTO `bible_verses_tr` VALUES ('5042', '45', '9', '31', 'ισραηλ δε διωκων νομον δικαιοσυνης εις νομον δικαιοσυνης ουκ εφθασεν');
INSERT INTO `bible_verses_tr` VALUES ('5043', '45', '9', '32', 'δια τι οτι ουκ εκ πιστεως αλλ ως εξ εργων νομου προσεκοψαν γαρ τω λιθω του προσκομματος');
INSERT INTO `bible_verses_tr` VALUES ('5044', '45', '9', '33', 'καθως γεγραπται ιδου τιθημι εν σιων λιθον προσκομματος και πετραν σκανδαλου και πας ο πιστευων επ αυτω ου καταισχυνθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5045', '45', '10', '1', 'αδελφοι η μεν ευδοκια της εμης καρδιας και η δεησις η προς τον θεον υπερ του ισραηλ εστιν εις σωτηριαν');
INSERT INTO `bible_verses_tr` VALUES ('5046', '45', '10', '2', 'μαρτυρω γαρ αυτοις οτι ζηλον θεου εχουσιν αλλ ου κατ επιγνωσιν');
INSERT INTO `bible_verses_tr` VALUES ('5047', '45', '10', '3', 'αγνοουντες γαρ την του θεου δικαιοσυνην και την ιδιαν δικαιοσυνην ζητουντες στησαι τη δικαιοσυνη του θεου ουχ υπεταγησαν');
