INSERT INTO `bible_verses_tr` VALUES ('5049', '45', '10', '5', 'μωσης γαρ γραφει την δικαιοσυνην την εκ του νομου οτι ο ποιησας αυτα ανθρωπος ζησεται εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('5050', '45', '10', '6', 'η δε εκ πιστεως δικαιοσυνη ουτως λεγει μη ειπης εν τη καρδια σου τις αναβησεται εις τον ουρανον τουτ εστιν χριστον καταγαγειν');
INSERT INTO `bible_verses_tr` VALUES ('5051', '45', '10', '7', 'η τις καταβησεται εις την αβυσσον τουτ εστιν χριστον εκ νεκρων αναγαγειν');
INSERT INTO `bible_verses_tr` VALUES ('5052', '45', '10', '8', 'αλλα τι λεγει εγγυς σου το ρημα εστιν εν τω στοματι σου και εν τη καρδια σου τουτ εστιν το ρημα της πιστεως ο κηρυσσομεν');
INSERT INTO `bible_verses_tr` VALUES ('5053', '45', '10', '9', 'οτι εαν ομολογησης εν τω στοματι σου κυριον ιησουν και πιστευσης εν τη καρδια σου οτι ο θεος αυτον ηγειρεν εκ νεκρων σωθηση');
INSERT INTO `bible_verses_tr` VALUES ('5054', '45', '10', '10', 'καρδια γαρ πιστευεται εις δικαιοσυνην στοματι δε ομολογειται εις σωτηριαν');
INSERT INTO `bible_verses_tr` VALUES ('5055', '45', '10', '11', 'λεγει γαρ η γραφη πας ο πιστευων επ αυτω ου καταισχυνθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5056', '45', '10', '12', 'ου γαρ εστιν διαστολη ιουδαιου τε και ελληνος ο γαρ αυτος κυριος παντων πλουτων εις παντας τους επικαλουμενους αυτον');
INSERT INTO `bible_verses_tr` VALUES ('5057', '45', '10', '13', 'πας γαρ ος αν επικαλεσηται το ονομα κυριου σωθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5058', '45', '10', '14', 'πως ουν επικαλεσονται εις ον ουκ επιστευσαν πως δε πιστευσουσιν ου ουκ ηκουσαν πως δε ακουσουσιν χωρις κηρυσσοντος');
INSERT INTO `bible_verses_tr` VALUES ('5059', '45', '10', '15', 'πως δε κηρυξουσιν εαν μη αποσταλωσιν καθως γεγραπται ως ωραιοι οι ποδες των ευαγγελιζομενων ειρηνην των ευαγγελιζομενων τα αγαθα');
