INSERT INTO `bible_verses_tr` VALUES ('5061', '45', '10', '17', 'αρα η πιστις εξ ακοης η δε ακοη δια ρηματος θεου');
INSERT INTO `bible_verses_tr` VALUES ('5062', '45', '10', '18', 'αλλα λεγω μη ουκ ηκουσαν μενουνγε εις πασαν την γην εξηλθεν ο φθογγος αυτων και εις τα περατα της οικουμενης τα ρηματα αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5063', '45', '10', '19', 'αλλα λεγω μη ουκ εγνω ισραηλ πρωτος μωσης λεγει εγω παραζηλωσω υμας επ ουκ εθνει επι εθνει ασυνετω παροργιω υμας');
INSERT INTO `bible_verses_tr` VALUES ('5064', '45', '10', '20', 'ησαιας δε αποτολμα και λεγει ευρεθην τοις εμε μη ζητουσιν εμφανης εγενομην τοις εμε μη επερωτωσιν');
INSERT INTO `bible_verses_tr` VALUES ('5065', '45', '10', '21', 'προς δε τον ισραηλ λεγει ολην την ημεραν εξεπετασα τας χειρας μου προς λαον απειθουντα και αντιλεγοντα');
INSERT INTO `bible_verses_tr` VALUES ('5066', '45', '11', '1', 'λεγω ουν μη απωσατο ο θεος τον λαον αυτου μη γενοιτο και γαρ εγω ισραηλιτης ειμι εκ σπερματος αβρααμ φυλης βενιαμιν');
INSERT INTO `bible_verses_tr` VALUES ('5067', '45', '11', '2', 'ουκ απωσατο ο θεος τον λαον αυτου ον προεγνω η ουκ οιδατε εν ηλια τι λεγει η γραφη ως εντυγχανει τω θεω κατα του ισραηλ λεγων');
INSERT INTO `bible_verses_tr` VALUES ('5068', '45', '11', '3', 'κυριε τους προφητας σου απεκτειναν και τα θυσιαστηρια σου κατεσκαψαν καγω υπελειφθην μονος και ζητουσιν την ψυχην μου');
INSERT INTO `bible_verses_tr` VALUES ('5069', '45', '11', '4', 'αλλα τι λεγει αυτω ο χρηματισμος κατελιπον εμαυτω επτακισχιλιους ανδρας οιτινες ουκ εκαμψαν γονυ τη βααλ');
