INSERT INTO `bible_verses_tr` VALUES ('5071', '45', '11', '6', 'ει δε χαριτι ουκετι εξ εργων επει η χαρις ουκετι γινεται χαρις ει δε εξ εργων ουκετι εστιν χαρις επει το εργον ουκετι εστιν εργον');
INSERT INTO `bible_verses_tr` VALUES ('5072', '45', '11', '7', 'τι ουν ο επιζητει ισραηλ τουτου ουκ επετυχεν η δε εκλογη επετυχεν οι δε λοιποι επωρωθησαν');
INSERT INTO `bible_verses_tr` VALUES ('5073', '45', '11', '8', 'καθως γεγραπται εδωκεν αυτοις ο θεος πνευμα κατανυξεως οφθαλμους του μη βλεπειν και ωτα του μη ακουειν εως της σημερον ημερας');
INSERT INTO `bible_verses_tr` VALUES ('5074', '45', '11', '9', 'και δαβιδ λεγει γενηθητω η τραπεζα αυτων εις παγιδα και εις θηραν και εις σκανδαλον και εις ανταποδομα αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('5075', '45', '11', '10', 'σκοτισθητωσαν οι οφθαλμοι αυτων του μη βλεπειν και τον νωτον αυτων διαπαντος συγκαμψον');
INSERT INTO `bible_verses_tr` VALUES ('5076', '45', '11', '11', 'λεγω ουν μη επταισαν ινα πεσωσιν μη γενοιτο αλλα τω αυτων παραπτωματι η σωτηρια τοις εθνεσιν εις το παραζηλωσαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('5077', '45', '11', '12', 'ει δε το παραπτωμα αυτων πλουτος κοσμου και το ηττημα αυτων πλουτος εθνων ποσω μαλλον το πληρωμα αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5078', '45', '11', '13', 'υμιν γαρ λεγω τοις εθνεσιν εφ οσον μεν ειμι εγω εθνων αποστολος την διακονιαν μου δοξαζω');
INSERT INTO `bible_verses_tr` VALUES ('5079', '45', '11', '14', 'ει πως παραζηλωσω μου την σαρκα και σωσω τινας εξ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5080', '45', '11', '15', 'ει γαρ η αποβολη αυτων καταλλαγη κοσμου τις η προσληψις ει μη ζωη εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('5081', '45', '11', '16', 'ει δε η απαρχη αγια και το φυραμα και ει η ριζα αγια και οι κλαδοι');
INSERT INTO `bible_verses_tr` VALUES ('5082', '45', '11', '17', 'ει δε τινες των κλαδων εξεκλασθησαν συ δε αγριελαιος ων ενεκεντρισθης εν αυτοις και συγκοινωνος της ριζης και της πιοτητος της ελαιας εγενου');
INSERT INTO `bible_verses_tr` VALUES ('5083', '45', '11', '18', 'μη κατακαυχω των κλαδων ει δε κατακαυχασαι ου συ την ριζαν βασταζεις αλλ η ριζα σε');
INSERT INTO `bible_verses_tr` VALUES ('5084', '45', '11', '19', 'ερεις ουν εξεκλασθησαν οι κλαδοι ινα εγω εγκεντρισθω');
INSERT INTO `bible_verses_tr` VALUES ('5085', '45', '11', '20', 'καλως τη απιστια εξεκλασθησαν συ δε τη πιστει εστηκας μη υψηλοφρονει αλλα φοβου');
INSERT INTO `bible_verses_tr` VALUES ('5086', '45', '11', '21', 'ει γαρ ο θεος των κατα φυσιν κλαδων ουκ εφεισατο μηπως ουδε σου φεισηται');
INSERT INTO `bible_verses_tr` VALUES ('5087', '45', '11', '22', 'ιδε ουν χρηστοτητα και αποτομιαν θεου επι μεν τους πεσοντας αποτομιαν επι δε σε χρηστοτητα εαν επιμεινης τη χρηστοτητι επει και συ εκκοπηση');
INSERT INTO `bible_verses_tr` VALUES ('5088', '45', '11', '23', 'και εκεινοι δε εαν μη επιμεινωσιν τη απιστια εγκεντρισθησονται δυνατος γαρ εστιν ο θεος παλιν εγκεντρισαι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('5089', '45', '11', '24', 'ει γαρ συ εκ της κατα φυσιν εξεκοπης αγριελαιου και παρα φυσιν ενεκεντρισθης εις καλλιελαιον ποσω μαλλον ουτοι οι κατα φυσιν εγκεντρισθησονται τη ιδια ελαια');
INSERT INTO `bible_verses_tr` VALUES ('5090', '45', '11', '25', 'ου γαρ θελω υμας αγνοειν αδελφοι το μυστηριον τουτο ινα μη ητε παρ εαυτοις φρονιμοι οτι πωρωσις απο μερους τω ισραηλ γεγονεν αχρις ου το πληρωμα των εθνων εισελθη');
INSERT INTO `bible_verses_tr` VALUES ('5091', '45', '11', '26', 'και ουτως πας ισραηλ σωθησεται καθως γεγραπται ηξει εκ σιων ο ρυομενος και αποστρεψει ασεβειας απο ιακωβ');
INSERT INTO `bible_verses_tr` VALUES ('5092', '45', '11', '27', 'και αυτη αυτοις η παρ εμου διαθηκη οταν αφελωμαι τας αμαρτιας αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5093', '45', '11', '28', 'κατα μεν το ευαγγελιον εχθροι δι υμας κατα δε την εκλογην αγαπητοι δια τους πατερας');
INSERT INTO `bible_verses_tr` VALUES ('5094', '45', '11', '29', 'αμεταμελητα γαρ τα χαρισματα και η κλησις του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5095', '45', '11', '30', 'ωσπερ γαρ και υμεις ποτε ηπειθησατε τω θεω νυν δε ηλεηθητε τη τουτων απειθεια');
