INSERT INTO `bible_verses_tr` VALUES ('5097', '45', '11', '32', 'συνεκλεισεν γαρ ο θεος τους παντας εις απειθειαν ινα τους παντας ελεηση');
INSERT INTO `bible_verses_tr` VALUES ('5098', '45', '11', '33', 'ω βαθος πλουτου και σοφιας και γνωσεως θεου ως ανεξερευνητα τα κριματα αυτου και ανεξιχνιαστοι αι οδοι αυτου');
