INSERT INTO `bible_verses_tr` VALUES ('5107', '45', '12', '6', 'εχοντες δε χαρισματα κατα την χαριν την δοθεισαν ημιν διαφορα ειτε προφητειαν κατα την αναλογιαν της πιστεως');
