INSERT INTO `bible_verses_tr` VALUES ('5109', '45', '12', '8', 'ειτε ο παρακαλων εν τη παρακλησει ο μεταδιδους εν απλοτητι ο προισταμενος εν σπουδη ο ελεων εν ιλαροτητι');
INSERT INTO `bible_verses_tr` VALUES ('5110', '45', '12', '9', 'η αγαπη ανυποκριτος αποστυγουντες το πονηρον κολλωμενοι τω αγαθω');
INSERT INTO `bible_verses_tr` VALUES ('5111', '45', '12', '10', 'τη φιλαδελφια εις αλληλους φιλοστοργοι τη τιμη αλληλους προηγουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('5112', '45', '12', '11', 'τη σπουδη μη οκνηροι τω πνευματι ζεοντες τω  {VAR1: καιρω } {VAR2: κυριω } δουλευοντες');
