INSERT INTO `bible_verses_tr` VALUES ('5115', '45', '12', '14', 'ευλογειτε τους διωκοντας υμας ευλογειτε και μη καταρασθε');
INSERT INTO `bible_verses_tr` VALUES ('5116', '45', '12', '15', 'χαιρειν μετα χαιροντων και κλαιειν μετα κλαιοντων');
INSERT INTO `bible_verses_tr` VALUES ('5117', '45', '12', '16', 'το αυτο εις αλληλους φρονουντες μη τα υψηλα φρονουντες αλλα τοις ταπεινοις συναπαγομενοι μη γινεσθε φρονιμοι παρ εαυτοις');
