INSERT INTO `bible_verses_tr` VALUES ('5120', '45', '12', '19', 'μη εαυτους εκδικουντες αγαπητοι αλλα δοτε τοπον τη οργη γεγραπται γαρ εμοι εκδικησις εγω ανταποδωσω λεγει κυριος');
INSERT INTO `bible_verses_tr` VALUES ('5121', '45', '12', '20', 'εαν ουν πεινα ο εχθρος σου ψωμιζε αυτον εαν διψα ποτιζε αυτον τουτο γαρ ποιων ανθρακας πυρος σωρευσεις επι την κεφαλην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5122', '45', '12', '21', 'μη νικω υπο του κακου αλλα νικα εν τω αγαθω το κακον');
INSERT INTO `bible_verses_tr` VALUES ('5123', '45', '13', '1', 'πασα ψυχη εξουσιαις υπερεχουσαις υποτασσεσθω ου γαρ εστιν εξουσια ει μη απο θεου αι δε ουσαι εξουσιαι υπο του θεου τεταγμεναι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('5124', '45', '13', '2', 'ωστε ο αντιτασσομενος τη εξουσια τη του θεου διαταγη ανθεστηκεν οι δε ανθεστηκοτες εαυτοις κριμα ληψονται');
INSERT INTO `bible_verses_tr` VALUES ('5125', '45', '13', '3', 'οι γαρ αρχοντες ουκ εισιν φοβος των αγαθων εργων αλλα των κακων θελεις δε μη φοβεισθαι την εξουσιαν το αγαθον ποιει και εξεις επαινον εξ αυτης');
INSERT INTO `bible_verses_tr` VALUES ('5126', '45', '13', '4', 'θεου γαρ διακονος εστιν σοι εις το αγαθον εαν δε το κακον ποιης φοβου ου γαρ εικη την μαχαιραν φορει θεου γαρ διακονος εστιν εκδικος εις οργην τω το κακον πρασσοντι');
INSERT INTO `bible_verses_tr` VALUES ('5127', '45', '13', '5', 'διο αναγκη υποτασσεσθαι ου μονον δια την οργην αλλα και δια την συνειδησιν');
INSERT INTO `bible_verses_tr` VALUES ('5128', '45', '13', '6', 'δια τουτο γαρ και φορους τελειτε λειτουργοι γαρ θεου εισιν εις αυτο τουτο προσκαρτερουντες');
INSERT INTO `bible_verses_tr` VALUES ('5129', '45', '13', '7', 'αποδοτε ουν πασιν τας οφειλας τω τον φορον τον φορον τω το τελος το τελος τω τον φοβον τον φοβον τω την τιμην την τιμην');
INSERT INTO `bible_verses_tr` VALUES ('5130', '45', '13', '8', 'μηδενι μηδεν οφειλετε ει μη το αγαπαν αλληλους ο γαρ αγαπων τον ετερον νομον πεπληρωκεν');
INSERT INTO `bible_verses_tr` VALUES ('5131', '45', '13', '9', 'το γαρ ου μοιχευσεις ου φονευσεις ου κλεψεις ου ψευδομαρτυρησεις ουκ επιθυμησεις και ει τις ετερα εντολη εν τουτω τω λογω ανακεφαλαιουται εν τω αγαπησεις τον πλησιον σου ως εαυτον');
INSERT INTO `bible_verses_tr` VALUES ('5132', '45', '13', '10', 'η αγαπη τω πλησιον κακον ουκ εργαζεται πληρωμα ουν νομου η αγαπη');
