INSERT INTO `bible_verses_tr` VALUES ('5134', '45', '13', '12', 'η νυξ προεκοψεν η δε ημερα ηγγικεν αποθωμεθα ουν τα εργα του σκοτους και ενδυσωμεθα τα οπλα του φωτος');
INSERT INTO `bible_verses_tr` VALUES ('5135', '45', '13', '13', 'ως εν ημερα ευσχημονως περιπατησωμεν μη κωμοις και μεθαις μη κοιταις και ασελγειαις μη εριδι και ζηλω');
