INSERT INTO `bible_verses_tr` VALUES ('5149', '45', '14', '13', 'μηκετι ουν αλληλους κρινωμεν αλλα τουτο κρινατε μαλλον το μη τιθεναι προσκομμα τω αδελφω η σκανδαλον');
