INSERT INTO `bible_verses_tr` VALUES ('5163', '45', '15', '4', 'οσα γαρ προεγραφη εις την ημετεραν διδασκαλιαν προεγραφη ινα δια της υπομονης και της παρακλησεως των γραφων την ελπιδα εχωμεν');
