INSERT INTO `bible_verses_tr` VALUES ('5168', '45', '15', '9', 'τα δε εθνη υπερ ελεους δοξασαι τον θεον καθως γεγραπται δια τουτο εξομολογησομαι σοι εν εθνεσιν και τω ονοματι σου ψαλω');
