INSERT INTO `bible_verses_tr` VALUES ('5170', '45', '15', '11', 'και παλιν αινειτε τον κυριον παντα τα εθνη και επαινεσατε αυτον παντες οι λαοι');
INSERT INTO `bible_verses_tr` VALUES ('5171', '45', '15', '12', 'και παλιν ησαιας λεγει εσται η ριζα του ιεσσαι και ο ανισταμενος αρχειν εθνων επ αυτω εθνη ελπιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5172', '45', '15', '13', 'ο δε θεος της ελπιδος πληρωσαι υμας πασης χαρας και ειρηνης εν τω πιστευειν εις το περισσευειν υμας εν τη ελπιδι εν δυναμει πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('5173', '45', '15', '14', 'πεπεισμαι δε αδελφοι μου και αυτος εγω περι υμων οτι και αυτοι μεστοι εστε αγαθωσυνης πεπληρωμενοι πασης γνωσεως δυναμενοι και αλληλους νουθετειν');
INSERT INTO `bible_verses_tr` VALUES ('5174', '45', '15', '15', 'τολμηροτερον δε εγραψα υμιν αδελφοι απο μερους ως επαναμιμνησκων υμας δια την χαριν την δοθεισαν μοι υπο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5175', '45', '15', '16', 'εις το ειναι με λειτουργον ιησου χριστου εις τα εθνη ιερουργουντα το ευαγγελιον του θεου ινα γενηται η προσφορα των εθνων ευπροσδεκτος ηγιασμενη εν πνευματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('5176', '45', '15', '17', 'εχω ουν καυχησιν εν χριστω ιησου τα προς θεον');
