INSERT INTO `bible_verses_tr` VALUES ('5178', '45', '15', '19', 'εν δυναμει σημειων και τερατων εν δυναμει πνευματος θεου ωστε με απο ιερουσαλημ και κυκλω μεχρι του ιλλυρικου πεπληρωκεναι το ευαγγελιον του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5179', '45', '15', '20', 'ουτως δε φιλοτιμουμενον ευαγγελιζεσθαι ουχ οπου ωνομασθη χριστος ινα μη επ αλλοτριον θεμελιον οικοδομω');
INSERT INTO `bible_verses_tr` VALUES ('5180', '45', '15', '21', 'αλλα καθως γεγραπται οις ουκ ανηγγελη περι αυτου οψονται και οι ουκ ακηκοασιν συνησουσιν');
