INSERT INTO `bible_verses_tr` VALUES ('5185', '45', '15', '26', 'ευδοκησαν γαρ μακεδονια και αχαια κοινωνιαν τινα ποιησασθαι εις τους πτωχους των αγιων των εν ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('5186', '45', '15', '27', 'ευδοκησαν γαρ και οφειλεται αυτων εισιν ει γαρ τοις πνευματικοις αυτων εκοινωνησαν τα εθνη οφειλουσιν και εν τοις σαρκικοις λειτουργησαι αυτοις');
