INSERT INTO `bible_verses_tr` VALUES ('5188', '45', '15', '29', 'οιδα δε οτι ερχομενος προς υμας εν πληρωματι ευλογιας του ευαγγελιου του χριστου ελευσομαι');
INSERT INTO `bible_verses_tr` VALUES ('5189', '45', '15', '30', 'παρακαλω δε υμας αδελφοι δια του κυριου ημων ιησου χριστου και δια της αγαπης του πνευματος συναγωνισασθαι μοι εν ταις προσευχαις υπερ εμου προς τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('5190', '45', '15', '31', 'ινα ρυσθω απο των απειθουντων εν τη ιουδαια και ινα η διακονια μου η εις ιερουσαλημ ευπροσδεκτος γενηται τοις αγιοις');
INSERT INTO `bible_verses_tr` VALUES ('5191', '45', '15', '32', 'ινα εν χαρα ελθω προς υμας δια θεληματος θεου και συναναπαυσωμαι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5192', '45', '15', '33', 'ο δε θεος της ειρηνης μετα παντων υμων αμην');
INSERT INTO `bible_verses_tr` VALUES ('5193', '45', '16', '1', 'συνιστημι δε υμιν φοιβην την αδελφην ημων ουσαν διακονον της εκκλησιας της εν κεγχρεαις');
INSERT INTO `bible_verses_tr` VALUES ('5194', '45', '16', '2', 'ινα αυτην προσδεξησθε εν κυριω αξιως των αγιων και παραστητε αυτη εν ω αν υμων χρηζη πραγματι και γαρ αυτη προστατις πολλων εγενηθη και αυτου εμου');
INSERT INTO `bible_verses_tr` VALUES ('5195', '45', '16', '3', 'ασπασασθε πρισκιλλαν και ακυλαν τους συνεργους μου εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('5196', '45', '16', '4', 'οιτινες υπερ της ψυχης μου τον εαυτων τραχηλον υπεθηκαν οις ουκ εγω μονος ευχαριστω αλλα και πασαι αι εκκλησιαι των εθνων');
INSERT INTO `bible_verses_tr` VALUES ('5197', '45', '16', '5', 'και την κατ οικον αυτων εκκλησιαν ασπασασθε επαινετον τον αγαπητον μου ος εστιν απαρχη της αχαιας εις χριστον');
INSERT INTO `bible_verses_tr` VALUES ('5198', '45', '16', '6', 'ασπασασθε μαριαμ ητις πολλα εκοπιασεν εις ημας');
INSERT INTO `bible_verses_tr` VALUES ('5199', '45', '16', '7', 'ασπασασθε ανδρονικον και ιουνιαν τους συγγενεις μου και συναιχμαλωτους μου οιτινες εισιν επισημοι εν τοις αποστολοις οι και προ εμου γεγονασιν εν χριστω');
INSERT INTO `bible_verses_tr` VALUES ('5200', '45', '16', '8', 'ασπασασθε αμπλιαν τον αγαπητον μου εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5201', '45', '16', '9', 'ασπασασθε ουρβανον τον συνεργον ημων εν χριστω και σταχυν τον αγαπητον μου');
INSERT INTO `bible_verses_tr` VALUES ('5202', '45', '16', '10', 'ασπασασθε απελλην τον δοκιμον εν χριστω ασπασασθε τους εκ των αριστοβουλου');
INSERT INTO `bible_verses_tr` VALUES ('5203', '45', '16', '11', 'ασπασασθε ηροδιωνα τον συγγενη μου ασπασασθε τους εκ των ναρκισσου τους οντας εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5204', '45', '16', '12', 'ασπασασθε τρυφαιναν και τρυφωσαν τας κοπιωσας εν κυριω ασπασασθε περσιδα την αγαπητην ητις πολλα εκοπιασεν εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5205', '45', '16', '13', 'ασπασασθε ρουφον τον εκλεκτον εν κυριω και την μητερα αυτου και εμου');
INSERT INTO `bible_verses_tr` VALUES ('5206', '45', '16', '14', 'ασπασασθε ασυγκριτον φλεγοντα ερμαν πατροβαν ερμην και τους συν αυτοις αδελφους');
INSERT INTO `bible_verses_tr` VALUES ('5207', '45', '16', '15', 'ασπασασθε φιλολογον και ιουλιαν νηρεα και την αδελφην αυτου και ολυμπαν και τους συν αυτοις παντας αγιους');
INSERT INTO `bible_verses_tr` VALUES ('5208', '45', '16', '16', 'ασπασασθε αλληλους εν φιληματι αγιω ασπαζονται υμας αι εκκλησιαι του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5209', '45', '16', '17', 'παρακαλω δε υμας αδελφοι σκοπειν τους τας διχοστασιας και τα σκανδαλα παρα την διδαχην ην υμεις εμαθετε ποιουντας και εκκλινατε απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5210', '45', '16', '18', 'οι γαρ τοιουτοι τω κυριω ημων ιησου χριστω ου δουλευουσιν αλλα τη εαυτων κοιλια και δια της χρηστολογιας και ευλογιας εξαπατωσιν τας καρδιας των ακακων');
INSERT INTO `bible_verses_tr` VALUES ('5211', '45', '16', '19', 'η γαρ υμων υπακοη εις παντας αφικετο χαιρω ουν το εφ υμιν θελω δε υμας σοφους μεν ειναι εις το αγαθον ακεραιους δε εις το κακον');
INSERT INTO `bible_verses_tr` VALUES ('5212', '45', '16', '20', 'ο δε θεος της ειρηνης συντριψει τον σαταναν υπο τους ποδας υμων εν ταχει η χαρις του κυριου ημων ιησου χριστου μεθ υμων  {VAR2: αμην }');
INSERT INTO `bible_verses_tr` VALUES ('5213', '45', '16', '21', 'ασπαζονται υμας τιμοθεος ο συνεργος μου και λουκιος και ιασων και σωσιπατρος οι συγγενεις μου');
INSERT INTO `bible_verses_tr` VALUES ('5214', '45', '16', '22', 'ασπαζομαι υμας εγω τερτιος ο γραψας την επιστολην εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5215', '45', '16', '23', 'ασπαζεται υμας γαιος ο ξενος μου και της εκκλησιας ολης ασπαζεται υμας εραστος ο οικονομος της πολεως και κουαρτος ο αδελφος');
INSERT INTO `bible_verses_tr` VALUES ('5216', '45', '16', '24', 'η χαρις του κυριου ημων ιησου χριστου μετα παντων υμων αμην');
INSERT INTO `bible_verses_tr` VALUES ('5217', '45', '16', '25', 'τω δε δυναμενω υμας στηριξαι κατα το ευαγγελιον μου και το κηρυγμα ιησου χριστου κατα αποκαλυψιν μυστηριου χρονοις αιωνιοις σεσιγημενου');
INSERT INTO `bible_verses_tr` VALUES ('5218', '45', '16', '26', 'φανερωθεντος δε νυν δια τε γραφων προφητικων κατ επιταγην του αιωνιου θεου εις υπακοην πιστεως εις παντα τα εθνη γνωρισθεντος');
INSERT INTO `bible_verses_tr` VALUES ('5219', '45', '16', '27', 'μονω σοφω θεω δια ιησου χριστου  {VAR1: ω } η δοξα εις τους αιωνας αμην [προς ρωμαιους εγραφη απο κορινθου δια φοιβης της διακονου της εν κεγχρεαις εκκλησιας]');
INSERT INTO `bible_verses_tr` VALUES ('5220', '46', '1', '1', 'παυλος κλητος αποστολος ιησου χριστου δια θεληματος θεου και σωσθενης ο αδελφος');
INSERT INTO `bible_verses_tr` VALUES ('5221', '46', '1', '2', 'τη εκκλησια του θεου τη ουση εν κορινθω ηγιασμενοις εν χριστω ιησου κλητοις αγιοις συν πασιν τοις επικαλουμενοις το ονομα του κυριου ημων ιησου χριστου εν παντι τοπω αυτων τε και ημων');
INSERT INTO `bible_verses_tr` VALUES ('5222', '46', '1', '3', 'χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5223', '46', '1', '4', 'ευχαριστω τω θεω μου παντοτε περι υμων επι τη χαριτι του θεου τη δοθειση υμιν εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('5224', '46', '1', '5', 'οτι εν παντι επλουτισθητε εν αυτω εν παντι λογω και παση γνωσει');
INSERT INTO `bible_verses_tr` VALUES ('5225', '46', '1', '6', 'καθως το μαρτυριον του χριστου εβεβαιωθη εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5226', '46', '1', '7', 'ωστε υμας μη υστερεισθαι εν μηδενι χαρισματι απεκδεχομενους την αποκαλυψιν του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5227', '46', '1', '8', 'ος και βεβαιωσει υμας εως τελους ανεγκλητους εν τη ημερα του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5228', '46', '1', '9', 'πιστος ο θεος δι ου εκληθητε εις κοινωνιαν του υιου αυτου ιησου χριστου του κυριου ημων');
INSERT INTO `bible_verses_tr` VALUES ('5229', '46', '1', '10', 'παρακαλω δε υμας αδελφοι δια του ονοματος του κυριου ημων ιησου χριστου ινα το αυτο λεγητε παντες και μη η εν υμιν σχισματα ητε δε κατηρτισμενοι εν τω αυτω νοι και εν τη αυτη γνωμη');
INSERT INTO `bible_verses_tr` VALUES ('5230', '46', '1', '11', 'εδηλωθη γαρ μοι περι υμων αδελφοι μου υπο των χλοης οτι εριδες εν υμιν εισιν');
INSERT INTO `bible_verses_tr` VALUES ('5231', '46', '1', '12', 'λεγω δε τουτο οτι εκαστος υμων λεγει εγω μεν ειμι παυλου εγω δε απολλω εγω δε κηφα εγω δε χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5232', '46', '1', '13', 'μεμερισται ο χριστος μη παυλος εσταυρωθη υπερ υμων η εις το ονομα παυλου εβαπτισθητε');
INSERT INTO `bible_verses_tr` VALUES ('5233', '46', '1', '14', 'ευχαριστω τω θεω οτι ουδενα υμων εβαπτισα ει μη κρισπον και γαιον');
INSERT INTO `bible_verses_tr` VALUES ('5234', '46', '1', '15', 'ινα μη τις ειπη οτι εις το εμον ονομα εβαπτισα');
INSERT INTO `bible_verses_tr` VALUES ('5235', '46', '1', '16', 'εβαπτισα δε και τον στεφανα οικον λοιπον ουκ οιδα ει τινα αλλον εβαπτισα');
INSERT INTO `bible_verses_tr` VALUES ('5236', '46', '1', '17', 'ου γαρ απεστειλεν με χριστος βαπτιζειν αλλ ευαγγελιζεσθαι ουκ εν σοφια λογου ινα μη κενωθη ο σταυρος του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5237', '46', '1', '18', 'ο λογος γαρ ο του σταυρου τοις μεν απολλυμενοις μωρια εστιν τοις δε σωζομενοις ημιν δυναμις θεου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5238', '46', '1', '19', 'γεγραπται γαρ απολω την σοφιαν των σοφων και την συνεσιν των συνετων αθετησω');
INSERT INTO `bible_verses_tr` VALUES ('5239', '46', '1', '20', 'που σοφος που γραμματευς που συζητητης του αιωνος τουτου ουχι εμωρανεν ο θεος την σοφιαν του κοσμου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('5240', '46', '1', '21', 'επειδη γαρ εν τη σοφια του θεου ουκ εγνω ο κοσμος δια της σοφιας τον θεον ευδοκησεν ο θεος δια της μωριας του κηρυγματος σωσαι τους πιστευοντας');
INSERT INTO `bible_verses_tr` VALUES ('5241', '46', '1', '22', 'επειδη και ιουδαιοι σημειον αιτουσιν και ελληνες σοφιαν ζητουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5242', '46', '1', '23', 'ημεις δε κηρυσσομεν χριστον εσταυρωμενον ιουδαιοις μεν σκανδαλον ελλησιν δε μωριαν');
INSERT INTO `bible_verses_tr` VALUES ('5243', '46', '1', '24', 'αυτοις δε τοις κλητοις ιουδαιοις τε και ελλησιν χριστον θεου δυναμιν και θεου σοφιαν');
INSERT INTO `bible_verses_tr` VALUES ('5244', '46', '1', '25', 'οτι το μωρον του θεου σοφωτερον των ανθρωπων εστιν και το ασθενες του θεου ισχυροτερον των ανθρωπων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5245', '46', '1', '26', 'βλεπετε γαρ την κλησιν υμων αδελφοι οτι ου πολλοι σοφοι κατα σαρκα ου πολλοι δυνατοι ου πολλοι ευγενεις');
INSERT INTO `bible_verses_tr` VALUES ('5246', '46', '1', '27', 'αλλα τα μωρα του κοσμου εξελεξατο ο θεος ινα τους σοφους καταισχυνη και τα ασθενη του κοσμου εξελεξατο ο θεος ινα καταισχυνη τα ισχυρα');
INSERT INTO `bible_verses_tr` VALUES ('5247', '46', '1', '28', 'και τα αγενη του κοσμου και τα εξουθενημενα εξελεξατο ο θεος και τα μη οντα ινα τα οντα καταργηση');
INSERT INTO `bible_verses_tr` VALUES ('5248', '46', '1', '29', 'οπως μη καυχησηται πασα σαρξ ενωπιον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5249', '46', '1', '30', 'εξ αυτου δε υμεις εστε εν χριστω ιησου ος εγενηθη ημιν σοφια απο θεου δικαιοσυνη τε και αγιασμος και απολυτρωσις');
INSERT INTO `bible_verses_tr` VALUES ('5250', '46', '1', '31', 'ινα καθως γεγραπται ο καυχωμενος εν κυριω καυχασθω');
INSERT INTO `bible_verses_tr` VALUES ('5251', '46', '2', '1', 'καγω ελθων προς υμας αδελφοι ηλθον ου καθ υπεροχην λογου η σοφιας καταγγελλων υμιν το μαρτυριον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5252', '46', '2', '2', 'ου γαρ εκρινα του ειδεναι τι εν υμιν ει μη ιησουν χριστον και τουτον εσταυρωμενον');
INSERT INTO `bible_verses_tr` VALUES ('5253', '46', '2', '3', 'και εγω εν ασθενεια και εν φοβω και εν τρομω πολλω εγενομην προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('5254', '46', '2', '4', 'και ο λογος μου και το κηρυγμα μου ουκ εν πειθοις ανθρωπινης σοφιας λογοις αλλ εν αποδειξει πνευματος και δυναμεως');
INSERT INTO `bible_verses_tr` VALUES ('5255', '46', '2', '5', 'ινα η πιστις υμων μη η εν σοφια ανθρωπων αλλ εν δυναμει θεου');
INSERT INTO `bible_verses_tr` VALUES ('5256', '46', '2', '6', 'σοφιαν δε λαλουμεν εν τοις τελειοις σοφιαν δε ου του αιωνος τουτου ουδε των αρχοντων του αιωνος τουτου των καταργουμενων');
INSERT INTO `bible_verses_tr` VALUES ('5257', '46', '2', '7', 'αλλα λαλουμεν σοφιαν θεου εν μυστηριω την αποκεκρυμμενην ην προωρισεν ο θεος προ των αιωνων εις δοξαν ημων');
INSERT INTO `bible_verses_tr` VALUES ('5258', '46', '2', '8', 'ην ουδεις των αρχοντων του αιωνος τουτου εγνωκεν ει γαρ εγνωσαν ουκ αν τον κυριον της δοξης εσταυρωσαν');
INSERT INTO `bible_verses_tr` VALUES ('5259', '46', '2', '9', 'αλλα καθως γεγραπται α οφθαλμος ουκ ειδεν και ους ουκ ηκουσεν και επι καρδιαν ανθρωπου ουκ ανεβη α ητοιμασεν ο θεος τοις αγαπωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('5260', '46', '2', '10', 'ημιν δε ο θεος απεκαλυψεν δια του πνευματος αυτου το γαρ πνευμα παντα ερευνα και τα βαθη του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5261', '46', '2', '11', 'τις γαρ οιδεν ανθρωπων τα του ανθρωπου ει μη το πνευμα του ανθρωπου το εν αυτω ουτως και τα του θεου ουδεις οιδεν ει μη το πνευμα του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5262', '46', '2', '12', 'ημεις δε ου το πνευμα του κοσμου ελαβομεν αλλα το πνευμα το εκ του θεου ινα ειδωμεν τα υπο του θεου χαρισθεντα ημιν');
INSERT INTO `bible_verses_tr` VALUES ('5263', '46', '2', '13', 'α και λαλουμεν ουκ εν διδακτοις ανθρωπινης σοφιας λογοις αλλ εν διδακτοις πνευματος αγιου πνευματικοις πνευματικα συγκρινοντες');
INSERT INTO `bible_verses_tr` VALUES ('5264', '46', '2', '14', 'ψυχικος δε ανθρωπος ου δεχεται τα του πνευματος του θεου μωρια γαρ αυτω εστιν και ου δυναται γνωναι οτι πνευματικως ανακρινεται');
INSERT INTO `bible_verses_tr` VALUES ('5265', '46', '2', '15', 'ο δε πνευματικος ανακρινει μεν παντα αυτος δε υπ ουδενος ανακρινεται');
INSERT INTO `bible_verses_tr` VALUES ('5266', '46', '2', '16', 'τις γαρ εγνω νουν κυριου ος συμβιβασει αυτον ημεις δε νουν χριστου εχομεν');
INSERT INTO `bible_verses_tr` VALUES ('5267', '46', '3', '1', 'και εγω αδελφοι ουκ ηδυνηθην λαλησαι υμιν ως πνευματικοις αλλ ως σαρκικοις ως νηπιοις εν χριστω');
INSERT INTO `bible_verses_tr` VALUES ('5268', '46', '3', '2', 'γαλα υμας εποτισα και ου βρωμα ουπω γαρ ηδυνασθε αλλ ουτε ετι νυν δυνασθε');
INSERT INTO `bible_verses_tr` VALUES ('5269', '46', '3', '3', 'ετι γαρ σαρκικοι εστε οπου γαρ εν υμιν ζηλος και ερις και διχοστασιαι ουχι σαρκικοι εστε και κατα ανθρωπον περιπατειτε');
INSERT INTO `bible_verses_tr` VALUES ('5270', '46', '3', '4', 'οταν γαρ λεγη τις εγω μεν ειμι παυλου ετερος δε εγω απολλω ουχι σαρκικοι εστε');
INSERT INTO `bible_verses_tr` VALUES ('5271', '46', '3', '5', 'τις ουν εστιν παυλος τις δε απολλως αλλ η διακονοι δι ων επιστευσατε και εκαστω ως ο κυριος εδωκεν');
INSERT INTO `bible_verses_tr` VALUES ('5272', '46', '3', '6', 'εγω εφυτευσα απολλως εποτισεν αλλ ο θεος ηυξανεν');
INSERT INTO `bible_verses_tr` VALUES ('5273', '46', '3', '7', 'ωστε ουτε ο φυτευων εστιν τι ουτε ο ποτιζων αλλ ο αυξανων θεος');
INSERT INTO `bible_verses_tr` VALUES ('5274', '46', '3', '8', 'ο φυτευων δε και ο ποτιζων εν εισιν εκαστος δε τον ιδιον μισθον ληψεται κατα τον ιδιον κοπον');
INSERT INTO `bible_verses_tr` VALUES ('5275', '46', '3', '9', 'θεου γαρ εσμεν συνεργοι θεου γεωργιον θεου οικοδομη εστε');
INSERT INTO `bible_verses_tr` VALUES ('5276', '46', '3', '10', 'κατα την χαριν του θεου την δοθεισαν μοι ως σοφος αρχιτεκτων θεμελιον τεθεικα αλλος δε εποικοδομει εκαστος δε βλεπετω πως εποικοδομει');
INSERT INTO `bible_verses_tr` VALUES ('5277', '46', '3', '11', 'θεμελιον γαρ αλλον ουδεις δυναται θειναι παρα τον κειμενον ος εστιν ιησους ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('5278', '46', '3', '12', 'ει δε τις εποικοδομει επι τον θεμελιον τουτον χρυσον αργυρον λιθους τιμιους ξυλα χορτον καλαμην');
INSERT INTO `bible_verses_tr` VALUES ('5279', '46', '3', '13', 'εκαστου το εργον φανερον γενησεται η γαρ ημερα δηλωσει οτι εν πυρι αποκαλυπτεται και εκαστου το εργον οποιον εστιν το πυρ δοκιμασει');
INSERT INTO `bible_verses_tr` VALUES ('5280', '46', '3', '14', 'ει τινος το εργον μενει ο επωκοδομησεν μισθον ληψεται');
INSERT INTO `bible_verses_tr` VALUES ('5281', '46', '3', '15', 'ει τινος το εργον κατακαησεται ζημιωθησεται αυτος δε σωθησεται ουτως δε ως δια πυρος');
INSERT INTO `bible_verses_tr` VALUES ('5282', '46', '3', '16', 'ουκ οιδατε οτι ναος θεου εστε και το πνευμα του θεου οικει εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5283', '46', '3', '17', 'ει τις τον ναον του θεου φθειρει φθερει τουτον ο θεος ο γαρ ναος του θεου αγιος εστιν οιτινες εστε υμεις');
INSERT INTO `bible_verses_tr` VALUES ('5284', '46', '3', '18', 'μηδεις εαυτον εξαπατατω ει τις δοκει σοφος ειναι εν υμιν εν τω αιωνι τουτω μωρος γενεσθω ινα γενηται σοφος');
INSERT INTO `bible_verses_tr` VALUES ('5285', '46', '3', '19', 'η γαρ σοφια του κοσμου τουτου μωρια παρα τω θεω εστιν γεγραπται γαρ ο δρασσομενος τους σοφους εν τη πανουργια αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5286', '46', '3', '20', 'και παλιν κυριος γινωσκει τους διαλογισμους των σοφων οτι εισιν ματαιοι');
INSERT INTO `bible_verses_tr` VALUES ('5287', '46', '3', '21', 'ωστε μηδεις καυχασθω εν ανθρωποις παντα γαρ υμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5288', '46', '3', '22', 'ειτε παυλος ειτε απολλως ειτε κηφας ειτε κοσμος ειτε ζωη ειτε θανατος ειτε ενεστωτα ειτε μελλοντα παντα υμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5289', '46', '3', '23', 'υμεις δε χριστου χριστος δε θεου');
INSERT INTO `bible_verses_tr` VALUES ('5290', '46', '4', '1', 'ουτως ημας λογιζεσθω ανθρωπος ως υπηρετας χριστου και οικονομους μυστηριων θεου');
INSERT INTO `bible_verses_tr` VALUES ('5291', '46', '4', '2', 'ο δε λοιπον ζητειται εν τοις οικονομοις ινα πιστος τις ευρεθη');
INSERT INTO `bible_verses_tr` VALUES ('5292', '46', '4', '3', 'εμοι δε εις ελαχιστον εστιν ινα υφ υμων ανακριθω η υπο ανθρωπινης ημερας αλλ ουδε εμαυτον ανακρινω');
INSERT INTO `bible_verses_tr` VALUES ('5293', '46', '4', '4', 'ουδεν γαρ εμαυτω συνοιδα αλλ ουκ εν τουτω δεδικαιωμαι ο δε ανακρινων με κυριος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5294', '46', '4', '5', 'ωστε μη προ καιρου τι κρινετε εως αν ελθη ο κυριος ος και φωτισει τα κρυπτα του σκοτους και φανερωσει τας βουλας των καρδιων και τοτε ο επαινος γενησεται εκαστω απο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5295', '46', '4', '6', 'ταυτα δε αδελφοι μετεσχηματισα εις εμαυτον και απολλω δι υμας ινα εν ημιν μαθητε το μη υπερ ο γεγραπται φρονειν ινα μη εις υπερ του ενος φυσιουσθε κατα του ετερου');
INSERT INTO `bible_verses_tr` VALUES ('5296', '46', '4', '7', 'τις γαρ σε διακρινει τι δε εχεις ο ουκ ελαβες ει δε και ελαβες τι καυχασαι ως μη λαβων');
INSERT INTO `bible_verses_tr` VALUES ('5297', '46', '4', '8', 'ηδη κεκορεσμενοι εστε ηδη επλουτησατε χωρις ημων εβασιλευσατε και οφελον γε εβασιλευσατε ινα και ημεις υμιν συμβασιλευσωμεν');
INSERT INTO `bible_verses_tr` VALUES ('5298', '46', '4', '9', 'δοκω γαρ οτι ο θεος ημας τους αποστολους εσχατους απεδειξεν ως επιθανατιους οτι θεατρον εγενηθημεν τω κοσμω και αγγελοις και ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('5299', '46', '4', '10', 'ημεις μωροι δια χριστον υμεις δε φρονιμοι εν χριστω ημεις ασθενεις υμεις δε ισχυροι υμεις ενδοξοι ημεις δε ατιμοι');
INSERT INTO `bible_verses_tr` VALUES ('5300', '46', '4', '11', 'αχρι της αρτι ωρας και πεινωμεν και διψωμεν και γυμνητευομεν και κολαφιζομεθα και αστατουμεν');
INSERT INTO `bible_verses_tr` VALUES ('5301', '46', '4', '12', 'και κοπιωμεν εργαζομενοι ταις ιδιαις χερσιν λοιδορουμενοι ευλογουμεν διωκομενοι ανεχομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5302', '46', '4', '13', 'βλασφημουμενοι παρακαλουμεν ως περικαθαρματα του κοσμου εγενηθημεν παντων περιψημα εως αρτι');
INSERT INTO `bible_verses_tr` VALUES ('5303', '46', '4', '14', 'ουκ εντρεπων υμας γραφω ταυτα αλλ ως τεκνα μου αγαπητα νουθετω');
INSERT INTO `bible_verses_tr` VALUES ('5304', '46', '4', '15', 'εαν γαρ μυριους παιδαγωγους εχητε εν χριστω αλλ ου πολλους πατερας εν γαρ χριστω ιησου δια του ευαγγελιου εγω υμας εγεννησα');
INSERT INTO `bible_verses_tr` VALUES ('5305', '46', '4', '16', 'παρακαλω ουν υμας μιμηται μου γινεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5306', '46', '4', '17', 'δια τουτο επεμψα υμιν τιμοθεον ος εστιν τεκνον μου αγαπητον και πιστον εν κυριω ος υμας αναμνησει τας οδους μου τας εν χριστω καθως πανταχου εν παση εκκλησια διδασκω');
INSERT INTO `bible_verses_tr` VALUES ('5307', '46', '4', '18', 'ως μη ερχομενου δε μου προς υμας εφυσιωθησαν τινες');
INSERT INTO `bible_verses_tr` VALUES ('5308', '46', '4', '19', 'ελευσομαι δε ταχεως προς υμας εαν ο κυριος θεληση και γνωσομαι ου τον λογον των πεφυσιωμενων αλλα την δυναμιν');
INSERT INTO `bible_verses_tr` VALUES ('5309', '46', '4', '20', 'ου γαρ εν λογω η βασιλεια του θεου αλλ εν δυναμει');
INSERT INTO `bible_verses_tr` VALUES ('5310', '46', '4', '21', 'τι θελετε εν ραβδω ελθω προς υμας η εν αγαπη πνευματι τε πραοτητος');
INSERT INTO `bible_verses_tr` VALUES ('5311', '46', '5', '1', 'ολως ακουεται εν υμιν πορνεια και τοιαυτη πορνεια ητις ουδε εν τοις εθνεσιν ονομαζεται ωστε γυναικα τινα του πατρος εχειν');
INSERT INTO `bible_verses_tr` VALUES ('5312', '46', '5', '2', 'και υμεις πεφυσιωμενοι εστε και ουχι μαλλον επενθησατε ινα εξαρθη εκ μεσου υμων ο το εργον τουτο ποιησας');
INSERT INTO `bible_verses_tr` VALUES ('5313', '46', '5', '3', 'εγω μεν γαρ ως απων τω σωματι παρων δε τω πνευματι ηδη κεκρικα ως παρων τον ουτως τουτο κατεργασαμενον');
INSERT INTO `bible_verses_tr` VALUES ('5314', '46', '5', '4', 'εν τω ονοματι του κυριου ημων ιησου χριστου συναχθεντων υμων και του εμου πνευματος συν τη δυναμει του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5315', '46', '5', '5', 'παραδουναι τον τοιουτον τω σατανα εις ολεθρον της σαρκος ινα το πνευμα σωθη εν τη ημερα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('5316', '46', '5', '6', 'ου καλον το καυχημα υμων ουκ οιδατε οτι μικρα ζυμη ολον το φυραμα ζυμοι');
INSERT INTO `bible_verses_tr` VALUES ('5317', '46', '5', '7', 'εκκαθαρατε ουν την παλαιαν ζυμην ινα ητε νεον φυραμα καθως εστε αζυμοι και γαρ το πασχα ημων υπερ ημων  {VAR1: ετυθη } {VAR2: εθυθη } χριστος');
INSERT INTO `bible_verses_tr` VALUES ('5318', '46', '5', '8', 'ωστε εορταζωμεν μη εν ζυμη παλαια μηδε εν ζυμη κακιας και πονηριας αλλ εν αζυμοις ειλικρινειας και αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('5319', '46', '5', '9', 'εγραψα υμιν εν τη επιστολη μη συναναμιγνυσθαι πορνοις');
INSERT INTO `bible_verses_tr` VALUES ('5320', '46', '5', '10', 'και ου παντως τοις πορνοις του κοσμου τουτου η τοις πλεονεκταις η αρπαξιν η ειδωλολατραις επει οφειλετε αρα εκ του κοσμου εξελθειν');
INSERT INTO `bible_verses_tr` VALUES ('5321', '46', '5', '11', 'νυνι δε εγραψα υμιν μη συναναμιγνυσθαι εαν τις αδελφος ονομαζομενος η πορνος η πλεονεκτης η ειδωλολατρης η λοιδορος η μεθυσος η αρπαξ τω τοιουτω μηδε συνεσθιειν');
INSERT INTO `bible_verses_tr` VALUES ('5322', '46', '5', '12', 'τι γαρ μοι και τους εξω κρινειν ουχι τους εσω υμεις κρινετε');
INSERT INTO `bible_verses_tr` VALUES ('5323', '46', '5', '13', 'τους δε εξω ο θεος κρινει και εξαρειτε τον πονηρον εξ υμων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5324', '46', '6', '1', 'τολμα τις υμων πραγμα εχων προς τον ετερον κρινεσθαι επι των αδικων και ουχι επι των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('5325', '46', '6', '2', 'ουκ οιδατε οτι οι αγιοι τον κοσμον κρινουσιν και ει εν υμιν κρινεται ο κοσμος αναξιοι εστε κριτηριων ελαχιστων');
INSERT INTO `bible_verses_tr` VALUES ('5326', '46', '6', '3', 'ουκ οιδατε οτι αγγελους κρινουμεν μητι γε βιωτικα');
INSERT INTO `bible_verses_tr` VALUES ('5327', '46', '6', '4', 'βιωτικα μεν ουν κριτηρια εαν εχητε τους εξουθενημενους εν τη εκκλησια τουτους καθιζετε');
INSERT INTO `bible_verses_tr` VALUES ('5328', '46', '6', '5', 'προς εντροπην υμιν λεγω ουτως ουκ εστιν εν υμιν σοφος ουδε εις ος δυνησεται διακριναι ανα μεσον του αδελφου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5329', '46', '6', '6', 'αλλα αδελφος μετα αδελφου κρινεται και τουτο επι απιστων');
INSERT INTO `bible_verses_tr` VALUES ('5330', '46', '6', '7', 'ηδη μεν ουν ολως ηττημα εν υμιν εστιν οτι κριματα εχετε μεθ εαυτων δια τι ουχι μαλλον αδικεισθε δια τι ουχι μαλλον αποστερεισθε');
INSERT INTO `bible_verses_tr` VALUES ('5331', '46', '6', '8', 'αλλα υμεις αδικειτε και αποστερειτε και ταυτα αδελφους');
INSERT INTO `bible_verses_tr` VALUES ('5332', '46', '6', '9', 'η ουκ οιδατε οτι αδικοι βασιλειαν θεου ου κληρονομησουσιν μη πλανασθε ουτε πορνοι ουτε ειδωλολατραι ουτε μοιχοι ουτε μαλακοι ουτε αρσενοκοιται');
INSERT INTO `bible_verses_tr` VALUES ('5333', '46', '6', '10', 'ουτε κλεπται ουτε πλεονεκται ουτε μεθυσοι ου λοιδοροι ουχ αρπαγες βασιλειαν θεου ου κληρονομησουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5334', '46', '6', '11', 'και ταυτα τινες ητε αλλα απελουσασθε αλλα ηγιασθητε αλλ εδικαιωθητε εν τω ονοματι του κυριου ιησου και εν τω πνευματι του θεου ημων');
INSERT INTO `bible_verses_tr` VALUES ('5335', '46', '6', '12', 'παντα μοι εξεστιν αλλ ου παντα συμφερει παντα μοι εξεστιν αλλ ουκ εγω εξουσιασθησομαι υπο τινος');
INSERT INTO `bible_verses_tr` VALUES ('5336', '46', '6', '13', 'τα βρωματα τη κοιλια και η κοιλια τοις βρωμασιν ο δε θεος και ταυτην και ταυτα καταργησει το δε σωμα ου τη πορνεια αλλα τω κυριω και ο κυριος τω σωματι');
INSERT INTO `bible_verses_tr` VALUES ('5337', '46', '6', '14', 'ο δε θεος και τον κυριον ηγειρεν και ημας εξεγερει δια της δυναμεως αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5338', '46', '6', '15', 'ουκ οιδατε οτι τα σωματα υμων μελη χριστου εστιν αρας ουν τα μελη του χριστου ποιησω πορνης μελη μη γενοιτο');
INSERT INTO `bible_verses_tr` VALUES ('5339', '46', '6', '16', 'η ουκ οιδατε οτι ο κολλωμενος τη πορνη εν σωμα εστιν εσονται γαρ φησιν οι δυο εις σαρκα μιαν');
INSERT INTO `bible_verses_tr` VALUES ('5340', '46', '6', '17', 'ο δε κολλωμενος τω κυριω εν πνευμα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5341', '46', '6', '18', 'φευγετε την πορνειαν παν αμαρτημα ο εαν ποιηση ανθρωπος εκτος του σωματος εστιν ο δε πορνευων εις το ιδιον σωμα αμαρτανει');
INSERT INTO `bible_verses_tr` VALUES ('5342', '46', '6', '19', 'η ουκ οιδατε οτι το σωμα υμων ναος του εν υμιν αγιου πνευματος εστιν ου εχετε απο θεου και ουκ εστε εαυτων');
INSERT INTO `bible_verses_tr` VALUES ('5343', '46', '6', '20', 'ηγορασθητε γαρ τιμης δοξασατε δη τον θεον εν τω σωματι υμων και εν τω πνευματι υμων ατινα εστιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5344', '46', '7', '1', 'περι δε ων εγραψατε μοι καλον ανθρωπω γυναικος μη απτεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5345', '46', '7', '2', 'δια δε τας πορνειας εκαστος την εαυτου γυναικα εχετω και εκαστη τον ιδιον ανδρα εχετω');
INSERT INTO `bible_verses_tr` VALUES ('5346', '46', '7', '3', 'τη γυναικι ο ανηρ την οφειλομενην ευνοιαν αποδιδοτω ομοιως δε και η γυνη τω ανδρι');
INSERT INTO `bible_verses_tr` VALUES ('5347', '46', '7', '4', 'η γυνη του ιδιου σωματος ουκ εξουσιαζει αλλ ο ανηρ ομοιως δε και ο ανηρ του ιδιου σωματος ουκ εξουσιαζει αλλ η γυνη');
INSERT INTO `bible_verses_tr` VALUES ('5348', '46', '7', '5', 'μη αποστερειτε αλληλους ει μη τι αν εκ συμφωνου προς καιρον ινα σχολαζητε τη νηστεια και τη προσευχη και παλιν επι το αυτο συνερχησθε ινα μη πειραζη υμας ο σατανας δια την ακρασιαν υμων');
INSERT INTO `bible_verses_tr` VALUES ('5349', '46', '7', '6', 'τουτο δε λεγω κατα συγγνωμην ου κατ επιταγην');
INSERT INTO `bible_verses_tr` VALUES ('5350', '46', '7', '7', 'θελω γαρ παντας ανθρωπους ειναι ως και εμαυτον αλλ εκαστος ιδιον χαρισμα εχει εκ θεου ος μεν ουτως ος δε ουτως');
INSERT INTO `bible_verses_tr` VALUES ('5351', '46', '7', '8', 'λεγω δε τοις αγαμοις και ταις χηραις καλον αυτοις εστιν εαν μεινωσιν ως καγω');
INSERT INTO `bible_verses_tr` VALUES ('5352', '46', '7', '9', 'ει δε ουκ εγκρατευονται γαμησατωσαν κρεισσον γαρ εστιν γαμησαι η πυρουσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5353', '46', '7', '10', 'τοις δε γεγαμηκοσιν παραγγελλω ουκ εγω αλλ ο κυριος γυναικα απο ανδρος μη χωρισθηναι');
INSERT INTO `bible_verses_tr` VALUES ('5354', '46', '7', '11', 'εαν δε και χωρισθη μενετω αγαμος η τω ανδρι καταλλαγητω και ανδρα γυναικα μη αφιεναι');
INSERT INTO `bible_verses_tr` VALUES ('5355', '46', '7', '12', 'τοις δε λοιποις εγω λεγω ουχ ο κυριος ει τις αδελφος γυναικα εχει απιστον και αυτη συνευδοκει οικειν μετ αυτου μη αφιετω αυτην');
INSERT INTO `bible_verses_tr` VALUES ('5356', '46', '7', '13', 'και γυνη ητις εχει ανδρα απιστον και αυτος συνευδοκει οικειν μετ αυτης μη αφιετω αυτον');
INSERT INTO `bible_verses_tr` VALUES ('5357', '46', '7', '14', 'ηγιασται γαρ ο ανηρ ο απιστος εν τη γυναικι και ηγιασται η γυνη η απιστος εν τω ανδρι επει αρα τα τεκνα υμων ακαθαρτα εστιν νυν δε αγια εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5358', '46', '7', '15', 'ει δε ο απιστος χωριζεται χωριζεσθω ου δεδουλωται ο αδελφος η η αδελφη εν τοις τοιουτοις εν δε ειρηνη κεκληκεν ημας ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('5359', '46', '7', '16', 'τι γαρ οιδας γυναι ει τον ανδρα σωσεις η τι οιδας ανερ ει την γυναικα σωσεις');
INSERT INTO `bible_verses_tr` VALUES ('5360', '46', '7', '17', 'ει μη εκαστω ως εμερισεν ο θεος εκαστον ως κεκληκεν ο κυριος ουτως περιπατειτω και ουτως εν ταις εκκλησιαις πασαις διατασσομαι');
INSERT INTO `bible_verses_tr` VALUES ('5361', '46', '7', '18', 'περιτετμημενος τις εκληθη μη επισπασθω εν ακροβυστια τις εκληθη μη περιτεμνεσθω');
INSERT INTO `bible_verses_tr` VALUES ('5362', '46', '7', '19', 'η περιτομη ουδεν εστιν και η ακροβυστια ουδεν εστιν αλλα τηρησις εντολων θεου');
INSERT INTO `bible_verses_tr` VALUES ('5363', '46', '7', '20', 'εκαστος εν τη κλησει η εκληθη εν ταυτη μενετω');
INSERT INTO `bible_verses_tr` VALUES ('5364', '46', '7', '21', 'δουλος εκληθης μη σοι μελετω αλλ ει και δυνασαι ελευθερος γενεσθαι μαλλον χρησαι');
INSERT INTO `bible_verses_tr` VALUES ('5365', '46', '7', '22', 'ο γαρ εν κυριω κληθεις δουλος απελευθερος κυριου εστιν ομοιως και ο ελευθερος κληθεις δουλος εστιν χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5366', '46', '7', '23', 'τιμης ηγορασθητε μη γινεσθε δουλοι ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('5367', '46', '7', '24', 'εκαστος εν ω εκληθη αδελφοι εν τουτω μενετω παρα τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5368', '46', '7', '25', 'περι δε των παρθενων επιταγην κυριου ουκ εχω γνωμην δε διδωμι ως ηλεημενος υπο κυριου πιστος ειναι');
INSERT INTO `bible_verses_tr` VALUES ('5369', '46', '7', '26', 'νομιζω ουν τουτο καλον υπαρχειν δια την ενεστωσαν αναγκην οτι καλον ανθρωπω το ουτως ειναι');
INSERT INTO `bible_verses_tr` VALUES ('5370', '46', '7', '27', 'δεδεσαι γυναικι μη ζητει λυσιν λελυσαι απο γυναικος μη ζητει γυναικα');
INSERT INTO `bible_verses_tr` VALUES ('5371', '46', '7', '28', 'εαν δε και γημης ουχ ημαρτες και εαν γημη η παρθενος ουχ ημαρτεν θλιψιν δε τη σαρκι εξουσιν οι τοιουτοι εγω δε υμων φειδομαι');
INSERT INTO `bible_verses_tr` VALUES ('5372', '46', '7', '29', 'τουτο δε φημι αδελφοι  {VAR2: οτι } ο καιρος συνεσταλμενος το λοιπον εστιν ινα και οι εχοντες γυναικας ως μη εχοντες ωσιν');
INSERT INTO `bible_verses_tr` VALUES ('5373', '46', '7', '30', 'και οι κλαιοντες ως μη κλαιοντες και οι χαιροντες ως μη χαιροντες και οι αγοραζοντες ως μη κατεχοντες');
INSERT INTO `bible_verses_tr` VALUES ('5374', '46', '7', '31', 'και οι χρωμενοι τω κοσμω τουτω ως μη καταχρωμενοι παραγει γαρ το σχημα του κοσμου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('5375', '46', '7', '32', 'θελω δε υμας αμεριμνους ειναι ο αγαμος μεριμνα τα του κυριου πως αρεσει τω κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5376', '46', '7', '33', 'ο δε γαμησας μεριμνα τα του κοσμου πως αρεσει τη γυναικι');
INSERT INTO `bible_verses_tr` VALUES ('5377', '46', '7', '34', 'μεμερισται η γυνη και η παρθενος η αγαμος μεριμνα τα του κυριου ινα η αγια και σωματι και πνευματι η δε γαμησασα μεριμνα τα του κοσμου πως αρεσει τω ανδρι');
INSERT INTO `bible_verses_tr` VALUES ('5378', '46', '7', '35', 'τουτο δε προς το υμων αυτων συμφερον λεγω ουχ ινα βροχον υμιν επιβαλω αλλα προς το ευσχημον και ευπροσεδρον τω κυριω απερισπαστως');
INSERT INTO `bible_verses_tr` VALUES ('5379', '46', '7', '36', 'ει δε τις ασχημονειν επι την παρθενον αυτου νομιζει εαν η υπερακμος και ουτως οφειλει γινεσθαι ο θελει ποιειτω ουχ αμαρτανει γαμειτωσαν');
INSERT INTO `bible_verses_tr` VALUES ('5380', '46', '7', '37', 'ος δε εστηκεν εδραιος εν τη καρδια μη εχων αναγκην εξουσιαν δε εχει περι του ιδιου θεληματος και τουτο κεκρικεν εν τη καρδια αυτου του τηρειν την εαυτου παρθενον καλως ποιει');
INSERT INTO `bible_verses_tr` VALUES ('5381', '46', '7', '38', 'ωστε και ο εκγαμιζων καλως ποιει ο δε μη εκγαμιζων κρεισσον ποιει');
INSERT INTO `bible_verses_tr` VALUES ('5382', '46', '7', '39', 'γυνη δεδεται νομω εφ οσον χρονον ζη ο ανηρ αυτης εαν δε κοιμηθη ο ανηρ αυτης ελευθερα εστιν ω θελει γαμηθηναι μονον εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5383', '46', '7', '40', 'μακαριωτερα δε εστιν εαν ουτως μεινη κατα την εμην γνωμην δοκω δε καγω πνευμα θεου εχειν');
INSERT INTO `bible_verses_tr` VALUES ('5384', '46', '8', '1', 'περι δε των ειδωλοθυτων οιδαμεν οτι παντες γνωσιν εχομεν η γνωσις φυσιοι η δε αγαπη οικοδομει');
INSERT INTO `bible_verses_tr` VALUES ('5385', '46', '8', '2', 'ει δε τις δοκει ειδεναι τι ουδεπω ουδεν εγνωκεν καθως δει γνωναι');
INSERT INTO `bible_verses_tr` VALUES ('5386', '46', '8', '3', 'ει δε τις αγαπα τον θεον ουτος εγνωσται υπ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5387', '46', '8', '4', 'περι της βρωσεως ουν των ειδωλοθυτων οιδαμεν οτι ουδεν ειδωλον εν κοσμω και οτι ουδεις θεος ετερος ει μη εις');
INSERT INTO `bible_verses_tr` VALUES ('5388', '46', '8', '5', 'και γαρ ειπερ εισιν λεγομενοι θεοι ειτε εν ουρανω ειτε επι της γης ωσπερ εισιν θεοι πολλοι και κυριοι πολλοι');
INSERT INTO `bible_verses_tr` VALUES ('5389', '46', '8', '6', 'αλλ ημιν εις θεος ο πατηρ εξ ου τα παντα και ημεις εις αυτον και εις κυριος ιησους χριστος δι ου τα παντα και ημεις δι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5390', '46', '8', '7', 'αλλ ουκ εν πασιν η γνωσις τινες δε τη συνειδησει του ειδωλου εως αρτι ως ειδωλοθυτον εσθιουσιν και η συνειδησις αυτων ασθενης ουσα μολυνεται');
INSERT INTO `bible_verses_tr` VALUES ('5391', '46', '8', '8', 'βρωμα δε ημας ου παριστησιν τω θεω ουτε γαρ εαν φαγωμεν περισσευομεν ουτε εαν μη φαγωμεν υστερουμεθα');
INSERT INTO `bible_verses_tr` VALUES ('5392', '46', '8', '9', 'βλεπετε δε μηπως η εξουσια υμων αυτη προσκομμα γενηται τοις ασθενουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5393', '46', '8', '10', 'εαν γαρ τις ιδη σε τον εχοντα γνωσιν εν ειδωλειω κατακειμενον ουχι η συνειδησις αυτου ασθενους οντος οικοδομηθησεται εις το τα ειδωλοθυτα εσθιειν');
INSERT INTO `bible_verses_tr` VALUES ('5394', '46', '8', '11', 'και απολειται ο ασθενων αδελφος επι τη ση γνωσει δι ον χριστος απεθανεν');
INSERT INTO `bible_verses_tr` VALUES ('5395', '46', '8', '12', 'ουτως δε αμαρτανοντες εις τους αδελφους και τυπτοντες αυτων την συνειδησιν ασθενουσαν εις χριστον αμαρτανετε');
INSERT INTO `bible_verses_tr` VALUES ('5396', '46', '8', '13', 'διοπερ ει βρωμα σκανδαλιζει τον αδελφον μου ου μη φαγω κρεα εις τον αιωνα ινα μη τον αδελφον μου σκανδαλισω');
INSERT INTO `bible_verses_tr` VALUES ('5397', '46', '9', '1', 'ουκ ειμι αποστολος ουκ ειμι ελευθερος ουχι ιησουν χριστον τον κυριον ημων εωρακα ου το εργον μου υμεις εστε εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5398', '46', '9', '2', 'ει αλλοις ουκ ειμι αποστολος αλλα γε υμιν ειμι η γαρ σφραγις της εμης αποστολης υμεις εστε εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5399', '46', '9', '3', 'η εμη απολογια τοις εμε ανακρινουσιν αυτη εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5400', '46', '9', '4', 'μη ουκ εχομεν εξουσιαν φαγειν και πιειν');
INSERT INTO `bible_verses_tr` VALUES ('5401', '46', '9', '5', 'μη ουκ εχομεν εξουσιαν αδελφην γυναικα περιαγειν ως και οι λοιποι αποστολοι και οι αδελφοι του κυριου και κηφας');
INSERT INTO `bible_verses_tr` VALUES ('5402', '46', '9', '6', 'η μονος εγω και βαρναβας ουκ εχομεν εξουσιαν του μη εργαζεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5403', '46', '9', '7', 'τις στρατευεται ιδιοις οψωνιοις ποτε τις φυτευει αμπελωνα και εκ του καρπου αυτου ουκ εσθιει η τις ποιμαινει ποιμνην και εκ του γαλακτος της ποιμνης ουκ εσθιει');
INSERT INTO `bible_verses_tr` VALUES ('5404', '46', '9', '8', 'μη κατα ανθρωπον ταυτα λαλω η ουχι και ο νομος ταυτα λεγει');
INSERT INTO `bible_verses_tr` VALUES ('5405', '46', '9', '9', 'εν γαρ τω μωσεως νομω γεγραπται ου φιμωσεις βουν αλοωντα μη των βοων μελει τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5406', '46', '9', '10', 'η δι ημας παντως λεγει δι ημας γαρ εγραφη οτι επ ελπιδι οφειλει ο αροτριων αροτριαν και ο αλοων της ελπιδος αυτου μετεχειν επ ελπιδι');
INSERT INTO `bible_verses_tr` VALUES ('5407', '46', '9', '11', 'ει ημεις υμιν τα πνευματικα εσπειραμεν μεγα ει ημεις υμων τα σαρκικα θερισομεν');
INSERT INTO `bible_verses_tr` VALUES ('5408', '46', '9', '12', 'ει αλλοι της εξουσιας υμων μετεχουσιν ου μαλλον ημεις αλλ ουκ εχρησαμεθα τη εξουσια ταυτη αλλα παντα στεγομεν ινα μη εγκοπην τινα δωμεν τω ευαγγελιω του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5409', '46', '9', '13', 'ουκ οιδατε οτι οι τα ιερα εργαζομενοι εκ του ιερου εσθιουσιν οι τω θυσιαστηριω προσεδρευοντες τω θυσιαστηριω συμμεριζονται');
INSERT INTO `bible_verses_tr` VALUES ('5410', '46', '9', '14', 'ουτως και ο κυριος διεταξεν τοις το ευαγγελιον καταγγελλουσιν εκ του ευαγγελιου ζην');
INSERT INTO `bible_verses_tr` VALUES ('5411', '46', '9', '15', 'εγω δε ουδενι εχρησαμην τουτων ουκ εγραψα δε ταυτα ινα ουτως γενηται εν εμοι καλον γαρ μοι μαλλον αποθανειν η το καυχημα μου ινα τις κενωση');
INSERT INTO `bible_verses_tr` VALUES ('5412', '46', '9', '16', 'εαν γαρ ευαγγελιζωμαι ουκ εστιν μοι καυχημα αναγκη γαρ μοι επικειται ουαι δε μοι εστιν εαν μη ευαγγελιζωμαι');
INSERT INTO `bible_verses_tr` VALUES ('5413', '46', '9', '17', 'ει γαρ εκων τουτο πρασσω μισθον εχω ει δε ακων οικονομιαν πεπιστευμαι');
INSERT INTO `bible_verses_tr` VALUES ('5414', '46', '9', '18', 'τις ουν μοι εστιν ο μισθος ινα ευαγγελιζομενος αδαπανον θησω το ευαγγελιον του χριστου εις το μη καταχρησασθαι τη εξουσια μου εν τω ευαγγελιω');
INSERT INTO `bible_verses_tr` VALUES ('5415', '46', '9', '19', 'ελευθερος γαρ ων εκ παντων πασιν εμαυτον εδουλωσα ινα τους πλειονας κερδησω');
INSERT INTO `bible_verses_tr` VALUES ('5416', '46', '9', '20', 'και εγενομην τοις ιουδαιοις ως ιουδαιος ινα ιουδαιους κερδησω τοις υπο νομον ως υπο νομον ινα τους υπο νομον κερδησω');
INSERT INTO `bible_verses_tr` VALUES ('5417', '46', '9', '21', 'τοις ανομοις ως ανομος μη ων ανομος θεω αλλ εννομος χριστω ινα κερδησω ανομους');
INSERT INTO `bible_verses_tr` VALUES ('5418', '46', '9', '22', 'εγενομην τοις ασθενεσιν ως ασθενης ινα τους ασθενεις κερδησω τοις πασιν γεγονα τα παντα ινα παντως τινας σωσω');
INSERT INTO `bible_verses_tr` VALUES ('5419', '46', '9', '23', 'τουτο δε ποιω δια το ευαγγελιον ινα συγκοινωνος αυτου γενωμαι');
INSERT INTO `bible_verses_tr` VALUES ('5420', '46', '9', '24', 'ουκ οιδατε οτι οι εν σταδιω τρεχοντες παντες μεν τρεχουσιν εις δε λαμβανει το βραβειον ουτως τρεχετε ινα καταλαβητε');
INSERT INTO `bible_verses_tr` VALUES ('5421', '46', '9', '25', 'πας δε ο αγωνιζομενος παντα εγκρατευεται εκεινοι μεν ουν ινα φθαρτον στεφανον λαβωσιν ημεις δε αφθαρτον');
INSERT INTO `bible_verses_tr` VALUES ('5422', '46', '9', '26', 'εγω τοινυν ουτως τρεχω ως ουκ αδηλως ουτως πυκτευω ως ουκ αερα δερων');
INSERT INTO `bible_verses_tr` VALUES ('5423', '46', '9', '27', 'αλλ υπωπιαζω μου το σωμα και δουλαγωγω μηπως αλλοις κηρυξας αυτος αδοκιμος γενωμαι');
INSERT INTO `bible_verses_tr` VALUES ('5424', '46', '10', '1', 'ου θελω δε υμας αγνοειν αδελφοι οτι οι πατερες ημων παντες υπο την νεφελην ησαν και παντες δια της θαλασσης διηλθον');
INSERT INTO `bible_verses_tr` VALUES ('5425', '46', '10', '2', 'και παντες εις τον μωσην εβαπτισαντο εν τη νεφελη και εν τη θαλασση');
INSERT INTO `bible_verses_tr` VALUES ('5426', '46', '10', '3', 'και παντες το αυτο βρωμα πνευματικον εφαγον');
INSERT INTO `bible_verses_tr` VALUES ('5427', '46', '10', '4', 'και παντες το αυτο πομα πνευματικον επιον επινον γαρ εκ πνευματικης ακολουθουσης πετρας η δε πετρα ην ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('5428', '46', '10', '5', 'αλλ ουκ εν τοις πλειοσιν αυτων ευδοκησεν ο θεος κατεστρωθησαν γαρ εν τη ερημω');
INSERT INTO `bible_verses_tr` VALUES ('5429', '46', '10', '6', 'ταυτα δε τυποι ημων εγενηθησαν εις το μη ειναι ημας επιθυμητας κακων καθως κακεινοι επεθυμησαν');
INSERT INTO `bible_verses_tr` VALUES ('5430', '46', '10', '7', 'μηδε ειδωλολατραι γινεσθε καθως τινες αυτων ως γεγραπται εκαθισεν ο λαος φαγειν και πιειν και ανεστησαν παιζειν');
INSERT INTO `bible_verses_tr` VALUES ('5431', '46', '10', '8', 'μηδε πορνευωμεν καθως τινες αυτων επορνευσαν και επεσον εν μια ημερα εικοσιτρεις χιλιαδες');
INSERT INTO `bible_verses_tr` VALUES ('5432', '46', '10', '9', 'μηδε εκπειραζωμεν τον χριστον καθως και τινες αυτων επειρασαν και υπο των οφεων απωλοντο');
INSERT INTO `bible_verses_tr` VALUES ('5433', '46', '10', '10', 'μηδε γογγυζετε καθως και τινες αυτων εγογγυσαν και απωλοντο υπο του ολοθρευτου');
INSERT INTO `bible_verses_tr` VALUES ('5434', '46', '10', '11', 'ταυτα δε παντα τυποι συνεβαινον εκεινοις εγραφη δε προς νουθεσιαν ημων εις ους τα τελη των αιωνων κατηντησεν');
INSERT INTO `bible_verses_tr` VALUES ('5435', '46', '10', '12', 'ωστε ο δοκων εσταναι βλεπετω μη πεση');
INSERT INTO `bible_verses_tr` VALUES ('5436', '46', '10', '13', 'πειρασμος υμας ουκ ειληφεν ει μη ανθρωπινος πιστος δε ο θεος ος ουκ εασει υμας πειρασθηναι υπερ ο δυνασθε αλλα ποιησει συν τω πειρασμω και την εκβασιν του δυνασθαι υμας υπενεγκειν');
INSERT INTO `bible_verses_tr` VALUES ('5437', '46', '10', '14', 'διοπερ αγαπητοι μου φευγετε απο της ειδωλολατρειας');
INSERT INTO `bible_verses_tr` VALUES ('5438', '46', '10', '15', 'ως φρονιμοις λεγω κρινατε υμεις ο φημι');
INSERT INTO `bible_verses_tr` VALUES ('5439', '46', '10', '16', 'το ποτηριον της ευλογιας ο ευλογουμεν ουχι κοινωνια του αιματος του χριστου εστιν τον αρτον ον κλωμεν ουχι κοινωνια του σωματος του χριστου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5440', '46', '10', '17', 'οτι εις αρτος εν σωμα οι πολλοι εσμεν οι γαρ παντες εκ του ενος αρτου μετεχομεν');
INSERT INTO `bible_verses_tr` VALUES ('5441', '46', '10', '18', 'βλεπετε τον ισραηλ κατα σαρκα ουχι οι εσθιοντες τας θυσιας κοινωνοι του θυσιαστηριου εισιν');
INSERT INTO `bible_verses_tr` VALUES ('5442', '46', '10', '19', 'τι ουν φημι οτι ειδωλον τι εστιν η οτι ειδωλοθυτον τι εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5443', '46', '10', '20', 'αλλ οτι α θυει τα εθνη δαιμονιοις θυει και ου θεω ου θελω δε υμας κοινωνους των δαιμονιων γινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5444', '46', '10', '21', 'ου δυνασθε ποτηριον κυριου πινειν και ποτηριον δαιμονιων ου δυνασθε τραπεζης κυριου μετεχειν και τραπεζης δαιμονιων');
INSERT INTO `bible_verses_tr` VALUES ('5445', '46', '10', '22', 'η παραζηλουμεν τον κυριον μη ισχυροτεροι αυτου εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('5446', '46', '10', '23', 'παντα μοι εξεστιν αλλ ου παντα συμφερει παντα μοι εξεστιν αλλ ου παντα οικοδομει');
INSERT INTO `bible_verses_tr` VALUES ('5447', '46', '10', '24', 'μηδεις το εαυτου ζητειτω αλλα το του ετερου εκαστος');
INSERT INTO `bible_verses_tr` VALUES ('5448', '46', '10', '25', 'παν το εν μακελλω πωλουμενον εσθιετε μηδεν ανακρινοντες δια την συνειδησιν');
INSERT INTO `bible_verses_tr` VALUES ('5449', '46', '10', '26', 'του γαρ κυριου η γη και το πληρωμα αυτης');
INSERT INTO `bible_verses_tr` VALUES ('5450', '46', '10', '27', 'ει δε τις καλει υμας των απιστων και θελετε πορευεσθαι παν το παρατιθεμενον υμιν εσθιετε μηδεν ανακρινοντες δια την συνειδησιν');
INSERT INTO `bible_verses_tr` VALUES ('5451', '46', '10', '28', 'εαν δε τις υμιν ειπη τουτο ειδωλοθυτον εστιν μη εσθιετε δι εκεινον τον μηνυσαντα και την συνειδησιν του γαρ κυριου η γη και το πληρωμα αυτης');
INSERT INTO `bible_verses_tr` VALUES ('5452', '46', '10', '29', 'συνειδησιν δε λεγω ουχι την εαυτου αλλα την του ετερου ινα τι γαρ η ελευθερια μου κρινεται υπο αλλης συνειδησεως');
INSERT INTO `bible_verses_tr` VALUES ('5453', '46', '10', '30', 'ει δε εγω χαριτι μετεχω τι βλασφημουμαι υπερ ου εγω ευχαριστω');
INSERT INTO `bible_verses_tr` VALUES ('5454', '46', '10', '31', 'ειτε ουν εσθιετε ειτε πινετε ειτε τι ποιειτε παντα εις δοξαν θεου ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('5455', '46', '10', '32', 'απροσκοποι γινεσθε και ιουδαιοις και ελλησιν και τη εκκλησια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5456', '46', '10', '33', 'καθως καγω παντα πασιν αρεσκω μη ζητων το εμαυτου συμφερον αλλα το των πολλων ινα σωθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('5457', '46', '11', '1', 'μιμηται μου γινεσθε καθως καγω χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5458', '46', '11', '2', 'επαινω δε υμας αδελφοι οτι παντα μου μεμνησθε και καθως παρεδωκα υμιν τας παραδοσεις κατεχετε');
INSERT INTO `bible_verses_tr` VALUES ('5459', '46', '11', '3', 'θελω δε υμας ειδεναι οτι παντος ανδρος η κεφαλη ο χριστος εστιν κεφαλη δε γυναικος ο ανηρ κεφαλη δε χριστου ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('5460', '46', '11', '4', 'πας ανηρ προσευχομενος η προφητευων κατα κεφαλης εχων καταισχυνει την κεφαλην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5461', '46', '11', '5', 'πασα δε γυνη προσευχομενη η προφητευουσα ακατακαλυπτω τη κεφαλη καταισχυνει την κεφαλην εαυτης εν γαρ εστιν και το αυτο τη εξυρημενη');
INSERT INTO `bible_verses_tr` VALUES ('5462', '46', '11', '6', 'ει γαρ ου κατακαλυπτεται γυνη και κειρασθω ει δε αισχρον γυναικι το κειρασθαι η ξυρασθαι κατακαλυπτεσθω');
INSERT INTO `bible_verses_tr` VALUES ('5463', '46', '11', '7', 'ανηρ μεν γαρ ουκ οφειλει κατακαλυπτεσθαι την κεφαλην εικων και δοξα θεου υπαρχων γυνη δε δοξα ανδρος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5464', '46', '11', '8', 'ου γαρ εστιν ανηρ εκ γυναικος αλλα γυνη εξ ανδρος');
INSERT INTO `bible_verses_tr` VALUES ('5465', '46', '11', '9', 'και γαρ ουκ εκτισθη ανηρ δια την γυναικα αλλα γυνη δια τον ανδρα');
INSERT INTO `bible_verses_tr` VALUES ('5466', '46', '11', '10', 'δια τουτο οφειλει η γυνη εξουσιαν εχειν επι της κεφαλης δια τους αγγελους');
INSERT INTO `bible_verses_tr` VALUES ('5467', '46', '11', '11', 'πλην ουτε ανηρ χωρις γυναικος ουτε γυνη χωρις ανδρος εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5468', '46', '11', '12', 'ωσπερ γαρ η γυνη εκ του ανδρος ουτως και ο ανηρ δια της γυναικος τα δε παντα εκ του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5469', '46', '11', '13', 'εν υμιν αυτοις κρινατε πρεπον εστιν γυναικα ακατακαλυπτον τω θεω προσευχεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5470', '46', '11', '14', 'η ουδε αυτη η φυσις διδασκει υμας οτι ανηρ μεν εαν κομα ατιμια αυτω εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5471', '46', '11', '15', 'γυνη δε εαν κομα δοξα αυτη εστιν οτι η κομη αντι περιβολαιου δεδοται αυτη');
INSERT INTO `bible_verses_tr` VALUES ('5472', '46', '11', '16', 'ει δε τις δοκει φιλονεικος ειναι ημεις τοιαυτην συνηθειαν ουκ εχομεν ουδε αι εκκλησιαι του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5473', '46', '11', '17', 'τουτο δε παραγγελλων ουκ επαινω οτι ουκ εις το κρειττον αλλ εις το ηττον συνερχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5474', '46', '11', '18', 'πρωτον μεν γαρ συνερχομενων υμων εν τη εκκλησια ακουω σχισματα εν υμιν υπαρχειν και μερος τι πιστευω');
INSERT INTO `bible_verses_tr` VALUES ('5475', '46', '11', '19', 'δει γαρ και αιρεσεις εν υμιν ειναι ινα οι δοκιμοι φανεροι γενωνται εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5476', '46', '11', '20', 'συνερχομενων ουν υμων επι το αυτο ουκ εστιν κυριακον δειπνον φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('5477', '46', '11', '21', 'εκαστος γαρ το ιδιον δειπνον προλαμβανει εν τω φαγειν και ος μεν πεινα ος δε μεθυει');
INSERT INTO `bible_verses_tr` VALUES ('5478', '46', '11', '22', 'μη γαρ οικιας ουκ εχετε εις το εσθιειν και πινειν η της εκκλησιας του θεου καταφρονειτε και καταισχυνετε τους μη εχοντας τι υμιν ειπω επαινεσω υμας εν τουτω ουκ επαινω');
INSERT INTO `bible_verses_tr` VALUES ('5479', '46', '11', '23', 'εγω γαρ παρελαβον απο του κυριου ο και παρεδωκα υμιν οτι ο κυριος ιησους εν τη νυκτι η παρεδιδοτο ελαβεν αρτον');
INSERT INTO `bible_verses_tr` VALUES ('5480', '46', '11', '24', 'και ευχαριστησας εκλασεν και ειπεν λαβετε φαγετε τουτο μου εστιν το σωμα το υπερ υμων κλωμενον τουτο ποιειτε εις την εμην αναμνησιν');
INSERT INTO `bible_verses_tr` VALUES ('5481', '46', '11', '25', 'ωσαυτως και το ποτηριον μετα το δειπνησαι λεγων τουτο το ποτηριον η καινη διαθηκη εστιν εν τω εμω αιματι τουτο ποιειτε οσακις αν πινητε εις την εμην αναμνησιν');
INSERT INTO `bible_verses_tr` VALUES ('5482', '46', '11', '26', 'οσακις γαρ αν εσθιητε τον αρτον τουτον και το ποτηριον τουτο πινητε τον θανατον του κυριου καταγγελλετε αχρις ου αν ελθη');
INSERT INTO `bible_verses_tr` VALUES ('5483', '46', '11', '27', 'ωστε ος αν εσθιη τον αρτον τουτον η πινη το ποτηριον του κυριου αναξιως ενοχος εσται του σωματος και αιματος του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('5484', '46', '11', '28', 'δοκιμαζετω δε ανθρωπος εαυτον και ουτως εκ του αρτου εσθιετω και εκ του ποτηριου πινετω');
INSERT INTO `bible_verses_tr` VALUES ('5485', '46', '11', '29', 'ο γαρ εσθιων και πινων αναξιως κριμα εαυτω εσθιει και πινει μη διακρινων το σωμα του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('5486', '46', '11', '30', 'δια τουτο εν υμιν πολλοι ασθενεις και αρρωστοι και κοιμωνται ικανοι');
INSERT INTO `bible_verses_tr` VALUES ('5487', '46', '11', '31', 'ει γαρ εαυτους διεκρινομεν ουκ αν εκρινομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5488', '46', '11', '32', 'κρινομενοι δε υπο κυριου παιδευομεθα ινα μη συν τω κοσμω κατακριθωμεν');
INSERT INTO `bible_verses_tr` VALUES ('5489', '46', '11', '33', 'ωστε αδελφοι μου συνερχομενοι εις το φαγειν αλληλους εκδεχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5490', '46', '11', '34', 'ει δε τις πεινα εν οικω εσθιετω ινα μη εις κριμα συνερχησθε τα δε λοιπα ως αν ελθω διαταξομαι');
INSERT INTO `bible_verses_tr` VALUES ('5491', '46', '12', '1', 'περι δε των πνευματικων αδελφοι ου θελω υμας αγνοειν');
INSERT INTO `bible_verses_tr` VALUES ('5492', '46', '12', '2', 'οιδατε οτι εθνη ητε προς τα ειδωλα τα αφωνα ως αν ηγεσθε απαγομενοι');
INSERT INTO `bible_verses_tr` VALUES ('5493', '46', '12', '3', 'διο γνωριζω υμιν οτι ουδεις εν πνευματι θεου λαλων λεγει αναθεμα ιησουν και ουδεις δυναται ειπειν κυριον ιησουν ει μη εν πνευματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('5494', '46', '12', '4', 'διαιρεσεις δε χαρισματων εισιν το δε αυτο πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('5495', '46', '12', '5', 'και διαιρεσεις διακονιων εισιν και ο αυτος κυριος');
INSERT INTO `bible_verses_tr` VALUES ('5496', '46', '12', '6', 'και διαιρεσεις ενεργηματων εισιν ο δε αυτος εστιν θεος ο ενεργων τα παντα εν πασιν');
INSERT INTO `bible_verses_tr` VALUES ('5497', '46', '12', '7', 'εκαστω δε διδοται η φανερωσις του πνευματος προς το συμφερον');
INSERT INTO `bible_verses_tr` VALUES ('5498', '46', '12', '8', 'ω μεν γαρ δια του πνευματος διδοται λογος σοφιας αλλω δε λογος γνωσεως κατα το αυτο πνευμα');
INSERT INTO `bible_verses_tr` VALUES ('5499', '46', '12', '9', 'ετερω δε πιστις εν τω αυτω πνευματι αλλω δε χαρισματα ιαματων εν τω αυτω πνευματι');
INSERT INTO `bible_verses_tr` VALUES ('5500', '46', '12', '10', 'αλλω δε ενεργηματα δυναμεων αλλω δε προφητεια αλλω δε διακρισεις πνευματων ετερω δε γενη γλωσσων αλλω δε ερμηνεια γλωσσων');
INSERT INTO `bible_verses_tr` VALUES ('5501', '46', '12', '11', 'παντα δε ταυτα ενεργει το εν και το αυτο πνευμα διαιρουν ιδια εκαστω καθως βουλεται');
INSERT INTO `bible_verses_tr` VALUES ('5502', '46', '12', '12', 'καθαπερ γαρ το σωμα εν εστιν και μελη εχει πολλα παντα δε τα μελη του σωματος του ενος πολλα οντα εν εστιν σωμα ουτως και ο χριστος');
INSERT INTO `bible_verses_tr` VALUES ('5503', '46', '12', '13', 'και γαρ εν ενι πνευματι ημεις παντες εις εν σωμα εβαπτισθημεν ειτε ιουδαιοι ειτε ελληνες ειτε δουλοι ειτε ελευθεροι και παντες εις εν πνευμα εποτισθημεν');
INSERT INTO `bible_verses_tr` VALUES ('5504', '46', '12', '14', 'και γαρ το σωμα ουκ εστιν εν μελος αλλα πολλα');
INSERT INTO `bible_verses_tr` VALUES ('5505', '46', '12', '15', 'εαν ειπη ο πους οτι ουκ ειμι χειρ ουκ ειμι εκ του σωματος ου παρα τουτο ουκ εστιν εκ του σωματος');
INSERT INTO `bible_verses_tr` VALUES ('5506', '46', '12', '16', 'και εαν ειπη το ους οτι ουκ ειμι οφθαλμος ουκ ειμι εκ του σωματος ου παρα τουτο ουκ εστιν εκ του σωματος');
INSERT INTO `bible_verses_tr` VALUES ('5507', '46', '12', '17', 'ει ολον το σωμα οφθαλμος που η ακοη ει ολον ακοη που η οσφρησις');
INSERT INTO `bible_verses_tr` VALUES ('5508', '46', '12', '18', 'νυνι δε ο θεος εθετο τα μελη εν εκαστον αυτων εν τω σωματι καθως ηθελησεν');
INSERT INTO `bible_verses_tr` VALUES ('5509', '46', '12', '19', 'ει δε ην τα παντα εν μελος που το σωμα');
INSERT INTO `bible_verses_tr` VALUES ('5510', '46', '12', '20', 'νυν δε πολλα μεν μελη εν δε σωμα');
INSERT INTO `bible_verses_tr` VALUES ('5511', '46', '12', '21', 'ου δυναται δε οφθαλμος ειπειν τη χειρι χρειαν σου ουκ εχω η παλιν η κεφαλη τοις ποσιν χρειαν υμων ουκ εχω');
INSERT INTO `bible_verses_tr` VALUES ('5512', '46', '12', '22', 'αλλα πολλω μαλλον τα δοκουντα μελη του σωματος ασθενεστερα υπαρχειν αναγκαια εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5513', '46', '12', '23', 'και α δοκουμεν ατιμοτερα ειναι του σωματος τουτοις τιμην περισσοτεραν περιτιθεμεν και τα ασχημονα ημων ευσχημοσυνην περισσοτεραν εχει');
INSERT INTO `bible_verses_tr` VALUES ('5514', '46', '12', '24', 'τα δε ευσχημονα ημων ου χρειαν εχει αλλ ο θεος συνεκερασεν το σωμα τω υστερουντι περισσοτεραν δους τιμην');
INSERT INTO `bible_verses_tr` VALUES ('5515', '46', '12', '25', 'ινα μη η σχισμα εν τω σωματι αλλα το αυτο υπερ αλληλων μεριμνωσιν τα μελη');
INSERT INTO `bible_verses_tr` VALUES ('5516', '46', '12', '26', 'και ειτε πασχει εν μελος συμπασχει παντα τα μελη ειτε δοξαζεται εν μελος συγχαιρει παντα τα μελη');
INSERT INTO `bible_verses_tr` VALUES ('5517', '46', '12', '27', 'υμεις δε εστε σωμα χριστου και μελη εκ μερους');
INSERT INTO `bible_verses_tr` VALUES ('5518', '46', '12', '28', 'και ους μεν εθετο ο θεος εν τη εκκλησια πρωτον αποστολους δευτερον προφητας τριτον διδασκαλους επειτα δυναμεις ειτα χαρισματα ιαματων αντιληψεις κυβερνησεις γενη γλωσσων');
INSERT INTO `bible_verses_tr` VALUES ('5519', '46', '12', '29', 'μη παντες αποστολοι μη παντες προφηται μη παντες διδασκαλοι μη παντες δυναμεις');
INSERT INTO `bible_verses_tr` VALUES ('5520', '46', '12', '30', 'μη παντες χαρισματα εχουσιν ιαματων μη παντες γλωσσαις λαλουσιν μη παντες διερμηνευουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5521', '46', '12', '31', 'ζηλουτε δε τα χαρισματα τα κρειττονα και ετι καθ υπερβολην οδον υμιν δεικνυμι');
INSERT INTO `bible_verses_tr` VALUES ('5522', '46', '13', '1', 'εαν ταις γλωσσαις των ανθρωπων λαλω και των αγγελων αγαπην δε μη εχω γεγονα χαλκος ηχων η κυμβαλον αλαλαζον');
INSERT INTO `bible_verses_tr` VALUES ('5523', '46', '13', '2', 'και εαν εχω προφητειαν και ειδω τα μυστηρια παντα και πασαν την γνωσιν και εαν εχω πασαν την πιστιν ωστε ορη μεθιστανειν αγαπην δε μη εχω  {VAR1: ουθεν } {VAR2: ουδεν } ειμι');
INSERT INTO `bible_verses_tr` VALUES ('5524', '46', '13', '3', 'και εαν ψωμισω παντα τα υπαρχοντα μου και εαν παραδω το σωμα μου ινα καυθησωμαι αγαπην δε μη εχω ουδεν ωφελουμαι');
INSERT INTO `bible_verses_tr` VALUES ('5525', '46', '13', '4', 'η αγαπη μακροθυμει χρηστευεται η αγαπη ου ζηλοι η αγαπη ου περπερευεται ου φυσιουται');
INSERT INTO `bible_verses_tr` VALUES ('5526', '46', '13', '5', 'ουκ ασχημονει ου ζητει τα εαυτης ου παροξυνεται ου λογιζεται το κακον');
INSERT INTO `bible_verses_tr` VALUES ('5527', '46', '13', '6', 'ου χαιρει επι τη αδικια συγχαιρει δε τη αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('5528', '46', '13', '7', 'παντα στεγει παντα πιστευει παντα ελπιζει παντα υπομενει');
INSERT INTO `bible_verses_tr` VALUES ('5529', '46', '13', '8', 'η αγαπη ουδεποτε εκπιπτει ειτε δε προφητειαι καταργηθησονται ειτε γλωσσαι παυσονται ειτε γνωσις καταργηθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5530', '46', '13', '9', 'εκ μερους γαρ γινωσκομεν και εκ μερους προφητευομεν');
INSERT INTO `bible_verses_tr` VALUES ('5531', '46', '13', '10', 'οταν δε ελθη το τελειον τοτε το εκ μερους καταργηθησεται');
INSERT INTO `bible_verses_tr` VALUES ('5532', '46', '13', '11', 'οτε ημην νηπιος ως νηπιος ελαλουν ως νηπιος εφρονουν ως νηπιος ελογιζομην οτε δε γεγονα ανηρ κατηργηκα τα του νηπιου');
INSERT INTO `bible_verses_tr` VALUES ('5533', '46', '13', '12', 'βλεπομεν γαρ αρτι δι εσοπτρου εν αινιγματι τοτε δε προσωπον προς προσωπον αρτι γινωσκω εκ μερους τοτε δε επιγνωσομαι καθως και επεγνωσθην');
INSERT INTO `bible_verses_tr` VALUES ('5534', '46', '13', '13', 'νυνι δε μενει πιστις ελπις αγαπη τα τρια ταυτα μειζων δε τουτων η αγαπη');
INSERT INTO `bible_verses_tr` VALUES ('5535', '46', '14', '1', 'διωκετε την αγαπην ζηλουτε δε τα πνευματικα μαλλον δε ινα προφητευητε');
INSERT INTO `bible_verses_tr` VALUES ('5536', '46', '14', '2', 'ο γαρ λαλων γλωσση ουκ ανθρωποις λαλει αλλα τω θεω ουδεις γαρ ακουει πνευματι δε λαλει μυστηρια');
INSERT INTO `bible_verses_tr` VALUES ('5537', '46', '14', '3', 'ο δε προφητευων ανθρωποις λαλει οικοδομην και παρακλησιν και παραμυθιαν');
INSERT INTO `bible_verses_tr` VALUES ('5538', '46', '14', '4', 'ο λαλων γλωσση εαυτον οικοδομει ο δε προφητευων εκκλησιαν οικοδομει');
INSERT INTO `bible_verses_tr` VALUES ('5539', '46', '14', '5', 'θελω δε παντας υμας λαλειν γλωσσαις μαλλον δε ινα προφητευητε μειζων γαρ ο προφητευων η ο λαλων γλωσσαις εκτος ει μη διερμηνευη ινα η εκκλησια οικοδομην λαβη');
INSERT INTO `bible_verses_tr` VALUES ('5540', '46', '14', '6', 'νυνι δε αδελφοι εαν ελθω προς υμας γλωσσαις λαλων τι υμας ωφελησω εαν μη υμιν λαλησω η εν αποκαλυψει η εν γνωσει η εν προφητεια η εν διδαχη');
INSERT INTO `bible_verses_tr` VALUES ('5541', '46', '14', '7', 'ομως τα αψυχα φωνην διδοντα ειτε αυλος ειτε κιθαρα εαν διαστολην τοις φθογγοις μη δω πως γνωσθησεται το αυλουμενον η το κιθαριζομενον');
INSERT INTO `bible_verses_tr` VALUES ('5542', '46', '14', '8', 'και γαρ εαν αδηλον φωνην σαλπιγξ δω τις παρασκευασεται εις πολεμον');
INSERT INTO `bible_verses_tr` VALUES ('5543', '46', '14', '9', 'ουτως και υμεις δια της γλωσσης εαν μη ευσημον λογον δωτε πως γνωσθησεται το λαλουμενον εσεσθε γαρ εις αερα λαλουντες');
INSERT INTO `bible_verses_tr` VALUES ('5544', '46', '14', '10', 'τοσαυτα ει τυχοι γενη φωνων εστιν εν κοσμω και ουδεν  {VAR1: αυτων } αφωνον');
INSERT INTO `bible_verses_tr` VALUES ('5545', '46', '14', '11', 'εαν ουν μη ειδω την δυναμιν της φωνης εσομαι τω λαλουντι βαρβαρος και ο λαλων εν εμοι βαρβαρος');
INSERT INTO `bible_verses_tr` VALUES ('5546', '46', '14', '12', 'ουτως και υμεις επει ζηλωται εστε πνευματων προς την οικοδομην της εκκλησιας ζητειτε ινα περισσευητε');
INSERT INTO `bible_verses_tr` VALUES ('5547', '46', '14', '13', 'διοπερ ο λαλων γλωσση προσευχεσθω ινα διερμηνευη');
INSERT INTO `bible_verses_tr` VALUES ('5548', '46', '14', '14', 'εαν γαρ προσευχωμαι γλωσση το πνευμα μου προσευχεται ο δε νους μου ακαρπος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5549', '46', '14', '15', 'τι ουν εστιν προσευξομαι τω πνευματι προσευξομαι δε και τω νοι ψαλω τω πνευματι ψαλω δε και τω νοι');
INSERT INTO `bible_verses_tr` VALUES ('5550', '46', '14', '16', 'επει εαν ευλογησης τω πνευματι ο αναπληρων τον τοπον του ιδιωτου πως ερει το αμην επι τη ση ευχαριστια επειδη τι λεγεις ουκ οιδεν');
INSERT INTO `bible_verses_tr` VALUES ('5551', '46', '14', '17', 'συ μεν γαρ καλως ευχαριστεις αλλ ο ετερος ουκ οικοδομειται');
INSERT INTO `bible_verses_tr` VALUES ('5552', '46', '14', '18', 'ευχαριστω τω θεω μου παντων υμων μαλλον γλωσσαις λαλων');
INSERT INTO `bible_verses_tr` VALUES ('5553', '46', '14', '19', 'αλλ εν εκκλησια θελω πεντε λογους δια του νοος μου λαλησαι ινα και αλλους κατηχησω η μυριους λογους εν γλωσση');
INSERT INTO `bible_verses_tr` VALUES ('5554', '46', '14', '20', 'αδελφοι μη παιδια γινεσθε ταις φρεσιν αλλα τη κακια νηπιαζετε ταις δε φρεσιν τελειοι γινεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5555', '46', '14', '21', 'εν τω νομω γεγραπται οτι εν ετερογλωσσοις και εν χειλεσιν ετεροις λαλησω τω λαω τουτω και ουδ ουτως εισακουσονται μου λεγει κυριος');
INSERT INTO `bible_verses_tr` VALUES ('5556', '46', '14', '22', 'ωστε αι γλωσσαι εις σημειον εισιν ου τοις πιστευουσιν αλλα τοις απιστοις η δε προφητεια ου τοις απιστοις αλλα τοις πιστευουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5557', '46', '14', '23', 'εαν ουν συνελθη η εκκλησια ολη επι το αυτο και παντες γλωσσαις λαλωσιν εισελθωσιν δε ιδιωται η απιστοι ουκ ερουσιν οτι μαινεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5558', '46', '14', '24', 'εαν δε παντες προφητευωσιν εισελθη δε τις απιστος η ιδιωτης ελεγχεται υπο παντων ανακρινεται υπο παντων');
INSERT INTO `bible_verses_tr` VALUES ('5559', '46', '14', '25', 'και ουτως τα κρυπτα της καρδιας αυτου φανερα γινεται και ουτως πεσων επι προσωπον προσκυνησει τω θεω απαγγελλων οτι ο θεος οντως εν υμιν εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5560', '46', '14', '26', 'τι ουν εστιν αδελφοι οταν συνερχησθε εκαστος υμων ψαλμον εχει διδαχην εχει γλωσσαν εχει αποκαλυψιν εχει ερμηνειαν εχει παντα προς οικοδομην γενεσθω');
INSERT INTO `bible_verses_tr` VALUES ('5561', '46', '14', '27', 'ειτε γλωσση τις λαλει κατα δυο η το πλειστον τρεις και ανα μερος και εις διερμηνευετω');
INSERT INTO `bible_verses_tr` VALUES ('5562', '46', '14', '28', 'εαν δε μη η διερμηνευτης σιγατω εν εκκλησια εαυτω δε λαλειτω και τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5563', '46', '14', '29', 'προφηται δε δυο η τρεις λαλειτωσαν και οι αλλοι διακρινετωσαν');
INSERT INTO `bible_verses_tr` VALUES ('5564', '46', '14', '30', 'εαν δε αλλω αποκαλυφθη καθημενω ο πρωτος σιγατω');
INSERT INTO `bible_verses_tr` VALUES ('5565', '46', '14', '31', 'δυνασθε γαρ καθ ενα παντες προφητευειν ινα παντες μανθανωσιν και παντες παρακαλωνται');
INSERT INTO `bible_verses_tr` VALUES ('5566', '46', '14', '32', 'και πνευματα προφητων προφηταις υποτασσεται');
INSERT INTO `bible_verses_tr` VALUES ('5567', '46', '14', '33', 'ου γαρ εστιν ακαταστασιας ο θεος αλλ ειρηνης ως εν πασαις ταις εκκλησιαις των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('5568', '46', '14', '34', 'αι γυναικες υμων εν ταις εκκλησιαις σιγατωσαν ου γαρ επιτετραπται αυταις λαλειν αλλ υποτασσεσθαι καθως και ο νομος λεγει');
INSERT INTO `bible_verses_tr` VALUES ('5569', '46', '14', '35', 'ει δε τι μαθειν θελουσιν εν οικω τους ιδιους ανδρας επερωτατωσαν αισχρον γαρ εστιν γυναιξιν εν εκκλησια λαλειν');
INSERT INTO `bible_verses_tr` VALUES ('5570', '46', '14', '36', 'η αφ υμων ο λογος του θεου εξηλθεν η εις υμας μονους κατηντησεν');
INSERT INTO `bible_verses_tr` VALUES ('5571', '46', '14', '37', 'ει τις δοκει προφητης ειναι η πνευματικος επιγινωσκετω α γραφω υμιν οτι του κυριου εισιν εντολαι');
INSERT INTO `bible_verses_tr` VALUES ('5572', '46', '14', '38', 'ει δε τις αγνοει αγνοειτω');
INSERT INTO `bible_verses_tr` VALUES ('5573', '46', '14', '39', 'ωστε αδελφοι ζηλουτε το προφητευειν και το λαλειν γλωσσαις μη κωλυετε');
INSERT INTO `bible_verses_tr` VALUES ('5574', '46', '14', '40', 'παντα ευσχημονως και κατα ταξιν γινεσθω');
INSERT INTO `bible_verses_tr` VALUES ('5575', '46', '15', '1', 'γνωριζω δε υμιν αδελφοι το ευαγγελιον ο ευηγγελισαμην υμιν ο και παρελαβετε εν ω και εστηκατε');
INSERT INTO `bible_verses_tr` VALUES ('5576', '46', '15', '2', 'δι ου και σωζεσθε τινι λογω ευηγγελισαμην υμιν ει κατεχετε εκτος ει μη εικη επιστευσατε');
INSERT INTO `bible_verses_tr` VALUES ('5577', '46', '15', '3', 'παρεδωκα γαρ υμιν εν πρωτοις ο και παρελαβον οτι χριστος απεθανεν υπερ των αμαρτιων ημων κατα τας γραφας');
INSERT INTO `bible_verses_tr` VALUES ('5578', '46', '15', '4', 'και οτι εταφη και οτι εγηγερται τη τριτη ημερα κατα τας γραφας');
INSERT INTO `bible_verses_tr` VALUES ('5579', '46', '15', '5', 'και οτι ωφθη κηφα ειτα τοις δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('5580', '46', '15', '6', 'επειτα ωφθη επανω πεντακοσιοις αδελφοις εφαπαξ εξ ων οι πλειους μενουσιν εως αρτι τινες δε και εκοιμηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('5581', '46', '15', '7', 'επειτα ωφθη ιακωβω ειτα τοις αποστολοις πασιν');
INSERT INTO `bible_verses_tr` VALUES ('5582', '46', '15', '8', 'εσχατον δε παντων ωσπερει τω εκτρωματι ωφθη καμοι');
INSERT INTO `bible_verses_tr` VALUES ('5583', '46', '15', '9', 'εγω γαρ ειμι ο ελαχιστος των αποστολων ος ουκ ειμι ικανος καλεισθαι αποστολος διοτι εδιωξα την εκκλησιαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5584', '46', '15', '10', 'χαριτι δε θεου ειμι ο ειμι και η χαρις αυτου η εις εμε ου κενη εγενηθη αλλα περισσοτερον αυτων παντων εκοπιασα ουκ εγω δε αλλ η χαρις του θεου η συν εμοι');
INSERT INTO `bible_verses_tr` VALUES ('5585', '46', '15', '11', 'ειτε ουν εγω ειτε εκεινοι ουτως κηρυσσομεν και ουτως επιστευσατε');
INSERT INTO `bible_verses_tr` VALUES ('5586', '46', '15', '12', 'ει δε χριστος κηρυσσεται οτι εκ νεκρων εγηγερται πως λεγουσιν τινες εν υμιν οτι αναστασις νεκρων ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5587', '46', '15', '13', 'ει δε αναστασις νεκρων ουκ εστιν ουδε χριστος εγηγερται');
INSERT INTO `bible_verses_tr` VALUES ('5588', '46', '15', '14', 'ει δε χριστος ουκ εγηγερται κενον αρα το κηρυγμα ημων κενη δε και η πιστις υμων');
INSERT INTO `bible_verses_tr` VALUES ('5589', '46', '15', '15', 'ευρισκομεθα δε και ψευδομαρτυρες του θεου οτι εμαρτυρησαμεν κατα του θεου οτι ηγειρεν τον χριστον ον ουκ ηγειρεν ειπερ αρα νεκροι ουκ εγειρονται');
INSERT INTO `bible_verses_tr` VALUES ('5590', '46', '15', '16', 'ει γαρ νεκροι ουκ εγειρονται ουδε χριστος εγηγερται');
INSERT INTO `bible_verses_tr` VALUES ('5591', '46', '15', '17', 'ει δε χριστος ουκ εγηγερται ματαια η πιστις υμων ετι εστε εν ταις αμαρτιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('5592', '46', '15', '18', 'αρα και οι κοιμηθεντες εν χριστω απωλοντο');
INSERT INTO `bible_verses_tr` VALUES ('5593', '46', '15', '19', 'ει εν τη ζωη ταυτη ηλπικοτες εσμεν εν χριστω μονον ελεεινοτεροι παντων ανθρωπων εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('5594', '46', '15', '20', 'νυνι δε χριστος εγηγερται εκ νεκρων απαρχη των κεκοιμημενων εγενετο');
INSERT INTO `bible_verses_tr` VALUES ('5595', '46', '15', '21', 'επειδη γαρ δι ανθρωπου ο θανατος και δι ανθρωπου αναστασις νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('5596', '46', '15', '22', 'ωσπερ γαρ εν τω αδαμ παντες αποθνησκουσιν ουτως και εν τω χριστω παντες ζωοποιηθησονται');
INSERT INTO `bible_verses_tr` VALUES ('5597', '46', '15', '23', 'εκαστος δε εν τω ιδιω ταγματι απαρχη χριστος επειτα οι χριστου εν τη παρουσια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5598', '46', '15', '24', 'ειτα το τελος οταν παραδω την βασιλειαν τω θεω και πατρι οταν καταργηση πασαν αρχην και πασαν εξουσιαν και δυναμιν');
INSERT INTO `bible_verses_tr` VALUES ('5599', '46', '15', '25', 'δει γαρ αυτον βασιλευειν αχρις ου αν θη παντας τους εχθρους υπο τους ποδας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('5600', '46', '15', '26', 'εσχατος εχθρος καταργειται ο θανατος');
INSERT INTO `bible_verses_tr` VALUES ('5601', '46', '15', '27', 'παντα γαρ υπεταξεν υπο τους ποδας αυτου οταν δε ειπη οτι παντα υποτετακται δηλον οτι εκτος του υποταξαντος αυτω τα παντα');
INSERT INTO `bible_verses_tr` VALUES ('5602', '46', '15', '28', 'οταν δε υποταγη αυτω τα παντα τοτε και αυτος ο υιος υποταγησεται τω υποταξαντι αυτω τα παντα ινα η ο θεος τα παντα εν πασιν');
INSERT INTO `bible_verses_tr` VALUES ('5603', '46', '15', '29', 'επει τι ποιησουσιν οι βαπτιζομενοι υπερ των νεκρων ει ολως νεκροι ουκ εγειρονται τι και βαπτιζονται υπερ των νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('5604', '46', '15', '30', 'τι και ημεις κινδυνευομεν πασαν ωραν');
INSERT INTO `bible_verses_tr` VALUES ('5605', '46', '15', '31', 'καθ ημεραν αποθνησκω νη την  {VAR1: ημετεραν } {VAR2: υμετεραν } καυχησιν ην εχω εν χριστω ιησου τω κυριω ημων');
INSERT INTO `bible_verses_tr` VALUES ('5606', '46', '15', '32', 'ει κατα ανθρωπον εθηριομαχησα εν εφεσω τι μοι το οφελος ει νεκροι ουκ εγειρονται φαγωμεν και πιωμεν αυριον γαρ αποθνησκομεν');
INSERT INTO `bible_verses_tr` VALUES ('5607', '46', '15', '33', 'μη πλανασθε φθειρουσιν ηθη χρησθ ομιλιαι κακαι');
INSERT INTO `bible_verses_tr` VALUES ('5608', '46', '15', '34', 'εκνηψατε δικαιως και μη αμαρτανετε αγνωσιαν γαρ θεου τινες εχουσιν προς εντροπην υμιν λεγω');
INSERT INTO `bible_verses_tr` VALUES ('5609', '46', '15', '35', 'αλλ ερει τις πως εγειρονται οι νεκροι ποιω δε σωματι ερχονται');
INSERT INTO `bible_verses_tr` VALUES ('5610', '46', '15', '36', 'αφρον συ ο σπειρεις ου ζωοποιειται εαν μη αποθανη');
INSERT INTO `bible_verses_tr` VALUES ('5611', '46', '15', '37', 'και ο σπειρεις ου το σωμα το γενησομενον σπειρεις αλλα γυμνον κοκκον ει τυχοι σιτου η τινος των λοιπων');
INSERT INTO `bible_verses_tr` VALUES ('5612', '46', '15', '38', 'ο δε θεος αυτω διδωσιν σωμα καθως ηθελησεν και εκαστω των σπερματων το ιδιον σωμα');
INSERT INTO `bible_verses_tr` VALUES ('5613', '46', '15', '39', 'ου πασα σαρξ η αυτη σαρξ αλλα αλλη μεν σαρξ ανθρωπων αλλη δε σαρξ κτηνων αλλη δε ιχθυων αλλη δε πτηνων');
INSERT INTO `bible_verses_tr` VALUES ('5614', '46', '15', '40', 'και σωματα επουρανια και σωματα επιγεια αλλ ετερα μεν η των επουρανιων δοξα ετερα δε η των επιγειων');
INSERT INTO `bible_verses_tr` VALUES ('5615', '46', '15', '41', 'αλλη δοξα ηλιου και αλλη δοξα σεληνης και αλλη δοξα αστερων αστηρ γαρ αστερος διαφερει εν δοξη');
INSERT INTO `bible_verses_tr` VALUES ('5616', '46', '15', '42', 'ουτως και η αναστασις των νεκρων σπειρεται εν φθορα εγειρεται εν αφθαρσια');
INSERT INTO `bible_verses_tr` VALUES ('5617', '46', '15', '43', 'σπειρεται εν ατιμια εγειρεται εν δοξη σπειρεται εν ασθενεια εγειρεται εν δυναμει');
INSERT INTO `bible_verses_tr` VALUES ('5618', '46', '15', '44', 'σπειρεται σωμα ψυχικον εγειρεται σωμα πνευματικον εστιν σωμα ψυχικον και εστιν σωμα πνευματικον');
INSERT INTO `bible_verses_tr` VALUES ('5619', '46', '15', '45', 'ουτως και γεγραπται εγενετο ο πρωτος ανθρωπος αδαμ εις ψυχην ζωσαν ο εσχατος αδαμ εις πνευμα ζωοποιουν');
INSERT INTO `bible_verses_tr` VALUES ('5620', '46', '15', '46', 'αλλ ου πρωτον το πνευματικον αλλα το ψυχικον επειτα το πνευματικον');
INSERT INTO `bible_verses_tr` VALUES ('5621', '46', '15', '47', 'ο πρωτος ανθρωπος εκ γης χοικος ο δευτερος ανθρωπος ο κυριος εξ ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('5622', '46', '15', '48', 'οιος ο χοικος τοιουτοι και οι χοικοι και οιος ο επουρανιος τοιουτοι και οι επουρανιοι');
INSERT INTO `bible_verses_tr` VALUES ('5623', '46', '15', '49', 'και καθως εφορεσαμεν την εικονα του χοικου φορεσομεν και την εικονα του επουρανιου');
INSERT INTO `bible_verses_tr` VALUES ('5624', '46', '15', '50', 'τουτο δε φημι αδελφοι οτι σαρξ και αιμα βασιλειαν θεου κληρονομησαι ου δυνανται ουδε η φθορα την αφθαρσιαν κληρονομει');
INSERT INTO `bible_verses_tr` VALUES ('5625', '46', '15', '51', 'ιδου μυστηριον υμιν λεγω παντες μεν ου κοιμηθησομεθα παντες δε αλλαγησομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5626', '46', '15', '52', 'εν ατομω εν ριπη οφθαλμου εν τη εσχατη σαλπιγγι σαλπισει γαρ και οι νεκροι εγερθησονται αφθαρτοι και ημεις αλλαγησομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5627', '46', '15', '53', 'δει γαρ το φθαρτον τουτο ενδυσασθαι αφθαρσιαν και το θνητον τουτο ενδυσασθαι αθανασιαν');
INSERT INTO `bible_verses_tr` VALUES ('5628', '46', '15', '54', 'οταν δε το φθαρτον τουτο ενδυσηται αφθαρσιαν και το θνητον τουτο ενδυσηται αθανασιαν τοτε γενησεται ο λογος ο γεγραμμενος κατεποθη ο θανατος εις νικος');
INSERT INTO `bible_verses_tr` VALUES ('5629', '46', '15', '55', 'που σου θανατε το κεντρον που σου αδη το νικος');
INSERT INTO `bible_verses_tr` VALUES ('5630', '46', '15', '56', 'το δε κεντρον του θανατου η αμαρτια η δε δυναμις της αμαρτιας ο νομος');
INSERT INTO `bible_verses_tr` VALUES ('5631', '46', '15', '57', 'τω δε θεω χαρις τω διδοντι ημιν το νικος δια του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5632', '46', '15', '58', 'ωστε αδελφοι μου αγαπητοι εδραιοι γινεσθε αμετακινητοι περισσευοντες εν τω εργω του κυριου παντοτε ειδοτες οτι ο κοπος υμων ουκ εστιν κενος εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5633', '46', '16', '1', 'περι δε της λογιας της εις τους αγιους ωσπερ διεταξα ταις εκκλησιαις της γαλατιας ουτως και υμεις ποιησατε');
INSERT INTO `bible_verses_tr` VALUES ('5634', '46', '16', '2', 'κατα μιαν σαββατων εκαστος υμων παρ εαυτω τιθετω θησαυριζων ο τι αν ευοδωται ινα μη οταν ελθω τοτε λογιαι γινωνται');
INSERT INTO `bible_verses_tr` VALUES ('5635', '46', '16', '3', 'οταν δε παραγενωμαι ους εαν δοκιμασητε δι επιστολων τουτους πεμψω απενεγκειν την χαριν υμων εις ιερουσαλημ');
INSERT INTO `bible_verses_tr` VALUES ('5636', '46', '16', '4', 'εαν δε η αξιον του καμε πορευεσθαι συν εμοι πορευσονται');
INSERT INTO `bible_verses_tr` VALUES ('5637', '46', '16', '5', 'ελευσομαι δε προς υμας οταν μακεδονιαν διελθω μακεδονιαν γαρ διερχομαι');
INSERT INTO `bible_verses_tr` VALUES ('5638', '46', '16', '6', 'προς υμας δε τυχον παραμενω η και παραχειμασω ινα υμεις με προπεμψητε ου εαν πορευωμαι');
INSERT INTO `bible_verses_tr` VALUES ('5639', '46', '16', '7', 'ου θελω γαρ υμας αρτι εν παροδω ιδειν ελπιζω δε χρονον τινα επιμειναι προς υμας εαν ο κυριος επιτρεπη');
INSERT INTO `bible_verses_tr` VALUES ('5640', '46', '16', '8', 'επιμενω δε εν εφεσω εως της πεντηκοστης');
INSERT INTO `bible_verses_tr` VALUES ('5641', '46', '16', '9', 'θυρα γαρ μοι ανεωγεν μεγαλη και ενεργης και αντικειμενοι πολλοι');
INSERT INTO `bible_verses_tr` VALUES ('5642', '46', '16', '10', 'εαν δε ελθη τιμοθεος βλεπετε ινα αφοβως γενηται προς υμας το γαρ εργον κυριου εργαζεται ως και εγω');
INSERT INTO `bible_verses_tr` VALUES ('5643', '46', '16', '11', 'μη τις ουν αυτον εξουθενηση προπεμψατε δε αυτον εν ειρηνη ινα ελθη προς με εκδεχομαι γαρ αυτον μετα των αδελφων');
INSERT INTO `bible_verses_tr` VALUES ('5644', '46', '16', '12', 'περι δε απολλω του αδελφου πολλα παρεκαλεσα αυτον ινα ελθη προς υμας μετα των αδελφων και παντως ουκ ην θελημα ινα νυν ελθη ελευσεται δε οταν ευκαιρηση');
INSERT INTO `bible_verses_tr` VALUES ('5645', '46', '16', '13', 'γρηγορειτε στηκετε εν τη πιστει ανδριζεσθε κραταιουσθε');
INSERT INTO `bible_verses_tr` VALUES ('5646', '46', '16', '14', 'παντα υμων εν αγαπη γινεσθω');
INSERT INTO `bible_verses_tr` VALUES ('5647', '46', '16', '15', 'παρακαλω δε υμας αδελφοι οιδατε την οικιαν στεφανα οτι εστιν απαρχη της αχαιας και εις διακονιαν τοις αγιοις εταξαν εαυτους');
INSERT INTO `bible_verses_tr` VALUES ('5648', '46', '16', '16', 'ινα και υμεις υποτασσησθε τοις τοιουτοις και παντι τω συνεργουντι και κοπιωντι');
INSERT INTO `bible_verses_tr` VALUES ('5649', '46', '16', '17', 'χαιρω δε επι τη παρουσια στεφανα και φουρτουνατου και αχαικου οτι το υμων υστερημα ουτοι ανεπληρωσαν');
INSERT INTO `bible_verses_tr` VALUES ('5650', '46', '16', '18', 'ανεπαυσαν γαρ το εμον πνευμα και το υμων επιγινωσκετε ουν τους τοιουτους');
INSERT INTO `bible_verses_tr` VALUES ('5651', '46', '16', '19', 'ασπαζονται υμας αι εκκλησιαι της ασιας ασπαζονται υμας εν κυριω πολλα ακυλας και πρισκιλλα συν τη κατ οικον αυτων εκκλησια');
INSERT INTO `bible_verses_tr` VALUES ('5652', '46', '16', '20', 'ασπαζονται υμας οι αδελφοι παντες ασπασασθε αλληλους εν φιληματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('5653', '46', '16', '21', 'ο ασπασμος τη εμη χειρι παυλου');
INSERT INTO `bible_verses_tr` VALUES ('5654', '46', '16', '22', 'ει τις ου φιλει τον κυριον ιησουν χριστον ητω αναθεμα μαραν αθα');
INSERT INTO `bible_verses_tr` VALUES ('5655', '46', '16', '23', 'η χαρις του κυριου ιησου χριστου μεθ υμων');
INSERT INTO `bible_verses_tr` VALUES ('5656', '46', '16', '24', 'η αγαπη μου μετα παντων υμων εν χριστω ιησου αμην [προς κορινθιους πρωτη εγραφη απο φιλιππων δια στεφανα και φουρτουνατου και αχαικου και τιμοθεου]');
INSERT INTO `bible_verses_tr` VALUES ('5657', '47', '1', '1', 'παυλος αποστολος ιησου χριστου δια θεληματος θεου και τιμοθεος ο αδελφος τη εκκλησια του θεου τη ουση εν κορινθω συν τοις αγιοις πασιν τοις ουσιν εν ολη τη αχαια');
INSERT INTO `bible_verses_tr` VALUES ('5658', '47', '1', '2', 'χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5659', '47', '1', '3', 'ευλογητος ο θεος και πατηρ του κυριου ημων ιησου χριστου ο πατηρ των οικτιρμων και θεος πασης παρακλησεως');
INSERT INTO `bible_verses_tr` VALUES ('5660', '47', '1', '4', 'ο παρακαλων ημας επι παση τη θλιψει ημων εις το δυνασθαι ημας παρακαλειν τους εν παση θλιψει δια της παρακλησεως ης παρακαλουμεθα αυτοι υπο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5661', '47', '1', '5', 'οτι καθως περισσευει τα παθηματα του χριστου εις ημας ουτως δια χριστου περισσευει και η παρακλησις ημων');
INSERT INTO `bible_verses_tr` VALUES ('5662', '47', '1', '6', 'ειτε δε θλιβομεθα υπερ της υμων παρακλησεως και σωτηριας της ενεργουμενης εν υπομονη των αυτων παθηματων ων και ημεις πασχομεν ειτε παρακαλουμεθα υπερ της υμων παρακλησεως και σωτηριας και η ελπις ημων βεβαια υπερ υμων');
INSERT INTO `bible_verses_tr` VALUES ('5663', '47', '1', '7', 'ειδοτες οτι ωσπερ κοινωνοι εστε των παθηματων ουτως και της παρακλησεως');
INSERT INTO `bible_verses_tr` VALUES ('5664', '47', '1', '8', 'ου γαρ θελομεν υμας αγνοειν αδελφοι υπερ της θλιψεως ημων της γενομενης ημιν εν τη ασια οτι καθ υπερβολην εβαρηθημεν υπερ δυναμιν ωστε εξαπορηθηναι ημας και του ζην');
INSERT INTO `bible_verses_tr` VALUES ('5665', '47', '1', '9', 'αλλα αυτοι εν εαυτοις το αποκριμα του θανατου εσχηκαμεν ινα μη πεποιθοτες ωμεν εφ εαυτοις αλλ επι τω θεω τω εγειροντι τους νεκρους');
INSERT INTO `bible_verses_tr` VALUES ('5666', '47', '1', '10', 'ος εκ τηλικουτου θανατου ερρυσατο ημας και ρυεται εις ον ηλπικαμεν οτι και ετι ρυσεται');
INSERT INTO `bible_verses_tr` VALUES ('5667', '47', '1', '11', 'συνυπουργουντων και υμων υπερ ημων τη δεησει ινα εκ πολλων προσωπων το εις ημας χαρισμα δια πολλων ευχαριστηθη υπερ ημων');
INSERT INTO `bible_verses_tr` VALUES ('5668', '47', '1', '12', 'η γαρ καυχησις ημων αυτη εστιν το μαρτυριον της συνειδησεως ημων οτι εν απλοτητι και ειλικρινεια θεου ουκ εν σοφια σαρκικη αλλ εν χαριτι θεου ανεστραφημεν εν τω κοσμω περισσοτερως δε προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('5669', '47', '1', '13', 'ου γαρ αλλα γραφομεν υμιν αλλ η α αναγινωσκετε η και επιγινωσκετε ελπιζω δε οτι και εως τελους επιγνωσεσθε');
INSERT INTO `bible_verses_tr` VALUES ('5670', '47', '1', '14', 'καθως και επεγνωτε ημας απο μερους οτι καυχημα υμων εσμεν καθαπερ και υμεις ημων εν τη ημερα του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('5671', '47', '1', '15', 'και ταυτη τη πεποιθησει εβουλομην προς υμας ελθειν προτερον ινα δευτεραν χαριν εχητε');
INSERT INTO `bible_verses_tr` VALUES ('5672', '47', '1', '16', 'και δι υμων διελθειν εις μακεδονιαν και παλιν απο μακεδονιας ελθειν προς υμας και υφ υμων προπεμφθηναι εις την ιουδαιαν');
INSERT INTO `bible_verses_tr` VALUES ('5673', '47', '1', '17', 'τουτο ουν βουλευομενος μη τι αρα τη ελαφρια εχρησαμην η α βουλευομαι κατα σαρκα βουλευομαι ινα η παρ εμοι το ναι ναι και το ου ου');
INSERT INTO `bible_verses_tr` VALUES ('5674', '47', '1', '18', 'πιστος δε ο θεος οτι ο λογος ημων ο προς υμας ουκ εγενετο ναι και ου');
INSERT INTO `bible_verses_tr` VALUES ('5675', '47', '1', '19', 'ο γαρ του θεου υιος ιησους χριστος ο εν υμιν δι ημων κηρυχθεις δι εμου και σιλουανου και τιμοθεου ουκ εγενετο ναι και ου αλλα ναι εν αυτω γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('5676', '47', '1', '20', 'οσαι γαρ επαγγελιαι θεου εν αυτω το ναι και εν αυτω το αμην τω θεω προς δοξαν δι ημων');
INSERT INTO `bible_verses_tr` VALUES ('5677', '47', '1', '21', 'ο δε βεβαιων ημας συν υμιν εις χριστον και χρισας ημας θεος');
INSERT INTO `bible_verses_tr` VALUES ('5678', '47', '1', '22', 'ο και σφραγισαμενος ημας και δους τον αρραβωνα του πνευματος εν ταις καρδιαις ημων');
INSERT INTO `bible_verses_tr` VALUES ('5679', '47', '1', '23', 'εγω δε μαρτυρα τον θεον επικαλουμαι επι την εμην ψυχην οτι φειδομενος υμων ουκετι ηλθον εις κορινθον');
INSERT INTO `bible_verses_tr` VALUES ('5680', '47', '1', '24', 'ουχ οτι κυριευομεν υμων της πιστεως αλλα συνεργοι εσμεν της χαρας υμων τη γαρ πιστει εστηκατε');
INSERT INTO `bible_verses_tr` VALUES ('5681', '47', '2', '1', 'εκρινα δε εμαυτω τουτο το μη παλιν ελθειν εν λυπη προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('5682', '47', '2', '2', 'ει γαρ εγω λυπω υμας και τις εστιν ο ευφραινων με ει μη ο λυπουμενος εξ εμου');
INSERT INTO `bible_verses_tr` VALUES ('5683', '47', '2', '3', 'και εγραψα υμιν τουτο αυτο ινα μη ελθων λυπην εχω αφ ων εδει με χαιρειν πεποιθως επι παντας υμας οτι η εμη χαρα παντων υμων εστιν');
INSERT INTO `bible_verses_tr` VALUES ('5684', '47', '2', '4', 'εκ γαρ πολλης θλιψεως και συνοχης καρδιας εγραψα υμιν δια πολλων δακρυων ουχ ινα λυπηθητε αλλα την αγαπην ινα γνωτε ην εχω περισσοτερως εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('5685', '47', '2', '5', 'ει δε τις λελυπηκεν ουκ εμε λελυπηκεν αλλ απο μερους ινα μη επιβαρω παντας υμας');
INSERT INTO `bible_verses_tr` VALUES ('5686', '47', '2', '6', 'ικανον τω τοιουτω η επιτιμια αυτη η υπο των πλειονων');
INSERT INTO `bible_verses_tr` VALUES ('5687', '47', '2', '7', 'ωστε τουναντιον μαλλον υμας χαρισασθαι και παρακαλεσαι μηπως τη περισσοτερα λυπη καταποθη ο τοιουτος');
INSERT INTO `bible_verses_tr` VALUES ('5688', '47', '2', '8', 'διο παρακαλω υμας κυρωσαι εις αυτον αγαπην');
INSERT INTO `bible_verses_tr` VALUES ('5689', '47', '2', '9', 'εις τουτο γαρ και εγραψα ινα γνω την δοκιμην υμων ει εις παντα υπηκοοι εστε');
INSERT INTO `bible_verses_tr` VALUES ('5690', '47', '2', '10', 'ω δε τι χαριζεσθε και εγω και γαρ εγω ει τι κεχαρισμαι ω κεχαρισμαι δι υμας εν προσωπω χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5691', '47', '2', '11', 'ινα μη πλεονεκτηθωμεν υπο του σατανα ου γαρ αυτου τα νοηματα αγνοουμεν');
INSERT INTO `bible_verses_tr` VALUES ('5692', '47', '2', '12', 'ελθων δε εις την τρωαδα εις το ευαγγελιον του χριστου και θυρας μοι ανεωγμενης εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('5693', '47', '2', '13', 'ουκ εσχηκα ανεσιν τω πνευματι μου τω μη ευρειν με τιτον τον αδελφον μου αλλα αποταξαμενος αυτοις εξηλθον εις μακεδονιαν');
INSERT INTO `bible_verses_tr` VALUES ('5694', '47', '2', '14', 'τω δε θεω χαρις τω παντοτε θριαμβευοντι ημας εν τω χριστω και την οσμην της γνωσεως αυτου φανερουντι δι ημων εν παντι τοπω');
INSERT INTO `bible_verses_tr` VALUES ('5695', '47', '2', '15', 'οτι χριστου ευωδια εσμεν τω θεω εν τοις σωζομενοις και εν τοις απολλυμενοις');
INSERT INTO `bible_verses_tr` VALUES ('5696', '47', '2', '16', 'οις μεν οσμη θανατου εις θανατον οις δε οσμη ζωης εις ζωην και προς ταυτα τις ικανος');
INSERT INTO `bible_verses_tr` VALUES ('5697', '47', '2', '17', 'ου γαρ εσμεν ως οι πολλοι καπηλευοντες τον λογον του θεου αλλ ως εξ ειλικρινειας αλλ ως εκ θεου κατενωπιον του θεου εν χριστω λαλουμεν');
INSERT INTO `bible_verses_tr` VALUES ('5698', '47', '3', '1', 'αρχομεθα παλιν εαυτους συνιστανειν  {VAR1: ει } {VAR2: η } μη χρηζομεν ως τινες συστατικων επιστολων προς υμας η εξ υμων συστατικων');
INSERT INTO `bible_verses_tr` VALUES ('5699', '47', '3', '2', 'η επιστολη ημων υμεις εστε εγγεγραμμενη εν ταις καρδιαις ημων γινωσκομενη και αναγινωσκομενη υπο παντων ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('5700', '47', '3', '3', 'φανερουμενοι οτι εστε επιστολη χριστου διακονηθεισα υφ ημων εγγεγραμμενη ου μελανι αλλα πνευματι θεου ζωντος ουκ εν πλαξιν λιθιναις αλλ εν πλαξιν καρδιας σαρκιναις');
INSERT INTO `bible_verses_tr` VALUES ('5701', '47', '3', '4', 'πεποιθησιν δε τοιαυτην εχομεν δια του χριστου προς τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('5702', '47', '3', '5', 'ουχ οτι ικανοι εσμεν αφ εαυτων λογισασθαι τι ως εξ εαυτων αλλ η ικανοτης ημων εκ του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5703', '47', '3', '6', 'ος και ικανωσεν ημας διακονους καινης διαθηκης ου γραμματος αλλα πνευματος το γαρ γραμμα αποκτεινει το δε πνευμα ζωοποιει');
INSERT INTO `bible_verses_tr` VALUES ('5704', '47', '3', '7', 'ει δε η διακονια του θανατου εν γραμμασιν εντετυπωμενη εν λιθοις εγενηθη εν δοξη ωστε μη δυνασθαι ατενισαι τους υιους ισραηλ εις το προσωπον μωσεως δια την δοξαν του προσωπου αυτου την καταργουμενην');
INSERT INTO `bible_verses_tr` VALUES ('5705', '47', '3', '8', 'πως ουχι μαλλον η διακονια του πνευματος εσται εν δοξη');
INSERT INTO `bible_verses_tr` VALUES ('5706', '47', '3', '9', 'ει γαρ η διακονια της κατακρισεως δοξα πολλω μαλλον περισσευει η διακονια της δικαιοσυνης εν δοξη');
INSERT INTO `bible_verses_tr` VALUES ('5707', '47', '3', '10', 'και γαρ ουδε δεδοξασται το δεδοξασμενον εν τουτω τω μερει ενεκεν της υπερβαλλουσης δοξης');
INSERT INTO `bible_verses_tr` VALUES ('5708', '47', '3', '11', 'ει γαρ το καταργουμενον δια δοξης πολλω μαλλον το μενον εν δοξη');
INSERT INTO `bible_verses_tr` VALUES ('5709', '47', '3', '12', 'εχοντες ουν τοιαυτην ελπιδα πολλη παρρησια χρωμεθα');
INSERT INTO `bible_verses_tr` VALUES ('5710', '47', '3', '13', 'και ου καθαπερ μωσης ετιθει καλυμμα επι το προσωπον εαυτου προς το μη ατενισαι τους υιους ισραηλ εις το τελος του καταργουμενου');
INSERT INTO `bible_verses_tr` VALUES ('5711', '47', '3', '14', 'αλλ επωρωθη τα νοηματα αυτων αχρι γαρ της σημερον το αυτο καλυμμα επι τη αναγνωσει της παλαιας διαθηκης μενει μη ανακαλυπτομενον ο τι εν χριστω καταργειται');
INSERT INTO `bible_verses_tr` VALUES ('5712', '47', '3', '15', 'αλλ εως σημερον ηνικα αναγινωσκεται μωσης καλυμμα επι την καρδιαν αυτων κειται');
INSERT INTO `bible_verses_tr` VALUES ('5713', '47', '3', '16', 'ηνικα δ αν επιστρεψη προς κυριον περιαιρειται το καλυμμα');
INSERT INTO `bible_verses_tr` VALUES ('5714', '47', '3', '17', 'ο δε κυριος το πνευμα εστιν ου δε το πνευμα κυριου εκει ελευθερια');
INSERT INTO `bible_verses_tr` VALUES ('5715', '47', '3', '18', 'ημεις δε παντες ανακεκαλυμμενω προσωπω την δοξαν κυριου κατοπτριζομενοι την αυτην εικονα μεταμορφουμεθα απο δοξης εις δοξαν καθαπερ απο κυριου πνευματος');
INSERT INTO `bible_verses_tr` VALUES ('5716', '47', '4', '1', 'δια τουτο εχοντες την διακονιαν ταυτην καθως ηλεηθημεν ουκ εκκακουμεν');
INSERT INTO `bible_verses_tr` VALUES ('5717', '47', '4', '2', 'αλλ απειπαμεθα τα κρυπτα της αισχυνης μη περιπατουντες εν πανουργια μηδε δολουντες τον λογον του θεου αλλα τη φανερωσει της αληθειας συνιστωντες εαυτους προς πασαν συνειδησιν ανθρωπων ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5718', '47', '4', '3', 'ει δε και εστιν κεκαλυμμενον το ευαγγελιον ημων εν τοις απολλυμενοις εστιν κεκαλυμμενον');
INSERT INTO `bible_verses_tr` VALUES ('5719', '47', '4', '4', 'εν οις ο θεος του αιωνος τουτου ετυφλωσεν τα νοηματα των απιστων εις το μη αυγασαι αυτοις τον φωτισμον του ευαγγελιου της δοξης του χριστου ος εστιν εικων του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5720', '47', '4', '5', 'ου γαρ εαυτους κηρυσσομεν αλλα χριστον ιησουν κυριον εαυτους δε δουλους υμων δια ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('5721', '47', '4', '6', 'οτι ο θεος ο ειπων εκ σκοτους φως λαμψαι ος ελαμψεν εν ταις καρδιαις ημων προς φωτισμον της γνωσεως της δοξης του θεου εν προσωπω ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5722', '47', '4', '7', 'εχομεν δε τον θησαυρον τουτον εν οστρακινοις σκευεσιν ινα η υπερβολη της δυναμεως η του θεου και μη εξ ημων');
INSERT INTO `bible_verses_tr` VALUES ('5723', '47', '4', '8', 'εν παντι θλιβομενοι αλλ ου στενοχωρουμενοι απορουμενοι αλλ ουκ εξαπορουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('5724', '47', '4', '9', 'διωκομενοι αλλ ουκ εγκαταλειπομενοι καταβαλλομενοι αλλ ουκ απολλυμενοι');
INSERT INTO `bible_verses_tr` VALUES ('5725', '47', '4', '10', 'παντοτε την νεκρωσιν του κυριου ιησου εν τω σωματι περιφεροντες ινα και η ζωη του ιησου εν τω σωματι ημων φανερωθη');
INSERT INTO `bible_verses_tr` VALUES ('5726', '47', '4', '11', 'αει γαρ ημεις οι ζωντες εις θανατον παραδιδομεθα δια ιησουν ινα και η ζωη του ιησου φανερωθη εν τη θνητη σαρκι ημων');
INSERT INTO `bible_verses_tr` VALUES ('5727', '47', '4', '12', 'ωστε ο μεν θανατος εν ημιν ενεργειται η δε ζωη εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5728', '47', '4', '13', 'εχοντες δε το αυτο πνευμα της πιστεως κατα το γεγραμμενον επιστευσα διο ελαλησα και ημεις πιστευομεν διο και λαλουμεν');
INSERT INTO `bible_verses_tr` VALUES ('5729', '47', '4', '14', 'ειδοτες οτι ο εγειρας τον κυριον ιησουν και ημας δια ιησου εγερει και παραστησει συν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5730', '47', '4', '15', 'τα γαρ παντα δι υμας ινα η χαρις πλεονασασα δια των πλειονων την ευχαριστιαν περισσευση εις την δοξαν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5731', '47', '4', '16', 'διο ουκ εκκακουμεν αλλ ει και ο εξω ημων ανθρωπος διαφθειρεται αλλ ο εσωθεν ανακαινουται ημερα και ημερα');
INSERT INTO `bible_verses_tr` VALUES ('5732', '47', '4', '17', 'το γαρ παραυτικα ελαφρον της θλιψεως ημων καθ υπερβολην εις υπερβολην αιωνιον βαρος δοξης κατεργαζεται ημιν');
INSERT INTO `bible_verses_tr` VALUES ('5733', '47', '4', '18', 'μη σκοπουντων ημων τα βλεπομενα αλλα τα μη βλεπομενα τα γαρ βλεπομενα προσκαιρα τα δε μη βλεπομενα αιωνια');
INSERT INTO `bible_verses_tr` VALUES ('5734', '47', '5', '1', 'οιδαμεν γαρ οτι εαν η επιγειος ημων οικια του σκηνους καταλυθη οικοδομην εκ θεου εχομεν οικιαν αχειροποιητον αιωνιον εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('5735', '47', '5', '2', 'και γαρ εν τουτω στεναζομεν το οικητηριον ημων το εξ ουρανου επενδυσασθαι επιποθουντες');
INSERT INTO `bible_verses_tr` VALUES ('5736', '47', '5', '3', 'ειγε και ενδυσαμενοι ου γυμνοι ευρεθησομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5737', '47', '5', '4', 'και γαρ οι οντες εν τω σκηνει στεναζομεν βαρουμενοι  {VAR1: επειδη } {VAR2: εφ ω } ου θελομεν εκδυσασθαι αλλ επενδυσασθαι ινα καταποθη το θνητον υπο της ζωης');
INSERT INTO `bible_verses_tr` VALUES ('5738', '47', '5', '5', 'ο δε κατεργασαμενος ημας εις αυτο τουτο θεος ο και δους ημιν τον αρραβωνα του πνευματος');
INSERT INTO `bible_verses_tr` VALUES ('5739', '47', '5', '6', 'θαρρουντες ουν παντοτε και ειδοτες οτι ενδημουντες εν τω σωματι εκδημουμεν απο του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('5740', '47', '5', '7', 'δια πιστεως γαρ περιπατουμεν ου δια ειδους');
INSERT INTO `bible_verses_tr` VALUES ('5741', '47', '5', '8', 'θαρρουμεν δε και ευδοκουμεν μαλλον εκδημησαι εκ του σωματος και ενδημησαι προς τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('5742', '47', '5', '9', 'διο και φιλοτιμουμεθα ειτε ενδημουντες ειτε εκδημουντες ευαρεστοι αυτω ειναι');
INSERT INTO `bible_verses_tr` VALUES ('5743', '47', '5', '10', 'τους γαρ παντας ημας φανερωθηναι δει εμπροσθεν του βηματος του χριστου ινα κομισηται εκαστος τα δια του σωματος προς α επραξεν ειτε αγαθον ειτε κακον');
INSERT INTO `bible_verses_tr` VALUES ('5744', '47', '5', '11', 'ειδοτες ουν τον φοβον του κυριου ανθρωπους πειθομεν θεω δε πεφανερωμεθα ελπιζω δε και εν ταις συνειδησεσιν υμων πεφανερωσθαι');
INSERT INTO `bible_verses_tr` VALUES ('5745', '47', '5', '12', 'ου γαρ παλιν εαυτους συνιστανομεν υμιν αλλα αφορμην διδοντες υμιν καυχηματος υπερ ημων ινα εχητε προς τους εν προσωπω καυχωμενους και ου καρδια');
INSERT INTO `bible_verses_tr` VALUES ('5746', '47', '5', '13', 'ειτε γαρ εξεστημεν θεω ειτε σωφρονουμεν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5747', '47', '5', '14', 'η γαρ αγαπη του χριστου συνεχει ημας κριναντας τουτο οτι ει εις υπερ παντων απεθανεν αρα οι παντες απεθανον');
INSERT INTO `bible_verses_tr` VALUES ('5748', '47', '5', '15', 'και υπερ παντων απεθανεν ινα οι ζωντες μηκετι εαυτοις ζωσιν αλλα τω υπερ αυτων αποθανοντι και εγερθεντι');
INSERT INTO `bible_verses_tr` VALUES ('5749', '47', '5', '16', 'ωστε ημεις απο του νυν ουδενα οιδαμεν κατα σαρκα ει δε και εγνωκαμεν κατα σαρκα χριστον αλλα νυν ουκετι γινωσκομεν');
INSERT INTO `bible_verses_tr` VALUES ('5750', '47', '5', '17', 'ωστε ει τις εν χριστω καινη κτισις τα αρχαια παρηλθεν ιδου γεγονεν καινα τα παντα');
INSERT INTO `bible_verses_tr` VALUES ('5751', '47', '5', '18', 'τα δε παντα εκ του θεου του καταλλαξαντος ημας εαυτω δια ιησου χριστου και δοντος ημιν την διακονιαν της καταλλαγης');
INSERT INTO `bible_verses_tr` VALUES ('5752', '47', '5', '19', 'ως οτι θεος ην εν χριστω κοσμον καταλλασσων εαυτω μη λογιζομενος αυτοις τα παραπτωματα αυτων και θεμενος εν ημιν τον λογον της καταλλαγης');
INSERT INTO `bible_verses_tr` VALUES ('5753', '47', '5', '20', 'υπερ χριστου ουν πρεσβευομεν ως του θεου παρακαλουντος δι ημων δεομεθα υπερ χριστου καταλλαγητε τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5754', '47', '5', '21', 'τον γαρ μη γνοντα αμαρτιαν υπερ ημων αμαρτιαν εποιησεν ινα ημεις γινωμεθα δικαιοσυνη θεου εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('5755', '47', '6', '1', 'συνεργουντες δε και παρακαλουμεν μη εις κενον την χαριν του θεου δεξασθαι υμας');
INSERT INTO `bible_verses_tr` VALUES ('5756', '47', '6', '2', 'λεγει γαρ καιρω δεκτω επηκουσα σου και εν ημερα σωτηριας εβοηθησα σοι ιδου νυν καιρος ευπροσδεκτος ιδου νυν ημερα σωτηριας');
INSERT INTO `bible_verses_tr` VALUES ('5757', '47', '6', '3', 'μηδεμιαν εν μηδενι διδοντες προσκοπην ινα μη μωμηθη η διακονια');
INSERT INTO `bible_verses_tr` VALUES ('5758', '47', '6', '4', 'αλλ εν παντι συνιστωντες εαυτους ως θεου διακονοι εν υπομονη πολλη εν θλιψεσιν εν αναγκαις εν στενοχωριαις');
INSERT INTO `bible_verses_tr` VALUES ('5759', '47', '6', '5', 'εν πληγαις εν φυλακαις εν ακαταστασιαις εν κοποις εν αγρυπνιαις εν νηστειαις');
INSERT INTO `bible_verses_tr` VALUES ('5760', '47', '6', '6', 'εν αγνοτητι εν γνωσει εν μακροθυμια εν χρηστοτητι εν πνευματι αγιω εν αγαπη ανυποκριτω');
INSERT INTO `bible_verses_tr` VALUES ('5761', '47', '6', '7', 'εν λογω αληθειας εν δυναμει θεου δια των οπλων της δικαιοσυνης των δεξιων και αριστερων');
INSERT INTO `bible_verses_tr` VALUES ('5762', '47', '6', '8', 'δια δοξης και ατιμιας δια δυσφημιας και ευφημιας ως πλανοι και αληθεις');
INSERT INTO `bible_verses_tr` VALUES ('5763', '47', '6', '9', 'ως αγνοουμενοι και επιγινωσκομενοι ως αποθνησκοντες και ιδου ζωμεν ως παιδευομενοι και μη θανατουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('5764', '47', '6', '10', 'ως λυπουμενοι αει δε χαιροντες ως πτωχοι πολλους δε πλουτιζοντες ως μηδεν εχοντες και παντα κατεχοντες');
INSERT INTO `bible_verses_tr` VALUES ('5765', '47', '6', '11', 'το στομα ημων ανεωγεν προς υμας κορινθιοι η καρδια ημων πεπλατυνται');
INSERT INTO `bible_verses_tr` VALUES ('5766', '47', '6', '12', 'ου στενοχωρεισθε εν ημιν στενοχωρεισθε δε εν τοις σπλαγχνοις υμων');
INSERT INTO `bible_verses_tr` VALUES ('5767', '47', '6', '13', 'την δε αυτην αντιμισθιαν ως τεκνοις λεγω πλατυνθητε και υμεις');
INSERT INTO `bible_verses_tr` VALUES ('5768', '47', '6', '14', 'μη γινεσθε ετεροζυγουντες απιστοις τις γαρ μετοχη δικαιοσυνη και ανομια τις δε κοινωνια φωτι προς σκοτος');
INSERT INTO `bible_verses_tr` VALUES ('5769', '47', '6', '15', 'τις δε συμφωνησις χριστω προς  {VAR1: βελιαρ } {VAR2: βελιαλ } η τις μερις πιστω μετα απιστου');
INSERT INTO `bible_verses_tr` VALUES ('5770', '47', '6', '16', 'τις δε συγκαταθεσις ναω θεου μετα ειδωλων υμεις γαρ ναος θεου εστε ζωντος καθως ειπεν ο θεος οτι ενοικησω εν αυτοις και εμπεριπατησω και εσομαι αυτων θεος και αυτοι εσονται μοι λαος');
INSERT INTO `bible_verses_tr` VALUES ('5771', '47', '6', '17', 'διο εξελθετε εκ μεσου αυτων και αφορισθητε λεγει κυριος και ακαθαρτου μη απτεσθε καγω εισδεξομαι υμας');
INSERT INTO `bible_verses_tr` VALUES ('5772', '47', '6', '18', 'και εσομαι υμιν εις πατερα και υμεις εσεσθε μοι εις υιους και θυγατερας λεγει κυριος παντοκρατωρ');
INSERT INTO `bible_verses_tr` VALUES ('5773', '47', '7', '1', 'ταυτας ουν εχοντες τας επαγγελιας αγαπητοι καθαρισωμεν εαυτους απο παντος μολυσμου σαρκος και πνευματος επιτελουντες αγιωσυνην εν φοβω θεου');
INSERT INTO `bible_verses_tr` VALUES ('5774', '47', '7', '2', 'χωρησατε ημας ουδενα ηδικησαμεν ουδενα εφθειραμεν ουδενα επλεονεκτησαμεν');
INSERT INTO `bible_verses_tr` VALUES ('5775', '47', '7', '3', 'ου προς κατακρισιν λεγω προειρηκα γαρ οτι εν ταις καρδιαις ημων εστε εις το συναποθανειν και συζην');
INSERT INTO `bible_verses_tr` VALUES ('5776', '47', '7', '4', 'πολλη μοι παρρησια προς υμας πολλη μοι καυχησις υπερ υμων πεπληρωμαι τη παρακλησει υπερπερισσευομαι τη χαρα επι παση τη θλιψει ημων');
INSERT INTO `bible_verses_tr` VALUES ('5777', '47', '7', '5', 'και γαρ ελθοντων ημων εις μακεδονιαν ουδεμιαν εσχηκεν ανεσιν η σαρξ ημων αλλ εν παντι θλιβομενοι εξωθεν μαχαι εσωθεν φοβοι');
INSERT INTO `bible_verses_tr` VALUES ('5778', '47', '7', '6', 'αλλ ο παρακαλων τους ταπεινους παρεκαλεσεν ημας ο θεος εν τη παρουσια τιτου');
INSERT INTO `bible_verses_tr` VALUES ('5779', '47', '7', '7', 'ου μονον δε εν τη παρουσια αυτου αλλα και εν τη παρακλησει η παρεκληθη εφ υμιν αναγγελλων ημιν την υμων επιποθησιν τον υμων οδυρμον τον υμων ζηλον υπερ εμου ωστε με μαλλον χαρηναι');
INSERT INTO `bible_verses_tr` VALUES ('5780', '47', '7', '8', 'οτι ει και ελυπησα υμας εν τη επιστολη ου μεταμελομαι ει και μετεμελομην βλεπω γαρ οτι η επιστολη εκεινη ει και προς ωραν ελυπησεν υμας');
INSERT INTO `bible_verses_tr` VALUES ('5781', '47', '7', '9', 'νυν χαιρω ουχ οτι ελυπηθητε αλλ οτι ελυπηθητε εις μετανοιαν ελυπηθητε γαρ κατα θεον ινα εν μηδενι ζημιωθητε εξ ημων');
INSERT INTO `bible_verses_tr` VALUES ('5782', '47', '7', '10', 'η γαρ κατα θεον λυπη μετανοιαν εις σωτηριαν αμεταμελητον κατεργαζεται η δε του κοσμου λυπη θανατον κατεργαζεται');
INSERT INTO `bible_verses_tr` VALUES ('5783', '47', '7', '11', 'ιδου γαρ αυτο τουτο το κατα θεον λυπηθηναι υμας ποσην κατειργασατο υμιν σπουδην αλλα απολογιαν αλλα αγανακτησιν αλλα φοβον αλλα επιποθησιν αλλα ζηλον αλλ εκδικησιν εν παντι συνεστησατε εαυτους αγνους ειναι εν τω πραγματι');
INSERT INTO `bible_verses_tr` VALUES ('5784', '47', '7', '12', 'αρα ει και εγραψα υμιν ουχ εινεκεν του αδικησαντος ουδε εινεκεν του αδικηθεντος αλλ εινεκεν του φανερωθηναι την σπουδην  {VAR1: υμων } {VAR2: ημων } την υπερ  {VAR1: ημων } {VAR2: υμων } προς υμας ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('5785', '47', '7', '13', 'δια τουτο παρακεκλημεθα επι τη παρακλησει υμων περισσοτερως δε μαλλον εχαρημεν επι τη χαρα τιτου οτι αναπεπαυται το πνευμα αυτου απο παντων υμων');
INSERT INTO `bible_verses_tr` VALUES ('5786', '47', '7', '14', 'οτι ει τι αυτω υπερ υμων κεκαυχημαι ου κατησχυνθην αλλ ως παντα εν αληθεια ελαλησαμεν υμιν ουτως και η καυχησις ημων η επι τιτου αληθεια εγενηθη');
INSERT INTO `bible_verses_tr` VALUES ('5787', '47', '7', '15', 'και τα σπλαγχνα αυτου περισσοτερως εις υμας εστιν αναμιμνησκομενου την παντων υμων υπακοην ως μετα φοβου και τρομου εδεξασθε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('5788', '47', '7', '16', 'χαιρω  {VAR2: ουν } οτι εν παντι θαρρω εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5789', '47', '8', '1', 'γνωριζομεν δε υμιν αδελφοι την χαριν του θεου την δεδομενην εν ταις εκκλησιαις της μακεδονιας');
INSERT INTO `bible_verses_tr` VALUES ('5790', '47', '8', '2', 'οτι εν πολλη δοκιμη θλιψεως η περισσεια της χαρας αυτων και η κατα βαθους πτωχεια αυτων επερισσευσεν εις τον πλουτον της απλοτητος αυτων');
INSERT INTO `bible_verses_tr` VALUES ('5791', '47', '8', '3', 'οτι κατα δυναμιν μαρτυρω και υπερ δυναμιν αυθαιρετοι');
INSERT INTO `bible_verses_tr` VALUES ('5792', '47', '8', '4', 'μετα πολλης παρακλησεως δεομενοι ημων την χαριν και την κοινωνιαν της διακονιας της εις τους αγιους δεξασθαι ημας');
INSERT INTO `bible_verses_tr` VALUES ('5793', '47', '8', '5', 'και ου καθως ηλπισαμεν αλλ εαυτους εδωκαν πρωτον τω κυριω και ημιν δια θεληματος θεου');
INSERT INTO `bible_verses_tr` VALUES ('5794', '47', '8', '6', 'εις το παρακαλεσαι ημας τιτον ινα καθως προενηρξατο ουτως και επιτελεση εις υμας και την χαριν ταυτην');
INSERT INTO `bible_verses_tr` VALUES ('5795', '47', '8', '7', 'αλλ ωσπερ εν παντι περισσευετε πιστει και λογω και γνωσει και παση σπουδη και τη εξ υμων εν ημιν αγαπη ινα και εν ταυτη τη χαριτι περισσευητε');
INSERT INTO `bible_verses_tr` VALUES ('5796', '47', '8', '8', 'ου κατ επιταγην λεγω αλλα δια της ετερων σπουδης και το της υμετερας αγαπης γνησιον δοκιμαζων');
INSERT INTO `bible_verses_tr` VALUES ('5797', '47', '8', '9', 'γινωσκετε γαρ την χαριν του κυριου ημων ιησου χριστου οτι δι υμας επτωχευσεν πλουσιος ων ινα υμεις τη εκεινου πτωχεια πλουτησητε');
INSERT INTO `bible_verses_tr` VALUES ('5798', '47', '8', '10', 'και γνωμην εν τουτω διδωμι τουτο γαρ υμιν συμφερει οιτινες ου μονον το ποιησαι αλλα και το θελειν προενηρξασθε απο περυσι');
INSERT INTO `bible_verses_tr` VALUES ('5799', '47', '8', '11', 'νυνι δε και το ποιησαι επιτελεσατε οπως καθαπερ η προθυμια του θελειν ουτως και το επιτελεσαι εκ του εχειν');
INSERT INTO `bible_verses_tr` VALUES ('5800', '47', '8', '12', 'ει γαρ η προθυμια προκειται καθο εαν εχη τις ευπροσδεκτος ου καθο ουκ εχει');
INSERT INTO `bible_verses_tr` VALUES ('5801', '47', '8', '13', 'ου γαρ ινα αλλοις ανεσις υμιν δε θλιψις αλλ εξ ισοτητος εν τω νυν καιρω το υμων περισσευμα εις το εκεινων υστερημα');
INSERT INTO `bible_verses_tr` VALUES ('5802', '47', '8', '14', 'ινα και το εκεινων περισσευμα γενηται εις το υμων υστερημα οπως γενηται ισοτης');
INSERT INTO `bible_verses_tr` VALUES ('5803', '47', '8', '15', 'καθως γεγραπται ο το πολυ ουκ επλεονασεν και ο το ολιγον ουκ ηλαττονησεν');
INSERT INTO `bible_verses_tr` VALUES ('5804', '47', '8', '16', 'χαρις δε τω θεω τω διδοντι την αυτην σπουδην υπερ υμων εν τη καρδια τιτου');
INSERT INTO `bible_verses_tr` VALUES ('5805', '47', '8', '17', 'οτι την μεν παρακλησιν εδεξατο σπουδαιοτερος δε υπαρχων αυθαιρετος εξηλθεν προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('5806', '47', '8', '18', 'συνεπεμψαμεν δε μετ αυτου τον αδελφον ου ο επαινος εν τω ευαγγελιω δια πασων των εκκλησιων');
INSERT INTO `bible_verses_tr` VALUES ('5807', '47', '8', '19', 'ου μονον δε αλλα και χειροτονηθεις υπο των εκκλησιων συνεκδημος ημων συν τη χαριτι ταυτη τη διακονουμενη υφ ημων προς την αυτου του κυριου δοξαν και προθυμιαν υμων');
INSERT INTO `bible_verses_tr` VALUES ('5808', '47', '8', '20', 'στελλομενοι τουτο μη τις ημας μωμησηται εν τη αδροτητι ταυτη τη διακονουμενη υφ ημων');
INSERT INTO `bible_verses_tr` VALUES ('5809', '47', '8', '21', 'προνοουμενοι καλα ου μονον ενωπιον κυριου αλλα και ενωπιον ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('5810', '47', '8', '22', 'συνεπεμψαμεν δε αυτοις τον αδελφον ημων ον εδοκιμασαμεν εν πολλοις πολλακις σπουδαιον οντα νυνι δε πολυ σπουδαιοτερον πεποιθησει πολλη τη εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('5811', '47', '8', '23', 'ειτε υπερ τιτου κοινωνος εμος και εις υμας συνεργος ειτε αδελφοι ημων αποστολοι εκκλησιων δοξα χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5812', '47', '8', '24', 'την ουν ενδειξιν της αγαπης υμων και ημων καυχησεως υπερ υμων εις αυτους ενδειξασθε και εις προσωπον των εκκλησιων');
INSERT INTO `bible_verses_tr` VALUES ('5813', '47', '9', '1', 'περι μεν γαρ της διακονιας της εις τους αγιους περισσον μοι εστιν το γραφειν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5814', '47', '9', '2', 'οιδα γαρ την προθυμιαν υμων ην υπερ υμων καυχωμαι μακεδοσιν οτι αχαια παρεσκευασται απο περυσι και ο εξ υμων ζηλος ηρεθισεν τους πλειονας');
INSERT INTO `bible_verses_tr` VALUES ('5815', '47', '9', '3', 'επεμψα δε τους αδελφους ινα μη το καυχημα ημων το υπερ υμων κενωθη εν τω μερει τουτω ινα καθως ελεγον παρεσκευασμενοι ητε');
INSERT INTO `bible_verses_tr` VALUES ('5816', '47', '9', '4', 'μηπως εαν ελθωσιν συν εμοι μακεδονες και ευρωσιν υμας απαρασκευαστους καταισχυνθωμεν ημεις ινα μη λεγωμεν υμεις εν τη υποστασει ταυτη της καυχησεως');
INSERT INTO `bible_verses_tr` VALUES ('5817', '47', '9', '5', 'αναγκαιον ουν ηγησαμην παρακαλεσαι τους αδελφους ινα προελθωσιν εις υμας και προκαταρτισωσιν την προκατηγγελμενην ευλογιαν υμων ταυτην ετοιμην ειναι ουτως ως ευλογιαν και μη ωσπερ πλεονεξιαν');
INSERT INTO `bible_verses_tr` VALUES ('5818', '47', '9', '6', 'τουτο δε ο σπειρων φειδομενως φειδομενως και θερισει και ο σπειρων επ ευλογιαις επ ευλογιαις και θερισει');
INSERT INTO `bible_verses_tr` VALUES ('5819', '47', '9', '7', 'εκαστος καθως προαιρειται τη καρδια μη εκ λυπης η εξ αναγκης ιλαρον γαρ δοτην αγαπα ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('5820', '47', '9', '8', 'δυνατος δε ο θεος πασαν χαριν περισσευσαι εις υμας ινα εν παντι παντοτε πασαν αυταρκειαν εχοντες περισσευητε εις παν εργον αγαθον');
INSERT INTO `bible_verses_tr` VALUES ('5821', '47', '9', '9', 'καθως γεγραπται εσκορπισεν εδωκεν τοις πενησιν η δικαιοσυνη αυτου μενει εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('5822', '47', '9', '10', 'ο δε επιχορηγων σπερμα τω σπειροντι και αρτον εις βρωσιν χορηγησαι και πληθυναι τον σπορον υμων και αυξησαι τα γεννηματα της δικαιοσυνης υμων');
INSERT INTO `bible_verses_tr` VALUES ('5823', '47', '9', '11', 'εν παντι πλουτιζομενοι εις πασαν απλοτητα ητις κατεργαζεται δι ημων ευχαριστιαν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5824', '47', '9', '12', 'οτι η διακονια της λειτουργιας ταυτης ου μονον εστιν προσαναπληρουσα τα υστερηματα των αγιων αλλα και περισσευουσα δια πολλων ευχαριστιων τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('5825', '47', '9', '13', 'δια της δοκιμης της διακονιας ταυτης δοξαζοντες τον θεον επι τη υποταγη της ομολογιας υμων εις το ευαγγελιον του χριστου και απλοτητι της κοινωνιας εις αυτους και εις παντας');
INSERT INTO `bible_verses_tr` VALUES ('5826', '47', '9', '14', 'και αυτων δεησει υπερ υμων επιποθουντων υμας δια την υπερβαλλουσαν χαριν του θεου εφ υμιν');
INSERT INTO `bible_verses_tr` VALUES ('5827', '47', '9', '15', 'χαρις δε τω θεω επι τη ανεκδιηγητω αυτου δωρεα');
INSERT INTO `bible_verses_tr` VALUES ('5828', '47', '10', '1', 'αυτος δε εγω παυλος παρακαλω υμας δια της πραοτητος και επιεικειας του χριστου ος κατα προσωπον μεν ταπεινος εν υμιν απων δε θαρρω εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('5829', '47', '10', '2', 'δεομαι δε το μη παρων θαρρησαι τη πεποιθησει η λογιζομαι τολμησαι επι τινας τους λογιζομενους ημας ως κατα σαρκα περιπατουντας');
INSERT INTO `bible_verses_tr` VALUES ('5830', '47', '10', '3', 'εν σαρκι γαρ περιπατουντες ου κατα σαρκα στρατευομεθα');
INSERT INTO `bible_verses_tr` VALUES ('5831', '47', '10', '4', 'τα γαρ οπλα της στρατειας ημων ου σαρκικα αλλα δυνατα τω θεω προς καθαιρεσιν οχυρωματων');
INSERT INTO `bible_verses_tr` VALUES ('5832', '47', '10', '5', 'λογισμους καθαιρουντες και παν υψωμα επαιρομενον κατα της γνωσεως του θεου και αιχμαλωτιζοντες παν νοημα εις την υπακοην του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5833', '47', '10', '6', 'και εν ετοιμω εχοντες εκδικησαι πασαν παρακοην οταν πληρωθη υμων η υπακοη');
INSERT INTO `bible_verses_tr` VALUES ('5834', '47', '10', '7', 'τα κατα προσωπον βλεπετε ει τις πεποιθεν εαυτω χριστου ειναι τουτο λογιζεσθω παλιν αφ εαυτου οτι καθως αυτος χριστου ουτως και ημεις χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5835', '47', '10', '8', 'εαν τε γαρ και περισσοτερον τι καυχησωμαι περι της εξουσιας ημων ης εδωκεν ο κυριος ημιν εις οικοδομην και ουκ εις καθαιρεσιν υμων ουκ αισχυνθησομαι');
INSERT INTO `bible_verses_tr` VALUES ('5836', '47', '10', '9', 'ινα μη δοξω ως αν εκφοβειν υμας δια των επιστολων');
INSERT INTO `bible_verses_tr` VALUES ('5837', '47', '10', '10', 'οτι αι μεν επιστολαι φησιν βαρειαι και ισχυραι η δε παρουσια του σωματος ασθενης και ο λογος εξουθενημενος');
INSERT INTO `bible_verses_tr` VALUES ('5838', '47', '10', '11', 'τουτο λογιζεσθω ο τοιουτος οτι οιοι εσμεν τω λογω δι επιστολων αποντες τοιουτοι και παροντες τω εργω');
INSERT INTO `bible_verses_tr` VALUES ('5839', '47', '10', '12', 'ου γαρ τολμωμεν εγκριναι η συγκριναι εαυτους τισιν των εαυτους συνιστανοντων αλλα αυτοι εν εαυτοις εαυτους μετρουντες και συγκρινοντες εαυτους εαυτοις ου συνιουσιν');
INSERT INTO `bible_verses_tr` VALUES ('5840', '47', '10', '13', 'ημεις δε ουχι εις τα αμετρα καυχησομεθα αλλα κατα το μετρον του κανονος ου εμερισεν ημιν ο θεος μετρου εφικεσθαι αχρι και υμων');
INSERT INTO `bible_verses_tr` VALUES ('5841', '47', '10', '14', 'ου γαρ ως μη εφικνουμενοι εις υμας υπερεκτεινομεν εαυτους αχρι γαρ και υμων εφθασαμεν εν τω ευαγγελιω του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('5842', '47', '10', '15', 'ουκ εις τα αμετρα καυχωμενοι εν αλλοτριοις κοποις ελπιδα δε εχοντες αυξανομενης της πιστεως υμων εν υμιν μεγαλυνθηναι κατα τον κανονα ημων εις περισσειαν');
INSERT INTO `bible_verses_tr` VALUES ('5843', '47', '10', '16', 'εις τα υπερεκεινα υμων ευαγγελισασθαι ουκ εν αλλοτριω κανονι εις τα ετοιμα καυχησασθαι');
INSERT INTO `bible_verses_tr` VALUES ('5844', '47', '10', '17', 'ο δε καυχωμενος εν κυριω καυχασθω');
