INSERT INTO `bible_verses_tr` VALUES ('5860', '47', '11', '15', 'ου μεγα ουν ει και οι διακονοι αυτου μετασχηματιζονται ως διακονοι δικαιοσυνης ων το τελος εσται κατα τα εργα αυτων');
