INSERT INTO `bible_verses_tr` VALUES ('5881', '47', '12', '3', 'και οιδα τον τοιουτον ανθρωπον ειτε εν σωματι ειτε εκτος του σωματος ουκ οιδα ο θεος οιδεν');
INSERT INTO `bible_verses_tr` VALUES ('5882', '47', '12', '4', 'οτι ηρπαγη εις τον παραδεισον και ηκουσεν αρρητα ρηματα α ουκ εξον ανθρωπω λαλησαι');
