INSERT INTO `bible_verses_tr` VALUES ('5884', '47', '12', '6', 'εαν γαρ θελησω καυχησασθαι ουκ εσομαι αφρων αληθειαν γαρ ερω φειδομαι δε μη τις εις εμε λογισηται υπερ ο βλεπει με η ακουει τι εξ εμου');
