INSERT INTO `bible_verses_tr` VALUES ('5897', '47', '12', '19', 'παλιν δοκειτε οτι υμιν απολογουμεθα κατενωπιον του θεου εν χριστω λαλουμεν τα δε παντα αγαπητοι υπερ της υμων οικοδομης');
